CREATE SCHEMA `Team4DB`;

CREATE TABLE `Team4DB`.search ( 
	search_id            int  NOT NULL  AUTO_INCREMENT  PRIMARY KEY,
	search_type          int  NOT NULL DEFAULT 0   ,
	search_key           varchar(20)  NOT NULL    ,
