 );

CREATE TABLE `Team4DB`.shop ( 
	`shop_id`            int  NOT NULL  AUTO_INCREMENT  PRIMARY KEY,
	`member_id`          int  NOT NULL    ,
	`shop_tax_id`        char(8)  NOT NULL    ,
	`shop_name`          varchar(200)  NOT NULL    ,
	`shop_zip_code`      char(6)  NOT NULL    ,
	`shop_city`          char(6)      ,
	`shop_address`       longtext  NOT NULL    ,
	`shop_latitude`      decimal(11,8)  NOT NULL    ,
	`shop_longitude`     decimal(11,8)  NOT NULL    ,
	`shop_description`   longtext      ,
	`shop_tag`           text      ,
	`shop_rating`        decimal(2,1)  NOT NULL DEFAULT 0.0   ,
	`shop_rating_count`  int  NOT NULL DEFAULT 0   ,
	`shop_rating_total`  int  NOT NULL DEFAULT 0   ,
	`shop_email`         varchar(100)      ,
	`shop_phone`         varchar(50)  NOT NULL    ,
	`shop_price_level`   int      ,
	`shop_opening_time`  text      ,
	`shop_website`       text      ,
	`shop_main_img`      varchar(200)      ,
	`shop_gallery`       text      ,
	`shop_create_time`   timestamp  NOT NULL DEFAULT CURRENT_TIMESTAMP   ,
	`shop_update_time`   timestamp  NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE now()  ,
	`shop_total_view`    int  NOT NULL DEFAULT 0   ,
	`shop_reserv_status` int  NOT NULL DEFAULT 0   ,
	CONSTRAINT `shop_tax_id_UNIQUE` UNIQUE ( `shop_tax_id` ) 
 );

CREATE TRIGGER shop__before_insert
BEFORE INSERT ON shop
FOR EACH ROW SET NEW.shop_create_time = NOW(), NEW.shop_update_time = NOW();

CREATE TRIGGER shop__before_update
BEFORE UPDATE ON shop
FOR EACH ROW SET NEW.shop_create_time = OLD.shop_create_time, NEW.shop_update_time = NOW();

CREATE INDEX `SHOP_member_id_FK` ON `Team4DB`.shop ( `member_id` );

