INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 99, 1, '53651580', '胖肚肚燒肉-中山店 (平價吃到飽 必吃美食 人氣高cp值推薦)', '104', '台北市', '中山區建國北路一段74號', 25.04916960, 121.53631790, '吃到飽', '吃到飽 CP值高、服務態度佳！?店員服務親切 東西好吃\n一定要找店員小雲服務人員 小雲烤肉技術不錯?人很親切又主動服務?服務人員：小雲，頂級的桌邊服務?，滿分有幾顆星就給幾顆被漂亮女孩小雲服務食物都變超好吃，\n服務很貼心，讚', 4.5, 485, 2183, 'Xg3J8sib4@gmail.com', '02 2507 2909', 4, '["星期一: 12:00 \\u2013 00:00","星期二: 12:00 \\u2013 00:00","星期三: 12:00 \\u2013 00:00","星期四: 12:00 \\u2013 00:00","星期五: 12:00 \\u2013 00:00","星期六: 12:00 \\u2013 00:00","星期日: 12:00 \\u2013 00:00"]', 'https://m.facebook.com/pages/category/Yakiniku-Restaurant/%E8%83%96%E8%82%9A%E8%82%9A%E7%87%92%E8%82%89%E5%90%83%E5%88%B0%E9%A3%BD%E4%B8%AD%E5%B1%B1%E5%BA%97-100990314973665/', '0.jpg', '[0.jpg]', '2021-06-08 09.42.59 上午', '2021-06-08 09.42.59 上午', 485, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 100, 1, '66356651', '台北火之舞蓁品燒 和牛放題 (必吃美食、燒烤吃到飽、人氣餐廳)', '106', '台北市', '大安區忠孝東路四段216巷19弄7號', 25.04042810, 121.55339560, '吃到飽', '吃到飽 餐點品質好，服務人員態度很棒！很棒的一間燒烤店，老闆很喜歡女生，對待女生也比較好，店內人員都很客氣，有任何問題都馬上幫你解決。\n一個頭髮短短的可愛男生，烤肉技術很棒，店內調酒很不錯喝。\n可以躲在化妝?，拍照很漂亮。同事推薦，5個人一起來吃，肉很好吃!服務很好。\n2人想吃沙拉，但怕份量太多（一份是4人量），特貼心的幫我們換成小碗裝?\n今天出門的時候朋友身體就有點不舒服，吃到後來想喝水，要求問可不可以飲料換成水，很貼心的拿馬克杯給我們?感謝~近期消費\n\n可能是人工手寫菜單\n所以導致漏單嚴重\n\n不過整體服務尚可\n食材新鮮\n不過干貝是組合魚板干貝 小失望限量橫隔膜超好吃?服務也很好\n生蠔?不會開都有服務人員幫忙?', 3.8, 1149, 4366, 'uGgyxdpGJ@gmail.com', '02 8773 7899', 3, '["星期一: 17:00 \\u2013 00:00","星期二: 17:00 \\u2013 00:00","星期三: 17:00 \\u2013 00:00","星期四: 17:00 \\u2013 00:00","星期五: 17:00 \\u2013 00:00","星期六: 12:00 \\u2013 00:00","星期日: 12:00 \\u2013 00:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.00 上午', '2021-06-08 09.43.00 上午', 1149, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 101, 1, '63699549', '貓咪貓咪6s 桌遊披薩吃到飽餐廳（台北東區旗艦店）', '106', '台北市', '大安區忠孝東路四段15號2樓之3', 25.04184390, 121.54440330, '吃到飽', '吃到飽 很棒的桌遊店???想給10顆星！\n邊玩遊戲還有吃到飽的Pizza、炸物、咖哩飯，連啤酒也可以無限暢飲，還有可愛的貓咪??皮蛋\n\n遊戲種類很多很豐富，每一位店員都真的很用心教我們每款遊戲如何玩、講解的很清楚，能感受到電源本身對桌遊的熱情度，結束還主動幫我們在外面看板拍照??????\n\n推薦大家來這邊可以和親朋好友一起度過快樂的桌遊時光唷??原本抱著桌遊店的吃到飽應該就是隨便吃，但意外的好吃！\n披薩居然是薄片，比某些連鎖的披薩店好吃多了！\n咖哩飯也完全不馬虎，用料超實在的！\n飲料吧也看得出來有用心在佈置，真的難得~~~4／3號前往貓咪咖啡廳\n有點難過的事被安排在看不到的貓咪區域?想說遊戲結束後去看 結果已經被關在籠子裡面也看不到了(。??︿??。)\n但有稍微瞄到是可愛的貓貓\n然後店員熱情講解我覺得很棒\n豬排咖喱飯我覺得比我想像中好吃?\n遊戲結束後會幫忙拍大家團體照 很加分\n因為我去過其他分店 都沒有一個會主動說拍照\n???非常棒的桌遊店！平常幾乎沒機會去這樣的店，服務人員非常耐心的介紹遊戲方法，並且推薦好玩的遊戲！\n餐點也很好吃，披薩是薄片，味道很好！韓式炸雞也很道地，飲料也不馬虎，每種服務都很實在！\n貓咪很親人很可愛！\n會讓人想要再來喔。環境舒適，裝潢很讚 ，飲料好喝\n最棒的是＞店員每個都超親切 推爆?\n還有最後的驚喜有夠讚 ，真的會笑死\n這間絕對會是“想再來”的桌遊餐廳?', 4.8, 1179, 5659, 'pCZusyeaLX@gmail.com', '0966 249 068', 2, '["星期一: 11:30 \\u2013 21:30","星期二: 11:30 \\u2013 21:30","星期三: 11:30 \\u2013 21:30","星期四: 11:30 \\u2013 21:30","星期五: 11:30 \\u2013 21:30","星期六: 11:30 \\u2013 21:30","星期日: 11:30 \\u2013 21:30"]', 'https://www.twcatcat.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.01 上午', '2021-06-08 09.43.01 上午', 1179, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 102, 1, '47974278', '神來一鍋-精緻鴛鴦火鍋 建國店/中山區美食/中山區餐廳/中山區火鍋/中山區吃到飽/中山區麻辣鍋/行天宮火鍋/行天宮吃到飽', '10491', '台北市', '中山區建國北路二段105號', 25.05907880, 121.53730360, '吃到飽', '吃到飽 吃很多年很多次的火鍋店，質量很夠，，，很值得推薦的雞柳，是我吃過最好吃的雞柳。麻辣鍋跟花雕雞鍋，好吃又便宜！很好吃，食物都很新鮮自助式火鍋\n價格528+10%\n食材新鮮\n而且在拿自助料的時候可以發現這間店的用心\n比如\n在醬料區放置小電風扇避免蟲在醬料區飛\n而放在冰箱自取肉盤肉盤\n而剛切下來的肉新鮮遇到空氣會變扁扁不美觀\n所以他會用一個空盤遮住\n希望肉盤保留原本的新鮮\n青菜也是撥好一葉葉的讓客人方便夾取\n取自助食材的地方也衛生乾淨\n服務人員會定時檢查確認是否有髒\n也會定期看客戶桌上有無空盤\n如有也會立刻幫你收走\n讓你吃的輕鬆自在\n醬料部分他們有自製辣油\n因?我不太敢吃辣\n但身旁的有人很愛辣\n常常在其他地方吃不到辣味\n但在這裡吃得到辣味有感動的感覺\n\n火鍋自助式價格一開始覺得高\n但吃完個人覺得CP值有回來\n以他們服務人員的定期巡點\n也有打理好自助吧檯衛生\n海鮮、肉類食材新鮮好吃\n冰品多元化任你選\n水果還是草莓\n真的很划算\n誠摯推薦尚未來的朋友們', 4.8, 1173, 5630, '0A4iq6VmpA@gmail.com', '02 2503 9877', 2, '["星期一: 17:00 \\u2013 00:00","星期二: 17:00 \\u2013 00:00","星期三: 17:00 \\u2013 00:00","星期四: 17:00 \\u2013 00:00","星期五: 17:00 \\u2013 00:00","星期六: 11:30 \\u2013 00:00","星期日: 11:30 \\u2013 00:00"]', 'https://www.godhotpot.com/hotpot', '0.jpg', '[0.jpg]', '2021-06-08 09.43.02 上午', '2021-06-08 09.43.02 上午', 1173, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 103, 1, '78931678', '品。帝王蟹吃到飽', '10491', '台北市', '中山區新生北路一段152號', 25.05136680, 121.52755740, '吃到飽', '吃到飽 好好吃喔，服務人員很親切套餐CP值巨高，老闆娘巨美兩個人用餐\n除了入座的帝王蟹肉很多之外\n之後的每一次加點帝王蟹肉都只有2「盤」（一盤2-3塊蟹肉）\n\n最後一次加點不但沒有任何通知，\n最後一次加點我們要了帝王蟹，結果只來了「2 塊」肉。\n\n我們詢問為什麼還有一個半小時卻什麼都不能點了、甚至剛剛是最後加點都沒有講？\n（他的最後一次加點用詞是  「還需要什麼嗎？」）\n\n染金髮的男店員回答：外面都這樣\n啊不然你們還要什麼現在講啊\n\n態度差爆= =\n\n---------------\n\n除此之外上餐速度很慢。\n餐前的生蠔要了三四次才來\n加點的帝王蟹腳也要一直注意請不同的店員幫我們問?有點怕人吃的感覺\n\n----------------\n\n帝王蟹我結對首推永安市場站旁邊的北海帝王蟹\n2000吃到飽真的沒在客氣，所有東西都給的很大方、叫了馬上來\n肉鮮甜又大塊，有烤的有火鍋的還有起司焗烤的\n不會後悔\n去吃就對了帝王蟹跟干貝 第一輪上的還算新鮮\n後面上的吃起來有一股味道\n肉很多都黏在殼上 回家後邊拉邊爬文\n才知道原來那是不新鮮\n蚌殼越上越小顆 加點的部分都小小盤\n不怪店家 畢竟成本考量\n只是海鮮最重要是吃新鮮\nby the way用這麼多海鮮煮的火鍋\n湯意外的不怎麼樣 沒有大海的鮮甜\n這也是蠻詭異的地方(海鮮鮮甜去哪裡了??)\n結婚周年慶來慶祝換來烙賽 夫妻輪流上廁所\n\n再來說說肉類 種類少\n畢竟這裡主打海鮮  所以沒關係\n我們不吃羊 所以不評論\n但伊比利豬吃起來 不像伊比利豬\n牛小排普通 牛舌拿來火鍋覺得好像炭烤比較合適\n\n唯一的優點是服務\n服務生很快速清潔桌面上垃圾桶\n貼心詢問需不需要加點跟推薦菜色\n另外 原本不吃飯後甜點的我\n吃了一口豆花 挖賽~~可以給滿分\n\n交通算便利 附近有停車場 公車站\n捷運需要走一段路\n不過也可以當吃飽後的散散步\n結論:此生不會再去 掰掰~~天使紅蝦不新鮮，不知道冷凍多久了？唯一好吃的只有一開始招待的生食級干貝，桌面沒食物服務生也不會主動詢問要不要加點，導致我吃的很趕，老闆娘非常準時來結帳，感覺有夠差，這種店吃一次就夠了', 4.3, 1297, 5577, 'TPszRvM@gmail.com', '02 2541 1563', 3, '["星期一: 15:00 \\u2013 00:00","星期二: 15:00 \\u2013 00:00","星期三: 15:00 \\u2013 00:00","星期四: 15:00 \\u2013 00:00","星期五: 15:00 \\u2013 00:00","星期六: 11:30 \\u2013 00:00","星期日: 11:30 \\u2013 00:00"]', 'http://www.shabushabu.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.02 上午', '2021-06-08 09.43.02 上午', 1297, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 104, 1, '05900382', '熱炒火鍋殿︱鍋物熱炒烤炸吃到飽專門店', '106', '台北市', '大安區敦化南路一段160巷24-1號', 25.04344920, 121.54757490, '吃到飽', '吃到飽 ?熱炒火鍋殿︱鍋物熱炒烤炸吃到飽專門店\n\n餐點??\n\n火鍋吃到飽 ?饗 $899 (含虎牌啤酒暢飲)\n價格有分｜$599｜$699｜$899｜\n\n$899方案\n湯頭\n｜澳門卜卜鍋｜重慶麻辣烤魚｜韓式番茄泡菜鍋｜\n｜川味麻辣鍋｜日式壽喜燒｜家常骨湯鍋｜台式番茄鍋｜\n肉品\n牛\n｜嚴選牛五花｜嚴選牛瘦肉｜嚴選雪花牛｜嚴選沙朗牛｜\n｜和牛雪花｜美國翼板牛｜特級安格斯背肩牛｜\n｜美國頂級板腱牛｜澳洲8+9和牛沙朗｜美國無骨牛｜\n豬\n｜松阪豬｜嚴選豬培根｜嚴選梅花豬｜極黑黑豚豬｜\n｜伊比利豬｜\n海鮮\n｜小蛤｜白蝦｜草蝦｜鯛魚片｜鱸魚片｜鱸魚肚｜魷魚｜\n｜花枝｜中卷｜三點蟹｜鮑魚片｜帆立貝｜鱈場蟹肉條｜赤嘴鮮貝｜淡菜｜象拔蚌｜肥豬蝦｜戰車龍蝦｜大蛤｜\n｜魷魚｜扇貝｜軟絲｜鮮蚵｜鮑魚｜大干貝｜香蕉蝦｜\n｜天使紅蝦｜巴沙魚捲｜百花油條｜\n蔬菜\n｜水蓮｜茼蒿｜山藥｜香菇｜絲瓜｜南瓜｜芋頭｜玉米｜\n｜木耳｜高麗菜｜青江菜｜娃娃菜｜花椰菜｜金針菇｜\n｜杏包菇｜玉米筍｜大黃瓜｜\n手工餃類\n｜三記魚餃｜三記蝦餃｜三記燕餃｜鮮蝦雲吞｜\n自製滷味\n｜滷牛筋｜滷牛肚｜滷牛腱｜滷三寶｜\n主食\n｜眷村味大塊滷肉飯｜白飯｜王子麵｜烏龍麵｜\n｜北方拉麵｜北方刀削麵｜\n\n??用餐時間2小時 最後40分鐘前加點 需收10%服務費\n為維護出餐品質 每次最多加點十樣 海鮮每次最多加點兩份\n-\n火鍋鍋底是澳門卜卜鍋 整鍋滿滿蛤蜊好鮮哦?\n服務人員先用鍋子爆炒後再倒入高湯～\n而且大火大鍋滾蛤蜊 店員要我們一打開馬上夾起來吃\n這樣蛤蜊的肉質才不會老掉 我然蛤蜊裡面滿滿的湯汁！\n這邊的肉品還不錯吃 不會說吃到飽然後肉都是很硬的那種\n感覺老闆很用心在做 都是好吃的肉片 不會柴\n海鮮也很新鮮 還有戰車龍蝦 很特別呢 整個很多樣話\n飲料也是很充足的 看是要拿瓶裝 還是自己裝都有\n這邊不管是肉還是海鮮還是配料都是非常多樣化的\n是可以讓你選到不知道要選什麼的好吃店家\n整個很推 大推 建議可以朋友家人一起同行??我覺得499的很超值，部分餐點也都頗有水準，算是挺合我意的店\n也因為價格非常便宜，確實有些不好吃的餐點，但並不影響我對它的評價\n這裡也給大家一點點避雷建議...\n\n烤魚下巴：烤的時間不太夠，不夠焦，吃起來土味太重，也非常膩口\n\n涼拌干貝唇：可能是食品保存的方式，我們吃的時候並沒有完全退冰，還可以吃到指甲大小的冰塊，大家可以放一會再吃，感觀比較不會那麼差\n\n避風塘炒蟹：師傅並不擅長這道菜，調味太鹹且並不酥脆\n\n打拋豬：不知道是不是我們是最後一組客人，來的比較晚，整個絞肉乾掉，\n\n其他的或許沒有到中上，但也都有一般家常水準\n\n以499+10%的價位，我非常喜歡，推薦給大家唷，這邊補充一點，推薦的是499價位的喔，其他價位的我沒吃過，未來也不打算嘗試＃這就是一家爛餐廳 ＃熱炒火鍋殿\n#地獄體驗 #連一星都沒有\n\n就是爛餐廳，沒有什麼好說的。\n如果想考驗自己的耐心，或確認自己是否有容人的雅量，可以來試試，絕對會讓你爆炸的。\n\n我訂位是12點半，大概12點10分到，是全店第一桌客人，服務小姐態度相當好，然後非常快速的上了卜卜鍋（湯）跟基本盤（會先出五道菜），之後讓我們點……然後？？……沒有然後，然後我就再沒看到過我的菜了，我吃完基本盤，到我下一道菜出現，中間隔30分鐘。\n\n特別拍照椒鹽龍珠這張單，是因為上面的時間大概是12點半，再次強調我是全店的一桌客人，到2點20分之前，中間已無數次的詢問這道菜的下落，服務員都說廚房在準備了，2點20分的時候，他跟我說這道菜不會出現，他們賣完了……？？？？（世上果然有鬼）\n\n當場覺得自己真的很善良，我一直告訴我自己，就算罵哭服務員也沒有意義，我也沒有到超級生氣，只是一切都很莫名其妙。\n\n這家店漏單非常嚴重，廚房的師傅，好像隨他的心意出菜，最好笑的是，他明明是電腦輸入的電子單，後面廚房依然找不到單子。店員表示，每一次點限點八道菜，結果8樣中如果有2樣來了，已經是奇蹟了。\n\n更有意思的是，當你不斷告知，哪一道菜沒有來之後，服務員也沒有態度不好，他就會把那張單子拿走，跟你說他現在去後面催廚房，然後……然後單子就不見了，明明廚房應該有一張一模一樣的單子啊 ！因為服務員把你的單子拿走，你點過菜的證據消失，死無對證，服務員還表示：廚房說他們已經出完了。#明明就沒有啊\n\n這家店用熱炒來吸引人，但出餐的速度還有掉單的機率，會讓人誤以為去了法國餐廳。\n\n我看Google的介紹，火鍋殿是以不同的方式營運，也許是之前收益不好，所以他更改了營運的方式，但這個更改太讓人絕望了，就倒閉吧！別再害人了。\n\n還有還有，因為我在減肥，最近比較少喝飲料，所以當服務員說，飲料在後面的冰箱，自行取用，我表示只需要水就好了，他就說他們瓶裝水，昨日被客人拿完，新的廠商還沒送來，所以只有飲料。我沒有不高興，只是覺得好誇張喔，就是水，這樣店裡最便宜的東西，他怎麼會沒有呢。\n\n這個湯鍋啊，不能夠再加主食（我點蛤蜊鍋，不能再加蛤蜊），但可以點一些牛肉或是蔬菜，像火鍋一樣，也是從12點半點起，中間無數次告知我們後面正在準備（是從牛開始養嗎），明明就是用火鍋的方式讓大家吃這些肉片，但沒有提供任何醬料，桌上連玫瑰鹽、醬油都沒有。\n\n還是覺得自己運氣不錯，是跟媽媽一起來，所以很多事就算了，如果是跟朋友來這家店，讓我丟了大臉，當場可能不是用飆罵或大鬧可以解決的，很開心老天沒有讓這件事發生，不然我可能就要上新聞了。\n\n#久違的長篇食記\n#只是不想任何人受害\n#我心都在滴血\n#還好是最便宜的價位\n#我後面的人點了最貴的_不知道他的心是怎麼想的考慮很久才想推薦，因為怕以後人多不好訂位，但東區競爭怕他倒，真的可以來吃，是重口味熱潮，499還有罐裝飲料喝到飽，cp值好高這裡的負評真的讓人感同身受，就不一一贅述\n沒留過一星，想一想還是覺得應該做點什麼幫助廣大饕客\n“有意隱瞞部分真相來盈利”是用餐完後的感受，建議打開任一外送app，盲點一家餐廳下訂，絕大機率會有更好的餐飲體驗，祝福用餐愉快', 4, 705, 2820, 'dqpizhwI6@gmail.com', '02 2778 8227', 4, '', 'https://www.facebook.com/%E7%81%AB%E9%8D%8B%E6%AE%BF-2060436040666616/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.03 上午', '2021-06-08 09.43.03 上午', 705, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 105, 1, '16124286', '『 兩餐 』 ?? 韓國年糕火鍋吃到飽-忠孝店', '106', '台北市', '大安區忠孝東路四段98號3F', 25.04133770, 121.54636180, '吃到飽', '吃到飽 太讚了好好吃態度又好好???味道很不錯，服務業很好。推薦年糕好吃\n韓國小點心也好吃\n冰砂好喝CP值很高的吃到飽餐廳 可以吃到3餐好吃 有點怕辣也能接受', 4.1, 3390, 13899, 'V9ZqVSS4mZ@gmail.com', '02 8772 6617', 2, '["星期一: 11:30 \\u2013 19:30","星期二: 11:30 \\u2013 19:30","星期三: 11:30 \\u2013 19:30","星期四: 11:30 \\u2013 19:30","星期五: 11:30 \\u2013 19:30","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', 'https://facebook.com/DookkiZhongXiao/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.04 上午', '2021-06-08 09.43.04 上午', 3390, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 107, 1, '47519649', '好客酒吧燒烤', '106', '台北市', '大安區忠孝東路四段98號11樓', 25.04133050, 121.54629150, '吃到飽', '吃到飽 還不錯吃的火烤兩吃店！地點不錯捷運很方便 風景也好  餐點還可以吧好久沒來東區，跟老朋友約在這吃燒烤，幾年前來過，感覺還不錯，這次來覺得他們真的還是不錯的\n進門的量額溫，跟噴酒精消毒，很有一回事，感覺還不錯，門口的服務人員蠻體貼的我因為趕著來，中午天氣又熱，一量居然溫度太高了，好心的請我在等候區坐一下，約過幾分鐘再量我就正常了，入席了還被老朋友嘲笑一番\n今天吃基本價格598的火烤兩吃，比較喜歡的肉是豬里肌，還有低旨牛，當然牛五花還是我的最愛，海鮮的泰國蝦雖然不大但口感還不錯，覺得蠻新鮮的，蒲燒雕也是我的菜，甜點的日式烤年糕真的推薦，先點三份吃完再點三份，最後冰淇淋是我的最愛酷聖石，大挖3球，大滿足\n今天雖然是平日，但好像有碰到公司聚餐，客人還蠻多的，服務生不多感覺出他們的忙碌，但還是很認真的又是送菜又是換烤網又是收盤每個服務生都是小快?在忙，真是辛苦了，有機會會再來的很棒!爛到流湯 一桌五個大男人 送餐一次一小盤(另加瘋狂漏單) 畫個正字號還過來念畫多少都一樣只有三份!五個人你跟我說一次最多三份是在哈囉?吃到飽寫假的?用餐時間還有4x分鐘就跑來最後加點 是有多想趕人?第一次吃到飽肚子沒多少東西就走出來 難怪整間餐廳只有我們這組客人 尤其是一個刺青瘦瘦的男生 客人欠你錢嗎?服務業那麼痛苦就不要幹  省的你痛苦我痛苦 一顆星是給一個很親切的女服務生服務真的不是很好，尤其是帶位的時候，整體裝潢跟用餐氣氛有種舊舊跟死氣沈沈的感覺，上菜是滿快的啦，數量也是有給足。\n\n飲料不管是可爾必思還是柳橙汁都非常淡，就像是那種飲料太甜加水，卻不小心加過頭的味道。\n\n這次完全沒有點豬肉所以豬肉部分不知道，不過牛肉的部分只建議點有厚度的排類或是骰子牛，肉片類都又鹹又油，不是，是非常油；雞肉的話....恩就是雞肉，雞軟骨還不錯。\n火鍋部分沒有什麼亮點，差不多就是解膩吧，因為第一次送上來的蝦子感覺不是很新鮮，後面就沒有繼續點蝦子了。\n\n+199元的餐點中「好客牛排」烤完就是夜市牛排的味道一模一樣，我覺得應該可以再附上鐵板麵；和牛肉排點了一次，味道還行，不過和牛本身就油不建議多吃。\n\n總之這間味道是非常普通啦，這個價位我完全推薦可以去吃別間。', 4.1, 4374, 17933, 'zJnkjV@gmail.com', '02 8772 8832', 3, '["星期一: 12:00 \\u2013 00:00","星期二: 12:00 \\u2013 00:00","星期三: 12:00 \\u2013 00:00","星期四: 12:00 \\u2013 00:00","星期五: 12:00 \\u2013 00:00","星期六: 12:00 \\u2013 00:00","星期日: 12:00 \\u2013 00:00"]', 'https://my-friends.com.tw/menu1/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.05 上午', '2021-06-08 09.43.05 上午', 4374, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 108, 1, '35152821', '啾哇嘿喲 韓式烤肉專門店 l 大安區 l 東區 l 忠孝復興 l 韓式烤肉 l 韓式燒烤 l 烤肉 l 燒烤l 吃到飽 l 韓式料理', '106', '台北市', '大安區敦化南路一段160巷23號', 25.04368070, 121.54752870, '吃到飽', '吃到飽 以往會估狗餐廳評價，不曉得這次是哪條神經被打到，\n沒事先查好評論就訂位前往用餐，真的是我人生做過最後悔的一件事....\n昨天去餐廳用餐過節日（七夕），進去用餐前服務生也沒事先告知冷器壞掉，這樣的用餐環境我們可以接受嗎？而且今天是“烤肉”餐廳，烤爐溫度在夏天的氣候下會非常~非常的高溫...\n帶位後就直接把菜單遞給我們什麼也不說明，就走了...\n我們只好自己研究菜單跟服務生點餐，點完餐後，因還沒開烤，\n但坐一下就開始覺得室內空氣很悶熱，跟男友說他們冷氣有開跟沒開一樣，該不會是冷氣壞掉了吧?\n想說沒關係，再給他一點機會，看看會不會美味程度可以安撫一下我們的心，但真的是我們太看得起他了...\n我們真的很後悔沒在上菜前先跟人員告知不打算吃了，要直接閃人...後面才是惡夢～\n後來我們選了最貴的價位$699（這個價位不算平價）\n服務生給的加點菜單油噠噠的...觸摸到就覺得不舒服！\n肉質像咬橡皮筋一般...豬肉腥味非常重～\n自助區及環境真的很髒亂很可怕....\n剛開始第一輪組合肉上菜就越吃越不開心！\n吃不到40分就討論等等就買單走人了，\n並不是我們要求很高，他是連?下去的勇氣都沒有....\n畢竟也吃過不少平價吃到飽的餐廳，真的第一次遇到這麼糟糕的餐廳了！\n(話說也是看到某個Youtube"米Ｘ"推薦想說不錯才去體驗看看的，真的很想說那個Youtube真的很不真誠，雖說不是業配，只能說他的口味很特別....＝ ＝||)\n我們並不是點最便宜$399的低價位...是$699 $699 $699耶....\n真的是一家非常糟糕餐廳！真的很心疼當天消費＄1500多...\n寧願去夜市吃美食都還比較美味～\n真的是花錢找罪受！！一坐下位置椅腳就掉到牆角的溝，低頭一看像一條水溝，而且地板油膩不乾淨，拿飲料醬料的牆邊也堆滿食材，凌亂不衛生?\n\n剛開始煮過沒多久，牆壁就有蟑螂在爬，請服務員過來處理，一句不好意思也沒說，用衛生紙抓掉後就若無其事地離開！\n\n從點菜到上菜，沒有介紹過吃法和菜色，醬料、火鍋料、飲料、加湯都是自取，服務員也都沒表情、厭世，明明訂位是五位，餐具也只準備三副！這樣竟然也收服務費，而且用餐時間1.5小時，要烤要煮時間根本不夠！\n\n綜合以上，價位一個人725$！完全cp值低到爆！完全不推！如同前面給一星的客人一樣\n覺得這個價錢不如去吃別間\n\n料理的部分其他人講的很詳細就不多說了\n都大同小異明明吃到飽但火太小烤很久肉又上很慢\n\n服務的部分真的只有一開始帶位介紹器皿跟後續上肉\n醬料飲料自取很常見\n但湯頭火鍋料小菜全部都自助\n不懂服務費是什麼意思???\n\n最讓人無言的是\n提前三週訂位也在期限內付了訂金\n結果當天報到發現根本沒登記訂位\n不只位置沒登記付了訂金也沒註記?\n\n結帳跟店員說訂金是2400後來又被追回說老闆說是2000才對\n跟店員說還是你要調監視器?\n直接低頭說等一下我再問一下\n\n後來才跑來說哦對哦是2400沒錯沒事了\n連道歉也沒有從頭到尾都很無語\n\n消費體驗非常之差^_^因為是吃到飽所以時間很重要可是那個爐子沒火了切換的效率非常低讓我沒有辦法吃的非常划算cp值十分不高牛肉的品質非常的不好吃豬肉比牛肉還要正常一點最好吃的是泡麵跟飲料還有泡菜海鮮煎餅像是沒有味道的蔥油餅如果大家要去的話是建議不要點牛肉我們原本是想要好好慶祝的可是我們選擇這家之後反而吃的不高興所以上面的話是我們綜合一下的留言可能有帶一點個人的偏見但這是我們的真實回饋用餐體驗極差 兩人用餐想吃多樣化的食材\n要求店員上一人份卻被打槍 不知道店家不給一人份的理由是什麼 堅持兩人吃就是給兩人份\n\n食材部分\n帶骨牛肉 不是油 就是筋 烤完基本丟掉大部分都是不能吃的\n豬五花 韌到完全吃不下 烤完吃了一片就不想動了\n不知道是本人我對食材太要求 還是貴店真的沒做好食材管控 我認為可以吃的只有牛肉片與無骨雞肉\n\n最後加點的部分 提醒最後30分鐘要加點 真的就是提醒而已 也不管你要不要加點 也沒有工作人員詢問是否要加點\n最後的部分有位平頭店員說出讓本人我嘆為觀止的話\n\n最後五分鐘提醒 如果用餐時間到了 我們店員會來直接幫你收拾整理\n\n言下之意應該就是 我們時間到了就要收囉！\n我不管你嘴巴有肉 還是正在烤 我們時間到就是要收囉\n\n給貴店建議 食材 畢竟在台北市做平價燒烤不容易 食材品質還是要顧一下 這種都是筋的肉我不如直接去旁邊吃孫*寶\n不過態度方面實在有夠爛 感覺就是時間到了就是要趕人走 五分鐘的那個提醒真的讓人想當場就走人了', 4, 4016, 16064, '8RzibpeIeb@gmail.com', '02 2752 2653', 2, '["星期一: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期六: 11:30 \\u2013 15:00, 17:00 \\u2013 23:30","星期日: 11:30 \\u2013 15:00, 17:00 \\u2013 23:30"]', 'https://www.facebook.com/koreachuwaheiyo/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.06 上午', '2021-06-08 09.43.06 上午', 4016, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 110, 1, '14064078', '台北西華飯店 B-one自助餐廳', '105', '台北市', '松山區民生東路三段111號B1樓', 25.05811120, 121.54579920, '吃到飽', '吃到飽 母親節早鳥訂位可以打9折\n每人送半隻龍蝦，送媽媽一隻紅色康乃馨就下午茶而言，菜色是真的少了些，但是880的飯店下午茶。\n這樣菜色算是可以的了。二樓晚上的服務生\n服務很棒\n尤其是12/9???五星級飯店 地下一樓 buffet 一樓義大利餐廳 二樓怡園中式餐廳 ！飯店大廳華麗 客房舒適 有地下停車場 游泳池和健身房。\n交通便利 臨近中山國中捷運站 位於市中心 是台北市鬧中取靜高級享受的住宿首選非常不推，因為是搭配住宿配套去的，一去了之後因為沒有任何前餐，所以先點了紅酒，結果倒紅酒倒的菜單都是，一句抱歉就走了。\n然後丁骨牛排上了，肋眼部分就是一人一口，剩下的紐約客完全沒有油花，難咬到爆炸，問了服務人員正常嗎？\n居然只回：哦～本來就這樣。\n嗯....一個大飯店的西餐廳水準也就這樣而已\n連丁骨的骨邊肉都要等到我跟他說是不是要幫我處理才處理。\n倒水的人員連問都沒問過都直接加水然後走掉。\n\n反而加點的鴨肝比龍蝦、丁骨牛排好吃多了(BTW骨邊肉比紐約客也好吃Ｎ倍)，那個紐約客，真的跟網路499牛排有得拼。', 3.6, 436, 1570, '2cFQOnAtu@gmail.com', '02 2718 1188', 4, '["星期一: 06:30 \\u2013 10:30","星期二: 06:30 \\u2013 10:30","星期三: 06:30 \\u2013 10:30","星期四: 06:30 \\u2013 10:30","星期五: 06:30 \\u2013 10:30","星期六: 06:30 \\u2013 10:30","星期日: 06:30 \\u2013 10:30"]', 'https://www.sherwood.com.tw/zh-tw', '0.jpg', '[0.jpg]', '2021-06-08 09.43.07 上午', '2021-06-08 09.43.07 上午', 436, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 112, 1, '40445610', '雨林餐廳 - 美麗信花園酒店', '10491', '台北市', '中山區市民大道三段83號', 25.04504110, 121.53722690, '吃到飽', '吃到飽 非常難得給負評的我，冷靜思考後還是決定要給評論，餐廳從原先滿心期待訂的Light Cafe臨時被換到一樓的行政餐廳，沒有事前通知，被告知因為用餐人數不足？行政餐廳燈光非常不足，餐桌椅也不像飯店，東西都很破舊，包含菜單和餐盤。\n\n菜色更令人悲劇，不曉得是不是因為當作防疫旅館的關係，主廚已不在廚房，家人們點了台塑牛排、羊排、炒飯和漢堡，沒有一樣可正常食用。\n\nEZTABLE 下訂前沒有告知花園酒店是防疫旅館就算了，整個用餐環境和菜色都令人難以下嚥，整晚客人只有兩桌，將這家臨時變動訂位餐廳的飯店作為慶祝母親節大餐的地方，我對父母感到非常抱歉。都會遺珠秘境！\n佐滿眼綠意用餐～\n餐點可大可小、手藝了得！\n價值價格匹配！花園造景在都會餐廳是有風味的,然食物生魚片冰老,各肉品的口感就一般般,價與質落差大,沒想再去今年的機構年終餐敘因為辦在這裡因此得以知道這處地點，環境很大而且有明確的區分那些食物在哪取用，因應新冠疫情取餐要戴口罩，但不影響取餐的體驗，熟食好吃，日式料理部分都是現做很新鮮，非常推薦。三明治很棒！但是不愛用餐時，工作人員在旁整理座位，不適合喜歡安靜用餐的人。', 4, 548, 2192, 'L5DY81a@gmail.com', '02 7702 5888', 2, '["星期一: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期二: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期三: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期四: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期五: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期六: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30","星期日: 06:30 \\u2013 10:00, 11:30 \\u2013 14:00, 18:00 \\u2013 21:30"]', 'http://www.miramargarden.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.09 上午', '2021-06-08 09.43.09 上午', 548, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 113, 1, '78770614', '小豬樂石韓式烤肉吃到飽-忠孝店', '105', '台北市', '松山區市民大道四段157號', 25.04485230, 121.55118350, '吃到飽', '吃到飽 上週五去吃，等了40分鐘才上肉，也沒有跟我們說明為何需要等這麼久，只是沒有意義的一直說：抱歉。餓到都想直接結帳走人的時候肉終於來了。\n遺憾的是肉質非常普通，其他配菜也不好吃。這種價格真的可以有更好的選擇，難得的週五約會就踩了雷餐廳，奉勸大家快避開！補充一下，想起來還是很氣，兩個人兩千多，還難吃的要死。\n——————————————\n可笑炸雞，悲慘沾醬。\n店員會出去門口聊天，你沒有真的走進去，他是不會鳥你的，人家才沒有在管你有沒有在看菜單。\n算了啦，反正也不好吃。先不評論好不好吃，我想就算再難吃我可能也不會上來留言給評價，但就這樣的價格，以及標榜頂級套餐跟旗艦店的店家，這樣用餐與服務經驗我實在是完全無法接受\n\n首先，這兩點只是建議，完全不構成負評的原因\n一開始我看完菜單表明我們不吃海鮮有對海鮮過敏，請問服務生是否可以把menu上每一個套餐裡都有的兩種海鮮換掉，服務生再三詢問後回覆我沒有辦法換，都是配好的。這時候我們開始想說是不是如果點單點不要點套餐可以避開海鮮，後來要點餐時服務生再度告知我們規定都是要點套餐喔，單點只提供加點。好吧我也是認了，如果規定就是規定也是沒辦法，只是海鮮過敏跟不吃海鮮的人其實不佔少數，如果不提供讓客人直接單點的服務，是不是可以考慮提供沒有海鮮的套餐可能會比較貼心，畢竟沒有人想浪費食物。\n\n接著，裝著雞湯的保溫壺只要一倒雞湯他的底部就開始滴水，一開始我們以為是有漏，提醒了服務人員，我還跟他說倒的時候要小心不要燙到，服務人員拿回去檢查後再拿回來告知那個應該是洗碗機的水不是雞湯，我聽完也是笑笑。對，是不危險，但我再拿起來倒雞湯他還是在那邊一直滴水，換一個很難嗎？這麼新開的一家店，給客人的保溫瓶一直在滴水，嗯。\n\n再來讓我最火大的一點來了，吃飯吃到一半隔壁桌來了兩位客人，我剛好瞥到他們在點餐的畫面，桌上放著一張我從踏進這間店坐下來點餐一直到吃到一半都沒看過不知道從哪裡出現的菜單，上面寫著超值雙人套餐1380。我當下有點傻眼想說是隱藏版菜單嗎？還是特殊菜單？我拿起手機找到店家fb上面看到的確有這份菜單，接著我招呼了服務人員，請問他說這份菜單是有時間限定的嗎？還是是有限量限時供應？服務人員回覆說沒有，一直都有供應喔。我再度回覆，但我沒有看到這個菜單啊？這時旁邊有一位看起來，貌似是老闆的先生跟著服務生過來也聽到了我的疑問和服務生的回答，他回覆我：這個是新的菜單啦，放在外面，因為是新的所以沒有在你剛剛看到的menu上面。我當下有點驚訝與傻眼的喔了幾聲，服務人員也就離開了。\nHello？你在跟我開玩笑嗎？請問是可以點還是不能點？menu放在外面，所以請問，我是要到外面去點餐嗎？我要怎樣才可以看到放在外面的菜單？還是這是一個隨機的餐點，有在外面運氣好剛好看到的客人恭喜你才可以點，沒有看到的活該倒楣點不到喔！\n這樣的回覆，我完全無法接受，喔尤其還是從一位貌似是老闆的人口中給我的解釋，我實在也是想請問你新開發要給客人點的菜單只放在外面是什麼意思？是否可以解讀成你們不想讓客人點比較便宜的套餐？\n\n我們今天點的套餐是1680，點餐時還在討論我們其實不太餓，這樣份量不知道會不會吃不完，看完1380的菜單後我只能說如果我有看到這張menu，我只會點這樣我們就覺得夠了。吃個飯，各種受騙感襲來\n\n謝謝這間新餐廳讓我吃得一肚子火，這樣價格然後如此的服務品質飲料區的金拮汁很好喝 不會太甜也不酸\n在台北算平價的吃到飽燒烤店\n推薦豬前腿肉 口感較很嫩、還有孜然口味的豬肉\n蔬菜種類較少\n香菇跟杏鮑菇烤起來的口感偏乾\n壽星有九折優惠\n食材都蠻新鮮的\n老闆人很熱情且健談吃了那?多間的韓式燒烤還第一次吃到有點火的韓式燒烤沒幾桌客人服務態度差就算了加個東西來的慢連基本的燒烤換烤網都要自己跟他們說網都黑到起火了來換網還邊起火叫客人自己夾火網上的東西去新的網火網還掉在桌上連聲抱歉都沒有我還第一次遇到這種事的～吃的真的有點火純個人感受', 4.1, 114, 467, 'urMAG11hd@gmail.com', '02 2577 0933', 4, '["星期一: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期二: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期三: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期四: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期五: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期六: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00","星期日: 12:00 \\u2013 15:00, 17:30 \\u2013 22:00"]', 'http://www.piggystone.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.09 上午', '2021-06-08 09.43.09 上午', 114, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 114, 1, '94102605', '大戈壁蒙古烤肉', '104', '台北市', '中山區松江路152號3樓', 25.05429650, 121.53285220, '吃到飽', '吃到飽 雖然菜色沒很高檔，但是可以吃很飽，收費很親民用餐空間寬敞，取用食材，動向分明台北少數平價蒙古烤肉，價格超值，大吃一頓的好地方開在松江路上有名的蒙古燒肉大餐廳。\n烤肉方式也是讓您可以在蒙古烤肉食材區，自選愛吃的肉類跟蔬菜帶過去給廚師燒烤（但這區肉被掃光的頻率最快）\n\n可惜可能因口味問題，吃起來會覺得好像稍偏清淡一些，較不合個人胃口，桌上其他煮食部分，倒是很有誠意的還附有火鍋給您，讓您可以換換方式享受。\n開架式的火鍋用料選材也就像是傳統台式火鍋店一樣，但注意並不會提供太多超高級食材。\n\n近來發現該店也特化成會接待陸客觀光團的用餐餐廳，環境噪音變大且同時段時會面臨東西可能吃不到或是排較久，難免會覺得在此用餐頗為心累小時後在台灣跟國中時在國外吃過類似的，蠻懷念的。以這個299的價位且不用服務費在台北可以吃到蒙古烤肉跟火鍋兩吃真的是絕無僅有。食材方面因為來之前已經有看了不少評價所以心理多少有數，不過以這個價位真的沒什麼好挑的。\n\n我跟家人是吃中午12:30場次的，準時到但是還是要排隊等店家先清場，其實根本不用電話預約因為還是要現場排。大約12:45進場，先依照很多人建議的不要去擠蒙古烤肉，先攻火鍋跟熟食是非常正確的！火鍋料的肉只有兩種就牛肉跟豬肉，其他就基本火鍋料但是補料很迅速所以不會夾不到料。熟食種類不多以炸類為主，所以想吃較新鮮的要自已多注意抓時間看出菜時間。水果種類有四種也還可以但是大家都推的珍珠奶茶我是覺得還好因為茶很熱加了很多冰還是溫溫的。挫冰我個人不愛吃加上配料都是化學類跟極甜所以我一口都沒吃。\n\n最後將近1:20去用了蒙古烤肉。一碗放青菜一碗放肉這樣份量炒出來才會快一碗而且不用擔心料一直掉。肉就有４種分別是豬肉牛肉雞肉還有羊肉。烤肉師父動作很快也不用等很久，不難吃畢竟是自己調味的，但是鍋上有一些焦渣所以多少食材上回沾上一些。\n\n整體感覺還不錯加上收盤的阿姨們都很有效率態度也okay，不過是要求質感或是享受高級食材的千萬不能選這家。比較適合有預算考量跟想吃粗飽的客人！', 3.7, 2727, 10090, '3TomAaa@gmail.com', '02 2581 9151', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/%E5%A4%A7%E6%88%88%E5%A3%81%E8%92%99%E5%8F%A4%E7%83%A4%E8%82%89-112839143771439/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.10 上午', '2021-06-08 09.43.10 上午', 2727, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 115, 1, '76270381', '漢來海港餐廳-敦化店', '106', '台北市', '大安區敦化南路1段246號6樓', 25.04019770, 121.54836980, '吃到飽', '吃到飽 近期再度前往用餐,建議一定要先訂位,連平日中午都客滿,生意好到很誇張@@現在鐵板區有煎牛小排跟大尾的小卷,都很好吃,尤其是牛小排,比某某天堂的好吃很多,而且出的速度很快,基本上不太會吃不到,然後今天的烤鴨也很好吃,皮脆又不會過硬~~~\n\n=========================================================\n10月15日再度用餐座無虛席,全滿....建議要訂位再來用餐;今天最棒的菜色就是蒸鮮蠔~~~這次有加了蔥蒜薑跟一些醬汁去腥,特別的好吃.螃蟹個頭小,鮮度普通沒特別想吃,烤鴨出得很慢,品質也不如以往好吃.炸蝦\n跟炸豬排都好吃,生魚片鮭魚常常是空的,鯛魚卻是滿滿,不免讓人聯想!!現做舒芙雷好吃,剛出爐馬上食用入口即化,但太甜~~~而且製作速度太慢大排長龍~~製作的師傅非常的忙碌仍保持優良的服務態度給予肯定!!如果沒有鮮蠔我只會給3顆星!!!\n\n=======================================================\n9/14來吃個午餐,菜色品質依然維持水準,補菜也補得很快,基本上所有的菜都不用怕吃不到~~之前都沒有拿披薩,這次吃了一小塊,起司非常多,餅皮又脆,很好吃.今天有烤鮮蠔,鮮度有改善,比上次來吃的時候好很多.其實這種等級的BUFFET,你不用期待會有什麼高級食材~~但普通食材用心做也能很好吃的,敦化店來吃了很多次都是幾乎客滿,即使是平日午餐我目測也有九成滿,其實不外乎就是品質穩定~~~然後那個鐵版煎台就弄了一個蔥炒肉的菜而已,豈不大材小用??可以多一些現煎的料理啦!!!甜點蛋糕都很好吃,水準之上\n===========================================================\n敦化店平日晚餐以前沒訂位大概就要排候補了,受到大環境影響,今天大概坐了85%滿,也算很不錯了.....可見平時口碑有做起來.海鮮部分,蒸鮮蠔不好吃,個頭很大,但吃起來粉粉乾乾的,沒甚麼汁,.有一些腥味,可以考慮用台灣的會好吃很多.生魚片大概有7種,只吃鮭魚跟墨魚,鮭魚油花多口感極佳,墨魚軟中帶脆的口感也很棒.螃蟹小隻,鮮度還行.蝦就一般般.蒸鮮魚還行.藥燉排骨喝起來不錯.烤鴨好吃,爐烤牛排蠻軟嫩,還蠻好吃的.熱菜的部分還蠻多的,看起來也都還ok,有一個白醬鮭魚很好吃\n生菜沙拉樣式也非常多,還有現打果汁,果肉都還留著沒有濾掉,這樣比較濃,現煮麵區的紅油炒手好吃,只是他的辣油會麻我不喜歡.甜點部分都不錯吃今天整體吃下來還算滿意,菜色真的非常多,每樣菜都有用心做,不會有那種撐場面濫竽充數的,補菜也很快,雖然沒有甚麼令人驚豔的菜,但這種價位的自助餐我覺得很OK爛掉的服務就像企業體內的癌細胞，儘管他的外表看似強壯，但爛在裡面的這些毒性，終有一天會把這個企業拖垮。\n---------------------------------------------------------\n真的沒必要來這裡用餐受氣。\n漢來海港城敦化店，這家餐廳的服務之爛，堪稱我見過的所有餐廳之首。\n標榜主打的海鮮吃起來冰冰冷冷卻不新鮮，吃到飽的食材卻不多元，服務人員的態度也是冰冰冷冷，跩到高傲的程度，講話用鼻孔看人，彷彿消費者欠他一百萬。這個系統如果可以評負分，我真的很想評到負一百萬分。\n\n諮詢過的入座時間，到場卻被櫃檯批說我們晚到，櫃檯小姐還堅持說他們的員工不可能這樣說，家裡兩個人聽電話同時記下的時間，我當下還寫在紙上，難道是我們跟鬼說話嗎？\n電話就說了要幫家人慶生，說了煩惱不確定要選前一天還是當天，員工也沒告知優惠訊息，網站查不到活動，選擇前一天用餐，結果到場工作人員詢問有沒有當日壽星，才知道當天壽星6折，差了一天什麼優惠都沒有！\n娘娘腔男主管一副我們餐廳那麼有名，我們的優惠大家都知道的嘴臉，還硬說官網有清楚標示，結果你們自己找了半天都找不到，最後才發現是在最新消息的“第六頁”！\n讓人生氣的不是那一點優惠，而是那種不屑和不吃你可以走的態度，對，漢來海港就是有名了不起，反正很多盤子會自己把錢送上門來，根本不擔心少幾個客人！\n\n一家企業有沒有做好服務，是不是真的重視消費者的感受並精益求精，是一個企業能否做得長久的依據。\n我們好聲好氣的詢問，你卻一副我們就是要來找麻煩的樣子，面對消費者的詢問和建議，傲慢和滿不在乎的態度，從小處就可以看的出這家企業根本就不在乎客人，只想著斂財、大賺消費者的錢。\n\n服務人員無能，主管態度高傲惡劣，娘娘腔男主管跩的二五八萬似的，一副拿著雞毛當令箭，狗眼看人低的嘴臉，臉上充滿了對人的不尊重...\n這些我至今回想都感到噁心。\n寧願毀掉消費者聚餐慶生的好心情，毀滅消費者的好感與信任。我真的覺得這家店可以收起來了。\n爛掉的服務就像企業體內的癌細胞，儘管他的外表看似強壯，但爛在裡面的這些毒性，終有一天會把這個企業拖垮。終於趁著員工聚餐的機會，來到之前新聞報導中的高人氣吃到飽的Buffet。\n因為是打著海港餐廳，但是很多人親自來過後，評語並不是那麼優，最主要的原因是海鮮並沒有想像中的“鮮”，您沒有看錯，本人也是這麼認為，它們主打的海鮮真的沒有頂新鮮的，但還不至於會讓您一吃馬上就跑廁所就是了。\n\n我舉我自己的評論您大概就可以知道了；如果您有吃過有一家叫做“爭X”的迴轉壽司，那您一定可以知道那家迴轉壽司標榜的是什麼，就是“鮮”，您就算是覺得迴轉輸送帶上面的握壽司，不夠鮮，您可以要求師傅當場製作您要的餐點；但是海港城這是另外一種吃到飽的概念，食物量的供給，當然就是大量供應，也就是餐廳自助餐臺上的食材讓顧客無限取用到“不要不要的”，所以，當天我拿了海港城的生魚片握壽司品嚐後，既然馬上浮出\n“爭X”  VS. “海港城餐廳”\n結果“爭X”完勝\n\n但必須老實說，海港城這Buffet 的餐點內容的確琳瑯滿目，新鮮現打果汁個人沒打100也有90起跳，甜點也是多樣性，尤其是它主打的 “舒芙蕾”每人一次只能拿一個，因為是現做（這樣您就能懂我上面舉那個“爭X”迴轉壽司的例子了吧？），冰淇淋有著名的\n“哈”字開頭的品牌，及美國一家品牌，因為我只吃“哈”牌，所以，無法將另一家美國品牌做評論。\n\n比較讓人想不到的是，它既然還賣起咖啡豆，這是萬萬讓任何人都想不到的，的確它的咖啡有一定之水準，所以，它才敢自推自賣起餐廳內提供的咖啡豆，喜歡喝咖啡的人，可以參考看看。\n\n因為食材真的琳瑯滿目太多，並沒有每樣東西都吃，但是.......,它的＃烤牛肉 是不及格的，我是不太挑嘴的人，但是挑嘴的家人拿了一盤牛肉後，沒再繼續去拿第二盤，歸咎原因是，它太熟了，根本就是浪費了那麼大面積的整塊牛肉食材，，我也沒拿，或許您會說，人家是“海港城”標榜的是海鮮.......,我到底在評論什麼，但就如上面我說的，它標榜的海鮮都不到“頂鮮”的位置，請問去過一次的您還會再想光臨第二次嗎？\n＃\n除非您是要去“海港城”花NTD.968 午餐/人\n“喝咖啡” 及 “現打果汁”，配甜點  及  “哈”牌冰淇淋\n\n新增一張別的客人的評論截圖，她第四點，點出了我所說的。還有另一位客人評論內容跟我可以說是如出一轍（附上截圖）。\n\n20210128 新聞終於爆出了，有客人在台中店用餐後，上吐下瀉了，這代表?什麼？常吃海鮮的饕客，很清楚的知道，問題出在哪裡了，所以，我一開始就點出了問題，這次台中店出問題，並不意外，因為是同一集團公司的，品管上面就是不夠嚴謹，我這次只是再將新聞報出來的部分補充上來，並沒有降其當初的3顆星評論，因為這樣就已經代表很嚴重了，不用再去調整星等，因為於事無補，不是嗎？當初上新聞是因為什麼，因為它的海鮮新鮮，現在上新聞呢？問題已經凸顯出來很久了，公司管理階層出問題，，這是一定無可厚非的；品管出問題？未必，因為知名度打開後，公司為了謀取更高的毛利率，就選擇成本較低的次級產品，不是嗎？商品可以這樣，可是食材這種東西，是關乎身體健康問題的，甚至嚴重會出人命的啊！第二次來吃，一樣沒訂位，剛開始被帶到會併桌的多人區、但服務員陸續依照來客狀況評估，又給大家分別拆換到小桌分開用餐了，覺得應變不錯，很舒適。\n\n稍微統整一下兩次來吃的品項：\n\n*特色菜品：鵝肝醬、魚子醬。好像很少人拿，但是其實蠻好吃的，鵝肝醬口感很飽滿，香氣濃郁，魚子醬是強勢竄上來的香味，細細脆脆的，每吃一點，都可以回味一會兒，兩次都不由自主的弄了一小角放在盤子裡，搭配其他冷盤間歇著吃。\n\n1. 甜點區：必須說，樣式真的很多種、而且很精緻，中午、晚上來，都有隨時補滿，不會像之前兩次在另一家知名吃到飽，要拿甜品的時候都剩下一點而已。但是缺點還是有的，就是多數都偏甜，吃一盤五六種一口小蛋糕，大概要配掉兩杯不甜的茶或氣泡水。冰淇淋還好，舒芙蕾沒試過。\n\n2. 冷海鮮：去每家都一定要試試的生魚片，個人一直都偏好鮭魚、鮪魚，只有吃這兩種，肉不甜，但沒有怪味，算是沒什麼吸引力。小壽司的牛肉、炙燒鮭魚倒是不錯、握壽司吃了蝦的，不推薦，沒特色。另外有貴妃鮑、白蝦、鰻魚，意外好吃，喜歡的可以來試試。沙拉區有燻鮭魚，大大方方整盤給夾，簡直感人！\n\n3. 熱海鮮：吃了酸菜蝦、蟹、豆豉鮭魚，特別推薦鮭魚，有入味、有油、新鮮、部分骨頭嚼起來脆脆的像軟骨，回夾兩三次！\n\n4. 熱肉類：切片區的豬跟牛都很恰到好處，就是一整片他切給你，即使懶得拿刀子，都能輕鬆咬開的嫩度，醬料也都很出色。另外有吃燉牛肉、咖喱羊、德國豬腳都沒有吃到太硬的，覺得品質管理不錯。\n\n5. 小點：燒賣，沒有特別好吃，但是絕對紮實也不會軟爛。特色是滷肥腸，必夾！\n\n6. 湯品：自沖生牛肉湯，牛肉很好，湯頭普普，溫度維持的很穩定！角落裡的湯也有喝，還好。\n\n7. 飲料：分兩區，甜點旁邊有氣泡水跟調酒；後方沙拉區有調味飲、現打果汁、咖啡、無糖綠茶，果汁區真的很熱門！也是真的用料實在！咖啡豆子用的也還不錯！調酒的角嗨可以加一點氣泡水、放一塊橙片，爽口推薦喔！\n\n8. 點餐：有看到海鮮粥（湯？）、義大利麵、跟春捲（？），個人很冷感沒有嘗試，但是來往的人蠻多都有端，可能以後會試試。\n\n兩次都沒踩雷，還有場內洗手台可以用，廣播提醒也很親切，是最目前喜歡的吧啡餐廳！甜點好棒！\n但不便宜喔~', 4.1, 7756, 31800, '4fle7f@gmail.com', '07 412 8068#1', 3, '["星期一: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期二: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期三: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期四: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期五: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期六: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期日: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30"]', 'https://www.hilai-foods.com/brand-content/1#branch-1', '0.jpg', '[0.jpg]', '2021-06-08 09.43.11 上午', '2021-06-08 09.43.11 上午', 7756, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 116, 1, '96604749', '頂燒', '104', '台北市', '中山區民權東路二段5號', 25.06280590, 121.52843370, '吃到飽', '吃到飽 食材新鮮，服務人員親切，收費不算貴。食物好吃，服務人員很用心服務佳, 出餐快，肉鮮味美，價格公道。酸菜白肉鍋好吃，當季水果甜  布丁甜點好吃不膩口料多實在 東西新鮮', 3.7, 1524, 5639, 'TYXSjqVj@gmail.com', '02 2597 0011', 2, '["星期一: 11:30 \\u2013 03:00","星期二: 11:30 \\u2013 03:00","星期三: 11:30 \\u2013 03:00","星期四: 11:30 \\u2013 03:00","星期五: 11:30 \\u2013 03:00","星期六: 11:30 \\u2013 03:00","星期日: 11:30 \\u2013 03:00"]', 'https://www.facebook.com/TW25970011/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.12 上午', '2021-06-08 09.43.12 上午', 1524, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 117, 1, '41978448', '威布好食 Wb House｜外帶75折｜東區早午餐｜義大利麵餐廳｜台北早午餐推薦｜台北義大利麵｜東區咖啡廳｜聚餐餐廳｜平日不限時咖啡廳｜台北brunch｜寵物友善餐廳｜友善外送', '106', '台北市', '1, No. 15號忠孝東路三段251巷7弄大安區', 25.04304080, 121.54229770, '早午餐', '早午餐 交通：?????\n整潔衛生：?????\n服務態度：?????\n價格：?????\n餐點：?????\n出餐速度：?????\n\n逛sogo逛一逛沒什麼想吃的\n突然想到附近有一家早午餐店\n所以就先打電話詢問了有沒有位子\n有位子就go~\n\n電話說只有吧台的位子\n不過到店後有人離開就立馬換到一般座位\n椅子底下都有籃子可以置物\n看好以後就可以去櫃檯點餐了\n\n餐具還有水是自助式的自己拿\n還有兒童餐具\n\n這次點了一個威布小食光 飲料紅茶\n沙拉換成了湯\n裡面有 德式香腸 雞腿肉 培根 法式吐司 炒蛋 薯角\n不得不說這薯角真是太好吃了\n炸的酥酥脆脆\n玉米濃湯也不錯 放到冷掉了喝也好喝\n不過希望以後法式吐司有其他選項可以替換\n\n總而言之是一家很不錯的店～\n有機會還會再去當天用餐有點了三杯冰紅茶，有兩杯的杯子都沒有洗乾淨，一杯上面有口紅印、一杯上面有乾掉的菜渣，跟店員反應後，一名男店員竟然反過來跟我們說這是外層髒而已要不要擦乾淨就好，聽到當下超傻眼，正常來說應該直接說幫客人換一杯新的吧，你外面都沒洗淨了我怎麼知道裡面是不是也沒洗乾淨？而且也完全沒有說不好意思之類的。\n但一位中長髮的女店員態度就好很多，還有招待我們一碗湯。\n員工訓練真的有待加強，不過餐點都很好吃，希望下次再來會有更好的用餐體驗。外送訂購一個個墨魚海鮮蛋捲，送來一個一般蛋捲...店家說太忙墨魚用完，做之前不能先跟消費者反應嗎？就是強迫消費者買單？還說料有給比較多...實際吃起來只有三個花枝角一隻蝦....本來料是有多少...以這個價格來說非常值得推薦 每一項食物都非常有水準\n外送溫度不太夠以及水蒸氣讓麵包和薯條有點太濕有點小可惜疫情期間外帶自取75折很划算\n拿回公司食物還是燙的 很棒\n番茄紅蝦雞肉義大利麵蠻濃厚\n清炒鮮蔬麵較清淡但也很入味\n漢堡排法式吐司也不錯\n\n現打香蕉奇異果汁也很好喝\n推薦', 4.4, 686, 3018, 'mNgvL9blC@gmail.com', '02 8773 5311', 4, '["星期一: 11:00 \\u2013 19:30","星期二: 11:00 \\u2013 19:30","星期三: 11:00 \\u2013 19:30","星期四: 11:00 \\u2013 19:30","星期五: 11:00 \\u2013 19:30","星期六: 10:00 \\u2013 19:00","星期日: 10:00 \\u2013 19:00"]', 'https://lihi.tv/YQ5FK/GOOGLE', '0.jpg', '[0.jpg]', '2021-06-08 09.43.25 上午', '2021-06-08 09.43.25 上午', 686, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 118, 1, '28039559', 'BRUN不然', '106', '台北市', '大安區安和路一段7號', 25.03975900, 121.54974300, '早午餐', '早午餐 一份早午餐300多加一杯飲料人均4～500 大安區算合理基本價位，但食材上可以更有巧思一些。餐點部分姑且不評。關於服務這塊，一個個子矮矮的中分短髮女服務員，服務態度需要被教育。我們兩位用餐，接到入座通知。到餐廳，外面完全沒有人在等位。中分短髮女服務員說：你們只能坐吧台。\n望眼過去大約有4～5空桌。有些四人桌中間架起透明隔板。我們指向其中一張靠門口的四人桌說：我們可以坐哪裡嗎？\n她說：那邊會有6人客人，如果你們不坐吧台，那你們要繼續等位。\n另一個長髮女服務員在她耳邊跟她說：沒關係啦，給他們坐啦。\n我們就順利的坐上靠門 “早已架上透明隔板的四人桌的兩人位”。入座到用餐結束的1個半小時，根本沒有所謂的6人客人進來，離場時很多桌子也還是空著。( PS.周末只接受4:30後的訂位喔。)\n因為靠近門口，也目睹了一些有趣的服務態度。一位阿姨要離開，然後誤以為門旁邊的手部清潔器是電動門的按鈕，一直按著等門開，中分短髮女服務員(again)站在大約5步的距離說：拉！用拉的。拉！\n阿姨很不好意思說：不好意思，不好意思，我以為是電動門。\n中分短髮女服務員回：那你剛進來，難道門不是用推的嗎？\n\n??我只能說，恭喜請到好員工??BRUN不然-台北敦化\n?106台北市大安區安和路一段7號\n?二訪會再回訪(二訪印象較好)\n?無飲料低消.低消餐點或是飲料皆可\n??桌面空間較小.價位上來說份量有點少\n\n?小酒館炸雞?NT.250\n超級好!吃!\n差點就要請再給我一盤謝謝\n連炸雞皮都好吃\n菜單上是寫鑰匙行的再版\n感覺要來去吃吃看鑰匙行了喔哈哈\n\n?法式炒蛋2.0?NT.340\n炒蛋很好吃就算是冷掉也不會吃起來怕怕的\n配上可頌很搭但是可頌份量有點少\n馬鈴薯個人很喜歡\n但是同行友人覺得有點太鹹\n鮭魚的口感還行沒有什麼腥味\n\n?番茄香料啤酒燉豬肉米飯?NT.300\n燉豬肉不是很嫩的豬肉比較有嚼勁\n飯是用藜麥飯口感QQ的\n醬料部分也還行不到太鹹一定要配飯的那種環境65分：真的是蠻吵的，如果是想要作點事情，或者想要找地方休息放鬆，不太建議來這裡，比較適合三五朋友大聲開槓的地方，跟一般咖啡店相比，是相對吵雜的混亂感，可能也是店家想要形塑的氛圍，店員看起來也忙不太過來。\n\n餐點80分：點早午餐的蛋捲+奶茶，算不錯吃，不過確實如其他評論所說，口味偏鹹，需要一直喝水，飲料則是蠻甜的，所以只喝一半，以為會跟其他餐廳一樣預設無糖，但來不及問店員是不是可以無糖，因為店員蠻忙的。\n\n價位75分：可能是地段的關係，所以偏貴，但基本上還可以接受，只是我可能期待想要找地方放鬆，沒想到一坐進來就很想離開@@\n\n總消費：451元(含一成服務費)相信不用多做介紹各位美女帥哥就知道「BRUN」，Greta也是收藏了千百年才到訪。平日提供訂位服務，假日只能預訂16:30之後的位子。BRUN的菜單有更換過，迷倒一堆少女的「海膽醬干貝燉飯」已經沒有販售了，哭！Greta和朋友都是衝著它來了阿～\n\n澳洲炒蛋早午餐拼盤有炒蛋、培根、香腸、番茄、酵母麵包、馬鈴薯泥。培根超級肥，99%都是肥肉，最後就是留盤的命運。 馬鈴薯泥有濃濃的奶味，依然是吃了幾口就不碰了。其中最惹人愛的是酵母麵包，搭配微鹹香的奶油好討喜，最不起眼卻是最喜歡的。\n\n朋友點的「煙燻鴨巴薩米克醋炒義大利細圓麵」有夠醋，醋醋到位，口口不漏。吃起來醬料偏奶，難怪朋友吃了半盤就說膩了。\n\n雖然餐點表現不如預期，但好險咖啡有返回一成，不論是原味拿鐵還是黑糖拿鐵，都能嚐到咖啡的香醇，不會被牛奶或黑糖蓋過，是好喝的咖啡。個人覺得CP值沒有很高，餐點份量小、美味度普、價格高，到訪一次就好。不然早午餐，很好吃，用心、簡單的設計。真的好！', 4.1, 1178, 4830, 'FTSiLvZS@gmail.com', '02 2772 5222', 2, '["星期一: 08:30 \\u2013 17:30","星期二: 08:30 \\u2013 22:00","星期三: 08:30 \\u2013 22:00","星期四: 08:30 \\u2013 22:00","星期五: 08:30 \\u2013 22:00","星期六: 08:30 \\u2013 22:00","星期日: 08:30 \\u2013 22:00"]', 'https://www.instagram.com/brun.taipei/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.26 上午', '2021-06-08 09.43.26 上午', 1178, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 119, 1, '48801202', '鹿境早午餐 Arrival Brunch & Cafe', '105', '台北市', '松山區南京東路四段133巷4弄26號', 25.05230440, 121.55415440, '早午餐', '早午餐 店內位置不多建議前往前先訂位，店員會到座位幫你點餐，餐具.檸檬水自取！餐點可以直接選組合好的早午大餐盤，也可以選自己任選搭成鹿境小餐盤！限量的法式吐司不一定每次去都吃的到，餐點附的飲料可以加價換單點飲品！餐點的 1.祖傳燉肉漢堡有大量燉煮過的番茄塊醬.紫洋蔥絲.生菜葉.口感軟嫩不油的控肉.蓬鬆的漢堡蓋皮組成！  2.法式吐司大餐盤（費城牛肉）有外表吸附了蛋汁背面像布丁口感微甜的厚吐司.大量的綜合生菜.吃的到塊狀的地瓜泥.蘋果跟芭樂與小番茄的水果.肉質偏瘦又厚實的漢堡排.醃製細嫩的牛肉片！  3.鹿境經典大餐盤有鬆軟的五穀雜糧麵包.厚實飽滿咬下去充滿水份的義式烘蛋. 咬下去會噴汁的墨魚香腸跟帶骨香腸. 偏瘦不油軟嫩的厚培根. 酥脆的炸薯條.大量的綜合沙拉.蘋果跟芭樂與小番茄的水果！      飲料的美式咖啡偏苦不澀，鹿境經典奶茶有溫順的奶味帶一點麥香紅茶的香氣，Mimosa香檳有柳橙冰沙的口感帶一點酒精味！餐點的蔬果都很新鮮，沙拉也都用六種以上搭配而成，沙拉醬是胡麻醬為主！水果生菜盤$120??\nCP值很不高！看到沙拉盤的料\n真心推薦還是吃大拼盤?\n\n原味牛肉漢堡$200???\n不能說不好吃！\n但缺少些驚艷的感覺\n很平淡又簡單的漢堡?\n應該要點椒麻雞腿堡?\n\n椒麻雞腿排漢堡套餐$290?????\n同行友人點的！她們是第二次來\n很令人驚艷的漢堡?\n醬汁很到味！吃二個都不嫌多\n\n素食大拼盤$280?????\n同行友人點的！\n蔬菜份量感覺跟$120的差不多\n真是後悔?應該吃大餐盤的！\n大餐盤的炒蛋都很用心！\n有杏包菇還有香料菜拌炒的味道\n\n結論：建議看一下評論照片後再點餐\n這家店很好吃！建議要做功課才不會失望！\n我就是一個失望難過看著別人餐點的例子從餐點到飲品都可以感受到店家的用心，食材新鮮不馬虎，選擇很多樣！\n最令人印象深刻的是一百分的服務，不是制式化的服務，而是非常貼心的為每位客人的需求考量，讓用餐心情與氣氛都很棒！\n當日是與部門聚餐，主管與同事都非常喜歡。\n很推薦！在小巨蛋附近的巷弄內，店面不大座位不多，建議要打電話來訂位喔！\n\n今天剛好法式吐司賣完了，所以我們點了椒麻卡啦雞腿大餐盤、費城牛肉手工漢堡排大餐盤跟二份鹿境小餐盤。\n\n小餐盤可以自己選擇，原本以為量會很少，但不會耶！好多喔！\n\n大餐盤的生菜沙拉、烘蛋都好好吃，椒麻卡啦雞，肉質很juicy 好讚?推推推\n\n卡啦雞腿有四種醬可以選，雖然不愛香菜，但店家用香菜做成的醬搭上雞腿排好讚?\n\n小餐盤有15樣可以選，但我們到的時候有一些都賣完了，泡菜肉片、德式香腸、麵包+蛋沙拉?\n\n整體?鹿境推推推應該是近期吃到最滿意的早午餐，價格合理、份量十足！吃完只有讚嘆老闆的用心經營！用餐環境舒適，拼盤選擇多，每一樣都好吃，沒有雷！', 4.7, 905, 4254, '3atMLYAh@gmail.com', '02 2717 0007', 2, '["星期一: 休息","星期二: 08:00 \\u2013 16:00","星期三: 08:00 \\u2013 16:00","星期四: 08:00 \\u2013 16:00","星期五: 08:00 \\u2013 16:00","星期六: 08:00 \\u2013 16:00","星期日: 08:00 \\u2013 16:00"]', 'https://www.facebook.com/%E9%B9%BF%E5%A2%83-Brunch-Cafe-258614234701994/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.27 上午', '2021-06-08 09.43.27 上午', 905, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 120, 1, '80321449', '歐浮找餐-松江店-中山平價早午餐|人氣早餐|必吃早餐|熱門美食|松江南京平價早午餐|必吃早午餐', '104', '台北市', '中山區松江路132巷22-2號', 25.05287570, 121.53093580, '早午餐', '早午餐 全麥蛋餅是好吃的（不過喜歡濕潤麵皮的可能不適合），韓式歐巴麵就比較不到韓式的水準....但是鐵板麵還不錯。另外打拋豬飯也算好吃整體食物頗有水準的早午餐店\n薯餅蛋餅(酥皮千層)酥酥脆脆\n法式吐司同行友人超推薦\n蘿蔔糕外酥內軟也是我喜歡的口感\n鱈魚排很棒但是漢堡皮不是我的愛\n不過＋80套餐好像不是很划算\n建議大家單點就好食物   ???????\nCP值 ???????\n裝潢   ????????\n環境   ?????????\n服務   ????????\n\n?食物普通，\n點了一個西西里雞腿法法拉套餐+薯餅蛋餅(酥皮)\n*西西里雞腿\n完全吃不到味道只有一點番茄味+糖\n整個堡被美乃滋味道蓋過，讓人覺得廉價\n*酥皮薯餅蛋餅\n酥皮整個乾扁沒有酥皮口感\n還多了一點油蒿味\n\n?環境+裝潢\nCP值可能是google map在這裡最多的評價\n但老實說，只能說他位置選得好，\n鄰近公園整體非常舒適\n空間乾淨寬敞，\n早餐店做的像是咖啡廳的舒服感\n\n?服務\n我就只有點餐，服務生端過來\n一切正常運作，無特別感受\n也沒有任何互動，如此\n\n如果找一個可以跟朋友小聚的地方\n可以選這裡，相信會是不錯的體驗\n如果一個人獨自用餐如我\n餐點注重的客人，可以考慮一下\n\n另外我看他桌都有點鐵板麵\n或許你們可以試試看\n\n最後，提一下這是連鎖店唷～環境餐點都很棒， 但廚房油煙會飄過來， 不喜歡 。（用心防疫）炸物好油！！\n青醬雞腿義大利麵有我最不喜歡的孜然味\n然後桌面都好髒好黏 吃完才知道原來是連鎖的\n那旁邊摩斯還比較好吃', 4.2, 120, 504, 'vUqD9zjadU@gmail.com', '02 2522 1298', 4, '["星期一: 06:30 \\u2013 15:00","星期二: 06:30 \\u2013 15:00","星期三: 06:30 \\u2013 15:00","星期四: 06:30 \\u2013 15:00","星期五: 06:30 \\u2013 15:00","星期六: 07:30 \\u2013 16:00","星期日: 07:30 \\u2013 16:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.28 上午', '2021-06-08 09.43.28 上午', 120, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 121, 1, '52369164', 'MorningGo Brunch | 小巨蛋站早午餐 | 松山區必吃早午餐 | 寵物友善餐廳 | 民生社區早午餐 | 南京敦化路口', '105', '台北市', '松山區南京東路四段53巷3弄5號', 25.05228830, 121.55079570, '早午餐', '早午餐 店員大推的粉漿蛋餅跟法國薯條真是好吃到爆！蛋餅的口感很特別，跟河粉蛋餅不太一樣，會想再加點其他口味，可惜胃已塞不下。薯條也是必點，不用番茄醬就很好吃！看照片就知道手工蜜糖芋泥吐司裡面的芋泥都滿出來了，還吃的到整塊的芋頭，店家給料給得很大方！因為是減糖，所以甜而不膩，滿足口腹之慾的同時還照顧到健康。只是忘了請店員晚點做，不然熱熱地吃一定更美味～有機會要在二訪三訪N次訪！用餐時間只60分鐘真的有點太短，點了豆漿紅茶跟芋泥紅茶都蠻不好喝的～\n餐點我跟朋友都覺得還ok!春川辣雞粉漿蛋餅+熔岩起司，蛋餅皮很特別，很有嚼勁，和一般早餐店蛋餅皮有蠻大差異，推薦嚐鮮!!\n口味搭配起來很順口，份量以早餐來說蠻飽足的～\n老闆和老闆娘說店裡都是真材實料，重點還有療癒店犬陪吃XD這次是第一次去，我吃的是「溏心miso燒肉堡」，這麵包體本身很香，經過烘烤後，表面白芝麻由外而內色澤漸深，麵包香則因此和著香濃的白芝麻香！燒肉也是現炒的樣子，調味很濃厚帶有甜香，肉質軟嫩不老，麵包體夾著生菜，吃起來沒有負擔，很好吃；）上面放的溏心蛋非常好吃，不過嘴巴不夠大不便就口，只好把溏心蛋倒放讓蛋液沾在上方，蛋白部分則分開配著吃 :P\n\n另外我也點了杯熱錫蘭紅茶，現泡讓茶葉香自然濃郁，甜味也很適當，建議剛上桌可趕快拍張照，茶表面會有白白熱氣，做對位置拍整杯可以有店面的霓虹燈倒影，我覺得好看XDD\n\n另外，我的同行則點了「費城牛肉堡」，牛肉本身很香，但吃的時候要小心會有濃郁湯汁爆滴唷！建議吃的時候要平拿不要傾斜XD\n\n整體服務品質也很好，店員會做詳細介紹，巷弄店面安靜、乾淨，適合早餐或早午餐時段去 chill 一下！吃飽再去 ikea 晃晃~服務太度極度的差\n\n餐點速度慢的離譜\n味道死鹹\n在也不會光顧\n花錢要受氣\n就是這家沒錯\n開心的一早就這麼毀了', 3.9, 260, 1014, '8zQlRyfH@gmail.com', '0908 975 158', 4, '["星期一: 07:30 \\u2013 15:00","星期二: 07:30 \\u2013 15:00","星期三: 休息","星期四: 07:30 \\u2013 15:00","星期五: 07:30 \\u2013 15:00","星期六: 08:00 \\u2013 15:30","星期日: 08:00 \\u2013 15:30"]', 'https://www.facebook.com/morninggobrunch/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.29 上午', '2021-06-08 09.43.29 上午', 260, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 122, 1, '53903586', 'At.First早寓/大安美食&早午餐&餐廳', '106', '台北市', '大安區延吉街70巷5弄6號', 25.04235900, 121.55362600, '早午餐', '早午餐 改裝前來過非常滿意，料理好吃價錢合理，但重新開幕後的二訪實在令人失望\n1.問個問題店員一副我在找他們麻煩的臉色跟口氣，極度不耐煩\n2.送錯餐連句抱歉都沒有，頭也不回的離開\n3.這是最令人不爽的，規定是「尖峰」時間90分鐘，但當時店裡還有半數以上的空位，卻堅持要我們離開\n\n雖說不收服務費，可是最基本的人與人之間的應對態度應該要有吧？時間一到就趕人，毫無溫度，且店內餐點也完全不便宜，跟朋友們花了不少錢在這，卻得到超級糟糕的體驗，以後不會再訪了。（看了一下評價很多都是洗出來的）東西好吃?\n但我第一次了解到，所謂不收服務費的服務了\n原來不用介紹餐點，原來什麼規定都打在菜單上就好了，原來來這家餐廳要先做好功課，原來不收服務費都這麼冷漠，可以不用歡迎客人也不需再度光臨的！啊～謝謝妳們沒說：謝謝您歡迎再度光臨！因為我不會再去了！\n三顆星給廚房辛苦了，餐廳可能只能靠你們繼續了，櫃台員只有負兩顆星！優點:\n1. 店內環境漂亮\n2. 廁所乾淨\n3. 食物賣相好\n4. 沒服務費\n\n缺點:\n1. 沒很好吃\n2. 服務態度冷淡\n3. 以這樣的價位用料算少\n\n結論:\n喜歡拍照的年輕女生應該會喜歡\n想吃美食或重視cp值的人就不適合很普，裝潢也很普\n不知為何居然有4.6顆星\n點了古巴三明治就像鐵板炒肉\n就是普通到不行的早餐店，份量稍多而已\n帶外縣市朋友來吃覺得丟臉老法骰子?牛?班尼迪克蛋??我最愛的抖抖～水波蛋一定要戳??蛋黃淋在骰子牛上面?\n配上滿滿生菜番茄??外面是潛艇堡一口咬只下去只能5顆?\n這裡的環境超好超美超好拍??', 4.6, 243, 1118, '2Ou8oM6drl@gmail.com', '02 8772 6867', 4, '["星期一: 10:00 \\u2013 20:00","星期二: 10:00 \\u2013 20:00","星期三: 10:00 \\u2013 20:00","星期四: 10:00 \\u2013 20:00","星期五: 10:00 \\u2013 20:00","星期六: 09:00 \\u2013 20:00","星期日: 09:00 \\u2013 20:00"]', 'http://www.atfirst0306.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.29 上午', '2021-06-08 09.43.29 上午', 243, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 123, 1, '92484336', 'WOW早午餐輕食', '105', '台北市', '松山區八德路三段199巷12號', 25.04912500, 121.55622220, '早午餐', '早午餐 店面舊舊小小的 ～但 ～真的很開心有緣能遇到這家店！！\n\n這家的賣點應該是老闆吧？！?老闆真的太親切了，幾乎把每個客人當家人在照顧，小至客製化餐點，大至人生規劃？?\n\n餐點CP值還不錯唷～重點是非常用心！！其實廚師有沒有用心在做餐點，這點我還是吃的出來的～今天選招牌總匯套餐（法式吐司＋炒蛋＋薯條＋香腸） 每個都不踩雷！！還有最後加點的美式咖啡，是我最愛的咖啡的幸福味道??\n\n令人有點意外的是這家似乎是隱藏的寵物餐廳？！ 有不少人帶狗狗一起來用餐！我是不介意～但如果有人介意就要注意了～～\n\n唯一沒辦法給五星的點是。。。餐廳內部問得到油煙味  這一點有點小小受不了 ～但其他真的大推！！東西很好吃???上班唯一選擇就是wow早午餐?\n他們的蔬菜蛋餅竟然是包菠菜跟杏鮑菇！\n而不是生菜沙拉，好吃死了！\n附的是一隻小竹叉，感覺就沒筷子那麼浪費\n鮮奶茶也好喝已經不是第一次了?這蛋餅，我真的吃不下去。\n\n回覆店家：食物不要燒焦不是基本的嗎，為什麼變成要提醒？之前你們也是做燒焦的蛋餅，因為有空所以可以去和你們換，今天人已經到達了並不位於你們附近的地方，打開餐盒，準備吃早餐，一看又燒焦成這樣。\n難道一切應怪我？被早餐店耽誤的義大利麵師傅 （義大利麵 奶油好吃\n\n蒜味也好吃）\n\n美式鬆餅也很棒\n\n站在煎台的人員 真的是一家早餐店的靈魂\n\n實在太不想評論 怕評論了 越來 越多人來吃\n\n下次來就沒位子坐了 ?但還是希望可以生意好到擴店\n\n服務人員態度都很親切 讓人吃的開心 回家也開心', 4.4, 225, 990, 'D3L9WSR@gmail.com', '02 2577 5340', 1, '["星期一: 休息","星期二: 07:00 \\u2013 14:00","星期三: 07:00 \\u2013 14:00","星期四: 07:00 \\u2013 14:00","星期五: 07:00 \\u2013 14:00","星期六: 07:00 \\u2013 14:00","星期日: 07:00 \\u2013 14:00"]', 'https://www.facebook.com/WOWbrunch/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.30 上午', '2021-06-08 09.43.30 上午', 225, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 124, 1, '31151814', '總柴早午餐', '106', '台北市', '大安區市民大道三段128巷6號', 25.04452230, 121.53464170, '早午餐', '早午餐 估估雞偏鹹，想吃飽的人可以選吐司 但薄餅比較有滋味\n玉米布丁酥 就是軟的可樂餅啦！\n紅茶好喝~是不甜的那種氣氛不錯北科附近的平價早午餐店，餐點跟飲料份量都蠻大的。店內還有一隻可愛的黑柴。在地好店家，好吃，老闆娘人也很好，只可惜都沒機會遇到兩隻吉祥物土司太厚且料不多，吃起來很乾，用餐環境蠻小的，空調似乎沒開導致有點悶熱，', 4.5, 165, 743, 'O5GGs2g@gmail.com', '0926 097 593', 4, '["星期一: 09:00 \\u2013 15:00","星期二: 09:00 \\u2013 15:00","星期三: 09:00 \\u2013 15:00","星期四: 09:00 \\u2013 15:00","星期五: 09:00 \\u2013 15:00","星期六: 09:00 \\u2013 15:00","星期日: 09:00 \\u2013 15:00"]', 'https://www.facebook.com/ShibaRL/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.31 上午', '2021-06-08 09.43.31 上午', 165, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 125, 1, '00156652', '馬李王早午餐（寵物友善）', '104', '台北市', '中山區中原街34號', 25.05730820, 121.52877380, '早午餐', '早午餐 連續兩天的早餐都是訂購貴店，第一天訂購的商品有雞肉捲跟招牌炸雞以及骰子牛義大利麵，真的好吃！但第二天有點失望?訂購的野菇清炒義大利麵外帶的麵蓋裂掉了導致本來要求的醬汁多變乾麵，迷你起司條感覺沒有炸透，還吃得到有點粉粉的口感，希望能多多留意，防疫期間，你們辛苦了！小小的店家，早午餐的份量十足，好吃，價格也很平價實在。\n\n超級寵物友善，除了贈送來店的狗狗無鹽蛋各一份之外，寵物可以坐在客椅，店家也都有注意環境衛生。\n\n我消費的當天帶著2隻毛孩，坐在推車有網蓋，訂位有先告知店家相關資訊，所以特別安排在比較寬敞的座位，很特別的是除了贈送狗狗餐點之外，店家還特別招呼毛孩，超親切的。\n\n目前我們只要是有毛孩同行的朋友聚會都優先考慮到馬李王，值得一提的是，店家做給毛孩的雞肉球實在太高分了！真的不是隨便捏的，超有彈性又紮實！實在又專業！人寵都喜歡，大推???挑平日午休再訪，附贈薯條好吃。有先打電話訂餐，到店不久就能直接開動就是爽！餐點、服務、整潔、氣氛都讓人滿意的早午餐餐廳。今日湯是南瓜濃湯 真心好好喝！\n身為南瓜濃湯狂熱份子的我認可！\n不會太水 又很有味道 還加入了紅蘿蔔跟玉米\n\n清炒骰子牛義大利麵份量不小\n麵體很棒 味道不錯～\ncp值很高！\n\n麻油雞燉飯很驚艷\n把原本印象中厚重的麻油雞\n變成很清爽入口的燉飯\n其中枸杞和高麗菜吃起來會甜甜的 很棒\n雞腿肉也煎的好嫩好香！\n不過還是要原本就喜歡麻油雞的人適合點\n你會很愛！\n如果原本就不愛這味\n這盤應該也不會讓你突然喜歡上麻油雞?\n\n海鹽奶蓋紅茶 好喝\n炸餛飩 內餡是酸甜的奶油起司味 很特別\n口感跟奶油餅內餡蠻像的\n\n下次再來嘗試早餐拼盤與漢堡！\n老闆娘服務很好～～原本怕捲餅份量太少，結果根本吃不完?而且以早午餐來說真的很便宜，有很多想吃的餐點，會再來吃的?', 4.6, 261, 1201, 'bXUtqG5qh@gmail.com', '0908 517 686', 2, '["星期一: 10:30 \\u2013 21:00","星期二: 10:30 \\u2013 21:00","星期三: 10:30 \\u2013 21:00","星期四: 10:30 \\u2013 21:00","星期五: 10:30 \\u2013 21:00","星期六: 10:30 \\u2013 21:00","星期日: 10:30 \\u2013 21:00"]', 'https://www.facebook.com/%E9%A6%AC%E6%9D%8E%E7%8E%8B%E6%97%A9%E5%8D%88%E9%A4%90-286645495152866/?ref=bookmarks', '0.jpg', '[0.jpg]', '2021-06-08 09.43.31 上午', '2021-06-08 09.43.31 上午', 261, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 126, 1, '36478986', '福迪兄弟Foodie Bro Brunch', '10468', '台北市', '中山區吉林路315巷40號1樓', 25.06343570, 121.53156000, '早午餐', '早午餐 Uber訂雙魚起司三明治及美式炒蛋，最後只來了炒蛋，還是司機送達後才通知...\n另外200元真的只有一個漢堡，沒有其他配餐喔！13:12分點餐完 大約過了半小時才來說有東西沒了要我換 再過了半小時 14:05餐點才來\n後面的客人都吃完一組 新客人餐點都上了 我的還沒上 一句不好意思也說不出口\n蒸的很蚌餐點好吃，餐廳裝潢明亮。點了大早餐，份量足夠，可以吃得飽。不過可惜室內會有油煙味，這點目前比較可惜。雞肉很好吃！！！！！！推推\n薯條還可以 份量很多～～～\n奶茶好喝\n\n扣分因為油煙超重QQ吃完全身都是煙味～\n價格250左右東西真的好吃，很想再二訪(可能等疫情過了…)，可惜目睹了做菜的兩位師父沒戴口罩還交談…吧檯在打包食物也跟沒戴口罩的朋友聊天，重點是那個櫃檯真的很弱…明明當下就沒人，兩道菜還可以少給一個沙拉…有沒有心工作…餐廳不是本應把健康衛生擺第一，何況是開放廚房…', 4.3, 486, 2090, 'E4Fd20nD5u@gmail.com', '02 2593 6673', 2, '["星期一: 09:00 \\u2013 16:00","星期二: 09:00 \\u2013 16:00","星期三: 09:00 \\u2013 16:00","星期四: 09:00 \\u2013 16:00","星期五: 09:00 \\u2013 16:00","星期六: 09:00 \\u2013 20:00","星期日: 09:00 \\u2013 20:00"]', 'https://foodie-bro.business.site/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.32 上午', '2021-06-08 09.43.32 上午', 486, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 127, 1, '22507802', 'ZADON-松山區必吃早餐|特色早餐|平價早午餐|特色早午餐|人氣早午餐|在地推薦早餐店', '105', '台北市', '松山區敦化北路222巷54號', 25.06010700, 121.54690500, '早午餐', '早午餐 XO醬蘿蔔糕蠻辣的，但真材實料吃得到小魚干，蘿蔔糕是軟爛的那種口感！輕食的雞肉煎得很香，沙拉普通，整體乾淨明亮，在這區算還不錯。平價，好吃的早餐店。\n推薦蛋餅、蘿蔔糕、蔥餅等煎台製作的早餐?\n紅茶也很好喝唷，但有時比較忙品質會略有浮動。店內客人也沒到很多但態度不好\n內用飲料給客人用滑的\n女店員撞到人也裝作沒事\n\n每道餐點趁過年還都漲價5元\n要不是過年期間附近只有這家有開不會來吃很多元又好吃的早餐點，店員很熱情態度非常好，重點是自製辣椒超好吃。服務很好，店略小空間不足，建議外帶。', 4, 129, 516, 'Aguf3U9tL@gmail.com', '02 2717 0509', 4, '["星期一: 06:30 \\u2013 13:00","星期二: 06:30 \\u2013 13:00","星期三: 06:30 \\u2013 13:00","星期四: 06:30 \\u2013 13:00","星期五: 06:30 \\u2013 13:00","星期六: 06:30 \\u2013 13:00","星期日: 06:30 \\u2013 13:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.33 上午', '2021-06-08 09.43.33 上午', 129, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 128, 1, '76907108', 'Gattino餐廳 全日早午餐 義大利麵 甜點', '106', '台北市', '大安區光復南路280巷32號', 25.03988310, 121.55601700, '早午餐', '早午餐 四天前\n男生服務人員積極熱心介紹\n覺得環境優美服務熱情打電話預約\n女生服務人員很和善溫柔\n\n上完洗手間回來\n看到蟑螂餐盤爬過去躲到餐具盒\n老闆把食物和餐具移動隔壁桌\n請我繼續用餐\n\n結帳完越想越覺得覺得不太對\n人在外面打手機\n跟店家反應建議處理方式\n當下應該要撤掉餐點而不是叫客人繼續吃\n\n老闆回說客人不應該無線上崗\n有點傻眼和難過\n沒遇到過這種事\n當下不知道怎麼反應\n但覺得不舒服\n\n才會人在外面還打電話說這個事情\n反而因此被責罵有點難過\n懷著期待的心情去的\n老闆一直問說現在妳想怎麼處理\n當下如果我有反應給他免單也沒關係\n\n老闆有解釋他當下看到的蟑螂是在餐具那邊\n所以才只移動餐點和餐具\n還有他只是長相比較凶聲音比較大\n不代表他就是壞人\n\n原本打電話是想建議\n希望未來客人遇到這件事時\n溫和的詢問客人\n\n不知道為什麼打完電話超難過的\n很後悔打電話\n所以就結束電話\n後面也沒有處理了~\n\n收到電話帳單花了快兩百~\n聽老闆說他自己有多好而沒有解決問題~店裡有各式各樣的貓咪\n貓菜單/貓公仔/貓畫/貓燈等等\n貓控來應該很開心\n\n餐點部分種類蠻多的\n早午餐/燉飯/意大利麵/披薩\n\n很適合聚餐 大家一起點來分食\n菜色都不雷，熱熱吃好香呀\n\n下次還會想再來的餐廳?美味程度：????\n停車便利性：??\n價位：?\n服務：???\n環境整潔度：????\n\n位於小巷弄內，所以停車的部分機車比較好停，汽車則是要到國父紀念館附近停，不太好找車位\n\n餐點的種類還行，當天點的餐都不雷，品質不錯，份量偏少，價格中高\n\n服務態度兩極，點餐時有個先生感覺像老闆，介紹的比較仔細，也很親切！但是服務生有一個小姐...態度就很差，對客人不屑的感覺.......有待加強\n\n整體來說還可以，不會特地跑來吃，但是有經過偶爾可能會吧.....每年我生日都會來Gattino，提早預約都有壽星蛋糕?讓人超開心\n這次吃了之前沒吃過的12寸披薩? 披薩麵皮帶有一定厚度,口感十分軟Q\n有股麵包烘焙過後的香氣在口中\n綜合海鮮口味$450超好吃，料超多每一口都吃的到~\n\n義大利麵-手工菠菜麵 $360\n波菜麵寬度較寬,吃起來有嚼勁\n加入辛香溫和的綠胡椒+香濃蜂蜜芥末醬\n完全融入麵體\n醬汁也不會收太乾?\n\n加套餐$199都有飲料跟手工蛋糕，很棒喜歡這間店溫馨的氣氛與佈置\n特別是沙發椅 坐起來很舒服 像是在家裡吃飯一樣\n餐點部分 也都好吃 第一次看到松阪豬披薩 立刻點一份 現場還會幫忙刨起司 而且是佈滿整個披薩 很阿撒力\n接骨木也好喝', 4.4, 459, 2020, 'BB7ugvr@gmail.com', '02 8771 0109', 2, '["星期一: 11:30 \\u2013 21:30","星期二: 11:30 \\u2013 21:30","星期三: 11:30 \\u2013 21:30","星期四: 11:30 \\u2013 21:30","星期五: 11:00 \\u2013 21:30","星期六: 11:00 \\u2013 21:30","星期日: 11:00 \\u2013 21:30"]', 'https://www.facebook.com/GattinoTaipei/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.34 上午', '2021-06-08 09.43.34 上午', 459, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 129, 1, '64096346', '九點零一分韓式早午餐', '104', '台北市', '中山區中原街91號1樓', 25.05977170, 121.52901310, '早午餐', '早午餐 泡菜炒泡麵很好吃?九點零一分韓式早午餐\n\n餐點??\n\n?明治哥好吃耶(我是三明治啦)\n大口咬豬里肌 $55\n(熱賣！因為肉厚要煎比較久哦 / 厚切豬里肌)\n\n?大滿足炒炒韓國拉麵(我很辣)\n「韓國進口Q彈拉麵，佐泡菜大火快炒爽爽吃，打顆生雞蛋(要全熟請於點餐時告知)，哇?！五告齁呷！\n小提醒：外帶請儘速吃完，放久易結球影響風味哦\n(可加起司+$10 2倍肉肉+$25)\n牛牛好初 $90\n(我也加泡菜和蛋蛋喔 / 培根牛肉片)\n豬肉萬萬歲 $85\n(我也加泡菜和蛋蛋喔 / 梅花豬肉片)\n\n?飲品(飲料均可選擇冰/熱飲)\n咕嚕紅茶(固定 微糖) $25\n咕嚕鮮奶茶(固定 微糖) $45\n-\n三明治吃起來普通 可能我本就不太喜歡豬肉\n但是肉真的是滿厚 很飽滿的一個三明治～\n可我還是比較喜歡炒韓國拉麵 泡菜加蛋就已經很夠味\n何況又加了肉片 哇牛肉的好好吃 豬的也不錯\n但其實吃起來是一樣的 只是肉片的部分不一樣而已\n味道上面沒有什麼分別哦 放心點?\n麵真的是很多 起司也是鋪滿滿的 麵也不會軟爛 很好吃\n紅茶比鮮奶茶好喝 比較推紅茶哦??\n這邊等位子都會等比較久 然超多 所以要請耐心等候！牛牛好初炒泡麵 好吃！\n敢吃一點點辣的就可以吃 不會辣到受不了\n\n豬里肌總匯三明治也很推\n感覺得出是用心堆疊出的三明治\n麵包、蛋都很柔軟\n菜的厚度剛剛好 也擺的很整齊\n咬起來十分順口\n味道也很融合很讚剛剛點外送，猶豫熊貓外送沒有單點服務，我備註說明咖啡因為溫度變化或不知道的原因 ，曾經點過沒味道就沒點了，有備註加咖啡，如果不行能直接刪單的，但又做了一杯真的只有乃和冰塊融化的咖啡出來，感覺錢丟水溝，非常不開心，熊貓外送也沒單獨點麵，Uber又不能備註。點了一般早餐都有的里肌肉三明治\n蛋、生菜、肉片和吐司的酥脆度平衡的不錯、味道也可以（屬清淡）、唯一可惜的是里肌肉片的筋沒處理好、幾乎每一口都吃得到咬不動的筋。', 4.1, 750, 3075, 'QYPezE@gmail.com', '0908 163 036', 1, '["星期一: 07:00 \\u2013 14:00","星期二: 07:00 \\u2013 14:00","星期三: 07:00 \\u2013 14:00","星期四: 休息","星期五: 07:00 \\u2013 14:00","星期六: 07:00 \\u2013 14:00","星期日: 07:00 \\u2013 14:00"]', 'https://shop.ichefpos.com/store/IqMg1Tq7/ordering', '0.jpg', '[0.jpg]', '2021-06-08 09.43.34 上午', '2021-06-08 09.43.34 上午', 750, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 130, 1, '47306605', '光合箱子 南京店', '105', '台北市', '松山區南京東路三段303巷8弄5號', 25.05299710, 121.54704140, '早午餐', '早午餐 平日下午4.來用餐很舒適～雖然好吃，但太貴了。滿滿的菜跟吐司加蛋，菜可能很新鮮但是醬料給太少了吧??還要自己跟服務生續，料理覺得沒有需要什麼廚藝手法的部分，就是青菜烤一烤然後擺盤，that’s all!瞬間覺得自己是小白兔之類的草食動物\n\n有提前訂位結果店員把我們安排在一個樓梯旁邊的位置，這個位置很差因為樓梯旁邊有兩桌，會碰到另一桌的椅背，如果把椅子往前調整又會很難進去位置，提前訂位還獲得爛位真的令人超級不開心！明明別的區域也有還沒入坐的兩人桌，覺得莫名其妙\n\n回到餐點的部分，看起來吃不飽但是最後發現是吃菜吃飽的，飲料因為是套餐附的，喝起來淡如水??然後整個cp值非常差，被四點多星騙進來了?以後找餐廳會記得先看過負評，比較不會期望越大、失望越大優格超好吃！我來南京東路三段303巷8弄的舒曼六號用餐，才看到光合箱子。之所以會來舒曼，也是因為朋友的指引，舒曼來此開業才一年多，有點名氣，目前網友PO上的相片有接近1000張，而光合箱子被網友PO上的相片有3600\n張，也不曉得光合箱子在這兒開店了多久，但我想光合箱子應該也是相當熱門的店家。\n\n店家在座位的設計上很特別，店前有不小的空間是室外座位區，有遮蔭，縱然是在夏天，早上，或者是傍晚，都會很舒服，春秋就不用說了，若是下雨天，會更有情調。\n\n11月23日午餐時間，室內座位大約坐滿了客人，店外僅有一桌有客人，或許是夫婦，或許是朋友，來這兒消費者的穿著都滿整齊，風度優雅。\n\n我看了菜單，好像樣樣都好吃。先約朋友去附近的TGB很牛碳烤牛排吃一頓，然後就來光合箱子打牙祭。', 4.1, 2038, 8356, 'fKx2LAA5@gmail.com', '02 2719 8851', 2, '["星期一: 09:00 \\u2013 17:30","星期二: 09:00 \\u2013 17:30","星期三: 09:00 \\u2013 17:30","星期四: 09:00 \\u2013 17:30","星期五: 09:00 \\u2013 17:30","星期六: 09:00 \\u2013 17:30","星期日: 09:00 \\u2013 17:30"]', 'https://m.facebook.com/daylightbrunch/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-08 09.43.35 上午', '2021-06-08 09.43.35 上午', 2038, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 131, 1, '75716199', '貳悅食日早午餐（寵物友善）', '105', '台北市', '松山區敦化北路50巷18號', 25.05073730, 121.54777180, '早午餐', '早午餐 謝謝店家的暖心服務~~~餐點非常好吃又感受得到健康~~能夠健康跟美味同時兼具太好了??????\n班尼迪克蛋真的中間會有蛋液流出；上面的荷蘭蛋黃醬也增添了口感的層次分豐富度~\n炒時蔬解膩之餘還讓人補充蔬果量~~\n紅茶也好喝！！\n真心誠意祝福店家能夠挺過這次疫情，未來還想繼續到店光顧啊~~~謝謝你們???溫馨舒適的早午餐店，是寵物友善餐廳。\n有店狗但要碰運氣才會遇到哦。\n這次點了古早味小食200元，有附紅茶，餐點份量蠻豐富的，還可以吃到小塊炸雞唷。\n友人點的早午餐和義大利麵看起來也不錯吃，味道很香。\n有機會可以來試試看，是個不錯的早午餐店！今天預定10:00用餐，跟朋友很快速地點完餐。我點的是蕃茄奶油咖喱燉雞肉，10:58才上桌，雖然店員有說明因為燉咖哩要一些時間，已經再擺盤，可是之後還是等了快10分鐘，而且隔壁桌其中一位客人跟我點相同的餐點，他們比我們晚入座，餐點卻先上了??\n餐點是好吃的，我很喜歡帕瑪森麵包的口感，馬鈴薯沙拉也很好吃，環境整潔，可惜出餐順序的管理要再加強。很喜歡店內氛圍\n東西好吃 份量足 店員服務態度很不錯\n重點是清潔衛生做得很好\n店狗身上也不會有異味，毛蓬蓬的好可愛??\n\n當天有帶貓咪去餐廳\n還提供水跟貓草還有炒蛋\n體驗真的太棒啦～～～～??來這裡真的是對的第一次來\n店員還會問你寵物需要水或用餐嗎\n一開始以為他們有買狗餐點，後來詢問才知道\n他們的用餐是會免費供應炒蛋給寵物\n用餐差不多還會拿小零食給寵物\n今天比較可惜的是沒看到他們博美店長', 4.4, 124, 546, 'nXtHid@gmail.com', '02 8771 3911', 4, '["星期一: 10:30 \\u2013 14:30","星期二: 10:30 \\u2013 14:30","星期三: 10:30 \\u2013 14:30","星期四: 10:30 \\u2013 14:30","星期五: 10:30 \\u2013 14:30","星期六: 10:30 \\u2013 14:30","星期日: 10:30 \\u2013 14:30"]', 'https://instagram.com/february__ten?igshid=wfl8vwhgjghx', '0.jpg', '[0.jpg]', '2021-06-08 09.43.36 上午', '2021-06-08 09.43.36 上午', 124, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 132, 1, '56552831', 'Sincere Rays貓本早午餐', '104', '台北市', '中山區南京東路三段89巷11-8號1樓', 25.05291620, 121.53851900, '早午餐', '早午餐 很棒的Brunch 餐廳，環境乾淨整齊悠美，又有洗手台和很棒的洗手乳。好聽的音樂加上好吃的餐點，還有店員親切的服務，值得再訪！！熱美式100薯格格100\n早午餐盤288 /蛋+可頌88\n覺得份量算滿多的也不錯吃\n餐廳採自助式 所以沒有收取服務費\n空間明亮寬敞 用餐起來還不錯不錯吃但沒什麼特別的記憶點，份量不大，我是小鳥胃的人，也只覺得剛剛好飽。\n最大的優點大概是在連假的假日還能用餐限時到2小時。軟殼蟹還滿有特色\n但以早餐來說口味比較重\n酪梨早午餐相較起來比較無負擔，口味溫和\n\n竹炭拿鐵長相還滿特別，喝起來倒是偏無感環境舒適，音樂輕鬆適中，餐點飲料也美味。軟殼蟹可頌帶點微辣的醬汁很開胃(可惜份量感覺稍微偏少)。早午餐的滑蛋是我吃過數一數二香嫩厚實有誠意的，配著下面的酸種麵包真的想給廚房裏的年輕主廚一個讚。美式與拿鐵咖啡也是有水平。可憐的外場兼吧台美眉一個人忙不過來時會忘記笑容，原以為她很嚴肅，等比較不忙時，你會發現她很親切可愛的。加油～', 4.8, 45, 216, 'spxN0jN@gmail.com', '02 2508 2282', 4, '', 'https://www.instagram.com/sincererays/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.37 上午', '2021-06-08 09.43.37 上午', 45, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 133, 1, '23993806', '松山調色盤-精緻早午餐', '105', '台北市', '松山區八德路二段366巷23號', 25.04697240, 121.54609950, '早午餐', '早午餐 門口看起來小小的，其實裡面座位還不少，店內簡單明亮的裝潢，覺得舒服而且乾淨，是半開放式的廚房可不會聞到油煙味，吃完身上也不會臭臭的\n\n生意很好，幾乎都是滿座的狀態，可翻桌率很高，其實不太需要等，也可以先電話訂位就是了\n\n入口處拿菜單，自助找位、去櫃檯點餐結帳、拿餐具醬料，用餐完畢後也要將餐盤端去回收櫃\n\n美式香料炸雞拼盤$165\n炸雞超級超級好吃！搭配的烤糖霜厚片吐司我也是好喜歡，有點奶酥的味道～即便放了一下下才吃都很美味\n\n健康蔬菜蛋餅$30\n墨西哥里肌燒肉蛋餅$50\n是偏軟Q的餅皮，口感很不錯，連喜歡吃焦酥餅皮的友人都被收買，里肌肉的調味也很不錯\n搭配店家自製青醬，好吃到不行\n\n古早味紅茶$25\n好喝又不會太甜\n\n鴛鴦奶茶$35\n雖然是三合一，還是喜歡覺得好喝\n\n這家是連超挑剔的朋友也讚不絕口，還列入他會回訪的名單(??? ?? ???)\n還有多種行動支付，對於不愛用現金的人超級方便！美味又營養可口的早餐店（必收藏！）?\n營業時間剛剛好\n適合早起和夜班下班\n飢腸轆轆的人來填飽肚子?\n蛋餅燉飯三明治應有盡有\n真的什麼都想吃 選擇困難呀！\n超推義式冷麵～?\n份量大菜多麵多 吃好飽\n胡麻醬不夠還可以再取！\n吃完可以飽到晚餐了?\n還有雙人水果茶!?\n100元真的太便宜又好喝 ?\n外面早午餐小小一杯就180\n這裡份量大CP值高\n喝不完還可以外帶?\n空間環境明亮舒適\n餐桌間距合宜沒壓迫感\n適合三五好友慢慢放鬆享受早餐時光?\n老闆親切溫暖～ ?\n還會依照人數給份量建議\n避免我們點太多吃不完(真的好險沒多點XD)\n提供行動支付?\n太適合我這個出門不帶錢包的人?\n結論就是好優秀的早午餐店 荷包不心疼\n下次再帶好友來吃?????餐點新鮮好吃\n目前為止還沒點過覺得不好的\n連烏龍麵都讓我驚艷?\n每週路過這裡一定會來報到\n沒有時間久留就點蛋餅類也是超級好吃\n照片是泡菜蛋餅大推?\n老闆跟老闆娘都很親切\n有次忘記水壺隔了一週去拿竟然連濕紙巾也幫我保留的好好的?\n會再持續光臨?店面非常舒適，裝潢漂亮，座位可見廚房寬敞乾淨不擁擠，客人完全不會聞到油煙，很加分，店員也非常親切，讓人感到環境很優質?\n料理更是驚豔，食物品質很好，沒有過多的調味或油炸吃起來也很美味兼顧健康，不會像有些店一樣吃起來死鹹太油太乾還很貴，而客人如果有口味想調整或嘗試也可以自己拿調味料，可以滿足各個人這點蠻周到的！\nCP值很高也很有特色，是一間會想帶親朋好友回訪的店，下次來會想吃吃看炸雞沙拉還有水果茶?超～多種類可以選擇，有選擇障礙的人一定超痛苦??內用環境蠻舒服的，餐點口味也好吃，炸雞蠻讓人驚豔的，可惜胃容量不夠大，下次要再點有附糖霜吐司的餐點', 4.8, 317, 1522, '666V0JGT@gmail.com', '02 2771 8097', 4, '["星期一: 10:00 \\u2013 14:00","星期二: 07:00 \\u2013 17:00","星期三: 07:00 \\u2013 17:00","星期四: 07:00 \\u2013 17:00","星期五: 07:00 \\u2013 17:00","星期六: 08:00 \\u2013 17:00","星期日: 08:00 \\u2013 17:00"]', 'https://www.facebook.com/%E8%AA%BF%E8%89%B2%E7%9B%A4%E6%97%A9%E5%8D%88%E9%A4%90-%E6%9D%BE%E5%B1%B1%E7%B8%BD%E5%BA%97-102835651447612/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.37 上午', '2021-06-08 09.43.37 上午', 317, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 134, 1, '38523629', '醬吃Brunch', '104', '台北市', '中山區朱崙街77號', 25.05004920, 121.54288980, '早午餐', '早午餐 今天坐邊桌 一直被螞蟻騷擾 ??（感覺在搬家...加上邊桌 很容易有吐司屑殘留?）\n\n先推他們做餐飲的用心，堅持純手工，自然的選材～比較可以吃的安心 ～\n\n單點的CP值偏低，但特餐的CP值太高了（今天點滿福總匯特餐）！！端過來的時候真的傻眼（還好最終沒浪費食材..但超撐）飲料可以選甜度也很加分，這在一般早午餐店不常見～只是今天吃的薯條和熱狗覺得用料有點多（偏鹹）但這是個人喜好問題～\n\n唯一希望可以改進的除了螞蟻騷擾之外，應該是環境的油煙味吧～用餐久了，會有種窒息感?  ....除此之外 真心推薦 ??飯沒有煮熟\n米粒中心都還是硬的\n請服務員幫忙加熱\n男服務員一臉不情願\n一句話都沒說\n臭臉把飯拿進廚房餐點還算可以，但店員服務品質真的讓人不會想再去，東西送錯桌，詢問店員，也一副無所謂的樣子，把東西接走，一句不好意思也沒有，整家店店員都沒生氣，除了煎台師傅會招呼。芋泥系列和期待有落差...\n既然主題是芋泥為什麼搞的肉鬆才是主角呀～\n不過東西還是好吃！打卡送薯條的活動,結果打完卡說薯條賣完了\n吃完後發現新來的客人卻有薯條?\n感覺真的很差,如果人多成本考量可以直說阿?', 4.2, 87, 365, 'ZuaZww@gmail.com', '02 2776 3055', 4, '["星期一: 06:30 \\u2013 14:00","星期二: 06:30 \\u2013 14:00","星期三: 06:30 \\u2013 14:00","星期四: 06:30 \\u2013 14:00","星期五: 06:30 \\u2013 14:00","星期六: 07:30 \\u2013 15:00","星期日: 休息"]', 'http://www.instagram.com/jiangchrbrunch', '0.jpg', '[0.jpg]', '2021-06-08 09.43.38 上午', '2021-06-08 09.43.38 上午', 87, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 135, 1, '15389805', '天淳津品-國父紀念館美食/東區下午茶/大安區早午餐/台北必吃', '106', '台北市', '大安區光復南路290巷38號', 25.03954440, 121.55607930, '早午餐', '早午餐 和朋友來一起吃，店家真的非常用心\n\n用餐環境：?????\n簡單質感的配色加上挑高,用餐座位數雖然不是太多，但是坐在高腳椅上與用餐桌面的距離剛好，與店面音樂的搭配不會感到太擾心，店面環境及廁所都很乾淨，可以舒適的慢慢享用\n\n食物美味度：?????\n女生消費者進來對於食品營養新鮮與否、美味度、來源、及份量都有提高的意識，與朋友各點一份秘製醬滷牛腱與柚香雞腿套餐，我們都非常的滿意，滷牛腱的牛肉入味不卡喉，柚香雞腿的雞肉非常彈嫩，野薑花雞湯可以看到上面店家辛苦熬製，喝一口非常溫潤，沒有一般雞湯厚重的油耗味，非常清爽。從上餐到完食刈包的時間食物都還是有一定的溫度，刈包裡的湯汁直至穿都沒有滲入刈包皮。雖然竹炭刈包皮有一點黏牙，牙齒會黑黑的，但從食材的挑選及搭配，甚至裝著刈包的紙袋都非常用心（不沾油不沾汁），還是非常值得推薦！\n\n店家的人都非常友善健談，也很願意分享做好食物的理念，推推～這次點了鮭魚和滷牛腱口味的刈包。\n\n牛腱最上層朋友說有醃黃瓜，不是酸黃瓜是用梅子醃過的淡淡香味，光是聽到這樣就覺得很用心。\n\n我點了鮭魚單點，鮭魚表面還有一點梅納反應的香氣，油脂跟水份保持的很好，中間的蛋還做成愛心形狀，很可惜被其他料壓住看不到?\n醬料則是有點塔塔醬的味道，不確定是不是自己做的醬但是整體組合搭配的平衡很好。\n\n總蛋白質份量其實是蠻足的，吃完不會覺得空虛。\n\n飲料朋友點了一杯有點蘋果西打的味道，總之料理優質，飲料也是中上，加上裝潢和氛圍雖然店小但中午去也還算是適合坐著跟朋友聚餐聊天的店家。\n\n在國父紀念館附近，可以刷手機載具開發票，感受的出店裡的各個細節都有被重視到，地段與裝潢絕對不便宜所以價位覺得合理偏超值?。國父紀念館站。假日中午用餐時間，連跑了2間口袋名單的店，都因為太臨時沒定位，而無法用餐，直到來到這第3間的天淳津品，店精緻有裝潢，位不多，但幸好還能入座。很喜歡點的炙燒蒔蘿鮭魚+鮭魚卵 和柚香烏龍茶飲。用料給的很實在。\n\n到店時，兩小桌有客人，便先安排坐吧台，之後小桌客人用好餐離席，便詢問老闆能否坐到小桌，老闆也大方說可以，並不會因為是一人用餐，而限制只能坐吧台，服務非常周到客氣！\n\n離開時，老闆提及寫下評論會送新開發的小點心，但當時Google 使用出現問題，就無法立馬上傳評論。和老闆說非常不好意思，也不好就這樣拿送的小點心，但老闆很客氣說沒關係，真的很不好意思啊?送的小點心包裝也太好了啦！小提袋很是時尚！而且小點心很受家人好評。\n\n是一間食物 服務 讓人會再去的店。我們點了鮭魚卵套餐跟和風香料雞，我覺得蒔蘿加入醬的味道很剛好，魚卵的鹹甜點綴著鮭魚油的香味，搭配生菜跟蒔蘿醬的味道，鮮！喜愛香草跟鮭魚的人真的要試試看蒔蘿鮭魚，另外和風香料雞的作法居然是舒肥，嫩的不可思義，雖然個人比較喜歡脆的雞皮，但整體也是很平衡的調味，黑色的麵皮非常Q彈非常好吃，我絕對不會說闆娘人超好又親切，老闆要加薪啦～餐還沒上，先喝烏梅汁就有驚人到，難得的濃郁的古法烏梅汁，店家還貼心的再給裝有冰塊的杯子，可以享受舒服空間和音樂的同時，沒酒精作用，倒著烏梅汁也很醉人，牛腱及鮭魚割包上桌後，依然驚人，牛腱入味不老，鮭魚厚厚一大片，因為新鮮不用刻意一堆調味，就很好吃鮮嫩不乾不老，滿滿的驚喜和飽足感，好品味好手藝好服務的不一樣割包店??', 4.8, 303, 1454, 'rVJ8KW89@gmail.com', '02 2772 5550', 4, '["星期一: 11:00 \\u2013 19:00","星期二: 11:00 \\u2013 19:00","星期三: 11:00 \\u2013 19:00","星期四: 11:00 \\u2013 19:00","星期五: 11:00 \\u2013 19:00","星期六: 11:00 \\u2013 19:00","星期日: 休息"]', 'https://facebook.com/TianChunUmami/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.39 上午', '2021-06-08 09.43.39 上午', 303, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 136, 1, '51795729', '好口福早午餐', '10491', '台北市', '中山區錦州街258號', 25.06020560, 121.53456520, '早午餐', '早午餐 好吃，但今天咬到蛋殼兩次培根手工脆蛋餅50$\n藍莓吐司40$\n蛋餅完全沒有脆皮的口感，濕濕軟軟的，跟我想像中的完全不一樣，藍莓吐司有烤跟沒烤一樣，\n大家都是辛苦錢，不曉得是不是因為地段賣比較貴還是？？？\n整體吃起來很普通，完全不懂為什麼比一般早餐店貴至少10元以上，真的不值這些價位==終於吃到了朋友強力推薦的好口福早餐， 這次點的是手工豬肉嫩蛋還有法式花生豬嫩蛋套餐，真的是超好吃的? 口感滑順不油膩，份量也非常的足夠，豬排感覺是自己做的不是一般早餐店的肉排非常的清爽，超級推薦?防疫做的很快，服務態度好的早餐店。薯泥蛋餅覺得蠻特別的，飲品的味道也很到位。調味料可自己添加，覺得貼心。味道不錯，但這價格cp值偏低，容易吃不飽', 4, 403, 1612, 'EN5HqrL@gmail.com', '02 2507 1995', 2, '["星期一: 07:00 \\u2013 13:30","星期二: 07:00 \\u2013 13:30","星期三: 07:00 \\u2013 13:30","星期四: 07:00 \\u2013 13:30","星期五: 07:00 \\u2013 13:30","星期六: 07:00 \\u2013 13:00","星期日: 07:00 \\u2013 13:00"]', 'https://www.facebook.com/%E5%A5%BD%E5%8F%A3%E7%A6%8F%E6%97%A9%E5%8D%88%E9%A4%90-1623180034598508/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.40 上午', '2021-06-08 09.43.40 上午', 403, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 137, 1, '62495006', 'Dozo創作和食居酒屋', '106', '台北市', '大安區光復南路102號', 25.04357480, 121.55734020, '居酒屋', '居酒屋 裝潢很棒，但食物真的有待加強……跟男友去吃飯慶生\n原先有訂位6點後面電話告知改6:30\n電話打很多通都沒接就算了\n入座後要點餐按服務鈴按了8次以上都沒人理\n光等服務員幫我們點餐就直接等到7點多\n結果餐點起碼都要等一小時\n而且訂位人員說我們用餐時間只到7:50\n\n下午還有先寄放要送的生日禮物\n原本很期待跟男友在這邊慶生\n結果氣氛直接毀掉\n後面索性不用餐\n生日禮物原本要當驚喜也沒了\n\n體驗蠻糟的\n\n*回覆餐廳的留言\n\n本人可以理解母親節檔期很忙的狀況\n但在我看來\n你們的服務人員屢次經過我這一桌卻沒有注意到我們無數次的招手，看起來都是忙著送餐給客人，不過身為外場服務人員怎麼會連其他客人都無法顧及到？\n只有疑似主管的人員注意到我們的招手\n才有接下來的狀況\n\n若是可以加強一下訓練抑或是可以調整一下制度會更好\n\n彌補的部分就不需要了\n不過也不會再光臨貴店了不好意思DOZO創作和食居酒屋??\n已經來過很多次的我，\n每次去都是幾道必點菜再加上幾道沒有吃過的菜，再加上幾道新菜！！\n先推薦必點的！也是我超愛的！！\n第一道首推白醬炒烏龍\nDOZO 的白醬真的超級好吃，也是我去這麼多次來，\n唯一味道沒有變太多的主餐，這次也是因為太懷念～白醬炒烏龍就跑去吃了(yum)\n酪梨壽司也是我最愛之一～酪梨卷整體的調味我超愛～裡面的玉子燒跟乳酪也很好吃～\n這次點了咖哩溫泉陶板燒～這就有點不喜歡了～咖哩味雖然有～但沒有很入味～裡面的魚菜單上是寫有青魽、海鱺、旗魚但全炒在一起又有炸過！！魚炸了後有點柴！！覺得不喜歡(sleepy)\n鹽烤豬肉梅子番茄串燒，這也是不太喜歡的！！我記得上回在一家自助餐的豬肉番茄串很好吃！！這裡的，豬肉有點油、番茄不甜不酸，整個吃下來一點特點也沒有，才兩串我也吃到覺得膩 。。 原本期待高高的，這個價位跟口感還蠻讓我失望的(shock)\n最後我又加點了麻糬提拉米蘇，就是突然想吃甜的，就點了比較想吃的，麻糬的皮很好吃，但是裡面的餡就。。\n整體來說～我還是很愛他們的壽司卷跟炒烏龍，其餘的就是大家可以各種嚐嚐囉～～氣氛感覺還好\n但價格不親民\n有點貴服務人員態度需加強\n很少兒童適合的食物', 4, 2068, 8272, '6KtblWW@gmail.com', '02 2778 1135', 3, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 01:00"]', 'http://www.dozoizakaya.co/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.51 上午', '2021-06-08 09.43.51 上午', 2068, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 138, 1, '92693787', '大村武串燒居酒屋-大安店【梅酒專賣】', '106', '台北市', '大安區大安路一段31巷44號', 25.04388440, 121.54757930, '居酒屋', '居酒屋 梅酒喝到飽！爽啦！13種梅酒限時暢飲??????499/2hr星期天 晚上9點到生意很好\n都是大學生的感覺\n燒烤偏甜 食物尚可雞肉類都很不錯，雞皮意外的超級好吃，配梅酒讓人一口接一口停不下來跟補習班老師+學長姐+學妹一起來這邊結業聚餐！！懷念那個明太子煎蛋！！\n唯一美中不足的是，櫃檯外的一位男服務生臉有點臭臭的。我起身三次（拿餐具筷子杯子，我離最近）（還有結尾上洗手間）\n\n一直瞪著眼睛問我：怎麼了嗎？（感覺怪怪的）\n\n一般餐廳服務生都是問：請問有什麼需要服務的嗎？（吧）\n況且其實我也沒有詢問他，或是示意他什麼...這個感覺態度有點不舒服\n\n但整體來說還是不錯的！這家東西好吃！', 4.7, 1006, 4728, 'aMvFwfLv@gmail.com', '02 2731 1958', 4, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 01:00","星期六: 18:00 \\u2013 01:00","星期日: 18:00 \\u2013 01:00"]', 'https://www.facebook.com/ot27311958', '0.jpg', '[0.jpg]', '2021-06-08 09.43.53 上午', '2021-06-08 09.43.53 上午', 1006, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 139, 1, '76996662', '大黑居酒食堂/台北居酒屋/東區美食/市民大道美食/台北消夜/東區居酒屋/東區推薦餐廳', '105', '台北市', '松山區市民大道四段155號', 25.04484060, 121.55107460, '居酒屋', '居酒屋 來好多次了，真的每一道都好好吃\n每次來店員也都很親切有趣，也會真心推薦哪一道好吃，三個女生吃飽一個人約七百元\n?豬五花\n?胡麻時蔬\n?明太子炒飯\n?蛤蠣烏龍麵\n?炒水蓮\n?明太子飯團\n?松露杏苞菇\n?水蜜桃氣泡酒飲很少有居酒屋可以坐兩小時的，且價位便宜\n推推\n-\n二訪還是很推的居酒屋，烤麻糬最後當甜點，建議四個人點一份就好，會有兩塊，不然會吃的很膩唷！真的是好吃誒 ~\n日本啤酒1瓶100 ~ 都接近熱炒的價格了\n... 魚如果是現流的，在台北市蛋黃區是可以接受的 (但是我沒點魚), 不過基本肉類都在標準值.\n2 個人吃飽喝足 1998 台幣.味道偏吃粗飽的店 但價位稍高\n餐點中規中矩 串燒偏鹹 生食沒有特別驚艷\n東西吃到一半會被服務生的手伸進桌中收盤子打擾 雖然很有效率 但是還是很錯愕地中斷用餐的節奏 造訪一次足矣來記得要去招牌看一下推薦料理\n整體吃下來不錯\n兩個女生以上再點九貫壽司要不然會吃不完～\n下次我應該就會單點自己想吃的\n因為配好的不一定合各自胃口\n\n推推唷!', 4.2, 689, 2894, 'TsPB4j@gmail.com', '02 2570 6855', 2, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 01:00","星期六: 18:00 \\u2013 01:00","星期日: 18:00 \\u2013 01:00"]', 'https://www.daikoku155.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.54 上午', '2021-06-08 09.43.54 上午', 689, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 140, 1, '81187199', '三虎居酒屋 Izakaya Santora', '106', '台北市', '大安區延吉街131巷11號', 25.04276430, 121.55481360, '居酒屋', '居酒屋 絕對沒問題??食物好吃酒類的選擇多、生啤是我最喜歡的orion。\n而且店裡的氛圍很好老闆又親切，不管是聚會還是一個人小酌，都非常推唷?\n\n雰???味?素敵???\n\n#N訪不知道幾遍了、食物氣氛還是一樣五?三虎是回台北後第一家吃的居酒屋，之後再吃其他居酒屋都覺得沒那麼好吃，就這樣變成常客了～食物非常好吃，幾乎點什麼都不會錯！ 老闆的人也非常好！非常推薦！親切的店員，\n好吃的食物。\n\n二訪，\n東西依然無可挑剔的好吃，\n但是可惜似乎因為市場休市，\n很多想點都沒了。菜都很好吃，串燒也超棒\n配杯啤酒真的很適合夏天\n\n疫情稍緩一定要再去', 4.5, 568, 2556, 'n2UX6Z@gmail.com', '02 2731 8890', 2, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 01:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.55 上午', '2021-06-08 09.43.55 上午', 568, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 141, 1, '53690422', '錵見鈅 居酒屋 台北推薦居酒屋|大安日式餐酒館|東區推薦美食|大安聚餐餐廳|東區推薦居酒屋|東區日式料理', '106', '台北市', '大安區延吉街58-6號', 25.04393660, 121.55346820, '居酒屋', '居酒屋 朋友生日來聚餐，但是當天師父只有一位，所以出餐速度有點慢了些，餐點的部分沒有太大的驚艷，價格也不便宜今天沒有拍到美食照，回家要寫才發現，好可惜。\n今天吃到的每一道，大家都很捧場，還沒有遇到很雷的，很值得前來一試哦！\n店裏的大哥很親切，包廂聚餐氣氛也很好??\n謝謝?廚師手藝很好，食材新鮮?忘記點了什麼，記得串燒?皮一般般，可以燒更好。 其他菜式都是過得去，但感覺一般般。\n但老闆延熱情的。泡菜好吃   肉類不太好吃\n洋蔥沒有烤熟  一半生 一半熟可能忘記翻面？\n炸花枝很誇張  先切再果粉  全都是粉皮  幾乎吃不到花枝……', 4.8, 95, 456, 'yPsEqSH@gmail.com', '02 8772 1055', 4, '["星期一: 休息","星期二: 18:00 \\u2013 00:30","星期三: 18:00 \\u2013 00:30","星期四: 18:00 \\u2013 00:30","星期五: 18:00 \\u2013 01:30","星期六: 18:00 \\u2013 00:30","星期日: 18:00 \\u2013 00:30"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.55 上午', '2021-06-08 09.43.55 上午', 95, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 142, 1, '31601263', '川越?-午餐定食 中山區美食居酒屋 串燒料理推薦 特色美食餐廳 建國美食 日式串燒', '10491', '台北市', '中山區建國北路一段70號', 25.04909290, 121.53634240, '居酒屋', '居酒屋 很難吃而且餐點很不精緻、調味死鹹，兩人1500up且吃不飽相撲火鍋3-4人份，上來就4片肉片，3顆丸子。收我528！！超差的用餐體驗！！\n點餐的時候店員還說這3-4人份很大份喔！吃的完嗎？心想正餓著，吃不完再說。結果是這種狀況，感覺很差！跟店員反應，店員還說那可以加湯。眼翻到太平洋去了。我說：我不要喝湯喝飽！很差的用餐體驗，再也不會去用餐了。這是我畢生吃過最難吃日式串燒店，沒有之一。\n點了串燒拼盤結果只有一串吃得完，因為整盤比7-11加熱食品還不如，醬多到根本看不出來有沒有烤過，當然吃起來也不像烤過，都不知道在吃什麼！\n價格當然是飛天了，好吃價格高就算了，吃起來像微波食品你給我賣這價格就說不過去了。\n如果可以我希望是0星評價。完全浪費我的錢！\n而且食材不新鮮\n戴著料理手套給錢\n噁心?價錢餐點不太符合，沒有生啤酒?有麒麟精釀啤酒，只好點了金牌2支！才發現怎麼不去熱炒店吃串燒就好?', 3.7, 129, 477, 'W5NRpT@gmail.com', '02 2503 1870', 2, '["星期一: 17:00 \\u2013 23:30","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期六: 17:00 \\u2013 23:30","星期日: 休息"]', 'https://www.facebook.com/%E5%B7%9D%E8%B6%8A%E9%A7%85%E6%97%A5%E5%BC%8F%E4%B8%B2%E7%87%92%E5%B0%88%E9%96%80%E5%BA%97-460941213966266/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.56 上午', '2021-06-08 09.43.56 上午', 129, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 143, 1, '12192441', '隱家居酒屋', '10491', '台北市', '中山區林森北路67巷122號', 25.04777270, 121.52820620, '居酒屋', '居酒屋 地方很乾淨 坐位很舒服 很鬆動 ,老闆 老闆娘人很nice , 食物吃了4道 , 每一道都很好吃 , 很有心的食物 !  以後想吃居酒屋必會想到這家??店不大但氣氛好又乾淨，老闆跟老闆娘都好親切，每道菜都好吃，大推紫蘇番茄豬肉串！趕時間的不適合，但有時間的話一邊聊天一邊搭配酒和慢慢的上菜，真的跟家裡一樣舒適～老闆+老闆娘超級親切,\n私房一夜干和蕃茄豬肉串好好吃,\n超級推唷! 可來試試?巷弄中的小店。老闆夫婦很可愛也很健談，會跟客人聊許多日本飲食文化和特色料理，聊著聊著就會變出菜單上沒有列出來的隱藏料理XD\n食物超級美味，氣氛又很溫馨，有種來到深夜食堂的感覺?食材美味 老闆親切 淡菜驚艷 烤蝦超配', 4.8, 96, 461, 'xAjA465@gmail.com', '02 2511 8283', 4, '["星期一: 18:00 \\u2013 00:00","星期二: 11:30 \\u2013 14:30, 18:00 \\u2013 00:00","星期三: 11:30 \\u2013 14:30, 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 11:30 \\u2013 14:30, 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/pages/%E9%9A%A0%E3%82%8C%E5%AE%B6/365646936929368?rf=577625205716055', '0.jpg', '[0.jpg]', '2021-06-08 09.43.57 上午', '2021-06-08 09.43.57 上午', 96, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 144, 1, '91472760', '狗一下居食酒堂-忠孝店', '106', '台北市', '大安區敦化南路一段190巷18號', 25.04253890, 121.54760830, '居酒屋', '居酒屋 很滑...很嫩...可爾必思好喝適合朋友聚餐，上餐快速，串燒類都推薦料理很好吃\n裝潢也很有味道\n但是用餐服務與品質很差\n吃到飽方案\n一桌一次點最多7樣 合理不多不少也不會亂\n出餐我們漏了好幾樣點的餐點\n別說很忙 所以漏了這種幹話\n我們第一單第二單有重複點的\n同桌因為有人上廁所不知道 反映以為送了兩次\n服務員還拿單子過來說 你們有點喔!\n一共點了兩次 我們都有確認!\n但是第一單點的生魚片一直持續到第四單第五單\n都沒有上桌 明明就有再核對客人寫的訂單\n卻沒人發現有漏餐沒送嗎?\n擺明就是故意拖餐... 反映後\n應對上 完全沒有 不好意思 抱歉 等等安慰客人的話語\n不論是儲幹 還是經理 都是一副高高在上的方式在對待客人\n感受實在非常非常 不妥\n簡單幾句 我幫你確認看看等等補上 就這樣~\n第一次反映第一單的生魚片沒來\n等了30分鐘後 才補上....\n後面第三單我們又點了一次(隔了20分鐘)\n一樣在最後加點的時候 還沒來\n直接在反映一次 拿著櫃檯打的白單過來確認\n我們單子有畫 但是他們的餐單上面 完全沒有打上去...\n都看在眼裡...甚至在提前買單時\n同桌友人拿錢到櫃檯要結帳\n被櫃檯的人員告知 稍等我兩分鐘 我先點餐等等幫你結帳\n然後就忙著接在接單 等了一會\n直接跟我們同桌友人說 你先回去座 等等再來\n且用詞都是很不客氣 也沒有不好意思 抱歉等等委婉告知\n東西好吃沒錯 但對待客人不用這麼冷漠\n我們也不是奧客 真心覺得 完全沒有服務品質可言\n美食1顆 裝潢1顆 你們只配2顆星星\n用餐一開始就要客人給5星換取免費飲料續杯\n也只是你們衝評價的手段而已\n我們在乎用餐感受的 覺得你們不配我是衝著飲料喝到飽評分的，不過東西真的棒！吃到飽 真的是 很穩\n本人已自己名譽擔保', 4.8, 9076, 43565, 'OohqMD@gmail.com', '02 2731 3585', 2, '["星期一: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期六: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00","星期日: 11:30 \\u2013 14:30, 17:00 \\u2013 00:00"]', 'https://www.facebook.com/dog01/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.58 上午', '2021-06-08 09.43.58 上午', 9076, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 145, 1, '89065113', '零九年居酒屋', '106', '台北市', '大安區市民大道四段60號', 25.04488580, 121.54673150, '居酒屋', '居酒屋 短短一個禮拜來了兩次\n串燒跟炸物都滿好吃的\n最推明太子雞腿肉、起司無骨牛小排、鹽蔥雞腿肉\n杏鮑菇烤得很剛好，很鮮美可口\n炸物最推起司薯球?有夠唰嘴\n醬燒雞蛋豆腐超級好吃！雖然說鹹酥雞攤隨便點個雞蛋豆腐都很好吃\n但是在這裡現吃真的是好香酥多汁，調味也還不錯\n\n最不推的話一定要提到炒飯！炒得很失敗還吃得到一坨白飯??調味也不太行，飯很軟爛\n\n酒類有點沙瓦，就一般沒什麼酒精濃度的汽水口感\n8+9highball喝起來味道有點淡，感覺水加滿多？\n叫輸贏的梅酒shot很好喝！梅酒香氣夠酒味也濃，如果還有再訪一定直接來一盤～\n\n最後真的不得不說\n這裡出菜速度真的很～慢～\n趕時間千萬不要來！！！\n就算客人不多速度依然很慢消費相對比較平價的一間居酒屋?\n環境很適合三五朋友一票聚會聊天\n飲食也挺好吃\n明太子給的很大氣??明明過年前就定位了\n2/18一到現場居然沒有營業?\n預約當下沒說 就算臨時公休也應該打電話講一下吧 非常糟糕\n只好跟朋友臨時改吃火鍋Yakitori restaurant with vintage Taiwanese school style decor. The food was good and inexpensive.\n東西好吃的平價居酒屋，價位在台北鬧區算CP值不錯，裝潢走中學復古風格，蠻可愛的。以環境來說，整體感覺想營造一個國小教室的感覺，天花板上裝有懷舊電風扇，但是天花板過低導致吹在頭上的風力有點強，不是很舒服。\n\n餐點來說，蛤蜊湯、炒泡麵正常發揮、中規中矩，另外點了牛雞豬三種串燒，牛肉有點難嚼，雞肉普普通通，豬肉在三者裡面算比較推的，但口味上都沒有特別驚豔。\n\n點了柚子沙瓦，喝起來味道很淡，有種透過水的感覺，冰塊融化了以後，變成調味水的感覺？炒水蓮不太清脆，覺得應該是放太久不新鮮的關係，因為跟同桌都有吃到1、2口的餿味。\n\n價格來說，它非常平價，可是相對來說，環境食物也跟價格相符，非常普通，食材、環境、料理技術都沒有特別之處，吃到餿味扣一星，若下次想吃平價串燒，會考慮市民大道其他店。', 4.4, 207, 911, 'hKMEpRO1Y@gmail.com', '02 2740 7528', 1, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 01:00"]', 'https://www.facebook.com/BlahX3Izakaya/', '0.jpg', '[0.jpg]', '2021-06-08 09.43.58 上午', '2021-06-08 09.43.58 上午', 207, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 146, 1, '86803270', '酌九?司居酒屋', '10558', '台北市', '松山區市民大道四段185-1號', 25.04470210, 121.55212120, '居酒屋', '居酒屋 炸物：牛蒡甜不辣\n冷物：炙燒比目魚\n主食：海膽烏龍麵\n串燒：明太子雞柳、牛肉剝皮辣椒\n湯品：薑絲鮮魚湯\n烤物：現流金鯧魚\n炒物：明太子玉子燒\n食材新鮮美味，非常好吃?食材新鮮好吃，老闆跟服務生都親切到極點，大推一波???不得不說食物方面真的很好吃！\n至少目前沒踩到雷。\n干貝壽司跟牛小排壽司大推！！！！！\n炒時蔬也很棒～壽司很好吃，完全必點\n推薦點六貫or十二貫，每次去一定會點～比目魚鰭邊肉真的是太好吃啦餐點好吃，不貴\n最喜歡鹽烤玉米筍\n適合朋友們一起小酌吃東西，氣氛滿自在舒服的。', 4.5, 251, 1130, 'Cuw9VUPOO@gmail.com', '02 2579 0095', 4, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 01:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.43.59 上午', '2021-06-08 09.43.59 上午', 251, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 147, 1, '49571814', 'MURA邑居酒屋-日式串炸燒 小酌聚會 深夜美食堂(必喝生啤 推薦人氣餐廳)', '10491', '台北市', '號 No, No. 36林森北路119巷中山區', 25.05035780, 121.52639980, '居酒屋', '居酒屋 △ 食物擺盤美\n▲ 料理味道好\n△ 服務態度佳\n▲ 味道很日本(道地)\n△ 菜單豐富\n▲ 提供中、日文菜單\n△ 中、日文皆可對應\n▲ 座位舒適(部份要脫鞋)\n△ 可刷卡、可付現\n▲ 餐前、後提供熱毛巾\n△ 男女廁所分開\n▲ 單價均高\n△ 啤酒有「天使之環」\n\n————————\n\n來這邊用餐時，在點的料理上來前，會送上一小盤「開胃菜（前菜）」也就是日本居酒屋常見的「?通?」。一般就是簡單的料理或是涼拌的小菜，有一些比較簡單的還會有泡菜、毛豆 等等，當天提供的是【鱈魚白子】。這道也是有費用的喔～ 並不是免費提供。\n\n實際點了這幾道菜，感想如下：\n\n雞肉串燒\n台灣黑豬五花肉串燒\n明太蛋捲\n菠菜滑蛋、醬油麴添加\n廣島炸生蠔\n日式炸雞塊\n醬油麴冷豆腐\n炸鮪魚\n\n————————\n\n???串\n雞肉串燒：\n雞肉厚實、口感多汁又燙口，因為肉本身夠厚，吃進嘴裡也是滿滿的紮實感，軟中帶嫩的嚼勁非常棒，外表微微的焦感，將肉汁都鎖在裡面，很推的一品，連點了兩次！\n\n?豚???串???\n台灣黑豬五花肉串燒：\n肉感大塊，吃起來味道很不錯，但是不曉得是否為豬本身的問題，吃起來有淡淡豬味，不過不是豬臭味，也是好吃，但是比較推雞肉串燒\n\n????草玉子??、?油?入?\n菠菜滑蛋、醬油麴添加：\n菠菜本身毫無苦味，蛋的口感為滑蛋，相當滑嫩，而醬油調味上與菠菜融合的相當好，下酒也下飯，吃了幾口就立即點了一碗白飯來搭配。\n\n明太子玉子??\n明太蛋捲：\n蛋捲吃起來口感很嫩，切開後裡面明太子滿滿，不會像是某些居酒屋給的很少，但是個人覺得味道上有點偏甜，但是吃起來挺道地的。建議趁熱吃是最棒的，不適合放涼～\n\n?島?????\n廣島炸生蠔：\n整體上覺得包覆在裡面的 生蠔 有稍微乾一些些，不曉得是否食材本身的問題？但是以口感來說，還算是不錯，搭配醬料吃很對味。\n\n唐揚?\n日式炸雞塊：\n「外酥內嫩」，調味很適中、不過鹹、炸皮不刮嘴，平衡度跟大小都很剛好，很推薦點的一品炸物。\n\n?油?冷奴\n醬油麴冷豆腐：\n豆腐就是很單純的味道，但是上面的麴醬，帶點微微的鹹度，當成前菜的話很適合～ 非常開胃\n\n鮪???揚?\n炸鮪魚：\n一般台灣居酒屋不常見的一道炸物，使用的是鮪魚肉的炸物。酥酥薄薄的外皮下，魚肉很厚實，吃起來肉不但紮實又不乾，而且因為有厚度的關係，與炸雞感受完全不同。喜歡帶點酸度的話，加上檸檬後，魚的鮮味會更明顯，有來用餐，建議要點來試試喔～\n\n＝＝＝＝＝＝＝＝＝\n\n整體調味非常道地，與日本居酒屋的味道幾乎是相同的，雖單價上是高一些，但是無論是服務、料理味道、擺盤 等等，我覺得都很到位，在林森北這邊的一級戰區，這家店是相對突出，經友人介紹，實際品嚐後給很高評價，來這邊是一定不會踩雷，值得回訪的店家！\n\n但是生意很好，建議用餐前也先訂位會比較好，而部份座位需要脫鞋，但是會給拖鞋，方便消費者上洗手間，不用再穿穿脫脫～\n\n如果來用餐，帶日本朋友也不會失禮，這邊有日本人的服務人員，溝通上絕對沒有問題，而且洗手間是男女生分開，在洗手間裡面也準備了牙線、酒精、漱口水、髮蠟等等，而且也有 中、日 的菜單，真的非常貼心。\n\n啤酒的鮮度也很好，杯子洗非常乾淨，有點生啤喝的人，喝一口就會知道，啤酒會很完美的在杯子內緣劃下一圈圈的泡沫圓，通常稱為「天使之環 Angel Ring」喝一口就會留一下圈，要產生這個的話，杯子清潔尤其重要，唯有乾淨的杯子才能形成，所以來喝啤酒的人要好好留意一下喔～這是我第一次還沒進店裡就想先給五顆星的店，從電話中就可以感受到店家是用心對待每位客人，我們人多的關係選擇了料理長推薦的omakase料理，由料理長親自搭配並料理，雖然沒有品嚐到主食的部分有點可惜，但都有吃得很飽，而且每道菜都非常好吃。\n氣氛:100\n餐點口味:100\n服務態度:100\n是間很用心經營的店!\n真心不騙!絕對會再2訪\n想吃看看其他沒品嘗到餐點。\n料理?本??美味????\n笑??素敵?、優????????????達\n?????^^非常用心的料理以及親切的服務，\n我已經幾乎吃遍整本菜單，\n每次都還是有驚豔感！\n煙燻類料理是一定要吃吃看的，\n整條滾燙軟嫩的玉子燒也不可錯過！\n基本上沒有地雷，\n可以的話建議慢慢花時間吃完整本菜單哈哈哈哈哈\n圖片是鹽蔥牛舌，單吃就很鮮香，配上鹽蔥更順口！聽朋友推薦，第一次到這間居酒屋，星期一到店用餐的，因為是離峰時段所以現場就有位子可以入座。店內有吧台和和室座位，毫不猶豫直接選擇和室座位???\n\n今天點了冷製合鴨 明太子雞翅 豪華刺身 鹽烤魷魚 炸雞 鮭魚飯糰 明太子奶油烏龍麵\n原本還很怕會踩雷，但每一道料理都出乎意料之外的好吃，擺盤也很精緻，可惜當時只顧著聊天都忘了拍照???\n上菜時服務員還會貼心的介紹餐點和吃法，說是用對的方式吃會更能體會到食材的美味???\n\n後來和老闆娘稍微聊了一下，老闆娘說店裡的每道菜都是店內料理長親手製作，聽說料理長之前都會去日本研修，難怪每道料理都這麼好吃。\n\n總結一下，這間店室內空間寬敞，坐起來很舒服，店內的服務生也很親切，料理也都很好吃，價格也非常合理，真心推薦給喜歡日式居酒屋或是想找聚餐地點的人。???\n順帶一題，店內客人也蠻多日本人的，也有日本人的服務生，差點讓我以為自己是身在日本的居酒屋裡(*???)食物好，氛圍好，老闆好，整個就是間99.9分的店啊！！剩下的0.1就是可惜太早關門了??這家的明太子起司玉子燒如果說是條通第二沒人敢說自己是第一，還有更多隱藏版餐點等著各位來發掘喔～～', 4.9, 471, 2308, 'zR3qij@gmail.com', '02 2511 1577', 4, '["星期一: 00:00 \\u2013 01:00, 17:00 \\u2013 01:00","星期二: 17:00 \\u2013 01:00","星期三: 17:00 \\u2013 01:00","星期四: 17:00 \\u2013 01:00","星期五: 17:00 \\u2013 01:00","星期六: 17:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/Mura11936', '0.jpg', '[0.jpg]', '2021-06-08 09.44.00 上午', '2021-06-08 09.44.00 上午', 471, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 148, 1, '47709800', '鳥竹.酒食', '104', '台北市', '中山區龍江路265號', 25.05941600, 121.54084750, '居酒屋', '居酒屋 每年都會來用餐好幾次\n每次都會吃到當季新鮮食材的料理\n主廚真的很用心\n連帶朋友去朋友都讚不絕口\n很溫馨的一間店\n老闆娘不忙時還會關心我們用餐狀況\n有時還會陪我們喝個幾杯\n真心推薦\n帶朋友去絕對不踩雷\n推推推推一進去被問有沒有訂位，回答說沒有，店裡的人就回：已經客滿！再請問等不行嗎？店家很直接回說：沒位！！（意思是不行？！）其實無法告知要等多久沒關係，客人會自行選擇是否要等，但直接這樣回覆，會讓人感覺，意思是沒訂位，連等都不行。好吃，東西新鮮，而且都是現點現做！\n雖然座位數不多，但反而顯得溫馨～\n沒先訂位的話還不一定有位子！\n而且每次用完餐老闆娘也都會再次確認今天用餐狀況，非常用心。道道好吃的不得了?\n會讓人想要來天天報道的店\n怎麼沒有七顆星可按...OMG玉米天婦羅好特別，必吃，青干魚頭荒煮也超強，整體都很出色，老闆會觀察客人的用餐節奏來出餐，簡單來說，是一間平價版的頂級居酒屋', 4.6, 246, 1132, 'b4tnKQ@gmail.com', '02 2517 3978', 2, '["星期一: 17:30 \\u2013 00:00","星期二: 17:30 \\u2013 00:00","星期三: 17:30 \\u2013 00:00","星期四: 17:30 \\u2013 00:00","星期五: 17:30 \\u2013 00:00","星期六: 17:30 \\u2013 00:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.44.00 上午', '2021-06-08 09.44.00 上午', 246, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 149, 1, '24355025', '哎呦好味酒食', '106', '台北市', '大安區敦化南路一段160巷49號', 25.04364200, 121.54677780, '居酒屋', '居酒屋 跟一般居酒屋比起來餐點普普但單價有點貴，CP值不太高。\n日式炸雞比較像台式鹽酥雞，份量不多，滋味普普。\n酥炸大腸還不錯。\n起司玉子燒份量大但吃起來醬汁貌合神離的沒入味。\n水蓮松阪豬有點太鹹。\n反倒是贈送的甜點黑糖花生烤麻糬很好吃。\n服務還不錯，桌子地板有點黏，可能不會再去了。個人覺得餐廳不太好吃，很多都過鹹、因為評分有送甜點所以評分很高，個人不會再去，地上跟桌上也黏黏的上菜非常慢，雞肉很柴。在東區要吃居酒屋肯定有更好的選擇不說東西好不好吃，但吃到鋼刷絲，得到的回饋就是有鋼刷的“那道菜”不算錢，幽默酒蒸蛤蠣鹹、炒烏龍麵鹹。牛舌老。', 4.6, 317, 1458, 'Wy0YCb@gmail.com', '02 2773 0611', 4, '["星期一: 18:00 \\u2013 02:00","星期二: 18:00 \\u2013 02:00","星期三: 18:00 \\u2013 02:00","星期四: 18:00 \\u2013 02:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 02:00"]', 'https://www.facebook.com/wodelicious/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.01 上午', '2021-06-08 09.44.01 上午', 317, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 150, 1, '16632251', '大曜居酒屋-松山特色居酒屋|必去居酒屋|推薦串燒|清酒推薦|清酒推薦|必吃居酒屋', '105', '台北市', '松山區復興南路一段47巷1號1樓', 25.04538380, 121.54431690, '居酒屋', '居酒屋 餐點都還蠻好吃的，每一樣都十分出色，但出餐的部分還蠻誇張的，中間隔了大概有半個小時都沒有出我們的餐，還遺漏了烏龍麵沒上，然後告知我們內外場的人都有劃烏龍麵有出餐了，但我們真的沒吃到阿！最後雖然是沒收烏龍麵的錢，只是被懷疑總覺得不太舒服，另外講真的除了出餐也沒啥服務，不知道收服務費的用意是？餐點整體而言是好吃的，干貝海膽也很新鮮符合$250的水準，但牛肋條的筋有點多不太推薦等待入位時候看到有一隻浪浪小貓跑到店門口等著，隨後店員開了門，拿出了一點東西來餵食牠（應該是魚肉），很感動！生魚片很新鮮，餐點都各具特色，口味也很好，生啤酒更是一級棒！建議大家可來聚餐或家族聚會都很適合喔！！環境乾乾淨淨！\n食材我個人覺得很可以接受, 燒烤是以一枝起算，\n所以這裡是吃質不是吃粗飽的喔 ~\n單隻克數是有誠意的 ? 飽到不是問題\n至於酒 ... 單鍾盅的選擇可以多1-2項\n(因為我人數少點單隻太多 ... ）\n\n最重要的是 ... 放的音樂我喜歡\n~ 因為是耳熟能詳的華語樂曲日語翻唱, 然我感覺身在日本但是很熟悉 ... 個人很愛', 4.8, 413, 1982, 'WlwnXiuGN@gmail.com', '02 2731 9788', 4, '["星期一: 17:30 \\u2013 01:00","星期二: 17:30 \\u2013 01:00","星期三: 17:30 \\u2013 01:00","星期四: 17:30 \\u2013 01:00","星期五: 17:30 \\u2013 01:00","星期六: 17:30 \\u2013 01:00","星期日: 17:30 \\u2013 01:00"]', 'https://japanese-izakaya-restaurant-19529.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-08 09.44.02 上午', '2021-06-08 09.44.02 上午', 413, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 151, 1, '79036502', '魚君 ????? 鮮魚專門居酒屋', '106', '台北市', '大安區光復南路290巷66號', 25.03944440, 121.55527780, '居酒屋', '居酒屋 一家店員都很很年輕的店\n忽然覺得自己是不是誤闖年輕人的店\n因為一進門迎面而來的都是年輕客人\n?????\n\n餐食：\n◎三色丼NT.580\n因為今日是北海道海膽\n故每份需再加60元\n一送上來變成四色丼?\n我跟先生一起傻眼\n服務生感覺是希望我們能接受\n雖然同價位但海膽會變少啊?\n而且我們超討厭鮪魚泥極度厭惡\n就讓她去換給我們了?\n干貝/海膽/鮭魚卵  蠻鮮的\n醋飯也不錯\n不過整體真的算蠻小份的\n(我看評價普遍都寫這個?)\n而且我沒有一家海鮮丼飯的飯吃的完\n但這家可以欸?\n\n◎烤鮭魚頭 NT.420\n我本人不怎麼愛吃烤魚\n(我是嗜生的)\n但先生給予好評??\n\n◎角嗨 NT.180\n難得不用別交代就給偏濃厚的?\n但店員不小心多做了一杯\n還問我要不要多喝一杯?\n\n◎可樂NT.80\n\n這家店特別的地方\n就是明明店內看似都還有空位\n但都還是要等欸?\n沒訂位路過想吃就還是得乖乖等吧\n店內位置不算多\n建議提前訂位才是明確選擇??料豐味美 新鮮好吃環境氣氛和服務生的服務都不錯，丼飯整體品質不差，新鮮美味，四種食材各自表現的不錯，不過在四色丼裡面吃到鮪魚筋和一片甜蝦殼覺得訝異，這是不應該出現的瑕疵；炒火龍果花有點一般，湯品則是可以再用心一些，缺乏厚度和層次，有點可惜。透過 UberEats 訂餐，\n中高價位包裝保鮮不夠嚴謹，生魚片、熱食沒有分開，也沒有保冰袋。\n吃完晚餐當晚極不舒服想吐，狂拉肚子！\n希望店家一定要改進！尤其是作生魚片相關不可不甚呀餐點一直都很好吃，支持了很多次\n海鮮品質都維持的很好\n但今天的女服務生態度真的不怎麼好?\n也沒有交接好餐點\n（反而男服務生態度非常好）\n送收餐都不說一聲...\n是好險最後有男服務生拯救一切\n希望這位女服務生如果有情緒的話可以暫時先離開\n而不是把情緒給客人?\n\n另外放題活動的酒送得非常慢，慢到我不如單點慢慢喝一杯還比較划...\n加上我們有同行的餐點送上來不到20分鐘，就被服務生說用餐時間只剩10分鐘\n感覺就很？？？？', 3.8, 742, 2820, 'GbVrnNuTs@gmail.com', '02 2772 8959', 2, '["星期一: 17:30 \\u2013 00:00","星期二: 17:30 \\u2013 00:00","星期三: 17:30 \\u2013 00:00","星期四: 17:30 \\u2013 00:00","星期五: 17:30 \\u2013 00:00","星期六: 17:30 \\u2013 00:00","星期日: 17:30 \\u2013 00:00"]', 'https://www.facebook.com/%E9%AD%9A%E5%90%9B-%E3%81%95%E3%81%8B%E3%81%AA%E3%81%8F%E3%82%93-%E9%AE%AE%E9%AD%9A%E5%B0%88%E9%96%80%E5%B1%85%E9%85%92%E5%B1%8B-477280926111211/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.03 上午', '2021-06-08 09.44.03 上午', 742, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 152, 1, '16177551', '亂烤居酒屋', '104', '台北市', '中山區南京東路二段21巷6號', 25.05245890, 121.52868200, '居酒屋', '居酒屋 闆娘是阿美族姑娘親切熱情老闆是烤物高手無人能敵難怪這麼受歡迎上面寫可以外帶，實際去又不能外帶，白跑一趟超讚！烤功一流，闆娘老闆幽默風趣！菜好吃，老闆親切，客人素質很ok門面暗暗的，有種沒開店的錯覺。\n東西好吃，很適合聊天小酌好去處。', 4.2, 87, 365, 'HVyEjA@gmail.com', '02 2541 0002', 2, '["星期一: 18:00 \\u2013 00:00","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.44.03 上午', '2021-06-08 09.44.03 上午', 87, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 153, 1, '84840038', '燒鳥???匠Torisho 雞匠串燒居酒屋', '106', '台北市', '大安區大安路一段52巷8號', 25.04303480, 121.54582530, '居酒屋', '居酒屋 服務非常不好，提前3週訂位時再三確認要那個包廂，結果都沒打來確認訂位，我自己打去確認時說包廂給別人了，因為我的訂位上沒註明。店員說雖然我比另一組早訂，但是沒註明到還是不能給我。態度很不好，一句道歉都沒有，掛我電話，沒有安撫客人的不高興，也沒說要彌補對客人的差錯。店員很情緒化。雞匠串燒初體驗基本上還不錯。多人吃所以可以多點一些不同口味。以價錢來說，份量小了點但味道都還不錯，烤的火候也恰恰好。最好吃的應該是烤雞翅塞明太子，yaki tori 也烤的不賴，明太子馬鈴薯前菜也好吃，蛤蜊湯帶點材魚味很棒！雞肉丸串燒沾蛋黃也挺不錯的。可以二訪！好吃，雞肉丸子串帶有雞軟骨非常優秀、牛肉也很嫩、去過兩次還想再去，唯一缺點是價位偏高。整體推薦。食材新鮮料理美味，但不敢置信這個年代了還會有服務員勸酒文化，而且是我最討厭的「不喝不夠man」的激將法。\n同行朋友已經表明健康因素不喝酒了，還繼續「我們的酒包治百病」，真的很噁心。\n除此之外的服務也都很普通，相當不積極主動，按了服務鈴還是要等一陣子才會有人。\n是外場品質嚴重跟不上內場的店東西新鮮好吃，前台的帥哥推薦的生魚片組合，總是讓人滿意。菜單的料理種類豐富，還有些特別版的在小手寫版上，服務也不錯。\n環境及裝潢優良，適合約客戶應酬，或特別約會～', 4.3, 460, 1978, '0OqPir2QSR@gmail.com', '02 8773 8875', 2, '["星期一: 17:30 \\u2013 00:00","星期二: 17:30 \\u2013 00:00","星期三: 17:30 \\u2013 00:00","星期四: 17:30 \\u2013 00:00","星期五: 17:30 \\u2013 00:00","星期六: 17:30 \\u2013 00:00","星期日: 17:30 \\u2013 00:00"]', 'https://torishotaipei.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-08 09.44.04 上午', '2021-06-08 09.44.04 上午', 460, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 154, 1, '13687357', '一隅 -ichi guu-| 居酒屋 日本料理 松江南京 隱?店', '104', '台北市', '中山區四平街20號1樓', 25.05357850, 121.53088810, '居酒屋', '居酒屋 地點非常方便，離捷運站近，位在幽靜的巷弄內；餐點都非常精緻好吃又別具特色，尤其乾煎豬肝，是從來沒吃過的味道，非常美味！每道菜份量不多，價格相對來說非常昂貴，座位是全部木頭製作，方正有稜角，椅背完全垂直，坐起來比較不那麼舒適。超棒的啊啊啊啊啊！\n很懷念在日本念書的感覺\n真的算是道地又懷念的味道！\n以前在日本必吃又很容易飽的\n生雞蛋拌飯必點！！！！超推超好吃！！！！\n不知道是不是因為在日本很餓都這樣吃所以覺得好懷念喔喔喔喔喔！\n店裡的??妹妹也都超可愛的～\n店員很親切店裡的食物質感沒話說！\n會想一直一直一直回訪的店！\n也會推薦給身邊的朋友～\n不過真的店裡的位置不太多\n要去之前一定要先訂位唷！\n要吃很平價的這間比較不建議！\n真的是很道地的日本居酒屋的感覺！\n不過個人覺得跟日本比起來價格還是很親民！\n但習慣了很便宜的那種一串幾十塊的可能就比較不建議！\n因為很道地份量上來說也較為精緻！\n建議太餓的時候可以多點幾碗雞蛋拌飯就對了 ??果實酒都很好喝！\n店員很多是日本人\n每次去也很多日本客人～\n很日本味的居酒屋\n去了有種出國的感覺 太想念日本了\n食物超好吃的\n最喜歡肉豆腐 關東煮也很好吃！貼心可愛的小店，老闆在日本學藝，服務人員都是日本人，小菜跟酒品都很棒老闆超親切，長的像卡通人物?', 4.6, 145, 667, 'g0bGz3XX@gmail.com', '02 2567 5530', 4, '["星期一: 11:30 \\u2013 14:00, 18:00 \\u2013 00:00","星期二: 11:30 \\u2013 14:00, 18:00 \\u2013 00:00","星期三: 11:30 \\u2013 14:00, 18:00 \\u2013 00:00","星期四: 11:30 \\u2013 14:00, 18:00 \\u2013 00:00","星期五: 11:30 \\u2013 14:00, 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/%E4%B8%80%E9%9A%85-ichi-gu--754416184703736/info?tab=page_info', '0.jpg', '[0.jpg]', '2021-06-08 09.44.05 上午', '2021-06-08 09.44.05 上午', 145, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 156, 1, '28006571', '串稻光復店 | 國父紀念館串烤居酒屋', '106', '台北市', '大安區光復南路290巷41號', 25.03923180, 121.55594810, '居酒屋', '居酒屋 食物很普通，松阪豬騷味很重、牛肉串硬到咬不動、肉質看起來過紅有點怪，一夜干沒味道，餐點送來時都不是熱的，炒水蓮份量還行、酒蒸蛤蜊的蛤蜊肉算新鮮大顆，烤山藥普通。\n\n說是居酒屋但基本的肉類處理真的不行，種類也少到令我驚訝。（如果是很少但是超好吃那就算了但可惜不是）\n\n點餐系統是簡體字中國語非常奇怪。\n一坐下請我們掃QR CODE進入點餐系統，打開卻是上一桌客人吃的單、好險有先檢查，店員有馬上處理，但還是請大家自己注意。\n\n自助區環保餐具剩三隻筷子我也是傻眼?還要特地請店員補\n\n回家認真看評價4.5基本上應該都是打卡送東西洗來的，進去之前可以三思，店家食物品質須改善。\n\n看到一堆好吃好吃讚讚讚的評價我都開始懷疑自己是不是不懂吃惹呢～\n加油好嗎～態度超級差，帶著行動不方便的母親，18:30至現場，還沒走進門口，直接說現在內用要21:30才會有位子。現場用餐客人目測僅有3.4組，還有一大堆空位(是七月鬼在坐嗎?)，詢問是都被訂位滿了嗎? 跟我說訂位滿了。\n好!! 我接受，跟母親至隔壁餐廳用餐，用餐完出來20:00，經過店面，剩下1.2組客人，請問你的訂位????  原來店裡面有阿飄嗎?? 既然那麼不想接待客人，乾脆店面收一收好了!!\n\n這間店在夠X吉有賣折價券，千萬不要買!!  這麼差勁的店家，一顆星都不想給!!!服務超好，好吃又好喝 cp 值超高！！！\n服務生ㄎㄧㄤㄎㄧㄤ的很好笑，而且我們要走了還又請我們喝一杯\n下次還要再來?除了牛肉、羊肉串燒肉質太老硬以外，其他的餐點都很美味，烤鮭魚頭、烤飯糰、鱸魚湯、酒燒蛤蠣、明太子雞翅、培根蕃茄都很美味，因為不加小費10 %，自然物超所值，吃的很開心！\n\n另外離國父紀念館捷運站3號出口不遠，約再走7分鐘路程，所以交通也很便利喔！用餐環境也明亮舒適，雖然因為串燒較老硬，只給四顆星，但是還會再次惠顧喔！尤其烤飯糰真的非常好吃。看到4.6顆星 買gomaji券來的\n有點後悔...之後應該不會再來\n來分享一下食物的部分\n明太子烏龍麵 麵體太軟爛完全沒有口感 明太子的美乃滋加太多了 味道不對\n覺得不錯的就是培根蘆筍卷跟雞腿排 有入味\n然後豆乾跟甜不辣都好乾...\n還有點鯖魚 雞軟骨 就是普普 不想拍了\n總結～東西就是 可以吃 但是不好吃的程度', 4.5, 450, 2025, 'zjibcLI@gmail.com', '02 2711 2800', 4, '["星期一: 17:00 \\u2013 00:00","星期二: 17:00 \\u2013 00:00","星期三: 17:00 \\u2013 00:00","星期四: 17:00 \\u2013 00:00","星期五: 17:00 \\u2013 00:00","星期六: 17:00 \\u2013 00:00","星期日: 17:00 \\u2013 00:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 09.44.06 上午', '2021-06-08 09.44.06 上午', 450, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 157, 1, '95147615', 'JE Kitchen', '106', '台北市', '大安區光復南路346巷48號', 25.03847300, 121.55607700, '約會餐廳', '約會餐廳 【慎入！！本文很長！！】且僅供參考！！\n\n用餐時段：【平日午餐】\n備註：去之前爬文，滿滿的負評服務態度，個人覺得是時間點選的不佳，而並非全是員工的問題，在一個晚餐餐期、假日，我不認為有哪一間餐廳，甚至是【高檔餐廳】可以服務到完美無缺，所以在這裡建議平日午間用餐！你可以得到完善的服務！）\n\n以身為同行的評價，我給4星，依據【用餐環境、服務態度、食材質量、裝飾擺盤】，少一顆是因為餐點的協調性。\n\n用餐環境：從外面看進去感覺室內很昏暗，入內卻意外的明亮，座位之間有做到一定的間隔，不會覺得擁擠，由於今日用餐的人數不算多，目測6桌共10人算是4-5成滿，並沒有負評中說的吵雜，純粹當日用餐的客人素質。唯一昏暗的是廁所，不過保持的很乾淨無異味，門的設計挺特殊。\n\n服務態度：就像上面備註提到的一樣，雖然不是惡意測試，但我拋出的問題員工都有問必答，並沒有什麼讓人觀感不佳的表現，訪桌也很勤，出餐點控制的不錯，沒有所謂的過多等待，對餐點也很了解、介紹詳細。\n\n食材質量：先說食材的新鮮度是有一定的水準，再來說說食物量，除非你是小鳥胃！不然你不要覺得在這邊你可以吃的飽！以一個正常女性的食量，去之前建議先吃個小東西墊肚子（個人是吃摩斯的漢堡），最後一個套餐下來吃完覺得剛好。\n\n裝飾擺盤：這次沒有拍照…他們在裝飾這塊看的出來有花不少心思，顏色鮮豔、色彩豐富，水杯、餐具有擦拭乾淨無水痕，服務生端盤會手握小白巾，能避免指紋，看起來也比較衛生。\n\n餐點協調：這是評分唯一沒給星的原因，對口味的認知是很客觀的，今日吃到的每道餐點，可以感覺的到料理方式、組合不斷的在刺激你的味蕾，從一開始的鮪魚冷盤、餐前奶油麵包、午魚開胃菜、牛排主餐、甜品、茶點都是西式，中間穿插的雞肉丸湯品、茶碗蒸小點卻是中式的，會讓人錯亂我今天到底在吃甚麼？沒有關連、一致性。\n\n最後總結，整體來說用餐體驗是中上的，至於價位、份量算可以接受的範圍（畢竟是吃巧不是吃飽），此次來嚐鮮，沒有讓人有想二訪的衝動，一樣的價位我會選擇體驗其他沒吃過的餐廳。2020.2月用餐\n梨子。奇異果冰淇淋：很清爽的冷食，精緻點綴的餃子內包梨子，並沒有整個捏死，輕碰可恢復成一片圓形透明麵皮。奇異果冰淇淋非常冰鎮，可感受其新鮮。\n\n白酒＋紅酒：搭配好的品項，並無多問品牌。非常順口，白酒清爽，搭配開胃菜系列; 紅酒醇厚，搭配主食。\n\n餐前麵包：外皮極為酥脆，內餡鬆軟，完全無需用力咀嚼的那種嚼勁，可搭配熱氣融化後的奶油增加濕潤感。\n\n馬鈴薯湯。起司。松露：沈著的松露異香撲鼻，融入於熬煮濃稠的起司馬鈴薯濃湯。可搭配麵包食用。\n\n鱈魚白子。蟹膏。番茄：鱈魚白子為鱈魚精囊。平時個人不吃任何內臟，看其形狀類似腸子，吃起來鱈魚味道充斥口中，極為滑嫩，如冰淇淋般瞬間融化，搭配蟹膏與發酵番茄泡沫食用，消除腥味，為少數可以入口之臟器。\n\n蔬菜。白帶魚。蛋：相當台式食材。龍鬚菜鮮嫩不具纖維感，白帶魚與玉子燒同樣柔軟，搭配清甜魚骨高湯。\n\n蔬菜。羔羊背排。蘆筍。花生：此為升級的主餐。蔬菜為健康菜的黏滑口感，蘆筍則爽脆。羔羊背排不是極為軟嫩，肉質紮實，但毫無帶筋感，咬起來很順口，搭配花生馬鈴薯泥一起食用。\n\n乳酪。起司。梨：切片吹彈可破的水梨，覆蓋於乳酪起司冰淇淋之上，並有彩色花瓣裝點。帶點蜂蜜香氣的爽脆水梨，搭配濃郁乳酪冰淇淋。再度為冰淇淋喝采，應當為鎮店甜品。\n\n烤吐司。草莓。花生：冰淇淋再度登場，依舊讓人驚艷。薄片烤吐司和新鮮草莓為冰淇淋增添口味豐富度，畫龍點睛的硬花生增加口感於側。食材不重複出現，還能做到替換厭惡食物的要求，廚房極為用心。幾道菜餚混入日料元素，增加驚豔趣味感。外場讓氣氛靜而不迫，約會談天皆宜。\n\n以上是廢話。\n\n光是第二道茄子海膽蛋黃，我已被絕妙口感徹底征服。小牛胸腺調味濃厚華麗，頓時覺得在下語言的蒼白。一刀劃下A5和牛排，柔嫩油潤更勝米其林級火鍋中的同級肉片。其他也令人滿意，值得。服務不行，氣氛不行，餐點沒有一致性，雞肝那道味道很不好又是冷食，熱食的溫度也不足，精緻度不足。以這價位，服務的品質非常需要再加強，不斷催客人要關門了這點很不行！還要求我們吃快點不然送餐送不完，那麼最後點餐時間就應該往前移，服務業不應該讓客人感受不好！9點40就在隔壁桌噴酒精.......搞得像百元餐廳。好吃~\n位在仁愛路四段巷子內的創意料理餐廳，附近有公有地下室停車場，也有私人停車場，餐廳外觀不明顯，但是按地址找就很簡單，餐廳位在單行道。\n當然去這種餐廳最好是預約，餐廳不大但是座位區不會太擠太靠近，餐廳佈置簡單的歐式風格，沒有太多佈置但是簡約高雅。\n這次是中午吃商業套餐，一人套餐980元水資+服務費，如果主餐換牛肉變成1280元，整套包含前菜、沙拉、湯品、主餐、咖啡、甜點。整套吃起來好吃又不貴，口感實屬個人主觀意識，本人覺得適合台灣人口味，從生菜沙拉到甜點每道菜都很有創意。\n唯一小缺點是沒有每一道菜換湯匙及叉子，有可能會把上一道菜的味道留在在湯匙上，進而影響到下道菜的口味。\n餐廳可以刷卡，供應午餐及晚餐。', 4.3, 554, 2382, 'ypyHIZSBk@gmail.com', '02 2741 7115', 3, '["星期一: 11:00 \\u2013 19:00","星期二: 11:00 \\u2013 19:00","星期三: 11:00 \\u2013 19:00","星期四: 11:00 \\u2013 19:00","星期五: 11:00 \\u2013 19:00","星期六: 11:00 \\u2013 19:00","星期日: 11:00 \\u2013 19:00"]', 'http://www.jekitchen.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.28 上午', '2021-06-08 09.44.28 上午', 554, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 158, 1, '63210655', '好米亞 Goodies Cuisine Bistrot', '106', '台北市', '大安區敦化南路一段270巷6號', 25.03885120, 121.54790780, '約會餐廳', '約會餐廳 原本不想留評論了，但店家的回應實在令人失望\n\n當初訂位就有詢問如果不能併桌，分兩桌兩桌的距離會很遠嗎？店家回答就「在旁邊」而已，沒想到店家口中的「旁邊」那麼遠?那是顧客理解的問題還是店家說明不清呢？\n\n無法併桌我們可以理解，但幫小朋友加椅子我認為這是雙方可以溝通的，但店家完全把問題丟給消費者，這讓人感覺實在很差\n\n更不要提部分餐點實在不是很美味，店員態度極差，板著一張臉好像我們欠她錢.......，要水杯跟餐具也都姍姍來遲，我可以理解有其它桌客人需要服務，但我們沒有付服務費？需要被這樣對待嗎？\n\n總之，我不會推薦朋友來，也不會再來第二次。希望店家在指責客人前，也多想想自己是否還有可以改進的地方。平心而論，餐點的搭配十分優秀，找不到什麼能挑剔的地方，水準在平均值之上。\n\n但在服務方面是真的差勁到不行，預定六點用餐五點五十服務生還在大門口抽煙，整個用餐過程外場人手明顯不足導致服務品質低落。\n\n從沒有水杯到餐具東缺西少，我前前後後至少得跟服務生招呼五次，收拾臨桌時吵雜的程度不像是西餐館更像是一家熱炒店，在加上副餐飲品也是沒有詢問直接在餐前上這一點，我在相同價位下還沒遇過服務比這裡差的店家。\n\n如果你此趟目的是為了填飽肚子品嚐美食，那麼這家店或許能很合你的胃口；但如果你今天選擇這家店來慶生、過紀念日或是其他需要一些氣氛的活動，那就我而言這家店有很大的機率讓你失望。\n\n以上純屬個人淺見，畢竟這家店評分也不低，希望不要再有人像我一樣得到跟預期不一樣的結果。週末也有商業午餐，加180元可升級套餐。\n例湯：白花椰菜濃湯*****\n沙拉*****\n墨魚燉飯*****\n海膽燉飯****\n松露野菇燉飯*****\n燉羊膝*****\n蘋果塔****\n檸檬塔****\n\n今天最喜歡墨魚燉飯，非常喜歡料理的方式，裡面竟然有油蔥酥，誰想到西式的墨魚燉飯裡面竟然有台灣味。不過第一次約會或是還在曖昧不明時不建議點這道餐點，嘴巴跟牙齒會黑，如果要真愛大考驗可以挑戰。\n海膽燉飯有點說不出好壞，燉飯應該是用蝦湯去熬煮的，直接配上海膽，有滿滿的海鮮味。但我覺得應該可以再進步\n松露野菇燉飯表現也很好，味道很高級，很有水準。\n燉羊膝很有羊味，軟硬適中，老人家也很好入口。\n濃湯跟沙拉的味道也很好，擺盤也是超乎想像，餐具很用心搭配，而且沒有缺角。\n會再回訪餐點OK\n但服務想給 0 分 CP 值超低！花快 2000 超不享受。\n一個女店員不知道臉是在臭什麼，點個餐不知道在不甘願什麼。問個當日商品不應該嗎，想外帶蛋糕讓你覺得很煩嗎。\n最令人不滿的是\n自己先說排餐要等比較久，我不知道平常出餐要多久，但我們的是點餐到送上主餐花了快40分鐘，但這絕不是問題，問題是送餐同時還要用冷冷的口氣提醒用餐時間。吃的很緊張。口氣好點可以嗎？看一下出餐點和用餐狀況好嗎？不然就直接限制點餐內容好嗎？\n什麼好米亞，壞米亞啦！（胃痛）推1.豬排/義大利麵好吃2.白酒好喝。3.有當月生日點甜點送你蠟燭加寫happy birthday 的盤子。\n1.低消500 2.10%服務費3.有限時。\n\n不推\n備註：他們預設時間還沒到時，若沒有起身結帳會趕人哦，全體低消沒到會催點餐哦。（口氣不好的那種）\n不會再去，現在很多餐酒館，可以選擇。', 4.4, 2225, 9790, 'BGzm2ar@gmail.com', '02 8771 8285', 3, '["星期一: 11:30 \\u2013 22:30","星期二: 11:30 \\u2013 22:30","星期三: 11:30 \\u2013 22:30","星期四: 11:30 \\u2013 22:30","星期五: 11:30 \\u2013 23:00","星期六: 11:30 \\u2013 23:00","星期日: 11:30 \\u2013 22:30"]', 'https://www.facebook.com/GoodiesCuisine', '0.jpg', '[0.jpg]', '2021-06-08 09.44.29 上午', '2021-06-08 09.44.29 上午', 2225, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 159, 1, '60498068', 'CASA de AMIGOS-忠孝敦化推薦打卡餐廳|推薦約會餐廳|美食推薦|國父紀念館必吃|東區美食推薦|美食推薦', '106', '台北市', '大安區延吉街72之16號', 25.04239320, 121.55415260, '約會餐廳', '約會餐廳 兩個人剛好經過東區，看到網路上推薦這間餐廳就來品嚐了，店內環境乾淨，異國風裝潢，整體用餐氣氛不錯\n\n點了兩份套餐，加上但單點蝦子料理，餐點整體上口味都還不錯，蝦子好吃，但是飯的部分口感稍嫌有點硬，其餘餐點都還不錯，店員服務親切，適合午餐下午茶的好地方。\n\n推薦可以品嚐的義國料理利用FunNow預約的雙人套餐1599，除了能吃飽之外，食物真的很美味。\n西班牙海鮮湯非常香濃，海鮮味很足夠!\n小卷搭配的醬有夠美味，還有鴨胸非常amazing!\n最讓我驚豔的是海鮮燉飯，我從來沒有在台灣吃到如此道地的西班牙海鮮燉飯，飯的軟硬度、香氣，整體真的又道地又美味，下次絕對會再回去享用QAQ\nSangria表現一般，沒有特別驚豔的地方；甜點的吉拿棒跟蛋糕都十分美味。整體來說是間值得去的好餐酒館!5星好評!!最想吃的海鮮燉飯兩種口味在casa de amigos都超級好吃！這次慶生吃雙人套餐加單點其他項目，每個都超級好吃，鴨胸配甜菜根泥跟西班牙烘蛋也是一定要試試看，吉拿棒跟米布丁真的是發瘋好吃，米布丁很順口！價格實惠，店內服務跟環境都是滿分?熱情溫暖西班牙氛圍濃厚，生日還會幫忙播生日快樂歌 很嗨！辦聚會或、喜歡或是想試試看西班牙菜的朋友大推～餐點富特色，也頗為美味！服務人員態度也很好！\n只是用餐過程中有兩次另外要東西，像是開水...等，服務人員完全忘記，當天是星期一，現場客人僅2-3桌，而且用過餐點後，也遲遲不上甜點，等候了半小時，我們要求才送上，希望服務人員能再用心一點囉！非常nice的店家，店長非常親切，朋友在這裡給我一個suprise 的生日，很有溫度的店家，平常下班可以喝喝小酒，點一點tapas，下次會再帶朋友來的唷??', 4.3, 174, 748, 'eHrQuqVEs@gmail.com', '02 8772 8006', 4, '["星期一: 12:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期二: 12:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期三: 12:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期四: 12:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期五: 12:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期六: 17:00 \\u2013 23:30","星期日: 17:00 \\u2013 23:00"]', 'https://zh-tw.facebook.com/casadeamigos888/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.30 上午', '2021-06-08 09.44.30 上午', 174, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 160, 1, '72633923', 'Flower桂香預約制私宅料理', '10491', '台北市', '中山區龍江路229巷5號', 25.05741890, 121.54105480, '約會餐廳', '約會餐廳 謝謝你們毀了我的情人節環境很美，裝潢和佈置都充滿異國風情，可惜我們坐的區域有滴水的狀況，有點破壞氣氛心情。\n麵包和?醬很美味，桂花香的奶油很受好評環境非常適合紀念日或是重要場合，服務人員非常親切而且很專業！謝謝桂香的給我們一個這麼棒的回憶??這價格 牛肉竟然筋一堆 咬不爛??\n裝潢典雅\n食物不行很棒的用餐環境 典雅的裝潢 在龍江路的巷弄里\n菜色屬水準之上\n若是喜歡氣氛好的用餐氛圍 這裡到不失為一個選項', 3.9, 455, 1775, '29vfqPN8XV@gmail.com', '02 2518 0528', 4, '["星期一: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期二: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期三: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期四: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期五: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期六: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期日: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00"]', 'https://www.facebook.com/pages/%E6%A1%82%E9%A6%99-%E7%A7%81%E5%AE%85-Flower-No5-RSVP/619731021402667?fref=ts', '0.jpg', '[0.jpg]', '2021-06-08 09.44.31 上午', '2021-06-08 09.44.31 上午', 455, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 161, 1, '08469343', '推薦~新開幕~?餐酒館JIANG BISTRO&BAR/台北中山約會餐廳/包場聚餐/雞尾酒吧', '10442', '台北市', '中山區新生北路一段66號1樓', 25.04718250, 121.53033930, '約會餐廳', '約會餐廳 沒想到在台北市中心還可以享受到道地的韓式汗蒸幕\n這一間?餐酒館門口就可以停放四台車 停車非常的方便\n一進門變對它的裝潢色調驚艷\n孔雀綠 粉色 及灰色 讓整體空間有有一種時尚又沉穩的範圍\n之後才知道這樣的設計師是特別從法國請來的\n我們那天購買的是汗蒸幕下午茶套餐\n有附一杯飲料 ? 鬆餅?與烤雞翅\n私心大推我們加點的Pizza 餅皮薄脆 起士濃郁\n調得開心之後我們轉移陣地到餐酒館樓上的飯店房間\n汗蒸幕在一個可以容納8人左右的房間內\n裡面有椅子及坐墊\n非常適合在裡面做一些拉筋還有瑜伽的動作\n當然放鬆放空發呆都可以\n而這裡還貼心的提供了汗蒸專門的服裝\n毛巾及進口礦泉水讓我們使用\n誒換衣服的休息的地方這是大方提供了飯店的房間浴室讓我們做使用\n我們一行人有男友女所以貼心提供分開兩個房間方便讓我們換衣梳妝\n正式進入到汗蒸幕的房間之後\n連地板跟牆壁都有加熱\n整個房間維持在40度左右\n特別的是不像是烤箱真的時候會讓你覺得皮膚乾燥或是在傳統蒸氣式裡面會讓你覺得悶熱伸手不見五指\n在烤箱或蒸氣室我都待不了5分鐘就想出來了\n但是這個房間溫度提升卻不悶熱而且溫和地讓身體自然的出汗衣服一點都不乾燥非常神奇\n汗蒸的好處就是可以讓你身體排出深層的汗\n也有美膚的效果所以攜帶面膜來敷臉也是非常合適的\n在經過兩個小時的出汗之後呢回到房間盥洗完整個人非常的神情氣爽全身都放鬆了\n神奇的是我們一行八個人都覺得晚上的睡眠品質變得非常得好睡有睡不好狀況的朋友們也非常推薦\n?餐酒館的強項還有他晚上提供的許多種類特調的調酒以及餐點\n調酒的種類除了基本的調酒之外他有特色的特調調酒也非常值得一試\n有融合果汁類酸甜的也有加入茶的茶酒可以選擇咖啡調酒等\n或者你可以和調酒師的討論出一款你自己的特別特調\n餐點從炸物 義大利麵? 燉飯等 一直到 晚間套餐等擁有非常多的選擇\n我們一行人多所以各種餐點都有點到\n牛排是採用和牛等級的prime牛排?\n松露薯條海鮮燉飯是大家讚不絕口等選擇\n而且這邊還有專業的音響及點歌系統跟麥克風?\n包場還可以變成超大型的KTV包廂\n非常適合朋友聚餐或商務聚餐站在?餐酒館的門口其實就能感受到裡頭氣派的氛圍，\n還貼心的有代客泊車的服務，\n入內後有一個大大的櫃檯，\n就連櫃檯的裝潢都很有飯店高級的感覺，\n詢問之下才知道右邊是餐廳，\n左邊搭電梯上樓後是 #朵兒旅館\n這裡最特別的就是他們是台灣首創的韓國汗蒸幕主題餐廳，\n相信很多人一定都有去過陽明山或是北投…等等的地方，\n可以先泡個溫泉再到餐廳用餐，\n而他們在這個疫情不能出國的期間想到了最棒的方式，\n除了用餐之外，\n還可以搭配體驗汗蒸幕以及豪華套房休息的新專案。\n-\n店內的座位有分很多種，\n兩人座、四人座、半包廂式的通通都有，\n另外還有包場的服務，\n如果包場還可以唱歌，\n還有吧台區，\n想一個人用餐，\n或是心情不好想小酌的時候…\n也很適合來這裡。\n-\n菜單的內容蠻多的，\n有午間套餐、晚間套餐、搭配好的980套餐，\n另外還有單點的主餐、鬆餅、飲品、調酒…等等，\n最特別的是還有汗蒸精緻套餐。\n-\n住宿的部分也分很多種，\n這次特別介紹戀人住宿的情人房，\n一開房門「WOW~佈置的好美」\n床上滿滿的擺設，\n好浪漫好適合慶生或是求婚喔~\n在床的一旁還擺設了情人雅座，\n也可以要求把餐點送進房裡享用唷~\n-\n住在情人套房也可以另外登記汗蒸幕，\n只是要外加200元，\n200元的部分是備品的費用，\n可以使用2.5個小時，\n當然這2.5個小時就是屬於自己的空間拉~\n裡面有電視、獨立桌椅，\n都可以隨心所欲的使用，\n因為是獨立空間，\n記得訂房時就要趕快預約，\n不然一下就被預約走囉~\n-\n●戀人雙人套餐 NT2880【含戀人住宿一晚 NT3999】\n○香料奶油麵包\n麵包烤到外酥內軟，\n沾上特製的香料奶油醬料，\n咬入口中的每一口都散發著濃濃的香料味，\n真的好好吃。\n○主廚特製百香果沙拉\n愛吃沙拉的我超愛這一道，\n滿滿的新鮮水果乾加上特製沙拉醬，\n酸甜口感非常開胃，\n一旁的鮭魚很鮮甜，\n口感細緻滑嫩，\n看那亮光滑的魚肉就知道有多新鮮了。\n○花東辣椒莎莎醬炸雞翅\n如果不想用正餐只想要小酌的話非常推薦這一道，\n莎莎醬是使用花東剝皮辣椒結合墨西哥辣椒，\n再加入馬告胡椒、洋蔥蒜末以及青蔥調料製作而成的，\n咬下去的每一口都有滿滿的辣勁，\n雞翅的肉質只有嫩可以形容，\n這道真的很適合當下酒菜。\n○西班牙辣味拌炒香料野菇\n這道很特別，\n裡面有蘑菇、香菇以及杏鮑菇，\n加入西班牙香料下去拌炒，\n一上桌那鐵鍋吱吱作響的聲音再加上濃濃的香料香氣肚子真的就馬上餓了，\n吃了一口真的超涮嘴，\n不過真的有一點辣，\n所以怕辣的朋友記得先點杯喝的囉~\n○美國肋眼心牛排6oz\n美國肋眼心牛排是使用美國Prime等級的牛肉，\n經過45天溼式熟成的牛排，\n我們點選店家推薦的5分熟來品嚐看看，\n一上桌看到紅通通的肉質又不會流血，\n想必一定是高級肉質，\n切一塊放入嘴裡真的是好吃的無話可說，\n邊咬的時候還邊散發出濃濃的牛肉香氣，\n一旁還有店家提供的玫瑰鹽與芥末籽醬可以沾取喔。\n○BBQ醬炭烤豬肋排\n一聞到豬肋排的香味簡直讓我回憶了關島旅行的時光，\n看到肉本體的大小直接會有一種爽感，\n真的好大塊呀~\n用刀子輕輕的劃過骨頭與骨頭之間肉就直接分開了，\n這細緻又軟嫩多汁的肉真的完全沒有話說，\n沒吃過炭烤豬肋排的朋友們真的可以來?餐酒館品嚐一次。\n○海膽魚卵燉飯\n○海膽魚卵燉麵\n海膽魚卵有飯跟麵可以選擇，\n可以兩個人都選擇麵也可以都飯，\n而我們是點各一種來品嚐看看，\n這是我最愛的一道，\n每一口都有波波的口感之外還有濃濃的海膽滋味，\n味道鮮甜又滑順，\n真的會想一口接著一口吃完。\n○不能莓有妳\n不能莓有妳是甜點鬆餅的口味，\n另外還有蜂蜜跟巧克力可以選擇，\n不愛甜食的我選擇了莓果口味來享用，\n?餐酒館的鬆餅口感很特別，\n是帶有一點黏性的餅皮，\n沾上一旁的冰淇淋和果醬一起吃簡直太完美了。\n○邁泰(調酒)\n○初戀(調酒)\n看的出來?餐酒館的調酒不只好喝，\n就連外觀都好好看，\n這兩杯調酒口感都偏酸甜，\n一個是水果口味，\n另一個是葡萄，\n兩款真的都好好喝。最近新發現的新開幕的餐廳結合韓式汗蒸幕的西式餐酒館,,剛好女朋友生日，所以果斷先來嘗鮮體驗，果然不出所料，慶生布置華麗 座位舒適之外,餐點奢華美味 每道都別具巧思,重點是讓人有溫度的頂級服務,從一進餐廳到結束，無微不至的細心服務我們,連送蛋糕的時機點也會事先溝通,讓女朋友的生日完美的畫下句點, 如果問我會不會二訪, 我的答案絕對是會 ! 我覺得給5顆心還太少,非常感謝餐廳的用心,已經列入口袋名單 謝謝!裝潢很美   音樂也選擇的很有品味\n味道跟火侯不會輸給米其林\n絕對會再回訪的菜色豐富  價錢合宜\n裝潢風格我也蠻喜歡的\n食物很新鮮  擺盤漂亮\n調酒也很有特色', 4.9, 408, 1999, 'Z9JbiJVkZ@gmail.com', '02 2523 3367', 4, '["星期一: 12:00 \\u2013 02:00","星期二: 12:00 \\u2013 02:00","星期三: 12:00 \\u2013 02:00","星期四: 12:00 \\u2013 02:00","星期五: 12:00 \\u2013 02:00","星期六: 12:00 \\u2013 02:00","星期日: 12:00 \\u2013 02:00"]', 'https://jiang.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.32 上午', '2021-06-08 09.44.32 上午', 408, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 162, 1, '13695929', 'Leone Restaurant & Bar 台北餐酒館/中山餐酒館/約會餐廳/包場/酒吧/聚餐', '104', '台北市', '中山區林森北路353巷20號', 25.05699980, 121.52623340, '約會餐廳', '約會餐廳 部分服務人員態度很糟糕，希望只是個案。生乳捲超好吃? 店員親切熱情 酒好喝環境舒適空間很舒服\n\n東西好吃 大推燉飯！！很特別的特色料理\n適合朋友小小酌\n品嚐不一樣的口味～一進來就被這間店美到?服務員也很親切，謝謝??\n今天點餐的菜單，跟網路上眾多篇網誌（都是2020年間的）看到的菜單完全不一樣耶？現在的菜單內容跟選項似乎精簡了許多?不過對我這個選擇困難的人來說，倒是少了很多糾結的時間啦?\n食物的部分，我超喜歡西班牙燉飯??使用義大利米並保留了米心的口感，難得能在台灣吃到是真正的道地、不是台式的西班牙燉飯，超滿意?\n馬告炸雞也是很推薦的必點招牌菜，肉質軟嫩可口，吃起來比看起來還更好吃許多，是最完美的下酒菜?\n海鮮烘蛋吃得出來用料的實在，但是這道口感就真的很普，可能吃過更多比這個好吃的海鮮烘蛋吧，就沒有很推這道了?\n整體而言很喜歡這間，有機會一定會再回訪?', 4.8, 794, 3811, 'kk6NWSEg@gmail.com', '02 2581 3109', 4, '', 'https://www.facebook.com/Leone-RestaurantBar-101343618238577/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.32 上午', '2021-06-08 09.44.32 上午', 794, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 163, 1, '66362889', 'MUGI 木屐-市民大道餐酒館推薦|東區日式餐酒館|松山特色餐酒館|大安人氣餐酒館|必吃餐酒館|約會餐廳', '105', '台北市', '松山區市民大道四段137號', 25.04493730, 121.55050850, '約會餐廳', '約會餐廳 好吃可是吃不飽的店 ? 但是真的處理的很好。魚外酥內軟，牛肉熟度也都剛剛好！\nFood is good and sophisticated but pricey and small-portioned. Both the fish and meat were done perfectly.食材、風味跟環境都很棒，\n但價位稍高了一點，\n如果能在上菜時多分享一點菜色的構思，\n讓顧客清楚明白廚師的用心就更好了??忠孝敦化站步行十分鐘\n\n?赤蝦壽司捲$480\n?黑松露生牛肉沙拉$580\n?黑松露鮑魚蒸蛋$280\n?熟成奶油味增豬180g $380\n?酸奶鮭魚菲力160g $480\n\n每一道都精緻的好吃?第一道先上的松露生牛肉，白點點是白松露(單吃也超香) 生牛肉味道很特別  軟嫩好吃??\n\n第二道松露蒸蛋$280卻蠻大碗的，蛋裡有2塊扎實的小塊鮑魚、上面是松露跟魚卵！\n\n第三道味增豬超！嫩！三團橘色是南瓜泥?搭配起來很好吃、中和豬肉比較鹹的味道??\n\n招牌?赤蝦壽司捲登場?\n雖然我不愛海膽但幸好這道完全沒有海膽味道、明蝦味反而比較重、跟海膽、一點點醋飯完全完美的融合??\n\n最後上的是酸奶鮭魚排、煎的外脆內嫩的口感，酸奶很扎實的是酸奶?很像希臘優格?搭配起來很奇妙\n\n餐廳最裡面都是2人位居多、其他有吧台位跟在地球燈下的4-5人位一組而已！座位不多但蠻舒適的?晚餐時段5:30就有了?廚師只有一個就是了！店內還有提供各式酒喔（是餐酒館來著！十分推薦喔\n\n?可刷卡 有服務費\n??可電話網路訂位\n??17:30 - 23:30很棒的日式料理店，食材新鮮，但每道料理，都算層層挑逗味蕾。尤以味噌豬，肉扎實且美味。新式的創意日料\n沒想到日料的擺盤能像法式料理那樣美\n每一道菜都相當精緻 食材新鮮 真的好吃\n舉凡菜單到料理 細節都做出質感\n不過每道菜的份量都不多 但小酌墊墊肚子可以', 4.7, 76, 357, 'C21n8u7cBN@gmail.com', '02 2577 2020', 4, '["星期一: 17:30 \\u2013 23:30","星期二: 17:30 \\u2013 23:30","星期三: 17:30 \\u2013 23:30","星期四: 17:30 \\u2013 23:30","星期五: 17:30 \\u2013 23:30","星期六: 17:30 \\u2013 23:30","星期日: 17:30 \\u2013 23:30"]', 'https://www.facebook.com/MUGI.bistro/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.33 上午', '2021-06-08 09.44.33 上午', 76, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 164, 1, '22885810', 'Ulove羽樂歐陸創意料理', '105', '台北市', '松山區南京東路四段162號', 25.05127060, 121.55649430, '約會餐廳', '約會餐廳 服務人員非常的親切有禮！\n會主動的介紹並推薦餐點，對於我們提出的疑問也都超有耐心、滿是笑容地一一解答並時時刻刻的注意著我們的需求??\n\n?維也納麵包\n?匈牙利紅酒燉肉腸\n首先上桌的是維也納麵包和特別招待的匈牙利肉腸～\n招待的肉腸料理是帶有辣感的濃郁番茄口味，超級開胃！拿來搭配麵包也很適合！\n飯店等級的維也納麵包內層塗了甜甜的奶油醬、外層被烤的酥酥脆脆，是可以無限享用的唷！\n?酥炸牛肚\n?酥炸帆立貝\n驚！從來沒有吃過用這種烹調方式的牛肚和帆立貝！酥脆的外層下有著他們原始香氣已及軟嫩的肉質！再沾上店家自製帶有番茄塊的番茄醬實在很美味！老闆～我想多點兩份帶回家配著喝酒看電影?\n?和風花椒嫩雞沙拉\n深深擄獲我媽的心??\n新鮮的菜葉、舒肥雞胸肉攪拌均勻後入口先有麻油清香後有花椒香散開，口味十分特別！\n?西班牙油蒜風味蝦\n貼心的將蝦殼都已去除！\n啊啊啊～是我愛的濃郁蒜香與蝦香?\n吃完再用麵包沾取精華醬汁來食用超級鮮美！剩下來的時間我都在誓死捍衛最後僅存的醬汁捨不得讓他被收走！\n?法式羊排佐烏斯特醬汁 $718\n必點菜色之一！\n肉質鮮嫩的紐西蘭羊排配上自製的烏斯特醬汁還帶了點迷人的孜然風味！\n羽樂貼心的在羊骨處包上了鋁箔方便我們直接拿起食用！\n完全滿足我大口吃肉的爽快感！\n實在太正點了！\n?烏斯特豬肋眼燉飯 $328\n我和媽媽搶著吃的燉飯，深怕一個不注意就被對方吃光了！\n選用義大利米的燉飯保留了米芯的口感為整個餐點更提升了一個層次！\n西班牙豬肋眼軟嫩的程度更勝頂級牛肉！\n太令人驚艷！\n平時不太吃辣的我覺得小辣的選擇剛剛好，香而不膩！\n是絕佳的搭配！\n?提拉米蘇\n?比利時頂級巧克力\n?奇異果蘋果汁\n?綜合果汁\n100%現打果汁還可以客製的調整甜度與冰塊，去冰無糖的選擇微酸微甜配上甜點清爽解膩！特別喜歡羽樂的提拉米蘇是酒味偏濃的大人口味，我絕對可以再來三個！！??今天與友人吃了2組雙人套餐跟單點一些美味的食材?\n\n??如??\n\n（?雙人套餐?主餐選2+$618）\n?麵包x2\n?羽樂小點x2\n?開胃小點x2\n?甜點x2\n?飲料x2\n\n?主餐\n\n爐烤小管櫻花蝦墨魚燉飯$368\n小管咬下的瞬間就知道，絕對是新鮮的料理，鮮味很重，鮮甜可口，櫻花蝦則是提出整體香氣與味道的層次，吃完牙黑黑啦?\n\n干貝鮮蝦海膽燉飯$388\n米芯的硬度可調整，小干貝、鮮蝦、海膽醬、鯡魚卵、杏?菇、花椰菜，非常豐富，燉飯的味道很鮮，也超濃郁，超棒?\n\n香煎豬肋眼佐照燒奶油醬汁$548\n原來豬也有肋眼部位！！而且舒肥過的豬肋眼肉質超粉嫩?這道菜讓我超驚豔！！\n\n法式羊排佐烏斯特醬汁$718\n肉質超嫩，一點羊騷味都沒有，沾烏斯特醬整個層次再升一個檔次?\n\n蜜鴨胸金沙奶油細扁麵$348\n鹹蛋黃的料理超喜歡?，麵條整個吸附醬汁，有肉有菜，整體讓人口水直流，食慾大增啊?\n\n?羽樂小點\n酥炸牛肚、酥炸帆立貝、松露起司薯條、墨西哥辣椒起司球\n\n?開胃小點\n金沙松露起司烘蛋、西班牙油蒜風味蝦、軟殼蟹酸辣沙拉、檸檬煙燻鮭魚\n\n?甜點\n比利時頂級巧克力、茅屋香草起司、、黑糖波士頓派、提拉米蘇\n\n?飲品\n泰式奶茶\n玫瑰紅棗茶\n綜合果汁\n奇異果蘋果汁\n拿鐵\n\n????????????\n一直想來朝聖許久了，終於有時間來到Ulove羽樂歐陸創意料理，店內的環境很舒適溫馨，用餐環境劃分好多區域，有四人座位區，多人聚餐區，沙發包廂區，而且沙發區還有設計門簾（想要隱私的人非常適合??）店內是可以容納到80人，並備有專業投影及影音設備，這裡用餐氛圍相當棒，暖黃色燈光讓人有回到溫暖的家感覺，牆面上簡單佈置且大方優雅，木質調的色系搭配咖啡色系坐椅，時尚設計用餐心情很愉悅廚房也很講究，完全媲美五星級飯店廚房的規格。開放式的廚房，可以看到廚師們認真料理的模樣，也能見到食材料理安心的過程。\n\n以愛為名及為家人製作餐點為出發，Ulove羽樂歐陸創意料理的菜單封面上可愛的雙寶，就是老闆的寶貝-羽樂、亮亮，意指輕易的就能獲得快樂，還有耀眼的帥氣亮亮?也是對女兒、兒子的祝福。可見父親對雙寶濃濃的愛完全投射在這家餐廳中??\n\n?老闆有位名人姐姐?林依晨\n餐廳整體也讓我覺得零負評??\n但會讓我更喜歡這間店原因是，不僅僅只是有我喜歡的女神加持，而是這間餐廳的真誠服務與餐點打動了我，就是有種”回到家用餐”的自在感，在這用餐你會感覺到他們對每位客人的重視，來到這樣令人感到放鬆舒心的環境，是近期最能令人感到療癒的瞬間了?服務和用餐環境都很好，餐點也都很精緻，搭配套餐可以吃很飽\n唯一不足是出餐速度太快，如果可以一道吃完再上下一道會更好，收餐盤的速度也有點趕，莫名會吃的很急..\n主廚招待餐點，像是杏鮑菇跟德國香腸佐紅醬，中規中矩\n附贈小圓麵包，可以一直續，剛烤好的麵包抹上甜甜的奶油醬，雖偏油，但真的很涮嘴，推\n燉飯，店員雖然點餐時會問是否要軟一點，但感覺口感還是偏硬，是可以再做調整，但似乎是米本身的關係，無法像台灣米可以煮軟，店員也有建議可以改點麵條。另外，這間的奶油白醬吃多會有比較膩口的感覺\n羊排，因為沾醬是牛骨製的，所以改成了綠胡椒醬汁，吃起來就是綠咖哩的味道?羊肉口感偏有嚼勁，帶些微羊騷味，除了有點油之外，蠻推的\n小點的部分大多都中規中矩，比較驚艷的：碳油鴨胸沙拉，碳香味很足，就像炭火剛燻完一樣，鴨胸很嫩，推\n炸物，附贈義式蕃茄醬?鱈魚條，搭配多力多滋的餅乾，有點奇怪?松露起司薯條，沒什麼起司的味道，中規中矩，但份量蠻多的\n甜點，提拉米蘇，蛋糕底部有酒，所以會比較濕，上頭的粉撒的很足，整體吃起來味道很香濃\n綜合果汁，奇異果味道很重，顏色是蔓越莓居多，好喝\n當月壽星有免費小蛋糕，像是橙香口味的磅蛋糕，整體偏甜帶點沙沙的糖顆粒感，中間有一坨奶油\n結帳時有送一箱綜合米果，因為老闆有在做公益?服務的細心程度真的在水準之上 雖然都是小事情 卻讓人都覺得很窩心 第五顆星是被服務填滿的 ????\n從接電話裡面的改人數\n到現在場的點餐\n店家的主動拍照\n上菜\n主菜（有因為滿桌的菜放慢上主菜的節奏）\n詢問口味合適\n甜點\n給照片（貼心的ps)\n飲料（飲料可能需要在加油）\n離開（每位外場服務人員都有小心提示哪裡要小心）\n飯的口味讓人有印象甚至會讓我下次去再點飯\n至於麵我覺得就普普而已（麵這方面可能需要再加強）計劃要幫朋友慶生 ?\n網路訂位被安排在小包廂裡面座位很舒服！\n餐點超爆好吃服務也很親切 ~\n朋友不小心打翻喝一口的飲料還幫我們送上全新的 ?\n甚至還能合照拿到店家的精美小卡 ?\n壽星太害羞所以婉拒服務生唱生日快樂歌的服務 ?\n會想要推薦給有經營美食帳的朋友分享 ?', 4.7, 2118, 9955, 'R4AvKIXjOs@gmail.com', '02 2578 2656', 2, '["星期一: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期二: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期三: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期四: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期五: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期六: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30","星期日: 11:30 \\u2013 15:30, 17:30 \\u2013 21:30"]', 'https://www.facebook.com/ulovebistro/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.34 上午', '2021-06-08 09.44.34 上午', 2118, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 165, 1, '96089221', 'Mosun 墨賞新鐵板料理餐廳', '106', '台北市', '大安區忠孝東路四段155號2樓', 25.04174260, 121.55001040, '約會餐廳', '約會餐廳 好吃到主菜忘了拍?服務很好！墨賞集團旗下除了鐵板燒\n還有知名的艾朋牛排餐酒館\n觀望這一家鐵板燒料理很久了\n這次和家人一起來吃生日餐\n文末整理生日優惠資訊給大家參考\n想嚐鮮的人可以挑生日月來吃很划算\n家庭聚餐、生日、浪漫約會都很適合\n生意很好建議要訂位再前往\n所有的座位都環繞著鐵板\n每一區也都有專門的服務生\n不用擔心因為位置差異\n都能享受到同等的桌邊服務\n服務親切也會提醒哪一些餐點可以免費續\n師傅會視每個人的用餐情況準備餐點\n我用餐速度偏慢\n我覺得這一點很貼心也不會讓人有壓力\n當天我們五人用餐被安排在小包廂\n非在外面的大公共區\n我們跟另外一組五人併在一間\n包廂很舒適也更有隱密感\n吃完正餐會另外帶位到甜點區享用甜點飲料\n甜點區的位置也很好聊天\n補足了吃鐵板燒因位置的限制不方便多人聊天\n很多客人吃飽就坐在甜點區聊天\n這一點店家想得很周全\n同時也能空出鐵板燒位置輪替給下一組客人\n料理的部分比較多道口感心得放在下面\n-\n??花尾蝦海陸套餐(+主餐任選一) $1480+10%\n\n??太平洋野生花尾蝦北海道干貝\n選擇了其中一個海陸套餐多了這一盤多了$400\n一般主餐價位$1080+10%我覺得就很值得了\n這一盤我覺得沒有值得這個價位\n蝦子沒有很鮮甜這一道可以pass\n\n??白蘭地櫻桃鴨胸\n有點這道可以欣賞點火秀真的很壯觀\n鴨胸的份量也給的很多最後還吃不完打包帶走\n熟度也剛剛好這道也很推薦\n但整體而言主餐以外的配餐更令人驚艷\n相較之下墨賞的主餐顯得比較弱一些\n\n??法式手工餐包(可以免費續加)\n因為怕佔胃空間就沒有再續加\n這一道只有拍到奶油忘記拍了麵包\n麵包若怕之後吃不下也可以打包帶走\n附上奶油搭配熱騰騰麵包很對味\n\n??開胃菜-松露魚子醬蒸蛋\n蒸蛋搭配松露醬調味再加上鵝肝慕斯\n最後放了魚子醬超級奢華的蒸蛋\n搭配雞蛋造型可愛又好吃\n\n??開胃菜-南洋冰卷&胡麻燻鴨胸\n平常比較少吃到這種料理\n當開胃菜相當合適\n貼心設計好一口份量不膩口\n\n??洛神花Sorbet(可以免費續加)\n搭配料理很解膩順口還可以續加\n\n??蔓越梅醋\n點套餐才有的蔓越梅醋\n好喜歡這道醋酸甜恰當可惜不能續\n\n??松露酥皮海鮮巧達湯\n大家筆記??這一道很常賣完\n電話訂位的時候可以先預訂\n酥皮真的超級酥脆\n是我吃過最喜歡的酥皮\n濃湯的口味我很喜歡用料十足\n\n??松露百菇沙拉\n師傅菇炒得很入味就連單吃也好吃\n再拌上滿滿的松露醬香味四溢\n這一道非常推薦也是必點之一\n\n??季節鐵板炒飯-櫻花蝦炒飯\n炒飯的口感粒粒分明還有搭配魚卵\n以及櫻花蝦提味鹹度剛剛好\n\n??冰淇淋熱蛋糕\n也是為了可以看點火點這一道?\n以前看有抹茶口味的\n可惜那時候用餐時沒有只有原味的\n蛋糕因為高溫過後有點焦焦得更好吃\n但冰淇淋融化速度快也要趕快吃完\n\n??伯爵熱奶茶\n飲料的部分中規中矩但不會太甜\n搭配蛋糕做一個完美的ending\n-\n?生日優惠\n當月壽星本人八折再加送生日蛋糕\n但需要事先兩天前預約\n並告知店家要使用優惠\n要注意若遇上特殊節日(情人節、聖誕節、除夕含初一初二、父親節、母親節等不適用此優惠)\n要記得避開選在前後幾天使用優惠唷\n-\n喜歡我的食記分享歡迎追蹤 ig?00.loveeat服務周到，鐵板師傅專業，東西很好吃喔！之前鐵板燒吃夏*尼居多，今天第一次吃墨賞，因價位跟型態相似，故以兩家做比較。\n以餐點製作來說，兩家都很用心，食物很美味，主廚的互動感比夏*尼更好，且很關心顧客用餐狀況。\n但以服務人員的服務品質來說，似乎沒有像王*集團一樣達到標準化（個人經驗從H*t7到夏*尼的服務態度都非常細心跟貼心，其價格上差異反映在餐點跟餐具等物質面），倒不是說一定要顧客至上，但至少不要讓顧客感到服務上的差異，例如今天服務人員幫鄰旁兩側的客人介紹餐點，卻遺漏中間的客人沒介紹，做為第一次去吃的人不免感到困惑。\n另外夏*尼的服務人員會在可以續用的餐點吃完時主動詢問客人是否需要再續用，例如櫻花蝦炒飯等，但貴店續用卻是經廚師善意跟其他桌的客人提醒時才碰巧得知（不過這可能貴店方式跟夏*尼不同，畢竟我們是第一次去吃）。\n以上因個人較重視服務品質，如有過苛評論請見諒，謝謝。\n另外貴店入內時僅量體溫沒有主動以酒精消毒手部，且每組客人用餐間距顯有不足（可以參考台北車站微*商場的防疫措施），近來本土疫情再起，還請貴店多注意。龍哥王子美食介紹：眾多藝人一致?讚的鐵板料理，全台十大料理好吃貼鐵板，布置優雅漂亮美麗.趁著生日優惠八折趕快來享用，今天品嚐明蝦干貝海陸餐：1開胃菜：共兩道一道是一口干貝和一口鴨胸肉，吃在嘴裡瞬間開胃很美味2法式手工麵包：裡面裹上奶油口感極佳，內用無現享用3酥皮海鮮湯：外層的酥皮酥脆美味，裡頭濃郁好喝有鮮蝦和蛤蜊與魚片4主餐：花尾蝦鮮嫩彈牙，干貝入口即化口口入味，鴨胸軟嫩順口七分熟吃起來剛剛好5焦糖布丁：甜又順口好吃極了6抹茶飲料：好喝又甜讓人喝的很順暢就是太小杯了7水果沙拉：有番茄還有鳳梨與芭樂和葡萄，新鮮又好吃，8櫻花蝦炒飯：料多味美也是無限供應，總共吃了10道菜真的很飽又高興哦！', 4.4, 2862, 12593, 'fqspKW@gmail.com', '02 2731 9966', 3, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 23:00"]', 'http://www.mosun.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.35 上午', '2021-06-08 09.44.35 上午', 2862, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 166, 1, '46220369', 'L先生義法餐廳', '105', '台北市', '松山區民生東路四段131巷21號', 25.05922950, 121.55349710, '約會餐廳', '約會餐廳 米其林的價位，普通的體驗\n前菜、麵包跟湯口味還算不錯\n主餐加價580的牛排又小又沒味道，最好吃的是旁邊的薯條，整體來說不如加價180的戰斧豬排\n甜點部分只有法式千層讓人驚豔，其他檸檬塔、提拉米蘇跟加價100的舒芙蕾實在一般\n另外水也是每次開瓶都要另外收費\n整體用餐感受相當普通整體口味偏向義大利料理，用餐環境很有義式家庭餐館的風格相當舒適，餐點也相當好吃，非常值得前來一試！\n------\n再訪\n麵包跟甜點覺得不太行，降為四星結婚9週年光顧，對的起其價位的美位，戰斧豬排是招牌，老婆很滿意，我點羊排，很嫩而且完全?羊騷味，頭一次吃過那麼嫩的羊肉，前菜，甜點等也都好吃法式餐廳 真的是每道菜都是照進度來\n一開始先是麵包+牛肉泥\n開胃菜（鮮蝦慕斯-口味覺得還好）\n湯（玉米濃湯）\n主餐（牛排）\n甜點（千層，這是我覺得最好吃的）\n飲料\n吃下來真的是蠻飽的\n價位分成兩種，差在開胃菜可選兩個\n有些餐點是要加價的很喜歡他們的主餐和餐前麵包 擺盤精緻 今天是去慶生 有個愉悅的晚上???', 4.4, 797, 3507, 'WlkxXR@gmail.com', '02 8770 5505', 3, '["星期一: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期二: 休息","星期三: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期四: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期五: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期六: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期日: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00"]', 'http://www.monsieur-l.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.35 上午', '2021-06-08 09.44.35 上午', 797, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 167, 1, '07426941', 'ChiaoDuo House巧?洋房', '106', '台北市', '大安區仁愛路四段345巷5弄17號', 25.03849820, 121.55483500, '約會餐廳', '約會餐廳 地道的義大利菜??\n\n來慶生就是想瘋狂的點一下，沒錯！就這樣我們瘋狂的吃完一頓大餐，重點是很好吃...\n\n1.套餐麵包搭配油醋，味道簡單美味，剛好空腹要喝酒，來點麵包很好，上面的酥片更是畫龍點睛。\n\n2.套餐香檸油醋沙拉，雖然看似簡單，但卻格外用心，放了3-4種不一樣的生菜、酸豆還有義式風乾的番茄，點綴了了這一切。\n\n3.套餐南瓜濃湯，店員很貼心提醒了濃湯偏濃，因為用了許多蔬菜還有南瓜燉煮直接打成泥狀，上面的鮮奶油則是打成泡泡狀，讓奶油更能融入濃湯（酥片是麵包上的）。\n\n4.薄片菲力生牛襯芝麻葉，我的媽呀！超開胃～牛肉、芝麻葉、起司、酸豆所搭配出來的層次，真的很美味。\n\n5.北義波隆納肉醬特寬扁麵，帶有一點麵心的寬扁麵，被肉醬包覆著，完全入味！肉醬不單單只有肉醬，含有蔬菜的味道，讓整個肉醬麵味道更加豐富，下一道很驚人...\n\n6.牆上的特別菜單「鴨肝栗子燉飯」，本人就是愛好鴨肝、鵝肝...所有肝類，所以我非點不可，重點好吃到無可挑剔，有點米心的燉飯，味道濃郁不散漫，勇往直前的衝進我的口中，栗子香氣、甜味緩緩地散出，再來是鴨肝的鹹味與油脂，讓燉飯更有異國風味，amazing ！！！\n\n7.米蘭風味「燉羊膝」，很常吃牛膝的我，也做過燉牛膝，但今天看到羊膝，也是忍不住點了下去，羊膝旁的玉米糊加了牛奶及胡椒、香草燉煮，好吃到傻眼，重點那只是在羊膝旁的配菜，重點來了！豐富蔬菜燉煮出來的羊膝，軟爛中帶有一點點肉感，沒有羊的騷味反而多了很多香料及蔬菜甜味，還有...骨髓可以吸～真的很好吃啦！\n\n8最後套餐的提拉米蘇，壽星也獲得加大份量，還有點綴及蠟燭，也幫我們播了生日快樂音樂，欸欸欸...甜點也不馬虎...好好吃！濕濕潤潤，淡淡酒香～收復了濃濃的前菜主食的飽足感，超棒的收尾....\n\n大推！我打這麼多就是因為很好吃ㄇㄟ～\n\n真的啦！！！！！已經不知道回訪幾次，是一家吃了會非常幸福的餐廳，尤其工作疲憊時去吃，能瞬間回血。把身邊的人都帶去一輪了，似乎該貢獻個評價，畢竟自己也是看評價才能發現這家寶藏餐廳。\n-\n先說結論：全部餐點沒有雷，只有好吃跟超級好吃，可以盲點\n\n私心最愛濃郁起司寬麵，翻照片發現有拍照的共4次，最近新歡還有鴨肝栗子燉飯\n\n不太會評論食物，用海量的照片來表示真的回訪很多次了。\n-\n外觀像娃娃屋一樣可愛，裡面是非常溫馨舒適的用餐環境，闆娘非常熱情，時不時招待小點心，每道菜上來會介紹餐點。\n\n+150/300可升級套餐，套餐的口味精緻，個人覺得這邊份量偏少一份餐吃不飽，升級套餐超值又管飽，適合小資選擇\n\n壽星預定時說明有送蛋糕\n\n人多時上菜慢，用餐時間約1.5-2小時\n-\n因為疫情現在外帶有八折，依舊美味不減，本來要去過生日但無法出門，外帶備註後還是送我蛋糕了，疫情中煩悶生活的小確幸一進店裡就有進入異空間的感覺\n氣氛曲風驟變\n彷彿來到義大利某間小餐館 小而精緻\n剛入坐的我已經開始期待餐點了?\n果然不負眾望 並遠遠超過期待！\n太開心台灣有這麼一間隱藏美食（門口並不起眼）\n尤其是羅馬風味豬肉捲 濃濃的香料味 酥脆的外皮 內裡軟嫩肥瘦均勻的豬肉 真的豪豪吃啊～\n雖然是道地義式料理 不過我一吃進去直覺聯想的是印度風味?不多說 去吃就懂了！\n喝南瓜濃湯彷彿在吃一整顆南瓜 鮮甜自然\n麵包跟沙拉就正常發揮\n起司義大利松露麵前幾口會覺得很好吃 但慢慢會有點膩 只能Share很難獨享\nPINK燉飯是以甜菜根為主軸 明顯的香氣跟甜味 配上新鮮的貽貝跟干貝 我個人非常喜歡 清爽 海味 （但同行友人就還好 我覺得甜菜根本來就風味明顯 喜歡的喜歡 討厭的討厭 見仁見智）\n甜點是我最愛的提拉米蘇\n而且是我最愛的作法：手指餅乾+咖啡酒+馬斯卡彭起司 完美?不是用鮮奶油?\n還有他們家special的沙巴雍\n帶有酒香及蛋香 配上微酸的切細水果 口感綿密細緻 有點像厚實濃郁版的奶泡\n\n完美Ending?\n\n價位乍看小貴 但比對他們家的氛圍 服務 食材 我覺得這價錢非常可以?\n只是每道菜都是主廚堅持原則慢工出細活 所以大家要做好‘等待’的心理準備\n店裡客人稍多 這頓飯2小時起跳從套餐的麵包開始就一路令人驚豔到最後的甜點，南瓜濃湯的部分不加水全靠南瓜與蔬菜的甜味撐場先100分，主食的鴨肝栗子燉飯也是超好吃的，每粒米都入味到不行，單點的羊膝燉的軟爛以外完全沒有羊騷味，搭配的玉米糊也是一絕，與羊膝互相扶持又不會搶走對方風采，最後的甜點提拉米蘇是個極棒的收尾，再來是店內氣氛好到不行，親切的闆娘與其他人員，真的很有一種“歡迎來我家作客吃飯”的溫馨感，絕對再訪再訪??衝著4.8分的好評來的，一試之後果然值得五分評價！\n前菜麵包和沙拉都有水準之上，南瓜濃湯調味很特別，沒有一般南瓜湯的甜膩和過重的奶味，灑上粗粒黑胡椒提味，算是我喝過最喜歡的之一。\n點了肉丸番茄義大利麵和波隆那肉醬寬麵，原本擔心醬汁口味太相近，沒想到兩種口味完全不同各有特色：肉丸麵醬汁溫順，像吹著南義大利溫柔的風，吃下去有種幸福的感覺，肉丸多汁不死鹹，搭配起來很棒。肉醬寬麵帶有炒過的肉末，口感紮實，香氣很夠味。而兩種麵條都偏硬非常義式。餐後提拉米蘇也好吃，咖啡個人覺得普通，算是可接受。另外餐廳內桌數不多，空間舒適，服務生只有一人但會仔細追蹤每桌的用餐進度，上菜很順，不需久等。\n總之整體推薦，會想要再回來嘗試其他菜色。', 4.8, 495, 2376, 'P1lcgdK@gmail.com', '02 2778 3347', 4, '["星期一: 休息","星期二: 17:30 \\u2013 23:30","星期三: 17:30 \\u2013 23:30","星期四: 17:30 \\u2013 23:30","星期五: 17:30 \\u2013 23:30","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 23:30","星期日: 11:30 \\u2013 14:30, 17:30 \\u2013 23:30"]', 'https://www.facebook.com/chiaoduohouse1123/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.36 上午', '2021-06-08 09.44.36 上午', 495, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 168, 1, '20792537', 'Merge taipei', '106', '台北市', '大安區大安路一段51巷34號', 25.04297500, 121.54752800, '約會餐廳', '約會餐廳 用餐環境舒適，餐點精緻，推薦豬五花、牛小排，沒吃過Merge別說你是老饕美味精緻的饗宴！\n東區低調奢華新選擇適合聚會、食物精美優秀餐廳?', 5, 7, 35, 'UCu1B1@gmail.com', '02 8771 8750', 4, '["星期一: 休息","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'http://www.merge.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.37 上午', '2021-06-08 09.44.37 上午', 7, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 169, 1, '36763230', '布娜飛比利時啤酒餐廳 Bravo Beer 台北市民店（聖誕啤酒月曆）', '105', '台北市', '松山區市民大道四段185號', 25.04476500, 121.55192800, '約會餐廳', '約會餐廳 市民店的用餐空間共有兩層樓\n有著落地玻璃，可以讓陽光灑落\n喜歡戶外空氣，門前也有露天座位\n\n室內的一、二樓都是用餐空間\n二樓靠窗的沙發座，氣氛浪漫\n桌子跟座椅都比一樓多\n兩人約會或多人聚餐都很適合\n另外二樓有一個吸煙空間\n四周有玻璃門圍起\n\n比利時啤酒就有高達200多種\n店內還可以抽水煙\n\n比利時精釀啤酒的歷史悠久\n技術十分純熟\n不限於一般麥花的風味\n連香料或是水果等都能入酒\n除了有瓶裝的啤酒，也有3.6公升派對型\n或是杯裝、特調類\n\n?雙人分享餐\n?三人同享餐\n?四人樂享餐\n?六人共享餐\n\n??比利時精釀啤酒\n??比利時生啤酒\n??特調啤酒\n??特調水煙\n??普切塔\n??湯\n??一起早午餐\n??好蛋捲捲\n??手工窯烤披薩\n??義式半月派\n??義大利麵\n??義式燉飯\n??精緻排餐\n??比利時啤酒淡菜鍋\n??拼盤\n??共享大拼盤\n??薯條\n\n?蔬活系列\n\n?沙拉\n?湯\n?蔬活早午餐\n?義大利麵&燉飯\n?披薩\n\n??點心\n??搖滾冰沙\n??無酒精飲料\n??氣泡蘇打\n??奶茶\n??咖啡\n\n?美式酥炸大拼盤\n\n新月薯、海鮮捲、洋蔥圈、法式炸魚塊\n美式炸雞翅\n\n份量蠻多，約4~6人分食\n海鮮捲有獨特的風味，不會油膩\n雞翅鮮嫩多汁，餅皮外圈厚內圈薄\n帶有酥脆口感，還算不錯\n\n?比利時生啤派對組\n\n建議由左至右飲\n\n?豪格登\n\n在台灣算是知名度最大\n的比利時啤酒了，喝起來非常爽口\n尾韻有著蜂蜜的清香\n\n?布魯日傻子金\n\n生啤啤的酒花韻味較強烈\n清爽的麥香口感，十分順口\n帶有淡淡的果香味，相當舒服好喝\n\n?草莓NO.1\n\n最受少女歡迎的No.1草莓生啤酒\n被評為全世界最好喝的水果酒\n酸酸甜甜的，呈現少女的粉紅色\n\n?地中海式海鮮濃湯佐手工麵包\n\n有蛤蜊、花枝、魚片、蝦子\n濃稠的濃湯，入口偏酸，卻十分開胃\n\n?手工窯烤披薩–布娜飛\n\n使用石頭磚窯來烤製\n餅皮皆是手桿自製，口感薄脆\n披薩口味眾多，此款布娜飛為經典款式\n以蕃茄醬為底\n鋪上培根、洋蔥、酸豆、羅勒\n中間再打上生雞蛋\n份量適中，配料豐富\n\n整體而言\n環境走美式風格，適合多人聚餐\n餐點種類繁多，口味大致普通\n客人只有兩組，但服務人員沒有主動點餐\n有待加強下午三點多來用餐，服務人請我們到靠窗落地位置用餐。餐廳乾淨明亮。人員親切，因為我們是小桌子，店員注意到我們點了比較多食物，主動提供了一張小桌子方便我們擺餐。非常貼心，然後食物用完之後，也貼心的奉上了濕紙巾。在與客人互動及尊重上保持了非常合宜的適當距離，用餐體驗非常舒適。雖然僅有我們一桌客人，但我們卻能夠滿滿感受到他們服務的熱忱及專業。後續點了一個布朗尼，我想要一杯熱茶，但店內沒有單點選項，不過他們卻很大方的提供了一杯熱茶，雖然也許之後各位看到並不一定會有這樣的優惠，但這杯茶的體貼是讓我願意在google評論上留下五星的評價，這件事情對我來說十分暖心，食物也是可圈可點。謝謝你們，這次的用餐體驗真的很讓人開心。??環境不錯，服務人員也親切，可惜美中不足的地方是，匹薩烤的太硬和上面的料太鹹柳橙和百香果啤酒，喝起來都是溫和順口的味道您好,\n我們想反映今晚10/10號在貴分店台北市民大道分店的用餐經驗. 起初貴分店的氣氛和服務讓我們認為是個好地方. 但之後的餐點品質實在無法領教. 第一, 淡菜的肉質實在很貧乏,店長解釋為這是紐西蘭進口淡菜. 但我們的餐飲經驗告訴我們這不是真的. 為了不影響用餐氣氛我們也未再追問. 第二, 軟殼蟹上桌時溫度偏溫經店長解釋應為烹飪流程問題且願意上新的,但我們因已食用且是有煮熟的未再要求要上新的,只建議要改善. 店長把軟殼蟹拿去烤箱加熱，但並沒有把下面盤飾生菜拿起來，以致於考完之後整個軟殼蟹變的溼溼軟軟的！我們已經不想再進行第二次的反應，只好把這種食物吞下，但口感實在是太糟了！！！第三, 招待的焗田螺還是溫的, 經店長解釋是說因為用”冰”啤酒調理所以吃起來是溫的. 這個解釋讓我們非常生氣, 畢竟有基本烹飪常識的人都會認為這是非常不符合邏輯的是. 感覺像是要打發奧客的感覺.\n\n結帳時更不愉快的是,當我表示之前在台中大遠百用餐時料理品質並非像我們今天所體驗的, 店長反說他在台中分店待過且大遠百關三年了. 不知道貴公司文化是否視之前優良的品質為過往爾不思考現在狀況以致店長階級有此反應.\n我們雖然到貴餐廳用餐機會不多且常為外帶比利時啤酒. 但今日體驗讓我們深感到貴店用餐只是被當奧客自取其辱！！！\n\n一些意見僅供參考, 我們只期待貴公司能夠加強訓練. 畢竟貴司的比利時啤酒是台灣有些獨有的. 我們也希望常有愉快的飲酒體驗', 4.6, 1374, 6320, 'uSicw3CzY@gmail.com', '02 2570 1255', 2, '["星期一: 11:30 \\u2013 00:00","星期二: 11:30 \\u2013 00:00","星期三: 11:30 \\u2013 00:00","星期四: 11:30 \\u2013 00:00","星期五: 11:30 \\u2013 00:00","星期六: 11:30 \\u2013 00:00","星期日: 11:30 \\u2013 00:00"]', 'http://www.bravobeer.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.38 上午', '2021-06-08 09.44.38 上午', 1374, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 170, 1, '28847075', 'Elfin Restaurant & Lounge 精靈餐酒館 包場/尾牙/餐廳/酒吧/宵夜/春酒', '106', '台北市', '大安區復興南路一段107巷5弄21號', 25.04433000, 121.54529000, '約會餐廳', '約會餐廳 氣氛很好欸《昂昂昂昂昂昂～～～～燉飯好吃。除了照片好拍，胖弟跟調酒師Nicky都熱情??\n食物不是只是拿來擺設是真的好吃\n尤其是那個牛排?跟干貝義大利麵\n跟朋友們3刷的店家價格偏高，很有氛圍的餐酒館\n不適合拍照。有四人套餐跟雙人套餐\n燉飯蠻好吃的，牛排也算不錯。生日跟朋友來這裡用餐！點了雙人套餐！\n雖然食物份量不是太多 但東西都很好吃\n裡面的店員跟吧台的姊姊們人都超好的???', 4.4, 953, 4193, 'QgGLcuY@gmail.com', '02 2731 0102', 2, '', 'https://www.facebook.com/elfintaipei/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.38 上午', '2021-06-08 09.44.38 上午', 953, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 171, 1, '09661845', 'Muzeo Gastronomy&Draft', '106', '台北市', '大安區忠孝東路四段170巷6弄14號', 25.04080760, 121.55011150, '約會餐廳', '約會餐廳 趁著疫情在家裡 好好整理前陣子 吃到很用心的餐酒館\n大推等級?????\n用心等級?????\n餐廳環境???（燈比較暗不太好拍照\nYummy等級????? ??太好吃了\n\n————\n\n14天鴨胸肉$690\n肉質軟嫩 沒有鴨的味道處理的很好\n可能是因為厚厚的口感比較好 滿有嚼勁的 愈嚼愈香 多汁 旁邊的白醬是用蘋果泥製成的搭配 咖啡濃縮 甜菜根\n-\n芋泥油封鴨$290\nCp值最高\n真的必點之一\n搭配松露芋泥 油封鴨腿炸金針菇 沾取鹹蛋黃醬汁 絕配啊～絕配 真的 完美搭配 醬料味道很濃郁 另人食指大動哈哈哈?\n-\n三色海鮮千層麵$480\n踩過無數遍雷到爆的千層麵終於有一間讓我覺得值得可以再訪了，感覺完全是餐廳自己的招牌菜 搭配白醬青醬紅醬 麵皮Q帶有韌性 好入口 點一份大家一起分食\n-\n蟹堡（好像沒有在菜單上 ，可以詢問店員還有沒有 ，有隱藏菜單\n蟹肉風味濃郁 麵包烤的好脆好脆\n-\n開心果提拉米蘇?絕對必點$280\n以前對於提拉米蘇都是巧克力是標配\n是堅果粉絲的必點啊啊 不會太甜 膩口 奶油不會太多 溶於口中 好好吃啊啊啊啊?\n\n整體來說 值得再次來訪 吃吃看每一次的隱藏菜單以及他們的調酒 好喝到爆法義麵包籃 $190：自製法義式乾麵包，略嚼勁但不難咀嚼，淡淡麵粉香氣撲鼻，沾上特製豚油醬著實開胃。\n\n酪梨蝦 $280：清爽酪梨醬上灑可可脆片增加口感，配於酥脆蝦餅與鮮蝦，沙拉盤般相當爽口與清新的口味。\n\n特製調酒 ~$250：提供相當多的創意酒單，也可以單點紅.白酒一杯。當天喝了一杯帶有茶香的酸甜調酒，非常喜歡！\n\n火烤透抽沙拉 $290：青木瓜絲上點綴火烤的透抽，搭配西瓜冷湯與芥茉美乃滋，口味偏清淡，是開胃小點。\n\n芋泥油封鴨 $290：傳統料理芋泥鴨，使用創新的包覆手法，酥炸的外皮像是湯包般的包裹著濃厚的芋泥與鴨肉，底部是松露芋泥醬，香味四溢，鹹蛋黃醬汁增加整體味覺，層次豐富。\n\n辣味燉豬 $330：墨西哥辣醬炒豬肉丁，肉質偏肥，是個人比較不喜歡的一道，但一旁的豬皮被炸得吹彈可破，稍帶油膩感，搭配四川花椒巧克力，口感算獨特。東西好吃，服務態度好，上菜速度快，餐點是精緻風格，份量都不多喔！\n\n雖然價格偏高，但店內氣氛很舒服，讓人有種放鬆的感覺。很適合兩人約會或是三五好友聚餐～\n\n開一瓶紅酒搭配著吃，非常推薦！雖然只有週末有提供午餐服務，但是我真的要說，他們的餐點實在是用心又美味。而且物超所值，我不是單純說食材，而是他們對每一道菜所下的功夫，還有對客人的服務水準，絕對遠超過所收餐點價錢！看了許多壞評幾乎都提到上餐慢、服務糟糕的體驗??我們也是第一次嘗試這家餐廳，沒有特別期待但最後的結局是晚間後身體一直產器氣..... 且很臭??? 可能是料理中有比較容易產氣的食物?\n提早訂位可能比較合適安排一個舒服的用餐區，六點訂位進餐廳後馬上有兩位服務生接洽量體溫的事宜。\n餐從點餐到結帳都蠻順利的，上菜有順序不會像台灣餐廳把做好的菜放上桌擠滿了桌面，滿意。\n第一道菜我們都非常喜歡，雖份量小但很適合當開胃菜！起士非常的美味！番茄很新鮮！泡沫酸酸的也很好吃，有趣的組合搭配。\n溫野菜非常好吃，很多蔬菜綜合一起口感特殊，還有半生熟蛋混在一起很美味，擺盤漂亮。\n鴨肉普通，份量小。燉飯味道我個人不喜歡，有一個說不出的怪味。\n推薦朋友聚餐但吃不飽，會再訪！\n\n唯一缺點洗手間不太乾淨，需要多巡視洗手間衛生整潔........！', 4.4, 343, 1509, 'xBbORNXEB@gmail.com', '02 2752 5299', 4, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 11:30 \\u2013 21:00"]', 'https://www.facebook.com/muzeotaipei/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.39 上午', '2021-06-08 09.44.39 上午', 343, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 172, 1, '96498467', 'ABV Bar & Kitchen美式餐酒館 美式烤肉 漢堡 牛排 鄉村菜 美式餐廳', '106', '台北市', '大安區敦化南路一段236巷16號', 25.04040890, 121.54767920, '約會餐廳', '約會餐廳 昨天6個大人享用了3人套餐也是非常有飽足感，每道菜的份量都不少。\n1.回魂醬沙拉很清爽並不會太辣。\n2.水牛城辣雞翅微辣好吃。\n3.炸雞鬆餅的炸雞酥脆多汁，鬆餅熱熱吃超好吃，希望可以推出單點鬆餅的系列，大推。\n4.原木烤牛胸非常大一塊，肉質並不會很柴，沾取酸甜的醬料很好入口，醬料會不知不覺就沾完。\n5.白醬烤半雞的醬汁不錯吃，但不是義大利麵的那種白醬，即便是雞胸的部位也很好吃。\n6.唯一比較不喜歡的是蟹肉餅，可能跟放涼有關係，海鮮的味道有點重，不太喜歡，僅個人口味意見。\n7.甜甜圈吃起來很像炸濕吐司，口感還不錯，飯後有個甜點結尾，非常完美。\n\n想要小酌的朋友，這裡真的是大推，上百種各國啤酒任君挑選，如果不知道要喝什麼也沒關係，冰箱前都會有服務員為你介紹並推薦，謝謝昨天為我服務的捲捲頭店員，不確定是不是店長？不好意思忘記看你叫什麼名字?\n服務非常好，擔心推薦我的啤酒不好喝，還過來詢問我味道如何，並且詳細介紹啤酒的成份，謝謝你?服務員介紹的很詳細，而且不誇大不實，雖然我們6個人還是建議先點3～4人套餐，吃不夠再點，結果我們真的吃蠻飽不用再加點，然後推薦的也都很好吃！啤酒超多樣也超好喝?楓糖炸雞鬆餅早午餐（290）：\n沙拉：搭配特製沙拉醬不錯吃，但醬汁份量，嚐幾口後，剩下的沙拉都沒沙拉醬了。\n薯條：非常普通，無任何特色。\n炸雞腿：外酥但炸過久，導致外皮有焦苦味，使得餐點主題印象下滑；肉質硬，未軟化肉質，咬下後會有種柴柴無味道的感覺。這道菜點的敗筆在於主食，外皮與肉質的敗筆，導致對這道菜點印象不佳，若有下次，並未會選擇此店家用餐。美式風格的早午餐\n#明尼蘇達薯球牛肉派\n碎牛肉培根起士奶油醬\n搭配脆脆的薯球\n有灑些微青蔥。\n搭配麵包片一起享用?\n\n#舊金山牡蠣歐姆蛋\n有培根洋蔥煎的恰恰?\n#鮭魚煙燻卷\n不敢吃生魚片的我\n也能嘗嘗了\n跟一般不同的是\n壽司是反著包的方式\n沒有油膩的味道,吃起來也滿爽口的。推薦品嚐。\n\n喜歡喝啤酒的有福了\n店內有300多種不同種類口味的啤酒,可以讓人喝到盡興\n\n我最喜歡的位子是\n有點像半戶外的位子\n畢竟年紀大了\n喜歡坐亮一點的位置?\n店家中午12點營業到凌晨1點\n還沒有限用餐時間喔?已踩點三家ABV主題餐廳，沒有踩雷過，\n食物都很有特色、美味，非常適合跟朋友聚會，\n也有不同人數的套餐可以選擇，每次都飽到不行。\n服務人員的態度都非常好，店面的位置也都距離捷運站不遠，真的是很棒的餐廳！?(?????????)?\n當日用餐生日贈送了布朗尼冰淇淋，真的好好食！', 4.6, 392, 1803, 'NviwNoR@gmail.com', '02 2721 6067', 4, '["星期一: 12:00 \\u2013 01:00","星期二: 12:00 \\u2013 01:00","星期三: 12:00 \\u2013 01:00","星期四: 12:00 \\u2013 01:00","星期五: 12:00 \\u2013 01:00","星期六: 12:00 \\u2013 01:00","星期日: 12:00 \\u2013 01:00"]', 'https://www.abv.com.tw/americano', '0.jpg', '[0.jpg]', '2021-06-08 09.44.40 上午', '2021-06-08 09.44.40 上午', 392, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 174, 1, '89376499', 'Jimmy''s Kitchen', '106', '台北市', '大安區仁愛路四段77號號 B1', 25.03821570, 121.54742280, '約會餐廳', '約會餐廳 2015-09-17（個人評價：４分；滿分：５分）\n\n前菜沙拉採取自助式，去拿完沙拉回來後，湯品跟麵包都放在桌上了。\n\n湯品是法式洋蔥湯，味道真的是很濃郁，可能是吃過最濃郁的洋蔥湯了；至於麵包?個人不太喜愛，因為餐刀切不開，會碎屑掉滿地，份量倒是蠻扎實的，好不容易吃完一個，服務生馬上問要再來一個麵包嗎？\n\n還有附上餐前酒及主廚招待的小點，味道不錯。\n\n主餐點了美國腓?米濃牛排，配上培根包覆的腓?、五分熟的鮮嫩，真的是超級好吃阿！\n\n切開不帶血絲的肉片，也證明了師父高超的技術；而配菜部分也有加熱過，不像有些餐廳的配菜又冷又硬難以入口。\n\n現在沾美也提供了慶生活動，就是一堆服務生圍著你唱生日快樂歌，加送一個蛋糕。\n\n網路上有人說來這裡用一次晚餐，聽到不下十次的生日快樂歌，真的是一點都不誇張，今晚我也聽了８次?我覺得琴師應該最熟的就是生日快樂歌了吧！\n\n沾美是一間非常老式的牛排西餐廳，有蠟燭、有餐前酒、有琴師、有昏暗的燈光及木質的裝潢，還有著年紀不算小的服務員，貼心又溫暖，屬於溫馨的用餐場所，如果價錢再便宜點就更好了ＸＤ一訪時食物跟服務態度都非常好，要帶家人二訪該店在訂位時，一開始接電話的女生服務非常好，我有15位大人2位小孩其中一位要兒童椅另一位放推車，小姐也重複資料沒問題，並問請問要幾點？中午11:30開始都可以選，我回覆我在網路看你們中午分兩段時間，小姐回覆現在沒有了，我們會14:30撤餐可以坐到15:00，我們因此選了12:30，小姐很客氣的留完資料說因為你們人數比較多，我請我們經理看一下！結果電話交到一位聽起來有年紀的男士，一開頭就很不耐煩的說：你們到底有幾位？小孩是要不要兒童椅？\n我重新告知了一次，結過他又重新問了我多少人訂位資料等等，最後說你要幾點？我說我們12:30，接下來他就非常不客氣態度很差（像教訓人似的）的說：我們沒有12:30！我們11:30就開始了，你12:30來後面人吃什麼訂也訂不到位置，我們老闆現在說了不給訂12:30！我說那如果晚一點呢？他還是態度非常差的說，晚是要多晚，我們沒再分兩場，你們要嘛就11:30來！剛剛小姐才來兩天什麼都不懂！你們要不要！\n態度咄咄逼人非常差！\n\n我知道貴點生意好人氣旺，對談間我也遵守電話禮儀，但貴店這樣的電話態度讓人實在不敢恭維，一訪時還拿到現場經理給的貴賓卡，要我們多多捧場，但準備二訪時只是在電話訂位時就遇到這樣這樣，也就無法再訂下去了！首先，從一開始進入餐廳，就能夠感受到賓至如歸\n服務生會幫你開門，幫你拉開椅子並且鋪上餐巾在你的腿上\n真的是很特別很精緻的服務\n餐廳有現場鋼琴伴奏，真的很優雅很舒服！\n\n從介紹菜單開始，就能感覺到服務生真的非常專業！介紹的很詳細！這邊的服務生年紀都偏大，感覺已經在這邊服務很久，非常專業有水準！\n\n餐前雞尾酒（沒有酒精）真的很好喝，但就是不知道能不能續約就對了！\n麵包很軟很軟，塗上奶油真的很想再多吃幾個，但因為怕吃不下主餐就沒有加點（可以加哦服務生很親切會問你要不要再來一個）\n沙拉的部分可以自行拿取，要吃多少吃多少，項目種類也非常多！\n主菜的部分我們點了美國菲力以及鵝肝牛排！兩項都非常好吃！美國牛的確跟服務生介紹的一樣，比紐西蘭牛再軟嫩順口一點！鵝肝單吃比較油一點，配著牛排吃更順口！\n飯後還有飲料可以選，服務生會先詢問你要喝什麼幫你餐後上！水果跟點心一樣是自助式可以自己拿，品項也很多很豐富吃得很開心！\n\n那天因為穿的比較少的關係有點冷，詢問服務生想要一杯熱水，結果服務生給了我一杯熱茶，真的超級貼心感動的！\n\n很特別的是只要有人生日，現場伴奏都會彈奏生日快樂歌！很特別很溫馨！下次生日會想要去那邊慶祝！\n\n整體的體驗真的很棒！謝謝那天貼心的服務生以及好吃的餐點！經友人推薦前往\nJimmy’s kitchen有別於飯店的buffet\n接待人員親切殷勤，會常常巡視照顧每一位客人\n餐點樣式較少但樣樣用心製作\n有多久沒吃到有鮮甜味的蝦子了\n新鮮好吃吃了一大盤\n許多飯店重量不重質，呈現的蝦早已流失甜味\n炙烤區也是師父一塊一塊烤成...一點點的鹽足以帶出炙烤的香氣及牛肉芬芳\n\n要說不完美之處有吃到一塊生魚片還沒完全退冰（有冰沙感）不過生魚片大致上可圈可點，新鮮鮮甜\n餐後甜點這塊較弱\n有些品項有點太不道地了..例如提拉米蘇底層是海綿蛋糕，很甜很膩，其他如烤布蕾布朗尼這類好發揮的甜點也只是還可以接受的等級，沒有冰淇淋也稍嫌美中不足\n\n總體而言主食滿意，副食較弱\n但仍然是會回訪的好店\n給予五星評價\n疫情結束後必定再次到訪非常復古的西式牛排店，中午不提供排餐主要是自助餐，從2021年5月1號開始漲價成一人$920另外還會加一成服務費。餐廳的空間位置非常的狹窄，跟其他桌子的位子都靠得很近，自助餐區的動線也比較狹窄，只能大概兩個人並肩就是極限。前菜有蝦子、蝦鬆、蟹肉棒、鮑魚等等，如果離走道比較遠的餐點服務員會幫忙夾取。日\n壽司有6-8種與生魚片種類大概5種，另外還有煙燻鮭魚。主食部分有羊排與爐烤牛排，都是由廚師當場現切現烤的。熱食種類以台式為主，另外有一些焗烤、薯條等等。蔬菜類有生菜沙拉，內容物跟一般台式的牛排自助吧差不多，熱食區還有一兩道蔬菜，選擇不多，不過大家主要都是以肉食為主。湯品則是玉米濃湯和四神湯。\n值得推薦的是甜點，完全勾起小時候的古早味蛋糕回憶，有藍莓蛋糕、黑森林巧克力（櫻桃罐頭+布朗尼）、還有蜂巢蛋糕，每一種都很樸實但是吃入口都驚為天人。\n飲料區以冷飲為主，如果需要熱茶或熱咖啡服務員會送上桌，如果事先跟餐廳說有壽星的話會有蛋糕，店員會為在桌子旁邊一起唱生日快樂歌，蛋糕還蠻大的大概六吋，唱完歌以後服務員會幫忙裝在盒子裡讓你可以帶回家。', 4.5, 3266, 14697, 'df2koj0qy5@gmail.com', '02 2711 7750', 3, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30"]', 'https://www.facebook.com/%E6%B2%BE%E7%BE%8E%E8%A5%BF%E9%A4%90%E5%BB%B3-184832631544950/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.42 上午', '2021-06-08 09.44.42 上午', 3266, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 175, 1, '83041724', '房間餐酒 The Room Bistro', '106', '台北市', '大安區敦化南路一段160巷56號', 25.04349520, 121.54673840, '約會餐廳', '約會餐廳 1. 滷味拼盤 $400\n美味度：?????\nCp值：?????\n??拼盤的份量不大，但食物的口味都不差，另外可以選一種沾醬，那天點了店員推薦的松露醬，覺得很棒！\n\n2. 風味炸雞腿 $250\n美味度：?????\nCp值：?????\n??肉蠻多汁的，外皮很酥脆！\n\n3. 歐洲貴妃椅上的高潮迭起\n美味度：?????\nCp值：?????\n??荔枝味是主體，味道有點偏甜，但覺得整體喝起來對於我這種不會喝酒的人來說還可以接受，酒精濃度不會太高，不太會喝的人可以試試這款\n\n店內氣氛很酷，但唯一的缺點是音樂太大聲太吵，跟朋友們講話要講比較大聲?以情趣為主題的餐酒～\n每個地方都有精心設計過\n小細節很多～還有特別設計的房間\n但菜單需要服務員幫忙介紹\n不然會有點不知道怎麼點\n每杯酒跟菜名的名字特別設計\n服務員非常細心回答\n是個跟朋友開心聚會又有小情趣的地方整體環境乾淨，本來期望蠻高的，但覺得普通。\n菜單設計好看但超級不容易看，配色有些在那樣的燈光下完全看不清楚，非常吃力。\n價位偏高，壽星是招待shot一杯沒有食物，建議網站需要修改，邊邊座位被忽略數次，雖然最後有被換到吧台。店員友善，豬耳邊酥語蠻特別的有嚼勁。整體評價中間普普，不好也不差，嚐鮮可以來。但是不會來第二次。還不錯，很有特色的酒吧，如果有什麼關於情感或情慾的話題，帶來這邊可能也比較能聊得開\n\n不過來帶我們到座位的姐姐似乎心情沒有很好，也沒有跟我們解釋一下精緻像時尚雜誌的菜單該怎麼點。旁邊一桌的就有服務生主動介紹菜單\n\n除了這部分之外，其實整體而言很棒，食物也非常好吃，又有設計感。還能去看小展覽室，很有趣的體驗\n\n滿推的～希望台北可以多開這種特色的店～被安排在角落位子很常被忽略\n時常要點餐找不到服務人員\n官網上的生日shot及餐點也是到現場才說已經結束只剩shot一杯（官網並無寫出活動時間）\n價位高 菜單設計好看但不實用看的很累\n餐點普通 酒偏甜 同等的價位相信東區有其他更好的選擇', 4.6, 295, 1357, 'ouRmp0z@gmail.com', '02 2773 1665', 4, '["星期一: 17:30 \\u2013 01:00","星期二: 17:30 \\u2013 01:00","星期三: 17:30 \\u2013 01:00","星期四: 17:30 \\u2013 01:00","星期五: 17:30 \\u2013 02:00","星期六: 17:30 \\u2013 02:00","星期日: 17:30 \\u2013 01:00"]', 'https://www.theroombistro.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.42 上午', '2021-06-08 09.44.42 上午', 295, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 176, 1, '02207889', 'am Daily', '106', '台北市', '大安區忠孝東路三段217巷3弄22號', 25.04294500, 121.53860200, '約會餐廳', '約會餐廳 飲料cp值超低，其他還不錯蛋餅好吃~晚餐時間前來用餐，客人不多\n餐廳氣氛很好，環境也很乾淨整潔\n墨魚義大利麵的用料新鮮、實在\n就是要當心嘴巴會黑黑的\n手工麵包本體風味口感都不差，不過沒有提供油醋醬是一個敗筆\n餐點擺盤精緻，賣相一流\n不過男士們用餐的份量可能稍嫌不足\n直接放棄嘗試餐酒：被價格嚇一跳而卻步了用餐環境舒適，空間雖不大，但很適合喜歡歐式現代的人。午餐點了個簡單的肉醬義大利麵，口感濃郁，麵條硬度適中，微辣，下次會再來試其他餐點。餐點極小份又貴，服務生愛理不理，限時2小時但餐點卻等1個多小時，從11點半點餐等到1點才送來，老闆說會送甜點補償，我們1點半就要回去上課，1:15分都還沒送來，結帳時問老闆娘能不能改外帶，結果她直接說沒內用就沒有噢，沒服務可言還收服務費^^，這餐每人快800塊，所有人都吃不飽還一肚子氣，歡迎各位盤子惠顧～～', 4.3, 902, 3879, 'o42YZEFj4k@gmail.com', '02 2775 5909', 2, '["星期一: 11:30 \\u2013 21:30","星期二: 11:30 \\u2013 21:30","星期三: 11:30 \\u2013 21:30","星期四: 11:30 \\u2013 21:30","星期五: 11:30 \\u2013 21:30","星期六: 10:30 \\u2013 21:30","星期日: 10:30 \\u2013 21:30"]', 'https://www.facebook.com/amDailyTaipei/', '0.jpg', '[0.jpg]', '2021-06-08 09.44.43 上午', '2021-06-08 09.44.43 上午', 902, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 177, 1, '57441403', '在室。甜點｜En vie P?tisserie（週三只接受預約、預訂）', '10491', '台北市', '中山區新生北路二段149巷26號', 25.06199090, 121.52850780, '甜點', '甜點 2021.4.5.於新位址再訪\n\n恭喜店家找到新的地點，洗手間空間一樣用心。不過座位間距都很靠近隔壁客人，人數一多會非常嘈雜。加上此次光顧的客人層次較不一，除了有聊天大聲以外，還有在座位大聲使用平板觀看網路節目的客人。店家如果能稍微留意場控會讓想好好品嚐甜點的客人覺得貼心。\n\n回到餐點的部份，這一次海綿水果蛋糕的海綿部份非常地乾，外層的鮮奶油也拯救不了它的口感了。相當可惜。\n\n2019.5.19.\n\n地點離捷運站非常近，很好找。\n有室外的座位，在雨遮下方而且可以遮陽。\n室內環境非常乾淨舒適，雖然店面小小的，不過座位的擺放經過設計，也可以容納不少人。\n\n甜點都有中上的水準，不收服務費，咖啡也很好喝。而且服務態度非常非常的親切。可以看得出來主廚對於甜點的要求是龜毛的。餐巾紙以及咖啡杯都印有店家的Logo或店名，對於這種小店是蠻罕見的用心。反轉蘋果塔有一點可惜的地方是，蘋果的香味被蓋住了，失去了自然的原味。\n\n另外一個用心的地方是洗手間，乾淨以外，還有在各處放上標語。比方說馬桶坐下來的視線正前方就寫著衛生紙可以沖進馬桶裡。\n\n是一家可以再光顧的好店。在室內享受一室悠然的在室甜點店，主廚總是在常見的味道中添加巧思，樸實卻不平凡。\n\n甜點部分，奶油乳酪系列總是恰如其分的結合所有的食材，所以我特別推薦有乳酪或奶油的產品。\n\n乳酪蛋糕是我覺得台北最好吃的！冬天則極推巴黎草莓！近期推出的肉桂捲也非常美味！檸檬塔也深獲許多友人的讚賞！！價位偏高但聖多諾黑有點令人失望，感覺裝飾好後放了好一陣子，焦糖都有點融化了，飲料也沒有值得那個價，只是環境蠻舒服的甜點十分好吃，年初吃到巴黎草莓就覺得驚豔，前幾天吃了蘋果塔也覺得很不錯，上頭的蘋果偏甜，但搭配餅皮卻很剛好。\n小小的店家很舒服，很適合悠閒地坐一下午。舒服的小店座落在捷運中山國小站附近，供應精緻的手工甜點，點了蒲公英跟蘋果塔兩樣甜點味道都很不錯，蒲公英酸酸甜甜的，味道特別，蘋果塔香甜帶有蘋果香，搭配下面的塔皮很好吃，兩樣甜點都不會太甜，咖啡部分也很好喝，拿鐵香醇，喝起來很順。\n\n店內用餐如果沒有客滿就不會特別限時，下次會想再過來試試其他口味的甜點。\n\nLovely dessert shop near MRT Zhonghan Elementary school, delicious pastries and great coffee latte, comfortable dining environment.', 4.6, 253, 1164, 'CBXnS4gi@gmail.com', '02 2562 7865', 2, '["星期一: 休息","星期二: 休息","星期三: 13:00 \\u2013 19:30","星期四: 13:00 \\u2013 19:30","星期五: 13:00 \\u2013 19:30","星期六: 13:00 \\u2013 19:30","星期日: 13:00 \\u2013 19:30"]', 'https://lin.ee/T6jJU5V', '0.jpg', '[0.jpg]', '2021-06-08 09.45.00 上午', '2021-06-08 09.45.00 上午', 253, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 179, 1, '37999373', '宙斯果霸-大安店 Zeus Bar- 大安推薦果汁|推薦甜點店|網美甜點店|人氣冰品|必吃冰店|深夜美食推薦|冬季熱飲|場地租借', '106', '台北市', '大安區大安路一段2號', 25.04490160, 121.54609520, '甜點', '甜點 我們點了一份雙人套餐，三明治表現普通，但冰品、飲料?表現很不錯，用料大方。\n\n那時候是冬天去的，草莓盛產的季節，草莓?相關的飲品都很棒很好喝很濃郁。\n\n珍珠非常好吃，屬於Q彈有嚼勁，我們同行六人都很喜歡。\n\n茶品，茶香足，喝起來不會像在喝水。\n\n當時的menu我們拿到的有少了幾頁，有向店員告知。啊哈哈因為我們為了點餐，朋友之間還鬥嘴了一下?????\n\n—————\n更：感謝?店家即時回覆，我才發現剛剛星星給錯顆，三明治?生菜?可以用大片的，番茄?厚一點（若沒有的話可以增加，爽口度會提升）、洋蔥?多一點。本來只想分享好吃的店\n但是這家是我不會再去\n內用照理說應該是用馬克杯\n卻給每位客人外帶杯\n就像手搖飲那樣\n先不論價格\n杉林溪包種茶應該是要放陶瓷壺和陶瓷杯才對\n沒有陶瓷壺\n也要有玻璃杯吧\n更何況是點熱的...\n竟然是塑膠杯....??（沒什麼好拍）\n\n問了店員一般熱飲不是應該用身後的紙杯嗎？\n回應是：這是外帶用的?\n\n舒芙蕾是蠻大份的\n價格算親民\n還至少有好看的刀叉和盤子\n感覺平衡點\n\n講句公道話\n在東區...\n這家店的價格已經算便宜\n是親民的～\n\n但.....\n\n店內妹妹的服務態度實在讓人很扣分???\n態度很差！晚上10點，依舊很多人在裡面用餐\n很多人在裡面聊天喝飲料\n雲朵系列蠻好喝\n哈密瓜雪花冰好吃\n單人杯有點小但是份量ok\n環境有點吵雜，不適合約會但適合聚會除了人手問題\n服務態度及餐點我都覺得很棒尤其是薯條\n今日如往常一樣 休假前一晚選擇到貴店用餐耍廢\n因套餐臨時變更 有價差疑慮\n店長直接多給薯條 薯條真的很好吃\n這個處理方式我給一百分先提及結論：老闆明顯安排人力不足\n請務必要改善，補足人力或不應接這麼多單\n\n1/21晚上十二點，店內僅有兩名店員\n點餐時店員親口說明要二十分鐘的餐點\n結果都超過一小時以上\n更不用說原先說明是半小時的舒芙蕾了\n除了現場客人很多之外\n居然還有很多外送單\n老闆拜託一點，有沒有在關心店的狀況啊？\n這不是好機會嗎？\n怎麼搞的大家都被雷...\n\n我是本桌最不認為速度很慢時，要一直催店員的客人\n（等了一個半小時的舒芙蕾）\n我非常有禮貌的過去說：不好意思～\n「請問舒芙蕾做了嗎？如果還沒可以取消沒關係」\n店員回我：已經做了\n\n又等了十分鐘後\n\n我過去再次有禮貌的說（真的是有禮貌不是反諷）：\n「不好意思～太久了我們想改外帶」\n店員居然很兇的回說：「怎麼樣！！」\n「什麼東西要改外帶！！」\n然後很用力地掀開布簾走出來拍桌看單\n\n我不知道你是被其他人惹到還是怎麼樣要對我發火\n今天不管是店員對顧客\n還是顧客對店員\n我覺得那種態度都是對人的 不 尊 重\n如果你認為我現場上述有什麼不正確的描述\n請你提出\n如果是純粹拿我這個看似好欺負、有禮貌的客人出氣\n基於尊重你欠我一個道歉\n\n真的等太久了大家明天還要上班\n並不是我們愛改外帶\n何況我的語氣行為都非常有禮貌\n\n再次重申，如果老闆認為晚上時段僅需兩人力\n請限制接客數、接單量\n或是在一開始就說明可能會等到快兩小時\n讓消費者選擇要不要消費\n\n差評到不能再差評\n\n喔對了還有一份雞塊沒有送上來呢\n真的沒那個時間跟你等或再次對話\n怎麼知道你會不會下次是翻桌', 4.1, 339, 1390, '9QeWiX@gmail.com', '02 2721 7661', 4, '["星期一: 11:00 \\u2013 03:00","星期二: 11:00 \\u2013 03:00","星期三: 11:00 \\u2013 03:00","星期四: 11:00 \\u2013 03:00","星期五: 11:00 \\u2013 03:00","星期六: 11:00 \\u2013 03:00","星期日: 11:00 \\u2013 03:00"]', 'https://www.facebook.com/pg/zeusbar777/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.01 上午', '2021-06-08 09.45.01 上午', 339, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 180, 1, '84984753', '絆 - kizuna 洋?子', '104', '台北市', '中山區松江路132巷28-1號', 25.05286180, 121.53061370, '甜點', '甜點 平日午餐$300，咖啡或紅茶自行續杯，餐點有附當日甜點\n\n回覆店家：可能是我記錯了！詢問友人，當天都有拿到甜點，不好意思！！水果鮮奶油蛋糕好吃！濃郁但不膩口！蛋糕好吃，裡面包的水果很新鮮，鮮奶油很恰到好處，茶也好喝，看了舊文裡指著比日式蛋糕卷遜色的發言，覺得比喻的不倫不類，老實說日本的蛋糕好吃程度也是參差不齊，更何況每人主觀喜好不同，有人愛甜有人不愛，這家店的蛋糕已達各方面的平衡，各方面都很滿意。水果蛋糕捲很棒，尤其是採用的鮮奶油。餅乾也不錯，自己品嘗或送禮都合適。親子友善，水果茶滿滿新鮮水果超滿足，蛋糕卷每種口味都好吃，值得再訪', 4.4, 150, 660, 'bo2KUlqn@gmail.com', '02 2581 9688', 2, '["星期一: 11:00 \\u2013 19:00","星期二: 11:00 \\u2013 19:00","星期三: 11:00 \\u2013 19:00","星期四: 11:00 \\u2013 19:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 休息"]', 'https://www.facebook.com/kizuna.tw/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.02 上午', '2021-06-08 09.45.02 上午', 150, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 181, 1, '09108335', '初米咖啡 Choose me Cafe&Meals', '10491', '台北市', '中山區錦州街423-1號', 25.06037420, 121.54231830, '甜點', '甜點 曾經很喜歡，但後來幾次覺得貴店只值「1顆星」！\n接電話態度極差，愛理不理的，口氣極度不耐，我是欠你錢嗎？\n連到現場取預訂餐，對客人的回應完全不應答，怎麼是有障礙嗎？是不是該去耳鼻喉檢查一下聽力和聲帶呢？\n另外，造型蛋糕實在是愈來愈「迷你」，現在小到連一個小朋友的掌心都不到，看到真是讓人傻眼貓咪，這家店以後完全不會想再訪或是有任何消費，態度差，食材也退步萬千，慘！#店員態度極差\n#前一天有電話訂位他們沒登記到\n#訂位電話有錄音他們也不肯聽\n#硬說他們系統沒有就是沒有\n\n上週五我們約了一位坐電動輪椅的障礙者隔天要訪談，上網找到此家咖啡廳，週五打電話訂位說要訂“明天下午2點”，並強調兩次，也與店家確認是否有無障礙設施。隔天（週六）下大雨到咖啡廳時，店家說我們沒有訂位不讓我們進去說已經沒位置，我們再三強調前一天早上有打電話過來訂位，服務人員一直說他們系統上顯示沒有，是我們錯了，後來我才想到我的通話都有自動錄音，要直接撥訂位錄音檔給服務人員聽，他也完全不願意聽。不清楚這究竟是個案還是這家咖啡廳員工訓練完全不到位，不需要客人至上，但至少處理事情時應該釐清事情經過，確認責任歸咎於誰，想辦法解決問題，而不是一味推卸責任，覺得自己不可能錯，一定是客人錯，哪有這種道理？後來想安排我們到地下室的位置，但我們有坐輪椅的人，怎麼可能走樓梯到地下室？即時有再硬喬一樓位置出來，我也不會想繼續待在這。我想我不會再去這間咖啡廳了，也誠心建議打電話去訂位的人要再三與店員確認訂位資訊，否則就只能吃下這樣的悶虧了。柴犬及哈士奇的造型蛋糕可愛也好吃。餐點上菜時間很快，餐具是自助式的拿取。第一次來，店員不怎麼熱絡是真的，商業午餐感覺是加熱的表面豐富便當，就跟菜單上一樣，有點失望，坐在店裡吃微波便當很不舒服，除了主食跟蛋，其餘配菜淡淡無味，難怪一餐廳感覺熟客都沒人點商業午餐跟著同學來到了「初米咖啡」，辦起了小型的同學會，附近停車沒有很方便，我們選擇榮星花園停車，本來沒有保持太大的期望，隨著甜點一道道的端上，漸漸能感受到這家小店的溫暖與用心。\n\n坐位讓人放鬆，因為前一個行程人多又吵雜的行程，心情被喧囂的環境搞的有點煩躁，稍坐片刻，吃完甜點後，與同學閒聊，心情得到平復，單純而美好的幸福時光。', 4, 980, 3920, '2QV56r@gmail.com', '02 2503 3386', 2, '["星期一: 10:00 \\u2013 21:00","星期二: 10:00 \\u2013 21:00","星期三: 10:00 \\u2013 21:00","星期四: 10:00 \\u2013 21:00","星期五: 10:00 \\u2013 21:00","星期六: 10:00 \\u2013 21:00","星期日: 10:00 \\u2013 21:00"]', 'https://www.facebook.com/ChooseMeCafe', '0.jpg', '[0.jpg]', '2021-06-08 09.45.03 上午', '2021-06-08 09.45.03 上午', 980, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 182, 1, '05160268', 'PAUL 法國保羅麵包 (仁愛店)', '106', '台北市', '大安區仁愛路四段107號1F', 25.03838710, 121.54977460, '甜點', '甜點 檸檬蛋糕好吃!生意很好電話打好久源自1889的品質之家\n發展至今已遍及全球\n道地法國美食的代表品牌\n延續至今已經超過130年了\n以麵包、甜點、早午餐聞名世界\n\n歷經五個世代，秉持著傳統精神，\n使用自已契作的鄉村冬麥品種的麵粉\n並遵循著古法，以自然的原料\n造就完美的麵包與法式甜點\n耗費工時製作每一個麵包\n堅持使用市場最佳品質的食材\n講求深度的法國美食\n\n整體空間並不大\n但氣氛營造類似於法國的沙龍\n帶著優雅的歐洲風格\n分為內用及外帶的入口\n\n?茶飲\n?咖啡\n?其他\n?酒品\n?日安巴黎\n?煎蛋卷\n?巴黎瘋可頌\n?套餐組合\n?前菜\n?沙拉\n?自家烘焙料理\n?今日湯\n?花都小酒館料理\n\n?甜點時光\n\n?甜蜜加價升級\n?經典雙人午茶套餐\n?豪華雙人午茶饗宴\n\n?現做甜點\n\n?香草烤布蕾\n?香蕉巧克力法式吐司\n?莓果蜂蜜法國吐司\n\n?甜點時光\n\n?晶鑽飛舞\n?鳶尾花巧克力\n?芋泥千層派\n?草莓芙蓮閃電泡芙\n?濃情花芋\n?草莓芙蕾雅夏洛特\n?紅寶石天堂草莓蛋糕\n?草莓奶酥塔\n?老奶奶手工檸檬蛋糕\n?經典提拉米蘇\n?巧克力千層派\n?草莓千層派\n?皇家檸檬塔\n?皇家巧克力塔\n?融榛果閃電泡芙\n?海鹽焦糖閃電泡芙\n?巧克力閃電泡芙\n?大馬卡紅\n?馬卡紅\n?可娜麗\n?黃金藍莓塔\n?寶石紅莓塔\n?諾曼第蘋果芙朗塔\n\n?豪華雙人午茶饗宴\n(經典小品*2+甜蜜小點*2+\n輕鹹食*2+甜點*2+飲料*2)\n\n?試吃麵包\n\n服務人員還端上熱呼呼的麵包\n搭配上奶油和草莓果醬一同食用\n\n?馬卡紅\n\n馬卡紅不指定口味會附上兩個\n外皮微酥一抿就碎，酥脆又輕盈\n輕咬一口，入口即化\n\n?瑪德蓮\n\n口感略為濕潤，不會過於甜膩\n\n?珍珠糖粒小泡芙\n\n口感鬆鬆軟軟，糖粒帶有脆脆的口感\n\n?輕纖水果杯\n\n開胃小點心，水果會依季節做變化\n內有繽紛的新鮮水果塊\n草莓、奇異果、鳳梨搭配甜蜜糖水\n酸酸甜甜，十分開胃\n\n?大西洋燻鮭三明治\n\n柔軟吐司夾著燻鮭魚、蕃茄與生菜\n大西洋燻鮭魚搭配蔬菜，清爽帶有飽足感\n麵包軟香、燻鮭鹹香有味，好吃\n\n?迷你三明治第戎春雞\n\n法國勃艮地第戎(Dijon)地區的名菜\n口感鬆軟有咬勁，雞肉不會乾柴\n\n?迷你洋蔥烤雞肉鹹派佐青蔬\n\n鹹派內餡是以傳統的方式製作\n以鮮奶油、雞蛋、起司做為主要餡料\n搭配著洋蔥與芥末烤雞肉\n兩塊扇形的鹹派放在一塊\n派皮酥香、內餡豐富滋味濃郁\n鹹香帶著濃郁的起司味\n還有洋蔥的自然甜味，清爽無負擔\n\n?草莓千層派\n\n酥脆千層派皮刷上了一層糖\n上面再撒上糖粉\n中間夾了卡士達醬，草莓微酸而解膩\n酥脆的派皮搭配甜而不膩的卡士達醬\n非常推薦\n\n?香蕉巧克力法式吐司\n\n新鮮的香蕉與香濃的巧克力搭配\n酥脆鬆軟的吐司塊，一同食用\n\n?芒果冰沙\n\n香甜可口的芒果，製成冰沙一同食用\n冰冰涼涼，十分清爽，有些過甜\n\n整體而言\n餐點種類繁多\n無論是主食下午茶應有盡有\n裝潢古典華麗，帶有法式浪漫風情\n非常適合姐妹們一同共度午茶時光不誇張\n每年我的生日一定會買一顆Paul的蛋糕\n#草莓夏洛特\n跟\n#老奶奶檸檬蛋糕\n都是我的最愛?\n\n這次跟姐妹們約來Paul仁愛店午茶\n大食怪的我們\n點了滿滿一桌\n有鹹食有甜點\n兩個胃同時都滿足\n\n店內店外\n也非常的好拍\n真的讓人有來到巴黎的感覺\n唯一不同的是台灣服務好100倍\n有去過巴黎的人一定都懂?\n\n另外Paul的新品\n#草莓芙蕾雅\n好吃到要落淚\n裡面的香草內餡香純濃郁卻不會過甜\n真心大推大家去試試看！\n（我今年的生日蛋糕就決定是它了??）\n\n然後還有一件很驚艷的是\nPaul居然連鹹食也非常好吃\n紅酒燉牛肉、肉醬麵、起司拼盤\n都好吃的讓人意猶未盡\n\n我們四個女生\n總共點了四個主食、四個蛋糕、一份豪華午茶組\n全。部。掃。光\n\n然後已經在期待下次的再訪了\n立誓要把菜單都吃一遍?\n\n- - -\n\n歡迎推薦任何美食餐廳給以希?\n?IG:rainy.tw\n?Blog:tiara.tw曾經很喜歡裡面的派，最近也開始喜歡可頌大部分的員工們都素質低，好離譜\n進門後七八個員工在面前各自做自己的事，現場只有我們兩個客人，我們站著看他們多久才要接待客人進去座位。\n\n更好笑是上來的餐點還是錯的，真是太優秀了...\n\n餐點吃一半後，我站在甜點櫃前挑選甜點，看完沒有人來服務點餐，還有個員工在我面前的甜點櫃後用著手機，可以考慮開除了吧？我想要花錢買個甜點沒人要來幫忙處理，再次重申現場沒有其他客人等候喔。\n\n如果單純想跟朋友一起喝喝下午茶聊天，不在乎餐點難吃服務態度很差的話，還是可以來花冤枉錢。', 4.2, 1841, 7732, '4VKW7I@gmail.com', '02 2771 3200', 2, '["星期一: 08:00 \\u2013 19:00","星期二: 08:00 \\u2013 19:00","星期三: 08:00 \\u2013 19:00","星期四: 08:00 \\u2013 19:00","星期五: 08:00 \\u2013 19:00","星期六: 08:00 \\u2013 19:00","星期日: 08:00 \\u2013 19:00"]', 'http://www.paultaiwan.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.03 上午', '2021-06-08 09.45.03 上午', 1841, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 183, 1, '34138844', '12:59 Design Studio-松山東區早午餐|延吉街美食推薦|特色下午茶|必吃美食|小巨蛋美食推薦|小巨蛋義大利麵', '105', '台北市', '松山區八德路三段74巷61號', 25.04581720, 121.55257440, '甜點', '甜點 環境好氣氛佳，餐點分量多，真的很適合來放鬆心情！\n今天3/7早上去吃brunch,都沒人超安靜！\n點了金沙舒芙蕾鬆餅跟野生菌菇歐姆蛋拼盤，都很好吃，但我對舒芙蕾還停留在甜的階段，哈哈！\n\n大推紅茶，真的超好喝，因為不起眼，都忘了給他來一張照片！是間和髮廊結合的餐廳，但屬於餐廳（地下室）的位置十分的少（3個四人座、兩個桌子超小的雙人座），當天去的時間剛好，只是先擠了小位置的雙人座，後來還是忍不住想要換到大桌子（不然吃得根本放不下）。\n（友人表示沒有很喜歡這種狹小的空間。）\n\n餐點的部份，義大利麵還不錯，就是松阪豬吃起來活下山豬肉（太有嚼勁了＠＠）；歐姆蛋的部分蠻可以的，只是配料的香腸和培根實在太鹹；飲料的部分，就是可爾必思的可爾必思，和中規中矩的黑糖歐蕾（快喝完時才發現以為是杯緣子的貓咪其實是攪拌湯匙?）\n\n付款部分，因應現在科技化的時代，無法行動支付（至少有apple pay吧）和無法刷卡的店家，確實都會扣分（應該是3.5顆星）。\n\n不過這間的舒芙蕾看起來蠻厲害的，有機會可能還是可以嘗試一下這樣。非常隱密的一間餐廳，需要先上一段小樓梯 ，穿過一個理髮廳才會到。\n\n主餐的牛排跟鴨胸都很有誠意，其他配菜也都有水準。\n\n不過黑色的竹炭麵包感覺很像饅頭，其中還有很硬的地方很像饅頭蒸過頭的乾皮，還有地方被旁邊水果的水沾到之後會爛爛的。\n\n舒芙蕾只能說吃不習慣...餐點超好吃 第一喜歡是舒芙蕾鬆餅 很鬆軟 甜甜的帶一點鹹 ，歐姆蛋早餐也好吃 蛋裡面有包起士和菇菇很對味，唯一的小缺點是鴨肉奶油義大利麵肉多肥美 但還有點血 稍微在煎熟一點會更好吃 整體來說都滿不錯 以後還會在光顧義大利麵跟我常吃的桂冠義大利麵味道根本一模一樣，不知道是不是直接買來微波加熱（同桌友人也這樣說）還賣這樣子的價位，去超市一盒不到50這邊要2.3百\n\n還有就是網路上照片跟實體也差太多\n一端上來直接傻眼這是什麼鬼擺盤.…..\n朋友推薦且看評論星數很高以為很厲害，結果是因為有送東西，真的是被騙到了?\n\n唯一值得的就是裝潢的蠻溫馨服務態度不錯但絕對不會再來了', 4.9, 487, 2386, 'fFwU6dfQsj@gmail.com', '02 2577 9828', 4, '', 'https://www.facebook.com/1259DesignStudio/?ref=aymt_homepage_panel&eid=ARAU6jfeU9373ATiWa_6bpYwhxXJ76n33GWOB9wnF18PGpdmrVLEtZu-XTJi8HDnqVwWl5J8ujYPQV20', '0.jpg', '[0.jpg]', '2021-06-08 09.45.04 上午', '2021-06-08 09.45.04 上午', 487, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 185, 1, '95904070', 'ABV Bar & Kitchen 地中海餐酒館-精釀Beer餐廳', '106', '台北市', '大安區光復南路260巷39號', 25.04014350, 121.55568080, '餐酒館', '餐酒館 料理美味，酒類選擇琳瑯滿目，CP值非常高！好多啤酒種類東西也都好吃食物好吃～不喝酒的人也可以來這裡享受美食??餐點很棒5-6人很夠吃地理位置也很好找，國父紀念館2號出口出來後，走路大概3-5分鐘就可以到了，對面也是一間蠻好吃的料理-山男，有機會也可以去那裡吃吃看，但選擇相對少比較多，但餐點比較精緻。\n\n這次點了3-4人套餐，份量真的很多（還是我們大家都很小鳥胃？），總之我覺得吃的蠻飽的，但美味度來說的話我覺得普通，沒有我想像的驚艷，不過如果是想找週五下班放鬆跟朋友喝酒聚餐的話，是個不錯的選項，但建議先訂位，店裡貌似也沒有用餐限制，蠻free。\n\n這次我們點了\n?附餐麵包：有個像是法國麵包沾蒜醬，還不錯，但不確定可不可以續盤\n?西班牙臘腸盤：略鹹略辣，適合啤酒\n?西南凱薩沙拉：最外面用一層像餅乾的東西托著，服務員是說要能碎之後跟沙拉扮在一起。但實施起來有點困難，最後大家都是自己弄自己的比較快\n?經典西班牙海鮮飯：味道普普，略油\n?斯拉夫尼亞披薩：薄皮披薩，上面有顆荷包蛋可以戳破沾著披薩一起吃，基本上披薩可以用起司沾黏一起的我都覺得還可以\n?義式酥炸魷墨：吃起來超像鹹酥雞的炸魷魚，普普\n?手工土耳其式牛肉餅串：這個是全部餐點裡面我最沒辦法接受的，牛肉味道很腥，可是因為我也在土耳其吃過類似料理，味道也差不多這樣，所以看個人能不能接受吧，不予置評（但我是不會再點就是了）\n?加利西亞章魚冷盤：失望，超級多馬鈴薯，跟少數切片魷魚腳，硬要說，下酒也不錯\n?鮮蕈松露馬鈴薯蛋餅：蠻特別吃起來也還不錯，不像我們蛋餅有蛋餅皮，他比較像是烘蛋裡面包料的口感，有 夠 大 份\n?克羅埃西亞式布丁蛋糕：超大一份！不會很甜，吃起來冰冰沙沙的，店員表示要盡快食用完畢，要不然蛋糕會塌下去。建議四人以上分食比較剛好，一個人很難吃的了很多\n\n剛好我是這個月的壽星，壽星出示證件給店家的話，會招待一份冰淇淋布朗尼。這也是超級大一份，然後布朗尼略甜，也是件一人多分食比較剛好！\n\n雖然我不怎麼喝酒，但朋友開了幾罐我覺得不錯的口味：比利時萊姆啤酒、鴿子啤酒。\n椰子啤酒看個人，蠻特別，其他還有很多很多可以選，選擇障礙的話也可以問問看服務員的意見～～\n鮮奶茶我個人沒有很愛，早知道點拿鐵了w\n\nBTW，朋友聚餐的話還是覺得可以來這邊！', 4.4, 1961, 8628, 'JUDHpxe@gmail.com', '02 8771 8114', 2, '["星期一: 12:00 \\u2013 01:30","星期二: 12:00 \\u2013 01:30","星期三: 12:00 \\u2013 01:30","星期四: 12:00 \\u2013 01:30","星期五: 12:00 \\u2013 01:30","星期六: 12:00 \\u2013 01:30","星期日: 12:00 \\u2013 01:30"]', 'https://abv.com.tw/mediterranean/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.22 上午', '2021-06-08 09.45.22 上午', 1961, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 187, 1, '79776583', 'ABV Bar & Kitchen 加勒比海餐酒館', '106', '台北市', '大安區敦化南路一段233巷61號', 25.04048280, 121.55242230, '餐酒館', '餐酒館 整體不錯\n啤酒價錢可以更透明化\n放個價目表整體都普通，大部分是精釀啤酒。絕對會回訪的一間店。我們三人點了1999分享餐吃的很飽，而且炸雞驚為天人的好吃，配上那個辣醬我瞬間忘記隔天要上班。烤肋排稍嫌油膩有點反胃，趕緊拿起一旁的啤酒壓壓驚。千層麵正常發揮，我只記得不錯吃然後就沒什麼記憶點了。有一道炸芭蕉沾牛肉的料理，第一次吃覺得特別，而且意外的搭。最後甜點的鐵鍋布丁我也好喜歡，可能是因為上面撒滿我愛的杏仁片吧！喔然後我點的啤酒是草莓風味，我忘記哪一款，雖然酒精濃度只有4.5%但我好喜歡，喝起來像是草莓味（很自然）很重的蘇打。之前吃過它的日式居酒屋系列覺得還不錯，昨晚試試它的加勒比海，個人認為以他的價位來說，令我失望，這家店可能是ABV 系列的比較早的一家，內部裝修不算新，而我覺得管理也沒有做的很好，挑酒推薦不是很用心搭配菜，我們點了炸物拼盤，串烤拼盤，牛肉沙拉，燉牛臉頰肉，我覺得只有炸物裡的炸雞算是口味還不錯，其他東西都讓我失望，應該不會再訪去用餐前，曾致電詢問是否有純素的餐點，店員說「有，但是很少」。點餐時，詢問下店員才說該店都只有蛋奶素，後來我請店家幫我問一下是否有選項是可以將蛋拿掉，店員就指著某品拼盤說「只能吃這個。」\n\n我點了該餐點，上來後有蛋而且沙拉醬是葷的！\n可不可以麻煩做好員工教育訓練，對自己的商品有認知，不要亂講一通，很糟糕的店家。', 4.4, 1450, 6380, 'T7EcUiVZQ@gmail.com', '02 2721 6997', 2, '["星期一: 12:00 \\u2013 01:30","星期二: 12:00 \\u2013 01:30","星期三: 12:00 \\u2013 01:30","星期四: 12:00 \\u2013 01:30","星期五: 12:00 \\u2013 01:30","星期六: 12:00 \\u2013 01:30","星期日: 12:00 \\u2013 01:30"]', 'https://abv.com.tw/caribbean/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.23 上午', '2021-06-08 09.45.23 上午', 1450, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 188, 1, '86995926', 'JAPOLI義大利餐酒館', '106', '台北市', '大安區忠孝東路四段49巷2號B1F', 25.04385980, 121.54463830, '餐酒館', '餐酒館 這地點，這價位，口味也很棒。滿分奉上。氣氛好\n服務不錯\n東西好吃\n推炸雞翅對外送員親切，餐點美味好吃味道好，服務親切，店裡氣氛佳明太子海鮮披薩太好吃了！！???\n七味粉雞翅???\n蒜味海鮮油封???\n拜託快來高雄開分店！！！???', 4.3, 2083, 8957, 'y40m40Een@gmail.com', '02 2731 6633', 2, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 11:30 \\u2013 21:00"]', 'https://www.facebook.com/japoli.kingsknow/?ref=bookmarks', '0.jpg', '[0.jpg]', '2021-06-08 09.45.24 上午', '2021-06-08 09.45.24 上午', 2083, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 190, 1, '27952245', 'Closet 衣櫥餐酒-大安餐酒館推薦/大安美食推薦/東區餐酒館推薦/東區美食推薦/台北美食推薦/大安酒吧/東區酒吧/台北餐酒館推薦/台北酒吧推薦', '106', '台北市', '大安區忠孝東路四段101巷15號', 25.04251380, 121.54814510, '餐酒館', '餐酒館 雖然有點吵雜，但酒還不賴放在口袋名單許久的一家餐酒館\n不知道是不是期待太高\n當天用餐完沒有留下好印象\n\n用餐當天有訂位也準時到\n一坐下還沒點餐就被告知要幫我們換座位\n我們也沒說什麼就換了位子（但其實很不想換）\n到新坐位之後沒有給我們菜單 主動要了才給\n考慮過程中服務生上前詢問需不需要介紹\n我們說不用 並先點了食物\n也告知服務生說先點這些 其他我們再看一下\n服務生一直很急忙要把菜單全部收走\n第二次加點完有告知說我們要留一份菜單\n服務生又一直想把菜單全部收走\n不知道在急什麼?\n\n最令人不開心的地方是\n服務生不斷在用餐過程中打擾\n當天跟友人盡興聊天卻一直被打斷\n不管是收東西還是加水都超級唐突\n我跟朋友被服務生嚇到好幾次\n明明收東西就不是很急的事\n不懂為什麼要在人家專注聊天的時候打斷\n而且還會從你背後突然出現或講話\n感覺真的很差\n\n唯一優點是上菜上酒的速度蠻快的\n調酒跟餐點口味都算不錯但沒到驚艷\n不過經歷這次用餐暫時不會想再二訪昨天幫男友過生日選擇了衣櫥\n點了鐵鍋燉蘑菇、松露薯條、翼板牛排等餐點均無雷，價格中上，不過仍帶給我們味蕾上很多驚喜，男友選酒品了秘密和盤尼西林，氣氛和層次做的很足，生日有送shot還不錯。\n\n至於很多人寫到的服務生部分，不是每位都很唐突，我只有遇到一位各種想收走我們桌上的東西，明顯還沒喝完讓我覺得有點唐突，其他人的服務都很不錯，唯一美中不足就是環境沒有想像中寬敞，桌跟桌離的很近，若你遇到講話很大聲笑得太狂的鄰居會有那麼點掃興。\n\n以上不專業評論供參很喜歡衣櫥的氣氛及餐點，每次到訪都很期待新酒單，不知道要喝什麼的時候點杯經典也絕對不失望！台北最喜歡的店家沒有之一！不得不說\n像這樣酷炫又好喝的酒吧\n對我的錢包最不利了XD\n實在是太喜歡這家店啦！真的能算是台灣前三名！', 4.3, 838, 3603, 's5mWZ7eMKY@gmail.com', '02 2731 7919', 3, '["星期一: 18:00 \\u2013 02:00","星期二: 18:00 \\u2013 02:00","星期三: 18:00 \\u2013 02:00","星期四: 18:00 \\u2013 02:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 02:00"]', 'https://www.facebook.com/Closet-Restaurant-Bar-144736812566803/?ref=hl', '0.jpg', '[0.jpg]', '2021-06-08 09.45.26 上午', '2021-06-08 09.45.26 上午', 838, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 191, 1, '07512878', 'PS TAPAS 西班牙餐酒館', '106', '台北市', '大安區安和路一段21巷19號', 25.03951080, 121.55124490, '餐酒館', '餐酒館 先講好的\n1.蒜味蝦很棒，蝦子Q彈風味濃厚，橄欖油及蒜片做成的醬汁配上外酥內軟的麵包簡直絕配，忍不住還多點了麵包配著吃\n2.脆卷炸蛋做為前菜也很棒，尤其小卷已經處理過可以直接入口，甚是貼心\n3.吉拿棒非常好吃，尤其是巧克力醬，整體吃起來甜中帶苦，加上辣椒和萊姆酒些許的酒味，替整道甜點增添不少風味\n4.熱紅酒是我喝過的版本裡面最濃郁好喝的，尤其在這樣的冬天喝上一杯，非常暖心\n5.Sangria也不錯，點的是紅酒版本，本身喜歡喝調酒，所以覺得風味還不錯，水果及紅酒不會搶走彼此的風味，共存共榮\n6.香料蔬菜串表現中規中矩，單純想吃點青菜，火候掌握得不錯，南瓜跟櫛瓜好吃，我有注意到底層鋪的醬料味道，很好吃\n7.布朗尼的質地做成外酥內軟，如果是加熱後配上冰淇淋，我想口感上會更豐富，但吃原本冷掉的也還算是中上水準\n8.秘密(松阪豬)，本身食材夠好，所以也是不會太差的菜色，邊邊略焦的口感吃起來不會有那麼重的油膩感，但當時另外兩道菜太鹹，導致我吃不出這道菜的風味\n9.海鮮巧達湯，有別於外面吃的到的海鮮巧達湯，鹹香醇厚，嘗試用加點的麵包沾著吃，味道不如這道湯品附上的麵包配搭的美味，可惜好像沒辦法點湯品附贈的麵包?\n10.最好的部分我想評給員工，雖然客人多聲音也吵雜，但員工的表現無疑是這家店最值得稱讚的地方，因為點的菜色比較多，中途也幫我們換到比較大的桌子，點菜方面也能給予適時的建議，也會詢問用餐是否滿意，我會為了員工的服務再來用餐(尤其是12/20當天晚上唯一的外場男生服務員，我感受的到你的NICE~~~~)\n\n接下來要講扣分的地方\n1.赤嘴蛤味道過鹹，我秉持著不浪費食物的心情硬是吃完，服務生詢問用餐是否滿意時，我提了這道菜，雖說口味因人而異，但友人也表示頗鹹，想必就不是只有我的舌頭壞掉\n2.墨魚大鍋飯吃起來口感也不錯，與正統的海鮮燉飯相差無幾，屬於米心還是硬的口感，但吃到一半口腔壓力還是疊上來了，雖然有提醒要拌勻，味道會比較均衡，我也照做了，但還是覺得鹹，尤其當時在桌上的另一道菜是赤嘴蛤，鹹上加鹹，差點吃不下去，看了評論也是好幾組客人表示一些菜色味道過重，不是不好吃，而是你們的廚師下手太重了，就算配酒我也還是覺得鹹\n3.最後我善意的提醒你們的清潔，在用餐過程中我與友人一直發現有隻蚊子飛來飛去，定睛一看發現居然是果蠅，在餐飲業中總是無法避免，但希望在清潔上你們能再加強就是了\n\n儘管如此，貴店還是我吃過的餐廳中，我還會願意再來的店家之一\n留這麼多長篇大論也是因為看到貴店對於評論還是有在注意的\n所以我還是給你們高分評價，希望下次來的時候該改善的能有改善??不會想再去消費，也不會想再跟此店家訂餐了??\n\n真的是一個非常差的訂餐體驗，因為要慶生，疫情關係也不能內用，\n很提早五月底就在官網訂了六月的餐、選定了外送配送時間，\n請店家要做官網訂餐外送也要考慮外送量能的問題。\n\n我是在官網訂餐，預計配送時間18:00-18:30\n六點出頭店家打來說配不到外送員可能送餐會晚一點，問大概幾點也無法確定。\n全家就在那邊等你們遲到又不確定幾點要來的餐點～\n等到18:30/19點都有致電詢問，也是鬼打牆： 沒配到外送員、依然無法確定配送時間。\n\n最後還是得自己去拿餐最快^^配一小時配不到外送員真的呵呵，我不是臨時才訂餐，今天的大雨也不是臨時才下的。\n\n食物看起來預先做好的？涼在那邊等著配對外送員？然後配那麼久配不到，食物品質真的ok嗎？\n\n拿到的食物明顯做好很久、不熱了、口感也很不好。\n總之這次的訂餐體驗非常不好，謝謝貴店讓我們的慶生心情被毀掉，等餐等到心很累，最後還得冒雨出去拿，提供官網配送、結果是這個品質真不敢恭維。\n\n爛透，連一顆星都不想給，沒那個量能、就不要做官網外送。\n不會想再去消費也不會想再跟此店家訂餐^^食物還不錯，但強烈建議店家改善外送服務的品質\n\n一共使用官網上的外送服務三次，除了第一次沒有問題，其他兩次的經驗都不是很好\n\n第二次\n請店家15:00-15:30送達，約14:40外送就打電話給我。但當時我還未在送達地點，請他稍等。過不久就接到店家來電，說外送人員已到達現場希望我能找人幫忙代收。為何外送人員自己早到要我們找其他人代收?\n\n第三次\n有了上一次的經驗，送餐前一天還打電話去餐廳確認送餐時間。對方說雖然我選的是12:00-12:30送達，但他們12:00才開始營業，所以送達時間應該會落在12:30左右。今天12:25電話響起，原本開心要領餐了，外送人員劈頭就開始說他等了一個小時(我整個黑人問號???店家不是12:00才開??)，加上周邊無法停車，加上他一個人也拿不動所有的東西，叫我們自己下樓拿。然後說什麼因為我改時間害他等了一個小時，店家後台沒改到時間等等。我告訴他我從來沒有改過時間，跟我抱怨沒有用，是店家昨天自己跟我說12:30才要送到的。\n\n建議店家可以換個搭配的外送餐點服務，畢竟你們的餐點不錯，外送服務應該要幫你們加分，而不是扣分。吃美食當然是開心的，但還要被外送人員抱怨真的是會很傻眼。很喜歡這家餐廳的氣氛!\n蒜蝦十分美味，盤中的蒜片搭配麵包根本完美!\n松阪豬熟度恰到好處，軟嫩又不失嚼勁，底下的馬鈴薯泥有畫龍點睛之效。\n期待已久的烤飯沒讓我失望，攪拌均勻後混合底部鍋粑口感深得我心!!\n收尾的吉拿棒更是出乎意料的驚喜，加了酒的巧克力醬實在太對味，要不是太飽肯定再續一盤!\nBTW蘋果酒的瓶子超可愛味道也很棒，喜歡甜酒的人別錯過了\n食物的口感 味道都很棒\n水果酒很甜很像果汁 酒味不濃\n米飯的口感Q 不會像很台式的做法爛爛的 有嚼勁好吃\n蒜味蝦和蒜炒菇的蒜香都超級濃郁\n非常好吃價錢也很合理，最喜歡的料理是雞肉燉飯和歡樂碳烤，沙拉也很好吃，非常推薦。對食物過敏這件事的敏感度極度低！\n客人食物過敏後的處理方式也很差！\n\n已經在點餐時說明自己不能吃堅果、會食物過敏、麻煩有的話可以拿話拿掉。店員說去問問看、會跟廚師說明，然後就未再回來回覆我、我就以為餐點一切沒問題。之後餐點正常上、我卻在吃到一份燉牛肉的時候感受到吃到堅果、立刻急性過敏反應發作、呼吸道緊縮。想詢問店員到底是吃到什麼堅果、卻一問三不知還要來回三次才能回答我到底吃到的過敏原是什麼、沒有意識到是很嚴重的急性過敏在發作、想要用這道我幫你換別的來代替、我根本也聽不進去只能趕緊回家吃藥。\n\n我並不是生氣店員、而是生氣貴餐廳怎麼會沒有好好重視食物過敏這件事情並如實訓練員工該怎麼應對或甚至在菜單上標註清楚過敏成分！如果我今天引發的是更嚴重的過敏反應的話、該怎麼辦？', 4.3, 2500, 10750, 'hyx21mC3r2@gmail.com', '02 2740 9090', 2, '["星期一: 11:00 \\u2013 21:00","星期二: 11:00 \\u2013 21:00","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 11:00 \\u2013 21:00"]', 'http://www.pstapas.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.27 上午', '2021-06-08 09.45.27 上午', 2500, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 192, 1, '33093311', 'B612 Restaurant & Bar - B612餐酒館', '106', '台北市', '大安區光復南路260巷51號', 25.04016880, 121.55517250, '餐酒館', '餐酒館 幾個月前路過，因緣際會終於吃到了。地點好找（國父紀念館站2號出口），老闆與服務生們人都很好很友善，用餐環境舒適，空間頗大的，不喜歡桌子靠太近的，會喜歡。餐點也很快上，菜單選擇滿多還正好都是我的菜，飲料也有非酒精的比如水果茶，以餐酒館來說太難得了。低消是每人一杯飲料，我點的是cocktail 的莓果派對，滿喜歡的，有機會下次再試試別的口味。服務態度親切，在討論客製化調酒都會再三確認跟說明提醒。\n\n環境氣氛佳，座位舒適，有吧台區、高腳桌、沙發區、聚會區，基本上各種性質的用餐目的都能達成！\n\n食物是有特色的異國料理風格，當成下酒菜真的很搭！絕對會再訪的！！！！同學來這裡請客，我們順便幫她慶生，本來只是麻煩餐廳幫忙冰蛋糕，但卻問了壽星的名字，沒想到用餐完餐廳主動無縫接軌的上了蛋糕還作畫！！！！而且我沒給他們蠟燭，還主動提供了跟蛋糕很搭的蠟燭給我們，超貼心！！不是壽星的我都感動的要哭了～～服務真的很好，一直來幫我們倒水，適合慶生的餐廳，有很多好吃的特色異國料理，鴨胸很嫩～墨魚細麵超好吃！調酒也很有特色，值得再訪！真的不得不稱讚這邊的服務很貼心，很少遇到讓人想稱讚的餐酒館！食物跟酒都有一定水準！推薦??為了給壽星驚喜準備了氣球跟蛋糕，還要躲起來?謝謝店員跟老闆都很願意配合我們的操作！給驚喜店員比我們還緊張！！慶生可自備蛋糕，先說的話老闆會幫忙畫版畫唷！！照片的小智就是老闆畫的！！！\n\n烘蛋跟牛排都很好吃，推薦的紅酒也很好喝！很推薦來這邊慶生或求婚唷??', 4.7, 468, 2200, 'EFs1epL4Ub@gmail.com', '02 2779 6088', 2, '["星期一: 11:00 \\u2013 13:30, 17:00 \\u2013 19:30","星期二: 11:00 \\u2013 13:30, 17:00 \\u2013 19:30","星期三: 11:00 \\u2013 13:30, 17:00 \\u2013 19:30","星期四: 11:00 \\u2013 13:30, 17:00 \\u2013 19:30","星期五: 11:00 \\u2013 13:30, 17:00 \\u2013 19:30","星期六: 休息","星期日: 休息"]', 'https://www.facebook.com/B612-Restaurant-Bar-387725725015415/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.27 上午', '2021-06-08 09.45.27 上午', 468, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 193, 1, '63987813', 'Secret bistro秘密小酒館-餐酒館 推薦 精釀啤 酒 義法料理 紅白 酒 特色料理 朋友聚會 生日慶生餐廳 打卡推薦', '104', '台北市', '中山區中原街139號1樓', 25.06414250, 121.52915530, '餐酒館', '餐酒館 我們是有事前訂位的團體聚餐（將近20人），餐廳當天包含廚師只有三人，出餐宇宙慢就算了，可能爐子沒很多，但是飲料和酒也都上得銀河慢，餐點就是普通好吃。\n我想說的是飲料，有幾個人包含我點了鮮奶茶，上來就是一杯奶茶風味的水，我本身有向店員反應，店員說幫我重做一杯，但是只換我的，並且第二杯上來就是一模一樣的稀。我再次反應，店員還是說要幫我重做一杯，但是我想不會有什麼分別的，就婉拒了。在此奉勸大家不要點鮮奶茶啦，這份對鮮奶茶的愛是不會有回報的。\n我不是第一次喝到難喝的鮮奶茶，但都沒這杯120元的鮮奶茶難喝，難喝出鮮奶茶的新極限，沒啥茶味也沒啥奶味，就像放了多冰的早餐店奶茶，冰塊全溶掉的味道，品質連早餐店奶茶的1/10都不到，你不想賣鮮奶茶可以不要賣，沒人逼你一定要在菜單裡放這個品項。不然去跟早餐店問一下鮮奶茶怎麼做也好啊讓人放鬆的小酒館 餐點好吃 很有特色。\n\n再訪。。。\n料理精緻用心，仔細品嘗得出真材實料，甚至在一些義大利餐廳之上，這樣的價位十分合理，在用料與細緻度上覺得是不能與平價餐廳相比的一間餐廳，有適合的餐聚會再再訪。剛進去的時候發現餐點很少而且沒有什麼適合孩子的文字上面的菜單不吸引人，但是最後我嘗試了先點了四道菜之後他的食材新鮮，口感非常恰當，小孩都吃完還要再吃尤其是他的貓耳朵義大利麵，總共點了七道菜最後一道菜是牛舌鵝肝跳跳談聽起來名字很特別但是可能我個人不太喜歡他的口感太軟，綿密了，大致上都很滿意，服務也很好，我個人非常推薦雖然價格不低，不過餐點大部分都不錯也能感受得到主廚的用心，本來想給四顆星，但甜點的提拉米蘇非常難吃，口感很差很像泡過水且味道也像被水稀釋過了一樣（像是冷凍後解凍沒處理好），非常可惜。料理非常有創意，每次吃都感覺像是舌尖冒險，廚師總是可以把各種令人想不到的食材，料理成一道經驗的佳餚，價格實惠，分常值得推薦', 4.6, 253, 1164, 'SC1dOp@gmail.com', '0905 677 285', 4, '["星期一: 休息","星期二: 17:00 \\u2013 22:30","星期三: 17:00 \\u2013 22:30","星期四: 17:00 \\u2013 22:30","星期五: 17:00 \\u2013 23:00","星期六: 17:00 \\u2013 22:45","星期日: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00"]', 'http://pse.is/KUL7U', '0.jpg', '[0.jpg]', '2021-06-08 09.45.28 上午', '2021-06-08 09.45.28 上午', 253, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 195, 1, '53737493', '你覺得呢餐酒館 What Do You Think 國父紀念館推薦餐廳｜東區人氣餐酒館｜大安區美食｜創意調酒', '106', '台北市', '大安區光復南路260巷45號', 25.04009800, 121.55550600, '餐酒館', '餐酒館 Follow Instagram: hangrygirls.tw\n\n雙人套餐 NT$1580\n明太子優格番茄蟹肉沙拉\n瑪扎拉瑞起司條\n紅麴腐乳炸雞腿塊\n海膽明太子海鮮麵\n香煎鮭魚北海道干貝\n氣死他奶奶的茶\n洛神冰茶\n洋蔥圈\n微醺單人套餐 NT$880\n松露野菇雞肉燉飯\n炸洋蔥圈\n富士山下\n火烤美國特級牛小排 NT$480\n紅心 QUEEN NT$400\n\n不說還以為今天來到了一家復古唱片行，沒想到真面目竟然是創意餐酒館，在這個讓人放鬆的氛圍下，必能好好享受美食。每天都有不同的優惠方案，也可以選擇較為划算的多人套餐，不管是餐點、飲料和酒類都有多樣選擇，好友聚會的好選擇。\n\n海膽明太子海鮮麵有著滿滿的海鮮，配上濃郁卻不油膩的奶油香氣與明太子，再看看香煎鮭魚北海道干貝，就能發現店家在用料上真的超級大手筆，金黃的香煎鮭魚與鮮甜肥美的干貝，簡直入口即化。紅麴腐乳炸雞腿塊外皮酥脆，雞腿肉鮮嫩多汁，即使放涼了也不影響口感，適合搭配酒類的炸物。額外單點的火烤美國特級牛小排表現得也很不錯，約五分熟的牛肉香氣四溢，看著這個份量也太滿足了！這裡的飲料各個顏值高，就像是藝術品一般，不管你喜歡喝酒，或是想要品嘗無酒精系列，都能找到屬於你的專屬口味。調酒好喝，東西好吃，氣氛很放鬆～～重點店員顏值都太高了吧??????服務態度超好，以後會再來！酒好喝?\n食物好吃\n氣氛歡樂\nbartender會聊天?\n個人口味偏向妹酒，因此都推甜甜酒感不重系列喲～\n#這不是土瓶蒸系列 白桃口味\n很好喝～喝起來像白桃烏龍茶?\n#恐龍蛋\n應該是鳳梨+芒果？？非常的果汁奶昔感，不適合一直喝易膩，但用來刷味道好適合。\n然後還有一款酒非常好喝，透明的味道像是西瓜、黃瓜汽水，很清爽，但忘記酒名了?\n鹹食方面，都很好吃餒??\n#西班牙海鮮燉飯 #辣味番茄鮮蝦麵 #綜合炸物拼盤 #招待薯條酒好喝，店員很親切～～～（顏值都好高?）\n萬聖節活動超可愛是馬力歐??店裡氣氛很好\n人都超親切 會跟大家玩在一起\n來這裡一點都不會無聊\n好玩一直來 酒好喝一直喝\n還沒來過一定要來玩', 4.9, 1298, 6360, 'KBWErZh@gmail.com', '02 2721 0968', 4, '["星期一: 休息","星期二: 18:00 \\u2013 02:00","星期三: 18:00 \\u2013 02:00","星期四: 18:00 \\u2013 02:00","星期五: 18:00 \\u2013 03:00","星期六: 18:00 \\u2013 03:00","星期日: 18:00 \\u2013 02:00"]', 'https://www.facebook.com/whatdouthink45', '0.jpg', '[0.jpg]', '2021-06-08 09.45.30 上午', '2021-06-08 09.45.30 上午', 1298, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 196, 1, '23168531', 'Hush Taipei -Cocktail Bar Bistro 餐酒館 沙發包廂 晚餐宵夜 義法料理 聚會包場', '106', '台北市', '大安區市民大道四段34號', 25.04482620, 121.54532010, '餐酒館', '餐酒館 酒精性飲料真的可參考?? 餐點部份...嗯，吃飽再來吧，不是很推薦（尤其海鮮燉飯、湯、烘蛋），沒有那個品質與值；純喝酒倒是可考慮，吃東西真的先不要???♀?店家可溝通配合cancel餐點，服務態度很好\n飲料好喝\n餐點0分\n前一組客人等一個小時，我們也等一個小時\n海鮮燉飯0分，番茄沒番茄味，海鮮不新鮮\n我是極討厭浪費食物的人，但那個燉飯我真的吃兩口就去結帳了\n餐很貴、等超久、又難吃\n我是老闆一定先把廚房開了店面好認，裝潢也ok，而空間稍悶偏小，餐點選擇也較少，酒倒是滿多選擇，菜單內容也很詳細，三五好友小聚小喝是可以的，但如太多人聚我認為會不太合適，不過意外的是真的算平價、能吃飽，而且服務非常好，然後可能因是平日所以出餐很快，餐點整體來說都還不錯，調酒也好喝！喜歡酸口味可以點Hush滿推的?昨天跟閨蜜在這裡小小慶祝我的生日，感受很棒！點了8菜2調酒1甜點加服務費才$4015很便宜～個人最推薦的是海膽燉飯、脫衣舞孃及跳舞小豬！點了一杯特調，向調酒師要求的調酒風味，描述的越細膩越能感受到他們做酒的誠意，非常值得一訪。適合約會??????聚餐?同樂的地方\n餐點、點甜跟酒飲CP值高?\n不喜歡酒味太濃的朋友\n可以跟調酒師說 為您做一杯客製化的調酒?\n服務小帥哥美女及帥氣專業調酒師?、超親切老闆\n?非常推薦?', 4.6, 252, 1159, 'fb0ewl@gmail.com', '02 2778 9069', 4, '["星期一: 18:00 \\u2013 01:30","星期二: 18:00 \\u2013 01:30","星期三: 18:00 \\u2013 01:30","星期四: 18:00 \\u2013 03:00","星期五: 18:00 \\u2013 03:00","星期六: 18:00 \\u2013 01:30","星期日: 18:00 \\u2013 01:30"]', 'https://www.facebook.com/hush27789069/?ref=settings', '0.jpg', '[0.jpg]', '2021-06-08 09.45.30 上午', '2021-06-08 09.45.30 上午', 252, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 197, 1, '48329863', '隱士餐酒館The Hermit Bistro', '106', '台北市', '大安區光復南路240巷51號', 25.04060190, 121.55508690, '餐酒館', '餐酒館 食物驚艷好吃\n台菜融入餐酒非常印象深刻好吃個人覺得低消800的店，不該是這種服務。\n\n今天點了兩杯調酒、牛肉粥、烤鵪鶉、東坡肉，坐吧檯。\n上了調酒之後過30分鐘才上烤鵪鶉（用餐時間2小時:D)，鵪鶉烤得太乾，加上整隻對切但沾醬卻是裝在高口的碟子裡，吃起來非常不方便；牛肉粥燉的稀爛，不合我的口味，另外給了分享的小碗卻不給湯匙，還要自己跟店員要，我的10%服務費是拿去餵狗了嗎？最後的東坡肉，嗯，我真的沒吃過繩子要我自己剪的東坡肉，肉也要自己切，還是在略有深度的鍋碗中切，真棒。調酒點的是黑金跟早晨綁架行動，黑金蠻好喝的，咖啡跟基酒搭配出的層次感很好，另一杯就略嫌平淡。\n\n接著來講講服務生好了，一開始為我點餐的吧檯手在我還沒講完餐點手就直接轉身離開，倒水也一直流出來，還能弄到桌上都是；整個吧檯的人員有一半把口罩戴在下巴，拿下來就沒有要戴回去的意思；製作餐點時還能把用具放在吧檯桌上，東西是有多到這樣？又是先說結論，如果你是一個飲食追求感官衝突的人，絕。對。大。推—「隱士餐酒館」。\n\n臺北東區可謂餐酒館的一級戰區，大小餐酒館林立，想要在這一個滿布酒國食客英雄滿出的場域，沒有高招，可能生存著實不易，而隱士則是這其中特然而立（力）的佼佼者；跳脫框架的創意料理，中西日洋混搭，加上各色酒品特調，搭配得毫無違和感，絕對超乎想像。\n\n隱士餐酒館The Hermit Bistro位在捷運忠孝敦化(3號出口)與國父紀念館站(2號出口)之間，兩者悠閒步行差不多都五分鐘內到達，是忠孝東路與延吉街往南的第一巷，區位相當便利，名字取作隱士相當具有道家哲學的味道「小隱於野，大飲於士」相當有inside。\n\n甫進店門，映入眼簾的是鐵灰色的吧台，清水模感與頗有野性的岩石牆面相互輝映，灰黑色系，大地素材的整體裝潢，岩窟感氛圍，頗符合隱士道行的意象；而吧檯的酒櫃與浮雕，頗有畫龍點睛之效，更體現靈魂與精神。然後天上聖母的媽祖傘，日月同輝的芭蕉扇，在吧台中間竟無違和的自然鑲嵌，著實有趣。\n\n先吃了主食「川味中卷椒麻麵」，整隻的中卷炒入台南意麵，椒麻香十足，搭上酸菜的中和，油鹹香椒麻而不膩。然後「咕咾百花鑲油條」是我當天的最愛，新鮮蝦漿塞進油條中，炸好厚！外酥內鮮，水分都還在，玩全不柴，與野菇、糖醋汁用咕咾肉的方式拌炒，做工繁複但是調味的恰到好處，鮮甜多汁又酥脆，感覺很衝突，卻又跟糖醋搭配成完美雙簧。「手作厚蝦餅」手打的草蝦漿很紮實爽口，外層潤餅皮則包覆的輕紗掩面，不會吃太多麵皮，薄而脆，滿口蝦味；而「黃金土魠魚」酥脆中還帶有魚肉鮮，青蔥點綴，下口如米荖，完全不油不膩，兩者都十分下酒，不，是百分下酒！「山海匯珍蛋」在蒸蛋上鋪上了天使蝦、鮭魚卵、燻鮭魚與黑松露、青蔥相佐，光看顏色就垂涎三尺，入口更是爆彈美味，好多朋友大推必點。最後用「黃金花膠土雞湯」與「煉乳螺絲捲」做收，土雞腿配上猶如燕翅的黃金草花膠燉煮，輔以干貝的鹹鮮，一種宮廷滋補養生湯，很適合酒後暖胃，然後外酥內軟的螺絲捲，淋上煉乳老派復古，但料理的細緻，猶如越式少女酥胸般的風情。\n\n當然，「我是來喝酒的！」對於餐酒館，酒才是我的重，餐在輕，可惜只有三人沒有辦法酒單全餐，只好下次再補滿。\n\n「草食性少女氣泡飲」在氣泡SOUR感中有芭樂的香味釋放，氣泡中喝著喝著出現了巴西利、雞鬼草、七里香、百應草之類的香草味，層次很多元，清爽順口，色、香、味、觸具有。\n\n「夜市裡的花生捲吧噗」香蘭葉的基底、百香果的酸甜，用酸甜味帶出香蘭特有的芋頭鹼味，用威士忌調拌花生醬做成冰淇淋感放在餅乾上做前口裝飾，立體液態版花生捲冰淇淋儼然。\n\n「綁架早晨行動」很喜歡SOUR的酒，在吃過油炸的食物之後，適合解膩，蘆筍慕斯爽口清麗，與嘴唇貼和，第一口的印象很呢喃；金桔的酸味與柑橘香與接骨木甜成為這杯酒的主體，然後小黃瓜的滑澀兩難的後味，讓酒體很適合搭配鹹辣之物。\n\n「黑金」喝起來中規中矩，用咖啡、雪莉酒、梅酒、松露調和，是變奏版的coffee negroni，用咖啡與雪莉酒苦味做基底，少了香艾酒的甜，卻改成從雪莉酒、梅酒來的葡萄與梅子果味帶出甜味，然後輔以松露的蕈香，很輕熟的特調。\n\n「藥你命三千」先說五加皮，《本草綱目》中記載：「補中益氣、治風濕痿痹、壯筋骨。」五加可煎湯、入丸、散或浸酒；五加皮藥酒味道辛、苦，有點沒有糖化的陳皮味加上一點不苦的青耆感，夏天飲用清熱，早期甚是有人加啤酒飲用。然後這調酒用大黃根當甜味源頭，上層的莓果慕斯有夠綿密，有點像仙楂、竹葉青跟桑椹、莓果綜合的衝突味道，喝起來十分奇特，好不好喝看個人，但我沒很懂。（笑）\n\n「仙杜瑞拉」據說是酒單上酒精最濃，喝起來的確酒體最厚，但神奇的是酒感沒很重，基酒生命之水?151?理應酒感重，但在鳳梨、百香果、莓果酸甜壓境下，讓生命之水的伏特加味減少許多，然後莓果慕斯讓一切包覆的很順口，不過要價半千，著實不菲。對於我而言可能多個半盎司的chartreuse，讓後腮多一點點蕁麻香更有層次，會覺得比較划算！\n\n其他還點了OLDFASHIONED、Negroni等經典雞尾酒，沒有意外，中規中矩的不差。整體而言，雞尾酒的風味是一般大眾會喜歡和接受的，對於酒精中高度患者而言，這裏特調則顯得相當少女系，要喝酒補乙醇的爽感不足，但網美程度絕對足夠，搭配了餐點著實爆炸加分。所以，非常感謝熱情的店員不吝的招待了眾多的SHOT，三四人也是酒精充滿的迎接下一RUN，整體就是狠的爽爽。\n\n最後的最後，絕對要先訂位，用餐有時間限制，所以建議鮮看好菜單可以快速點。無論是油炸、鮮拌或熬煮，各項餐食相信店員都可以推薦很相符的飲料，而且居然還有無酒精（餐酒館誰喝！？）。\n\n好啦，但貼心??絕對適合再訪，也絕對適合體驗。阿對，好像有低消！但幹，很好吃停不住，一下就超過，管他低不低消???然後廁所邊有網美牆，嗯哼嗯哼。不知不覺從已超過四訪...第一訪後就決定要把整本菜單從頭到尾都吃過一遍。在這裡用餐可以體會到對於想要提供給客人完美體驗的堅持，除了對於食材水準的要求超乎想像，調味及處理法的多樣性也常常讓我覺得驚喜！ 我不是美食家，但我相信我的舌頭，我也相信主廚不會端出讓人失望的料理！就如同我每一訪都有完美的感受！之前一直都想來的口袋名單，也吃到了最想吃的招牌「山海匯珍蛋」，魚卵和蒸蛋真的很搭，恰到好處的鹹味還有上面的大蝦和鮭魚，真的很好吃是必點??????\n\n其實他們的每一道菜都很不錯很有特色，有一道是辣味的玉米筍，很刷嘴會一口接一口??\n\n酒類推薦「熱炒烏梅醉」梅子香氣很好喝，和吃的鹹食都很搭，另一個「PM33」用香水瓶裝的調酒，上桌前店員會先在杯子噴上柑橘香水（可食用的），接著再現場倒入調酒，這款超級適合拍照，又美又好喝??', 4.8, 512, 2458, 'FMVjHqET@gmail.com', '0963 588 568', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-08 09.45.31 上午', '2021-06-08 09.45.31 上午', 512, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 199, 1, '20797325', '璽愛私餚-餐酒館', '104', '台北市', '中山區長安東路二段116巷3號', 25.04798350, 121.53599540, '餐酒館', '餐酒館 超推?食物美味?調酒很讚！！！很適合喜歡研究星座，姓名學等等得人來～餐點美味\n擺設精簡美觀\n星座調酒非常有個人特色\n尤其還有星盤解說 為什麼這麼調\n特調是這裡的一大特點\n醬料也是店家自己特製的\n是第二大特點只有在這裡能吃到台南鍋燒意麵的鄉愁。義大利麵剛好的彈牙，澎湃的配料。也只有喝他們家的酒不會頭痛，能享受到微醺的放鬆感???忍不住想要每個禮拜都來一次?貼文和貼圖和實際店面好像有很大的差距！\n是更換位置或經營團隊了吧！\n\n料理真的不是很好吃.....?美味的餐點~加上獨特的調酒~絕對是好友聚餐的首選！', 4.3, 170, 731, '7ApJLewskL@gmail.com', '0988 608 868', 4, '["星期一: 20:00 \\u2013 08:00","星期二: 20:00 \\u2013 08:00","星期三: 20:00 \\u2013 08:00","星期四: 20:00 \\u2013 08:00","星期五: 20:00 \\u2013 08:00","星期六: 20:00 \\u2013 08:00","星期日: 20:00 \\u2013 08:00"]', 'https://www.facebook.com/cicasa.handmade/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.32 上午', '2021-06-08 09.45.32 上午', 170, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 200, 1, '29203806', 'Spice Cuisine香料餐盤/台北美食/台北餐酒館/台北酒吧/中山區美食/南京復興美食/南京復興餐廳 /南京復興餐酒館', '10491', '台北市', '中山區南京東路三段216巷21號', 25.05108750, 121.54352930, '餐酒館', '餐酒館 ', 4.8, 287, 1378, 'cO9UD6@gmail.com', '02 2778 8841', 4, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 17:00 \\u2013 21:00"]', 'https://www.facebook.com/SpiceCuisineTw', '0.jpg', '[0.jpg]', '2021-06-08 09.45.33 上午', '2021-06-08 09.45.33 上午', 287, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 201, 1, '99202331', 'Greedy Bistronomy Cafe 咖啡·餐酒館', '106', '台北市', '大安區光復南路308巷42號', 25.03895280, 121.55558890, '餐酒館', '餐酒館 本想服務很好，用餐結束要給五顆星。\n\n不料，要去廁所經過廚房，看見廚房的兩位廚師，沒戴口罩（不是將口罩拉下，是完全沒有口罩在臉上）”””對著爐火上，正在料理的食物大聲說話”””，過了1小時之後又經過廚房，這次廚房有三個人，依然是兩位”””沒戴口罩在烹煮的食物前不停對話。”””\n\n這疫情時期，在不在意食安是一回事，基本衛生素養不是做表面就好，讓外場人員戴著口罩給客人看了心安，事實上呢？餐飲從業人員應更能在衛生上將心比心的不是嗎？\n\n廚房人員如果不在意客人食物衛生，那麼外場隨時戴著口罩也是徒具形式。\n\n吧台人員在走出吧台送飲料時，會將口罩戴著，回到吧台調飲料就將口罩拉到下巴。\n至少他沒有與人對話，尚在可接受範圍。\n\n至於食物好不好吃？\n客人看不見的地方就不在意衛生控管，再議是否美味也沒有意義。這次去服務態度很不好（好像是老闆娘！？）\n一般看起來是服務生的反而挺好的\n可惜可惜，曾是我的愛店約會聚餐的首選！餐點好吃，調酒也非常有特色，闆娘很熱情，絕對會再訪！辣味茄汁海鮮義大利麵 $460\n青醬鮭魚燉飯 $450\n貪吃漢堡包（太陽蛋加點） $370\n胡麻雞腿堡（沒有在照片中） $350\n\n義大利麵：料相當豐富，但麵條及口味皆普通。\n\n燉飯：食物送上來時是溫的；整體而言，友人心得是很不令人驚艷，但加上服務費卻五百多元，不推薦。\n\n漢堡：還蠻期待手打牛肉是熱騰騰的，但實際上偏溫；配料看似豐富但吃起來卻沒有特別突出的地方；薯條熱騰騰且酥脆，但附贈的起司沾醬有一股加工產品的味道。\n/\n\n不知道是不是強項在酒類\n我們所點的食物各自吃完都沒有很推薦\n這樣的價位加上服務費後很難再訪。\n\n但服務員皆相當友善，也很有耐心的介紹餐點或是照顧到客人的需求，除了送我們一碗堅果當小點心，幫忙拍照時也很努力想要拍得很好；很謝謝當晚與我們有互動到的服務員們，辛苦了！多一顆星給你們的服務，謝謝！用料實在、精心調味的美食，匠心獨具的原創調酒，我見過可稱為「貼心」的店家洗手間，但這間洗手間是「款待」，被你們的認真感動了，願長久！', 4.2, 745, 3129, 'UhO9phLriJ@gmail.com', '02 2752 0339', 3, '["星期一: 12:00 \\u2013 01:00","星期二: 12:00 \\u2013 01:00","星期三: 12:00 \\u2013 01:00","星期四: 12:00 \\u2013 01:00","星期五: 12:00 \\u2013 02:00","星期六: 12:00 \\u2013 02:00","星期日: 12:00 \\u2013 01:00"]', 'https://www.facebook.com/Greedy-Bistronomy-Cafe-%E9%A4%90%E9%85%92%E5%92%96%E5%95%A1-1547639165550242/info/?entry_point=page_nav_about_item&tab=page_info', '0.jpg', '[0.jpg]', '2021-06-08 09.45.34 上午', '2021-06-08 09.45.34 上午', 745, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 202, 1, '44247671', '森友匯水煙餐酒館/台北餐酒館/台北水煙/台北酒吧/大安區美食/大安區餐酒館/大安區健康餐', '106', '台北市', '大安區忠孝東路四段170巷6弄12號', 25.04080280, 121.55020440, '餐酒館', '餐酒館 ???往右滑有菜單\n水湮餐酒館賣健康餐囉，而且超用心的?\n-\n?\n雞胸肉真的太有誠意了\n這份量真的在健康餐店都很少見\n真的給好給滿又好吃\n我好喜歡?\n-\n?\n菜的種類和量表現的還不錯\n?????\n提供了薑黃藜麥飯、高麗菜、木耳、花椰菜、玉米筍、甜椒和水煮蛋\n都是用水煮的不會有過多的調味\n只有高麗菜我個人喜歡口感再嫩點\n其他都蠻好的?\n-\n?\n整體很有味道也很下飯?\n她們的東西真的不隨便\n連附的蛤蜊湯都超鮮的啦?\n-\n?\n價位在150-220元\n原本來是抱持著吃沒吃過的店\n而且因為店很漂亮來的\n沒想到一點都不馬虎\n整個超用心的?\n\n這間我最喜歡的部分是?肉、?飯、?湯\n還有?蔬菜的種類&顏色多也很棒?\n\n還有就是環境了\n這是平常在健康餐店絕對看不到的\n除了很漂亮外，整個也太舒服了\n我真的會在這睡著?\n\n吃健康餐除了健康和好吃外\n也可以很舒服?\n\n今日菜單：\n商業午餐-森食餐 舒肥雞胸肉 150元\n-附湯\n-附飲料 升級偷情可可+30元\nP 42\nC 50\nF ？\nCal ？\n（此為秤重肉和飯，並以APP估算結果，會有一些出入）路過進來用餐\n環境意外的舒適。\n餐點很美味，飲料好喝?\n店內竟然有行動電源租借站！\n反正如果可以給10顆星我就給11顆星??使用平台訂購這間餐廳的餐點，直到過了二十幾分鐘才取消訂單... 若是無法配合外送，就不該上架讓客人訂購......意外走進的店面，意外的舒適！\n店內放的音樂很像spa那種，讓人覺得很放鬆，牆壁有三個投影機投影的大螢幕，感覺很適合包場辦活動。\n店員親切，餐點好吃，也不貴！大推薦。東區精緻小店，店員親切服務好\n詢問問題都會很親切服務和回答\n也可以客製喜歡的調酒很貼心哦！\n不是會有新款調酒，食物好吃，店內風格很有創意chill', 4.7, 144, 677, 'F1Yzgd@gmail.com', '02 2711 0800', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 03:00"]', 'https://www.soulmate-522.com/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.34 上午', '2021-06-08 09.45.34 上午', 144, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 203, 1, '44150252', '純Chunmix餐酒館-東區美食推薦/大安餐酒館推薦/大安美食推薦/台北美食推薦/台北中餐廳推薦/東區餐酒館推薦/台北餐酒館推薦', '106', '台北市', '大安區光復南路290巷55號1樓', 25.03930630, 121.55527310, '餐酒館', '餐酒館 3.5?\n一家很特別的台式酒吧，不過店員上菜單一開始只先上食物的並沒有上酒單\n後來發現他們的酒櫃 擺放的酒超級少\n店員的靈敏度還蠻好的服務不錯\n\n??避風塘松阪豬×味道夠重很下飯????????\n??芙蓉海鮮蛋×弄得很像大阪燒的感覺，用美乃滋跟柴魚調味，才不會讓這道顯得太單調膩口\n??菲力牛鬆炒飯×特別請師傅加辣進去炒，辣椒有辣也有麻，稍微冷掉就沒有這麼美味，熱熱的吃也覺得是蠻普通的一道，吃不太出來有肉的感覺應該切得很小塊\n??山藥枸杞燉雞濃湯×濃濃的還不錯雞肉也很嫩\n\n?如果照片跑不出來 可以點↑↑上方我的頭像裡查看\n\n??飲食參考：\n甜：以星巴克「焦糖瑪奇朵」來說，半份濃縮、糖正常\n鹹：蠻重鹹的\n辣：中口味斟酌參考服務生從背後上酒，我朋友伸手想搭肩我，撥到酒倒我一身，老闆還要求我們賠酒錢，我都沒要求要洗衣費，最終還是付了酒錢，覺得員工訓練太差，上酒神不知鬼不覺一聲也不發就從背後上酒，今天是熱湯灑到我一身灼傷都不知道老闆賠不賠，居然還要我們負擔酒錢，覺得太差勁的服務品質，撥我一身濕也不先關心，只顧著清掃酒杯，我與朋友們也有協助幫忙，當回想起來再也不會想去。整體餐廳的風格也很讓人不舒服，說是中式設計餐廳真是看不出來，只覺得眼花撩亂看不出重點。一顆星都不想給，勸大家不要花冤枉錢，喝酒聚會講求氣氛，當天心情糟到不行！茶酒很強的一間酒館\n調酒種類多樣化\n又有創意很好玩很可以嘗試\n服務很好 會細心解說\n有酒感重的輕的都好喝\n店員貼心關心我們 還讓我們坐到店關門?\n下次一定還要再來吃餐點部份\n聽友人說很不錯呢！目前已經去過兩次（已約第三次\n菜品很驚艷，爆推豬肝和泡飯溫馨，美味，服務員又有明星臉，服務超好，下次一定再去', 4.7, 149, 700, 'o2Q4LI@gmail.com', '02 2776 3089', 4, '', 'https://m.facebook.com/%E7%B4%94Chunmix-105985504150937/', '0.jpg', '[0.jpg]', '2021-06-08 09.45.35 上午', '2021-06-08 09.45.35 上午', 149, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 205, 1, '97820255', '麵屋輝', '10491', '台北市', '中山區伊通街8-1號', 25.04956450, 121.53465610, '拉麵', '拉麵 前一陣子原本要被迫收店了，後來應觀眾要求後又重新裝潢後再出發。\n重新裝潢後店面變得更明亮乾淨，也把製麵室移到入口處。\n麵條是手工自製，今天做明天賣，湯頭是鮮魚和豚骨的完美融合，屬大阪風味。\n平日中午上班族很多，點餐要用販賣機，建議先看外面的菜單決定後再使用會比較清楚。\n原本的販賣機的操作有點難懂，有時也會怪怪的，機台上方有包乖乖寫"不會壞壞喔"，後來換了檯新的，變的比較好操作。\n如果還是不會操作會有日本的女服務生幫忙，如果沒有百元鈔票也可以請服務生幫忙換。\n沾麵是豚骨的柴魚湯底，味道相當濃厚，麵條是粗麵，拉麵則是使用細麵，兩塊叉燒一塊是梅花肉，一塊是五花肉，剛好瘦的肥的都有。\n中午時段+40元會有炸雞2塊和涼拌豆芽/小黃瓜2選1相當划算，炸雞鹹酥好吃，又撒滿黑胡椒粒，雖然這樣說有點失禮，但是味道跟頂呱呱的超像。\n重新開張後店裡不管服務或是餐點都感覺更好了，店裡不時有常客回訪，很開心這樣的好拉麵店能繼續在市場上。湯濃郁、麵好吃◇ 使用餐券自購機器\n◆ 純現金（百元鈔）\n◇ 店內舒適有冷氣\n◆ 免服務費、給麥茶\n◇ 不打包、不外帶\n◆ 無公休日（但有休息時間）\n◇ 有洗手間\n◆ 拉麵、沾麵\n◇ 蒜泥免費\n◆ 有開立發票\n◇ 平日有加購優惠\n◆ 捷運（松江南京站）\n\n以前就很常經過，但是一直沒有進去體驗～ 後來因為好奇味道，就直接體驗看看～\n\n店裡的品項不多，而餐點是自行用百元鈔的點餐機器，點完再把餐券給服務人員就可以，然後就會進行餐點製作。機器很好操作跟理解，還算不錯～ 主要是魚介豚股的拉麵、沾麵。\n\n第一次體驗，點了：\n叉燒魚介豚骨拉麵\n日式炸雞\n\n炸雞假日沒有加購單點，價格高一些，平日可以搭配麵，變成 2個 40元，雖然也不是特別便宜，但是體驗的話～ 還是可以接受\n\n等麵時，桌上有麥茶可以自行倒來喝，店裡很舒適，位置也寬敞，桌面也乾淨。等的時間好像不到 10 分鐘，麵就送來了～ 算是非常快的\n\n直接先喝一口湯，湯充滿了魚介的香氣以及濃厚的豚骨風，湯味道好，帶著濃厚但卻不死鹹，喝起來也無油膩感，我覺得很不錯。\n\n麵體本身不粗，有點接近細麵，所以在吃的時候會沾著湯汁一起被吸附上來，口感很好，軟硬適中～\n\n叉燒厚實大塊，吃起來有餃嚼勁跟口感，味道也不會過鹹或是跟麵不搭，比原本的 220元，升級為叉燒版，加了 60元，個人覺得很超值～\n\n再來是加點的炸雞，一般麵是主體，加單的配菜等等，都不會表現太厲害，所以一開始沒有太期待，但是一口咬下.... 雞肉豐厚多汁，肉汁就立即溢出來～ 而且味道平衡上也很好，不會太膩太鹹或太乾，我覺得很好吃～ 推薦！\n\n而加配料的時，有挑了免麵的蒜泥，加到麵裡後，湯頭又多了一分層次，蒜的香氣與湯融合的很恰當～ 個人也推薦加一下喔～\n\n而桌上的辣油，不辣不麻，但是帶有一股特殊香氣，加了之後口味上又一種不同的感受，推薦大家也都試試看。\n\n麵、湯、配料，味道都好，價格其實跟一般拉麵差不多，而麵會盤著湯汁一起吃，很有味道，是我喜歡的口感，後續也會再次回訪～\n\n如果真的要挑這家店的缺點，就是附近真的不好停車吧，建議搭到 松江南京站 再走過來，大概五分鐘就可以抵達喔！\n\n還有服務上可能要加強～ 對於進店裡的客人還是要招呼跟介紹，推薦一下拉麵或是說明口味，不能就站在客人旁邊，呆呆看著客人買餐券豚骨與魚介的混合雙湯，粗麵體的沾麵有嚼勁！一片五花配上一片梅花，雙重口感、肉質不柴。店內不收服務費，服務人員也很熱忱；是吃過C／P值最佳的一間純正日式拉麵店。沾麵值得推薦 ！\n人員流動率高，造成的品質不穩定嗎？沾麵醬汁巴不上麵條，鹹度也太淡了。醬汁沒有以前豚骨魚介的香氣跟喉韻，沾麵盛盤吃完下方還有一攤水 ...麵屋輝4/3\n第一次來訪麵屋輝 店面空間蠻寬敞舒適，燈光也不至於太暗，點餐機點完餐後即被引導到位子上，桌上有提供涼麥茶可供飲用\n\n沾麵：\n麵條：★★★☆☆\n冷麵偏硬，最裡面沒煮熟\n外面吃起來Q但要到底是硬的\n醬汁:★★★☆☆\n味道很充足濃郁，一開始豚骨的味道先出來，慢慢的帶入了洋蔥跟魚介的味道，最後嘴裡殘留著黑胡椒的清香，蠻有層次感的,但不知道為什麼，裡面會有硬硬的殼，我也不確定那是什麼\n叉燒：★★★★☆\n清爽不油膩，肥瘦適中\n湯頭：★★☆☆☆\n當沾麵吃完後，可以將剩餘的醬汁請服務員加入柴魚高湯變成豚骨魚介湯麵，請服務生加完湯後，嚐了第一口，柴魚湯加太多直接將豚骨魚介的味道稀釋掉，取而代之的是滿滿的柴魚味道，或許下次可以請服務員高湯量加少一點，而不是單純的把碗裝滿湯\n總結：★★★☆☆\n首次來訪麵屋輝，店面氣氛不錯，服務人員態度也都蠻熱情客氣的，可惜麵條部分偏硬且醬汁裡有殼，湯頭部分外場人員不會看剩餘醬汁決定加柴魚湯的多寡，導致最終出現的湯味道被埋沒掉，挺可惜的，沒意外應該是不會再回訪了，整體而言有點小失望', 4.1, 1705, 6990, 'dj5p5a8GFA@gmail.com', '02 2504 1800', 2, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00"]', 'http://terufood.com/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.38 上午', '2021-06-08 11.25.38 上午', 1705, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 206, 1, '59126548', '博多拉麵 市大店', '106', '台北市', '大安區市民大道四段74號', 25.04473290, 121.54759800, '拉麵', '拉麵 個人點了味增玉米海帶芽拉麵、鮮蝦可樂餅、燙青菜\n拉麵部分有點後悔，不是不好吃，而是我高估自己的食量，選了170的標準鍋，應該選140迷你鍋才對，份量上不論是麵、玉米、海帶芽都很多(是你吃太少)，湯味道感覺還普通不錯\n鮮蝦可樂餅好吃，雖然以50元個人覺得小\n燙青菜選擇有大陸妹跟高麗菜，我選了後者，呈現的份量約有1拳頭吧，價格是30元\n最後因為去得晚，也吃的慢，吃到超過他們的中午營業時間，感覺很不好意思?還行囉平價的拉麵，味道也很好吃，標準碗份量就很多了，1般食量的男生應該也可以吃飽，大碗太驚人，建議大胃王型的來點比較不怕吃不完浪費。在中午之前進去用餐…到了中午剛好看見絡繹不絕的人潮～\n此次用餐感覺很不錯^_^ 服務人員貼心的拿了髮圈過來, 還用很好的口吻回覆我問的笨問題(咖哩炸雞是什麼湯底?)。\n肉排拉麵…居然有二塊厚厚的，超滿足！\n停車…就停市民大道下，還算頗為方便的。\n(照片就隨便亂拍，哈)炸豆腐相當好吃，搭配上辣醬味道很棒，肉排咖哩麵份量十足', 4.2, 672, 2822, 'gEhb80@gmail.com', '02 2751 3630', 1, '', null, '0.jpg', '[0.jpg]', '2021-06-08 11.25.39 上午', '2021-06-08 11.25.39 上午', 672, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 207, 1, '83761530', '濃氣屋 拉麵 忠孝店', '106', '台北市', '大安區忠孝東路四段205巷9號B1', 25.04224250, 121.55198830, '拉麵', '拉麵 拉麵份量偏少 桌子 用具偏油 有看到 約3公分小蟑螂在 衛生紙盒上爬\n服務人員語速偏快但有禮貌\n會馬上給予大瓶冰水 並且有無限量供應咖哩飯\n炸雞翅還不錯\n———————————\n拉麵不怎麼樣 環境偏髒\n服務還行我終於來吃到了......\n咖啡飯真的很好吃?\n我點豚骨拉麵ㄘㄨ的..同事點細麵\n湯頭是濃郁的！可以吃飽的店 會送上一大壺水\n\n麵中規中矩 另有咖哩飯可以吃 雖然咖哩味濃但是我個人覺得飯很硬 …… 難道我要泡一下咖哩再吃嗎麵 湯 料 都不行餒\n麵條沒味道\n湯頭過鹹且容易膩\n肉片過硬\n溏心蛋一般\n\n整體沒有層次感\n食材們搭配不起來 各自表述\n\n雖然是餐廳\n但沒有餐廳應有的品味\n完全不會推薦來吃\n\n便宜的九湯屋 都比這個好吃\n自己煮的拉麵泡麵 也比較好吃…\n\n炸雞翅醃漬不太夠\n沒什麼香氣與味道\n麵皮也一般\n\n看了google星星還踩雷 唉\n吃了會心情down的一家店\n\n噢對了 裝潢一言難盡\n有點像 畫虎不成反類犬\n看不到用心 還有燈光昏暗\n\n僅有的一顆星 就給服務員吧叉燒軟嫩、味增湯頭帶有麻醬香氣可謂一湯多吃、咖哩飯的米粒軟中帶硬還可無限享用，炸雞鮮嫩多汁，用餐環境極具日本風格，這麼美味的食物不能只有我吃到。', 4.5, 1876, 8442, 'kkKDCb2n@gmail.com', '02 2771 1335', 2, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 23:00","星期六: 11:30 \\u2013 23:00","星期日: 11:30 \\u2013 22:00"]', 'https://www.facebook.com/nonkiyaramen2/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.41 上午', '2021-06-08 11.25.41 上午', 1876, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 208, 1, '06162210', '屯京拉麵 敦化南路店', '106', '台北市', '大安區敦化南路一段187巷36號', 25.04243390, 121.55116290, '拉麵', '拉麵 店員態度非常友善，五顆星讚讚！好吃，有兒童椅小孩可以來京蔥拉麵 沒有想像中的好吃 但還不錯了好吃! 算是滿喜歡的一間拉麵店之一 !服務態度差，人多排隊熱的要死', 4.1, 2287, 9377, '5LBOhvBe@gmail.com', '02 2731 7897', 2, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'http://foodexgroup.com/brand/#tunjing', '0.jpg', '[0.jpg]', '2021-06-08 11.25.42 上午', '2021-06-08 11.25.42 上午', 2287, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 209, 1, '32638285', '?Nagi 忠孝店', '106', '台北市', '大安區大安路一段75巷5號1', 25.04231970, 121.54657540, '拉麵', '拉麵 今天點的是限定王！麥笛王！烤過的牛胸肉切片！紅洋蔥 赤王球（很像肉泥）風味油 豚骨湯，最驚艷的是醃小黃瓜丁，意外的非常搭這次的湯頭跟麵條！但個人口味稍淡，所以吃起來有些負擔?\n—————————\n叉燒飯爆雷，不要亂點QQ第一次吃nagi 排蠻多人的\n\n赤王 / 235元 加糖心蛋30元\n從湯底、配料到麵體全部都可以客製化\n我選：\n鹹度普通、風味油普通、蒜蓉普通\n豬楠肉（有豬肩肉可選）、高麗菜（有蔥可選）\n細麵、三辛\n味噌和辣度滿舒服的 不是很嗆那種\n湯不太沾高麗菜所以菜跟湯的味道滿分離的\n溫泉蛋好吃 是滿滿蛋黃液那種?\n我聽說粗麵不好吃就選細麵\n豬楠肉蠻嫩的 而且肥瘦剛好\n\n除了蛋要加錢之外沒什麼不喜歡的\n有喝一口豚骨的湯（配置除了蒜多其他都是普通）\n覺得真的很普～～～\n但是桌上有豆芽菜跟酸菜可以加蠻棒的\n而且豆芽菜調味好吃！因店員說細麵是招牌所以點了硬細麵，但咬下去第一口就開始懷疑是不是劃錯單...完全沒有「硬麵」的感覺，讓人懷疑硬麵都如此，難道軟麵是可以入口即化嗎？\n\n角煮豬腩肉還可以，整體偏瘦，可能對一些人來說會覺得有點柴。\n\n湯頭醬汁鹹度選濃，但喝起來比辛拉麵還不鹹，整體而言是一碗讓人懷疑是不是送錯單的拉麵。\n\n兩顆星給外場服務生，外場服務態度很客氣，結帳時告知麵條過軟的問題，服務生表示下次可以立刻反應他們會重做一份。\n印象中之前在西門店用餐的體驗是還可以，可能各分店的狀況還是有差吧！我點的是豚王 不錯吃 桌上還有豆芽菜 配拉麵也好吃 後來吃了一碗還沒飽所以加點了叉燒飯 本來很期待 但不知道為什麼聞起來跟吃起來都有一種香精味 本來不太想吃 吃得有點乾 想說吃一口飯配一口湯 結果意外的超搭 變得超好吃 所以還是把它吃光了 整體而言都還不錯 會想去第二次?這次用餐吃的是限定王，湯頭濃郁爽口不油膩，叉燒入味軟嫩，麵條可依個人喜好選擇硬度，食量大的可以點白飯，在麵條吃完，加白飯下去攪拌後吃，整碗麵終告段落，滿足了五臟廟', 4.4, 3194, 14054, 'jv4sglh@gmail.com', '02 2778 1899', 2, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 11:30 \\u2013 21:00"]', 'https://www.facebook.com/NagiTaiwan/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.43 上午', '2021-06-08 11.25.43 上午', 3194, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 210, 1, '55556343', '赤?拉麵', '10491', '台北市', '中山區錦州街394號', 25.06008880, 121.54122690, '拉麵', '拉麵 拉麵很普普，叉燒一塊，豆芽菜沒很好吃\n有點台，不過價格是蠻評價的\n餐點除了拉麵外還有炸物\n有套餐選項：拉麵+炸物+飲料（可樂&美麗果之類的）一般都是170，只有加炸豬排的是190\n套餐類的拉麵有咖哩的豚骨的、白湯的等等\n炸物搭配有花枝丸、炸豆腐、炸花枝圈等等\n\n炸豆腐很好吃，可以點，豆腐的沾醬是有點帶甜的醬油膏，蠻好吃的\n碳酸氣泡飲料是玻璃瓶裝，因此十分有氣，配炸物很爽\n店內也有免費的熱茶可以喝\n可以免費加湯但只有在點餐的時候免費，後續要加要付錢\n老闆說可調濃淡\n\n其他資訊\n調料僅有辣椒粉粒\n店內有免費WiFi\n免付發票店家\n僅收現金無意間發現這間拉麵店\n身為日本拉麵的愛好者一定要過來看看\n\n點了白湯後來又改了豚骨拉麵\n拉麵口感不錯\n湯頭不會很鹹\n也可以一開始跟櫃檯說要多湯\n以100元這樣的CP值很棒了\n\n最重要是吃完不會一直口渴\n炸豆腐是3聽網友評論後加點\n好吃，又有特製醬汁\n值回票價偶然經過的拉麵店，看得出店內佈置有用心，茶水自取免費供應，拉麵的價格對得起它的味道，服務人員態度佳，叉燒拉麵的肉份量還行，算是CP值不錯!\n但請不要跟其他日本拉麵店做比較，因為口味及客層是不一樣的，這間是改良的台式口味。偶然經過這間拉麵店?\n沒想到一進來就成了主顧\n而且也滿足了\n下午吃不到好吃的食物的問題\n老闆的職人精神\n讓這間拉麵店\n兼具美味跟高CP值的店\n\n蠻值得推薦大家來試試老闆的手藝湯頭好，麵的口感也很好！\n老闆蠻貼心的，別桌客人離開後，有空出來的座位時，整理乾淨後，主動問我們要不要換桌(座位比較舒服)', 4, 405, 1620, 'JmCCuUb@gmail.com', '02 2501 0218', 2, '["星期一: 11:30 \\u2013 21:15","星期二: 休息","星期三: 11:30 \\u2013 21:15","星期四: 11:30 \\u2013 21:15","星期五: 11:30 \\u2013 21:15","星期六: 11:30 \\u2013 21:15","星期日: 11:30 \\u2013 21:15"]', 'https://japanese-restaurant-5806.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-08 11.25.44 上午', '2021-06-08 11.25.44 上午', 405, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 211, 1, '20701961', '山頭火拉麵 SOGO復興店', '106', '台北市', '大安區忠孝東路三段300號B2', 25.04089320, 121.54324170, '拉麵', '拉麵 ★拉麵迷請進→FB搜尋：『台灣拉麵愛好會』、IG：twramenclub\n\n品質穩定且服務良好，不過售價上偏高一些且有服務費。\n記得要達成VIP或是搭配信用卡免服務費。\n\n▎拉麵名稱／價格：味噌拉麵(小)+煙燻黑胡椒鴨肉／(200+120)*10%\n▎配置：無調整\n▎延伸：#感謝LINE中獎\n\n▎味噌拉麵(小)+煙燻黑胡椒鴨肉:\n\n味噌拉麵是定番4種口味中的其中一項，辣味噌因為我不吃辣所以沒有嘗試過。\n目前3種品項我自己吃過，我覺得味噌的最合拍，不愧是北海道的拉麵店味噌處理的有到水準。\n\n豚骨湯頭濃度本身就OK，搭配味噌的濃烈味道讓這碗湯第一口就令人深刻。\n味噌的味道濃郁且有層次，是喜歡的味噌拉麵類型。\n\n麵條口感軟硬度適中，微捲所以帶湯會比較好一些。\n預設的叉燒肉就普通，覺得還是豬頰肉好，山頭火的特色之一。\n\n加點的煙燻黑胡椒鴨肉很嫩很好吃，煙燻味帶點胡椒的香辣，真的很棒！\n推薦~好鹹的麵 可能台灣口味不適應超級爛\n欺騙消費者\n醬汁炸豬完全是騙人的，只用一片肉包起來????，還很鹹\n麵也是，加了50元的麵，完全感覺沒加到??????\n不值得推薦吃個北海道的山頭火，想說來吃吃看台灣的，結果超級難吃，豬頸肉口感太軟，調味太鹹，拉麵吃起來很像泡麵，太令人生氣了！但日本的是真的非常好吃豬頰肉鹽味拉麵好吃！豬頰肉超嫩！\n\n價錢小貴，但女生吃完覺得很飽。\n\n服務生態度和藹可親。', 3.6, 540, 1944, 'eTFC1CF@gmail.com', '02 2731 7533', 2, '["星期一: 11:00 \\u2013 21:30","星期二: 11:00 \\u2013 21:30","星期三: 11:00 \\u2013 21:30","星期四: 11:00 \\u2013 21:30","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 21:30"]', 'http://www.santouka.co.jp/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.45 上午', '2021-06-08 11.25.45 上午', 540, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 212, 1, '12859879', '日本拉麵', '106', '台北市', '大安區忠孝東路三段251巷3弄1號號', 25.04243940, 121.54167370, '拉麵', '拉麵 其實開了很多年的一家店，也是覺得小貴但各方面都中規中矩的不錯，不特別驚艷就是。\n\n20200914更新?\n炸雞！超強！必點！而且單點一份8塊有夠多！\n薑燒豬肉很普，不算好吃。但份量有夠大，吃得飽。看起來很普通的店，但是咖哩很好吃，分量很夠醬油拉麵的湯不錯1.店家禁止採訪/部落客/拍照\n2.一組客人四人以上時只能一起結帳(不能分開結)\n3.點餐時結帳\n4.餐具調味料在中間柱子要自取\n\n食物推薦及描述:\n1.炸雞是一定要推薦的，酥脆鮮嫩多汁。\n如果喜歡沾醬的人可以試試看塔塔醬，他們的醬有自己調過，微酸填填洋蔥香味，值得品嚐。\n\n2.招牌博多豚骨拉麵:\n海帶芽及玉米少許，灑上芝麻和一點蔥花，兩片大片的新鮮青菜(今天青菜是大陸妹)，叉燒肉片約0.5m直徑10cm一片。\n湯頭乳白但不會油膩，很清淡不會過鹹。\n麵條細麵，吃起來口感Q彈，咀嚼時有麵香，還不錯。\n\n3.和風醬油拉麵:\n配料相同。湯頭部分微甜，大概是昆布湯頭(?)，自己覺得有點像壽喜燒稀釋後的味道，但我哥說不像囧\n\n不過比起豚骨，我比較喜歡醬油的湯\n(一般我個人比較喜歡豚骨，但這邊的豚骨湯很健康，所以偏甜的醬油湯頭就比較得我心)從小吃到大', 4.3, 25, 108, '8kEoblP@gmail.com', '02-54669352', 4, '["星期一: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期二: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期三: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期四: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期五: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期六: 17:00 \\u2013 21:00, 23:30 \\u2013 00:00","星期日: 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 11.25.47 上午', '2021-06-08 11.25.47 上午', 25, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 213, 1, '75283403', '三友拉麵(民生店)', '105', '台北市', '松山區民生東路四段112巷1弄18號', 25.05773140, 121.55518450, '拉麵', '拉麵 一碗加了一成服務費要三百多的拉麵擺成這個樣，湯頭濃淡跟麵條軟硬可調整，不難吃，但也沒什麼特色會讓人想再回去吃的店.收了一成服務費，連第一杯水都是自己倒，服務了什麼？裝潢就是濃濃和風，平常幾乎均可滿座78成。麵條稍硬，湯濃卻過鹹；大概就是比照日本。停車要走遠些；最近就是附近全聯後面的立體停車場。還沒去麵是硬的，16：30過去，一附趕著下班的樣子，價格是貴在用餐環境上比較一般的日式拉麵，服務卻遠遠不及日本的服務，如果能帶點溫度與敬業精神就更好了。', 3.9, 861, 3358, 'qWzQ2w@gmail.com', '02 2545 0606', 2, '["星期一: 11:00 \\u2013 20:30","星期二: 11:00 \\u2013 20:30","星期三: 11:00 \\u2013 20:30","星期四: 11:00 \\u2013 20:30","星期五: 11:00 \\u2013 20:30","星期六: 11:00 \\u2013 20:30","星期日: 11:00 \\u2013 20:30"]', 'https://m.facebook.com/threefriends168/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-08 11.25.51 上午', '2021-06-08 11.25.51 上午', 861, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 214, 1, '43034665', '拉麵公子', '104', '台北市', '中山區八德路二段279號', 25.04711260, 121.54168900, '拉麵', '拉麵 【蘭姆酒濃厚咖哩拉麵 | $300 | 4★】\n時間：2021/01/02\n〈店家介紹〉\n?蘭姆酒使用了「加勒比海北海巨妖加香黑蘭姆酒」，再調入了香氣中帶有太妃糖、焦糖、甘草、烘焙香蕉麵包香的琥珀蘭姆酒，融進濃厚的咖哩湯之中，酒味稍微明顯，暖感會隨著酒香瀰漫整個口腔！配料附有風乾伊比利豬火腿、紹興酒封豬叉燒、君度橙酒溏心蛋、白蘭地糖漬富士蘋果丁！\n\n?註：本次沒有提供麵條種類選擇與辣度選擇。預設麵條使用粗捲麵，口味是小辣的程度，三種風乾伊比利豬火腿隨機出餐。\n〈個人心得〉\n星期六晚上的場次，17：05到是排在第十位，safe！湯頭基底感覺是日式咖哩，有微微的辣然後帶著滿滿酒香，吃完之後感覺全身都熱起來，不知道是不是酒精的緣故。我嚐不出來糖心蛋的橙酒味，應該是完全被湯蓋過去了。最有意思的是紹興酒叉燒，臺灣菜中熟悉的風味，不錯。\n\n【濃厚魚介雞白湯沾麵 】〈中盛200g、雙倍叉燒〉 價格$320+50 評分4.5★\n\n麵量可選中盛 200g、大盛 300g、特大盛 400g(好像+30)。\n比起平常多了炙燒豬梅花叉燒肉，叉燒肉雙倍份量(+50)、叉燒肉三倍增量(+？沒注意)。\n\n餐點上來直接喝一口湯試試，沾湯的膠質濃郁表面都凝結一層了，湯上面也看起來油亮亮的，喝起來鮮甜，鰹魚類的微酸跟豐富香氣超棒，沾麵帶點嚼勁軟硬度對我來說適中，吃到湯冷掉也可以再請店家加熱。叉燒有三種類舒肥雞肉、舒肥豬肉、炙燒豬梅花叉燒，我從清淡的開始嚐，雞肉不會膩口，舒肥豬肉跟炙燒梅花叉燒也好吃。吃完麵之後把清湯加入沾湯，喝完滿滿的精華。雖然味道上沒有讓人感到驚喜，但是整體表現不錯都很滿意。\n時間：2020_12_05\n\n「濃厚秋刀魚煮干雞白湯拉麵」4★\n第一次吃煮干拉麵，老闆是說有特意讓煮干的份量輕一點，所以吃起來有魚干風味但不至於過腥過苦，我覺得是普通人能接受的程度，還不錯。秋刀魚也是好吃，只是湯的主軸還是煮干比較沒有秋刀魚的感覺，可能我還沒辦法分開兩者的味道吧?，麵條部分這次好像是分成低加水細麵跟粗捲麵，選了粗捲麵也感覺口感不錯。\n2020.07.11\n\n「濃厚魚介螢賊拉麵」? 4.2★\n「煎櫻桃鴨胸肉」4★\n湯頭是再濃一點就變沾湯的程度，魚鮮味濃郁呀，就像老闆說的溫和醇厚，不過稍嫌有點鹹就是了。這款搭配手揉寬扁麵，從上桌一直到吃完麵時都是麵芯偏乾硬的感覺，而我會更喜歡勝王手揉麵那種雖厚但帶著彈性的口感。螢光烏賊帶著小豆島柚子香跟果酸味，很適合當成餐前的開胃小菜吃。配料的叉燒這次不像上次吃的滿意，但是筍干滷的鹹甜鹹甜意外的好吃。因為很滿意上次吃的鴨肉飯，就另外加點了煎鴨胸肉試試，肉經過舒肥保持軟嫩，調味上似乎也有加入些酸味，不知是否也是柚子汁，煎過的鴨肉反倒是沒有感受到鴨油的香氣，也沒有焦脆的鴨皮，其實是有點失望，想說早知道就點鴨肉飯，不過整體還是好吃的鴨肉。\n2020.05.02\n\n鹽生薑雞湯拉麵 4.5★\n比起鹽味雞湯多了薑的提味，除了生薑泥也放了花雕酒、薑酒，味道上更複雜些，是碗很暖胃好喝的湯頭。不過叉燒就只有雞肉哦。\n櫻桃鴨肉飯5★\n嘉義雞肉飯的靈魂是雞油，這碗鴨肉飯的靈魂絕對就是鴨油，香氣超級讚，櫻桃鴨的肉味跟一般吃到的鴨肉相比更醇厚鮮甜，好吃。\n(2020.01.04)\n\n2019.11.07\n鹽味雞湯溏心蛋拉麵 4★\n鹽味雞湯雖然清澈不過味道挺好，鹽味有帶出雞湯的鮮味。麵體的硬度我覺得偏硬，個人喜好大概是送上來四五分鐘後的硬度，比原本設定的再軟一些。魚板味道比較好，叉燒的味道都不錯，喜歡舒肥豬叉燒勝過舒肥雞叉燒，溏心蛋是咬下一口蛋黃會流洩而出的程度。海苔就沒特別喜歡，海苔好像是沒有調味。20210408 當天點的菜單（不是專業拍，是很日常隨手拍）\n\n我們點了\n??濃厚魚介螢賊墨魚麵 (期間限定) $310 ??????????\n??濃厚煮干雞白湯拉麵 $280 ????????\n??溏心蛋 $30 ??????????\n??白飯 $30 ??????\n\n??濃厚魚介螢賊墨魚麵 (期間限定) $310 （大推）??\n首先這碗拉麵的配料十分豐富(豬叉燒、雞叉燒、螢烏賊、青蔥、溏心蛋)\n主角螢烏賊的新鮮程度沒話說，有些許醃漬過，一咬下去整個鮮甜感爆發。\n湯頭基底是店家招牌的雞白湯佐以大量小卷與水產乾物熬製而成，味道溫和不會太濃(味道香醇沒話說!!!)\n輕鬆完食不會有負擔的湯頭，麵體部分是墨魚寬麵，我們其實都不太愛寬麵，但吃起來是有一定的Q彈感，麵體偏有回饋感~(如果喜歡較軟的麵體建議略過)\n\n??濃厚煮干雞白湯拉麵 $280 （推）??\n選擇最濃厚最重鹹的選項，配料一樣走豐富路線(豬叉燒、雞叉燒、溏心蛋、海帶、洋蔥丁、青蔥)\n湯頭同樣以店家招牌的雞白湯為基底，加入大量煮干熬煮而成~\n湯麵一端上來，濃重的魚煮干味道撲鼻而來，在嚐了一口湯頭~味道非常濃厚帶有煮干的苦韻，鹹味跟苦味交織在口中碰撞!\n感覺到膩感的時候，吃一口洋蔥丁來解膩，另外麵體部分使用細麵偏硬的口感，配上濃厚的湯體，這個組合真的沒話說，喜歡煮干的非常推薦。\n\n??溏心蛋 $30 （推）??\n熟度、口感、醃漬的味道都相當完美，中間蛋黃部分咬下去整個流出來~\n以拉麵店的水準來說是非常優質的溏心蛋。\n\n??白飯 $30\n主要用來加入湯裡面的(口感偏硬)，加入湯後就沒有偏硬的問題了!\n大力推薦點一碗白飯搭配拉麵湯頭來吃。\n\n總結：雖然需要排隊(但沒有排非常久)\n拉麵的部分在湯頭跟拉麵(Q度、口感)都十分的有水準!\n尤其湯頭濃厚香醇，另外配料方面品質都相當的高，在拉麵店家裡算是優於平均的一個選擇。大約七點半到 前面只有一組人運氣不錯\n點的內容是雞白湯拉麵+舒肥豬叉燒\n\n端上桌映入眼簾的是一碗很簡約的拉麵\n沒有太多多餘的裝飾\n一片舒肥雞 一片舒肥豬\n一片海苔 少許的綠色蔬菜與洋蔥末\n\n先送入口中的雞白湯\n不過於濃郁但帶洋蔥味的香醇\n沒有豚骨拉麵般的負擔 是一種Lite的小清新\n接著卷麵恰到好處的硬度 一口接著一口 Q彈順口\n舒肥雞叉燒的滑嫩是一種略帶夢幻的滑順觸口感\n而舒肥豬叉燒厚實的存在感在口中是種滿足\n整體感覺是一碗均衡不強勢的美味拉麵\n適合慰勞剛下班已疲憊不堪的靈魂20200727『濃厚秋刀魚白湯拉麵+豬叉燒3片』\n\n非常舒服順口的湯頭，鮮味與鹹度恰到好處\n有濃稠度但十分清爽的湯一飲而盡也毫無負擔\n佐以洋蔥丁和青蔥末，沒有一絲油膩\n麵體有些捲且偏細，口感稍嫌硬了點\n\n舒肥豬叉燒、舒肥雞叉燒、秋刀魚片各一\n豬叉燒的調味很夠，雞叉燒則是相當軟嫩\n秋刀魚帶有迷人的焦香，魚肉細緻而味美\n溏心蛋的熟度合格，蛋黃為不濃稠的流體\n加點的三片舒肥豬叉燒同樣很夠味\n厚度切得正好，肉質也頗緊實有嚼勁IG: Ludybaal\n\n【拉麵公子】\n濃厚煮干雞白湯拉麵 特製鯷魚醬油版\n&櫻桃鴨肉飯\n\n人生第一次煮干?\n剛剛爬了一下文區分魚介跟煮干的差別\n魚介以魚骨（魚）以及貝類（介）熬煮\n而煮干則是多種小魚干熬煮而成\n兩種湯底就會混入其他湯頭\n\n初嘗的第一口濃郁的魚乾味\n複雜濃厚撲鼻而來\n帶點魚乾本身的苦味\n洋蔥的甜稍稍可以解膩 不過沒有到突出\n比較像是隱藏於其中\n至於鯷魚醬油應該就是融入其中\n沒有特別比較原味應該是感覺不出來\n（或是我裡面的其中一味就是了）\n麵體偏硬而且是不馬虎的那種\n舒肥雞叉燒是難得的軟嫩\n大概是近期雞叉燒心目中的高標\n豚叉燒印象比較平淡\n整顆溏心蛋算是滿成功的\n流質帶點半熟感\n\n櫻桃鴨肉飯非常簡單就是四片鴨肉\n底下則是淋上醬油以及應該是鴨油\n其實吃起來非常中式鴨肉飯的感覺\n直接這樣吃就是樸實無華的簡單美味\n當然我點來必定要拿來拌入煮干裡面吃\n其實吃到後期煮干的苦味已經蓋過一切\n不會覺得鹹膩而是苦\n對於不吃苦瓜不咖啡的我來說有點吃力\n再加上店內沒有提供清高湯\n因此白飯是大量增加完食程序的好幫手\n另外鴨肉雖然還是有它原本肉質較軟韌\n不易咀嚼的特性 不過舒肥後已改善很多了\n尤其每一口吃得到有點柑橘亦或橙汁味\n印象滿不錯的\n\n整體來說 煮干初體驗算是不錯\n怕吃苦的我還是兩碗都完食了\n雖然不敢確定下次還會不會想再試\n不過愛吃濃厚的饕客必定要試試這一味\n至於新手的話個人應該不建議直接挑戰', 4.4, 874, 3846, 'i8dPHhZ51@gmail.com', '02-23641521', 2, '["星期一: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期二: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期三: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期四: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期五: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期六: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30","星期日: 11:30 \\u2013 13:30, 17:30 \\u2013 20:30"]', 'https://www.facebook.com/theramenboy', '0.jpg', '[0.jpg]', '2021-06-08 11.25.52 上午', '2021-06-08 11.25.52 上午', 874, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 215, 1, '49701486', '浪魂拉麵 ? 丼飯', '104', '台北市', '中山區錦州街446號', 25.06011520, 121.54318350, '拉麵', '拉麵 前台處理能力超差，等了超過半小時未上菜，忙著趕給外送單，沒辦法接單一開始就應告知，而非讓人枯等!薑汁燒肉丼飯有不少蔬菜，雖然味道一般，但CP質Ok\n豬排非常酥脆好吃～配上豬排醬的味道非常棒！\n然後他們的味噌湯料非常多，味道層次滿豐富的，很少有味增湯這麼多料的日式豬排定食，皮酥脆炸的非常好吃～但薑汁豬肉丼飯滿普通的，比較不推薦～\n用餐環境明亮乾淨，滿舒適的！覺得好吃！\n拉麵可以選擇麵條軟硬、粗細、湯頭濃度跟要不要加蔥，\n豬排飯吃超飽！\n點完餐要先結帳！豚骨拉麵\n台式口味\n麵條普通偏硬\n湯頭沒什麼特別\n唯獨叉燒有點水準\n\n味噌湯非常難喝\n\n故給三顆星', 3.9, 75, 293, 'IzSuSw@gmail.com', '02 2503 8827', 4, '["星期一: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期六: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:30 \\u2013 14:30, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 11.25.53 上午', '2021-06-08 11.25.53 上午', 75, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 216, 1, '42746834', '裸湯拉麵', '106', '台北市', '大安區敦化南路一段238巷1號', 25.04029810, 121.54789390, '拉麵', '拉麵 ?裸湯拉麵（敦化南路店）\n\n平日晚間7點抵達，不用候位，直接入座！店員會到座位點餐，服務很很親切！\n餐具茶水自取，吃完後再到櫃檯結帳唷（目前只收現金）\n\n??蒜味拉麵（兩倍蒜??） $210\n拉麵裡有銀芽、筍乾、木耳、蒜酥、蔥絲、兩片叉燒肉、半顆溏心蛋）\n拉麵是黃色麵體、捲捲的那種（我有特別說麵體硬一點），麵吃起來有點像是較粗較有咬勁的辛辣麵！是好吃的，只是比較不像是我想像中的「拉麵」?\n雖然我點兩倍蒜，但不會讓整碗的油膩感很重，湯底還是清爽的，這點加分！\n叉燒很薄、炙燒感不是很強烈，不過今天這碗的叉燒油脂佔的比例較重，可能比較不對我今天的胃口?\n\n??唐揚雞 $80\n我喜歡這個唐揚雞！有5大塊，是個CP值頗高的小菜！顏色不是那種金黃的，好像會讓你以為炸的過老，但是！脆度很好、雞肉本身又很多汁，雞汁都鎖在裡面！推薦想點小菜配的，可以選擇唐揚雞?\n\n??胡麻龍鬚菜 $45\n好吃的胡麻醬配上清脆的龍鬚菜，讓你的一餐增添一點健康的滋味?屬於「輕」拉麵\n第一口就意外了\n腦裡浮現的是：鹽巴咧？\n無論是配菜、肉、麵體本身都沒加鹽巴的感覺\n唯一的微鹹度來自湯底本身\n麵給的量很足、肉偏薄但炙燒的香氣咬下去秒撲鼻\n溏心蛋很乖、很標配\n意外蒜味等級可以自選 ?\n\n辣味拉麵選了2級辣度\n廚師毫不手軟 有辣！有2級的辣\n屬於刺激型的辣，痛痛的 甩你兩巴掌\n但下肚後 又沒事了\n\n泡菜有種好市多的滋味\n\n叉燒飯CP值爆炸高\n小碗的$150 有滿～～～～滿的肉，雖然偏乾，但加上一點芥末美乃滋 還是很滿足肉食人的我 還送飲品在台北算平價的拉麵～\n湯好喝，不會過於濃厚但味道很夠?\n小缺點是叉燒很薄很普\n\n（但它本來就主打湯頭吧?）\n不用排隊的店真的很棒???裸湯拉麵（不加豆芽）\n湯頭：豬骨+雞骨熬製的高湯\n口味濃郁但不至於膩口。麵條是偏蛋黃色的Q彈中等粗細麵條，的確像其他評論陳述，有點相似於辛拉麵。叉燒有炙燒後的香氣，但真的太油，如果沒有在上桌後立刻食用，叉燒上的油會影響拉麵的風味，破壞高湯的平衡。裝潢蠻文青的拉麵店 好吃\n沾麵很適合夏天清爽\n湯的拉麵濃霧\n小菜們也值得嘗試?', 4.2, 761, 3196, 'jpEyMs1ax@gmail.com', '02 8771 4670', 4, '["星期一: 11:00 \\u2013 21:30","星期二: 11:00 \\u2013 21:30","星期三: 11:00 \\u2013 21:30","星期四: 11:00 \\u2013 21:30","星期五: 11:00 \\u2013 21:30","星期六: 11:00 \\u2013 21:30","星期日: 11:00 \\u2013 21:30"]', 'http://www.facebook.com/PureRamen', '0.jpg', '[0.jpg]', '2021-06-08 11.25.54 上午', '2021-06-08 11.25.54 上午', 761, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 217, 1, '65006868', '博多幸龍總本店(博多豚骨拉麵?門店) ｜松山區 八德路 忠孝復興站 南京復興站 拉麵店/外帶/外送', '10552', '台北市', 'No. 16號, 八德路二段366巷松山區', 25.04746070, 121.54587600, '拉麵', '拉麵 好好吃的拉麵呀！不死鹹，湯頭很用心！謝謝用心的老闆！像回到日本一樣（氣氛\n湯頭濃厚好喝?大推～味道不錯吃好吃有空去品嚐看看 會感覺吃起來都早餐特別好吃??好吃\n開到三點真的是夜貓子的福音啊', 4.7, 1561, 7337, 'QlNdLtUABq@gmail.com', '02 2752 1050', 2, '["星期一: 11:00 \\u2013 14:30, 17:00 \\u2013 00:00","星期二: 11:00 \\u2013 14:30, 17:00 \\u2013 00:00","星期三: 11:00 \\u2013 14:30, 17:00 \\u2013 00:00","星期四: 11:00 \\u2013 14:30, 17:00 \\u2013 00:00","星期五: 11:00 \\u2013 14:30, 17:00 \\u2013 00:00","星期六: 休息","星期日: 休息"]', 'https://www.facebook.com/%E5%8D%9A%E5%A4%9A%E5%B9%B8%E9%BE%8DHakata-KORYU-1994391200597943/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.55 上午', '2021-06-08 11.25.55 上午', 1561, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 218, 1, '32591770', '咕咕雞白湯拉麵', '106', '台北市', '大安區忠孝東路四段45號B1', 25.04193110, 121.54465580, '拉麵', '拉麵 以美食街的標準來說水準非常高，點雞油拌麵有附雞白湯（可續一次）+胡麻豆腐/燙青菜二選一\n麵是黃色有點嚼勁的粗麵，陪上雞油跟筍乾很搭，要趁熱快吃不然冷了會變油。溏心蛋很有水準，甚至比外面有些拉麵店的溏心蛋都還要好吃?\n湯品的部分喝下去暖暖的感覺很補，冬天很適合偶爾來補一下。\n胡麻豆腐冰冰涼涼的很清爽，可以中和一下雞油的油膩感。\n如果下次還有要去sogo 吃東西會再回訪，是一家很不錯的餐廳選擇湯比想像中濃郁！雞肉像是舒肥的雞胸肉\n\n拌麵也做的很有嚼勁，所附的湯也是拉麵湯\n\n算在美食街裡滿好吃的作為百貨公司美食區拉麵來說，已經蠻優秀的，日式雞白湯頭偏鹹味道夠味，價格親民，豬叉燒偏油，雞肉叉燒跟蛋剛剛好，套餐220元有燙青菜跟黑豆茶，算不錯的選擇?跟家人一起來吃了一次之後就常常會幫忙外帶這裡的拉麵，服務生很有禮拜而且出餐真的很快 !!不管是蛋肉還是湯頭我覺得都很喜歡，而且在台北市的話這樣的套餐真的不貴，麵稍微再多一點就好了!!!給4顆星!出餐速度超快!幾乎一點完就可以拿餐了，湯頭沒有一般拉麵店那麼濃郁，但是我跟家人都覺得剛剛好，肉很軟嫩肥瘦適中以美食街來說我覺得還不錯。', 4.3, 69, 297, 'SiTLCwBvzP@gmail.com', '02 2721 7798', 4, '["星期一: 11:30 \\u2013 20:00","星期二: 11:30 \\u2013 20:00","星期三: 11:30 \\u2013 20:00","星期四: 11:30 \\u2013 20:00","星期五: 11:30 \\u2013 20:00","星期六: 11:30 \\u2013 20:00","星期日: 11:30 \\u2013 20:00"]', 'https://www.facebook.com/gugu.ramen/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.56 上午', '2021-06-08 11.25.56 上午', 69, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 219, 1, '60792443', '鷹流東京醬油拉麵蘭丸', '105', '台北市', '松山區延吉街2號', 25.04799930, 121.55330510, '拉麵', '拉麵 整體來說還會再回訪～～給4顆星是因為在外頭的排隊體驗實在太不好了（雖然有上網做功課但還是不確定哪些資訊才是正確的）\n\n第一次吃肉肉驚艷的鷹流拉麵～\n排隊的時候別擋住了隔壁店家，遇到隔壁店家排隊要折返～但有人會從另一個方向來，不知道也是第一次來吃還是本來想插隊....總之在外頭排隊的體驗不是很好，排隊方式以及點餐方式標示的不算明確，有人會還沒排到前六位就去點餐機（我也不清楚這樣子可不可行）點餐機雖然一旁有菜單跟解釋，但很多相似字詞，少了餐點圖輔助說明有點讓人難以理解。\n\n糖心蛋30元，是進店內後可以自行投幣後拿蛋～\n餐點很快上桌，有些肉有些脆脆的！湯頭點原味，一開始吃很讚，可以吃的出層次感，不過吃到後面就會滿鹹的，應該可以再請老闆沖淡湯繼續喝湯。\n\n麵體的部分普通，不過可以免費續麵這點很吸引人?\n\n題外話：發現老闆一開始問湯頭要原味還是清淡的，客人都會先回答「蛤」，但老闆還是很有耐心的再詢問一次?第一次吃到這麼難吃的拉麵QQ，湯頭單一死鹹，不是說叫醬油拉麵就可以嚐起來完全是碗醬油湯的吧@@。除去食物之外，店內用餐環境髒亂，店外排隊環境也欠佳。我還得幫上一位用餐的客人擦桌子，湯漬油膩什麼的還有遺留下的衛生紙，放了一塊抹布在用餐的客人面前也十分倒胃口，坐在店外椅子上等待時也是水溝在腳下，吸著來往汽機車的廢氣。看著店家在店內的小牌子寫著請用餐客人具同理心，不要讓後面的人等太久，真是覺得格外諷刺。最不具同理心的其實就是店家本身了吧，讓客人體諒店家忙碌，所以請客人自己維護用餐桌面整潔喔；放個骯髒抹布在客人面前，美其名是方便客人使用，實際上是店家不想幫你處理；讓客人們在那邊吸廢氣等待，營造出本店是排隊名店的表象，卻不思考是不是有讓客人們更舒適的等待方式呢？例如拿號碼牌等等。總之，覺得是極差的用餐體驗，別人說的店員態度不好的情況我倒是沒有遇到，但覺得很多項目是值得店家思考改善的。態度很差\n之前常來\n規則都知道\n最近疫情店家都有各自的新規定\n今天去路過看沒人排隊\n開門看到裡面有兩位客人\n正要詢問可以用餐嗎\n店員直接說要等\n接著要問現在要怎麼排有沒有新規定\n直接不給問叫我門關上\n當然我也直接離開\n以後也不會再來\n不平價的餐廳今天也沒客人可以忙態度還這麼跩店內位子很少，基本上一定要排隊，先在店外機器購買餐券（注意只收100、10、50）。\n\n可以加湯加麵，價格實在，日式道地醬油湯頭偏重口味，怕油可選清湯。溫泉蛋自行投錢取用。老闆會一直關心要不要加湯加麵。1. 拉麵超讚，湯頭鹹但不死鹹，麵條軟硬適中，叉燒讚。\n\n2. 餐點的視覺效果很讚，很適合拍照打卡。\n\n3 .只收現金，建議備妥百元鈔來買餐券。\n\n4. 位子很少，要有排隊排爆的心理準備。\n\n5. 蛋世界鹹，吃了會有想洗腎的感覺。', 4.1, 2209, 9057, 'XiwXgWxZ@gmail.com', '02-67767515', 2, '["星期一: 11:30 \\u2013 16:00, 17:00 \\u2013 22:00","星期二: 11:30 \\u2013 16:00, 17:00 \\u2013 22:00","星期三: 11:30 \\u2013 16:00, 17:00 \\u2013 22:00","星期四: 11:30 \\u2013 16:00, 17:00 \\u2013 22:00","星期五: 11:30 \\u2013 16:00, 17:00 \\u2013 22:00","星期六: 11:30 \\u2013 22:00","星期日: 11:30 \\u2013 22:00"]', 'https://www.facebook.com/%E9%B7%B9%E6%B5%81%E6%9D%B1%E4%BA%AC%E9%86%A4%E6%B2%B9%E6%8B%89%E9%BA%BA%E8%98%AD%E4%B8%B8ranmaru%E5%BB%B6%E5%90%89%E8%A1%97%E5%BA%97-257221581146412/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.57 上午', '2021-06-08 11.25.57 上午', 2209, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 220, 1, '25518898', '鳥人拉麵-台灣總店TOTTO Ramen', '106', '台北市', '大安區復興南路一段107巷5弄9號', 25.04425850, 121.54433640, '拉麵', '拉麵 ▎店　　家：鳥人拉麵-台灣總店TOTTO Ramen\n▎鄰近地點：忠孝復興站\n▎用餐時間：2020/6/25 17:48\n▎排隊狀況：無\n▎拉麵名稱：特製-鳥人白湯拉麵 (豚)/NT.230\n▎配　　置：預設\n\n看了看Google Map的紀錄，才發現兩年沒吃鳥人了。\n以當年雞白湯還沒那麼盛行的時候，鳥人可以算是先驅。\n而且鳥人是從美國開過來的日本拉麵，算相當特別。\n\n特製-鳥人白湯拉麵 配置：雞肉丸、叉燒*2、海苔、筍片、蔥絲。\n\n雞白湯很濃郁，味道跟記憶中的一樣，略帶奶味。\n叉燒可選擇要豬叉燒或雞叉燒，而豬叉燒的配置是兩塊叉燒肉和少許碎肉，叉燒有經過炙燒把焦香逼出來。\n雞肉丸子還不錯，但不向麵屋千雲那樣有放入雞軟骨，口感上較一般。\n麵條鹼水味似乎沒有以前那麼重，但也沒有太多記憶點。\n\n滿分10分我會給8.5分，基本款還是一樣好吃。\n過了兩年美味依然沒有變，這點相當值得鼓勵，而且又少數有開到深夜的拉麵，可刷卡，晚下班時是相當不錯的選擇。\n\n-------------------------------------------------\n\n美國來的日本拉麵\n大家推薦的豬肉丼相當好吃，兩塊叉燒炙烤過後配上美乃滋，相當香\n\n鳥人白湯拉麵的湯頭非常非常濃郁，有點像麵屋一燈的沾麵湯，但口味又更濃郁，拿來做湯泡飯也很搭，加上青蔥有畫龍點睛的效果\n麵條的部分就有點像是配角，細麵剛上桌的時候還有一點生味，過一回兒才慢慢融入湯頭的味道，但也說不上是好吃的麵條\n\n熱愛重口味的人會喜歡這兩道料理，不過我吃到一半就開始喝冰水...太讚了。。。想到就餓??辣不夠辣，湯頭沒想像中的雞湯味店裡空間不大，座位間有點擁擠，湯頭濃郁，麵條偏硬。相較後來新開的分店們，臺灣總店顯得嬌小，但也保留了草創的感覺。白牆、木桌、金屬廚房，或許這就是所謂的紐約工業風。\n菜單第一項：特製鳥人白湯拉麵，此次在豚、雞叉燒中選擇雞肉。低溫烹調的雞肉帶有些許黑胡椒，直接吃鮮嫩可口，配合湯頭更顯香氣。兩顆雞肉丸子肉質紮實，似乎用薑壓制了肉類帶來的腥味，同時還有超小軟骨們帶來的驚喜脆口感。濃縮雞精華的濃郁湯頭，其鹹、香直接但不會過重，配合海苔的海味、青蔥絲的香氣、洋蔥的甘甜微嗆，各有一番風味。筍乾味道甘甜溫潤；或許是刻板印象，但與口味直白的雞白湯相比，感覺更適合豚或魚介系湯頭。店家提供的麵量頗多；用力甩乾的麵條始終保有麵芯，入口是帶著湯頭而非吸收湯頭，持續昭示自己的存在感。\n由於青蔥、洋蔥終究影響口氣，洗手間內附有漱口水，頗感貼心。店家可以信用卡支付，支援電子發票載具，聊供參考。', 4.2, 3044, 12785, '6ZWoR3tAW@gmail.com', '02 2778 9866', 2, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 04:00","星期六: 11:30 \\u2013 04:00","星期日: 11:30 \\u2013 22:00"]', 'https://www.facebook.com/tottoramen.tw/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.58 上午', '2021-06-08 11.25.58 上午', 3044, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 221, 1, '95976424', '合味拉麵', '106', '台北市', '大安區大安路一段51巷14號', 25.04303630, 121.54669890, '拉麵', '拉麵 看了重播的康熙來了去吃，\n這店家真的太過份了～～\n整個份量給的超多超滿的!\n害我飽到要分兩餐才吃的完\n而且這樣一份才95元!!\n\n立馬號招親友用新台幣教訓 ?第一次來吃，以百元有找的價錢來說，Cp值很高\n但是份量很大碗，有驚嚇到???\n味道不是走高價位精緻路線，\n比較偏向樸實的好味道，還會想讓人再訪\n\n在東區什麼都貴的地方，像這樣的價錢，可以份量這麼多，泡菜內用無限，又有位置可以內用，算真的很難得了\n\n很多便宜的路邊小吃，幾乎是沒什麼位置可以坐沒有廁所，相比之下這家真的很難得點了一份綜合的，超級大碗，很餓可以來吃，但味道見仁見智，環境有待改善超好吃又大份量???\n懷念的味道～～\n從國中吃到現在也十幾年了\n偶爾還是會去回味一下???很久沒有來東區，剛好上課路過，吃了一大碗綜合拉麵$95，現場吃，泡菜辣椒任意加，東西不錯吃，價格大多數反應該區房東漲的租金', 4.2, 637, 2675, 'kjdSLIRfS3@gmail.com', '02 2751 7981', 1, '["星期一: 10:30 \\u2013 21:00","星期二: 10:30 \\u2013 21:00","星期三: 10:30 \\u2013 21:00","星期四: 10:30 \\u2013 21:00","星期五: 10:30 \\u2013 21:00","星期六: 10:30 \\u2013 21:00","星期日: 10:30 \\u2013 21:00"]', 'https://peko721.pixnet.net/blog/post/40161160', '0.jpg', '[0.jpg]', '2021-06-08 11.25.59 上午', '2021-06-08 11.25.59 上午', 637, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 222, 1, '68707330', '橫濱家系????大和家', '106', '台北市', '大安區市民大道四段12號', 25.04464810, 121.54444840, '拉麵', '拉麵 ??My google reviews: 3.85 stars\n.\n號稱最強家系拉麵，\n在台北也是小有名氣的拉麵店，\n是日本直營的分店，口味比較不會飄，\n日本的拉麵種類族譜實在太複雜，\n我也懶的深入研究，\n日本三大拉麵是北海道札幌拉麵、福岡博多拉麵和福島喜多方拉麵，\n不過還是有很多其它地區的拉麵也很有名，\n發祥自關東地區橫濱市的家系拉麵就是其中之一\n.\n週日晚上來，剛好客滿，\n等一下就入座了，\n不搞機器點餐，\n點了一個店家推薦的\n?黃金鹽味拉麵，\n第2張圖，中碗330元，\n湯頭跟麵條有貼心的列出台灣人推薦的濃淡軟硬，\n湯頭鹹度選淡，湯頭油脂選少，\n喝起來還真不錯，\n湯頭內沒有明顯豬背脂漂浮，濃而不稠，\n阿續航力的部分，我大概可以喝2/3碗，\n然後根據Wiki查到的資料，\n家系拉麵應該要有加雞油，不過好像喝不出來，\n麵條是中粗直麵，\n說是把麵粉內的比例降至最低，口感還OK，\n只是台灣人推薦軟硬度是普通，\n對我來講有點軟，\n搭配的青菜是波菜，比較特別，\n然後一堆海苔也是特色，\n叉燒的部分，是薄片的，\n炙燒香氣明顯，但口感跟味道很普通\n.\n店家推薦的\n?黃金醬油拉麵，\n第3張圖，中碗330元，\n家系拉麵主打的湯頭應該是醬油，\n不過我是喝不大出來跟鹽味的差別拉，\n除了湯比較黑一點之外，\n另外這家的調味佐料比較多一點，\n有黑胡椒、豆瓣醬、醋、白芝麻、\n大蒜泥、辣椒醬、薑絲、辣油，\n然後還發明了什麼大和家流美味吃法\n(最後一張圖)，\n搞一些怎樣吃的花招，還可以加白飯吃\n.\n?廣島產炸牡蠣，\n第4張圖，150元，4個，\n還行，\n?大和家特製煎餃，\n第5張圖，5個100元，\n一般\n.\n?總結，\n今天?2個人吃870元，\n拉麵是日本國民美食，\n現在也快變成台灣國民美食了\n(雖然價位好像特別貴)，\n之前新聞也有人做了一張捷運拉麵地圖，\n其實各大類湯頭的拉麵台灣各地都有很多家很好吃的，難分軒輊，\n少數幾家比較雷的看一下工程師Ted吃美食來排除就可以了，\n我個人也不算拉麵控，目前有寫過的就：\n?Nagi(3.8)、蔦(3.4)、鷹流東京醬油拉麵(1.9)、山嵐(4.25)、\n拉麵吧(3.1)、你回來啦(4.15)、雞二(4.05)、麵屋武藏(3.15)、\n重熙老麵(3.35)、一蘭(4)、柑橘Shinn-Soba(4.2)、\n鳥人(4)、大和家(3.85)\n(分數都有可能再微調)\n.\n更多精準中立的美食評論在\nIG: tedchen5152\n#工程師Ted吃美食台北▎店家：橫濱家系????大和家\n▎鄰近地點：忠孝復興站\n▎用餐時間：2020/7/11 11:40\n▎排隊狀況：醬油/鹽味拉麵 買一送一活動，排隊40分鐘\n▎拉麵名稱／價格：鹽味拉麵+叉燒 /NT.220+50\n▎配置：麵硬、鹹度普通、油脂普通\n\n遇到買一送一的活動，既使中午艷陽高照，還是拉著朋友一起來排隊吃傳說中的"家系最強"。\n\n鹽味拉麵配置：叉燒*1、鵪鶉蛋、海苔、菠菜。\n\n家系拉麵的標準配置，豚骨湯底加上菠菜與鵪鶉蛋，最好加點一碗白飯做湯泡飯。\n曾經有一陣子我很愛吃大和家，但這次不知道是氣溫的關係，還是人潮太多難以控制品質，總之跟我印象中的味道不太一樣。\n加點叉燒炙燒過的焦味有些過頭，湯頭表面依然漂浮著油脂把味道鎖住，但似乎多了點腥味。\n麵條表現倒是跟記憶中的差不多。\n\n滿分10分我會給8分。\n不管怎麼說，這還是一碗很不錯的拉麵。\n只是對比記憶中和其他麵友的回應，品質的確有下降\n\n-----------------------\n2017.12.29\n落座在市民大道旁，號稱家系最強的大和家不會讓你失望\n湯頭鹹度及麵條都可以調整，還有推薦的泡飯吃法\n一走進店裡立刻聞到炙燒後的香氣，今天點的是限量的爆肉拉麵，鋪上滿滿的叉燒，滿到看不見拉麵，很可愛的中間擺了鵪鶉蛋\n叉燒偏柴但咬下去是充滿油脂，豚骨醬油湯底上飄著一層漂亮金黃色的油，鹹度非常棒，麵條加水率低，配上滿滿的叉燒真的是大大滿足\n相當道地的日式拉麵，光坐在座位上等待的同時，就可以感覺到像是身在日本，有機會再來嘗試泡飯吃法1.價格太貴，價格較低的拉麵根本沒料、平均一片海苔要10$\n2.服務：點餐時因為拆座店員把點好的餐點全部拿出來讓我們認半天哪張是自己的（頗無言的畢竟現在多用點餐機\n3.用餐氣氛不佳，後桌的小孩子一直在樓梯上和二樓跑來跑去也沒人制止\n4.（純粹個人喜好）二樓兩排座位對坐的形式很像某連鎖拉麵店，整體體驗也跟連鎖店差不多\n5.我點：醬油拉麵240$ 湯頭鹹、油多、麵硬加白飯20$ 標準日本拉麵配置，btw麵條軟硬度的「超級軟」選項我看了很無言\n湯頭只有醬油的死鹹，喝不到層次感加了蒜頭以後有多一點風味（友人說像花X嵐的湯頭），油脂也沒有特別的風味\n同一碗麵的麵條粗細、軟硬參差不齊吸飽了醬油湯汁感覺偏鹹\n傳統的圓形叉燒只有一片，很薄沒什麼口感和油脂，鵪鶉蛋的部分只是一顆很普通的全熟水煮蛋\n真要說的話白飯加進湯裡面還不錯吃，\n本來滿心期待的，卻大失所望：店家沒有顧及整體用餐體驗，食物與價格不符，用料看不出誠意，感覺是走連鎖店的模式台北想要吃的拉麵非常多，但也無法一次全部吃完，就需要慢慢努力才可以，挑選必經店家橫濱家系大和家拉麵來享用，直接點上631醬油拉麵與大和家煎餃來當作套餐組合品嚐，選擇麵條硬度硬，鹹度與油脂普通的搭配，拉麵上桌就能看到漂亮的濃郁湯頭，據稱以特殊製法抽取出美味而無腥味的豚骨湯頭，另外加入獨家傳統醬油所製作的醬油拉麵，631更是之前就已經調查過相當值得一點的黃金組合，有海苔6片、叉燒3片、半熟蛋1顆所構成，吸入一口麵條相當有嚼勁卻又好入口，個人喜歡海苔加上叉燒直接吃下去的感受，鹹香味十足也相當帶感，之後上桌的煎餃雖然不大顆，但是每口都能吃到鮮甜好滋味，算是值得品嚐的小品，但還是覺得沒有點到米飯有點可惜，下次應該再來試試看！\n橫濱家系大和家拉麵的鹽味拉麵是個人也想品嚐的項目，不過這次重點在於加點叉燒蓋飯，才能把湯頭跟整體做個最佳組合，選擇631鹽味拉麵，因為真的很喜歡海苔6片、叉燒3片、半熟蛋1顆的「完美黃金比例」，配置上同樣是麵條硬度硬，而鹹度與油脂普通的組合，相比之下極品鹽味拉麵的確是與麵條的最佳黃金組合，喝上一口湯頭就有濃郁鹹香，但又不至於太過死鹹，喝得出來油脂與整體香醇度，但這次另外一個主角是叉燒蓋飯，因為拉麵本身的是普通叉燒，而叉燒蓋飯上的則是炙燒過的叉燒，香氣四溢加上美乃滋的組合，真的讓人一口接一口，上面肉香結合米飯吃半碗，接著淋上拉麵濃湯，拌入豆瓣醬與白芝麻後更加深整體味覺層次，真的覺得有碗飯是拉麵最佳好朋友！濃郁，麵Q，拉麵首推！好吃?', 4.2, 3487, 14645, '0S2oJANMT@gmail.com', '02 2771 8810', 2, '["星期一: 11:30 \\u2013 22:30","星期二: 11:30 \\u2013 22:30","星期三: 11:30 \\u2013 22:30","星期四: 11:30 \\u2013 22:30","星期五: 11:30 \\u2013 22:30","星期六: 11:30 \\u2013 22:30","星期日: 11:30 \\u2013 22:30"]', 'http://www.iekei-yamatoya.tw/', '0.jpg', '[0.jpg]', '2021-06-08 11.25.59 上午', '2021-06-08 11.25.59 上午', 3487, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 223, 1, '28397612', '三本手工拉麵', '104', '台北市', '中山區建國北路二段26號', 25.05309100, 121.53643920, '拉麵', '拉麵 （2020.10.24）冬天限定的暖薑麻油猴頭菇拉麵又來了，加了一點店家獨門的火焰辣椒炒醬，一上桌香氣撲鼻立馬開動，哪有時間完整拍照讓你們欣賞??\n\n脫去亂七八糟的人工調味外衣，才能品嚐到自然清甜的湯頭味道??服務人員態度都很好親切！但餐點不太對味，因此剩了蠻多的，歸類在下次不會再回訪的店?\n點了味增跟咖哩拉麵，都覺得麵體還不錯吃正常，味噌湯底有點甜，是比較台式的，咖哩的湯底很濃郁沒有不好吃但不太耐吃。拉麵都有搭配炸百葉(？)豆腐倒是蠻特別的?。小菜是搭配套餐159吃了炸豆腐（忘了正確餐點名）跟黃金泡菜，泡菜給了很多，不過味道有像加了芥末(？)，味道是濃郁不差，但非清爽口味的。價錢跟整體拉麵味道表現有點不符合，cp值比較低就是了，很可惜。它是一家蛋奶素、五辛素的店，點餐的時候會跟您確認蛋奶、蔥可不可以吃，它都是可以依消費者調整的。\n?環境\n環境佈置的蠻美的，含有一點日式的風味，從一進去便會聽到日語的歡迎光臨，服務員帶位之後就會幫你倒上好喝的麥茶。店內空間滿大的，初估至少快三十個座位，環境也很衛生乾淨，廁所的部分直走到底有兩間，也很乾淨舒適，服務生也會適時的、很貼心的幫你加水，服務很到位呢！\n\n?活動\n店內有活動哦！只要去粉專打卡加按讚就可以得到一個小菜哦！我們那天拿到的是黃金泡菜，很好吃耶！有點台韓想混的感覺，喜歡韓式泡菜的一定也要一試。\n\n?餐點\n??完熟番茄拉麵  $270\n非常純、濃郁的番茄湯，淺嚐一口便可以感受到做這一道湯頭的用心，濃郁香醇，再搭配浸滿湯汁的麵條，好吃好吃，真不愧是人氣商品。\n??日式味噌拉麵  $230\n這一道湯頭也很好喝，沒有像番茄那樣的濃郁，不過卻帶有一種回甘甜甜的滋味，它反而是被朋友們獲選的第一名湯頭，不知不覺就把湯全部給它喝光了。\n??火焰拉麵  $230\n看它畫了兩條辣椒就知道有如此的可怕，火焰火焰真的是那種麻辣等級，它有一種特殊的香氣，一入口還不會有很強烈的辣感，等個幾秒後勁很強呀！而且會讓你辣到喉嚨的感覺?哇?！超級適合非常愛吃辣的朋友來嘗試，不太吃辣的我就只敢嘗試一小口。\n\n以上三者的餐點，裡面有的配料都相同，有一個很特別的小橘球，我一開始吃到以為是什麼枇杷之類的水果，一問之下才知道原來是紅蘿蔔球，非常特別的食材，還有裡面最好吃也最驚艷的豆腐（應該就是揚?豆腐），外脆內紮實，不會像一般豆腐一樣軟的、沒有口感，它吃起來很像百頁豆腐的口感，我超愛，也覺得是到那家店一定要吃到的經典小菜。\n\n??田樂燒豆腐  $105\n燒烤板的豆腐，它的醬汁蠻特別的，有一種酸甜的滋味，還有加上海苔絲與碧玉筍來陪襯，不過整體吃起來比較一般，沒有讓我驚艷到比較可惜。\n\n??百香果青木瓜  $85\n好好吃哦，我的天呀！這青木瓜吃起來完全沒有澀味、被濃濃香甜的百香果汁包覆，非常的涮嘴，會讓你忍不住的一口接一口，喜歡青木瓜的朋友一定要給它點一下。素的拉麵還真的是第一次吃\n料很多，有菜有菇有豆製品，吃起來感覺很健康\n可惜的是價格偏高番茄拉麵的麵很彈不會爛爛的。蔬菜天婦羅很好吃。玉子燒是甜的，跟日本一樣。', 4.3, 800, 3440, 'w95fXMHR@gmail.com', '02 2516 6200', 2, '["星期一: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期六: 11:30 \\u2013 14:30, 17:00 \\u2013 21:30","星期日: 11:30 \\u2013 21:30"]', 'https://www.facebook.com/SanbenRamen', '0.jpg', '[0.jpg]', '2021-06-08 11.26.00 上午', '2021-06-08 11.26.00 上午', 800, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 224, 1, '90122456', '豚人拉麵復興店 京都一?寺豚人????', '10697', '台北市', '大安區大安路一段16巷6號', 25.04431740, 121.54592720, '拉麵', '拉麵 點完過了半小時都沒來，完全忘了！！Taiwanese ramen. 哩阿捏美賽喔咖嘎由咧20201211『清爽醬油豚骨+溏心蛋半顆』\n\n溏心蛋的口感佳，熟度與稠度恰好\n筍乾的大小不一，清脆中帶有一絲絲的甜\n蔥花和豆芽負責調和湯頭的厚重感\n然而豚骨基底的湯頭相當易入口\n有著豚骨的重口味、醬油的鹹與甜\n沒有過於油膩的負擔感\n喝盡最後一滴湯汁並非難事\n中卷麵的Q度十足，咀嚼起來非常有意思\n其沾附湯汁的能力亦在水準之上\n兩片具有一定分量的叉燒甚為美味\n肉質緊實但不柴，調味適中而不會過鹹或無味\n整體十分令人滿意與滿足\n餐點品質、衛生環境、人員態度皆優於本店\n大大地沖淡了原先對於本店的不佳感受拉麵湯頭濃郁不死鹹 份量滿足大部分人的需求 值得再訪?口味大概給四星，店主店員熱誠還是把星補滿好了。\n店內的確較悶熱，初秋的天氣吃完一樣滿頭大汗。\n免費取用的辣豆芽算是大眾能接受的辣度，本身食用感想是，僅有尾韻時有些許辣度，但豆芽不至過爛。單純當作小菜賣，都是可以的。\n現炸炸雞應該算嫩。或許原意是配著吃吧，調味不是過重。\n徹濃對我來說有點清淡，下回應該加點拉麵醬，或是辣味醬再試試看味道。\n雖說一般大家好像會傾向天龍或是中捲。\n個人因素，還是細麵，湯留著加入白飯吃。\n而這細麵，不用特別交代，硬度就還不錯。\n非特別需求硬麵者不用多告知店主。\n可惜的是加麵時所加的麵，切水似乎稍微…\n麵濕了些。\n湯頭不是非常燙，剛好可以直接入口的溫度，麵倒是挺燙的，搭配起來正好。\n有機會再去試試看爆盛。\n對了，小碗白飯的份量就不錯了，店家誠意十足。', 4.7, 443, 2082, '68J3yi@gmail.com', '0979 093 749', 4, '["星期一: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 休息","星期三: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期五: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期日: 12:00 \\u2013 14:00, 17:00 \\u2013 20:00"]', 'https://instagram.com/butanchu_tw_2?igshid=1n43763mspk11', '0.jpg', '[0.jpg]', '2021-06-08 11.26.01 上午', '2021-06-08 11.26.01 上午', 443, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 227, 1, '45346131', '農村阿嬤（鮮魚、放山雞）家常菜', '232', '新北市', '坪林區12號', 24.91608200, 121.68159260, '小吃', '小吃 服務態度好\n老板很用心\n價格 中位\n不過  如果在外面吃的話  很怕上面的燕子會大便當心！相對於價格，菜與麵線的量是預期的兩倍，所以性價比就是北宜路上同類餐廳的兩倍！\n去骨白帶魚卷就是原味，無特殊之處。\n一如多數北宜路野菜餐廳，喝到飽的奉茶比你我家的茶都好喝，別點湯了。純樸的農家菜，雖不豪華但是好吃，老闆很大方的提供桌面讓我們可以自己泡茶，但是非假日不影響營業，當然老闆也有泡茶招待，親切的阿嬤也一直問需要加白飯嗎？怕我們吃不夠，其實吃的非常飽，只因爲一道苦茶油雞，大家捨不得浪費雞油所以又加白飯淋苦茶雞油拌飯，招牌的酥炸袋魚捲很值得嚐鮮，真的不豪華的店面但有豪爽的經營者，溫暖每位過客第一次跟同學參加露營隔天中餐,就到農村阿嬤家常菜餐廳吃飯,被店家外觀吸引到 ; 食用間,喝到店裡免費提供冠軍茶,超潤口的；另外,第一次看筷子的衣-筷衣,超特別,跟大餐廳同步 ; 享用美食期間,也看到老闆的獨家料理：炸白帶魚、五香鹹豬肉、茭白筍、山豬肉...每樣菜吃到最後都不剩 ; 在這間餐廳用餐更是愜意,空氣聞到茶葉香,再搭配冠軍茶及佳餚美食,超級棒的用餐環境,很值得推薦家人及朋友下次再來享用美食。推薦帶魚卷、白斬雞（記得1天前預約！）', 4.8, 49, 235, 'uYhIYQPiWj@gmail.com', '0932 918 229', 4, '["星期一: 10:30 \\u2013 19:00","星期二: 10:30 \\u2013 19:00","星期三: 10:30 \\u2013 19:00","星期四: 10:30 \\u2013 19:00","星期五: 10:30 \\u2013 19:00","星期六: 10:30 \\u2013 19:00","星期日: 10:30 \\u2013 19:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.36.10 下午', '2021-06-16 07.36.10 下午', 49, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 228, 1, '32078893', '金瓜寮阿卿的店', '232', '新北市', '坪林區11號', 24.91620580, 121.68146300, '小吃', '小吃 很親切。就如回老家吃飯的溫暖。自在心身鮮美食材。安啦！i白斬雞很好吃，醃蘿蔔也好吃，阿卿很親切，很不錯的店樸實、美味的料理。老闆娘親切、和氣的態度讓菜色更加美味。 價位也很合理，有經過附近可來用餐。空氣好茶好喝老板娘熱情健談青菜豆腐好吃來此健行可以試試茶油雞必車吃', 4.2, 178, 748, '1N0zmpfm@gmail.com', '02 2665 6396', 2, '["星期一: 09:00 \\u2013 21:00","星期二: 09:00 \\u2013 21:00","星期三: 09:00 \\u2013 21:00","星期四: 09:00 \\u2013 21:00","星期五: 09:00 \\u2013 21:00","星期六: 09:00 \\u2013 21:00","星期日: 09:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.36.12 下午', '2021-06-16 07.36.12 下午', 178, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 229, 1, '14483301', '木森林香草民宿', '84445', '高雄市', '六龜區舊潭211號', 23.01268900, 120.66604250, '小吃', '小吃 🐶寵物友善\n高麗菜真的很好吃，當歸湯也不錯，麻婆豆腐還好。份量很十足，兩個人吃實在太撐了！真是好的去處，素食味道好好吃，山不在高有仙則靈，我的是這種感覺吃了10多年，依然是那麼美味，清蒸高麗菜必點，只營業假日!!餐點非常有特色又新鮮超推薦的素食餐廳，讓愛吃肉的我喜歡上素食', 4.2, 85, 357, 'x7nSgdqH@gmail.com', '07 689 5866', 4, '', 'http://vanillawood.looker.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.36.26 下午', '2021-06-16 07.36.26 下午', 85, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 230, 1, '16501608', '意魷味境小吃店', '114', '台北市', '內湖區內湖路一段323巷13號', 25.08366830, 121.56808000, '小吃', '小吃 雖然有點貴，但是魷魚羹很多魷魚，口味偏清淡可以自己加辣椒！推推中規中矩的魷魚羹麵，是好吃的，該有的味道都有，還不錯。乾意麵：中等，滷肉香氣十足，口味鹹甜，很適合配飯麵，口味偏向古早味，脂肪部分偏多，吃一兩份還好，如果吃太多容易膩，滷肉給的量不少，CP值很高，就小吃店來說挺不錯的，整體來說還不錯（麵與其說是意麵，不如說是白麵）\n乾冬粉：中上，大致上跟乾意麵相同，只是冬粉更入味，味道更好吃一點，但相對來說口感沒意麵那麼有嚼勁，整體來說還不錯\n花枝羹：普通，很普通的花枝羹，口味偏淡，建議加點調味料送餐的一位姐姐很漂亮又很親切 是我去這家店點動力環境乾淨，東西好吃', 3.7, 128, 474, 't3Dzj5hZ@gmail.com', '02 2627 4818', 1, '["星期一: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期二: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期三: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期四: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期五: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期六: 11:00 \\u2013 14:00, 17:30 \\u2013 20:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.36.56 下午', '2021-06-16 07.36.56 下午', 128, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 231, 1, '71847560', '來旺小吃店', '111', '台北市', '士林區至善路二段270號', 25.10050610, 121.55108410, '小吃', '小吃 我只給1顆星星的原因是 不衛生 我到別家自助餐或是麵店 他們都有戴口罩 我剛剛去 吃午飯 看見有一個女的 戴口罩 其他一男兩女 都沒有戴口罩 其中有一個女的沒戴口罩的一直打噴嚏 看得我 吃到一半 心裡想著 在我還沒來之前不知道 他有沒有打噴嚏 如果有的話 那我的菜 不是都有他的噴嚏嗎 想起來就噁心 以後我不敢去哪一家了 就在故宮博物院 對面超商 全家便利商店旁邊 下次不敢再去了 太恐怖了 我看那個女的在裝便當 打了兩次噴嚏 都沒有戴口罩 以後不敢去那邊吃了算還可以啦，因為沒有其他選擇了東西偏油偏鹹但個人覺得挺好吃的，但是飯有點煮太軟影響口感，菜是事先煮好的因此有些菜涼調了很有味道的一家台灣在地小吃  不過人真的很多  假日更多人 是間生意爆好的一家店  要去吃盡量在11點多的時候去吃  推薦雞腿便當好吃 讚', 3.4, 34, 116, 'XguAeD9m@gmail.com', '02 2841 1939', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.36.57 下午', '2021-06-16 07.36.57 下午', 34, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 232, 1, '85212795', '滷大媽小吃店', '104', '台北市', '台北市 中山區北安路630巷25弄1號1樓', 25.08281100, 121.55047000, '小吃', '小吃 ', 0, 0, 0, 'MqWoNVh@gmail.com', '02 8509 7661', 4, '', null, '', '', '2021-06-16 07.36.58 下午', '2021-06-16 07.36.58 下午', 0, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 233, 1, '36642826', '姐妹小吃', '10491', '台北市', '中山區北安路578巷8弄9號', 25.08048640, 121.54876180, '小吃', '小吃 雖然費用稍微高了些，但值得去吃看看，板娘她們都非常客氣，待人很好太貴，而且快炒店那麼囂張第一次看到口味不差，就是家常菜\n炒雞胗腥味有點重\n柳葉魚炸得剛剛好\n但是兩個老闆娘臉真的很臭很合口味，好吃\n不小心就吃了10幾年的熱炒店\n最愛炒牛肉👍🏻\n我覺得以台北大坪數店面來說\n這價位真的算是滿合理的了店家態度不敢再光顧了，\n看心情做生意。', 3.5, 15, 53, 'e9Z0n0ZAY@gmail.com', '02 2532 7866', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.36.59 下午', '2021-06-16 07.36.59 下午', 15, 0);
