INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 235, 1, '33724700', '大樹下美食(東吳大學)', '111', '台北市', '士林區臨溪路64-2號', 25.09527070, 121.54467450, '小吃', '小吃 過去還是大學生時常常去吃蛋炒飯，加飯加麵不用錢就是佛心，到現在還是如此，味道也沒有變～東吳學生不能錯過，特別是炒飯🤣沒良心 蛤蜊湯幾乎每顆蛤蜊都有沙 湯也一堆沙物美價廉，老板有佛心，加飯不加價！最好吃的炒飯\n便宜好吃又可以加飯\n炒飯粒粒分明\n太好吃了', 4.2, 97, 407, '1EBGQqb9sy@gmail.com', '0958 913 667', 4, '["星期一: 10:00 \\u2013 21:30","星期二: 10:00 \\u2013 21:30","星期三: 10:00 \\u2013 21:30","星期四: 10:00 \\u2013 21:30","星期五: 10:00 \\u2013 21:30","星期六: 10:00 \\u2013 21:30","星期日: 10:00 \\u2013 21:30"]', 'https://www.facebook.com/pages/%E6%9D%B1%E5%90%B3%E5%A4%A7%E5%AD%B8%E5%A4%A7%E6%A8%B9%E4%B8%8B/207982899227655', '0.jpg', '[0.jpg]', '2021-06-16 07.37.00 下午', '2021-06-16 07.37.00 下午', 97, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 236, 1, '37721430', '阿娥傳統小吃', '114', '台北市', '內湖區瑞光路513巷22弄5號', 25.07999100, 121.56912400, '小吃', '小吃 202102\n乾意麵 50元\n餛飩湯 40元\n內用也要排隊\n發現湯裡的綠菜夾著1小片枯黃的榕樹葉..銅板美食，料多實在\n不要拿館子來比較，只能說是內科優質商家！用foodpanda下訂單，過了半小時後才被取消訂單，若店家無法及時供應就別開外送。太油 或 沒味道  不推！憑良心說東西不錯吃，但是分量也太少了吧？？？叫了炒麵+油飯，炒麵兩口就吃完，真的是兩口，油飯多吃了三口吧，這樣要50元真的傻眼。', 3.1, 9, 28, 'mCDrz9LbIk@gmail.com', '0919 223 431', 4, '["星期一: 08:00 \\u2013 20:00","星期二: 08:00 \\u2013 20:00","星期三: 08:00 \\u2013 20:00","星期四: 08:00 \\u2013 20:00","星期五: 08:00 \\u2013 20:00","星期六: 休息","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.02 下午', '2021-06-16 07.37.02 下午', 9, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 237, 1, '63225162', '黑美林', '114', '台北市', '內湖區內湖路一段231號', 25.08268000, 121.56499700, '小吃', '小吃 好吃！味道很好吃又新鮮。楜椒粉太鹹了，沾的時候要小心。好吃，不過人極多，先定位比較安全鴨肉柔嫩多汁，讚啦！菜色無雷，服務態度佳', 4.3, 2478, 10655, 'WX1Vla8q93@gmail.com', '02 2627 2079', 2, '["星期一: 17:00 \\u2013 00:30","星期二: 17:00 \\u2013 00:30","星期三: 17:00 \\u2013 00:30","星期四: 17:00 \\u2013 00:30","星期五: 17:00 \\u2013 00:30","星期六: 17:00 \\u2013 00:30","星期日: 17:00 \\u2013 00:30"]', 'https://www.facebook.com/heimeilinloveyou/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.03 下午', '2021-06-16 07.37.03 下午', 2478, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 238, 1, '03288473', '壹佰號香菇燜雞腿飯-(必吃美食、平價小吃、人氣銅板料理)便當外送外帶', '114', '台北市', '內湖區內湖路一段285號攤位號942樓', 25.08241730, 121.56649450, '小吃', '小吃 問老闆能不能不飯，老闆說可以還多幫我加了一顆蔥蛋～以外食來說菜都算清淡不會太油，還不錯。好吃不油膩，還可以不要白飯多一個菜～是減醣飲食的好朋友！紅咖哩雞腿🍗燉得有軟爛，搭配乾意麵好好吃，醬料特別口味不錯，老闆和老闆娘也都很親切。點餐有彈性，白飯不要可以有四樣菜，適合不想吃主食的人。香港雞好吃味道又清淡可以給小寶寶吃，媽媽懶惰時的好幫手，而且份量很大，不怕吃不飽。', 4.1, 20, 82, 'EyVAUmAzu@gmail.com', '0958 116 849', 4, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.05 下午', '2021-06-16 07.37.05 下午', 20, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 239, 1, '99891053', '河內越南美食', '114', '台北市', '內湖區內湖路一段285號西湖市場二樓攤位號90', 25.08256660, 121.56651420, '小吃', '小吃 第一次來吃，點了牛肉河粉\n雖然我不是越南美食專家，但記憶中，這是粄條不是河粉吧🤔\n牛肉很明顯就是火鍋肉片😑\n一碗NT100價格適中，份量普通，食量大的人可能吃不飽，泰式酸辣河粉的海鮮蠻多的，湯頭口味不錯，蠻好吃的味道還行..像一般的台式口味店...湯頭清淡 河粉量不多 肉的份量還算足夠牛肉河粉肉片份量不多、河粉結成一團軟軟爛爛的，不符合價格。', 3.3, 30, 99, 'VUdi3HG2@gmail.com', '0933 880 379', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.06 下午', '2021-06-16 07.37.06 下午', 30, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 240, 1, '50785367', '府城晶華', '111', '台北市', '士林區至善路二段221號', 25.10120580, 121.54773710, '小吃', '小吃 上菜速度奇慢無比\n牛肉麵的麵條斷的亂七八糟，讓我非常懷疑是否是回收來的\n牛肉肉質只有普通\n牛肉麵的湯，還算可以\n\n我2/8，16：00左右，和朋友二人去吃，客人已經剩不多了，東西貴、服務人員素質不夠好（還想收服務費？）、上菜慢、東西不好吃\n\n我已經很心平氣和的轉身離開了，不想在故宮這地方，讓自己很沒水準的感覺\n\n總之，我很少會吃個東西吃到如此火大，最後，等不到我朋友的餐點來，退了一份就走了\n\n強烈建議：不要去這間吃\n\n我的言論，絕對經得起店家的法律追溯別說坑殺觀光客, 也別再扯陸客什麼的.\n光這種服務品質就對不起價格, 更扛不起招牌.\n\n真要在這吃牛肉麵......\n還是要選美國牛, 口味會比較好.1.才剛下樓 服務生就急匆匆幫我們安排座位坐下 好像趕鴨子上架一樣 都沒有給我們時間思考是否要在此地就餐\n2.食物的價格比外面店面貴一兩倍 而份量還沒有外面的多 味道也不是很好\n3.做餐的速度太慢 所有東西吃完了還有菜沒上 服務態度一般還要收取服務費\n個人認為這家店是在賺取觀光客的錢 食物沒什麼含量和質量 不值得大家去品嚐 吃過一次怕了 台式料理沒有這麼貴又不值的來嘗試招牌的晶華牛肉麵，價格偏高，口味不錯，但麵條沒有咬勁偏軟。肉燥飯超級好吃 有飯店的水準\n其他還好', 3.2, 274, 877, 'pD2CBVN9@gmail.com', '02 2882 9393', 4, '', 'http://www.silkspalace.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.07 下午', '2021-06-16 07.37.07 下午', 274, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 241, 1, '11778811', '明水397食堂', '10465', '台北市', '中山區北安路458巷47弄51號', 25.07865160, 121.54652270, '小吃', '小吃 今天買了一堆菜好好吃，可是其中有一盤鹹蜆仔，是酸的，而且還有發酵的氣泡感了，不知道明天能不能再補一盤🥺😭😭生腸很好吃，大腸頭也不錯。要吃什麼盡可能先打電話訂，不然太晚去要什麼沒什麼，出餐速度還算快，口味適中，值得再來。衛生環境真的太誇張了！蟑螂多到前面那條路都是小強在亂竄，餐廳後面的巷子也是一堆蟑螂，這叫附近住戶居民如何安心，衛生好好管理一下吧！用餐環境不錯、服務人員很親切\n記錯開店時間，還是讓我們提早進餐廳等待😂\n可以在這裡，吃到很不錯品質的魚，香煎馬頭魚必點！\n綠竹筍剛好季節限定～非常鮮甜好吃\n擺盤也非常用心～一點都不輸餐廳喲。糙米雞湯非常的古早味，店員相當輕切！首推滷肉飯....', 4.2, 370, 1554, 'y6AXooGjVO@gmail.com', '02 2532 5687', 2, '["星期一: 休息","星期二: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期三: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期四: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期五: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期日: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.09 下午', '2021-06-16 07.37.09 下午', 370, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 242, 1, '17789149', '金甌越南美食Quan An Viet Nam', '10491', '台北市', '中山區北安路621巷46號', 25.08281980, 121.54660200, '小吃', '小吃 風一吹，春捲全部飛走了上禮拜四來買外帶，老闆娘沒帶口罩其他人有帶，而且有聽到廚房傳來咳嗽聲與打噴嚏聲，我現在每天都提心吊膽的。覺得內用環境沒有到很好>越南春捲搭配醬汁好吃，炒河粉普通，酸辣海鮮湯也好吃。可用街口支付。乾拌米線不好吃，米線的質感黏黏稠稠的，醬汁沒有一般越南餐廳的酸甜味，真的很怪。', 3.9, 209, 815, 'A5VapUAu@gmail.com', '02 8509 5575', 1, '["星期一: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:30","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', 'https://m.facebook.com/%E9%87%91%E7%94%8C%E8%B6%8A%E5%8D%97%E7%BE%8E%E9%A3%9F-Quan-an-Viet-Nam-123742891831023/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.10 下午', '2021-06-16 07.37.10 下午', 209, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 243, 1, '35961292', '大直小館', '10491', '台北市', '中山區北安路535號之 1 號', 25.08018150, 121.54724740, '小吃', '小吃 和家人一同前來用餐。\n就在捷運大直站1號出口旁，步行一分鐘可達。\n秘製紙包雞（380元）是招牌菜餚之一，一份六個。上桌時，服務人員很熱情的示範食用方式：用筷子將紙由中間撕成兩半，即可夾出雞肉食用。每個紙包裡有兩塊去骨雞肉，使用的部位應該是雞腿？醃製入味鹹香，口感軟中帶有嚼勁，但醬汁有些油膩。\n乾燒花雕雞煲（350元）也是一道招牌菜。雞腿塊在鐵鍋裡滋滋作響，醬香和酒香撲鼻而來。去骨的雞腿肉，方便入口，鹹香下飯，總的來說推薦點用。\n廣東臘味炒飯（130元）份量頗大，臘腸和香腸切成大小適中而均勻的丁狀，米飯粒粒分明，可惜稍涼了後會覺得油膩。\n鹹蛋炒絲瓜（200元）有些微妙，雖然感覺得出讓絲瓜呈現較為脆硬而非平時常見的軟腴口感，但這樣容易出水的食材，還是會沖淡鹹蛋的滋味。\n咖哩焗豆腐（260元）比想像中清淡一些，咖哩不辣，薑黃下得多，豆腐可以再燒透點。\n另有搭配好的多人合菜套餐可以選擇。\n服務人員態度熱情，惜店裡油煙味稍大，桌與桌之間的距離也近了些。這一家應該算是大直的老店了，有非常道地的港式料理。個人非常喜歡這兒的港式蘿蔔糕，非常值得一試。老店了，口味還不錯。圖片是他們招牌的紙包雞與炸肥腸。第一次來這邊吃年夜飯，是一間以粵菜為主的餐廳\n離捷運站大直站很近，出站後步行5分鐘內可到算是便利\n因為是去吃年菜，所以基本上每桌都差不多\n蝦子新鮮，吃起來肉質彈牙\n北菇豆苗，香菇吃起來飽滿鮮嫩，但豆苗沒什麼味道\n京都排骨味道濃郁，但軟骨與硬骨多，炸完後肉質較為乾澀\n乾燒伊麵，味道算普普通通，但不錯吃\n雞湯味道清淡，不油也不膩，味道適中\n紙包雞，裡面包的是已經調好味道的雞腿肉塊，味道夠，但實在是太油膩了，\n魚，味道還行，中規中矩\n鵝掌，味道尚可，不太習慣吃鵝掌，骨頭與肉已經處理到分離狀態\n\n綜合以上結論，年菜價位稍偏高，年菜菜色都比較固定，變化不大，但有機會的話，會在嘗試去吃像是炒麵、燴飯、炒河粉之類的CP值頗高的一間餐館\n主打港式料理\n裝潢普通，但每盤份量大\n菜色不能算精緻，但是好吃', 3.9, 384, 1498, 'rr6nSa@gmail.com', '02 2533 1717', 2, '["星期一: 休息","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/dazhifood/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.11 下午', '2021-06-16 07.37.11 下午', 384, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 244, 1, '66794962', '來佳海南雞飯', '114', '台北市', '內湖區內湖路一段285巷8弄3號', 25.08266110, 121.56601600, '小吃', '小吃 預約外送說好12:40分送達，但卻提前1小時送到，等到真正要用餐的時間食飯菜跟湯都涼了，希望老闆能改進，可以有誤差，但可以不用提前那麼早送達。100塊3個菜的海南雞飯，價錢非常實惠，肉的部分是老闆隨機給，有時候是胸有時候有翅，外送要提早訂不然會訂不到，需要辣椒跟薑需事先跟老闆說。海南雞本身跟配菜還可以，飯略油，缺乏香氣。\n有附甜/鹹湯但自己發現才知道。櫃檯說沒有....上班族吃粗飽還可以。海南雞份量不少，這次點肉骨茶，味道也不錯，但覺得海南雞還是最划算，飯的口味獨特，餐廳附湯有分鹹甜兩種，滿滿雞熬成的湯，這次甜湯是綠豆湯，整體CP值還不錯。肉骨茶麵配三樣菜100元，麵是這樣裝的……吃起來味道是還可以吧，不過吃完後就拉肚子了，下次不會再點。', 3.7, 372, 1376, 'oRJdbPWRc@gmail.com', '02 2797 5417', 1, '["星期一: 10:30 \\u2013 20:15","星期二: 10:30 \\u2013 20:15","星期三: 10:30 \\u2013 20:15","星期四: 10:30 \\u2013 20:15","星期五: 10:30 \\u2013 20:15","星期六: 休息","星期日: 10:30 \\u2013 15:30, 16:30 \\u2013 18:50"]', 'https://www.facebook.com/ShengTaoSha', '0.jpg', '[0.jpg]', '2021-06-16 07.37.12 下午', '2021-06-16 07.37.12 下午', 372, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 245, 1, '00786315', '奇津小吃水餃', '114', '台北市', '內湖區內湖路一段285巷59弄19號', 25.08376830, 121.56749310, '小吃', '小吃 很家庭式的餐廳 整體用餐環境不能說非常好就是了...\n\n有提供洛神花茶 冬瓜茶 海帶湯 婆婆還會招待台式泡菜跟豆乾給客人先打打牙祭\n\n水餃的大小和價錢成正比 不同顏色的餅皮是真的吃得出原料的味道 像是紅麴 紅蘿蔔 和菠菜之類的\n\n另外可人提到的海鮮水餃讓我很好奇 所以點了兩個 用料真的沒話說 有蝦子 魷魚 章魚 等料 但是調味沒帶出海鮮味 具體形容比較像 蝦捲的味道（感覺有加芹菜調味）\n\n所有東西味道都偏清淡（其實吃不太出香味...）\n可能是因為店家主打養生吧😂看著地圖找尋附近美食時，意外發現這間五顏六色的水餃店，店面很樸素就像一個家，內用還有阿姨煮的洛神花茶、冬瓜茶跟湯可以喝，為人非常的客氣！水餃漂亮又健康~大推👍假日到訪用餐，有招待小菜，聊了一下，多是熟客口耳相傳來買，的確料鮮味美。附小菜飲料熱湯，水餃皮薄餡多有嚼勁料多實在 湯飲品無限供應\n最驚人的是水餃裡的蝦子竟然是Q彈的\n吃完直接外帶冷凍的水餃回家( ´▽｀)', 4.4, 79, 348, '9Aci49rfg@gmail.com', '02 2798 6957', 4, '["星期一: 11:00 \\u2013 20:30","星期二: 11:00 \\u2013 20:30","星期三: 11:00 \\u2013 20:30","星期四: 11:00 \\u2013 20:30","星期五: 11:00 \\u2013 20:30","星期六: 11:00 \\u2013 20:30","星期日: 11:00 \\u2013 20:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.13 下午', '2021-06-16 07.37.13 下午', 79, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 246, 1, '65301385', '三哥涼麵小吃', '10491', '台北市', '中山區樂群二路196號', 25.07961470, 121.55872810, '小吃', '小吃 劃單點餐還能手忙腳亂……點兩個涼麵兩碗味增湯這種最快的餐點，還沒什麼客人也能等上15分鐘…是在哈囉？水餃飽滿好吃，亦可外帶冷凍水餃，香菜餡包成餃子特殊！第一次在google上寫負評.....\n因為真的...\n人生第一次吃到沒味道的涼麵昨晚買了麵和兩個小菜外帶，其中一個小菜是小魚乾，非常辣，用開水洗兩次，還是辣，舌頭都麻了，我和老公吃了幾塊豆干和小魚干，沒法再吃，就扔了，沒多久兩個人就拉肚子。\n早上經過，進去跟店家說情況，並說以前買過兩次，跟昨晚的不一樣。小姐指著一碟小魚乾說正常的是這個，兩種辣椒，昨天老闆娘炒的時候只放朝天椒，對不起，以後會標示清楚。聽完，我就離開了。我再也不會去買了!!份量少\n加大男生吃不飽', 3.3, 83, 274, 'sC29mf@gmail.com', '02 8501 2158', 4, '["星期一: 07:00 \\u2013 20:00","星期二: 07:00 \\u2013 20:00","星期三: 07:00 \\u2013 20:00","星期四: 07:00 \\u2013 20:00","星期五: 07:00 \\u2013 20:00","星期六: 11:00 \\u2013 15:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.14 下午', '2021-06-16 07.37.14 下午', 83, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 247, 1, '60516789', '屠老師的店', '10491', '台北市', '中山區北安路779號', 25.08572700, 121.55361700, '小吃', '小吃 音樂人屠穎的店，位在大直北安路上，捷運劍南站附近，四周多停車場，交通方便。店內陳列很多空軍記念品、相片、海報及模型，是一間具有主題意識的餐廳。假日中午來生意不壞，建議事先訂位，以免向隅。今天點了屠老師的乾麵、海南雞+雞飯、獅子頭、椒麻梅花肉和炒芥蘭，都很好吃。特別推薦他的雞飯，就只有雞飯，據屠姐說是雞湯煮的，和一般南薑飯很不一樣，一傢伙克了兩碗。另一道椒麻梅花肉，用自製泡椒及新鮮瓜類混炒，甜香在前、麻辣在後，滋味豐富。另外，他的咖啡也意外地好喝，決不輸大型品牌聯鎖店。服務熱情、細心，環境也很整潔，是一間非常推薦的好餐廳。屠老師的麵160+海南雞飯260+水煮魚380=800/2人；1人400，超撐（沒吃完）\n+海南雞飯的雞很軟嫩，數量也很多\n水煮魚湯有點前味酸酸的，可是很好喝，雖然寫會辣，但不吃辣的人也可以開心喝；也很香\n屠老師的麵很香，叉燒還不錯\n服務生很親切，人很好很熱情，會主動關心客人的需求\n走道很寬廣，不像台北味子會排得很密\n-海南雞飯的雞很油，量多到吃到後來有點膩\n水煮魚上的椒麻好可怕，咬到一個舌頭半邊都麻了，還好後來服務生有拿撈掉的勺子來\n就是有一點點貴《乾拌鮮肉小餛飩》\n內餡挺香、口感也不錯👍，搭配上方🔝的小黃瓜🥒絲&青蔥確實多了清新的口感。\n附餐的湯 很清甜，有蘿蔔、虱目魚丸，口感相當紮實。\n\n《麻辣香鍋蝦-小》\n讚啦！👍 👍 👍必點菜色！\n裡頭的料無敵豐富，重點是口味非常的好吃😋！！\n帶著微微的麻辣感，但不會太辣非常下飯啊～\n\n《雞飯》\n我心想不過就是一碗白飯而已⋯⋯也要30元，挖一口吃下～嗯⋯(///▽///) 好香喔！ 怎麼白白胖胖的米飯🍚，吃起來有這樣鹹香的雞湯味，太好吃啦！我想不用配菜也可以克掉唄！😋\n\n《美式咖啡☕️》\n我沒有喝到\n\n《抺茶草莓🍓紅豆瑞士捲》\n老闆娘（我自己猜的）說是採用純天然的抺茶做的所以不能冰在外面冰箱展示怕灯照（好特殊的性質），而紅豆餡也是自家做的也挺不錯吃的不會太甜。點餐時她有特別提醒⏰，草莓🍓的跟菜單上的售價不同哦！這是$120！我當下沒再確認菜單上是寫多少，只聽到會有差價、而且是新鮮草莓🍓⋯⋯心裏非常期待！\n可惜的是上桌後發現，原來新鮮草莓🍓是捲在瑞士捲裡面的，所以包在紅豆奶油餡裏太久了，感覺已經⋯\n\n看來我自己腦袋裏的抺茶草莓🍓瑞士捲構圖已經被前些天的美美甜點給局限住了⋯(///▽///)，總之抺茶配上紅豆是不錯吃滴！「那個..菜單上有的我們全部都要」\n\n因為上一次沒訂位沒位置，這次又聽到是年前最後一天營業！所以霸氣的點了全品項 講出這句真是爽\n雖然還是有些餐點沒有提早預訂所以沒吃到到\n但是已經滿足🤪\n\n餐點很家常中規中矩\n好吃是好吃但沒有特別驚艷驚人之處\n最讓我喜歡的是海南雞飯的 「雞飯」 巨好吃\n主要是價位平易近人又有飽足感 所以讓人感到幸福\n\n這樣的小餐館 用料又相當實在也有實在的功夫 不需要怕太多加工\n因為現在的人可是相當挑嘴相當厲害的呢不過在這裡完全不需要擔心\n最後還如願吃到 大家推爆的波士頓派\n雖然我是吃不太懂啦哈哈哈哈 不過好像蠻厲害的\n\n然後還最喜歡女服務員 相當親切\n\n分享給各位餐廳充滿個性風格，擺滿屠穎老師的收藏品，老闆娘氣質溫柔，很細心的介紹菜色，美好的用餐體驗。', 4.7, 455, 2139, 'NNS3orK6@gmail.com', '02 8509 1374', 2, '', 'https://restaurant-78522.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-16 07.37.16 下午', '2021-06-16 07.37.16 下午', 455, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 248, 1, '16217948', '文湖小吃店', '114', '台北市', '內湖區文湖街53號', 25.08681900, 121.56233000, '小吃', '小吃 消遣中心', 2, 2, 4, '2TAdmb@gmail.com', '02 2659 2567', 4, '', null, '', '', '2021-06-16 07.37.17 下午', '2021-06-16 07.37.17 下午', 2, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 249, 1, '35977782', '小川西堂大直店', '10462', '台北市', '中山區大直街46巷6號', 25.08201910, 121.54604460, '小吃', '小吃 泡饃很特別\n湯頭味道多元有特色\n店面明亮乾淨還有水可以自己倒\n簡單舒適的一餐點外送快十次，原本是溏心蛋，最近兩次發現變成一般的蛋，整體口味還是很不錯，但希望外送可以有溏心蛋的選擇，或是可以選不要蛋，謝謝！爬完山，騎車來大直找美食，誤打誤撞來到川西堂吃麵，果然沒讓人失望，點了三樣餐點，胡麻澆汁豆腐。讓人好驚豔，而且加麵不用錢，值得我再訪的好店點了招牌川辣麵,湯頭有點麻,小辣,但可以喝. 麵條有點爛但整體還是很好吃. 豆腐稍硬但配上醬汁還是不錯. 是會二訪的店~麻辣口味十分到地.可惜面條不够出色.要吃麻辣,必定回訪. 湯頭令人想起竹北段純貞老火鍋牛肉.', 4.1, 544, 2230, 'OjoDsuC@gmail.com', '02 2533 9059', 2, '["星期一: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00"]', 'http://www.facebook.com/kokawanishi', '0.jpg', '[0.jpg]', '2021-06-16 07.37.18 下午', '2021-06-16 07.37.18 下午', 544, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 250, 1, '80548919', '晴山日本料理', '10462', '台北市', '中山區樂群二路55號3樓', 25.08049910, 121.55351120, '日式料理', '日式料理 非常好的用餐體驗服務人員服務很不錯，享用飛驒牛日式套餐。雖然主打飛驒牛，但是一人只有一片，整體擺盤很精緻、漂亮，但是味道真的比較一般，很多食材加紫蘇調味，那味道很強烈。\n整體口位普通，但服務跟環境都不錯。裝修和料理都極好，板前師傅很有日料性格用字簡潔，服務的經理很專業，酒器和餐具搭配恰當，適合商務晚餐，會再訪，週五中午用餐高朋滿座，美福的餐廳表現都很優秀，無論是空間，美食，服務都值得五星評價\n套餐海鮮為龍蝦半隻，新鮮且廚師手法讚讚\n使用美國運通優秀，更是高CP 值\n\n環境：⭐️⭐️⭐️⭐️⭐️\n食物：⭐️⭐️⭐️⭐️⭐️\n服務：⭐️⭐️⭐️⭐️⭐️晚餐的壽司套餐跟炭火套餐，表現得很好，師傅很用心，菜品也很不錯，用餐氣氛很好', 4.2, 468, 1966, 'vTMMyBvq@gmail.com', '02 7722 3393', 4, '["星期一: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期二: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期三: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期四: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期五: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期六: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30","星期日: 11:30 \\u2013 14:30, 18:00 \\u2013 21:30"]', 'http://www.grandmayfull.com/dining/HARUYAMA', '0.jpg', '[0.jpg]', '2021-06-16 07.37.21 下午', '2021-06-16 07.37.21 下午', 468, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 251, 1, '83926387', '幸和殿手作料理 - 日式料理、生魚片、壽司丼飯、火鍋、日本清酒', '114', '台北市', '內湖區內湖路一段285巷65弄24號', 25.08455650, 121.56834580, '日式料理', '日式料理 好餐廳好料理，看到某個修手機的自己做生意當老闆當得2266，還一直潑黑水蹭熱度就覺得好笑🤭真以為自己是個咖有多少影響力XD整天在臉書發文取暖沒人理開始賣一些雜貨代購，四家店開到快倒光員工剩兩個，不如好好想想怎麼養家吧，說實話當初也是台灣iPhone沒直營不然哪來那麼多雜魚代理維修，海水退了就知道誰沒穿褲子，可憐哪食材新鮮 吃到海膽的時候 鮮味美妙的擁上腦門 仿佛在大海裡ㄧ樣\n鮭魚香蕉捲味道很特別 可以試試\n涼拌菜爽口味道也很好\n明太子雞肉也是滿滿明太子\n配上凍凍的冰杯啤酒～～～～最高！！！靠近西湖捷運站，餐點好吃，老闆娘很美又熱情！初訪！\n許多新穎的菜色實在讓人驚艷，且有別以往去過的居酒屋，店員會介紹清酒口感、韻味！讓不知道怎麼點酒的友人原本興致缺缺，經過介紹後卻愛上清酒！餐點份量足夠，口感特別，但又不致於太誇張新穎不敢嘗試，已經決定下次還要再訪！幸和殿手作料理大堆！\n主廚很用心在製作料理\n外場美麗的店長招呼客人也超級厲害👍\n一整個被這裡的料理給圈粉了⋯⋯😋😋😋', 4.5, 642, 2889, '2JaEnYk6sP@gmail.com', '02 8751 9785', 2, '["星期一: 休息","星期二: 18:00 \\u2013 23:00","星期三: 18:00 \\u2013 23:00","星期四: 12:00 \\u2013 14:30, 18:00 \\u2013 23:00","星期五: 12:00 \\u2013 14:30, 18:00 \\u2013 23:00","星期六: 12:00 \\u2013 14:30, 17:30 \\u2013 23:00","星期日: 休息"]', 'https://www.shinhoten.com/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.21 下午', '2021-06-16 07.37.21 下午', 642, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 252, 1, '61688028', '揚屋大眾日式料理', '10491', '台北市', '中山區北安路621巷36號', 25.08278080, 121.54703520, '日式料理', '日式料理 意外的豬排定食還蠻好吃的實踐大學附近 平價、新鮮、好吃的日式料理店，老闆娘很熱情好客，味噌湯、小菜可以免費再來\n\nP.S. 老闆娘已經退休了，好懷念她，熱情、有人情味的歐巴桑（2021.1.11）疫情期間的外帶200元日式便當，那個豐富度實在是太扯了......以前讀大學的時候每週都吃，\n還有同學在揚屋打工，\n當時特別喜歡吃炒烏龍麵，\n今天有空再來吃也點了炒烏龍麵，\n配料跟醬汁的味道完全不同，\n一點也不香不好吃，\n再見啦揚屋～看來只能走入回憶了！\n另外點心茶凍的比例完全不對很硬。大直地區值得造訪的日式料理，惟最近一次，店家是不是忙壞了，上等生魚片定食，照片上的蝦竟然不見了⋯⋯', 4.1, 502, 2058, 'M4tzqpvIuM@gmail.com', '02 8509 3370', 2, '["星期一: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://m.facebook.com/love85093370/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.23 下午', '2021-06-16 07.37.23 下午', 502, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 253, 1, '57756235', '磚旬堂日式料坊', '114', '台北市', '內湖區內湖路一段285巷59弄12號', 25.08390150, 121.56740810, '日式料理', '日式料理 已二訪\n🟠無菜單日式料理，晚餐880元/人\n🟠須事先預約\n🟠夫妻一同經營，氣氛很好，內裝美\n🟠適合情侶、夫妻、闔家、部門聚餐\n\n⚪️服務：\n態度親切良好，和闆娘輕鬆聊天\n⚪️食物：\n師傅調味剛好，凸顯食材的新鮮與特色，份量足夠\n\n附註：下方照片少拍到炙燒生魚片，太好吃一下就吃完！來不及拍到就無菜單料理的餐廳而言，這間的價位不是很高但食材品質跟風味都很有水準。由於疫情影響導致這次用餐只有我跟同行朋友，但是店內氣氛很適合用餐後稍作休息聊天再離開。店員非常親切態度滿分 以無菜單料理來說cp值很高 價格不貴 對食材料理方式仍然很細緻用心\n連甜點都非常好吃👍有個完美句點的感覺\n不過等候上菜的時間有點久\n而且食物份量非常精緻 男生可能會吃不飽就是了餐點很美味，用餐環境也很好晚餐880的無菜單料理\n整套吃下來餐點滿豐富的 很精緻美味\n尤其生魚片的表現很棒！\n老闆娘人很好 用餐環境也蠻舒適的\n會想要定期回訪的店！', 4.6, 229, 1053, 'CCSCubAnHA@gmail.com', '02 2627 6066', 2, '["星期一: 休息","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 21:30"]', 'https://www.facebook.com/brick2627.6066/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.26 下午', '2021-06-16 07.37.26 下午', 229, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 254, 1, '90934010', '明水三井', '104', '台北市', '中山區明水路604號', 25.08169040, 121.55179620, '日式料理', '日式料理 三井日本料理名氣非常響亮，耳聞很久了，一直很期待有機會來試看看。這次趁著家人要慶生，全家人都來品嚐這些美味的料理。\n食材都很新鮮，生魚片真的是一吃就知道很有水準！擺盤很美，味道很好，也都有很有創意，每道菜我們都很喜歡。\n店員的服務態度很好，親切周到。雖然不便宜，但還是很值得再次品嚐。店內用餐氣氛也很好，整潔舒適。很不錯的用餐經驗。打電話詢問外帶營業到8：30，結果7：20 到現場想要現切生魚片外帶居然回復說生魚片收起來了不能切，餐檯一堆熟食也賣不出去 ，感覺現場人員已經在等下班了服務很好， 價格中上，但CP值很高\n\n我們有機會就來以他們家的價位，食材當然是要選級別以上的，否則，就只剩下去吃他家的裝潢與氣氛而已。我想我跟好友或家人是不選在這裡用餐的，他們家的知名度及給人較高檔的感覺，還是比較適合招待客戶及非親密的朋友聚餐，對於饕客級的消費者來說，很難在這家店找到驚艷。若純粹以消費心靈的愉悅度衡量，我給它4顆星84分評價。上餐速度很慢，一頓飯吃了三個多小時。\n\n等到太無聊來寫一下感想跟需要改進的地方：\n1.一開始就點了啤酒，結果菜都上了兩道酒還沒來，經過提醒才補送，應該是忘記了\n2.熱毛巾進餐廳時也沒有主動送上，經提醒才送來\n3.服務生上醬油壺時滴在桌上沒有自覺\n4.開放式廚房師傅口罩拉到下巴一邊製作食物還一直說話，觀察了兩個小時一直不把口罩戴好，實在衛生堪慮\n5.好不容易等到甜點，結果甜點都吃完了咖啡還沒來，經過提醒果然是”又”忘記了，吃一頓飯什麼都要一直問一直提醒，要了才會來，實在是心很累\n\n原本一顆星，最後結帳時有經理來關心狀況並道歉救回了兩顆星。其實三井食物品質不錯，以前的服務品質在印象中也是不錯，這次用餐遇到的狀況比較多其實有點失望，但是還是期待可以加強服務訓練有所改善，加油好嗎💪', 4.2, 1676, 7039, 'sKRV61@gmail.com', '02 2533 8802', 3, '["星期一: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期二: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期三: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期四: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期五: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期六: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期日: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20"]', 'http://www.mitsuitaipei.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.28 下午', '2021-06-16 07.37.28 下午', 1676, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 255, 1, '70262839', '百魚鮮食屋', '114', '台北市', '內湖區內湖路一段285巷65弄13號', 25.08444380, 121.56748950, '日式料理', '日式料理 真心推薦，一吃就知會二訪的店特地從桃園過來吃，真香新鮮好吃、便宜、服務好用料很實在，價格也不貴，同行友人吃完很滿意\n但就個人而言，鮭魚卵、鮑魚跟味增湯的口味好像都偏甜，不是很習慣，蝦子吃起來沒有味道，不知道是因為前面吃太甜味覺麻痺或是別的原因\n但甜點的紅豆湯出乎意料之外的不甜，所以我把它喝完了\n—-\n對不起，事隔多年，我朋友在一起跟我吃完新莊某間日料告訴我，他當時其實覺得很難吃湯很大一碗用料豐富\n生魚片刀工不佳，有幾片是一刀斷一刀不斷，筋很多，\n魚肉很大片吃了跑挺膩的，感覺品質跟新鮮度都還好\n玉子燒不管是口感及味道只能說挺台式的', 4.5, 1111, 5000, 'lCgJpjeHV@gmail.com', '0909 820 289', 2, '', 'https://www.facebook.com/baiyuhut/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.29 下午', '2021-06-16 07.37.29 下午', 1111, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 256, 1, '48690004', '明水三井 二館', '10491', '台北市', '中山區敬業一路59號', 25.08174870, 121.55242580, '日式料理', '日式料理 之前都在一館用餐，今天滿位才來二館，不得不說服務真的讓人覺得是在外面的小吃館，桌上用過該先收走的空盤、裝殼的都放著，即便已經請服務人員先幫我們收桌也還要三催四請整頓吃完才來，一館完全沒這個問題😅 再來最雷的點就是服務人員點餐一頓飯可以錯兩次！第一次點餐時點一尾魚，來半尾，詢問該女服務員得到的回覆是：喔可能是廚房覺得太大尾直接幫你們變半尾。 我滿頭問號，這應該不是廚房可以決定的事吧？客人點什麼來什麼不是很正常的嗎？服務員還再說一句：反正秤斤算錢的也沒差。我覺得點錯沒關係，但要勇於承認而不是辯解。客人等待的時間不是時間嗎？第二次詢問生魚片怎麼還沒上？（因為吃過很多次知道順序）都已經吃到後面的牛排炸物了😅 才知道服務員點成壽司。（點餐時我們還說今天不點握壽司）總之服務員真的雷到以後一館沒位子都不會想再來～母親節限制2小時用餐不給咖啡，結果店家自己在耽誤上菜超過2小時！！冷氣空調竟然壞掉，保證半小時修好，結果吃完沒修好過，炎夏還只供應純熱水喝？！上菜用摔的就跑走，更別提介紹餐點！菜沒上完就著急收錢結帳說要迎接下一場次客人，服務爛到不行！爛透了！！！！！下樓發現其他成群的客人都在圍剿櫃臺，店員只能一直狡辯死不補償也是奇觀！食物怎麼樣不知道\n但是母親節這天非常混亂\n表定14:40進去卻讓一堆人在外面等到14:50都還不讓我們進去餐廳內（不是沒到桌位上歐，而是讓我們晾在外面大太陽下）今天來用餐上菜出奇的慢，隔壁卓晚到的上的比我們還快，\n限制時間到菜都還沒出完，最後超時了還在催你走\n🔥🔥🔥\n服務費還有收你10%完全跟服務不配母親節我們是第二輪原訂1；40用餐，但是卻推說前面的人還沒吃完，讓我們到兩點才進店，等到進入店內，又沒有服務員給我們倒水', 4.1, 476, 1952, 'Ah18fAU@gmail.com', '02 2533 8802', 4, '["星期一: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期二: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期三: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期四: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期五: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00"]', 'http://www.mitsuitaipei.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.31 下午', '2021-06-16 07.37.31 下午', 476, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 257, 1, '66502244', '根亭日本蓋飯', '114', '台北市', '中山區北安路841巷內號', 25.08487890, 121.55760260, '日式料理', '日式料理 從小吃到大的美味好吃！\n有溫度的小店！\n老闆，老闆娘都有點年級了，像長輩煮給你的一樣，用心感受的到！相當平價的店\n每次來幾乎都點咖哩豬排\ncp值高\n天冷的時候再加點一碗味增湯\n心滿意足\n不過偶爾會遇到咖哩沒拌均勻就是XD伯伯是日本人，也不會說中文，以為會蠻日本味，不過感覺已融合台式口味，咖哩燴飯跟玉子燒還有可樂餅口味算清淡；豬排是薄的里肌肉口感叫結實；可樂餅主也很紮實，完全就沒什麼調味的馬鈴薯泥裡頭加了一些碎肉，個人不愛；雞腿丼的雞腿蠻嫩的；個人比較喜歡玉子燒，不過是隱藏版沒在菜單上，直接跟老闆娘點才有。\n整體口味就普通，但覺得價格算便宜，所以給4星😊廚房的環境比較髒，東西到是非常好吃量又多。', 4.4, 124, 546, 'DVUBdZaVQ@gmail.com', '0981 961 919', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 休息","星期三: 休息","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.32 下午', '2021-06-16 07.37.32 下午', 124, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 258, 1, '95750378', '初魚 鮨-明水店', '104', '台北市', '中山區明水路655號1樓', 25.08331640, 121.55074690, '日式料理', '日式料理 ', 0, 0, 0, 'V2bz9TMwg@gmail.com', '02 2366 0792', 4, '["星期一: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期二: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期三: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期四: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期五: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期六: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30","星期日: 12:00 \\u2013 14:00, 17:30 \\u2013 21:30"]', null, '', '', '2021-06-16 07.37.33 下午', '2021-06-16 07.37.33 下午', 0, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 259, 1, '02785941', '祥雲龍吟', '10491', '台北市', '中山區樂群三路301號5樓', 25.08302210, 121.55952390, '日式料理', '日式料理 第一次吃米其林餐廳是在荷蘭，\n當時因為英文不靈光，\n對當地食材和文化也不了解，\n結果吃起來很是納悶，\n覺得我大概無法懂米其林 🤔\n-\n幸好第二次吃米其林餐廳在台灣，\n餐廳是位於晶華酒店的 Impromptu。\n這次主廚會講中文了，\n而且用了蠻多在地食材，\n總算能夠稍微理解這些料理的意義。\n而且裝潢很輕鬆，\n有別於平常對於米其林餐廳的印象，\n感覺在這邊可以很自在，\n所以這對次飲食的體驗蠻不錯的。\n-\n這次慶祝結婚紀念日，\n就想說再找一間餐廳好好吃一頓，\n一入門就開始感受到日式餐廳的服務，\n在我們坐定位之後，\n對我們需求的觀察也很迅速。\n菜單很精美，\n外面用一個壓日期郵戳的信封裝著，\n內裡則寫著每道菜的原料有哪些，\n光菜單就是一個很好的紀念品。\n-\n就定位之後，\n就是驚呼連連的飲食體驗。\n幾乎每一道料理都很讓人滿意，\n但我的詞彙可能太貧瘠，\n無法好好介紹這些美味的食物，\n簡單聊聊兩個印象最深刻的菜。\n-\n第一個是酥炸雲林鰻魚佐番茄，\n從沒想過炸鰻魚能做到這種地步，\n外皮極其酥脆，\n但是魚肉本身又很鬆軟，\n沒有半點炸過頭的油味，\n咬下去之後對那口感跟味道，\n真心覺得非常驚艷！\n-\n第二個是迷迭香烤乳鴿，\n在吃之前本來就耳聞這是招牌，\n但招牌的威力還是到入口之後才深切感受到。\n調味之精準、外皮之酥脆、熟度恰到好處，\n吃下去後味道好像寫進腦子裡。\n-\n其實在這種餐廳，\n美味似乎只是基本，\n我覺得更有趣的是，\n跟客戶互動的掌握很精準。\n印象最深刻的是，\n料理總是在我開始想吃下一道時，\n就很巧的端了過來，\n而且每次上菜時的溫度，\n都是可以直接入口的，\n冷菜不會覺得太涼傷牙齒，\n熱菜不會燙到吹十次才吃一口，\n時間跟溫度的掌握度很完美。\n-\nMixed Wine Pairing 搭得很好，\n即便是不懂酒也不太會喝酒的我，\n都覺得能享受這些佐餐酒，\n有專業侍酒師選酒、介紹，\n真是非常棒的服務。\n-\n整個過程吃了快四個半小時，\n最後步出餐廳時，\n服務生、侍酒師甚至是料理長，\n都已經在門口迎接我們。\n真的是完美體現賓至如歸。\n-\n雖然沒吃過其他米其林二星，\n但是祥雲龍吟真的名副其實，\n就算來吃這頓飯要繞點路，\n也是完全值得結論：這是感受不到價值感的一頓晚餐\n一直很期待來祥雲龍吟用餐，結果敗興而歸\n\n4500的價位我期待被主廚的各種巧思驚艷\n無論是特別的食材\n或是在料理手法上有所突破跟創新\n\n先聊食材，我非常認同使用台灣的食材入菜\n當這些食材是我在菜市場就可以輕易取得的\n午仔魚、碗豆仁、金華火腿、海瓜子、胭脂蝦\n感受著實不好\n\n再來說烹調\n竹地雞十分乾柴\n米粉硬到我以為沒熟\n原鴨米軟爛沒有口感\n\n最後說說服務\n不太能理解二星的服務如此粗心\n跟友人平分帳單，簽單給反了，我們也簽反了\n還是我們自己發現請服務生重印再簽一次\n歸還信用卡時，小信封又裝反\n服務生詢問是否需要計程車，我們說需要\n到了電梯口，才發現沒有叫到\n於是自己uber 回家結束這傻眼的一晚訂位因爲包廂有低消所以訂一人6500的方案，結果吃到倒數第二道菜的時候才通知我們未達低消，說我們訂的是4500的方案. 最後多花了一人一千塊，加了一個冰淇淋跟一小杯梅酒. 明明就有看我們帶一堆酒所以不會點酒，為何不在一開始就跟我們說我們未達低消. 最後強迫花那一千塊吃那個冰淇淋. 不值得且完全破壞興致.完全沒有二星的感覺.反而有種死要錢的感覺.再也不會去了！！台北最用心最美味的日本料理餐廳，完整傳承日本龍吟的文化精神，主打完全台灣食材的呈現，遠勝一堆東區只會賣每日空運食材確不懂得料理的餐廳！餐酒的搭配也是台北最強，擁有台灣最強的侍酒師Johnny除了服務好 真的沒半點能誇獎的\n6500的餐 有三道湯快飽死外又難吃 東西廉價不值這個價 海膽才一點點大概半個小指不到 生魚片也很普通沒創意再來上餐超慢吃了3小  完畢 花了15000多非常傻眼', 4.6, 437, 2010, 'JvWVHEvk@gmail.com', '02 8501 5808', 4, '["星期一: 休息","星期二: 18:00 \\u2013 21:00","星期三: 18:00 \\u2013 21:00","星期四: 18:00 \\u2013 21:00","星期五: 18:00 \\u2013 21:00","星期六: 18:00 \\u2013 21:00","星期日: 18:00 \\u2013 21:00"]', 'http://www.nihonryori-ryugin.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.33 下午', '2021-06-16 07.37.33 下午', 437, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 260, 1, '43868295', '彌生軒 YAYOI 內湖瑞光店', '114', '台北市', '內湖區瑞光路407號', 25.07830510, 121.57124500, '日式料理', '日式料理 店員滿好的，中午時段給我們兩人四人座\n挑高的環境很舒服，冷氣吹起來舒適\n\n餐點極為普通，就是Yayoi品質\n豬排煮非常死鹹，白飯黏黏的，覺得大戶屋更好吃\n小菜、漬菜一般\n土瓶蒸中規中矩，料也一般\n常溫水滿好喝的，水沒什麼味道\n\n花這個錢不如多貼一點到隔壁天仁茗茶吃還有飲料\n\n*收信用卡、Apple Pay*中午不知道吃什麼時，會跟家人來這邊用餐，南蠻炸雞套餐很好吃～自助式點餐很方便，但對長輩來說還是有點太先進，沒看到服務人員會心慌慌。供應餐點速度合宜，醬汁入味，鹹淡適中。美中不足是空間略顯擁擠，家庭客多，聊天聲吵雜。去日本時就經常看到YAYOI 彌生軒這家日式丼飯店，台灣這幾年也開了許多店，聽說目前都已經有20多家分店了！\n\n在日本的YAYOI最著名的一道菜色就是鰻魚三吃，今天剛好在附近保養車子，就跟朋友相約到彌生軒內湖瑞光店品嘗一番，我們不免俗的點了最著名的鰻魚三吃日式定食和一道三色丼飯⋯\n\n鰻魚三吃還不錯，就是湯頭口味稍重了些；三色丼飯倒是中規中矩，就是米飯水準稍差了些。而且最近有在做一些促銷活動，FB按讚就可以打9折喔⋯\n\n總之，瑕不掩瑜，整體用餐感覺還是不錯的⋯⋯今天是吃母親節大餐，每間店的服務和水準都很好，很滿意。', 4.1, 894, 3665, 'LoE7hC@gmail.com', '02 8797 2628', 2, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 21:00"]', 'http://www.yayoi.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.34 下午', '2021-06-16 07.37.34 下午', 894, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 261, 1, '09190012', '德朗火鍋 明水店-法式頂級鍋物 團體多人餐廳(人氣精緻推薦)', '10466', '台北市', '1, No. 598號明水路中山區', 25.08134950, 121.55185990, '火鍋', '火鍋 主打「法式滴鑽澄清雞湯」的高質感火鍋店，一人一鍋，服務生會在座位前方親切地解說與提供服務，在火鍋用畢後還會拿白飯幫忙煮成雜炊（需要點時間，趕時間的話要稍微注意下）。\n\n價位不算便宜，適合約會吧。氣氛不錯，服務也很到位，但是就是太精緻了，獨特的雞湯頭口味還算不錯，雞精很暖胃，最後煮的粥給過，沒推薦加什麼配料，所以有點太單調，想要吃火鍋約會的可以來，同價位有太多可以讓你爆吃的選擇！朋友介紹來吃的，本來以為菜盤跟肉很少吃不飽，但是後面又合點一碗粥，吃完真的很撐🤣🤣 一開始那杯雞湯味道很特別，有蔬菜的香，菜盤的青菜都很新鮮很鮮甜，白飯也超好吃，店員服務也很棒！對於吃巧不吃飽的個人來說是很好的食材和選擇，菜盤份量雖偏少，但手工豆皮很嫩，蛤蜊和蝦新鮮大顆，特別的雞湯湯底令人回味，最後的雜炊濃郁不死鹹，環境舒適不吵雜，同行的朋友已光顧多回，特別說今天午間的服務人員是遇過態度最好又親切的👍🏻覺得下次不會再去，覺得除了湯頭雞湯很特別，其他沒有特別吸引人的地方，故覺得cp值不是很ok，雖然菜盤有蝦跟很大顆的蛤蠣，吃起來也ok,但不知為何總覺得橘色比較有那價值的感覺', 3.9, 280, 1092, 'XfORBZ2Cfq@gmail.com', '02 8509 7598', 4, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 22:00","星期六: 11:30 \\u2013 22:00","星期日: 11:30 \\u2013 22:00"]', 'http://hotpot.deloin.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.37 下午', '2021-06-16 07.37.37 下午', 280, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 262, 1, '29487224', '天鍋宴 大直店', '10491', '台北市', '1f, No. 53號樂群三路中山區', 25.08279100, 121.55277700, '火鍋', '火鍋 好吃。海鮮是最好的。踏進這令人讚不絕口的店。CP值非常高。\n今天本來打算要用共鍋的方式，共鍋另一人若國小以上，須加120元，附自助區餐點、湯鍋、附餐（飯...等），但服務生認真的勸退之後，點了招牌雙人套餐並退掉一份菜盤（扣150），雙人套餐超級豐盛的啊！有六種肉類可以選擇（6選4），菜盤，海鮮盤（一人份有一隻大草蝦、活蝦兩隻、一隻小卷、蛤蜊...等）。忽然覺得服務生真是勸退的太棒了啦。  根本超值超值😬😬\n一項一項慢慢說，蔬菜柴魚湯底甘甜，菜盤上沒有雷！唯一可能會讓人不喜歡的貢丸也是多汁的令人驚艷，高麗菜很新鮮，最喜歡這種很脆的高麗菜。海鮮盤上新鮮度都非常不錯，雖然活蝦好像是急速冷凍的蝦子，並不是真的活跳跳的那種，但新鮮度、肉的彈度一樣是可圈可點，小卷超新鮮，稍微燙一下又脆又甜，蛤蜊肉也超大超飽滿，海鮮盤裡頭的蝦仁漿跟花枝漿更是我的愛啊！😘😘😘好好吃，海鮮盤還放了去腥味的薑絲以及枸杞米酒，可以讓顧客煮一鍋鮮度爆表的海鮮湯，男朋友喝的很滿意。肉盤，我們點了雪花牛、沙朗牛、梅花豬、霜降雞肉，這幾種肉類也是沒有雷，雞肉是圓形薄切非塊狀，但一樣的甜味滿溢。\n自助區，醬料區什麼都有，是我看過最齊全的一次，飲料甜點也是選擇多多，東西一點都不馬虎，是非常值得嘉許的店家呀！雖然單價確實不低(最便宜的套餐要價450），但CP值之高，絕對不會讓你後悔從錢包把錢掏出來的！！好吃 料好又實在 對得起價格\n在三樓的服務人員雅文服務很棒  很細心  又有耐心帶中部的朋友來，兩位朋友讚不絕口！非常有面子，烏骨雞鍋真的非常高cp!不過這次在二樓服務人員沒有像第一次來的服務人員積極有些失望，很棒的經驗、食材新鮮，還願意再次前來~食材新鮮好吃，尤其是蛤蜊每顆都大顆飽滿，雙人套餐1380元，有肉有海鮮非常豐富，飲料櫃給很有誠意有多種選擇，而且不用服務費，可以刷卡。\n\nPS:壽星優惠有點弱，還要用「抽」的，差不多價位的好食多涮涮鍋壽星優惠就很棒', 4.5, 894, 4023, 'lyUglYL@gmail.com', '02 8501 1337', 2, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', 'http://skyhotpot.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.38 下午', '2021-06-16 07.37.38 下午', 894, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 263, 1, '34763319', '22:02火鍋美麗華店', '10466', '台北市', '中山區敬業三路20號3F', 25.08330720, 121.55716480, '火鍋', '火鍋 ✨22:02 火鍋。樂活✨\n分享一間過年適合聚餐的單點式火鍋店啦！\n肉品很讚，有分精緻和CP值的肉品，個人覺得CP值就超讚的喔！精緻的肉質更不用說💯\n海鮮也不錯，但蝦仁漿>白蝦>魚，家人覺得魚沒有到讓人驚艷。\n滷味三寶處理的很乾淨又Q又嫩❤️\n整體份量多，環境又很棒，CP值很高😍\n\n▫️鍋底費$120/人\n▫️霜降牛肉$368\n▫️無骨牛小排$468\n▫️滷味三寶$388\n▫️白蝦$159\n▫️日本黑鱈魚$339\n▫️蝦仁漿$228\n▫️非基改生豆包$168\n▪️附綜合菜盤*2\n▪️不辣醬、辣沾醬、怪味醬\n▪️柚子冰沙、梅子冰沙、銀耳露\n▪️水果盤\n\n🍴22:02火鍋美麗華店\n📍台北市中山區敬業三路20號3F\n🚇劍南路3號出口22:02火鍋。樂活 / 美麗華店，位於美麗華三樓。店門口的一整片綠吸引了我的視線，牆上時鐘造型的22:02裝飾相當醒目。門口為櫃檯區以及等候區，佈置得相當雅緻。\n偌大寬敞的用餐環境，整體裝潢非常舒適、環境乾淨整潔，除了一般座椅也有長形的沙發座，一旁架上也有販售各式各樣的有機蔬菜。\n服務人員的態度親切、上菜速度很快，上菜的每道菜色也都會詳細的說明，很是專業。\n餐點可以選擇套餐或單點，湯頭則有四種可以選擇，辣味鍋的辣度其實不是太辣，濃郁爽口的火鍋湯頭、健康蔬食搭配新鮮肉品與海鮮，在滿滿的綠意包圍下用餐，這樣的環境氛圍真的非常享受，已列入必回訪名單也會推薦給朋友嚐鮮。210512晚間用餐\n\n這次已經是第三訪\n用餐經驗十分愉快\n\n飲料冰沙的部分柚子跟火龍果都好喝\n還有一大壺的茶可以解膩\n副餐的地瓜飯跟寬麵寬粉選項頗多✨\n\n還有其他家沒有的獨特醬料\n\n因為有壽星當日依歲數送肉的活動，再加上飲料麵飯都可以續加，CP值超高的好店👍位於大直美麗華，品質還算不錯的，單點式火鍋店。 位置方便，捷運大直美麗華站 出口 後 ，走路5分鐘內可抵達。 開車來的話，樓下也有停車場可停車。 每個客人有鍋底基本消費。 本次主食挑選地瓜飯，覺得地瓜少了一點。  飲料可以讓你續杯，有三、四種選擇可挑選（冷熱皆有）。\n\n外場服務人員態度良好。 用餐環境 乾淨，不過因為是開放式用餐環境 ，可以聽到小孩哭鬧。 現場展示架提供新鮮蔬果，讓客人可以單獨購買、帶回去 。我整體用餐經驗中規中矩 ，不一定會有再次回來消費的動機。\n\n接受現金跟信用卡支付。蔬菜是有機的食材，可選擇兩種湯底，單點計費，兩人用餐不能點太多品項，單品數量會太多吃不完。', 4.1, 814, 3337, 'nzGuOhi@gmail.com', '02 8501 2220', 2, '["星期一: 11:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期二: 11:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期三: 11:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期四: 11:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期五: 11:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.facebook.com/2202LOHAS/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.39 下午', '2021-06-16 07.37.39 下午', 814, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 264, 1, '00798257', '天鍋宴西湖店3.0', '11442', '台北市', '內湖區環山路一段12號', 25.08328350, 121.56541280, '火鍋', '火鍋 食材新鮮衛生乾淨，菜盤總類豐富，海鮮新鮮肉類品質好，飲料，熱茶、咖啡可以無限暢飲，杜老爺曠世奇派是我家小孩大人的最愛，還有紫米紅豆、銀耳蓮子、綠豆薏仁等輪流替換的熱甜品，這樣豐富多樣的餐點提供還不收服務費喔！已經固定吃了好幾年的店家，店內服務人員親切積極，假日或特定節日有時候不好訂位，但每次用餐都沒讓我們失望過，是家人朋友聚餐的好選擇。精緻、用心不管吃幾次都不膩品質很穩定，海鮮很新鮮，因為疫情有外帶優惠也很划算！！推推👍麻辣湯底很不錯，鍋底也好吃，牛肩肉加價的份量也很夠，很適合食量較大的人來品嚐。飲料跟冰品的項目也很多元，但客人蠻多，建議先提前電話預約海鮮新鮮肥美，蛤蜊很大，龍蝦肉Q彈\n菜盤很大，菜都很新鮮，沒有便宜三餃（燕餃蛋餃魚餃）菜盤也能換肉盤或是蛤蜊\n服務很客氣有禮，有無限供應的曠世奇派', 4.6, 753, 3464, 'KutxUzmZT@gmail.com', '02 2658 1233', 2, '["星期一: 11:00 \\u2013 23:30","星期二: 11:00 \\u2013 23:30","星期三: 11:00 \\u2013 23:30","星期四: 11:00 \\u2013 23:30","星期五: 11:00 \\u2013 23:30","星期六: 11:00 \\u2013 23:30","星期日: 11:00 \\u2013 23:30"]', 'http://skyhotpot.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.40 下午', '2021-06-16 07.37.40 下午', 753, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 265, 1, '26639396', 'Extension 1 by 橘色', '114', '台北市', '內湖區瑞光路631號1樓', 25.08052920, 121.56641330, '火鍋', '火鍋 服務非常好👍🏻\n尤其是Luke\n讓人會想再訪這種價位真的沒有期待性價比高\n說是要吃服務 不過服務也很不行\n菜盤的蘆筍頭爛掉 服務生讓我們自己拔掉\n然後還一根一根問這個可以嗎\n說可以幫忙煮海鮮 會協助計時判斷熟度\n結果一去不復返\n大生蠔等到變蚵乾 最後我們自己撈起來\n吃完大概9點多 經過4-5個服務生\n完全都沒有人注意到我們在揮手想結帳\n不知道是不是主打養生 鍋底真的沒有味道\n平日晚餐的服務 給大家參考 :)高級的很有道理，各種肉類、海鮮食材新鮮，甜點杏仁豆腐又Q又軟。\n服務人員態度親切又專業~⭐⭐⭐ 幸福滿點的火鍋，聽說橘色內湖店才有這種服務，服侍的妥妥帖帖，服務生對話也有趣，因為聽說可以幫剝蟹，所以直接加點一隻平常不敢點的超麻煩青蟳，不僅完美剝好，而且還有擺盤，蝦子盤真的驚艷到我們，蝦尾整整齊齊的點綴在盤邊當裝飾，太浮誇了~~~~♪ヽ(･ˇ∀ˇ･ゞ)💕💕💕\n餐期碗盤餐具只要亂掉就立馬更新補上，最後的海鮮精華液煮的黃金粥也是好吃到想再嗑一鍋的衝動，完全刷新我對高級火鍋的印象，另外一間目前被擠到第二名去了~~~└( ＾ω＾)」🥂\n\n#小精靈模式\n#橘色\n#EXTENSION1\n#舒適用餐除了美味的餐點讓人想再次前往之外，更要大推Jimmy貼心又周到的服務，讚👍', 4.4, 793, 3489, '5OukIp@gmail.com', '02 2657 1237', 3, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 11:30 \\u2013 22:00"]', 'https://lihi1.com/cIiTD', '0.jpg', '[0.jpg]', '2021-06-16 07.37.41 下午', '2021-06-16 07.37.41 下午', 793, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 266, 1, '25091252', '金鍋盃小火鍋', '11452', '台北市', '內湖區環山路二段37號', 25.08544650, 121.57311830, '火鍋', '火鍋 再次消費，海鮮鮮度夠但種類不多，大多是蝦類、肉品種類多、菜盤就一般般沒有丸、餃、魚板之類，但高麗菜青菜可續盤，想吃肉就多點些肉盤，而湯頭都是要另外加價的，麻辣湯有辣但是不會麻，藥膳湯頭，也是淡而無味，沒有中藥的香味，冰棒一鍋只能拿一隻。以小火鍋而言價位屬中高價位，CP值覺得不高。每個人消費額要千餘元。唯一最愛的火鍋店沒有之一！肉質沒話說，海鮮更是鮮甜！海菜非常特別一定要嘗試！刀削麵配上自家炒的沙茶醬真的一口接一口停不下來！金鍋盃雙人餐、海鮮盤￼的品質好好喔￼👍👍👍\n蛤蜊很大顆耶～下次還要來😋￼甘蔗熬煮的湯頭，沒有加工品的菜盤\n吃來滿足又沒有負擔！\n用餐環境寬敞又舒服來過很多次了，每次重要節日都想到這裡，食材新鮮，海陸餐很豐富！', 4.5, 1266, 5697, 'kzp87q@gmail.com', '02 8797 3827', 3, '["星期一: 11:30 \\u2013 00:00","星期二: 11:30 \\u2013 00:00","星期三: 11:30 \\u2013 00:00","星期四: 11:30 \\u2013 00:00","星期五: 11:30 \\u2013 00:00","星期六: 11:30 \\u2013 00:00","星期日: 11:30 \\u2013 00:00"]', 'https://www.facebook.com/Jinguobei2/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.42 下午', '2021-06-16 07.37.42 下午', 1266, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 267, 1, '51817420', '（烤）小蒙牛頂級麻辣養生鍋Hotpot-內湖店', '114', '台北市', '內湖區內湖路一段248號', 25.08194050, 121.56649240, '火鍋', '火鍋 東西好吃，服務親切，值得再來聚餐！出餐慢 漏菜多 平板爛肉品新鮮，送餐速度快廁所沒定時清潔，髒髒的就是正統台灣火鍋店吃到飽！！', 4.2, 1722, 7232, 'W0K2loBeQ8@gmail.com', '02 2797 3366', 2, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 23:00","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 23:00","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 23:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 23:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 23:00","星期六: 11:30 \\u2013 23:00","星期日: 11:30 \\u2013 23:00"]', 'https://www.mongobeef.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.43 下午', '2021-06-16 07.37.43 下午', 1722, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 268, 1, '95981794', '連進騰爐酸菜白肉鍋-火鍋外帶/酸菜白肉麵/酸辣粉/個人鍋', '114', '台北市', '內湖區洲子街108號1樓', 25.08044920, 121.56961720, '火鍋', '火鍋 肉的品質有點太雷，其他還可評論前先說明一下，我至貴店用餐次數超過8次。\n本次僅針對最近一次用餐給予評論。\n\n此次消費於開始點餐時發現消費模式相較以往有些許不同…\n\n個人覺得\n\n優點的部份:\n1.食材不錯，新鮮，味道好，湯頭有特色\n2.增加了鴛鴦鍋可選擇也很不錯\n3.多了冰淇淋選項也有加分\n\n缺點部份:\n吃到飽用餐時間只有90分鐘，吃的很趕，肉品一次最多只能點3份而且很訝異的是那個肉片切的真是超級薄…沒錯看起來是透明的肉片…夠薄吧…三兩口就吃完了，好吃是好吃，但我受驚了。肉吃完後要續點對吧，因為我就是專門來吃肉的。現場約莫六桌客人，看到服務生是忙進忙出，我都不好意思舉手說要點餐，啊就是要來吃到飽的呀，肚子餓，我只能厚臉皮的一直舉手請服務生來點餐，沒辦法，肉片薄透輕如羽翼，還只能一次點三盤，一上桌馬上就又喀光了，那怎辦?再舉手再請服務生來呀，…可是才剛剛點過囧rz ，雖說服務生態度還不錯，但這樣的用餐方式是讓人覺得不愉悅的。\n\n環境衛生方面是個人尚可接受的程度\n\n消費較以往高了些\n\n多了服務費\n且只能現金結帳\n\n還要需兩位消費者掃描QR cord進行評論或滿意度調查才有折扣(結帳時服務生會微笑的跟您說，請給我們五顆星評價才能享折扣優惠哦…)這種評價有和意義?現在折扣後的價位跟以往沒有這項活動時的價格相同，也就是說其實根本沒有優惠…都是羊毛出在羊身上，我用餐離開後馬上更改為兩顆星了。CP還不錯,肉品有牛肉、豬五花、豬梅花、羊肉、雞肉。肉片品質還可以。"\n青菜香菇海鮮也都有,適合聚餐的好地方內湖創始店 開車或捷運都非常方便，肉品、海鮮、湯頭和丸子類都表現的非常好👍🏻很值得一來再來喔🙌🏻上菜太慢 太趕 第三次加點紅豆餡餅也沒到 就算了 紅豆餅真的好吃不死甜\n只可惜 真的上菜慢又趕 整體算好吃', 4.4, 2910, 12804, '05en0PE2C@gmail.com', '02 2798 7696', 2, '["星期一: 11:30 \\u2013 19:30","星期二: 11:30 \\u2013 19:30","星期三: 11:30 \\u2013 19:30","星期四: 11:30 \\u2013 19:30","星期五: 11:30 \\u2013 19:30","星期六: 11:30 \\u2013 19:30","星期日: 11:30 \\u2013 19:30"]', 'https://northeastpot.com/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.43 下午', '2021-06-16 07.37.43 下午', 2910, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 269, 1, '12831834', '龟覓の鍋物', '114', '台北市', '內湖區內湖路一段411巷19弄11號', 25.08242350, 121.57165500, '火鍋', '火鍋 藥燉鍋5星\n其他肉我吃不出來\n隔壁桌海鮮看起來不錯\n老闆娘過度熱情，室內用餐環境有點吵。\n草莓霜淇淋太讚了，比其他火鍋店的都好吃。\n可惜白飯單點有點貴。那天爬完剪刀山後很想吃火鍋，於是來到了這裡。\n\n老闆娘說她喜歡被叫大姐，因為老闆娘聽起來都在數錢，大姐聽起來是在做事(lol)，好的，大姐。\n\n先說結論，非常推他們的藥燉！\n\n他們的藥燉溫和順口，冷底如我吃了這一餐，整個星期身體都很暖。如果點了藥燉鍋，別擔心湯底會被稀釋的問題，因為加的是原湯，要多少都可以，大姐非常阿莎力。\n\n說到大姐，大姐真的非常熱情，那天我們也得到一盤招待的牛肉盤，超級開心！\n\n除了藥燉之外，也很喜歡他們的草莓霜淇淋，飯後一定要來一支。\n\n天冷的時候他們的窗戶都霧霧的，非常神秘，差點以為進去就出不來了呀（誤）不過幸好還是走了進去ＸＤ\n\n喜歡藥燉的可愛人不要錯過吶～食材新鮮，老闆娘待人親切，大方給料，一定要再去！昆布和排骨湯底天然好喝，菜盤的菜很新鮮，梅花豬片厚又甜，好吃!昆布湯頭蠻鮮甜的，梅花豬跟培根豬肉片蠻好吃的，老闆娘還額外招待一盤豬肉片，飲料也很齊全，還有冰淇淋也好吃，讚，下次想要嘗試藥燉排骨鍋。', 4.7, 226, 1062, '12MCSlL@gmail.com', '02 2627 2656', 4, '', 'https://www.facebook.com/Tortoiseme/?__tn__=kC-R&eid=ARBjP1TR43HbPfGCyMISGA7kJ3q4rVbyNSZAZiUNiwce21CXa5-L1Niv9R35BQR4weuEv9d7e3QBQD0T&hc_ref=ARRHltTI9TZyg25t_5YWOniHQdxYY-cZqjfxOO1KO97Nq8mhME_o-EjcME3WfGfRe7o&fref=tag&__xts__[0]=68.ARDmW10RheDzV3wMj6tKHhTxhyILdjm-zfTBRRWHiz6i9fLn5GMcu7iFFhFViXxEQczdJarrJJApWVcIkNerV9bdW-xwlHjUS4pAHnGCEWLFJJh_RdUW1JQO3p-GBMx5MxmIXk8hsMIqBVdYtQrVVwBO3U1ZrHiy81WBqSdMEI1fCcD-zlsaJEArz3cP7BFw9dYW-RbvoNDp4sjnFAD0ZSHYmjqWdNEATwSl0isEk11AszR9Tp55bfFB46Ur0BS8S6R8aH43Vr4aRWA-SM7RdgiupRpf4qN3', '0.jpg', '[0.jpg]', '2021-06-16 07.37.44 下午', '2021-06-16 07.37.44 下午', 226, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 270, 1, '51402479', 'The SOUP 蔬店。手作鍋物', '114', '台北市', '內湖區內湖路一段591巷12-3號', 25.08167990, 121.57339140, '火鍋', '火鍋 環境舒適，客人用餐完畢皆有消毒餐桌，點了梅花豬肉搭配泡菜湯底，另外加點一份蛋餃，結帳將近500元，味道不及普通，不會再訪。食材新鮮，南瓜和芋頭好吃，醬油清爽有加新鮮的金桔。套餐份量少吃完不會過飽，昆布鍋口味較清淡，環境乾淨舒服，價位略高的生活火鍋。食材新鮮，老闆娘很客氣，醬料果醋超棒溫馨的環境 肉質良好 蔬菜特定新鮮\n中等價位  適合家庭朋友小聚吃完很舒服的一餐\n昆布湯頭很順口\n配的蔬菜盤只有2-3個火鍋料其他都是蔬菜菇類\n主餐從便宜到貴都有\n也有季節時蔬可以選\n很棒！\n雞蛋是本來就有配的\n不走低價路線\n是走健康美味不便宜風格XD', 4.1, 528, 2165, '3LKPu2X@gmail.com', '02 8797 2336', 2, '["星期一: 休息","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/thesoup87972336/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.46 下午', '2021-06-16 07.37.46 下午', 528, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 271, 1, '73758420', '新這一鍋 台北ATT大直殿', '104', '台北市', '中山區敬業三路123號3樓', 25.08108460, 121.55546430, '火鍋', '火鍋 4/6 晚上8點用餐\n櫃檯領班一位綁包頭的女生\n帶入座也沒有說任何話就轉身離開\n示意她請她幫我們介紹菜單\n結果只講了湯頭就直接翻到雙人跟四人套餐的地方也沒介紹內容物\n問她所以你們只有雙人跟四人的選擇嗎\n結果櫃檯領班小姐居然回答我 我剛剛說了這整本都是菜單啊！\n難道我看不出來這是菜單嗎？\n下一秒他把其他的空餐具收走放在對面的沙發上！\n我有看錯嗎疫情期間把客人要使用的筷子直接放在每天都有人在坐的沙發上！我都傻眼了\n8點入座還不到9點就一直有服務生詢問這個還有需要嗎還要加點嗎湯要留著嗎\n兩個人花了兩千多塊用餐不到一小時就一直感受到服務生想趕人走如果是這樣就不要接8點的訂位\n整個用餐讓人非常不愉快開鍋囉！歡迎用膳！ 進場時，每位服務員高喊著，讓來餐廳用餐的客人就像是皇帝要用膳般的有尊貴感👑 服務態度也非常親切🥰\n\n與品牌「這一鍋」的差別只在於室內的裝潢，裝潢得非常氣派如同在皇宮中用餐，還多了自助吧可以享用飲品🥂其餘餐點部分皆相同，而各種食材上桌時的設計也相當有趣！\n\n#神仙牛肉(150g)是特選無骨牛小排切成薄片 「曬」在桿子上🐂\n\n#西班牙松阪豬肉(160g)➪捲成玫瑰花形狀的粉色松阪豬 可愛又吸睛\n\n#五彩鮮蔬盤➪內含各式無毒的水耕蔬菜(當天甚至還看到小菜蟲在爬呢)\n\n#盛味綜合丸(11入)➪肉類包含雪花牛肉丸與帶有椒麻味的川丸子，海鮮類包含鮮蝦 花枝丸，還有黑黑的墨魚丸🦑\n\n#千張豆腐皮➪黑色墨汁是微鹹的墨魚汁 豆腐皮只需下鍋3秒就可以撈起被霜淇淋耽誤的火鍋店\n\n但說實在湯頭跟肉類真的不值得!!\n\n神仙牛肉擺得很好看, 7片肉卻要價398不合理, 重點是吃起來不怎麼樣...\n\n伊比利豬沒吃到, 我四點多要點餐的時候和我說"要確認一下這時間有沒有", 我原本以為是最近沒進貨. 但結果好像是下午時段不供應, 五點半吃完走出去時, 發現別桌就一盤豬(可能是晚餐時段)... 我就很納悶菜單上是寫"限量供應", 而不是寫晚餐時段...再次來吃這一鍋，喜歡他們的老火鍋湯頭，聽服務人員說是用多種骨頭燉煮出來的，難怪這麼的香醇濃郁，還有辣鍋可以另外的加辣度感覺蠻好的。\n這次點的蝦子非常的新鮮，肉質也很Q彈，一份8隻很好吃，下次還要再點。\n另外覺得不錯的是他們的服務態度一直都保持的很親切，這會讓我們吃的很舒適也很開心，謝謝你們的服務！\n\n非常好吃的鴛鴦麻辣火鍋，湯汁味美鮮甜麻辣適中，牛肉品質滑嫩，有噱頭的是千張豆腐紙，很薄的豆皮像紙一樣，可以用毛病筆沾墨魚汁在上面寫字畫畫，然後再煮來吃，蠻有創意的服務親切、主動積極、清朝宮廷般的禮遇，環境清淨、方便，飲料、霜淇淋任客人自取、白飯好吃到讓兒子連吃三碗、讚不絕口！\n火鍋食材鮮美、任客人選擇，湯頭超讚、隨時主動幫客人加湯、加料（麻辣鍋的鴨血和滷到入味的豆腐、酸菜鍋的酸菜…）～吃不完還可以打包、店家還會幫客人加湯、加料！\n\n停車方便、同樓層也有逛不完的樂子…\n\n也許有人嫌貴…但是他們的服務品質絕對值得他們的價格！\n\n這一家…難得的讓我給他們五顆星！', 4.8, 1175, 5640, 'NaISFl@gmail.com', '02 7725 3388', 2, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 12:00 \\u2013 20:00"]', 'http://www.toponepot.com/index.php', '0.jpg', '[0.jpg]', '2021-06-16 07.37.48 下午', '2021-06-16 07.37.48 下午', 1175, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 272, 1, '96241961', '5味(五味)臭臭鍋.小火鍋-西湖店', '11445', '台北市', '內湖區內湖路一段323巷13號', 25.08350410, 121.56810650, '火鍋', '火鍋 老闆小氣 來的人太少再熱他都不願意開冷氣\n自己蛋多一顆擺在桌上 吃了說要加10塊\n吃的過程一直催你關火不然他很難收\n湯超級淡根本沒味道\n碗永遠只給一個 其他等藏起來 而且還是紙碗\n冰淇淋勺的水永遠不換 而且永遠只有一點點 只夠稍微沾到杓子的前端 請問這樣怎麼洗 而且冰淇淋一直黏在杓子上 況且你叫的還是最便宜的冰淇\n\n以前沒有其他家火鍋 只能吃這家\n老仙覺跟雲鼎阿二開了之後全校都在討論和歡呼 再也沒看過任何學生去 我自己也再也沒有去過\n加油 不要倒囉！\n要不是最低只能給一顆我一定給零顆平價小火鍋今天去用餐吃到酸掉的油豆腐跟店家反應，收到的結果是{豆類本身會發酵可能會有味道，你朋友吃都沒問題} ....?????\n很傻眼...你確定每一個品質都一樣，沒有要凹你，吃壞肚子你會負責ㄇ??\n態度不知道在差什麼 味道也就普通\n東西有問題應該是跟客人道歉吧???為什麼搞的是客人有問題桌上很油 白飯很硬 湯頭沒特色 用料品質不好最近去吃，老闆戴口罩了，但是端菜出來的都沒戴口罩，拿上來時候一直對著小火鍋說話.......好像現在是疫情期間吧！而且12/1號都變嚴格了，還不戴口罩，真的是越吃越退步，小朋友到處跑撞到人也不會說，裡面跑好像也不對！', 3.5, 335, 1173, 'YSXJsvs5x@gmail.com', '02 2657 2148', 1, '["星期一: 10:00 \\u2013 22:00","星期二: 10:00 \\u2013 22:00","星期三: 10:00 \\u2013 22:00","星期四: 10:00 \\u2013 22:00","星期五: 10:00 \\u2013 22:00","星期六: 休息","星期日: 10:00 \\u2013 22:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.37.48 下午', '2021-06-16 07.37.48 下午', 335, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 273, 1, '67993054', '滿座小火鍋', '10491', '台北市', '中山區北安路630巷9弄3號', 25.08320800, 121.54960340, '火鍋', '火鍋 老闆都很親切，店內有自助冰淇淋、茶、白飯、醬料吃到飽，白飯煮得很好吃，重點是小火鍋很新鮮，味道很好！口味一般，老闆態度不佳。帶小孩用餐，因為小孩吃不下，沒幫她單點一鍋。詢問是否可以另外單點一些給小孩，就不用共鍋費。老闆回嗆，一人一定要一鍋，不然幫妳打包。態度惡劣不敢恭維。路過好幾次 就今晚去吃吃看吧🐕CP值很高，服務也很好，大人小孩吃的超開心，而且冰淇淋真的讚啦！是有著滿滿蛤蜊的蛤蜊鍋！20多顆蛤蜊雖然不大，但個頭飽滿又多汁，非常新鮮，這樣的價格來說CP值真的很高👍🏻\n這次分別點了養生鍋與高湯鍋，養生鍋的中藥味不會太重，喝起來很清爽。但我更喜歡高湯鍋，吃得出食物的自然清甜，搭配起蛤蜊的鮮味更是一絕！\n不喜歡吃火鍋料的人還可以跟老闆說換成蔬菜喔，整鍋的菜加上無添加味精的湯頭，無負擔的美味。\n店內的冰淇淋也不馬虎，每天口味會不太一樣，當天的巧克力口味很不錯，不會太甜，吃得到微微的苦韻味，作為飯後甜點非常適合👍🏻', 4.2, 234, 983, 'Oi7FqmHWH@gmail.com', '02 2532 7200', 1, '["星期一: 休息","星期二: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 12:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'http://hsiangling5.pixnet.net/blog/post/86001362', '0.jpg', '[0.jpg]', '2021-06-16 07.37.49 下午', '2021-06-16 07.37.49 下午', 234, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 274, 1, '76253755', '尬一鍋精緻鍋物-內湖推薦火鍋|內湖餐廳|內湖宵夜|內湖美食|台北火鍋|商業午餐', '114', '台北市', '內湖區環山路一段24巷2號', 25.08444810, 121.56522840, '火鍋', '火鍋 過了幾天來評價的\n我跟我老公平均一週吃4次火鍋，也算蠻有經驗的了。想說這家還沒吃過去，來吃看看。\n我跟我老公是共鍋的，菜一上來，就被迫聽老闆娘介紹菜盤，其實我們沒有興趣聽，對視一眼，老闆娘還是沒有停下來的意思，繼續介紹，大概就是兩層意思：1.菜非常新鮮；2.菜的種類非常多，跟我們介紹這是什麼菜，那是什麼菜\n其實菜我都認得啦～小時候很常跟父母去菜場，真的不需要介紹，菜新不新鮮，我吃的出來啦。老闆娘如果很想客人知道，可以寫在菜單上或者在門口用電視機播放～我真的很！討 ！厭別人用手貼的很近指著我即將下肚的食物啦！\n我來吃火鍋，不是為了吃素的，肉盤卻小的傻眼，果然，老闆娘沒有要介紹肉盤的意思\n我們點的麻辣鍋，老闆娘說這個非常好喝，讓我們喝喝看，我們比較喜歡很辣的湯底，所以沒有喝麻辣湯的習慣，而且剛上來的湯底，還沒沸啦，是要喝什麼？就跟老闆娘婉拒了，結果老闆娘說，是喔？也有客人說不喜歡喝湯，但喝過都說好喝，你們不試試嗎？當下覺得好尷尬，是要看著我們喝的意思嗎？😂\n總結：菜是新鮮的，菜盤的種類也是非常多的，現打果汁也好喝。湯普普，肉很少，花了940，沒吃到幾片肉很少看到火鍋店菜盤種類這麼豐富！\n居然有蝦仁水餃（裡面包一整隻蝦）跟餡料滿滿的福州丸。\n\n菜盤除了常見的高麗菜、金針菇外放了很種類的小農蔬菜，有：奶油白菜、油菜花、金針花⋯（幾乎沒在火鍋店看過）\n\n而且每樣蔬菜都超級好吃，下鍋煮熟後味道很鮮甜，最後附的果汁也很好喝，非常滿意👍👍菜盤實在 鮮蝦水餃 福州魚丸 還有各種蔬菜 還有果汁 跟杏仁豆腐 值得推薦的在地美食 建議可以新增手工三記魚餃我一定每次都必點🤣🤣老闆還會熱情講解跟說明 好吃一進來環境讓人特別的舒服，食材也很新鮮健康，飯後還有免費的冰淇凌吃，還有招待的小甜點，不收服務費，服務還棒棒的😄環境整潔舒適，每個座位都超大，餐點cp值高，肉好吃海鮮也很新鮮，菜盤種類和份量多，餐後還有現打果汁與甜點，另外店內也有免費的明治冰淇淋，最重要的是不收服務費！超讚👍🏻', 4.8, 181, 869, '3sWGZqz99R@gmail.com', '02 8797 7738', 4, '["星期一: 11:30 \\u2013 23:30","星期二: 11:30 \\u2013 23:30","星期三: 11:30 \\u2013 23:30","星期四: 11:30 \\u2013 23:30","星期五: 11:30 \\u2013 23:30","星期六: 11:30 \\u2013 23:30","星期日: 11:30 \\u2013 23:30"]', 'https://m.facebook.com/pages/category/Hot-Pot-Restaurant/%E5%B0%AC%E4%B8%80%E9%8D%8B%E7%B2%BE%E7%B7%BB%E9%8D%8B%E7%89%A9-109458274134836/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.50 下午', '2021-06-16 07.37.50 下午', 181, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 275, 1, '58042298', '椒房殿麻辣鴛鴦火鍋', '104', '台北市', '中山區北安路640號', 25.08309890, 121.54925890, '火鍋', '火鍋 昨晚跟著已是“熟客”（意即回訪多次）的老闆、主管過去用餐，原本很期待結果挺失望...\n1.香菜皮蛋湯底：外面少見口味加上老闆大力推薦，那就表示一定要點啦！結果跟【女】店員order當下立刻被告知香菜不能續，因成本上升。\n哈囉....現在菜價極便宜好嗎！\n而湯底上桌只看到【4條香菜】載浮載沉，請問這樣的量又不能續加，會不會太小氣了？\n2.貴妃鮑：又是老闆大力推薦必吃不可的料，實際品嚐心得....還可以，就是加工鮑魚而已沒什麼特別。\n3.自助區&服務態度：讓我絕對不會再回訪的主因！疫情期間看到整個醬料區都沒加蓋實在令人傻眼，連水果也是沒冷藏、直接曝露空氣中、完全沒防護&保鮮；醬料更是開口大大迎接所有落菌...\n服務態度更是令人不敢恭維！訂位6：30，還沒八點就被告知最後一次加點，期間請店員加湯、補皮蛋、續肉都是緩慢上桌，慢送餐倒是很準時提醒加點期限。而8：30還沒到就開始各種動作明示暗示要我們結帳離開，老闆是已經可以刷臉辨識的VIP，也有好好口氣跟店員說店內尚有不少空位，可否再讓我們多坐一下？女店員直接表明：請先結帳！非常不給面子。\n而在老闆準備結帳時我們趕緊去吃冰，又被女店員示意妳們只能吃一次，因為要付賬了...\n好！我們也配合了，未料回座位吃沒幾口女店員走過來開始收拾餐桌,我們人還在有必要這樣趕人嗎？邊收還邊嘴要我們動作快一點，請問到底是在趕什麼！！更扯的是全程臭臉+大力收拾餐盤後，立馬再拿著酒精過來說要噴桌子，請我們加速因為酒精會噴到！我們並非奧客，當下也不是要關店了真的有必要這樣嗎？之前來過2.3次，口味不錯，男老板會親切解說湯底如何煮好吃，男服務生也是很親切服務很好！但今天晚上9點才到，2位女服務生臉超臭，很不想接客人，但又接了，吃飯過程，老的女服務員，都沒出現，但會突然叫我們聲音要小點，要加肉，女服務員還故意沒聽到！整體用餐感覺很不舒服，是不是整間店頂讓給別人了，湯頭味道也淡了，沒之前好吃，所以員工也都換新的呢？如果沒有頂讓給別人，哪老板就真的沒用心在經營了！食材品質還不錯，湯頭也是這個價位最好的，很有層次，不會像其他平價吃到飽火鍋一樣平淡無奇，香菜皮蛋鍋有種特殊的清香，外面吃不太到這種湯頭，自助吧的食材用料都不錯，但牛肉的品質稍差。\n用餐空間雖然比較狹小，但整體用餐過程算蠻舒服的，不會很吵鬧，大家都蠻小聲的講話，是個還不錯的用餐體驗。\n如果肉的品質再好一點會給到五顆星。第一次踏入朋友介紹的麻辣火鍋店，同時剛好網路也看到這家店，網上評價算是還不錯，立馬來體驗。用餐的環境空間不大，但乾淨舒服~ 這次點的是四川麻辣和皮蛋香菜，兩種湯頭都很棒~麻辣湯頭香氣重，有辣，麻偏少，煮完幾輪之後，湯依舊好喝，湯頭大勝多家知名吃到飽麻辣鍋，香菜皮蛋鍋很特別，香氣十足，皮蛋味少，害怕皮蛋的人可以嘗試放心享用，真的香甜。再來說到食材的部分，肉類品項少一些，但都新鮮好吃，火鍋料種類多樣特別，不像一般吃到飽都固定那幾樣，海鮮部分，鮮蚵和蛤蜊新鮮大顆肥美，菜類也非常多樣，有的外面比較少見的食材，另外還有吃到鯊魚醃，鮑魚片(超好吃)，牛肚(口感超好)，滿多是一般火鍋店看不到的食材，這類高CP值的好店，尤其在台北真的少見了，也難怪朋友會大力推薦，價格也相當親民~~找時間再來體驗蒙古麻辣~~食材豐富，湯頭也不錯，海鮮蠻新鮮的\n但不小心忘記在兩個小時內去拿冰淇淋，被老闆娘阻止了，有點小失望，下次要記得吃快一點..🥺', 4.3, 403, 1733, '1Oty8kh@gmail.com', '02 8509 7600', 2, '["星期一: 17:00 \\u2013 23:00","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 23:00","星期六: 11:30 \\u2013 15:00, 17:00 \\u2013 23:00","星期日: 11:30 \\u2013 15:00, 17:00 \\u2013 23:00"]', 'http://www.facebook.com/gfd85097600', '0.jpg', '[0.jpg]', '2021-06-16 07.37.51 下午', '2021-06-16 07.37.51 下午', 403, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 276, 1, '66196514', '8鍋臭臭鍋大直實踐店', '10462', '台北市', '中山區大直街36號', 25.08176740, 121.54628870, '火鍋', '火鍋 什麼都好，料多實在，冰淇淋竟然有荔枝口味，超好吃。唯二小缺點，涼麵有點硬，醬汁偏沒味道，然後竟然沒有王子麵無限吃！！其他都很棒。泰式酸辣鍋湯頭很好，店內魯肉飯吃到飽，有時有烏龍或冬粉可以續加八鍋的特點就是多樣的湯頭，很好吃，只是這家分店清潔優待加強有滷肉飯很有趣，鍋物口味選擇多火鍋口味眾多可選，肉燥飯、冰淇淋及果汁任你享用，價格亦非常親民，讚！', 4, 304, 1216, 'jWxyLregri@gmail.com', '02 2532 9395', 1, '["星期一: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/POTUSC/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.53 下午', '2021-06-16 07.37.53 下午', 304, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 277, 1, '27098268', '肉劇場經典Mini火鍋-大食代大直店', '10462', '台北市', '中山區樂群三路218號', 25.08231880, 121.55796160, '火鍋', '火鍋 爆炸難吃 同價位火鍋墊底 100元火鍋都比較好吃\n白飯都沒熟 沒有提供碗 主食晚到只能乾等\n飯上來又很失望\n玉米有奇怪的味道 很像龍眼乾的味道\n湯頭很多種 但樣樣都不精 用料都是先放入鍋內\n份量少 種類也少\n自助吧不錯 冰淇淋飲料都好\n店面是很寬敞 但衛生紙取用還要離開座位很不方便\n醬料區只有一區還很狹窄 醬料也難吃 沒什麼東西可以加\n只有傳統醬油 沙茶 烏醋 蔥末 洋蔥 辣椒  除了鹹味 也調不出其他味道了\n服務生不夠積極  簡單說沒什麼服務可言 就一般般\n評論怎麼4.5星我不知道 反正是不敢再來了五星差評\n服務態度不佳\n環境悶熱\n白開水有異味\n加點的肉盤有蟲，向服務生反應，得到的回應卻只是你坐哪裡，讓人感到心寒，連一聲不好意思都沒有，給我的感覺像是這是一件常態，這樣的服務態度讓人很不舒服\n請好好的重新訓練員工\n別讓少數素質差的員工影響到整間店家乃至於整棟建築物的其他餐廳。@大推CP值暴表\n東西品牌~量大，拿加盟店玓好物件，來推廣小店，湯頭好喝，海鮮新鮮，茶製雙淇淋，更是茶味獨特，真材實料。應該是近期最雷的一次用餐經驗，昨晚一家四口去用餐，我點了湯底比較貴一點點的泡菜湯底，完全沒有泡菜味，加點39元的麻辣鴨血，送上來只有薄薄兩片。。。\n整家店內，只有霜淇淋勉強可以。\n我點的羊肉泡菜鍋228元，如果上天願意再給我一次機會，我寧可去三媽吃兩鍋。\n雖然離家很近，走路10分鐘就可到達，但，絕對不會再去第二次。野宴集團還是把極野宴開回來吧！湯頭選擇多，點了蒙古跟老北京口味不錯，但是卡式爐用起來不理想，轉中火會一直熄滅。一開始店員把鍋擺得很靠近顧客，若沒有推遠水滾後會瞬間濺得滿身都是😥加點肉片跟海鮮，除了蝦子有點粉粉的其他新鮮度跟口味還可以。冰淇淋只有三個口味而且快挖空，鐵觀音茶味的霜淇淋還蠻特別且好吃。蒙古鍋有滿滿的枸杞但是幾乎都有黑點品質不佳，跟店員反應後態度良好，希望能改善，在場三位店員態度都不錯。', 4.5, 404, 1818, 'tLXpn4r6d@gmail.com', '02 8502 7755', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'http://www.yayan.com.tw/rou/store/7.html', '0.jpg', '[0.jpg]', '2021-06-16 07.37.54 下午', '2021-06-16 07.37.54 下午', 404, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 278, 1, '16188586', '12MINI經典即享鍋-內湖瑞光店(僅供外帶外送服務)', '114', '台北市', '內湖區瑞光路517號1樓', 25.07931960, 121.56950490, '火鍋', '火鍋 今天中午約11點45分訂購uber eat，訂購時顯示約需40分送達。\n約12點25分左右顯示"備餐完成"，也看到司機已經到店，預計12點40分送達。\n接下來一連更換了四位司機，送達時間也一直變更至1點15分。\n司機來電說店家根本還沒有備餐，詢問要司機等十分鐘，此時為12點45分左右。\n聯繫客服告知店家無備餐卻顯示已備餐完畢，客服告知因餐點過多請耐心等候!?沒做餐顯示備餐完畢，這已無關餐點了，是誠信的問題吧!?會讓多少消費者誤以為司機服務不佳而客訴。\n1點20分左右餐點來了:\n要蝦的沒蝦、點菜的都是火鍋料、少飯的少飯、加肉的沒加，甚至全部的容器外都沒有標籤貼紙顯示是什麼鍋，要客人用猜的????\n\n平價餐飲也要有一定的服務品質，真忙不過來麻煩關掉不要讓人點餐了~\n麻煩請多督導檢視，不要讓王品的招牌砸在這~不推薦訂餐點這家, 從ubereats點餐餐點竟然延遲60分鐘！每次點12mini 都會延遲, 但這是最誇張的一次\n11：35分點的餐, 本來預計12：10送到, 餐廳一直延預計送達時間到13：10（還在延）我們12：45打去詢問12mini竟然說他們還沒開始做！ 超級誇張\n點餐預計要1個半小時就出餐就應該要寫清楚, 我們就不會點了\n真的忙不過來就不要接單了！！！根本浪費客人跟外送員的時間UBER寫蔬菜鍋不加火鍋料，可是我吃到魚餃、魚蛋、米血糕、蛋方塊......是我認知有問題嗎，這些不是火鍋料???\n(有在重視回覆評論，我相信你們是不小心搞錯，幫加一顆星)服務態度很好，食材很新鮮，朋友推薦去吃的，比想像中好很多餒!超喜歡~~等一個餐可以等一個小時多，Ubereats客服不讓我取消，我又不能取消又要盲目地等，打電話過去店員也沒有任何歉意，只說：已經出發了。', 3.7, 323, 1195, 'Rlx5udJsg@gmail.com', '02 2656 1312', 1, '["星期一: 11:00 \\u2013 21:00","星期二: 11:00 \\u2013 21:00","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 11:00 \\u2013 21:00"]', 'https://m.facebook.com/12MINI/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.55 下午', '2021-06-16 07.37.55 下午', 323, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 279, 1, '08970402', '上官木桶鍋(內湖加盟店)', '114', '台北市', '內湖區內湖路一段324號', 25.08140690, 121.56984290, '火鍋', '火鍋 食材高級又新鮮海鮮和肉類都超級好吃這家是我的第一名口袋名單。吃遍台北市的涮涮鍋，還是愛上官木桶內湖店的可口美食和友善環境，大推！！！我點的是素食鍋（湯鍋裡有切片但香菇）\n起司牛奶  湯煮開時的味道\n聞起來非常香  但喝湯時  卻沒有那個味道\n覺得微微的失望\n\n菜籃因為發現拍到了小朋友的臉\n為了保護肖像權就不放了😂\n菜盤有1/4的高麗菜（都是大片的）\n一點大陸妹，金針菇、鴻禧菇、秀珍菇\n一兩朵雲耳、兩塊凍豆腐、一塊一版大豆腐\n各一塊的🎃和芋頭\n\n另一盤就如同照片\n醬料和飲料都要自己裝\n飲料提供蜂蜜水和柳橙汁\n飲料也有另外加錢的 看自己要不要\n\n結束時還有明日治的冰棒和豆酥朋可以選擇\n二則一\n\n員工都好不錯👍因宗教信仰關係不能吃牛肉\n所以選了豬肉跟一些海鮮\n送餐來的大眼妹妹跟我們夫妻說這種豬肉不用吃全熟，我們還是有點疑惑\n後來那個妹妹來告訴我們煮多久幾分熟\n老婆試著涮一下真的很軟嫩好吃，店員會很親切的介紹也吃到很剛好的熟度真的很好吃下次會在來', 4.7, 1461, 6867, 'Hb2HjU@gmail.com', '02 2797 1981', 3, '["星期一: 11:30 \\u2013 23:00","星期二: 11:30 \\u2013 23:00","星期三: 11:30 \\u2013 23:00","星期四: 11:30 \\u2013 23:00","星期五: 11:30 \\u2013 23:00","星期六: 11:00 \\u2013 23:00","星期日: 11:00 \\u2013 23:00"]', 'http://www.facebook.com/tel27971981/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.56 下午', '2021-06-16 07.37.56 下午', 1461, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 280, 1, '32301489', 'しゃぶしゃぶ温野菜日本涮涮鍋專門店 大直美麗華店', '104', '台北市', '中山區敬業三路20號5樓', 25.08330720, 121.55716480, '火鍋', '火鍋 超級大雷店，真的不要去你會後悔，從進去到出來店員至少出了五次包，剛開始進去介紹完，選好肉品價位後，完全沒說肉品怎麼加點，也沒給我們QR code，直到進場後大約20分鐘，剛開始上的肉品食用完後，詢問店員要怎麼作加點，才知道根本沒印QR code給我們，之後跟女友想說終於可以正常用餐了，結果發現掃描QR code進去出現的肉品選項根本不是我們選擇的價位該有的品項，又再拿去跟店員反應了一次，殊不知這次是品項正確，但是在我們加點後的20分鐘都沒有上肉來，細心的女友這時候才發現，單子上的桌號跟我們坐的桌號不一樣，我們剛點的肉可能都送到別桌了，又再次跟店員做了反應，才終於換上正確的QR code，用餐時間才90分鐘，前40分鐘就只吃到了剛開始上的幾份肉而已，你以為雷的只有這樣嗎，之後我們又發現了湯底該附的膠原晶球根本沒上，請問這個是火鍋吃完了再加下去讓他滾身體健康的是嗎==，問了第一個店員他說幫我們詢問看看，過了10分鐘完全沒有下文，又問了第二個店員才很快地幫我們附上來，然後到了最後20分鐘說要幫我們做最後加點的肉品，但是這些肉品直到我們離席都還沒有上來，我們已經等到用餐時間結束還沒上，對於你們的服務真的是感到沒救了，已經懶得再詢問服務人員了，真心不懂這種服務怎麼有臉收10%服務費==如果有員工或主管看到這則評論不服請直接打給我，當天有留實名制你們應該找的到，我是4/23號晚上6:36進場坐在36桌的客人，真的是第一次在吃到飽店是氣飽不是吃飽的，請加強你們的服務人員訓練素質好嗎，知道你們忙，但是光我這桌出了5次包，也太誇張了吧，不知道別桌怎麼樣啦，但是難得跟女友出門約會，結果選到一間爛到誇張的店我也是只能笑笑了呵呵。分享給大家知道，要去也拜託別去大直店。這家溫野菜很有趣，湯底一次提供三種讓你享用，滿足了選擇困難症的人\n\n蔬菜部分很滿意，有A菜、青江菜、高麗菜之類熱門蔬菜，主要是這些菜可以讓湯頭變得更香甜，所以會讓人想多吃一些\n\n肉品不會過薄或是質感不好，配上湯頭滾煮後吃起來口感很豐富，個人推薦牛肉可以多吃一些\n\n醬汁有很多選擇，鵝肝芝麻醬、麻辣甜味醬、日式醬油，要是愛吃辣也有生辣椒、生蒜泥、生薑泥，可以自己添加調配，最後加一點點蜂蜜到醬汁裡，整個吃起來辣中帶著蜂蜜香甜，啊嘶嘶嘶\n\n飯後可以來點霜旗霖，灑上巧克力米那個口感~爽外送超級雷，不管你點多少份單點，都只會拿到一份。\n這份好幾百上千塊的鍋，跟完全不加價兩百的鍋一樣…？蔬菜新鮮肉品質好，尤其是湯頭非常令人驚艷，湯頭煮滾時就可以聞到陣陣的香氣，調味適中不會有太重的人工味道，醬料方面選擇很多，互相搭配可以多吃好幾盤肉品！👍👍👍 聚餐的好選擇美麗華只要有消費就有3小時免費停車🅿️\n超過1000元就可以停4小時免費\n所以可以多多利用這好康\n對於習慣開車的人而言\n這是一個便利及利多\n\n5樓有各式各樣的餐廳\n在等待區域設置的休息區\n是非常寬廣座位都很舒服\n廁所又很方便\n\n我們今天選擇吃到飽\n肉質算還行\n可以有3種湯底\n喜歡喝湯的人\n可能要不斷請服務人員來加湯\n\n這次消費加點服務都很流暢\n沒有怠慢 ❤️👍\n\n炸物冷掉就不是那麼美味\n所以要看準上菜服務時機\n快來夾一下\n但我個人不愛吃炸物\n覺得口味沒有特推', 3.4, 411, 1397, 'eOB1uBnN@gmail.com', '02 8502 3324', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'http://www.onyasai.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.37.58 下午', '2021-06-16 07.37.58 下午', 411, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 281, 1, '60162017', '教父牛排 Danny''s Steak House', '104', '台北市', '中山區樂群三路58號', 25.08243630, 121.55292160, '牛排', '牛排 還沒摘星前在四維老店吃了兩次一直很難忘，相隔5年再次踏入教父牛排已經是摘星星的名店，價錢變貴以外似乎其他也回不去了，也許我不能用以前的價位相比，但除了烤大蒜還是我印象當中的美味外，其他不值一提，當天的老饕又鬆又老，毫無油脂，前菜和湯都一般般，其他同水準的餐廳有更好的表現，難道我是來牛排店享用大蒜的嗎?服務人員雖然客氣，但上菜的速度讓人覺得很有壓力，這一切都不像是星星餐廳該有的水準啊本日晚餐前往慶生，是近年唯一次完全無法接受的情況\n已事情了解用餐兩小時的限制，約17:35就抵達餐廳\n全程服務人員的速度，客觀說明如下:\n\n1.上Menu，尚未點餐，就直接上麵包\n2.牛排肉剛吃完，烤大蒜還沒吃，且還有一位仍在用主餐中，服務生立即過來詢問可否收空盤\n\n最無法接受的事情，其中一位仍在吃配菜的烤馬鈴薯時\n服務生已經把生日的蛋糕，蠟燭點火，送上桌!!!\n明明可以和甜點一起上桌，且距離兩小時尚有約40分鐘\n\n今日主因為慶生，所以在主要感受不佳下，僅給予一星\n牛排處理技術確實一流，餐點內容是否好吃，個人感受\n整體用餐非常的匆忙，服務確實也殷勤地補水\n但客人感受和服務動作之間的平衡，僅建議店家可在多拿捏之前覺得這一家餐廳是非常有品味又有品質的，但是這一次我們訂包廂沒想到幫一個壽星唱個生日歌都沒有，連水果也少了，經理的態度又非常的差,食物又一般....跟之前的素質差別太大了，下次絕對不會訂這家餐廳！這次生日聚餐是9位，硬要把一個人放到外面，只願意用八人包廂給8人，那家餐廳會這樣告知客人有一位坐外面，我們不是第一次使用8人包廂，這包廂我們坐過超過八人，貴餐廳用疫情因素拒絕我們坐9位，難道我們會因為多坐一位在包廂就感染病毒，坐8位就不會？然後在硬推12人包廂，主辦人為賓主盡歡，但4萬多的低消，只有主人跟貴公司知曉，被招待的朋友不好意思點太高價餐點，貴公司點餐人員也沒提醒還有一萬多的低消沒使用，是否該更換餐點或在點些其他餐點抵消費，最後才把剩餘一萬多元換酒帶走，硬推12人包廂給9人難道這不讓人覺得貴公司很糟糕嗎今晚四人聚餐\n點了美國頂級老饕牛排\n美國頂級菲力老饕牛排\n日本A5菲力牛排\n澳洲7+和牛紐約客牛排\n\n前菜是嫩煎鮪魚, 澳洲老虎明蝦,生削黑松露干貝, 季節蔬菜\n\n湯就是三種都點, 牛肝菌卡布其諾, 義式蔬菜清湯, 燻蕃茄濃湯\n\n因為不是第一次來, 驚豔程度沒有第一次來得明顯, 但不失對教父牛排的喜歡, 牛排推薦三分熟, 但完全感受不到腥味, 嫩酥恰到好處, 是可以怒吃15oz也不會膩的好吃\n\n牛肝菌濃湯是我愛上教父的起始, 每次來必點,\n\n這次可能訂位得比較早, 給了我們四人包廂, 不像之前跟隔壁的間隔都很近\n\n整體來說體驗真的很棒, 是吃牛排第一個會想到的店家人聚餐，上次在另外一家教父牛排印象不錯，所以再次選擇教父牛排只是不同分店。\n但這家的服務態度非常差，就像機器人一樣唸著自己的台詞，完全沒有人可以聽清楚他們的介紹，客氣的請服務生是否可以再說一次聽不清楚，旁邊一個男生打斷用很兇的口氣再說一次，然後問那這樣有聽清楚嗎？\n這次對教父十分失望，不會再光顧了。', 4.1, 2988, 12251, 'zNxW3AWBPb@gmail.com', '02 8501 1838', 4, '["星期一: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期二: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期三: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期四: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期五: 12:00 \\u2013 15:00, 18:00 \\u2013 22:00","星期六: 11:30 \\u2013 15:30, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 15:30, 17:30 \\u2013 22:00"]', 'https://zh-tw.facebook.com/DannysSteakhouse/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.00 下午', '2021-06-16 07.38.00 下午', 2988, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 282, 1, '97581776', 'N°168 Prime Steakhouse 牛排館', '10491', '台北市', '中山區敬業四路168號', 25.08416960, 121.55902810, '牛排', '牛排 我們選了 N°168牛排館 肋眼牛排雙人套餐一份。\n\n麵包搭配的海苔醬很有特色，我特別喜歡外酥內軟的麵包。\n前菜牡蠣擺盤精製，像是貝殼日式便當，牡蠣下面的麵調的很好吃，吃起來很溫和。\n洋蔥湯非常專業，沒有雜味\n主菜牛排選三分熟，嫩又多汁，好吃。\n點心可愛，但冰淇淋質地不佳，有點冰晶，蛋糕好吃。尤其生日蛋糕挺漂亮的，字很好看，裡面的麻糬，和外面的水果搭配很好，吃起來不會太膩，但上面的珍珠有點不行，太老放太久。\n飲料就是一般的茶，收尾。\n\n我其實很在意吃飯的安靜和空氣味道，廚房雖然是開放式，但沒有油煙，座位區不會有異味，而且很安靜，不過途中電梯有些客人有點大聲聊天有點煩，其他部分我覺得算是非常安靜的設計。\n\n服務生不是固定的人，不過上菜速度和補水速度都很快，管理和員工訓練應該做的很好。\n\n紅酒有一個低溫櫃保存，會在門口的地方讓服務生可以快速裝酒，左手邊有個紅酒杯，應該是用來調整容量的。算是比較科技的保存方式，一杯一杯點的應該是以這方式提供的。\n\n環境美觀度很棒，尤其是包廂內對稱度很高，但桌子紋路有點沒對稱，有點可惜。\n\n戶外基本上是四樓，游泳池和戶外的花園也在這邊，但要去游泳池要從五樓走樓梯下去，應該是有刻意設計的。不過我覺得這樣不太方便。不過和餐廳沒關，提一下。上次吃A Cut 這次吃鄧有癸第二間店168牛排。\n\n乾式熟成21年的肋眼真的非常強啊！表面微焦脆內裡十分軟嫩，風味非常完整的濃縮在裡面。\n使用橡木熏烤，一入口就有種在美國西部吃著野味的畫面掠過，驚人的香氣一入口就大膽的襲來，但肉質卻非常風雅。旁邊附了三種鹽、三種辛醬可以搭配。\n\n上次去A Cut點的是和牛，不同種類無法客觀比較，只是這次乾式熟成肋眼風味之豐富實在是令我印象極深刻。\n前菜干貝很好吃，湯品洋蔥湯中規中矩（A Cut的牛肉湯深得我心 好想再去喝🤣），沙拉的西班牙火腿也不錯。\n比A cut優異的部分是甜點：168馬戲團。\n個人超愛這種每個一點點但很精緻的東西。\n藍莓優格冰淇淋：優格味不會過酸搶過藍莓味，很推！\n中間的檸檬造型：是檸檬白巧克力外皮內裹香草慕斯，最裡面還有一坨檸檬薄荷酸甜醬，層次很豐富，個人很喜歡。\n烤布蕾小巧，味道也有一定水準。\n慶生送的黑櫻桃巧克力慕斯蛋糕也是很不錯。\n茶類的蜜桃茶是我喜歡的，甜甜蜜桃香又不澀，很好入口，適合今晚自以為少女der我，莓果茶則一般般。\n甜點的表現都不會過酸或過甜，挺符合個人胃口的。\n加點了一隻龍蝦，上次吃龍蝦是在高雄的漢來牛排館，個人覺得比漢來好多了。\n\n如果跟A Cut比較的話：\n用餐環境：A Cut > 168\n麵包： A Cut > 168 ( 不過168 這次海苔奶油沾醬很好吃）\n前菜： A Cut > 168 好吃程度差不多，但A cut更有創意一些。\n湯： A Cut > 168 ，一般湯品好喝程度差不多，但A Cut的和牛湯餃太強了！\n沙拉：168 > A Cut 湯或沙拉只能二擇一，但A cut選項較少。\n牛排：不同牛排就不比較了。但168乾式熟成絕對值得一吃！\nSide：168 > A Cut, 明太子皎白筍個人挺合的。其他都類似類似。\n甜點：168> A Cut, 但要看品項，168馬戲團可以吃到不同風味這種方式我蠻喜歡的。不過抹茶草莓慕斯普普。\n\n飲料差不多。\n\n如果在兩間猶豫的朋友，個人建議是：小朋友才做選擇！台北牛排-01 prime168\n受最近的疫情影響，很多台灣的飯店都推出了特別的折扣，其中也包含了大直的維多麗亞酒店。\n維多麗亞在上週開始推出了5800的米其林一波二食特惠活動（prime 168牛排館+一個晚上的住宿+早餐），等於是用平常來prime 168用餐的價格就可以多獲得一個晚上的住宿，實在是相當的划算！而這個相當優惠的活動，到活動結束的每個禮拜五六晚上時段都已經客滿了，剩下週日到週四的時段還有位置。\n如果對於prime168有興趣的朋友可以來參考看看，但是對於房間和早餐，我想就不用報太大的期待了。\n#維多麗亞酒店\n#prime168\n#台北住宿\n#台北ホテル  @ 維多麗亞酒店Grand Victoria Hotel初訪\n比教父好吃跟Acut差不多，前菜每道都很好吃，湯沙拉就感覺稍微弱一點，牛排真的好吃，外酥內嫩不帶血水👍\n但是服務遠超過其他間，其他餐廳也很專業但是服務有距離，像是因為得這樣做所以做，但是這裡服務感覺不一樣，我媽老花字看不清楚他很有耐心的一道道介紹，告訴我們怎麼點不會太多，之前去其他地方感覺就只想一直推銷高單價的餐點，用餐過程也是會跟我們閒聊關心我們的餐點牛排的熟度等等，很喜歡這種專業又親近的服務，最後也給了我們他的名片，謝謝Lester\n期待再訪真的吃過就回不去了！！！\n麵包外皮薄脆內軟\n蟹肉餅蟹味滿溢\n卡布奇諾濃湯菌菇味好濃\n紐約克三分帶血有嚼勁但又咬的斷不生\n老饕牛排三分看起來生但完全沒血水焦酥表層越嚼越香\n爐烤蔬菜番茄櫛瓜好甜馬鈴薯普通\n抹茶草莓慕斯\n太妃糖慕斯佐冰淇淋\n冰紅茶和熱拿鐵\n吃得超級滿足', 4.5, 1511, 6800, 'MVFJo21e5k@gmail.com', '02 6602 5678', 4, '["星期一: 12:00 \\u2013 15:00, 18:00 \\u2013 22:30","星期二: 12:00 \\u2013 15:00, 18:00 \\u2013 22:30","星期三: 12:00 \\u2013 15:00, 18:00 \\u2013 22:30","星期四: 12:00 \\u2013 15:00, 18:00 \\u2013 22:30","星期五: 12:00 \\u2013 15:00, 18:00 \\u2013 22:30","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30"]', 'http://www.grandvictoria.com.tw/dining_168.php', '0.jpg', '[0.jpg]', '2021-06-16 07.38.03 下午', '2021-06-16 07.38.03 下午', 1511, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 283, 1, '66871322', 'Ruth''s Chris Steak House (Dazhi) 茹絲葵經典牛排館 (大直)', '104', '台北市', '號 3樓, No. 301樂群三路中山區', 25.08302400, 121.55948620, '牛排', '牛排 5/15晚上19:30吃飯\n今天吃飯是由一位DANNY先生介紹點餐及為我們服務\n餐前法棍麵包🥖真的外酥內軟很好吃！我還續了一次沾著龍蝦湯吃超級美味\n龍蝦湯很濃郁很好喝，對清淡口味的人來說或許會有一點點偏鹹，但我覺得以這道龍蝦湯來說鹹度是合適的\n馬茲瑞拉黑柿蕃茄沙拉：蕃茄很Q很好吃，最重要的是上面的羅勒！超級香～超級配這道沙拉！\n餐前有一杯甜白酒超想買一瓶回家喝的\n肋眼牛排12盎司一上桌就很香超級香！很好吃！\n甜點換成香蕉派～～～真的超級好吃甜而不膩，前面吃這麼多後面還是直接吃完一個4吋香蕉派！\n\n因為有吃過168極黑和牛牛排和這個是完全不同呈現方式，本來覺得168是吃過最好吃的牛排，但本身茹絲葵的肋眼牛排就很香也很好吃又因為我們加點的沙拉、龍蝦湯、蟹肉糕、沙拉都很好吃，甜點也大贏～\n讓我覺得這次吃的很滿意\n\n也許是DANNY的服務大大的加分！\n讓我覺得吃得很開心🥳會一直想來吃的這種\n滿分10分會給9.5分\n(但0.5分不知道扣在哪，未來也許有更加分的機會)\n\n後來還有一位女服務生也很棒！餐廳位置很方便\n而且出入也都不錯\n餐點上也都不錯吃\n只是覺得台北民生店上的口感還是比較略勝一點，例如蝦子的醬這邊比較清淡，口感上有一部分軟了，民生店則是完整Q彈\n烤麵包的皮有一部分太硬又乾\n\n但是在於服務上則是比較好的，會解說而且會告知因為那一個比較油，那一個清淡，會建議先上哪一些餐點，這點真的不錯要是是一般餐廳我認為這裡的服務已經很好了，但我昨日晚上和家人聚餐的經驗讓我只能給出尚可的評價。先是櫃檯帶位，我等了幾分鐘才有服務生出來。等到入座點餐完畢後上菜有餐點送錯不清楚是哪位的餐點等問題出現。服務生在需要的時候也沒有即時出現在旁邊不清楚是人力不足還是怎麼樣。餐點很好吃但看來在人力的部分還需要加強，這樣的價位我覺得還是不值得來的。牛排真的很好吃，這裡點了肋眼跟菲力，強烈建議三分熟，端盛的盤子有高溫加熱，肉會像在鐵板燒持續加熱，散熱又比鐵板的速度慢，如果覺得過生也可以拿瓷盤去燙熟，即使是三分切割時也不太會有血水滲出與腥味。菲力相當的軟嫩，內部像是在吃鮪魚生魚片一樣軟嫩，清爽不油膩，適合女性的口味與份量；肋眼能夠吃得到三種肉的口感，瘦的肉感、油肥相疊、油脂豐富的口感都能體驗到，不過對女性來說份量比較多，如果食量不大，建議一人點套餐兩人一起分享配菜即可。湯品龍蝦湯相當濃厚，建議搭配麵包一起食用避免過鹹，且主菜配菜也避開龍蝦類比較好，免風味被湯先給麻痺。這次慶祝週年 Clare 的服務非常好 讓人自在 沒有過度制式化的服務話術 服務上充滿溫度 請她幫忙拍照也很熱情 謝謝她🥳 有一位年紀較大的主管也有在用餐中間向前關心我們的用餐狀況 謝謝你們 辛苦了 🥳', 4.3, 1000, 4300, 'gMxsEsYpoh@gmail.com', '02 8501 5900', 4, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30"]', 'https://www.ruthschris.com/restaurant-locations/daizhi-taipei?utm_source=google&utm_medium=organic&utm_campaign=local-business-listing', '0.jpg', '[0.jpg]', '2021-06-16 07.38.08 下午', '2021-06-16 07.38.08 下午', 1000, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 284, 1, '81501894', '佳佳牛排館', '10491', '台北市', '中山區大直街41號', 25.08258600, 121.54646500, '牛排', '牛排 疫情後再訪：\n目前提供外帶服務，雖然無法享用自助吧，但價格也降價回饋，並提供隨餐炒飯或麵（牛排醬可以加少一定，炒飯好好吃）\n外帶回家都已分裝好，再加購一碗經典鹹粥👌\n價格實惠且份量充足～\n重點是味道還是一樣讚！\n\n----\n鹹粥超好吃！小菜也任你挑選～嫩肩牛排熟度剛好，有回到早期台式牛排館的感覺，還蠻推薦的而且價格蠻可以👍🏼適合帶小朋友的餐廳今天點了一份豬排還有高麗菜，結果等我回家後發現高麗菜沒有，md，如果很多人就算了，就我一個人你們也可以給錯，反正就這次，下次不會再去了。從小吃到大，價錢是越來越貴了，\n炒飯依舊好吃，小菜雖少，但味道還行\n\n感覺沙拉吧比牛排好請標註仙草凍旁邊配和風醬的是幾個意思！\n（地下室空氣品質不佳）\n服務人員雖認真～但一直收正在用餐的桌面⋯⋯\n感覺吃得很不舒服', 3.8, 1432, 5442, 'y31Yai@gmail.com', '02 2533 0163', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 休息","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.09 下午', '2021-06-16 07.38.09 下午', 1432, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 285, 1, '93574131', '雅室牛排 大直旗艦店', '104', '台北市', '中山區樂群三路303號2樓', 25.08279590, 121.56032870, '牛排', '牛排 外帶了防疫套餐\n經典雅室牛排8oz\n蒸烤加拿大活龍蝦(半尾)\n雖然不能在1樓取餐有點不方便\n不過餐點品質真的很好\n龍蝦新鮮Q彈\n牛排回家路程較遠，所以比較熟了一點\n建議路程較遠，或是回家還想加熱的點三分熟外帶回家最剛好\n店員很親切，在現場付款即可生蠔新鮮好吃飽滿，酥皮濃湯好喝、牛排表現不差。\n用餐環境非常舒適，服務生各個都很親切，\n\n位於英迪格酒店內部，餐廳營造高級氛圍，中間餐桌的桌距有一點點近，床邊的話就沒這個問題。\n\n麵包可以一直續，屬於鬆軟的麵包口感，個人比較喜歡紮實的故沒有追加。\n\n湯品部分選了酥皮濃湯，好像是他們的招牌菜。這個酥皮和一般在夜市喝到的差異非常大！！非常酥，超級酥的酥皮，服務人員還會幫忙把酥皮取下放旁邊。\n\n烤田螺的部分因為有焗烤，反而吃不太到身材本身的鮮味，都是起司的味道。\n\n再來生蠔部分，完全沒有腥味！ 而且很飽滿！一個人吃個三顆都不會感到噁心，取而代之是鮮甜口感存在口腔之中。激推！！\n\n牛排部分表現中規中距，老饕牛排熟度點五分熟但可以再酥一點點會是更好。\n\n甜點部分就比較沒有驚艷感，水準中上。\n\n壽星還有一塊小蛋糕，其實吃起來比套餐的甜點還要好吃。\n\n這間店絕對可以再訪，嘗試更多不同菜色。商業午餐不推薦點經典牛排五分熟筋多咬不斷戰斧豬排肥肉多沒味道，說會反應也沒下文！號稱牛排老店來內湖開分店慕名而來大失所望，不會再來！餐廳佈置舒適寬敞，剛好遇到全台輪區停電，只能抹黑吃牛排，甜點可口好吃，最後還是有八五折，很特別的體驗...牛排不錯，但其他部分只能說差強人意,甜點則是完全不行\n以四個人快九千的消費，還有別的更好的選擇', 4.5, 346, 1557, 'ellPNeox@gmail.com', '02 8502 9960', 4, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30"]', 'https://steakinn-dazhi.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-16 07.38.09 下午', '2021-06-16 07.38.09 下午', 346, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 286, 1, '16675295', '歡樂購特大牛排 內湖店', '114', '台北市', '內湖區內湖路一段411巷13號', 25.08230710, 121.57124670, '牛排', '牛排 150元的台式牛排價位跟孫東寶差不多，但這裡的牛排味道極普通（麵條味道很不ok），醬的味道也酸酸的，飲料則是過甜，至於人嵾湯跟玉米濃湯就普通，冰淇淋是常見的客惟您，用餐的桌腳可能有壞，切牛排時桌子晃到不行（腳要踩住），比較特別的缺點是刀叉也是用過最難用的（很難拿）...\n\n不過飲料、湯都各有兩種，至少有多樣性的優點，唯一好吃的是大蒜麵包，烤的還不錯，算是彌補一點回來。有1樓和地下室兩層，尖峰時刻基本滿桌，地下室稍悶不通風\n自助飲料為冬瓜茶、玉米濃湯好喝、1樓也有提供自助冰淇淋，吃到飽沒問題\n商業午餐$130，cp值頗高，若午餐想吃點肉食可以試試一般的牛排店只有玉米濃湯，但這裡還有人參雞湯。\n▶️注意事項\n平日午餐有130元商業午餐(紐西蘭牛排)\n假日或晚餐則為150元(紐西蘭牛排)CP值算挺高的，外帶牛排+飲料or湯+土司=$100，味道還算不錯，現在買個便當也要80-100塊，對外食族來說，可以輪著換著吃。服務態度不佳，點餐順序無章，自己主動上樓面前點餐還回話「她會依順序點」，還兇要我們回位子結果晚到的都快吃完走人，餐才來 急待改善', 3.7, 251, 929, '6OIjJFdyMc@gmail.com', '02 2658 5038', 2, '["星期一: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期二: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期三: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期四: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期五: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期六: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.10 下午', '2021-06-16 07.38.10 下午', 251, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 287, 1, '82018676', '1942鐵板牛排', '104', '台北市', '中山區樂群三路218號', 25.08243380, 121.55759600, '牛排', '牛排 ', 0, 0, 0, 'xmZ1Crmf0b@gmail.com', '02-26854859', 4, '["星期一: 09:00 \\u2013 23:00","星期二: 09:00 \\u2013 23:00","星期三: 09:00 \\u2013 23:00","星期四: 09:00 \\u2013 23:00","星期五: 09:00 \\u2013 23:00","星期六: 09:00 \\u2013 23:00","星期日: 09:00 \\u2013 23:00"]', 'https://www.facebook.com/1942restaurant', '0.jpg', '[0.jpg]', '2021-06-16 07.38.11 下午', '2021-06-16 07.38.11 下午', 0, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 288, 1, '75898204', 'GMT義大利餐廳', '10462', '台北市', '號4樓, No. 55樂群二路中山區', 25.08054840, 121.55379390, '牛排', '牛排 本豬滿意。卡片優惠不能用行動支付的方式真的很過時\n出示信用卡相關卡號證明也採很硬\n真的很不方便\n\n桌邊服務人員態度很好\n\n食物方面：\n義大利麵都比較重口味偏油偏鹹\n（可能是各人口味問題）\n\n點了培根蛋黃義大利麵但是裡面沒有培根\n（有可能是打成醬？）兩年前從新聞報導中得知，美福大飯店聘請專業的義大利籍廚師坐鎮，從那時就一直想前往用餐。今日享用過後，果然不失所望，端上桌的料理絕對是最道地的義式佳餚。\n尤其是牛肉的烹調，選用Wylarah澳洲和牛，外層酥脆、中心牛肉維持著非常多汁柔軟的三分熟。\n絕對推薦大家來享用此次的聖誕特餐-「義享聖誕和牛宴」。除了主菜之外，每一道餐點都有主廚滿滿的獨特巧思與對料理的用心，值得推薦享受的好餐廳。👍🏻假日跟家人過來吃中餐，因為都吃不多，就以單點的方式點了一道前菜，蔬菜湯、海鮮湯、5oz 菲力、烤海鮮盤。餐廳生意滿好的，從入座到上菜，已經來了不少桌客人。麵包一上來就是熱的，外皮酥脆，吃起來挺舒服。前菜是菲力薄片＋帕瑪森起士＋芝麻葉，油 醋與食材搭配的不錯；蔬菜湯我認為較無特別的地方；嫩煎菲力牛排有搭配鴨肝，吃起來確實是嫩，但好像沒有煎過的感覺，整塊排是軟嫩的，我自己並不是很喜歡，且牛排刀非常不利，不容易切；碳烤海鮮我自己也是不太推薦，一上桌溫度就不太熱了，味道感覺不出是"碳" 烤海鮮，蝦子、干貝吃起來乾乾的，有點像是單純加熱加鹽的一盤海鮮，除了食材本身沒甚麼特別之處。\n整體來說，吃下來並不算滿意，價格也不低。喜歡歐洲料理的人可以試試。餐廳佈置及氛圍非常高雅,甜點蛋糕外表雖樸實但吃過後會讓人驚艷,餐廳服務人員也很專業,尤其是經理Alan服務到位又細心,整體推推!!!', 4.4, 495, 2178, 'J5MwebRn@gmail.com', '02 7722 3396', 4, '["星期一: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期二: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期三: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期四: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期五: 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期六: 11:00 \\u2013 14:30, 18:00 \\u2013 22:00","星期日: 11:00 \\u2013 14:30, 18:00 \\u2013 22:00"]', 'http://www.grandmayfull.com/dining/GMT', '0.jpg', '[0.jpg]', '2021-06-16 07.38.12 下午', '2021-06-16 07.38.12 下午', 495, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 289, 1, '17204712', 'Garden Kitchen', '10491', '台北市', '中山區樂群二路199號', 25.08045150, 121.55894350, '牛排', '牛排 位於台北大直萬豪酒店一樓，吃到飽餐廳。 地理位置還算方便。 環境優雅不在話下。 服務人員 彬彬有禮， 態度優良。\n\n本次所點的牛排、海鮮， 覺得煎煮的很棒。 相對，對牛排印像比較深刻。 所挑選的白酒跟紅酒 也都很好。 紅酒、白酒之間，我比較喜歡於他的白酒。\n\n可挑選的飯後甜點種類，比較少了一點，有點可惜。 來這裡消費，整體感覺算是有五星級飯店的水準。 不過也許是因為不餓的關係，也還沒有到驚豔。5分熟的老饕牛排❤️❤️❤️\n雖沒吃出濕式熟成的差別\n外脆內嫩的表現非常滿足\n\n泡泡檸檬🍋塔❤️\n綴以花瓣與青檸皮\n上層是鬆軟酸香的青檸蛋白\n下層是酸甜平衡的滑細口感\n\n招牌牛肉麵\n牛肉我比較無感\n麵條是不愛粗麵的我也喜歡的\n記得要辣椒(油潑辣子)❗️\n真的是非常非常非常香而不辣\n\n托福才有隱藏版的蜜瓜火腿\n還有加了小鮑魚的綜合海鮮沙拉\n\n而最珍貴的\n永遠無法言述...餐廳的裝潢很高雅，桌與桌之間的空間很寬敞，讓人不會有壓迫感。\n\n半自助的菜色種類雖然不多，但味道都很好，尤其甜點區的馬卡龍更是超棒，一定要試試看。\n\n至於主餐的肋眼，油花實在太多了，可惜。\n\n整體下來的氣氛很好，我很樂意再訪。由於是母親節前往，單價較高（約2300元）有附主餐（菲力+龍蝦），整體而言不錯，但其實若可以單吃沙拉吧就夠精彩了。\n母親節很適合親子，有準備氣球以及鮮花，蠻多用心的小驚喜。\n沙拉吧的蔬果區很優秀，壽司部分建議不用拿（鰻魚類不是真的鰻魚），咖啡頗苦XD算是半自助式的Buffet, 每個人都可以點選主餐，然後搭配Buffet. 雖然Buffet 區的面積不大，但是菜色都很精緻，很多還蠻特別的餐點，擺盤也很乾淨整齊。\n用餐氣氛很好，服務人員態度也都很親切，店內有很多擺設都非常漂亮，可惜沒有一一拍照。有機會的話還會想再去吃吃看。', 4.2, 597, 2507, 'VaJJg4z@gmail.com', '02 2175 7957', 3, '["星期一: 06:30 \\u2013 10:00, 12:00 \\u2013 14:30, 18:00 \\u2013 22:00","星期二: 06:30 \\u2013 10:00, 12:00 \\u2013 14:30, 18:00 \\u2013 22:00","星期三: 06:30 \\u2013 10:00, 12:00 \\u2013 14:30, 18:00 \\u2013 22:00","星期四: 06:30 \\u2013 10:00, 12:00 \\u2013 14:30, 18:00 \\u2013 22:00","星期五: 06:30 \\u2013 10:00, 12:00 \\u2013 14:30, 18:00 \\u2013 00:00","星期六: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 22:00","星期日: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 22:00"]', 'http://www.taipeimarriott.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.13 下午', '2021-06-16 07.38.13 下午', 597, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 290, 1, '96067105', '墨魚廚房 PASTACO&CURRYTACO｜台北內湖旗艦店 ｜手作咖哩飯 牛排 炸物專賣 人和食緣', '114', '台北市', '內湖區環山路一段90號', 25.08654040, 121.56744890, '牛排', '牛排 爬金面山的路上看到店家的活動布條在山上，\n覺得很過分，活動完請把自己的垃圾帶走！\n拆下幾個在下山途中還給店家，\n並告知他們不應該把垃圾留在山上，\n店家回應：喔我們留在山上當成宣傳？？\n有事嗎？\n拜託想打廣告走正常方式，\n這種無言店家，再怎麼好吃絕對抵制到底\n\n————————————————————\n謝謝店家回應，但老實說真的看不懂您在回應什麼\nQR CODE 我們也是有掃的 直接導到貴店臉書而已\n完全沒看到什麼拿下來換獎品\n還有下午上山，跟頭燈什麼有關係嗎？\n你們的布條會自己發光照明嗎\n有在爬山有在淨灘卻放了店家宣傳的布條在山上\n我也是不知道該說什麼\n只能說你們做了最壞的宣傳\n\n—————————————————————\n2/21最後回應：\n有爬大山 有撿垃圾 又代表什麼呢\n代表你撿了別人製造的垃圾後 留下自己的嗎？\n從頭到尾你有沒有撿垃圾我根本不在意，難道我那天幫別人撿了一袋也要跟你說嗎？\n再來，\n先別說登山布條的用意是在於指路，金面山的標示多到清楚的不得了，請問貴餐廳綁在涼亭，歇息處的用意...？要指去哪裡？\n其三，\n我們並不是沒有當面問喔，留在山上當宣傳的用意也是我們「當面」詢問貴店服務人員自己說的，拆下來之前我們也怕是不是隔天有活動要做指引還特地掃了QRCODE看，只看到臉書，而且上面並無相關活動訊息，布條看起來也有一段時間了，對我們而言看起來並不像前日才綁的。\n噢對了，臉書活動內容後來我找到了，也並不符合您說的2/19才綁的，怕是您貴人多忘事，還是只是覺得丟臉想爭一口氣？（附上貴店的臉書喚醒您的記憶，活動已經過了快一個月了唷☺️）\n\n最後，\n請不要一直拿你們有在撿垃圾有在爬山也住在附近來做道德綁架，不留下任何不屬於山林的東西是身為一個品德良好的登山客最基本的原則。第一次經過，因接近打烊沒吃到，但店員依然熱心介紹，讓我們隔週特地來用餐，果然沒讓人失望！從墨魚的鮮甜到布丁的香濃，處處能感受到用心，很值得來品嚐喔！爬完金面山，順便吃個中餐。擺盤很漂亮，東西也不錯吃，咖哩有辣味可以選擇，份量也足夠，還有飲料免費提供喝的飽。剛開幕時餐點表現不錯，時隔幾個月餐點水平顯著下降，付費方式跟不上時代，到現在依然只收現金，沒有發票，也無配合外送平台，只有外帶，記得剛去也是告知馬上會有行動支付，都過那麼久還是同一套說法，服務人員應該也是不同人了，先前的男生反應快多了，可能碰巧遇到新進人員，詢問餐點不知所云，還有不知是顧客不多沒開冷氣還是空調壞了，空氣悶悶的，可惜了先前的好體驗...墨魚辣咖哩$269(2021年),咖哩很濃郁，好吃。墨魚可以，但沒有期待的軟嫩。令我驚豔的是那些小配菜都是用好食材，玉米是水果玉米嗎，地瓜是57號的。味噌湯很用心，裡面還有鮭魚。豆腐也是有挑過的，淋的芝麻醬恰恰好，不會搶了豆腐的味道。布丁量剛好又非常好吃。整個餐是用心得不得了，且是每個細節。服務的小姐甜美到不行。', 4.8, 282, 1354, 'qHL3Ta@gmail.com', '02 2627 4888', 4, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00","星期二: 休息","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 20:00"]', 'http://www.rceating.com/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.14 下午', '2021-06-16 07.38.14 下午', 282, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 291, 1, '14743021', 'Pepper Lunch 胡椒廚房 美麗華店', '10491', '台北市', '中山區敬業三路20號 號B1', 25.08330720, 121.55716480, '牛排', '牛排 好吃,但價格偏高點了招牌胡椒牛排，嘴尖的孩子說，這牛排好，沒有多餘的調味，不似一般夜市勾芡過多的黑胡椒或是蘑菇醬，牛肉紮實，算是可以享受的。\n不過.....孩子說：份量稍少一些，可惜了好吃的料理，意猶未盡，多給點料更有飽足感。但就風味，及格了還可以加10分（給親切的櫃檯咩咩）。東西好吃，牛排品質不錯，之前大店面的時候就挺常吃的，調味東西好吃，算是美麗華美食街裡面我覺得CP值前幾高的店舖12分鐘完食 超級好吃\n單是胡椒跟鹽的調味就很棒了！\n下次有來這邊 會再光顧！\n可惜減肥中不能吃胡椒飯的那個QAQ從店面比較大的時候就會來光顧，招牌鐵板牛肉飯必加蛋！', 4.4, 549, 2416, 'eb9aa2dVO@gmail.com', '02 8501 5586', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.facebook.com/pepper.lunch.taiwan', '0.jpg', '[0.jpg]', '2021-06-16 07.38.15 下午', '2021-06-16 07.38.15 下午', 549, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 292, 1, '21583625', '豐FOOD．海陸百匯', '10491', '台北市', '中山區植福路8號1樓', 25.08368700, 121.55491800, '吃到飽', '吃到飽 第二次來訪，\n是因為弟弟生日，\n吵著要來吃，\n四人同行一人免費優惠。\n2,200元左右一人550。\n\n一開始大門前要先劃位，\n劃位左邊櫃台結帳，\n在往左邊是排隊進場及廁所，\n進場流程很順利，\n左邊依序是海鮮=生魚片+壽司區，湯品+素食+披薩區，港式料理+沙拉區\n右邊依序是甜點區，冰品區，水果區，飲料區，\n中間走到底依序是現煮區，熟食區，炸物區，咖啡泡茶區，\n拿餐食記的帶口罩，拿餐食記的帶口罩，\n拿餐食記的帶口罩，要記得要不然服務員會一直念你，\n\n一樣服務大於美食，\n這次好吃的沒很多，\n最好吃是鴨肉手捲，\n只有這裡大排長龍\n其他餐點就普普了，\n但還是會吃得飽飽，\n服務很好，收盤子妹妹都會很客氣的說:幫你收盤子可以嗎?謝謝!\n很有禮貌，也很客氣，收盤速度快，GOOD!\n\n這次的現煮區很慢，從開始沒多久點，\n都已經吃到甜點了，快吃飽了，\n點的才糖醋肉才來，吃不下了才來!\n補餐速度慢，有些餐點只看到一次就看不到了!\n\n優點:服務很好，東西很多。\n缺點:出餐很慢，補餐速度慢，沒有好吃的東西。\n\n結論:\n這次沒有第一次好吃，食物品質好像下降，現煮區動作也變慢了。\n服務沒問題，補餐很慢，有優惠的價格也不錯。很可惜退步了!\n\n一星兩分我給六分\n評分:6/10分\n\n服務大於美食的餐廳\n1.訂位有先告知有9X歲阿嬤當天安排取餐區旁邊~VERY GOOD!\n2.餐盤收得很快剛吃完馬上就收走了不會留太久~VERY GOOD!\n3.找不到東西詢問店員馬上帶妳去，而不是用說的~VERY GOOD!\n4.停車場就在B1很方便而且免費~VERY GOOD!\n\n接下來美食部分因為去時主打螃蟹祭~\n所以很多海鮮~不能吃海鮮的要想好!\n小弟只能看不能吃 /_\\~NO GOOD!!!\n\n熟食區前三名\n糖醋排骨-好吃我吃5盤\n紅燒牛肉麵-湯好喝\n墨西哥牛肉捲-第一次吃這種餐點~還不錯吃\n\n甜食區前三名\n烤布雷-很好吃我吃7杯\n哈根達斯-很多口味\n雞蛋糕-可以加煉乳或蜂蜜\n本想把哈根達斯各口味吃一遍，但烤布雷不小心吃7杯...好撐\n\n結論:\n東西很多，價格小貴，\n餐點好吃，點心好吃，\n服務很好CP中上，\n一星兩分，我給八分。\n評分:8/10分跟朋友一起來住家附近吃慶生晚餐，場地蠻大的，我們坐在靠近兒童遊戲區旁的小圓桌，去取餐來回都有點距離....然後像蝦子那些限量的就只能在現場排隊,所以我們四人都只有2人有得吃,另2人等下輪......如果帶2位行動不便的長輩來說...真的很麻煩.....\n用餐時間一直空桌因為一直跑去排隊,像燕子爸媽......好好笑\n最誇張的是, 生魚片區大家都在排隊等著廚師切生魚片........(這不是應該在下午備料時切嗎?? 可能生意太好,所以要一直切....) 然後大家看著他.....拿出很長的刀子.....什麼!!要先磨刀子, 我有看錯嗎,  五星級餐廳在營業時間....磨生魚片刀...??\n磨好了然後洗刀子....我以為我在吃路邊攤...(路邊攤也沒看過廚師在磨刀啊)\n終於要切生魚片了.........哇,這切生魚片的速度真的超級超級慢......老闆可能有交待一定要拖慢大家的速度,因為今天生魚片只能切一個定量,切完就不能切了, 然後終於拿到了幾片生魚片....小碎肉....這個生魚片不能說是生魚片\n應該是煮湯用的小碎肉吧,每片都超級小條...是給人吃的還是小嬰兒吃的呢............\n然後到了熟食區.....廚師就像很多人留言的.......好像在難民營排隊等分糧,要看那個廚師臉色...@@\n真的只有鴨肉的廚師親切很多,還有我們坐位區的服務生很親切....@@\n這次用餐我真的大開眼戒了 ,尤其是看到廚師在客人面前磨刀子............我還是去吃套餐式的餐廳好了［2020/08/09下午茶體驗］\n加分：有“確實”要求客人取餐戴口罩。\n減分：\n1.排隊動線混亂-要先排隊劃位結帳再排隊進場，但現場不同人員有不同引導說法，一度差點發生排了三、四十分鐘的隊伍還要重排的情形。\n2.人員溝通效果不彰-座位安排錯誤，進場後仍在搬移桌椅，甚至動用兩位服務人員以對講機向櫃檯人員溝通好幾分鐘，使客人久久無法入座。\n3.未有適當的入座用餐引導（離場前才看到牆邊螢幕有標示餐區）。\n4.有設定取餐動線，但標示不清；「不只一位」廚師對現場客人提問表現出不耐煩，還對善意誤闖的婦人提高音量碎念。\n5.豉汁排骨包裹毛髮。\n6.部分座位安排在走道上。\n7.16點過後服務人員不收餐盤（只收筷子刀叉）。\n8.用關燈方式趕客人（仍有八成客人在用餐）。之前吃過2次晚上時段，老公跟家人都覺得不錯，所以這次帶著朋友一起再去吃一次7/18的晚餐時段，結果沒想到根本就是錯的。\n上網訂位，系統就錯亂，我一開始本選4大1小，後來改成6大2小，系統一直給我6大3小，兒童餐椅的選項也沒得可以讓我選擇幾張，只有一個打勾框框，到位子上才發現只有一張餐椅，趕快叫服務人員再補一張。而且餐椅上也沒有安全帶，如果一個不小心，小孩受傷，請問這是要算誰的。\n到了訂位處，詢問訂位的小姐看到嬰兒推車就面有難色說，你有嬰兒推車，這個⋯⋯⋯⋯，我帶小孩去，本就有推車，如果餐廳夠細心，能題前先打個電話訊問，幫我們安排靠牆的坐位，我就不用自己還要想怎把推車藏起來不會打擾到其他人。\n我還記得第一次帶寶寶去，餐廳那時 還有給小孩一個小豬甜點包，小孩那時4個月雖然不能吃，但媽媽我當下真心覺得好窩心。結果這次去，服務人員來收錢，詢問有壽星嗎，我們說有一個小壽星，收錢的服務人員也不會變通一點，我知道小孩不會有折扣，但你們能趕快婉轉的給小孩氣球，還要我們自己詢問才有氣球，真的很無言。而且現在放暑假，人本就多，餐廳冷氣不涼，我都覺得熱，小孩能不悶熱嗎。\n最後來收錢的短髮小姐，你帶口罩，說話能大聲一點嗎！人多聲音就雜，抱著小孩，小孩也瓜瓜叫，你是覺得我聽得到你說話嗎！我知道我問你很多次6個人的金額，但你不用口氣差的大聲報金額，沒耐心請別做服務業，媽媽我玻璃心，我要顧小孩、顧家人、顧朋友，你是覺得我還要顧你的心情嗎！這10%服務費真的很不想給。\n喝個飲料 飲料不冰 冰塊機沒冰塊，看到旁邊冰桶插著湯匙，我弄冰塊到杯子，服務人員就擋著在面前補杯子，也不會看看有沒有客人要弄冰塊，我還要側著身體弄，感覺真的很差。\n這次用餐結束，我想我不會再去第四次，太爛了！\n我也會跟有寶寶的媽媽說，這不適合帶寶寶去！今天是第一次吃貴餐廳，也是我最後吃的一次。\n第一.人數控管不當\n\n第二.鐵板料理區豬排15分鐘左右的料理加上3.4分鐘的排隊，我每次要在排隊時前面已經10個人左右了。\n干貝第一次吃是有完美的煎好兩面，後來的都是有熟為主。\n旁邊的雞腿肉卷我放進嘴裡第一口是油膩感咬下去是整個炸開的油膩感，可能在雞肉上是用老母雞嗎～\n\n烤牛肉區 我吃不出來他是一頭有汁水牛肉，我們先不說公司用的是和牛，正常我拿到座位上熟的部分是不會柴到像放很久，如果是剛出爐的不至於這樣吧？我排的時候是整塊新的未動刀。我是知道熟的部分會沒有汁水沒錯，但不至於是柴。\n\n生魚片區\n人手不足 師傅一刀一片客人一夾23片，真心覺得辛苦師傅\n\n炸物區\n炸蝦因為人多導致面皮過厚，也因為人手不足師傅來不及處理。\n\n天使蝦因為排的人很多來不及解凍\n\n氣泡水機壞兩次\n\n甜點區\n烤布蕾因為人多導致師傅灑糖過厚，我咬下去的糖片對年輕人是沒差，但對於老人可能就要拿起來了\n或許可以換小點烤盅，那麼大一個灑糖跟焦糖化的時間讓師傅緊張到手抖。\n\n結論：\n來客數跟現場人員掌握上落差太多，人數多少你們可以掌握，請問公司策略是這樣讓客人排隊還要這樣壓榨員工真的很辛苦。一個人單價1200左右的品質，卻處處怕人吃又要投機取巧。\n我的食量大概是15塊牛排-20塊牛排。\n我今天只吃了：\n炸蝦*2、洋蔥圈*3、牛排*2、叻沙麵*1、洋蔥魚片三小盅、鮑魚三片、生魚片5片、干貝1顆半、鮑魚一顆\n雞腿肉兩小塊。\n\n花過多時間排隊=另類的飢餓行銷\n我願意排，但也要有相對應價位的品質跟出餐速度，我願意等你們用有品質的東西出來，不是我等到了品質沒有到位。往後也不想看到貴公司因為想賺錢而這樣壓榨員工跟這樣品質服務客人。', 4.2, 7768, 32626, 'qmqDBS@gmail.com', '02 8502 0555', 3, '["星期一: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:00"]', 'http://www.ffood.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.17 下午', '2021-06-16 07.38.17 下午', 7768, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 293, 1, '33319174', 'palette 彩匯自助餐廳', '104', '台北市', '中山區樂群二路55號1', 25.08052230, 121.55359810, '吃到飽', '吃到飽 來美福用餐已經有數次經驗，幾年前第一次吃到時帶著滿滿的驚艷與感動！當時的美福，有著吃到飽難能可貴的算新鮮的天使紅蝦、雖然料理種類不多但是肉質都是相當有水準，吃得出對品質的要求。甚至連茶包都是用相當不錯的品牌！\n那個時候，儘管所得不多還是很期待能有時候吃一頓美福來好好的滿足自己。\n再次回訪，天使紅蝦不見了，取代的是一口咬下去滿滿的油到立刻吐出來的松阪豬！\n原本的和牛壽喜燒沒了。\n主打印度與泰式的美食，頂多堪稱勉強可以入口。\n茶包換上自己的品牌，輕輕涮幾下後得到的是苦澀無比的茶湯。\n原本豐富的果醋換上廉價的化學果汁。\n除了前面的主餐還維持著一貫的水準外，自助餐部分已經完全失去了當年讓人念念不忘的水準，甚至連較平價的品牌都跟不上了！\n因此，今日決定給出三顆星，感謝當年的美福曾經讓我如此感動。\n也跟今後的美福說聲再見。上周六朋友生日在Easytable上預約了生日3+1free的晚餐用餐，入座時告知要使用生日方案，服務生漫不經心地回說結帳時再說，吃到中場服務生開始逐桌確認帳單，到我們這桌時，壽星本人再度告知要使用生日3+1free的方案時，\n1） 服務生只撂下一句「可是你們已經結帳完了」，然後調頭就走，服務態度之差與對細節之不專業讓人傻眼。但這還不是最幽默的，\n2) 之後請來下一位應該是主管的人物，則說這是easytable訂位，「完全不能退費」(原文引用)，而且我們已經「選擇」了另一個「紅酒方案」。\n關於紅酒方案這一點，確實是有寫在easytable的訂閱方案裡，文字內容經過解釋後確實如餐廳人員所言是「紅酒方案」與「生日3+1free」可「現場擇一」使用。但在入場、入座與結帳時，我們已經不下三次告知要使用生日專案的下，餐廳方顯然是因為已經確認到先在訂購網站上壓的信用卡已經結帳成功所以對我們的訴求漫不經心，同時餐廳本身服務人員對這些方案的內容根本就不熟悉，也不想管，即使是到了第一位主管來處理時，依然跳針，竟然還說出「因為你們沒有拒絕倒酒」（倒酒時我們四人都在外面取餐）、而且你們已經「喝了這些酒」所以就算成紅酒方案這種只想敷衍消費者的態度。\n首先，我必須承認我們確實不察喝下了那紅酒，但在我們多次告知使用生日專案的情況下，餐廳方依然前來倒酒，倒酒時沒人在座位上，卻指責我們「沒有拒絕」。餐廳方給的解釋看似是我們主動「選擇」了捨棄生日方案而選擇紅酒方案，\n３）並且在第三位應該是經理的男士來時，再度用手指指著另一位同行男性友人，說出「是他同意我倒酒的」（原字原句引用），而據該友人回應，當時他正在取餐，該經理（暫稱）只是問他『那桌是不是我們的位置』（這句回應是被手指指著當下的立即反射回應），並未告知是要去倒酒，且沒有詢問我們是否需要倒酒（選擇此方案，再次強調，我們已多次告知要使用生日方案）\n\n接下來更令人困擾的是，請經理（即３提及的男士）來幫忙處理時，經理在一開始一副不滿的態度，開始指責客人，並消極處理，要我們自己在星期六訂位網站根本沒人上班的時候要求我們打電話自己跟easytable取消，因為餐廳方對自身方案活動的不熟稔、對客人告知的訊息置之不聞，最後還要用餐的我們在用餐的兩個半小時裡，花費了超過半小時來處理這件事，然後還被要求自己去跟easytable取消，並且在取消收到退款前，被要求現場支付本次用餐的費用，等於我們要先支付兩次全體用餐的費用，這種處理方式和態度真的令人咋舌，服務完全跟不上收費的水準，後半段爭執後也根本很難有心情再吃下去，根本莫名其妙\n\n事件處理的結果是經理在我們要求下，才立即主動發mail給easytable要求退款，但還是要求我們二次付款。所幸星期一easytable很乾脆地退款給我們。\n\n回歸餐廳評論，熟食多樣水準也不差，唯獨整圈甜點的水準相當差，可麗露外殼幾乎已經軟化，感覺就放了很久失去脆度，其他甜點蛋糕在口味上也是一種死甜且黏膩的口感。其實食物滿不錯的，但服務方面就…\n上週就訂位了，留了全名電話說會寄簡訊確認，一直沒收到確認簡訊下午打去確認說沒訂到，今天還有位子所以再幫我訂一次（？？？所以我提前一週打電話是打心酸的？那如果今天沒位子呢？）\n到停車場，還沒取幣擋桿自動升起，車過了之後被管理員說要取停車幣，那個自動升起是防撞（？？？但我們就停在取幣機旁邊他就升起來了欸）（然後吃個晚餐停車要360，這種設備的停車場收360我的天）\n訂兩位結果帶到四位的桌，邊收多出來的餐具邊點餐，送餐的時候兩個人的都送錯品項\n\n東西是很好吃啦…但服務真的滿兩光的，雖然我個人是比較在意食物沒錯但五星級飯店服務這樣我應該不會考慮來住…\n\n食物給五顆星，行政&服務扣三顆5星級的收費 0.1顆星的服務\n於eztable 訂位 訂位需知載明「若欲使用其他現場優惠方案，擇一使用」\n帶位時即告知服務人員要使用4位同行1人免費的壽星方案，帶位人員說結帳時告知即可（ok,很正常的程序，其他餐廳也是結帳時出示證件做折扣）\n用餐至一半結帳人員來結帳並請出示eztable 訂位序號，再次告知「要使用壽星優惠」，結帳人員滿臉不耐說「你已經結過了」就走了！\n滿臉疑問請服務人員再來詢問，才被告知因為在eatable訂位已收信用卡四人用餐全額，並且態度強硬表示eztable 方案有送紅酒一杯，我們已喝紅酒，要用壽星方案需重新結帳並補足紅酒費用275元/人\n餐廳方紅酒是在眾人去取餐座位無人時自行倒上，並未告知是eztable 方案贈品，亦完全無告知喝了紅酒就不能使用壽星方案(且我們入座就告知帶位人員要壽星方案了）\n最荒謬的是貌似主管人員竟隨意栽贓同桌友人說「是這位先生要求倒酒」（？？？我們四人同時在取餐？你是看到鬼嗎？）\nㄧ間餐廳服務流程混亂、優惠方案說明有瑕疵就算了，把錯全怪到客人身上\n並要求要先結帳優惠方案6800元 下週上班日再自行打電話去eztable 請求退款8800元！！？？\n為什麼吃一頓飯要付2次錢\n\n主管在爭執過程中態度很差的將桌上的4杯喝不到一半紅酒全部收走\n是什麼天價紅酒？是你們倒錯不是我們要求的\n而且我已刷卡預付的eztable8800元是含這四杯了不起到極點的紅酒喔！\n\n用餐時間3.5小時 有0.5小時都在與餐廳方爭論\n更別說標明用餐時間到21:00，20:30不到餐台收到只剩甜品，哈根只有4個口味竟然一桶空桶\n炙燒壽司與現煮牛肉麵師傅可能被砍年薪20%\n詢問炙燒是否可稍微熟一點，面無表情說「統一規格，你可以去排別的」（？？）\n牛肉麵詢問哪一碗是寬麵被翻一個世紀超級大白眼！！\n服務生當著用餐客人的面大肆批評現場用餐客人？？\n\n真心建議美福主管去試試饗饗、旭集\n價格沒有美福高 餐點種類食材細緻度勝過美福許多倍\n餐台補到21:30整整\n更別說服務人員態度親切\n標榜五星級飯店的服務、全台北最貴的Buffet 真的讓人刷新三觀\n一頓生日宴吃的顏面盡失 滿肚子氣\n同行友人還被栽贓2021年5月4號第一次來彩匯吃下午茶，為了吃甜點而來，首先熟食的部分普普，當天牛肉麵的湯有一種抹布味很噁但牛肉塊好吃，現切爐烤牛排很軟嫩但沒什麼牛肉味算普通，印度系列很雷不知道是今天我衰沒吃到好吃的還是怎樣聽人說很好吃，旁邊的芒果飲品還行，冷盤蝦子大隻鮮甜，螃蟹也很鮮，生魚片鮭魚前面不錯但後面再去拿變的難吃可能放太久加上吃到有筋的部位，甜點今天裡面最喜歡抹茶（生乳卷）？名字忘了果然最搶手想再吃沒了不補，其他因為太甜(蛋糕類）即使好吃也被甜味蓋過去沒記憶點了，以前看別人的照片中有抹茶冰結果今天沒有抹茶控小失望，甜點是我吃過的包肥中（饗食，漢來，豐food)裡面排行第一但也沒高多少比漢來好一點點的程度，如果連甜點都不好吃那真的廢了因為今天吃的熟食沒有驚艷的地方，大概是這樣。', 4.2, 3825, 16065, 'yASlsyhH7m@gmail.com', '02 7722 3395', 4, '["星期一: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期二: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期三: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期四: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期五: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期六: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30","星期日: 06:30 \\u2013 10:30, 11:30 \\u2013 14:30, 15:00 \\u2013 17:00, 18:00 \\u2013 21:30"]', 'http://www.grandmayfull.com/dining/palatte', '0.jpg', '[0.jpg]', '2021-06-16 07.38.19 下午', '2021-06-16 07.38.19 下午', 3825, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 294, 1, '75866166', '帝一帝王蟹吃到飽', '114', '台北市', '內湖區內湖路一段210號', 25.08251410, 121.56439840, '吃到飽', '吃到飽 讓人不會想再去第三次\n第一次經驗很好，可能是公司聚餐包場，所以你們特別賣力服務，拿出最好的一面，讓我第二次就想到可以來這請家人吃一次，可惜第二次的經驗不會再讓我有想要來第三次的感覺了。\n\n服務品質：1.5🌟\n一開始進去，服務員確認名字電話就帶到兩人座位，突然另一個服務員阻止他說這位已經有另一組訂了，後來才知道有兩組是訂陳小姐，接訂位的服務員沒有確認好電話，於是我們就莫名被領到吧檯位，雖說我本來沒有在乎坐哪裡，可是有種不被專業對待的感覺，更扯的是其中一個比較資深的服務員還在旁邊訓斥失職的服務員，請問有考慮客人的感受嗎？也沒有有禮貌性道歉說不好意思，因為他們搞錯了，要委屈我們到吧檯位，如果你們這樣說，可能我一開始就不會那麼不爽，看來整間店的服務員都該多讀點書，尤其是說話之道類型的。\n另外特別點評其中一個瘦瘦高高戴眼鏡的服務員，整場總是嘻哈態度在服務，送餐也是直接丟餐盤到桌上，也不會喊一下什麼餐點，講話又特別小聲，不知道還以為講給螞蟻聽的嗎？不想服務可以不用做服務業，還是家裡特別有錢，出來工作只是好玩而已呢？\n我今天來花錢吃到飽，說真的我可以自己服務自己，還不用看你們臉色，呵呵。\n\n餐點品質：3🌟\n一開始幫忙烤的服務員烤的可以，後來就換了另一個，烤的著實難吃，肉又乾又老，帝王蟹還烤焦，真不明白貴店會讓這樣的人來烤，是真的很想客人不要再光顧吧？呵呵。\n\n總體來說，令人感到失望，不過爾爾而已，可以吃一次嚐鮮，但不適合二次回味，希望你們了解，餐點固然有價值，可是服務的本質應該回歸客戶的感受，否則就嚷客人自己烤吧，也省得你們一團亂讓人覺得不專業又沒有好的用餐環境。難得上台北找朋友玩\n看到YT介紹然後去吃晚餐\n因為提早到，致電店家可否將預定7點多往前改\n店家說可以，改成5點或5點半\n進店店員都沒有介紹環境\n也沒有說點飲料跟酒水的方式\n一開始就有跟店員說胡椒過敏\n請店員額外加的胡椒都不要撒\n送上來軟殼蟹很好吃，但是上來前並沒有說明裡面有魷魚，吃到不是軟殼蟹店員才說\n想點奶油帝王蟹也說招待才有\n點了4個手卷也只上了2個，詢問店員才發現根本只點了2個\n肉肉烤的有點硬\n海鮮烤的還不錯\n點蛤蜊不是肥肥的，而是煮很久扁扁的樣子\n點生菜只有第一次給生菜，後來都來和風沙拉\n點一份杏鮑菇，3個人給3片？\n點1份皎白筍，3個人給1隻切一半？\n最後加點的時候又點了軟殼蟹\n但是沒有特別提醒胡椒的事情\n上菜時軟殼蟹上滿滿的胡椒\n點甜點時也沒有泡芙\n黑糖蘋果很特別\n點巧克力聖代的巧克力不好吃\n提拉米蘇好吃但是超級硬，切的時候差點飛出去\n想點第二次甜點卻沒有任何店員經過\n問店員可不可以再點甜點時\n店員回覆時間已經到了\n\n嗯...這個價格，可以請店員在服務上再認真一點嗎\n雖然我還沒吃完就跑了2趟廁所\n有可能我腸胃太弱\n\n但還是感謝店家的店員發現我們皮夾沒拿\n還追出來請我們回去拿想給 6顆星⭐⭐⭐⭐⭐⭐\n吃了三年，\n每次都是懷著期待的心情🥺特地北上去吃🚙😋❤️😋\n老闆烤的伊比利豬超好吃🏆💯🥰🥰\n也會問吃的如何小聊 親切😘🌷🌷\n也有客製化的料理🍵🍵🍣🍣💯\n壽司 比專門的壽司“X鮮”、“有扭蛋（都不中）”還好吃很多~\n光是飯 就大贏了🎉🥇\n鮭魚新鮮、漂亮紋 又大塊 香甜\n都現做的😍\n很療癒我的心🥺💖\n（上班心情不好 只要想到 就開心了）帝王蟹太少了，都是一小節的烤，烤的時間又太長，吃不過癮，肉類味道還不錯。\n桌邊服務也挺用心，雖然不知道是不是為了控管客人時間才全程桌邊服務。\n有點分不清楚誰是服務生，因為也都沒穿著制服，有一位整個很鏘，問個問題回答不清楚，又直接把基本盤端走了，讓大家整個？？？\n\n海鮮的部分，海螺剛開始烤的不錯，後來幾個有些硬，生蠔新鮮度不夠，用了很大量的芥末跟檸檬汁遮蓋味道，生魚片也是一咬下去肉就散了，估計也放挺久的。\n\n蔬菜的山藥挺好的，桌邊的服務人員烤的剛剛好，帶殼玉米筍則太小了，茭白筍也有點老。\n\n甜點的部分，服務生說待會來詢問，結果都沒有來，之後是來告訴我們時間要到了，跟那位很鏘的服務生說還沒點甜點，得到的回應是「可是你們時間要到了，你們一次點完吧」整個感覺很莫名，我們不是一人花了$1,705來吃吃到飽的嗎，結果完全吃不過癮，連吃個甜點也很倉促。\n\n整場下來我唯一想誇獎的大概就是飲料的可爾必斯挺好喝的，估計是不會來第二次了，真的蠻失望的，而且整個過程都感覺不斷的在空等服務生烤好而已，上來的份量還不多，也許店家想走精緻吃到飽路線，但卻反而讓客人覺得店家很小氣，完全吃不過癮。牛肉肉質應該不錯，但除了厚的以外幾乎9分或全熟，生蠔跟生魚片倒是很滿意，帝王蟹普普肉黏殼黏很緊很乾，不會一直想吃，手捲龍蝦沙拉很普通。\n員工訓練待加強，感覺經驗不足，也不同太想做事的感覺，老闆or店長倒是滿強的\n可能因為地段關係反應到人事成本感覺人手不足', 3.9, 1215, 4739, 'QKCdtX9Vqk@gmail.com', '02 2627 2989', 3, '["星期一: 休息","星期二: 休息","星期三: 休息","星期四: 休息","星期五: 休息","星期六: 17:00 \\u2013 02:00","星期日: 休息"]', 'http://www.royalone.com.tw/index.html', '0.jpg', '[0.jpg]', '2021-06-16 07.38.20 下午', '2021-06-16 07.38.20 下午', 1215, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 295, 1, '39180499', '『 兩餐 』 두끼 韓國年糕火鍋吃到飽-大直店', '104', '台北市', '中山區敬業三路123號6樓', 25.08108460, 121.55546430, '吃到飽', '吃到飽 不錯吃，吃法新穎，蠻適合小孩來吃的非常差的經驗！\n帶小小孩推推車，要進自動門時來不及差點被夾到，服務生站在前面看我們手忙腳亂也沒來幫忙...\n第一次來不懂點餐規則，服務生桌邊解釋後我們不懂提出疑問，雖然有再解釋，但態度明顯不耐，又補一句：「你們等一下繞一圈就知道了」超傻眼。\n小小孩吃飯期間一直要學大人拿碗，不給就哭，請求店員是否可以給一個乾淨的碗好安撫，只回答：「沒辦法！！」連變通方法都沒有🤷🏻‍♀️🤷🏻‍♀️🤷🏻‍♀️ 再次傻眼......\n先不論餐點好不好吃，但教育訓練就是明顯不夠！還開在大直Att高檔消費區裡？我以為自己來到夜市路邊攤勒🙄\n不會再來了！也不建議來！！服務態度差到一個極致，\n推車推到門一半要被夾到看著我們繼續聊天\n到位置上後解釋用餐方式跟背書一樣，又因為戴口罩的關係聽不清楚，所以再次詢問，換來的是白眼跟不耐煩的態度繼續講；\n再問到第二個問題後直接回應「阿你們等等去繞一圈就知道了啦」\n用餐過程中要加肉大概要舉十次手以上才有人理你\n要幫小朋友分碗裝食物，\n想要再拿一個碗，回應是「沒辦法！」\n從頭到尾只有一開始裝鍋底醬跟拿一次肉，這樣要收我 10% 服務費會不會太誇張？\n後面聽到跟新來的客人介紹一樣背書+不耐煩，是不是該慶幸原來不是針對我們才這麼差阿2020/12/25 兩人前往用餐，爛到爆！真的！相信我！\n\n¹ 我去問櫃檯服務員兩次，她從來沒好好解釋排隊、預約的機制是怎樣，讓我們在那邊空等。\n\n² 等了好久，居然有二十六位國中底迪來預約吃飯，怎麼會讓二十六位一起訂位，我傻眼，而且他們太吵了素質好低好丟臉。\n\n³ 實際用餐在夾菜的時候，很明顯這間店就是有幾樣菜根本沒端上桌，兩個小時都沒見到；其實我在來用餐之前就有看過不少評論講到這件事，沒想到都是真的。\n\n結論：雖然是連鎖店不過建議還是參考一下各間分店的評分、評論再決定吃哪一間會比較好 :)2020.09.22 晚間約7點\n大直 ATT 6樓  兩餐\n一位年輕、有化妝、無配戴眼鏡、瘦瘦的、頭髮捲捲蓬蓬綁低馬尾、鞋子品牌是puma的一位女服務生，服務態度非常非常差，送起司來時，桌面因擁擠，起司鍋放不了，我們消費者幫忙整理桌面後，女服務員還是放不了，竟直接冷眼並且態度差勁的說：「竹籤」，這時，我們才意識到是有一根竹籤擋住了起司鍋的位子，能體諒起司鍋可能很燙很重，但用這種態度對待消費者，是貴公司訓練的嗎？再者，吃到後面時，湯壺裡沒有湯了，請該名女服務員加湯，該名女服務員竟走到桌邊，冷眼並用頭點向湯壺，這時我們消費者才意識到，她可能拿不到湯壺，我明白女服務員可能身材嬌小，拿不到，但她完全沒有伸出手，還要我們消費者主動拿給她，這點我十分不能接受。再來，我們旁邊那桌吃完了，服務員收桌子時，不慎將杯子打翻到地上，飲料有些微噴濺到我們腳上，服務員卻一句道歉都沒有。', 3.9, 312, 1217, 'VtrQIY0Xf@gmail.com', '02 7700 7112', 2, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 12:00 \\u2013 20:00"]', 'https://www.facebook.com/DookkiDaZhi/?ref=bookmarks', '0.jpg', '[0.jpg]', '2021-06-16 07.38.25 下午', '2021-06-16 07.38.25 下午', 312, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 296, 1, '49725003', '饗食天堂 台北大直店', '104', '台北市', '中山區樂群三路218號美麗華百樂園正後方商場4樓', 25.08231880, 121.55796160, '吃到飽', '吃到飽 7月改裝後來用餐\n假日剛好遇上父親節，整場滿座\n中午約12點入座，可用餐至14點\n\n改裝後現作餐點變多了\n但不確定是人手不足，還是刻意限制數量\n每樣餐點都要等滿久的\n而且還不見得吃的到\n每回取餐時都有一半的空盤來不及補\n\n現烤牛排好吃，鮮嫩多汁，不會很難咬或咬不斷\n現烤及焗烤生蠔還不錯，但要一直站在那等才拿的到\n烤鴨捲餅很推薦，鴨肉軟嫩不柴，好吃\n\n現作義大利麵很喜歡\n白醬很對我的口味\n可以自已選配料和麵\n\n現燙牛肉湯滿失望的，湯不是很熱\n沖完後味道很淡，沒有牛肉的香和鮮甜味\n顏色也和網路上看到的差很多，特別淡\n\n炸物和烤物都滿不錯的，魚肉很鮮甜\n同樣也是烤/炸的速度比不上拿的速度\n一半空盤，只吃了幾樣\n\n最後，甜點區的動線非常不好\n點心區左右兩邊各是冰淇淋和現作甜點\n兩者皆需排隊\n也因此，排隊的人把整個甜點區都擋住了!\n拿甜點都要道歉、借過、擠進去才拿的到.....\n且排隊的人一直在聊天，甜點有一大半都裸露在外\n比較喜歡原來的甜點櫃，改裝後取餐體驗不是很好\n但還是想推薦一下焦糖葡萄柚，很值得嘗試的甜點\n\n喜歡吃甜點和水果的建議不要太晚去拿\n可能因為在準備下午茶時段的東西\n最後半小時都沒什麼在補了\n一半都空的，失望.....\n\n來吃了滿多次饗食天堂\n第一次過節來吃，人真的好多\n補餐速度太慢，很多都沒吃到，滿可惜的個人認為食物還不錯吃，但是假日人不少這次用餐時間是平日晚上17:30準時入座，很失望，品管明顯下降，和2年前吃的品質差很多。\n1. 食物部分，缺點:除了熱炒湯區，其他熱食皆為涼，炒蛤蠣很多沙，口感不太新鮮，用很多醬汁炒蓋過不新鮮感；鴨肉好硬、乾；炸物部分每樣都太油；凱薩沙拉生菜裡的麵包酥是軟的、軟的，沙拉口感覺得像冰有些時間，然後沙拉冷盤那區，總是有汗臭味、抹布臭味，不知道是否為起司味，還是環境整潔需加強? ；甜點部分，紅茶戚風蛋糕沒紅茶味、除了烤布雷，其他都死甜。優點: 果汁類都不錯喝、新鮮，很推柳橙、芭樂汁，湯類推魚肉、牛肉湯。\n2. 廁所部分需多加定期清潔，17:30進來先去洗手間，但女廁就有2間馬桶未沖，而且是糞便，整間廁所都是味道，希望能頻繁定期清理。\n3. 服務態度部分是真的很好，會主動詢問需求、清盤，也希望整體品質也能像人員服務態度一樣好。以目前吃到飽來說，算豐富的❤️\n服務優下午茶菜色好到比其他家buffet晚餐還好，日式餐台有各式生魚片.壽司還有基本的炸物，比較推薦的是炸蝦.照燒雞腿及唐揚炸雞，中式餐台有其他家響食沒有的燙牛肉湯及燙魚片湯，不像某些buffet店燙牛肉會莫名的腥味，這裡的肉很嫩且湯頭加上一點薑跟聰就有足夠味道了，平常不怎麼喝湯的人都魚跟牛各喝了一碗，西式餐台五重起司披薩起司味真的很濃厚跟外面的披薩店比也不會輸，而且特別的是還有可以自己夾菜現作的義大利麵，比較可惜的是聽朋友說有燉牛肉可是忘記拿了。', 4, 3304, 13216, 'Y4cHGca@gmail.com', '02 8501 5979', 4, '["星期一: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期二: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期三: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期四: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期五: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期六: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30","星期日: 11:30 \\u2013 14:00, 14:30 \\u2013 16:30, 17:30 \\u2013 21:30"]', 'http://www.eatogether.com.tw/shop.php?mID=1&sub=1&shopID=17', '0.jpg', '[0.jpg]', '2021-06-16 07.38.28 下午', '2021-06-16 07.38.28 下午', 3304, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 297, 1, '22808404', '故宮晶華 Silks Palace', '111', '台北市', '士林區至善路二段221號', 25.10125060, 121.54779830, '吃到飽', '吃到飽 故宮與晶華聯名的港式餐廳，坐落在故宮正院旁，內裝相當的典雅隆重，充滿宮廷風的環境增添用餐氛圍，現場提供單點式或港式茶點吃到飽的選擇。港茶吃到飽的餐點選擇其實不少，蒸籠燒賣燒臘等等的選擇豐富，個人覺得梅汁高麗菜微酸甜的口感配上高麗菜的爽脆相當對味，冰鎮白蝦的甜度足夠，椒麻雲耳微辣感很棒，香菇烤麩口感還不錯，宮保皮蛋炒的火候不錯味道略淡些，鼓汁蒸鮮魚跟藤椒嗆辣蟹也是蠻值得的海鮮餐點，蘿蔔糕跟腸粉就有點遜色了需要再加油，樹子蒸排骨跟鳳爪也是續了好幾份吃得很過癮，腐皮鮮竹捲算是讓人眼睛一亮的餐點，值得讚許跟推薦(此為2021/5/2用餐，現恐配合防疫政策調整營業時間，請詳官網或電話確認)。紅燒牛肉麵超級超級鹹，吃過許多名店牛肉麵，從沒吃過\n這麼鹹的紅燒牛肉麵。\n鹽是大降價了嗎？第一次到這裡用餐\n在故宮裡面，是可以停在旁邊停車場，就不用走進來，但是停外圍也有好處，吃完後剛好消化，因為90分鐘吃完真的是飽到快吐😂\n\n港式點心的部分種類略少\n菜的口味都算是安全牌\n\n蝦餅，蘿蔔糕表現很優秀👍\n蜂巢芋頭酥是第2輪才點的，因為吃太飽所以沒再點，不過這芋頭酥是值得一吃的！👍\n水煮牛肉味道有夠，但肉片少少的\n北菇海參煲覺得味道略淡\n羊小排也點了兩次，真的滿大隻的，肉也嫩好入口👍\n叉燒，油雞，醉雞，燒鴨表現偏中下\n冰鎮白蝦新鮮度很足夠，Q彈好吃👍\n珊瑚豆腐煲也是普普（可能因為有芹菜吧😂）\n蒜香軟殼蟹調味的好，口感也好，是本次用餐的前三名👍👍\n酸辣海鮮羹我個人不愛，覺得味道和想像的不一樣，和服務人員要了紅醋加後才覺得好一點\n例湯沒有驚豔，但和海鮮羹比起來，算好喝的\n柚香蓮藕入口飄著不濃不淡的柚子味，非常剛好的味道，吃完整個味覺都活過來了👍\n蒸鮮魚和香辣子雞覺得沒太必要點😏\n宮保皮蛋沒有宮保的感覺，口味偏甜\n金沙杏鮑菇中等表現\n腐皮鮮竹捲是蒸的！！？\n炸春捲，牛肉餃，花枝餃，干炒牛河都是大眾可以接受的口味\n薑蔥撈麵上桌的時候嚇了我一大跳，兩個人的份量像4個人一樣，有點太多了，但是撇開太多這個部分，薑蔥的味道很香，麵也煮的很剛好，建議怕吃不飽的可以點！！🤣\n至於甜點類沒有什麼印象深刻，就是甜點啊😎\n\n整體吃下來，覺得沒買餐卷來吃的話，價格是偏貴的，如果想來吃，是可以先買餐卷再來比較划算\n最後還會給小杯哈根達斯！！\n真的真的吃很飽！！！！！！暫停營運中🤣🤣🤣😂😂😂服務變差，吃到飽五點半開始，五點我們就去報到，結果五點四十分才入座，入座後等了好久都沒人來服務，主動找服務員才會理人，而且只能吃到七點。服務員不是不理人，就是一下上一堆菜。不推薦這裡...', 4.1, 3349, 13731, 'UjUTvweS@gmail.com', '02 2882 9393', 3, '', 'http://www.silkspalace.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.29 下午', '2021-06-16 07.38.29 下午', 3349, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 298, 1, '57064090', '典華幸福機構(大直店)', '10466', '台北市', '中山區植福路8號', 25.08374720, 121.55480080, '吃到飽', '吃到飽 可能同時間太多婚宴進行\n服務有待加強婚宴日期 2020.10.03 推薦貼心細心又可愛的超強婚企 - Sherry\n我想推薦典華真的是很專業的婚宴會場, 不僅場地很美很氣派, 服務人員與婚禮企劃更是專業, 比起其他台北市的飯店, 覺得菜色與場地都絲毫不遜色, 婚宴人員更是比台北市其他知名飯店還要更專業在辦婚禮上面, 跟我一樣怕麻煩的新人真的可以選大直典華!!我們婚禮企劃Sherry從開始的婚禮諮詢, 仔細著聽著我們的需求, 提供我們建議, 帶著我們一步一步完成夢想中的婚禮, 在一旁細心的叮嚀我們該注意的事項, 心想如果他可以當我們婚禮的主持人那有多好~沒想到!真的是她!當下實在開心極了, 也很放心, 他了解我們的故事, 我們的需求, 感謝她的貼心還有活潑的主持風格, 靈活的臨場反應! 讓我們不這麼緊張, 真的很貼心!!很多飯店或是婚宴會場沒有提供婚禮企劃或是婚禮小管家的服務, 大直典華都有~還很專業!!(劃重點)跟我一樣第一次(笑)辦婚禮的新人可以選擇典華, 大直典華在文湖線的劍南路站, 坐捷運下車走五分鐘就到囉! 真的是很方便, 賓客們也不用在會場或是周邊找停車位, 在人多的台北市, 真的是很便利, 也不用怕對賓客不好意思! 如果你還在找婚宴場地的話, 我推薦大直典華喔~交通方便，場地寬敞，菜色好吃，大型聚會的好地方非常幸運能在典華大直辦婚禮\n不愧是婚顧起家\n從一開始接觸的Eunice\n就很有親切感\n她耐心的講解菜色 流程 注意事項\n對於我們的要求也想辦法解決\n還要感謝我們的主持人 Annie\n她是一個外向開朗的人\n把我想要溫馨浪漫的婚禮圓滿成功\n真的很棒\n不過也有一些覺得典華需要改善的地方\n晚宴的時間上有些緊湊\n造成我和老公 沒有真的坐下來主桌陪伴長輩們\n一口都沒吃的我們 覺得有些奇怪\n希望婚禮步調再慢一點\n能有機會可以招呼到賓客座位區太多，若是滿座連拿東西、行動都不方便，人一多拿個東西吃就要排隊，結果一整排食物可能只要拿中間想吃的幾樣，得從端點一個個排，一個個等，明知極不明智也極無效率，但那是消費者的事，沒員工主動現場協助動線，冰淇淋明明二側都有挖勺，但大家因排隊只從單側，一支挖勺慢慢挖，吃個冰淇淋愈排愈長，流動水也不開，一整杯髒水放挖勺，沒吃出問題是運氣好。\n少部份廚師看人多排隊，明顯故意拖，做個炙燒壽司東瞧西瞧(奇怪我也不知排了一堆人等你上盤有啥好看的)加聊天，手不動，等擺完一盤，所有動作再round一次(東瞧、西瞧、轉頭聊天)，4~5個人可以讓你排上近8分鐘。\n中午$1125加一成小費，這種價錢建議挑環境更優的，可以吃得更輕鬆愉快，不用花錢像難民。', 4.1, 11627, 47671, 'L3nQV3z@gmail.com', '02 8502 5555', 4, '', 'http://www.denwell.com/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.30 下午', '2021-06-16 07.38.30 下午', 11627, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 299, 1, '77956806', '豪緣素食館', '10465', '台北市', '中山區北安路458巷41弄23號', 25.07891690, 121.54586790, '吃到飽', '吃到飽 因本身吃素，到店內很乾淨，所有的食材非常新鮮，煮得也很好吃，不會過油也不會過鹹，也有稀飯可用，給一個大大的讚北部素食自助餐普遍高價位的情況，相較下此處價位便宜，菜色雖不算多，但皆可口，重點是續飯免費。住在大這那麼久無意間經過這間素食店 ，東西便宜又好吃 ，白飯還可以一直裝 。算是在台北市吃過最平價的。\n而且就在大直捷運站2號出口大約50公尺就到了， 非常推薦*\\(^o^)/*便宜,適合學生,但是常常有提供臭豆腐,可能有些人不適應.CP值高，不會太貴', 4.6, 128, 589, '9T0ouHvz@gmail.com', '02 2532 0887', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 休息","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 11:00 \\u2013 14:00","星期日: 11:00 \\u2013 14:00"]', 'https://blog.xuite.net/tcsybo/twblog/160804864-%E8%B1%AA%E7%B7%A3%E7%B4%A0%E9%A3%9F%E9%A4%A8', '0.jpg', '[0.jpg]', '2021-06-16 07.38.31 下午', '2021-06-16 07.38.31 下午', 128, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 300, 1, '38058778', '日炙炭火燒肉', '104', '台北市', '中山區北安路627號', 25.08340400, 121.54891900, '吃到飽', '吃到飽 昨天第一次來日炙炭火燒肉用餐(推薦親子餐廳)\n就在大直憲兵隊附近，離家好進的燒肉吃到飽\n店家兩個價位549/699 (差別在食材種類更多)\n這次點了699價位 (為了天使紅蝦跟美味牛舌)\n1.店家服務：覺得佛心，從送炭火跟第一組肉盤開始算開鍋時間\n限時用餐2小時 (周日晚上店家沒有在時間到前催趕我們)\n覺得....對帶孩子用餐的家庭很友善(家長很有感)\n上菜速度OK，闆娘跟老闆店員姊姊，都很親切。\n\n2.炭火溫度：本來看炭火感覺熱度有點弱，一般是超旺火\n後來我跟老公一致認同這熱度烤肉剛剛好\n完全不會因火太旺瞬間燒焦浪費食材\n親子可以輕鬆邊吃邊烤肉，舒適慢吃2小時\n讓我超級放心讓5歲兒子為我們服務((媽咪覺得出頭天))\n\n3.食材方面：專攻了好吃的霜降牛、五花牛、牛舌、牛肋條、牛小排、松阪豬....等，一次點餐可以8樣 (數量看人數配)，上餐完畢送回點菜單(再續點)也沒有漏單的狀況。\n>>>因為炭火溫度剛剛好，肉質夠鮮嫩\n>>>喜歡烤肉不愛吃肉的兒子，意外吃了好多\n店內有沙瓦飲料機、一開罐汽水、咖啡機、明治與哈根達斯冰淇淋\n孩子最喜歡的就是棉花糖(整罐放著隨客人取用)此生吃過最好吃的燒肉，不用調味本身味道就超讚，泡菜好吃到沒話說。老闆和老闆娘人也超親切，送我們超多限量版的肉、魚肚、蜂蜜蛋糕，主要是服務帶動會讓你想再來好多次，我真的很愛老闆娘和老闆，人超級Nice，還會教你怎麼烤最適合品嚐，這間真的是我每天出門賺錢的動力，比連鎖的燒肉火鍋吃到飽店都還有價值，愛日炙❤️禮拜天晚上去吃，也沒什麼人，我們是當天晚上最多人的一群，吃到時間快到一直趕人，明明後面就沒客人，一直趕我們，桌上就還一堆菜沒烤，難不成要我們直接放著走人嗎，上菜速度也是有夠慢，火也是有夠小，有附蔥鹽，說可以一直要，每次不是忘記就是要講好幾次才送來，還說買了兩百塊的蔥都快被我們吃完，吃不起就不要開吃到飽啊，真的沒看過一間吃到飽可以這樣開，我們十幾個人全都在討論這間店有多瞎，服務生阿姨也是超級煩，偶爾提醒叫親切，一直來，叫做煩人，天花板還漏水，點了三份干貝，阿姨說一份四顆，結果兩分鐘後拿出來，還是8顆？？？？當我幼稚園不會算數？？？總之不可能再來第二次了！海鮮肉品新鮮好吃,難得蓋飯類也調味得很好吃，女生食量小但又想吃白飯，居然吃完了一碗。天婦羅很有魚漿的鮮味。香檳沙瓦好喝，冰淇淋味道比哈根達斯好吃。洗手間乾淨，環境整潔用心。老闆娘很親切，會隨時注意情況，適時提醒怎麼烤怎麼點比較安全、比較好吃，比客人還早注意烤網黑了，即時協助更換。看之前評論說炭火溫度不夠，自己倒是覺得很ok,同行的男性友人們各個狂烤得很開心。很棒的用餐體驗，之後會再光顧。老闆很熱情 用餐環境好 食物新鮮 出菜速度快 大推大推(´▽｀)', 4.3, 70, 301, 'a9mFHto@gmail.com', '02 8509 3309', 4, '["星期一: 17:00 \\u2013 21:00","星期二: 17:00 \\u2013 21:00","星期三: 17:00 \\u2013 21:00","星期四: 17:00 \\u2013 21:00","星期五: 17:00 \\u2013 21:00","星期六: 11:30 \\u2013 15:30, 17:00 \\u2013 21:00","星期日: 11:30 \\u2013 15:30, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/sunshineroast/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.33 下午', '2021-06-16 07.38.33 下午', 70, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 301, 1, '16213441', '一番地壽喜燒 大直ATT店', '10466', '台北市', '中山區敬業三路123號5樓', 25.08118710, 121.55585780, '吃到飽', '吃到飽 上菜速度快，食材等級高！店家對於親子家庭的坐位安排比較沒有靈敏度超推牛小排與霜降\n飯後還有檸檬片一番地壽喜燒吃到飽吃的非常飽喔非常好的一家餐廳 服務好 肉質優異 值得再訪', 4.8, 1556, 7469, 'pc2UFo@gmail.com', '02 7725 8852', 2, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.sukiyakino1.com.tw/home.html', '0.jpg', '[0.jpg]', '2021-06-16 07.38.34 下午', '2021-06-16 07.38.34 下午', 1556, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 302, 1, '33327214', 'Marcus老倉庫內湖店', '114', '台北市', '內湖區瑞光路583巷25號1樓', 25.08079300, 121.56789930, '早午餐', '早午餐 空間大餐點好吃飲料可續杯自從老倉庫內湖店開幕以來，身為內湖人就一直常客。基本上每個月內的一天週末一定都會造訪一次。\n\n吃了幾年，但最近的品質與服務真的與之前差得越來越多。\n\n我覺得菜單的改變與食材的不用心就算了（烤葡萄柚上面居然沒有焦糖）。\n\n12/27中午過去用餐。結果發現沙發上有小蟑螂爬來爬去，就在客人坐著以及放外套包包的地方！\n\n還好我們是快吃完了才發現。\n\n當下沒有特別發作，想說留給店家一些面子，僅有結帳時才善意提醒說：你們可能要注意一下消毒，沙發上蟑螂有點多。\n\n沒想到女服務生僅回答：我們每月都有消毒，但蟑螂實在是太頑強了。\n\n？？哈囉？一句道歉都沒有，也沒有立即處理（例如請人過去沙發處理一下），是覺得有蟑螂在沙發上跟客人坐在一起理所應當？\n\n我理解做餐飲或多或少都有蟑螂，但服務生的回應與處理方式都非常不OK。\n\n對老倉庫內湖店真的失望透頂。\n\n要去的人請注意沙發上有蟑螂！！！舒肥雞口感不錯\n份量還可以，算是夠\n氣氛不錯服務差，餐點普通～永遠不會再光顧👎\n\n五一連假第1天，帶著小孩在AM 10:01開門營業時踏進餐廳，一進門沒人服務，自己找位置坐下，店員上前詢問我們會用餐多久，因為中午12:00有人訂位⋯怪哉！用餐時間2小時，有問題嘛？餐廳含我們只有2桌，是有人包場嗎？後面陸續來了5-6組客人，也沒如此關切用餐時間，是歧視小孩嗎？\n\n餐點一般，薯條上桌是冷的（麥當勞還熱的呢⋯），蛋捲套餐的牛奶吐司也忘記上⋯告知店員也沒有一句道歉，只說「稍等」就走了⋯這是什麼服務態度🤬\n\n最後，還是滿臉疑惑詢問店員，為何只有我們被關切用餐時間，店員推給疑似店長的人說「不是我問的」然後又走了⋯直到結賬前，都沒有人上前說明😤\n\n我們不是奧客，但餐廳連基本的服務和尊重都沒有，真的很糟糕～看到評論的鄉親，如果有您有小孩、如果您喜歡熱騰騰的薯條，千萬別來光顧受氣～⁣ig🔍：爆食阿金✨ 看更多美食評論⁣\n📍 Marcus老倉庫內湖店⁣\n價位等級💎⁣💎（加收10%服務費）⁣\n整體🙆‍♀️⁣\n美味🌕🌕🌕🌕🌑（紅茶扣了半點）⁣\n環境⁣🌕🌕🌕🌗🌑⁣\n這家價位偏高，加收一成服務費，餐點份量足，調味很棒👌部分飲料可以換可以續杯～適合聊天聚聚😂（貳樓早午餐飲料也可以續杯）⁣\n⁣\n🔸⁣法式香榭燻鮭⁣\n💰330⁣\n好吃的早午餐👍附贈的紅茶超難喝⋯⁣\n可頌不會很油但很脆🤤葡萄柚不苦加上面的焦糖讚讚。⁣\n燻鮭魚味道剛好也滿大片的👌⁣\n（因為我個人滿喜歡吃酸的，所以喜歡葡萄柚。也喜歡可頌、鮭魚、黑糖麥片優格，所以這個早午餐組合很對我的胃口）⁣\n⁣⁣\n🔍⁣⁣\n#阿金吃台北⁣\n#阿金吃內湖⁣\n#阿金吃早午餐⁣', 4, 1944, 7776, '7yd4FO@gmail.com', '02 2658 1777', 2, '["星期一: 10:00 \\u2013 21:30","星期二: 10:00 \\u2013 21:30","星期三: 10:00 \\u2013 21:30","星期四: 10:00 \\u2013 21:30","星期五: 10:00 \\u2013 21:30","星期六: 10:00 \\u2013 21:30","星期日: 10:00 \\u2013 21:30"]', 'https://m.facebook.com/Marcus.warehouse2/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-16 07.38.36 下午', '2021-06-16 07.38.36 下午', 1944, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 303, 1, '45975131', '方石頭早午餐', '114', '台北市', '內湖區內湖路一段285巷8弄4號', 25.08278070, 121.56580760, '早午餐', '早午餐 店家處理的態度不是很舒服。\n菜單上明明有畫要單點的東西，但每次都少給，打電話告知店家，想確認是否是我們自己沒有畫到？也想確認是否有多少錢，詢問後我們確實有畫到，店員也沒有多算到錢，但卻反過來告知說他們都有核對點的東西，而我們確實也有聽到店家確認過，卻還是少給？？這家早餐店是我非常常去的店，但就在今日中午用餐時，突然感覺身旁有東西掉下來，一看是一隻大蟑螂，告知店員有蟑螂時，店員只冷靜戴手套並抓起蟑螂丟掉，告訴我：哦～因為昨天消毒啦。事後也沒有任何抱歉的意思，反倒是我自己要求換位置，店員才勉為其難的幫忙。\n\n消毒的隔天，有蟑螂出現在客人桌上，造成常客的困擾，連一句抱歉都沒有，是否應該深深檢討？還是員工訓練不足？早上10點多用ubereat訂6號餐，我點的是「鮮奶」茶，你們給的是奶精的奶茶...噁,女店員直接用手戳洞已經封膜的飲料,再倒進另一杯,\n店長沒教育員工嗎?就一般早餐店,沒什麼特別的。\n明明套餐想要換的飲料跟原本附送的一樣價錢，卻要多收5元? 不解這邏輯。', 3.9, 202, 788, '6I8EhK0@gmail.com', '02 2627 7877', 1, '["星期一: 07:00 \\u2013 14:00","星期二: 07:00 \\u2013 14:00","星期三: 07:00 \\u2013 14:00","星期四: 07:00 \\u2013 14:00","星期五: 07:00 \\u2013 14:00","星期六: 07:30 \\u2013 14:00","星期日: 休息"]', 'https://www.facebook.com/FunStone2017/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.37 下午', '2021-06-16 07.38.37 下午', 202, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 304, 1, '44465538', '找店長', '114', '台北市', '內湖區內湖路一段285巷27弄3號', 25.08326210, 121.56706880, '早午餐', '早午餐 每份餐點都有用蒸籠裝，蠻可愛的。漢堡很超值，蛋餅就稍微貴一點。整體是好吃的。食物美味，份量也很充足，店員的態度也很良好\n輕食類都很推，私心推薦香煎雞肉跟煙燻牛肉的輕食餐🥺❤❤❤3/13中午透過Ubereats點餐\n1.價格親民飲料也不貴30～40元\n2.嫩煎雞腿漢堡非常好吃88元（打開看了就知道非常有質感的漢堡、跟早餐店的漢堡完全不一樣、完全不輸給外面200元的漢堡）\n3.田園蔬食超大一盒很新鮮121元（量超多超豐富）\n4.Cp值真的很高、相信現場吃一定更便宜、非常完美的早午餐店！\n\n謝謝你們讓我週六中午一起床就吃到美味食物，心情整個都開心起來了💪💪💪鮪魚起司蛋餅不錯 很飽滿 吃起來會感覺很豐富\n炸物的話 小肉豆沒有預期中的香味 但薯條還蠻酥脆好吃的\n\n飲料就真的很普通了...鮮奶茶很淡 紅茶是茶包放在熱水裡 著實讓我愣了一下\n\n環境來講有很多手繪裝飾 整體裝潢偏綠色 擺盤看得出想跟其他店家有所區別 但是...蛋餅跟蒸籠沒有關係吧...食物擠在一起其實蠻不方便吃的份量紮實的早餐\n大家推的沙拉還沒點過，但光是漢堡就讓我驚艷了', 4.5, 19, 86, '07J8xagc@gmail.com', '02 2657 1551', 4, '["星期一: 06:00 \\u2013 15:00","星期二: 06:00 \\u2013 15:00","星期三: 06:00 \\u2013 15:00","星期四: 06:00 \\u2013 15:00","星期五: 06:00 \\u2013 15:00","星期六: 06:00 \\u2013 13:00","星期日: 06:00 \\u2013 13:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.39 下午', '2021-06-16 07.38.39 下午', 19, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 305, 1, '84394470', '墨羚找食', '114', '台北市', '內湖區內湖路一段285巷69弄1號', 25.08486620, 121.56697050, '早午餐', '早午餐 新鮮爽口好吃😋抓餅蠻好吃的\n醬很鹹可以不加\n會讓人想繼續嘗試其他品項位置少 通風差 油煙味很重\n但早餐店嘛 東西好吃就行了 不是嗎？餐點很好吃喔！種類豐富選擇，奶茶也很好喝！餐點好吃，紅茶讚，位子不多，有點擠，通風不良，有油煙味', 4.2, 150, 630, 'vAs1ar@gmail.com', '02 8751 0206', 1, '["星期一: 休息","星期二: 06:00 \\u2013 13:30","星期三: 06:00 \\u2013 13:30","星期四: 06:00 \\u2013 13:30","星期五: 06:00 \\u2013 13:30","星期六: 06:30 \\u2013 14:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.40 下午', '2021-06-16 07.38.40 下午', 150, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 306, 1, '51087146', '富貴村', '10462', '台北市', '中山區大直街46巷2號1樓', 25.08205600, 121.54618400, '早午餐', '早午餐 每次都要等好久 建議先打電話訂 鮮奶茶很好喝 有別於一般早餐店奶茶(◍´ಲ`◍)從大學吃到出社會還是會特地回來吃的早餐店❤️\n那時接近評圖熬夜的動力就是一早要吃富貴村！\n老闆總是很有禮貌的招呼每一位客人\n有時一次湧入太多學生可能會造成店家忙不過來的情況，也請大家耐心等待或是稍微詢問一下單況🥰\n必吃韓式泡菜鐵板麵啦～🇰🇷🍝\n希望老闆能快快擴大經營！！\n期待第二家富貴村🎊著實有賓至如歸的感受\n每次一進店門\n不管再怎麼忙 店員們一定會說聲早安\n每道餐點都很不賴🤘🏼\n這就是為何每天吃早餐都會固定來報到的原因\n大直早餐愛店之一✨用餐環境比其他傳統早餐店乾淨很多，價位也都算便宜，CP值高，服務態度也很好😌不錯吃 但烤焦的漢堡給客人不好吧', 4.3, 221, 950, '00dojFEW@gmail.com', '02 2533 5561', 1, '["星期一: 06:00 \\u2013 13:30","星期二: 06:00 \\u2013 13:30","星期三: 06:00 \\u2013 13:30","星期四: 06:00 \\u2013 13:30","星期五: 06:00 \\u2013 13:30","星期六: 06:30 \\u2013 13:30","星期日: 06:00 \\u2013 13:30"]', 'https://zh-tw.facebook.com/FuQueibreakfast/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.41 下午', '2021-06-16 07.38.41 下午', 221, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 307, 1, '68886343', '恩家食堂 En family Brunch', '114', '台北市', '內湖區環山路一段92號', 25.08654830, 121.56750600, '早午餐', '早午餐 環境乾淨，東西好吃，價格合理。黑咖啡挺好喝，火腿蛋吐司的生菜給得有夠慷慨。內湖cp值最優早餐店\n每一樣餐點都很用心西湖最好吃的早餐！\n環境乾淨舒適，雖然沒有裝潢的高級，但就像家一樣舒服！\n最愛芝麻吐司，外面不好買到，用料實在，香氣十足！\n蛋餅很大份，玉米/肉份量都很多\n但都是使用黃蛋，非常新鮮\n非常推薦加花生醬吃得到滿滿顆粒，食材都超有誠意，鮮奶茶做無糖還很好喝！鐵板麵好吃，培根蛋餅份量紮實', 4.6, 13, 60, 'Gki87NCX5P@gmail.com', '02 2797 0211', 4, '["星期一: 08:00 \\u2013 13:00","星期二: 08:00 \\u2013 13:00","星期三: 08:00 \\u2013 13:00","星期四: 08:00 \\u2013 13:00","星期五: 08:00 \\u2013 13:00","星期六: 08:00 \\u2013 13:00","星期日: 08:00 \\u2013 13:00"]', 'https://www.facebook.com/enfamily27970211/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.42 下午', '2021-06-16 07.38.42 下午', 13, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 308, 1, '54653767', '晴天小店（晴天土司°三明治專賣店）', '114', '台北市', '內湖區文湖街7-2號', 25.08539800, 121.55984200, '早午餐', '早午餐 不推。蜂蜜芥末搭起士感覺口味很怪，甜度蓋過起士的鹹，吃起來不覺義式，口味易膩，價錢覺得稍貴，店內沒提供飲用水，需自己買一瓶十五塊。熱壓三明治酥脆、起司滿載，建議內用、選擇鹹口味，製作較花時間，趕時間不適合最喜歡香樁杏鮑菇三明治❤️換人做了 ，口感差太多、料比以前少一半以上、起司、吐司都遜，還有紅茶也遜斃了 ，不會再來 ，可以從名單刪除了三明治份量很足夠也很好吃', 4.3, 301, 1294, 'U6Xpa7BL2@gmail.com', '0921 930 852', 1, '["星期一: 休息","星期二: 07:00 \\u2013 14:00","星期三: 07:00 \\u2013 14:00","星期四: 07:00 \\u2013 14:00","星期五: 07:00 \\u2013 14:00","星期六: 07:00 \\u2013 14:00","星期日: 07:00 \\u2013 14:00"]', 'https://reurl.cc/R4gqmD', '0.jpg', '[0.jpg]', '2021-06-16 07.38.44 下午', '2021-06-16 07.38.44 下午', 301, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 309, 1, '94630812', '恩家食堂', '114', '台北市', '內湖區環山路一段92號', 25.08651770, 121.56748810, '早午餐', '早午餐 門口男店員口氣很差，一星送你非常推薦漢堡，漢堡的麵包都會烤過，外層酥脆內層鬆軟，比較可惜的是最近幾次去都沒看到早安特餐他們的招牌203燒肉漢堡，麵包皮很脆\n肉也很讚環境舒適，出餐快，餐點尚可問老闆為什麼我點的50元台幣法式總匯土司連個蛋絲都沒有？回答說我們公司的法式土司就是這樣加肉鬆跟起司啊啊啊啊！\n你乾脆叫法蘭西斯低溫烘培豬肉絲佐伯根地熟成乳酪三明治好了....', 3.5, 25, 88, 'LJZsYF@gmail.com', '02 2797 0211', 1, '["星期一: 08:00 \\u2013 13:00","星期二: 08:00 \\u2013 13:00","星期三: 08:00 \\u2013 13:00","星期四: 08:00 \\u2013 13:00","星期五: 08:00 \\u2013 13:00","星期六: 08:00 \\u2013 13:00","星期日: 08:00 \\u2013 13:00"]', 'https://www.facebook.com/enfamily27970211/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.45 下午', '2021-06-16 07.38.45 下午', 25, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 310, 1, '45778457', '蔬菜蛋餅早午餐', '11443', '台北市', '內湖區內湖路一段285巷27弄7之1號', 25.08325370, 121.56728860, '早午餐', '早午餐 建議現場吃，皮才會乾乾酥酥，這天外帶變溼軟版，口味也還是不錯~\n蛋餅很大，份量十足。建議老闆可以再把餅皮改薄一點，會更可口!起司蔬菜蛋餅：蛋餅皮比較大，相比之下起司顯得單薄許多，幾乎吃不到起司的感覺。蔬菜部份使用高麗菜，但可能沒有考慮高麗菜會出水的問題，再加上高麗菜沒有先處理過，出水後又再度沖淡起司的味道。整體來說除非加入醬油膏，不然很像在吃被熱餅皮包住的生菜沙拉。初訪點了基本盤蔬菜蛋餅，因為厚度使得蛋餅只能切成四塊，外皮酥脆有淡淡麵香，內餡的蔬菜甜脆，搭上醬油膏很好吃～另外外表雖然看起來是個外帶店，裡面卻有位子且提供不鏽鋼筷、瓷盤，內用也很方便哦👍🏼餐點很用心，服務親切，讓不喜歡吃菜的人都不會排斥的蔬菜蛋餅大推蔬菜蛋餅好吃！份量也很大份！小肉豆很好吃！', 4.9, 48, 235, 'jRc195y@gmail.com', '0955 524 648', 4, '["星期一: 06:30 \\u2013 13:00","星期二: 06:30 \\u2013 13:00","星期三: 06:30 \\u2013 13:00","星期四: 06:30 \\u2013 13:00","星期五: 06:30 \\u2013 13:00","星期六: 06:30 \\u2013 13:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.46 下午', '2021-06-16 07.38.46 下午', 48, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 311, 1, '14314780', '這樣生活咖啡Vida Asi Cafe-內湖新開幕咖啡廳|人氣咖啡廳|平價早午餐|不限時咖啡廳|特色簡餐|商務包場咖啡廳|地中海健康飲食', '114', '台北市', '內湖區內湖路一段411巷10號', 25.08248080, 121.57102800, '早午餐', '早午餐 空間寬敞，不限時，有很多插座，還有窗台位\n但窗台位感覺設計很不良，高度過高，無法潰腳，做五分鐘就腰酸了\n飲料滿普，奶茶很淡\n食材普普通通，不難吃也不會到好吃\n最重要的是，沒有內用杯！還附吸管 嘖嘖店家主打的鮮奶吐司是真的好吃\n烤得外表微酥裡面還是很柔軟 不會太乾 配上果醬或奶油會更香\n\n整份套餐來講\n菇類的香味沒被帶出來 再熟一點應該會更好\n然後番茄...很看個人 我是沒一次吃完啦orz\n炒蛋有時候有點碎 沒什麼鹹味 可以自己再加點調味料\n主菜的部分不管是魚還是雞都還可以 不會太乾 份量適中\n\n飲料的部分表現比較普通 奶茶蠻淡的 香蕉牛奶是淡淡的香蕉味 甜度大概是微糖的程度\n\n整體用餐環境很舒適 不限時間 有很多插座 且待起來沒有壓力\n\n小缺點是\n樓梯間有點壁癌跟釘子突出 處理一下感覺會比較衛生安全\n另外如果只是想吃吐司的話 搭配飲料點起來會比較不划算（飲料單點價格蠻高的）有點可惜沒有吐司跟飲料的搭配CP值很高的咖啡廳，餐點$200以下，早午餐+20元就能任選飲料。現在吐司還能換小菠蘿，菠蘿麵包很甜，不過不會太油；烤吐司不會太乾，不刮舌很好吃！烤蕃茄即使有黑胡椒，也蓋不過蕃茄本身的味道；炒菇蠻喜歡的。蝦子和雞肉覺得普通。旁邊還有醬料可以自己取用\n拿鐵奶味比較重，不過咖啡不是淡。這裡不限時，還有插頭可以用，很不錯的地方2021/05/11初訪：烤魚咖哩飯味道很不錯，每個座位都有插座很貼心，內用不限時間，舒適放鬆的好地方，值得再訪。亮點: 不限時 有插座 有wifi，餐點好吃份量大 而且價位相當合理。除了早午餐和各式吐司外，還有沙拉和現打果汁，選擇算相當豐富。老闆娘態度很好，點餐時剛好遇到在教新人，雖然新人不熟但老闆娘也是很有耐心的教。\n欠點: 廁所位於二樓，對行動不便的人比較不友善。\n\n付款: 現金/line pay/街口\n發票: 電子發票\n服務費: 無\n\n用餐時間: 2021.03', 4.4, 90, 396, 'R8uaHoTq@gmail.com', '02 2627 7537', 4, '["星期一: 08:00 \\u2013 20:00","星期二: 08:00 \\u2013 20:00","星期三: 08:00 \\u2013 20:00","星期四: 08:00 \\u2013 20:00","星期五: 08:00 \\u2013 20:00","星期六: 09:00 \\u2013 20:00","星期日: 09:00 \\u2013 20:00"]', 'https://www.facebook.com/VidaAsiCafe/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.47 下午', '2021-06-16 07.38.47 下午', 90, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 312, 1, '78786557', 'THE DOOR樂多', '10491', '台北市', '中山區大直街57巷26號', 25.08330900, 121.54774000, '早午餐', '早午餐 氣氛悠閒食物非常有誠意。點了卜派蛋捲，蘑菇和番茄丁用料大方，起司非常濃厚流洩整盤，飲料是熱紅茶熱美式咖啡無限自取還附上一小杯新鮮鳳梨果汁。水果居然有草莓，非常滿足！但下次可能會希望吐司可以烤焦香一點。\n\n老闆酷酷的，第一時間就告知餐點要等50分鐘，（因為老闆一個人搞定全場6桌），但還是很細心招呼待餐中的客人。漢堡份量充足好吃，老闆人也不錯。\n不過只有老闆一個人，沒時間等就不要來吃吧。樂多的好,真的是不用多說。\n先說優點好了，東西很多,絕對吃得飽。食材一吃就是新鮮好貨，沒再跟你開玩笑的美味。\n尤其是薯塊，請你一定要加點！這輩子沒吃過這麼好吃的薯塊！輕脆不硬、濕潤不軟、美味不油膩！\n配著麵包吃的鮮奶油是老闆親自手打的，完全沒有市售的難以入口，吃不夠你還可以再跟老闆要。一葉知秋，從這種小細節體現老闆的用心與堅持。\n整間店位子算多,水、咖啡、紅茶無限量供應。夏天來不會熱，冬天來不會冷。阿對了,還有室外座位可以選擇哦 ！\n\n缺點，人很多的話要等很久，因為只有老闆一個人，沒有其他工讀生。\n還有，老闆很有個性，你會覺得他服務態度不好但其實沒有，他只是很專注地在做出美味食物！互相體諒,樂處多多。\n\n總而言之，樂多不是那種網美店，有著浮誇的裝潢與華而不實的價格。他有著手作的溫暖跟好吃的味道。非常適合一個人來或是戀侶一同來，如果你是真心想要吃好吃的早午餐,想讓價格與美味成正比，我想非樂多莫屬了吧！料好實在、當地店家。老闆好棒棒～^_^\n\n餐點真的很大份 吃的很飽！價格又很實惠\n也可以久坐\n很用心還會端水果給我們！\n連水果都好甜哦', 4.2, 197, 827, 'F3HAsNBUq@gmail.com', '02 8509 8786', 2, '["星期一: 10:00 \\u2013 17:00","星期二: 10:00 \\u2013 17:00","星期三: 休息","星期四: 10:00 \\u2013 17:00","星期五: 10:00 \\u2013 17:00","星期六: 10:00 \\u2013 17:00","星期日: 10:00 \\u2013 18:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.38.49 下午', '2021-06-16 07.38.49 下午', 197, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 313, 1, '24418865', '麥味登 早午餐 ( 內湖園區店 )', '114', '台北市', '內湖區內湖路一段323巷30號', 25.08427100, 121.56798610, '早午餐', '早午餐 早上用外送平台叫早餐，點了巧克力吐司，外觀看起來沒問題，但咬下去全部都是焦的，原來烤焦的部分全部都用巧克力醬抹起來，真的慘，毀人清早就這麼簡單，my warm day? my burning day同時有內用外送單跟App點餐，雖然都會等比較久但品質穩定店員態度佳。很喜歡吃麥味登的健康好食嫩雞餐，又嫩又香。\n平時都在北投奇岩店吃，雞胸肉有兩大片，價格一百出頭我覺得沒問題👌\n但今天在胖達上點本間分店的，送來的雞胸小的可憐，像是一片切四份的感覺，口感還超柴🤭\n以連鎖分店來說，口味口感不討論，但份量應該要一樣的\n為求公正，這兩天會再點一次，拍照兩間分店比對圖附上來～☺️☺️以下如實回報給店家:\n週日上午去用餐時, 店面明顯人手不足，導致連飲料都等快20分鐘才上，餐點的部份早午餐系列,兩塊培根、一點炒蛋、一搓生菜及兩片小麵包就要120元,體驗不佳，故希望店家可以了解服務業做吃的很辛苦，大家能體諒，但要等30分\n鐘可以先說一聲，另外接外送foodpanda ubereats沒有不對，可是做餐點時間要分配好，而不是說今天外送比較多，現場的要等比較久。', 4, 164, 656, '7Hm3J9sQTu@gmail.com', '02 2627 5822', 4, '["星期一: 07:00 \\u2013 14:30","星期二: 07:00 \\u2013 14:30","星期三: 07:00 \\u2013 14:30","星期四: 07:00 \\u2013 14:30","星期五: 07:00 \\u2013 14:30","星期六: 07:00 \\u2013 14:30","星期日: 07:00 \\u2013 14:30"]', 'https://www.facebook.com/%E9%BA%A5%E5%91%B3%E7%99%BB%E5%85%A7%E6%B9%96%E5%9C%92%E5%8D%80%E5%BA%97-413729472803036/', '0.jpg', '[0.jpg]', '2021-06-16 07.38.51 下午', '2021-06-16 07.38.51 下午', 164, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 314, 1, '22142141', 'Hao & TOAST 豪吐司', '11449', '台北市', '內湖區港華街127號', 25.08390860, 121.57254310, '早午餐', '早午餐 都是極為簡單的配料\n價格還偏高\n吃起來口感很乾\nCp值不ok雖然咔啦雞是炸的但是整體吃起來清爽不油膩，店面不大但是很乾淨明亮舒適，硬要說的缺點就是有點小貴XD但是是會想再訪的店！第一次偶然吃到親戚買的吐司🍞～～超香超軟有種濃郁的香氣，單吃就很好吃，小孩也很喜歡！沒想到過幾天四歲的兒子就討著要吃這間的！乾淨..好吃..待客親切\n看過內湖餐桌環境乾淨的兩家店\n（豪吐司、尬一鍋）\n值得推薦..不得不推薦手做吐司🍞\n超級Q彈香嫩，有次同事吃了，讚不絕口，每次訂購都是好幾條，當天出爐的新鮮的吐司🍞同事一口氣可以吃完快一條😂\n我也超喜歡老闆娘親自做的蛋沙拉，好香好綿很清爽～\n還有她們的三明治香軟綿密超讚👍\n很推薦豪吐司👍', 4.7, 73, 343, 'EhH0G01MvR@gmail.com', '0978 699 402', 4, '["星期一: 休息","星期二: 07:00 \\u2013 14:30","星期三: 07:00 \\u2013 14:30","星期四: 07:00 \\u2013 14:30","星期五: 07:00 \\u2013 14:30","星期六: 07:00 \\u2013 14:30","星期日: 07:00 \\u2013 14:30"]', 'https://www.facebook.com/Hao-TOAST-%E8%B1%AA%E5%90%90%E5%8F%B8-914219492288493/?modal=admin_todo_tour', '0.jpg', '[0.jpg]', '2021-06-16 07.38.52 下午', '2021-06-16 07.38.52 下午', 73, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 315, 1, '93978335', 'Hearty哈媞手作三明治', '114', '台北市', '內湖區內湖路一段591巷15弄1號', 25.08161470, 121.57355600, '早午餐', '早午餐 【不只美更美味】\n這間跟韓國很紅的ixxxc很像\n以為會是偏重口味的餐點\n但是個人覺得整體搭配的非常剛好\n尤其是他的嫩蛋 非常鬆軟 蛋香味也有\n搭配美乃滋跟起士更加分\n肉片醃的入味 不會柴\n吐司是厚片對切 吃起來非常鬆軟\n表皮還烤脆脆的\n吃起來沒有像韓國那間那樣油膩\n是非常有自己風格的早餐\n配餐小肉豆口感蠻特別的 覺得新鮮\n德式香腸煎的恰恰 很好吃\n薯條跟雞塊稍嫌普通\n飲料也是普普 其實可以主攻三明治就好\n重點是一份網美三明治的價錢\n大概都60-70配飲料差不多100上下\n跟其他魍魅店比起來 CP值很高\n會想收在口袋名單裡面的好店➁⓿➁➀⓿➀➁➈\n\n✪ (招牌)花醬培根起司蛋吐司 $65\n✪ (獨家醬汁)照燒豬肉起司蛋吐司 $65\n\n去韓國吃過他們的奶油鐵板三明治後\n一直念念不忘\n好在後來台灣也開始有\n韓國連鎖的ISSAC奶油吐司～\n不用去韓國也可以吃到\n偶而一天在內湖科學園區附近\n發現這間➡️hearty哈媞手作三明治⬅️\n@hearty_tw\n主要賣的吐司類有甜的、也有鹹的可以選擇\n\n用一個站立式的紙盒裝著的吐司上桌囉～\n吐司外皮有先煎過\n但咬起來還是鬆鬆軟軟的\n配上微鹹的厚培根\n還有吃起來超滑嫩的炒蛋\n花生醬不是直接抹在吐司上喔\n而是直接淋在豐富的配料上\n咬下第一口時花生醬的\n濃香味直接爆在嘴巴裡🤤\n\n另一個點的是味道比較重的照燒豬肉\n裡面一樣有爆炸多的軟嫩炒蛋\n搭配上起司片，吃起來真的是很滿足耶早上6、7點音樂就開特大聲\n家中小孩都被吵醒了\n下樓發現是這家在放音樂\n可以麻煩小聲點嗎？公司下午茶訂購,花生培根,BBQ牛肉,辣味雞都很美味!送來的時候包裝非常完善,吐司鬆軟好吃!東西好吃、飲料也不錯，尤其是現煎蛋熱呼呼帶來滿滿的幸福感，對外帶族群包裝的很週到。當然其他評論如CP值不高等等也對，不過市面上早餐選擇很多，個人覺得還是值得願意多花一些些錢的消費者一試。\n店內座位區很小，實際坐下來用餐的油煙我覺得還好，最後建議飲料希望可以有不加糖的選擇。', 4.2, 141, 592, 'OSthvsnUY@gmail.com', '0933 223 602', 4, '["星期一: 08:00 \\u2013 13:00","星期二: 08:00 \\u2013 13:00","星期三: 08:00 \\u2013 13:00","星期四: 08:00 \\u2013 13:00","星期五: 08:00 \\u2013 13:00","星期六: 08:00 \\u2013 13:00","星期日: 休息"]', 'https://www.facebook.com/heartytw', '0.jpg', '[0.jpg]', '2021-06-16 07.38.54 下午', '2021-06-16 07.38.54 下午', 141, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 316, 1, '85278322', 'b.c. Brunch', '10491', '台北市', '中山區大直街70號', 25.08378970, 121.54620390, '早午餐', '早午餐 ', 5, 2, 10, 'Dlk9YpC@gmail.com', '0937 533 026', 4, '["星期一: 07:30 \\u2013 17:00","星期二: 07:30 \\u2013 17:00","星期三: 07:30 \\u2013 17:00","星期四: 08:30 \\u2013 17:00","星期五: 08:30 \\u2013 17:00","星期六: 10:00 \\u2013 14:00","星期日: 休息"]', 'http://evo789.wix.com/bcbrunch', '', '', '2021-06-16 07.39.14 下午', '2021-06-16 07.39.14 下午', 2, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 317, 1, '17400143', '義沐 Eat MU 台北大直店', '10466', '台北市', '中山區敬業三路123號6F', 25.08108460, 121.55546430, '早午餐', '早午餐 防疫到店自取總金額66折（甜點、沙拉、薄餅、套餐不供應，滿500送甜點）有空來防疫辛苦支撐的店家吧餐點算好吃 會給4顆星。\n店員教育訓練不足。三組家庭去吃，另外兩家都吃完我們主餐都沒上，請服務生確認，他都說好，但是我看他完全沒做確認這件事，也沒回復我狀況如何，請他再確認，確認我完跟我說，他還是沒動作。我們就在旁邊一直空等，直到我找了值班主任。\n當天值班主任臨場處理跟態度，我給5分，請一定要好好留住這個員工，至少她先同理顧客感受，態度溫柔，面對我們的不開心沒有逃避，本來我是絕對不會推薦朋友去吃，現在我會說先確認誰是當天值班主任再去吃。謝謝溫柔的妳讓我們全家還是可以保持還可以的心情走出店門。口味ㄧ般般，菜單上的東西感覺上沒什麼變化主食的肉類就是鮭魚、菲力、蛤蠣、干貝然後配紅綠白醬、或各色燉飯的變化，店內乾淨，服務不錯當天我們這桌的男服務人員很棒！點餐後確認每個人的餐點都正確。以連鎖餐飲跟價位來說，餐點也有水準，友人點的義大利麵蛤蜊非常大又新鮮，我的菲力溫沙拉也出乎意料好吃，份量看起來不大但是吃完很有飽足感早午餐的部分歐姆蛋套餐就不能有法式吐司，法式吐司的套餐就不能有歐姆蛋，要同時有蛋有吐司就只能點班尼迪克蛋的套餐，但是蛋也不能改歐姆蛋，蛋只能選半熟或是全熟，就是不能同時有法式吐司跟歐姆蛋的選擇，要加價單點也不行', 4.1, 227, 931, 'rBMbtiv2ju@gmail.com', '02 7725 9660', 4, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 12:00 \\u2013 20:00"]', 'http://www.mucafe.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.15 下午', '2021-06-16 07.39.15 下午', 227, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 318, 1, '56343635', '文湖麥味登', '114', '台北市', '內湖區文湖街49號', 25.08672900, 121.56202200, '早午餐', '早午餐 同樣是麥味登，北安的很好。這家就難吃到暴斃。鬆餅爆甜，培根爆鹹。\n湯味除了調味料粉就是太白粉勾芡。\n可頌燒焦，蛋沙拉超少，芥末味超多。\n連照配方做都不會！太難吃了！吳寶疫留言，再來備註一下。以免以後又踩雷。好食雞肉餐 沙拉不能換？別間就可以換….\n豆漿規定一定要大杯…..\n別間就可以點中杯…..\n不知道這間在幹嘛⋯⋯\n補充…..\n好食雞肉的肉 真的柴到不行…..\n不推！文湖麥味登真的很優，可以用手機點餐，還可以集點數換其他不同餐點，幾乎天天吃。等很久後發現\n餐點key錯⋯\n還不能退點餐人員的語氣讓人不敢恭維\n這麼沒耐性沒禮貌可以不用做服務業沒關係\n又沒人逼你= =', 3.7, 348, 1288, 'yn0cAJk@gmail.com', '02 8751 6996', 1, '["星期一: 07:00 \\u2013 15:00","星期二: 07:00 \\u2013 15:00","星期三: 07:00 \\u2013 15:00","星期四: 07:00 \\u2013 15:00","星期五: 07:00 \\u2013 15:00","星期六: 07:00 \\u2013 15:00","星期日: 07:00 \\u2013 15:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.39.16 下午', '2021-06-16 07.39.16 下午', 348, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 319, 1, '67277148', '李三食堂_內湖店', '114', '台北市', '內湖區內湖路一段120巷13號1F', 25.08238400, 121.56279280, '早午餐', '早午餐 大樓外觀一般般\n店內裝潢滿網美風格\n女生應該會滿喜歡的\n\n上餐速度滿快的\n便當看起來滿精緻，份量也不小\n整體來說還不錯\n我午餐又多了一個新的選擇了大樓外觀蠻一般\n但走進來餐廳很舒服，裝潢不錯\n扭轉餐盒還蠻有特色的\n中午在這邊休息一下很舒服公出經過"李三食堂"，名字很特別，剛好有在賣咖啡\n買了試試，意外好喝店面乾淨明亮，剛開幕的新店！\n控肉滷到軟嫩入味，配菜歐姆蛋有cheese增添滑嫩口感。\n個人覺得便當CP值高😋公司旁邊新的店，Bagel 蠻好吃的，想起在美國出差的日子，環境還不錯。', 4.7, 6, 28, 'j0AgkfI@gmail.com', '02 2657 3670', 4, '["星期一: 08:30 \\u2013 18:00","星期二: 08:30 \\u2013 18:00","星期三: 08:30 \\u2013 18:00","星期四: 08:30 \\u2013 18:00","星期五: 08:30 \\u2013 18:00","星期六: 休息","星期日: 休息"]', 'https://brunch-restaurant-2096.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-16 07.39.20 下午', '2021-06-16 07.39.20 下午', 6, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 320, 1, '07154587', '鮮打卡早餐鐵板燒', '114', '台北市', '內湖區基湖路35巷2弄', 25.07883040, 121.56593720, '早午餐', '早午餐 個人喜歡肉片>雞腿，便當青菜很多\n早餐的鐵板麵也很好吃，是醬汁收的比較乾的類型含醬汁土司/堡太濕，十分鐘後打開看到會很冏。便宜 、出餐快，早餐或午餐都不錯兩位老闆~每天都早起親自去市場挑選蔬果\n菜色豐富 食品安全 衛生合格\n對食品有高標準的把關 讓人吃的安心\n如果你是每天常為早餐要吃什麼而煩惱那你因該來試試他們的每日特餐 餐點不只每日不同 價錢也很便宜 而且不只每天都有不一樣餐點 每逢節日還會出平常吃不到的餐點 重點是價格便宜便宜又很好吃～～每天早餐套餐都不一樣，午餐賣的鐵板燒最喜歡吃乾煎雞腿排，一份只要80！推推推～～～', 4.3, 24, 103, '3RKB84QJ@gmail.com', '0982 050 244', 4, '["星期一: 06:30 \\u2013 13:00","星期二: 06:30 \\u2013 13:00","星期三: 06:30 \\u2013 13:00","星期四: 06:30 \\u2013 13:00","星期五: 06:30 \\u2013 13:00","星期六: 休息","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.39.21 下午', '2021-06-16 07.39.21 下午', 24, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 321, 1, '51060629', 'Lazy Point Restaurant & Bar', '11493', '台北市', '內湖區洲子街78號', 25.07956940, 121.57185930, '早午餐', '早午餐 Mars服務親切、熱忱氣氛佳，很適合好友相聚🍴🥂\n價格人均900~1200元，費用稍高\n愛松露的人可以嘗試他們的松露餐點🍝\n食物份量不多，大胃王可能會吃不飽，正所謂屬於吃氣氛、吃精緻🤭\n現場裝潢時尚適合偽網美們拍照🤪🤪\n\n最愛推薦❤❤❤\n開胃➡️鮭魚沙拉、蘑菇松露卡布奇諾湯\n主餐➡️牛小排嫩多汁，戰俘豬排份量足\n小點➡️松露薯條、甜辣炸雞翅\n甜點➡️香濃提拉米蘇(須多人分食)\n\n🆘️消費日期：110.04.22（晚）想特別提醒二樓的服務員有待加強改善笑容及服務熱忱，不然可惜了廚房好手藝，畢竟外場在消費者心中評價也佔重要的一環。老闆或是經理人應該要注意才是😥😥😥（內湖洲子街）東西好吃不雷！星星等級的意義\n5 顆星\t超讚\n4 顆星\t喜歡\n3 顆星\t還好\n2 顆星\t不喜歡\n1 顆星\t很不喜歡\n\n環境⭐️⭐️⭐️⭐️\n有空間感的設計不過有些角落有詭異的柱子在整層樓滿靠近正中間的地方很影響視覺。椅子的位置幾乎都與桌腳碰撞，要慎選傢俱和擺放方式。不然坐起來會比路邊攤還不舒服。\n\n味道⭐️⭐️⭐️\n以餐酒館的味道來說算是有中間到偏中上的水準，不過不是每一道餐點都好吃，點菜還是要稍微挑一下。擺盤都滿好看，但是拿來擺盤的配角味道都不是很好吃。\n\n服務⭐️⭐️⭐️⭐️\n需要增加人力。客人人數與服務人員的人數比例會讓他們很辛苦的。松露薯條感覺不是現炸的 口感溫溫的 不推\n酒不好喝\n1杯點琴酒基底感覺像Vodka 然後超甜....\n1杯裡面只有小黃瓜的味道？？店員一開始還介紹錯誤\n\n環境不錯 沒吃主餐 吃下來就是這樣的分數\n跟朋友點兩杯酒跟薯條 平均一人要$600....\n\n補充：一開始想換到3樓露天酒吧 店員說客滿 後來沒多久店員人很好的幫我們換上去 結果吧台有大概6-7個空位 :)', 4.3, 741, 3186, '1FVZoEYt@gmail.com', '02 8751 5509', 3, '["星期一: 11:30 \\u2013 22:30","星期二: 11:30 \\u2013 22:30","星期三: 11:30 \\u2013 22:30","星期四: 11:30 \\u2013 22:30","星期五: 11:30 \\u2013 01:00","星期六: 10:00 \\u2013 01:00","星期日: 10:00 \\u2013 22:30"]', 'http://www.lazypoint-taipei.com/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.22 下午', '2021-06-16 07.39.22 下午', 741, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 322, 1, '45351168', '北安町', '104', '台北市', '中山區北安路817-1號', 25.08503800, 121.55601400, '居酒屋', '居酒屋 雞軟骨、炒水蓮、烤牛肋條、明太子山藥、蛤蜊湯、鹽烤雞腿肉串，每一道都相當好吃，店家也很親切貼心。菜是還不錯的但2位服務妹妹太沒笑臉跟溫度了，建議老闆別讓沒溫度的服務人員毀了你辛苦成立的餐廳\n還有那麼久沒出菜，但菜來卻是溫溫的，少了之前剛做好的燙口感，應該是做好一陣子卻不上菜是為何？\n那1顆星是給你的服務妹妹的下班喝兩杯的好地方，燒烤炸物料理都好吃，搭配Orion生啤真的太讚了酥炸雞膝骨🐔拜託點起來！夭壽好吃\n吃完啤酒喝下去，只能說爽🤤\n\n松阪豬炒水蓮🥬整道菜很有鍋氣、讓人一吃想再吃\n\n鹽烤雞腿肉串🍗肉軟嫩多汁，雞皮烤的香脆\n\n月見燒肉飯🐷肉烤的軟嫩、醬汁也給的很足，但米飯偏濕潤個人比較不愛\n\n雞白湯30塊超大碗的～\n吃完來點熱湯直接達到幸福的頂端🤤菜色好，份量夠\n兩個人點這些，吃的很飽😆', 4.4, 313, 1377, 'gseP7oQTU@gmail.com', '02 2533 5601', 2, '["星期一: 休息","星期二: 18:00 \\u2013 23:30","星期三: 18:00 \\u2013 23:30","星期四: 18:00 \\u2013 23:30","星期五: 18:00 \\u2013 00:30","星期六: 18:00 \\u2013 00:30","星期日: 18:00 \\u2013 22:30"]', 'https://www.facebook.com/beianting/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.24 下午', '2021-06-16 07.39.24 下午', 313, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 323, 1, '69201202', '一鷺串燒居酒屋（大直旗艦店）', '10491', '台北市', '中山區植福路217號', 25.08307900, 121.56091300, '居酒屋', '居酒屋 兩人套餐實惠又好吃，真的選對餐廳了我是壽星\n遊戲一直贏\n很開心服務生服務超棒，食物也好吃，讚！！！燒烤好吃，店內自產品牌啤酒真棒，值得嚐鮮!店員服務態度優秀，食物非常美味。', 4.8, 2826, 13565, 'ISboQz3@gmail.com', '02 8501 5885', 3, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 12:00 \\u2013 20:00"]', 'http://www.yakitori.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.25 下午', '2021-06-16 07.39.25 下午', 2826, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 324, 1, '46291718', '醉燈食居大直店 - 居酒屋 燒烤 宵夜美食 平價 小酌聚餐 朋友聚會 推薦 串燒 人氣 日式料理 啤 酒燒酒調酒', '104', '台北市', '中山區北安路726-5號', 25.08542000, 121.55149200, '居酒屋', '居酒屋 車位多好停車，不過位置有點不好找！店內位置比西湖店多，但還是建議來之前打個電話問問看！推薦大阪燒炒麵，每次必點食材不錯，老闆燒烤的也很到胃，是三五好友一起吃好吃滿吃飽飽的地點，不排除小酌一番話，也可以一起舉杯大聲說，乾杯。超好吃，CP質高，親朋好友一起來，可以嘗到不一樣的美味。特別是下酒菜十分對味，一盤可以多喝好幾杯。老闆人很親切，上菜速度雖然要加快些，但慢工出細活，好吃的東西願意等一下。座位不多，出餐快而且好吃，氣氛很好不吵雜好吃😋 服務很棒 ！ 推薦宵夜可以來這用餐🍴', 4.8, 34, 163, '3U78Y3i@gmail.com', '02 2532 4098', 4, '["星期一: 18:00 \\u2013 01:00","星期二: 18:00 \\u2013 01:00","星期三: 18:00 \\u2013 01:00","星期四: 18:00 \\u2013 01:00","星期五: 18:00 \\u2013 01:00","星期六: 18:00 \\u2013 01:00","星期日: 休息"]', 'https://www.facebook.com/draydangshitone/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.28 下午', '2021-06-16 07.39.28 下午', 34, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 325, 1, '62983342', '醉燈食居-西湖店', '114', '台北市', '內湖區內湖路一段285巷69弄3號', 25.08491340, 121.56703070, '居酒屋', '居酒屋 外送串燒都穿破袋子了\n現在這時機超不衛生的吧⋯⋯\n\n雞肉串酸掉了…\n牛肉烤超柴…\n只剩大阪燒炒麵還可以吃第一次造訪。烤牛肋條，牛肉糯米椒，炸豆腐，炒水蓮都不錯，服務生的態度挺好的。店內座位不多，沒有事先訂位，還好很早去，所以還有2人的吧台可以坐。適合加班後自己，或跟朋友同事一起來的地方。還只吃過兩次，喜歡炒水蓮跟炸雞軟骨。適合三五好友聚餐小酌一下，料理口味都算不錯，cp值佳，店員服務態度也好～每一道料理都還蠻好吃\n且平價\n唯一美中不足就是高麗菜都是菜梗😓', 4.4, 274, 1206, 'rJqxICHf8@gmail.com', '02 8751 1765', 4, '["星期一: 18:00 \\u2013 00:00","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/zueideng/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.29 下午', '2021-06-16 07.39.29 下午', 274, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 326, 1, '18334460', '雷鳥居酒屋', '114', '台北市', '內湖區瑞光路284號', 25.07588550, 121.57520340, '居酒屋', '居酒屋 在Google 看到評價很好，還有4.5顆星\n但真的只能說是口味不合吧？ 呵呵\n被安排在吧台冰箱旁邊，燈光很不舒服，並且七味粉也沒提供，是我看到別桌有，才向服務人員詢問\n牛肉燒烤的醬都太甜，很膩\n鹽烤雞肉串，是沒加鹽，還是要自己加？\n香菇味道完全吃不出來，只有醬的甜跟油膩\n炸雞從外面的麵衣到口感 調味 都非常不好吃服務態度佳👍🏻\n整體裝潢也Ok👌🏻\n推～鹽烤雞肉蔥串、蔥鹽PRIME牛小排串💯6年前來過忘了是不是同名, 也忘記味道了, 沒想到今天讓我吃到生平最難吃的居酒屋! 這次真的不會忘了!最愛蔥鹽牛小排串!!!  一夜干也烤得很好!! 最愛小鼓梅酒黑醋栗烏龍茶酒真的好喝  下次還要來喝', 4.6, 392, 1803, 'KJc6BYdj@gmail.com', '02 2627 7173', 4, '["星期一: 18:00 \\u2013 00:00","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/%E9%9B%B7%E9%B3%A5-%E5%B1%85%E9%85%92%E5%B1%8B-169807433690590/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.30 下午', '2021-06-16 07.39.30 下午', 392, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 327, 1, '75812514', '一番星拉麵', '114', '台北市', '2樓, 內湖路一段285號 101攤位, 內湖區', 25.08241730, 121.56649450, '拉麵', '拉麵 ▎店家：拉麵一番星\n▎區域／鄰近地點：台北市／西湖捷運站西湖市場2F\n▎用餐時間：2021/1/21  18:45\n▎排隊狀況：無，開放空間需先找位置\n▎拉麵名稱／價格：味噌拌麵／NT.150\n▎配置：預設\n\n好一陣子沒有來一番星了，以前的營業時間都要當天看臉書才能確認，現在看起來都有固定的營業時間，現場除了老闆外還多了個店員。\n原本想吃限量的能量拌麵，到現場後才發現是中午限定，反正拌麵的品項都沒吃過，就點了味噌拌麵。\n\n味噌拌麵 配置：叉燒塊、高麗菜、青蔥、溫泉蛋。\n\n以拌麵來說，這醬汁的比例算很高，看起來油亮亮的，給人比較油膩的先入為主，整體份量很多，一點也不像200元內的拉麵。\n把溫泉蛋戳破和高麗菜一起拌開後享用，一咬下去，天啊！這也太好吃了吧！溫泉蛋拌開之後帶來口感超棒，有點像是壽喜燒沾醬的綿密感，味噌的味道並不明顯，尾韻的時候才會出現。\n高麗菜清爽又甜，適時的起到解膩的作用。\n鳥尾製麵的麵體吸附醬汁能力很好，彈牙有嚼勁，搭配有彈性的叉燒塊讓人非常滿足！\n\n滿分10分的話我會給10分！真的有夠好吃！\n怎麼也想不通，這些看似簡單的食材放在一起，可以讓人忍不住一口接一口，真的是平價美食啊！\n-------------------------------------------------------------------\n隱藏在東湖市場二樓的美食，日本老闆很可愛，剛好遇到在試賣新的雞魚白湯，道地的日式口味，很接近麵屋一燈的口感，但一碗才150而已！\n喜歡日式重口味的人一定會喜歡一番星！此生最愛的拉麵\n我愛一番星❤️❤️❤️吃完後感動覺得自己在日本了🤪🤪魚介拉麵（SP)，使用較粗的低醣麵，Q彈有勁，還會帶一點特殊的香氣。地點很好，也不怕沒位置，每天還有不同的SP，對想吃拉麵的內科上班族來說是一大福音！大推！樸素而美好的一家拉麵店，感謝一直包容我使用自備餐具的老闆，疫情期間還有零星的人來外帶，希望大家都能挺過疫情', 4.6, 327, 1504, 'RMg7olN9@gmail.com', '02-38848926', 4, '["星期一: 11:30 \\u2013 14:00, 17:00 \\u2013 19:00","星期二: 11:30 \\u2013 14:00, 17:00 \\u2013 19:00","星期三: 11:30 \\u2013 14:00, 17:00 \\u2013 19:00","星期四: 11:30 \\u2013 14:00, 17:00 \\u2013 19:00","星期五: 11:30 \\u2013 14:00, 17:00 \\u2013 19:00","星期六: 休息","星期日: 休息"]', 'https://www.facebook.com/efanxing/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.33 下午', '2021-06-16 07.39.33 下午', 327, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 328, 1, '10024462', '開漳拉麵', '114', '台北市', '內湖區內湖路一段323巷4弄8號', 25.08273870, 121.56748640, '拉麵', '拉麵 好吃 幾年前店開在環山路上時就很喜歡這間店的調味 老闆願意回西湖開店 我還滿開心的拉麵很有日本味真的很好吃，價格又實在，年輕老闆親切待客，會常去吃👍一直都只是經過，昨天下午13:30想說還沒吃午餐，就利用烘衣服的時候點了一碗味增拉麵，\n店面非常乾淨整潔，老闆是兩個年輕的大男孩，非常的熱情❤️，店裡還有檸檬冰水可以喝，\n因為休息時間是14:00為了不影響接續的工作，我減少了用餐的時間，雖然兩個大男孩直說：沒關係，我笑笑的說：我衣服也要烘乾了，差不多了，麵真的很好吃喔😋\nPs:貼心小叮嚀！喜歡味增又怕鹹可以跟老闆說或是在點餐的菜單備註一下！價格不合理，服務親切、發自內心的笑容，讓人感到非常開心。好吃 座位很乾淨 每一組客人離開都會用酒精消毒芝麻香辣拉麵(乾)$100，內有特調辣醬（對於嗜辣的人完全感受不到辣度🤣）、一顆溏心蛋、切丁的叉燒✚清燙川耳川耳&高麗菜，吃完充滿空虛，適合不太餓的食客。\n\n給一整顆溏心蛋🍳，真的超級大器，但溏心蛋的定義應該是蛋黃要呈現果凍感，而非半熟蛋...未在預期內會有的蛋液瞬間傾瀉，影響了麵的味道，說實話半熟蛋不是每個人能接受的😥。', 3.9, 148, 577, 'ttzOH6Ss@gmail.com', '02-92672385', 4, '', 'https://m.facebook.com/%E9%96%8B%E6%BC%B3%E6%8B%89%E9%BA%B5-2179376538801341/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.34 下午', '2021-06-16 07.39.34 下午', 148, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 329, 1, '90885224', '勝拉麵', '10491', '台北市', '中山區北安路621巷46號', 25.08285500, 121.54658800, '拉麵', '拉麵 首先呢我們那天坐在位置上等了20幾分鐘發現拉麵還沒來我想說可能這家店出餐速度偏慢吧 但想說算了可能真的忙 結果又過了10來分鐘我發現比我們晚進來的一群人還有一個外國人都已經有麵可以吃了 整個龜懶趴火 雖然我沒有懶趴但有火 難道我排隊是排身體健康做公益的嗎 🥺我們就跟店員說如果我們的餐還沒做的話就沒關係不用了 雖然最後店員也很有誠意的處理這件事情也說明原因了 但還是覺得用餐體驗很差嗚嗚 再說我點的麵也不是我的type（赤豚）那坨紅紅的像辣辣甜甜的番茄醬（甜>辣）跟我之前吃過的日本辣泡麵也異曲同工之妙 可能這是特色吧但不一定大家都會買單 只能說$110這個價錢不高 大家還是可以去體驗看看 或許只是我運氣不好用餐體驗不佳而已 ～大直日式拉麵店，聽說是重新裝潢的老店，十足和風，小巧可愛，充分利用每個空間。今天試了招牌义燒擔擔麵及香蔥蒜蓉拉麵，適合口味較重的朋友。湯頭、麵條、义燒肉、溏心蛋都處理得非常好 ，有一定的水準。韭菜水餃和野菜滋味濃郁，可以點套餐試試。\n#餐具也選得很好\n#適合一個人用餐肉ㄉ厚度很可以.....麵推薦硬麵！下次來吃吃看鋼絲麵好了！ 辣味擔擔飯還好 叉燒飯肉給ㄉ也算多～～～ 價格也是很平價！偶覺得很讚\n重新裝潢過ㄉ店內挺吸引人ㄉ\n湯匙太粗太深 吃飯會覺得有點不方便～\n我點的是豚骨叉燒麵 湯頭不是很濃郁的那種～但是我不喜歡太重口味 所以覺得挺好喝！（朋友說太濃的那種湯 有些反而會很膩 但是這家湯剛剛好 不會膩也不會太淡！）\n缺ㄉ一顆星是給沒有驚艷ㄉ感覺～這是淡麗醬油麻辣叉燒麵，口味偏清爽，湯頭不是走日式拉麵的濃郁路線，適合喜歡清淡風味的人重新裝修後，還是保有老闆一慣的風格特色，尤其門口的那片大窗戶，透過自然光線的滲透，讓整體空間光影更加平衡，有時又會讓人錯亂，仿佛是身在日本某古鎮吃著好吃的拉麵，真心堆推', 4.1, 240, 984, 'uzovuKlJ@gmail.com', '02 8509 7138', 1, '["星期一: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00","星期三: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 休息","星期五: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00","星期日: 11:30 \\u2013 14:00, 17:00 \\u2013 20:00"]', 'https://www.facebook.com/Chinmen/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.35 下午', '2021-06-16 07.39.35 下午', 240, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 330, 1, '39652963', '雞吉君拉麵', '114', '台北市', '內湖區環山路一段33號', 25.08621200, 121.56703380, '拉麵', '拉麵 就拉麵本身絕對給得了五顆星的推崇的，不論是濃郁夠味、喝得出用心費時熬煮的湯頭，還是入味軟嫩的叉燒肉，以及緊實彈性的拉麵體，每一樣都相當出類拔萃，非常好吃。如果是拉麵控、雞白湯愛好者建議真的可以來吃吃看。另外他們有一個加價添購叉燒飯的項目，建議也可以點來吃，雖然拉麵裡面已經有叉燒了但切丁淋醬配在飯裡面吃其實別有一番特色，也是相當推薦的。講完稱讚要來講一點批評了，我個人對於排隊不能解壓縮這件事情其實是可以認同的，但關鍵我覺得是解壓縮的定義及合理性以及告知的態度讓人覺得不太舒服。我能認同有幾個人吃就該幾個人排隊的規定，但排隊時只是其中有一人去對面超商領錢或是有人去上廁所等諸如此類被“檢查”到時都會被要求重新排到隊伍最後面。如果店家是要求到齊才可進場可能比較能接受，但類似有人去廁所這種事就被要求直接重排我是覺得其實不太合理。當然店家本來就有權樹立自己的規定或文化，這種事也是願打願挨，個人感受分享僅供參考。［食］\n雞吉君\n5⭐️\n❤️濃厚雞白湯拉麵：沒做任何的加量，剛開始上桌，看起來覺得偏少，但吃完實際上飽足感十足，正常一人食量絕對沒問題。\n常聽到推薦哪間拉麵好吃或高評價，但通常很難符合我的期待，唯獨目前雞吉君完全超乎我的想像。\n先喝第一口湯，厚實的湯頭及口味，就完全打中我的心，配上大量的碎紫洋蔥和蒜苗，整體配合的恰到好處，叉燒柔軟又入味，配著濃湯，雖沒肉的厚實，卻能感受的整體的厚實感。\n麵條是細麵，又Q又彈，在麵條的硬度上屬於適中偏軟。\n搭上碗邊的醬，帶有柚子香氣及一點嗆辣感，混合在湯裡，又將層次提升一層。\n整體來說，這是一碗個人心目中目前第一名的拉麵，每一個細節、口感及搭配的層次，都做的十分傑出，不愧是真的需要提早排隊的拉麵名店。\n#雞吉君 #濃厚雞白湯拉麵\n#schnee生活 #❤️☺😀️😑😞\n到訪📅：2021/04/292021.05.05= ★★☆☆☆\n◉推薦：雞叉燒\n◉簡評：服務佳，雞湯濃厚+柚子胡椒有點互搶\n◉環境：最內兩個位子真TMD窄，一直被隔壁撞\n◉提醒：拉麵口感偏Q~脆彈的感覺\n＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝＝\n一碗麵中有兩片雞叉燒兩片豬叉燒，豬叉燒吃起來有火腿的口感。雞湯濃厚夠燙喝起來有個好像蛤蜊濃湯的鮮(個人見解)，個人認為加上柚子胡椒與檸檬雖然可以降油解膩嘗清爽，但混入湯頭後好搶，有種想要濃郁被帶走味道~想要清爽被濃稠淹沒。建議喜歡拉麵的朋友試著先不將柚子醬混進去，但洋蔥一定要充分混合！最後兩個位置擠到煩，座椅是固定的，隔板也是固定的，但隔板根本沒有算好距離，坐在椅子正中間左手整個會在隔壁。吃個麵一直被撞，如果坐到最後倒數第二位的人乾脆不要吃了。週日六點等到快八點QQ\n我是點濃厚（標準的），其他淡麗與叉燒飯已經售完......\n湯喝起來有非常的濃稠～相當香\n肉部分有兩種，雞肉與叉燒各兩片（我的有多點叉燒所以+3）\n叉燒部分相當特別的是中間看起來有點粉紅，沒錯他是帶一點點生的叉燒\n不過因為湯有一定熱度的關係，可以自己涮一下XD\n更猛的是，我吃完這十幾分鐘，湯的熱度下降並不會很誇張，都有維持蠻剛好又不燙口的溫度\n豬肉叉燒部分也因此屬於帶一點點嚼勁的感受，感覺已經有點不像叉燒了呢（但是我覺得不錯吃\n雞肉叉燒部分呢，肉質相當細緻、軟嫩～表皮帶一點點燒過的香氣，非常令人驚艷！\n麵體本身呢，軟硬適中，屬於蠻Ｑ的，麵本身應該還是有那麼一點點蛋香，不明顯（畢竟拉麵整碗都有各種味道在互相交錯）\n配菜有蘿蔔絲、生紫洋蔥丁、萊姆片、蒜苗、柚子胡椒（碗邊綠色那個）\n蘿蔔絲起到的是增加一點微微的口感變化，讓整碗在吃的過程的多點層次\n萊姆片....我個人無感......\n蒜苗則是增加味道上變化在吃的過程有不同搭配，而且感覺相當新鮮，，也讓整碗在視覺上更佳～\n生紫洋蔥丁，味道相當新鮮，但也因此較有點刺激性，以近代比較新的日式拉麵來說是偶爾會見到的角色之一\n因為他相當新鮮的關係，在味道搭配與變化上真的有起到非常好的味道與口感的變化，讓一碗拉麵整晚吃而不覺得膩\n但是份量上對我來說給的有點多了，造成我在最後吃碗底的部分時，反而變成對湯頭有嚴重的干擾\n如果下次還有機會，我大概會想要把紫洋蔥丁分量減少三分之一或者一半吧～\n最後則是柚子胡椒，柚子胡椒這邊建議就是邊吃，然後偶爾沾一點沾一點的下去吃\n味道相當足，也起到些清爽的感覺，並不會有嗆辣感（比起來紫洋蔥丁可能還比較嗆XD\n\n整碗麵因為有不同的配菜與叉燒的關係\n儘管湯頭濃厚，卻可以在不同搭配上製造相當多口感與味覺變化\n不知道不覺就吃完整碗嘍很佩服雞吉君的工作人員，在停電的時刻，還願意堅守炎熱的廚房崗位，服務每一位想品嘗美食的客人，因此決定把第一次Google Maps評價獻給雞吉君。\n\n不知該說運氣好還是不好，點完餐要付費時，剛好停電，故榮幸地成為今日雞吉君包場的客人（？）\n\n點的是特濃雞吉君\n湯頭很鮮，也真的是濃到可以立匙\n麵條軟硬對我而言剛好\n雞胸和叉燒的調味也恰到好處\n高溫果然還是敵不過美味，吃得滿頭大汗還是很滿足\n\n和朋友都認可是一間值得再次來訪的店', 4.3, 2260, 9718, 'lBrHa6qR@gmail.com', '02 8797 8978', 2, '["星期一: 休息","星期二: 17:30 \\u2013 21:00","星期三: 17:30 \\u2013 21:00","星期四: 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00"]', 'https://m.facebook.com/%E9%B6%8F%E5%90%89%E5%90%9B-%E3%81%A8%E3%82%8A%E3%82%88%E3%81%97%E3%81%8F%E3%82%93-%E3%82%89%E3%83%BC%E3%82%81%E3%82%93%E9%9B%9E%E7%99%BD%E6%B9%AF%E5%B0%88%E9%96%80-1812208619091336/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.36 下午', '2021-06-16 07.39.36 下午', 2260, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 331, 1, '79805441', '福岡屋拉麵', '114', '台北市', '內湖區內湖路一段323巷6號', 25.08298900, 121.56790490, '拉麵', '拉麵 就是家賣便宜台式拉麵的店湯頭不錯，大約有四種，個人比較喜歡白湯。價錢約110-150分大小碗。叉燒有點硬，雞肉還不錯但有點少。店內有附冷飲。大大大推超級濃郁的\n❤白湯原味叉燒拉麵❤\n我是訂購外送的忠實顧客\n每次拿到的時候都是熱騰騰的\n肥瘦適中的叉燒也給得很大方\n希望永遠都能維持這樣的品質☺️價格普通，湯頭偏鹹，麵也可以，全都普通，較無特色，抹布臭太慢了，說等一下，快好了，等快10分鐘，餐點都沒好', 4.1, 838, 3436, '0gnXh5wfL@gmail.com', '02 8797 2595', 1, '["星期一: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期二: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期三: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期四: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期五: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期六: 11:15 \\u2013 14:00, 17:15 \\u2013 21:00","星期日: 休息"]', 'https://www.facebook.com/112627512144848/', '0.jpg', '[0.jpg]', '2021-06-16 07.39.37 下午', '2021-06-16 07.39.37 下午', 838, 0);
