INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 446, 1, '08997727', '麥味登精緻早午餐', '320', '桃園市', '中壢區龍岡路二段93號', 24.94604640, 121.22918190, '早午餐', '早午餐 巧克力布里歐皇厚吐司？巧克力厚片就厚片  打成這樣要50我以為多厲害⋯環境衛生堪憂\n內用門口、走道、內用桌旁都是蟑螂在移動\n誇張的是吃到一半蟑螂在桌上一起吃\n\n非常噁心 非常誇張結帳店員態度極差，不知道在不耐煩什麼？\n餐點好了嘴巴也不開口叫是誰的早餐，只是手在那揮示意你過去拿早餐\n感覺很差！\n過年期間沒有其他家早餐店開門做生意，只能買這家，但以後絕對不會再回顧！新的餐點\n適合減脂的人\n雞胸肉有嫩！一進店就看到蟑螂 衛生令人擔憂', 3.3, 64, 211, '0vt8JXvEj@gmail.com', '03 467 9428', 1, '["星期一: 06:00 \\u2013 14:00","星期二: 06:00 \\u2013 14:00","星期三: 06:00 \\u2013 14:00","星期四: 06:00 \\u2013 14:00","星期五: 06:00 \\u2013 14:00","星期六: 06:00 \\u2013 14:00","星期日: 06:00 \\u2013 14:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.54.53 下午', '2021-06-16 07.54.53 下午', 64, 0);
