INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 448, 1, '54734697', 'Q Burger 平鎮文化店', '324', '桃園市', '平鎮區文化街305號', 24.95041300, 121.20341000, '早午餐', '早午餐 打電話訂餐，到現場說餐點還沒好要等一下\n在現場等超久，等到現場點餐的人都取完餐了\n再去櫃檯問才發現餐點早就很好久了！！！！\n麵都乾掉了，餐點都冷掉了，實在是很傻眼，不會再來消費了電話點餐容易做錯，店員處理反應就是死不認錯！炸物超級少 這樣90塊超級不值得\n炸物都濕濕軟軟的餐點還不錯，店員的態度也挺好的餐點要熱結果來是微波很久的漢堡会燙手的那種，蛋都老掉漢堡乾的会好吃麽', 3.6, 124, 446, '4r90Wxu4@gmail.com', '03 491 6187', 1, '["星期一: 06:00 \\u2013 13:30","星期二: 06:00 \\u2013 13:30","星期三: 06:00 \\u2013 13:30","星期四: 06:00 \\u2013 13:30","星期五: 06:00 \\u2013 13:30","星期六: 06:00 \\u2013 14:00","星期日: 06:00 \\u2013 14:00"]', 'https://www.qburger.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.54.56 下午', '2021-06-16 07.54.56 下午', 124, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 449, 1, '71252779', '早安！您好 西式早午餐', '324', '桃園市', '平鎮區振平街16號', 24.94484240, 121.21949810, '早午餐', '早午餐 餐點色香味具全，環境又大又舒適，尤其炒泡麵真滴一覺，適合一家大小來用餐環境又大又舒服😌東西好吃😋炒泡麵一級棒！\n非常推薦來吃\n裝潢很漂亮 老闆老闆娘很大方！店內用餐氣氛超讚！長頸鹿超級適合拍照！黑咖啡35塊，不輸其它連鎖店！超級適合家庭朋友聚餐！裝潢真的很時尚，尤其是他的led長頸鹿！是拍照的好地點！ 蛋餅外酥內軟，真的第一名！環境優,食材新鮮,東西好吃,老闆,闆娘媽待人親切,是家優質的早餐店。', 4.5, 71, 320, 'zzRxaOjW@gmail.com', '03 428 1123', 4, '["星期一: 06:00 \\u2013 15:00","星期二: 06:00 \\u2013 15:00","星期三: 休息","星期四: 06:00 \\u2013 15:00","星期五: 06:00 \\u2013 15:00","星期六: 06:00 \\u2013 15:00","星期日: 06:00 \\u2013 15:00"]', 'https://www.facebook.com/1444178242536319/', '0.jpg', '[0.jpg]', '2021-06-16 07.54.57 下午', '2021-06-16 07.54.57 下午', 71, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 450, 1, '71272465', 'Q Burger 中壢林森二店', '320', '桃園市', '中壢區林森路167號', 24.94469000, 121.22671080, '早午餐', '早午餐 每次來吃蔥油餅加蛋都是用切的一格一格像蛋餅，近期就變成用撕的，用的像別人吃過的一樣噁心\n告知店員之前也沒有長這樣，店員還理直氣壯說只有加起司加肉才會切 不要用撕的要先講⋯請問你們換方式怎麼不先講？把早餐用的像餿水一樣誰要吃？\n最後也沒有換一份⋯只丟下一句話就走人🙄\n吃過這麼多分店 就你們店最多問題服務態度真的超好的，不小心忘記鑰匙在店裡，打烊後店長還特地前來開門拿鑰匙給我，真的非常不好意思跟感謝，即便是雨天特別騎車過來，店長還是沒有收下我的紅包，只說有機會常來吃就好，真的很感動加感恩。可朗芙巧克力醬料少得可憐連抹均勻都沒有的程度，朋友點的咖啡味道很淡，其他餐點一般般價格稍貴，整體CP值不OK，不推薦不知道在那邊衝三小 我點個樂活歐姆等了差不多半個鐘頭去催餐才上 如果要這樣慢慢上餐 代表你們根本就還沒準備好做生意\n而且叫你們的服務人員擦個桌子 講一講人也不知道跑去哪裡🤷環境舒服，適合用餐\n不過上餐順序跟速度有待加強\n等半小時有點誇張', 3.3, 21, 69, 'LXe2uKm@gmail.com', '03 457 9183', 4, '["星期一: 06:00 \\u2013 13:30","星期二: 休息","星期三: 06:00 \\u2013 13:30","星期四: 06:00 \\u2013 13:30","星期五: 06:00 \\u2013 13:30","星期六: 06:00 \\u2013 14:00","星期日: 06:00 \\u2013 14:00"]', 'https://www.qburger.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 07.54.58 下午', '2021-06-16 07.54.58 下午', 21, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 451, 1, '71408164', '野酌海鮮串燒屋/平鎮美食/平鎮燒烤/平鎮串燒/平鎮宵夜/平鎮宵夜推薦/平鎮居酒屋', '324', '桃園市', '平鎮區環南路三段201號', 24.94041500, 121.22153400, '居酒屋', '居酒屋 綜合肉串牛肉和羊肉的部分筋略多難嚼，炒中卷和炒青菜的調味偏淡，魚肚蛤蜊湯沒有薑絲稍有腥味，白飯太乾，服務好，空間不錯有冷氣，但偏熱。自開業一個月，都沒有幾乎造訪，昨晚剛好有機會去吃，朋友點了10道菜，從胡椒鳳螺開始，胡椒味稍辣了一些，如果能再收汁收乾一點，就更好吃了，烤魚，螃蟹，龍蝦（墊底舖盤的都腐因為吸了龍蝦的湯汁，豆腐變得很鮮），海鮮都很新鮮，炒空心菜加微辣，很開胃，現在營業時間改下午5點開賣喔~給4顆星\n的原因不是不好吃，是期待能夠再有進步的空間。\n\n湯，龍蝦\n\n。評價燒烤！\ncp值高特別推薦！\n服務更是沒話說～\n下了班三五好友小酌好去處👍🏻好停車\n真材實料的好味道\n價錢很公道唷!好吃！！\n服務也不錯！！\n連外帶都好吃', 4.7, 17, 80, 'VENwok@gmail.com', '03 458 9940', 4, '', 'https://www.facebook.com/pages/category/Restaurant/%E9%87%8E%E9%85%8C%E6%B5%B7%E9%AE%AE%E4%B8%B2%E7%87%92%E5%B1%8B-112043173835782/', '0.jpg', '[0.jpg]', '2021-06-16 07.55.00 下午', '2021-06-16 07.55.00 下午', 17, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 452, 1, '88635837', '月見町拉麵-中壢店', '320', '桃園市', '中壢區健行路175號', 24.94839950, 121.22790390, '拉麵', '拉麵 非常平價 所以我本身期待不高\n這次用餐點了2份餐點\n✔️天堂拉麵 加大 $105\n✔️人間拉麵 加大 $110\n\n我的天堂拉麵是豚骨湯頭 偏清淡 順口\n加大的量其實跟外面拉麵普通的量差不多\n\n男友點的人間拉麵 店員說是招牌\n不想吃太辣、但想嘗試微微辣的可以嘗試\n但是男友給負評😹 我不吃辣所以無法評價\n他給我的天堂拉麵也是負評\n所以就是見仁見智了😺 我覺得不會雷到極點就是了\n\n店員服務態度還不錯～\n店內空間也不小\n餐具跟水杯都是自助式的\n我覺得水杯可以使用環保一點的塑膠杯\n免洗杯實在是不大環保😅\n\n-\n\nIG：rosa__dvz 歡迎大家追蹤95元 有4塊叉燒 都有蛋！\n番茄湯很好喝 麵很紮實 整體來說很推吃起來是台式拉麵。月間町獨創必點「人間拉麵」小碗$90，平常吃辣所以他的辣度是剛好的，不會太辣吃完會覺得身體暖暖，麵裡的溏心蛋很好吃，叉燒柴柴吃起來稍有豬味，裡頭的應該是蔥段還是蒜段有醃製過蠻好吃的，其餘配料豆芽菜、木耳、 筍乾皆普通。店員很有禮貌，店內餐具自取，附桶裝水飲水機。不應該相信網路評價⋯肉就只是一般肉片 湯喝起來沒濃郁感沙茶拉麵吃起來有點像台南的#來碗拉麵\n不管是湯頭和麵條都很像🤣\n他比較不太日式 算是台式拉麵\n而且很便宜 湯頭好喝\n只是他的糖心蛋真的撥的不是很專業\n叉燒很薄 但不柴\n麵條蠻Q的 我們點加大 麵真的多\n.\n人間拉麵有點辣\n是那種喝進去之後 喉嚨會麻麻的\n但湯頭是好喝的\n不敢吃辣的人也可以試試看', 4.7, 64, 301, 'GgLrF0@gmail.com', '02-68644341', 4, '["星期一: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.55.02 下午', '2021-06-16 07.55.02 下午', 64, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 453, 1, '97461245', '赤兔日式燒餃', '324', '桃園市', '平鎮區和平路121號', 24.94732080, 121.22066480, '拉麵', '拉麵 這家店的內裝很好，東西也不錯吃，從外頭看上去頗有神秘感！CP值高,好吃不貴，但店有點小熱平價但是cp值很高的一間店，餐點真材實料，味道也不錯，咖哩蛋包飯不錯，餃子覺得還好，黃金泡菜好吃！最常帶女兒來點味增拉麵搭配黃金泡菜，150搞定很方便！9/5 不是九點關門   八點過去已經關門了  差 差 差原本在中原大學的赤兔原來還中壢還有分店，懷念的感覺！！真的還是很好吃！老闆很用心煎好每份煎餃，好吃咬下去瞬間湯汁爆發！煎餃必點，第一次點了親子飯加炸豬排也是cp值很高！', 4.1, 111, 455, 'yvrCRh@gmail.com', '03 457 2088', 1, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.55.03 下午', '2021-06-16 07.55.03 下午', 111, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 454, 1, '19054122', '多爾奇義式健康廚房', '324', '桃園市', '平鎮區大仁街11號', 24.92843530, 121.22699770, '約會餐廳', '約會餐廳 態度好好地去被大吼大叫的感覺很奇怪，對客人很好對外送員很差\n\n收到老闆的來信，我必須做出道歉：\n\n我進門後，第一眼沒看到實名制簡訊在哪，外面非常熱，我以為在櫃檯前也能掃描，直接走到最前面找老闆的。我真的不是故意的，我的經驗是必勝客跟達美樂都是在櫃檯也可以掃描。\n\n所以可能是老闆勸阻我時比較激動。沒在門外做掃碼，我也給了店家不愉快的體驗，我不是故意的，對不起。用外送平台叫來吃，好吃👍\n有時間會多去店內內用光顧。今年的飲食業。只能用一個慘字來形容。平鎮這個地方，向來都是北方的麵食，或眷村人們的家常菜，因為這個地方軍眷子弟居多，所以不需百家爭鳴，放眼望去各家百花齊放，各有各的老點各有各的常客。那味道不就是你媽媽或我阿姨的味道嗎？結個帳姨來媽去的～回您自個兒家去，好嗎？\n\n近幾年來，桃園從市中心慢慢向外快速拓寬發展，傳統的飲食習慣，早已滿足不了快速遷入的這些年輕族群\n\n以前你我經過此地，牛肉麵，包子，餃子，佔據了大部分的人們心中的那個味蕾感受\n\n在這波疫情當中，這裡的餐館逃的逃，倒的倒。但多爾奇，卻在這個地方簽下了美食的志願役\n\n此時、來自異國的風味，卻變成了一隻逆勢抗漲的績優股，穩扎穩打，後來居上，他們不是跟隨者，而是選擇當起了領導者，建立起了一個新的單位。\n\n我當天是平日去的，客人三三兩兩，我想東西也好吃不到哪裡去。隨便叫了一個義大利麵來吃，但吵雜的聲音影響到我的用餐心情，吳博毅跟富胖達。試問，在你用餐的時候就好像有兩隻狗不斷地在你的旁邊叫來叫去，當當～當當～當當～當當，您還吃的下去嗎？\n\n你各位啊，別怪我沒提醒你，不管假日平日，請先打電話過去預約，特別是那些沒有耐性的老饕們，想享受精緻的酥皮濃湯，與那已高湯結合的麵條，用餐的環境和座位的擺設，適合任何的組合，家庭，公司行號，前往用餐。\n\n而且，在平鎮這個軍眷居多的地方，吃著義式料理的畫面，這究竟是他貓的一個什麼樣的概念與畫面呢？老闆：你真的讓我猜不透你呀！叫了三百多外送直接加料實在好棒棒…狗在地上走，又任易讓狗在座位上睡覺，用餐時三不五時來舔你的腳，狗味較濃，用餐環境不佳，還是2隻狗，感到不太舒服.\n老闆應改善用餐環境，讓顧客感到舒適.', 3.8, 127, 483, 'WxMPEVH2v@gmail.com', '03 468 1508', 1, '["星期一: 11:00 \\u2013 14:30, 17:00 \\u2013 20:30","星期二: 11:00 \\u2013 14:30, 17:00 \\u2013 20:30","星期三: 11:00 \\u2013 14:30, 17:00 \\u2013 20:30","星期四: 11:00 \\u2013 14:30, 17:00 \\u2013 20:30","星期五: 11:00 \\u2013 14:30, 17:00 \\u2013 20:30","星期六: 11:00 \\u2013 15:00, 17:00 \\u2013 20:30","星期日: 11:00 \\u2013 15:00, 17:00 \\u2013 20:00"]', 'https://simpleat.ncco.com.tw/Dolic/SE00051/menu', '0.jpg', '[0.jpg]', '2021-06-16 07.55.05 下午', '2021-06-16 07.55.05 下午', 127, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 455, 1, '43236286', 'Amour 阿沐 (原 茂園和漢美食館)', '324', '桃園市', '平鎮區延平路二段371號', 24.94317370, 121.20725080, '約會餐廳', '約會餐廳 雖然便當是涼的，但是心是暖的。東西不好吃就算了，「沙灘咖啡廳」內下午茶用餐，樣式選擇少也就算了，但是遇到一群客人在小小店內喧嘩吵鬧，又在沙灘上走來走去拍照近20分鐘，搞得桌上塵土飛揚，最後還要我們讓位拍照，服務生只顧聊天，不制止🙄\n\n還要我們親自來維持我們的用餐權利，顯然他們只負責端盤子、收盤子工作！\n\n餐具，飲用水自己來，卻還要收10%服務費，有夠誇張🤔應該不會再來❌\n\n結帳時問服務生你們做了什麼可以收服務費？\n他們卻回應：網美咖啡店本來就是這樣經營的🤦🏻‍♂️現場實景很用心裝潢，服務人員貼心，不過原本下午茶時間已較短(14:15-16:00)，對於當天又有事擔誤的我們過去只好先狂拍才能享用美食😂，可惜沒辦法好好的享受美食和美景，裝潢的這麼美一定要拍好拍滿的呀！😍當天因較晚才訂位訂到路間一般座位，改天有時間還會想回訪訂美麗的位子拍好拍滿！餐廳外也超好拍！很多造景可以殺手機儲存空間，餐廳裡沒拍過癮餐廳外可以拍個夠！😜\n下午茶內容價格合理，低消$250，有貼心飲料蛋糕各一套餐$299，蛋糕較一般網美店比起來有誠意許多，還蠻好吃的耶！😍飲料的話喝起來蠻天然的，真材實料的感覺，但甜度都偏低，如果喜歡較甜口味可以請店員加糖看看囉~\n雖室內設計為唯美沙灘，但一下子物品或美食上真的會有薄沙覆蓋，腳上因為換穿店內提供的拖鞋一定會都是沙(不過沙質是很舒服的細沙)，一陣一陣會有朦朧沙塵出現😅，不介意的人建議再前往唷~享用完出來時可以用店家貼心提供的溼紙巾與紙巾擦腳，清潔效果不錯唷。\n另外16點時間一到清場的效率印象蠻深刻的，跟大部分店家的印象不太一樣😂，瞬間就清場了。😳來參加婚宴，動線規劃極為混亂、有的廳要沿著走廊走到底才會到，走廊又比較狹窄，各種摩肩擦踵、找路或走錯路回頭造成所有人塞在走廊上就有點煩了更別提廁所排到爆，有的廳內的動線規劃又沒考慮到收禮/送客拍照、又是另一個渾沌，最後最可怕的是桌子超小、桌距也超小，還好我們這桌正好有人沒到不然真的不知道怎麼擠，狹窄到不行的桌距也是我見過最辛苦的敬酒，要撤掉半桌才能送客的背板也是難得一見，食物就不評論了，建議要在這裡辦婚禮的新人多多考慮本人於2021.3.20日在阿沐舉辦婚宴\n先從場地說起，A廳非常的棒\n總桌數38桌仍然留有極大的空間\n桌與桌的間隔不會擁擠，走道更是寬大\n舞台的部分也是很寬敞\n足夠在舞台上玩小遊戲\n婚宴聽挑高的設計與裝飾都非常氣派\nA廳的動線很棒，入口不會與其它廳別相衝\n入口處也有三張長桌可供收禮金\n新娘休息室則是老婆讚不絕口\n當初會考慮這裡新娘休息室也是佔很大因素\n新娘休息室房間很大，可以容納很多人\n當天一直有朋友進來拍照聊天也不會覺得擁擠\n另外這裡提供免費的停車場\n讓參加婚宴的賓客不需在辛苦找停車位\n\n婚宴菜色也是讚不絕口\n雖然當天小弟非常忙碌\n沒有時間用心品嚐佳餚\n但是朋友反應非常熱絡\n對餐點滿意度非常高\n甚至有籌備婚禮的朋友跟我詢問這裡的資訊\n\n另外非常感謝阿沐的訂席靜宜\n提供詳細的資料跟細節\n也花很多時間與我們溝通討論\n幫助我們順利完成這次的婚宴\n\n最後還是要說一下小缺點\n原本預計是2020.6.6\n但是因為疫情影響所以延期\n但是原本2020.6.6有的優惠方案\n卻無法於2021.3.20沿用\n雖然了解時間優惠內容會不同\n但是延期是因為疫情迫不得已\n並非新人自行延期\n希望在這方面婚宴會館可以給新人多些體諒', 4.1, 2973, 12189, 'L7ZY0RDaWR@gmail.com', '03 495 1375', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.55.06 下午', '2021-06-16 07.55.06 下午', 2973, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 456, 1, '21893974', '古月新城', '320', '桃園市', '中壢區健行路41號', 24.95174580, 121.22670220, '約會餐廳', '約會餐廳 東西好吃😋服務人員親切沒有特約停車場\n不過附近很多收費的停車場\n機車停車場有計次 30元的 （也有看到60元/小時） 建議附近繞一下 有些超貴\n但地圖上找不到唷\n\n環境非常舒適 有免費的水\n建議訂位 服務人員還問我們大概會聚餐多久的時間\n然後幫我們排了三個小時 時間非常充足\n\n餐點部份偏貴 但味道不會太差\n但也撐不上好吃\n\n櫻桃鴨青醬燉飯 吃到後面超膩\n套餐附的甜點 有仙草丼跟乳酪可以選 味道也勉勉強強\n\n有桃園市民卡的話 享有特約折扣95折\n也有很多公司有特約 可以詢問看看\n只要一張卡就全桌享折扣\n\n對了 結帳時還可以分開結唷！\n非常貼心的服務\n\n廁所很舒適 有提供衛生棉 牙籤等等 還有沙發很舒服的地方\n用餐隱密舒適\n有獨立的空間\n可以輕鬆與親友用餐歡聚、聊天\n但是⋯⋯⋯⋯⋯⋯好難訂位啊！\n已經數不清幾次打電話去，卻被告知已訂滿了😭😭😭平均用餐時間為3小時\n接受定位，食物超好吃😊😊😊\n當月壽星飲料還有升級超棒👍👍👍\n當天唯一的缺點就是機車不好停， 繞超久的好在 店家有多等我們，才有位子可以坐😘氣氛佳用餐愉悅\n方便停車場\n適合談事業的好地方😊\n#三餐正常吃\n#保證有效無效退費\n#胰島素瘦身\n#保養老樣子\n#沒保養樣子老\n#健康瘦身\n#健康減肥\n#水解蛋白益生菌\n#腸溶包腹技術\n#益生菌\n#胃潰瘍\n#腸胃炎\n#腸道保健\n#0元創業\n#0元代理商\n#繆沛恩\n#宇證天團\n#愛笑的妙妙\n#宇孝健康管理中心\n#打造專屬個人健康管理', 4.2, 1481, 6220, 'XKbwYvf@gmail.com', '03 458 8270', 2, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 22:00","星期六: 11:30 \\u2013 22:00","星期日: 11:30 \\u2013 22:00"]', 'https://facebook.com/ComplexRestaurant.Zhongli/', '0.jpg', '[0.jpg]', '2021-06-16 07.55.07 下午', '2021-06-16 07.55.07 下午', 1481, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 457, 1, '63616015', 'E書房中壢店', '320', '桃園市', '中壢區健行路230號2樓', 24.94722620, 121.22823610, '約會餐廳', '約會餐廳 臨時起意跑來體驗\n想在悠閒的週末耍廢一下\n於是網路上找到這間評論不錯的店\n店裡的漫畫當然不像真正漫畫店這麼豐富\n但是線上熱銷的漫畫是非常的齊全喲\n最重要的是\n店裡的餐點本以為不過就是個料理包那種\n沒想到\n超級好吃的啦！！！\n真心佩服這位可愛又厲害的店員Lulu\n一個人又要顧櫃檯、煮餐點、收拾整理包廂\n這麼忙碌依舊的工作\n臉上依然是很親切的笑容\n雖然戴著口罩\n但是那對迷人的微笑眼睛\n都能讓人心情跟著愉悅起來\n真心大推這間E📖\n環境乾淨、餐點非常好吃、店員Lulu非常可愛\n著實渡過了一個美好的週末夜晚\n（完蛋現在講完好想再吃那盤甜不辣🤣🤣）\nPS那個什麼芝加哥卡拉雞腿飯辣死我了\n不敢吃辣的真的不要點😂😂😂\n然後附湯超好喝到想續碗🥰🥰來店前在FB事先詢問店家是否有做防疫措施，收到的回應是：\n\n當班人員都有量體溫、戴口罩、消毒\n\n環境上也有消毒擦拭\n\n也會要求客人量體溫戴口罩\n\n結果當天來店，櫃檯人員根本就沒戴口罩！也沒幫客人量體溫和消毒！今天第二次來，桌子跟上次來一樣沙沙的還有頭髮，你們的環境清潔和消毒真的都有在做嗎？！\n\n現在疫情時期，確診者去過中原大潤發、火車站，你們的店離這麼近！e書房又有中原店(可能分店員工也會調店支援)，怎麼會沒有任何防疫措施呢？？？？？這間24小時營業，不怕只要中標就沒人敢了來嗎？？？\n\n沒作防疫就很扯了！還欺騙客人耶！！！店內貼出設施餐具都有作消毒的聲明，我看根本也是作作樣子而已的吧？！作生意不老實，把消費者當白痴！不會再來了！！！因為常去，常遇到的店員都知道我對包廂有一些喜好，所以都會盡量給我所需的包廂，這點真的很棒！\n\n雖然很可惜有一位盡職又服務很好的店員已經離開了~~ 但是另一位店員Lulu也很可愛親切又有笑容，還記得之前有次頭不小心去撞到門，當下還主動關心並問我是否需要ok繃，真的非常貼心~~\n\n平時見她勤於清掃工作，店內環境比其他一般網咖乾淨又舒適明亮很多~~\n\n大廳可以借漫畫小說雜誌和影片，包廂內的電腦螢幕也很大，環境好和服務好是讓人持續會來的原因，推薦給各位👍位於健行大學旁，\n提供漫畫、影集觀賞，\n成為夏日的好去處，\n餐點收費很佛心，\n通通都是銅板價，\n讓人可以吃的很飽。疫情非常時期，櫃檯人員沒戴口罩、也沒放置酒精消毒器供人使用！要求客人要低消餐點，但從進門就沒任何防疫措施來看，餐具有無確實消毒實在讓人堪憂！這種地方隨便一待就是2、3個小時以上，只要有人確診就沒人敢來了！', 4.7, 352, 1654, 'A9BKaQPeA@gmail.com', '0934 102 440', 4, '["星期一: 24 小時營業","星期二: 24 小時營業","星期三: 24 小時營業","星期四: 24 小時營業","星期五: 24 小時營業","星期六: 24 小時營業","星期日: 24 小時營業"]', 'https://www.instagram.com/ebook0977527812/', '0.jpg', '[0.jpg]', '2021-06-16 07.55.08 下午', '2021-06-16 07.55.08 下午', 352, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 458, 1, '42337040', '匿境 lncognito cafe', '324', '桃園市', '平鎮區振平街92號1樓', 24.94291980, 121.21923120, '約會餐廳', '約會餐廳 餐點還ok，費用我覺得稍貴一點（算起來一個義大利麵套餐要300多快400）\n環境很不錯，文青氣息，但用google找不到店，是問管理員才找到每月一次的讀書會，我們都選擇在匿境舉辦。環境舒適，餐點好吃。母親節前夕，帥哥老闆還送了每位媽咪一朵紫色康乃馨，真是太有心了！大推 ！味道普通，燉飯義大利麵配料都很單調，不如評價的好吃。我點的牛排義大利麵口感不錯，就是有點偏鹹，匿境拿鐵也很棒，適合跟朋友一起來吃飯的地方。大推！！！女店員人超好的，而且很可愛！餐點好吃，老闆人也很nice！！吃好幾次了😆', 4.5, 302, 1359, 'BUGOQFD@gmail.com', '03 468 8892', 2, '["星期一: 11:00 \\u2013 21:00","星期二: 休息","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 10:00 \\u2013 21:00","星期日: 10:00 \\u2013 21:00"]', 'https://www.facebook.com/incognitocafe.tw/?modal=admin_todo_tour', '0.jpg', '[0.jpg]', '2021-06-16 07.55.09 下午', '2021-06-16 07.55.09 下午', 302, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 459, 1, '17917334', 'Right House', '324', '桃園市', '平鎮區忠孝路196號', 24.95212920, 121.21216620, '甜點', '甜點 好厲害的店 就像家裡巷口鄰居媽媽開的小餐廳\n剛剛點套餐附贈的無麩質莓果起司\n手作蛋糕好厲害⋯⋯\n其他看起來也都好好吃\n畢竟我真的超討厭蛋糕裡面有吉利丁的感覺\n\n美式也很用心\n剛剛吃的青醬燻雞義大利麵跟南瓜濃湯也不錯\n\n如果有人也來壢新醫院的話可以走過來\n有插頭可以做作業(⁎⁍̴̛ᴗ⁍̴̛⁎)[#tata吃平鎮］\n🚩桃園·平鎮｜#Right House\n✨輕食套餐(甜點/手工餅乾/飲品) $170\n(備註：有些蛋糕需補差價)\n-\n👉Right House位在育達高中附近的巷弄內，外觀是可愛的小木屋，內部裝潢是溫暖的鄉村風，在這聚會吃甜點感覺就很舒服愜意\n-\n🔅他們家的甜點真的誇張 中午左右就被預定光光\n每日早上FB粉專會Po出當天的切片蛋糕\n數量有限 要趕快電話預留\n不然可能即使到現場 但也都被預定光了\n若要吃主食(飯麵類)也要在下午四點前預約\n草莓布朗尼 巧克力濃郁微苦甜 整體不會太甜\n蛋糕體微濕潤 搭上酸甜的草莓 真的好吃😋\n-\n⚠️ 一週只營業四天唷，假日來是吃不到的！\n切片蛋糕約60元-100元間不等\n-\n📍Right House\n🏠桃園市平鎮區忠孝路196號\n☎️03-4022868\n🕘週二-五 11:30-19:00\n⛔️週一、六、日未營業\n-\n🔍IG追蹤➡️@tata.ifood\n🔍FB粉絲團搜尋➡️TaTa愛吃肉\n💬不定期更新美食在店內用餐完 立馬訂購生日蛋糕\n超級好吃 小孩吃到欲罷不能\n有空會在常來吃的蛋糕超好吃，生意很好，遇到節日要提早預訂套餐cp值很高！義大利麵份量、風味都很有水準！蛋糕大推提拉米蘇！', 4.5, 131, 590, 'ngWqJSM@gmail.com', '03 402 2868', 2, '["星期一: 休息","星期二: 11:30 \\u2013 18:30","星期三: 11:30 \\u2013 18:30","星期四: 11:30 \\u2013 18:30","星期五: 11:30 \\u2013 18:30","星期六: 10:00 \\u2013 11:00","星期日: 休息"]', 'https://www.facebook.com/RightHouseCakes/', '0.jpg', '[0.jpg]', '2021-06-16 07.55.11 下午', '2021-06-16 07.55.11 下午', 131, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 460, 1, '27391479', '阿蕊餐廳', '745', '台南市', '號 745 台南 市 區 南 122 鄉 道, 南122鄉道安定區', 23.12945200, 120.25079560, '小吃', '小吃 在地平價古早味，什錦湯麵、大魯麵、炒米粉……好吃。用餐環境較不優，建議外帶。吃的出來食材新鮮，花枝彈牙魷魚脆！\n\n唯一美中不足應該是現代人的感官不佳。住蘇厝20年，沒吃過', 3.1, 7, 22, 'fj5Ynas8@gmail.com', '06 592 1190', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.22 下午', '2021-06-16 07.57.22 下午', 7, 0);
