INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 462, 1, '57356562', '小英鍋燒麵', '745', '台南市', '安定區460號', 23.12997380, 120.25060300, '小吃', '小吃 好吃，價格一般不貴，從小吃到大的來採買麻花捲順便覓食中餐，這家店外觀不起眼也看似有歷史，賣著眾多口味的鍋燒意麵，鍋燒採用的碗是記憶中的款式，彷彿時光倒流回溯至小時候，店家態度很好，點了泡菜跟原味鍋燒，五十元一碗料算豐富，湯頭沒有過多添加物，稱不上非常好吃，但完整呈現古早味的感覺。從小吃到大的好味道\n鍋燒意麵好吃 料多泡菜鍋燒意麵 65元\n整體還不錯吃價格不貴', 4.3, 21, 90, 'NhgCTZXpfh@gmail.com', '06 592 3001', 4, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.23 下午', '2021-06-16 07.57.23 下午', 21, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 463, 1, '30786349', '王家百年麵店', '745', '台南市', '安定區南122鄉道', 23.12742670, 120.25109900, '小吃', '小吃 餛飩乾麵有五顆餛飩，餛飩蠻紮實的，麵條煮的適中，不會太過軟爛，肉燥調味挺香的，乾麵有附碗清湯。滷味口味上中規中矩，兩個豆干$15。\n乾麵價格中上食材新鮮，麵類Q彈實在不錯吃，自從換掉原本煮麵的，現在的人待客親切也不會一張臭臉麵相當好吃，滷味也非常夠味，小碗的都感覺是中碗的量非常的足夠，湯頭很棒。20210331王家百年麵店 肉燥乾麵＄40、黑白切55\n20210325王家百年麵店 榨菜肉絲麵＄60、黑白切滷菜＄90老闆態度不佳，衛生習慣不好', 4.2, 174, 731, 'Lo4GsDM@gmail.com', '02-16451177', 1, '["星期一: 休息","星期二: 10:00 \\u2013 19:30","星期三: 10:00 \\u2013 19:30","星期四: 10:00 \\u2013 19:30","星期五: 10:00 \\u2013 19:30","星期六: 10:00 \\u2013 19:30","星期日: 10:00 \\u2013 19:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.24 下午', '2021-06-16 07.57.24 下午', 174, 0);
