INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 467, 1, '53182857', '理善蔬食坊', '741', '台南市', '善化區烏橋中路110 號', 23.13088120, 120.26715090, '小吃', '小吃 挺不錯的素食餐廳，我們三個人分別點了火鍋、石鍋拌飯、焗烤，都符合水準。硬要挑剔的話，口味略重。是吃素的朋友可以收錄的餐廳名單。寬敞舒適的庭園餐廳，附停車場、座位多。簡餐、火鍋價格實惠，份量足。只是人多時需要等一會。只收現金無發票。\n火鍋還附一大杯金桔茶，實在有飽。2021/03\n乾淨又好吃的素食餐廳\n咖哩的石鍋拌飯很推\n第一次吃到這種口味\n濃濃的咖哩辛香辣和鍋巴很搭\n泰式炒麵放了很多菜\n吃起來酸酸辣辣的口感\n\n希望老闆隨餐附杯紅茶😎近安定交流道，停車方便\n料理入味，素食者的好選擇今天帶著長輩們去用餐，除了環境明亮 乾淨、餐點好吃、好停車 之外，最重要的是感受到南台灣滿滿的人情味，頭家嬤人真的很好，一定會再去的一家好店！推👍', 4.4, 373, 1641, 'Xs0Lrx6vi@gmail.com', '06 581 2377', 2, '["星期一: 休息","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 19:30"]', 'https://www.facebook.com/LiShanSuVeges/', '0.jpg', '[0.jpg]', '2021-06-16 07.57.27 下午', '2021-06-16 07.57.27 下午', 373, 0);
