INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 469, 1, '51392852', '陽春麵｜阿秀麵店', '516', '彰化縣', '埔鹽鄉彰水路一段327號', 23.99422350, 120.47642560, '小吃', '小吃 覺得普通 滷味有點過硬滷味很入味、乾麵也好吃...但\n一大盤滷味+4碗麵+一碗餛飩湯（七顆餛飩）\n這樣居然只要230元？！\n我還以為是店員拿錯單子，爆高CP值，路過必試試，但需要耐心喔～環境乾淨，好吃~沒耐心等待的人請繞道！米血 魯大腸 豆包非常入味，醬料甜鮮不死鹹，甜辣醬必加，麻醬麵推推沒有招牌，很容易錯過的一家小店\n門口很簡陋，但裡面空間很大，收拾速度快，也蠻乾淨的\n一家四口吃還點了一大盤滷味只要235元，超便宜👍\n很推薦大腸跟百頁，豆皮跟米血則是味道較淡，可以加一些店家提供的辣椒，非常對味！\n生意很好，正中午去還等了40分鐘才上菜，所以建議要早點到才不會等太久', 4.3, 572, 2460, 'pxS86HeUM@gmail.com', '04 865 4847', 1, '["星期一: 11:00 \\u2013 14:00","星期二: 11:00 \\u2013 14:00, 16:00 \\u2013 19:00","星期三: 11:00 \\u2013 14:00, 16:00 \\u2013 19:00","星期四: 11:00 \\u2013 14:00, 16:00 \\u2013 19:00","星期五: 11:00 \\u2013 14:00, 16:00 \\u2013 19:00","星期六: 11:00 \\u2013 14:00, 16:00 \\u2013 19:00","星期日: 11:30 \\u2013 14:00, 16:00 \\u2013 19:00"]', 'https://www.facebook.com/%E9%98%BF%E7%A7%80%E9%BA%B5%E5%BA%97-226101610802141/', '0.jpg', '[0.jpg]', '2021-06-16 07.57.39 下午', '2021-06-16 07.57.39 下午', 572, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 470, 1, '85946394', '餃子大王', '516', '彰化縣', '埔鹽鄉中正路27巷7弄3號', 23.99645840, 120.47608010, '小吃', '小吃 其實只能給4.5顆星，因為半顆扣掉是因為麵條\n麵條過爛，沒有Q度，湯頭入味，不死鹹，有香氣，牛肉嫩，不老，半筋半肉好味道在地實在好味道\n從小吃到大已經30幾年的老店牛肉麵很不ok，不像是熬煮過的湯頭，味道貧乏，建議點其它菜色。水餃🥟推薦\n小菜也都魯的很夠味道地小吃真材實料，推薦水餃皮Q餡多汁', 4, 147, 588, 'nECXsj@gmail.com', '04 865 4721', 1, '["星期一: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期二: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期三: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期四: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期五: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期六: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30","星期日: 11:00 \\u2013 13:30, 17:00 \\u2013 20:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.40 下午', '2021-06-16 07.57.40 下午', 147, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 471, 1, '09186334', '田莊休閒餐廳', '516', '彰化縣', '埔鹽鄉彰水路二段177號', 24.00295100, 120.48000680, '小吃', '小吃 超級不推的地方，公休日確接訂甕仔雞訂單\n，去到現場貼著本日公休，這是什麼生意人的態度呢（小心被耍了）下午3:00打去訂餐說6:00要去取餐，\n結果要去取餐時，店沒營業，電話也沒接..\n白跑一趟！感覺很差…碗盤用保鮮膜包起來\n拆開缺髒到不行。\n點了一盤蒜泥白肉。\n上來是一膏 水煮肉+油膏\n環境差 衛生差\n一顆星 給他的雞。假日蠻多人的，出餐有稍微等待，甕子雞建議先預訂不然要等很久，口味普普但還不錯吃好停車，環境不錯，適合帶小朋友來認識自然環境，有魚有猴子，也適合家人朋友小酌聊天', 3.8, 295, 1121, 'sAbGTE@gmail.com', '04 865 5150', 4, '["星期一: 休息","星期二: 10:00 \\u2013 20:00","星期三: 10:00 \\u2013 20:00","星期四: 10:00 \\u2013 20:00","星期五: 10:00 \\u2013 20:00","星期六: 10:00 \\u2013 20:00","星期日: 10:00 \\u2013 20:00"]', 'https://www.facebook.com/%E7%94%B0%E8%8E%8A%E4%BC%91%E9%96%92%E9%A4%90%E5%BB%B3-1667166850230342/', '0.jpg', '[0.jpg]', '2021-06-16 07.57.41 下午', '2021-06-16 07.57.41 下午', 295, 0);
