INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 473, 1, '24758346', '素食麵', '516', '彰化縣', '埔鹽鄉中正路15號', 23.99650770, 120.47614670, '小吃', '小吃 彰化縣埔鹽鄉_素食麵(招牌僅顯示素食.粥)\n\n1.炒麵還可以\n1.1麻將麵、竹筍湯、小菜都挺美味\n\n2.店內空間不大，約20座位左右\n2.1店內無兒童座椅\n2.2店員服務中規中矩\n2.3出餐迅速\n2.4菜單內容多元\n\n3.無專用停車位，須路邊停車真材實料，吃得出食材原味。口味還可以，但蒼蠅超多的古早味的早餐\n雖然充滿（菜味）\n還是值得推薦離順澤宮一~兩百米左右,素食好選擇\n\n鄉下地方,價位超便宜,菜色多樣,份量又足,口味好吃,老娘闆親切和藹,下回來必光顧\n\n兩人點了兩個小綜合湯50元,兩個中炒麵70元,兩大豆干25元+一盤青菜30元,一共才175元,吃得很飽😋💓👍', 4.6, 51, 235, '7DmnJWs@gmail.com', '04 865 3766', 1, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.42 下午', '2021-06-16 07.57.42 下午', 51, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 474, 1, '84722895', '阿娥埔鹽素食麵', '516', '彰化縣', '埔鹽鄉彰水路一段346號', 23.99517150, 120.47670660, '小吃', '小吃 從小吃到大的好味道😋炒麵沒有什麼複雜配料但就是好吃，鮮香湯頭是用玉米蘿蔔杏鮑菇去熬煮，配上豐富的豆皮豆腸豆雞豆筍豆包豆腐百頁湯料！！\n\n是那種回味再三的美味即使素食還是會主動想吃！！自製辣椒醬也夠味夠勁！\n\n素食炒麵+綜合湯NTD60.-\n綜合湯麵NTD55.-這是我在埔鹽吃過最好吃的素食麵，炒麵、炒米粉每日限量，賣完就沒了，想要品嚐的真的要早一點！像我今天就沒有吃到炒麵（殘念）改天一定再去吃！！這是偶然發現的好店，已經吃好多年了。每次只要來埔鹽一定要來吃意麵+綜合湯，綜合湯也是目前吃過的素食店家中，最好吃的一家。沒吃過的人，一定要來嚐看看。這間在地小吃是位於埔鹽鄉的美味素食麵，很多人特別喜歡來吃這間的麵配獨門自製辣椒醬，但我不太敢吃辣，沒敢加很多，但辣醬的香味真的很香！\n\n這次點了素食乾麵加一點點辣醬，不同以往印象中素菜、素料的味道，取而代之是一股沒吃過的清香，麵條清爽可口，細細的麵條剛好是我的最愛。另外點了一盤小菜，香菇、豆輪非常多汁，百頁豆腐也很有嚼勁，跟店員聊了一下，滷味、素料都是前晚到早上新鮮現做，不留隔夜，才能保持甜美味鮮的口感！\n\n早上店裡會有店家自製的炒麵跟炒米粉，有時候晚來賣光就吃不到了，想吃的話建議早點來享用。\n\n很多的熟客幾乎每天早上都來內用，點一碗素食麵配碗湯，感覺都是點固定的餐點。不過店內只有少數幾個位子，喜歡吃麵的朋友要帶朋友來也要注意一下。麵店的店員都很親切，不過一早吃早點的人很多，想吃好吃素食麵的朋友要有點耐心，很推薦喜歡吃素食麵的朋友來吃～', 4.3, 13, 56, 'cqH29qw@gmail.com', '04 865 4870', 4, '["星期一: 休息","星期二: 06:20 \\u2013 13:00","星期三: 06:20 \\u2013 13:00","星期四: 06:20 \\u2013 13:00","星期五: 06:20 \\u2013 13:00","星期六: 06:20 \\u2013 13:00","星期日: 06:20 \\u2013 13:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.43 下午', '2021-06-16 07.57.43 下午', 13, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 475, 1, '71955063', '埔鹽麵店', '516', '彰化縣', '埔鹽鄉彰水路二段26號', 23.99623900, 120.47675800, '小吃', '小吃 非常好吃的乾麵！\n辣椒也很好吃～', 4.2, 5, 21, 'Wu3mwBY@gmail.com', '02-75329289', 4, '["星期一: 06:00 \\u2013 13:30","星期二: 06:00 \\u2013 13:30","星期三: 06:00 \\u2013 13:30","星期四: 06:00 \\u2013 13:30","星期五: 06:00 \\u2013 13:30","星期六: 06:00 \\u2013 13:30","星期日: 06:00 \\u2013 13:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.44 下午', '2021-06-16 07.57.44 下午', 5, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 476, 1, '45416309', '埔鹽阿秀素食', '516', '彰化縣', '埔鹽鄉彰水路一段333號', 23.99434970, 120.47641590, '小吃', '小吃 聽說是埔鹽走過路過絕對不能錯過的素食麵，價格真的是嚇到我的便宜哈哈哈，在台中是目前還沒有吃到那麼好吃價格又那麼便宜的店家。麵大碗又便宜，小菜都好吃，都很喜歡，非常推薦大推…麻醬麵還有當天現做的菜捲，很有禮貌的年輕老闆。超級好吃的素食麵，大推麻醬麵。雖然是素食的，香氣一點都不輸葷麻醬。\n\n素食也能做得這麼好吃，真的是一門功夫了。\n\n吃完我彷彿能看見一道佛光。爬不起來就不要吃麵了啦。', 4.4, 81, 356, 'wpVeyGv@gmail.com', '04 865 7701', 1, '["星期一: 06:30 \\u2013 13:00","星期二: 06:30 \\u2013 13:00","星期三: 06:30 \\u2013 13:00","星期四: 06:30 \\u2013 13:00","星期五: 06:30 \\u2013 13:00","星期六: 06:30 \\u2013 13:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.44 下午', '2021-06-16 07.57.44 下午', 81, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 477, 1, '63219454', '鴨皮子', '516', '彰化縣', '埔鹽鄉中華路168號', 23.99820900, 120.47649190, '小吃', '小吃 服務很差，訂了說沒訂，自己沒核對好，還說客戶報錯電話，真夠瞎！我不會是第一個，繼續看其他人給的評價就知道了覺得可以不用預定啦\n預定現場還要拿號碼牌等很久..\n那就現場排就好了\n打電話有差嗎..\n一次買的經驗\n不會在第二次了\n不知道電話預訂是要幹嘛的..？還不錯吃，椒鹽口味比較重，記得電話先預約打電話先訂了 一樣要現場等 我先到了 去窗口那邊 人員叫我等一下 之後又有一位客人來 他沒有打電話訂 老闆娘卻先給他號碼牌 變成我最後拿 那我打電話做什麼？先去還最後走 感覺真的很差勁👎🏻這間烤鴨我也吃過兩三次了\n但是今天在片鴨上看到頭髮  有點倒胃', 4.5, 131, 590, 'NheGwv7Zi@gmail.com', '0906 925 520', 4, '["星期一: 16:00 \\u2013 18:00","星期二: 16:00 \\u2013 18:00","星期三: 16:00 \\u2013 18:00","星期四: 休息","星期五: 16:00 \\u2013 18:00","星期六: 10:30 \\u2013 13:00, 16:00 \\u2013 18:00","星期日: 10:30 \\u2013 13:00, 16:00 \\u2013 18:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.57.46 下午', '2021-06-16 07.57.46 下午', 131, 0);
