INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 502, 1, '85379389', '逐鹿小館 阿里山原木烤肉', '602', '嘉義縣', '番路鄉梅花六路2巷20號', 23.44766270, 120.58228430, '小吃', '小吃 烤肉.香腸和竹筒飯都很好吃\n但是烤蛋就像一般的水煮蛋, 沒有老闆娘說的半熟狀態烤肉好吃！\n品質不像網路上講的，\n這次在這點了，\n烤斑鳩、魷魚、山豬肉。\n都很美味。不新鮮！地瓜酸的老闆也確認過（表示食材的確不新鮮！）獵人餐裡的魚也很乾，像隔夜的，竹筒飯沒料沒關係，連香味也沒有，補的地瓜也不夠，總之很差食材不新鮮，點風味獵人餐，地瓜是餿的...點了四份，四份都餿...東西 味道普通 cp值低', 2.6, 9, 23, 'r7scUDJzY@gmail.com', '0971 537 149', 4, '["星期一: 休息","星期二: 休息","星期三: 休息","星期四: 休息","星期五: 休息","星期六: 09:00 \\u2013 18:30","星期日: 09:00 \\u2013 18:30"]', 'https://restaurant-93377.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-16 07.58.52 下午', '2021-06-16 07.58.52 下午', 9, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 503, 1, '99362300', '阿里山逐鹿部落獵人宴', '602', '嘉義縣', '番路鄉一路1號', 23.44667900, 120.58346290, '小吃', '小吃 部落料理，平常比較少吃到，請務必嘗試看看。蠻特別的，竹筒飯好吃，其他普通，蒜頭到是給的很阿薩力！鄒族由於莫拉克八八風災.阿里山 鄉受到重創，讓這群逐鹿部落的鄒族人來到嘉義番路，在這個新天地重新展開生活，是鄒族的前哨基地平臺，族人們好客愛分享，更樂於接待旅人們，值得推薦旅人們前來一遊. ♥¸¸.•*¨*您的朋友 周志聰~烤山豬肉香又多汁 是全區賣相最好 也真的好吃\n竹筒飯細長份量少 而且只有糯米飯 不推\n竹筍湯是用筍乾 加一小塊豬肋排阿里山逐鹿部落 節目表演 也有烤山豬肉還有在地的 紅藜開發產品可以來此逛逛 在阿里山的路上', 3.7, 15, 56, 'O2LO1y@gmail.com', '05 259 0089', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.58.53 下午', '2021-06-16 07.58.53 下午', 15, 0);
