INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 509, 1, '14106881', '高頂農莊餐廳', '312', '新竹縣', '橫山鄉大山背3鄰45-2號', 24.69888070, 121.13735970, '小吃', '小吃 口味菜色不錯，只是我個人習慣，喜歡用另一個小碗喝湯配飯，餐廳卻因人手不足無法提供，我只好用盤子吃飯，才能配湯⋯\n當天點了\n小鍋雞湯500\n半隻雞600\n青菜\n炒香菇\n共1440元\n店內有售雞蛋一盒100元+紙盒押金10元共110元土雞好吃,人手不足,人潮較以往少多了運動後用餐，還有無敵戶外用餐與山景老板自己養的雞很好吃！ 雖然沒有很多菜單但每一道菜都非常道地而且實在！ 假日很擠建議大家平日前往最佳！ 禮拜二公休！老闆非常娘親切\n地方也很清幽\n露營區很乾淨', 3.9, 77, 300, 'dIuAZ1TH@gmail.com', '03 593 5839', 4, '["星期一: 10:00 \\u2013 18:00","星期二: 休息","星期三: 10:00 \\u2013 18:00","星期四: 10:00 \\u2013 18:00","星期五: 10:00 \\u2013 18:00","星期六: 10:00 \\u2013 18:00","星期日: 10:00 \\u2013 18:00"]', 'https://zh-tw.facebook.com/peakmansion/', '0.jpg', '[0.jpg]', '2021-06-16 07.59.09 下午', '2021-06-16 07.59.09 下午', 77, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 510, 1, '23553439', '紅蟳龍餐廳', '312', '新竹縣', '橫山鄉312', 24.71211500, 121.13894100, '小吃', '小吃 養兩池的鱘龍魚， 還有幾隻山豬， 地點很幽靜 ，菜色也不錯， 價格也合理。就是讚。12345是在此家', 3.5, 4, 14, 'vOmxZm@gmail.com', '0938 103 807', 4, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.10 下午', '2021-06-16 07.59.10 下午', 4, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 511, 1, '57503659', '橘園餐廳', '312', '新竹縣', '橫山鄉橫豐產業道路15號號', 24.70294840, 121.13515020, '小吃', '小吃 老闆娘非常務實，會根據我們人數，告知我們點幾樣菜就夠了。我們打電話問地點在哪裡時，老闆娘說我們住的住宿地點就已經有餐廳，不需要跑到她那邊吃飯。（最後發現我們住的地方就在這家餐廳的對面街）真的是一個很特別的老闆娘。後來我們還是去吃了，很好吃，（客家菜是較偏油偏鹹的料理，但老闆娘的食材很天然，所以吃完不會覺得口乾舌燥），而且每個餐桌都各自安置於鐵皮木屋裡面。吃飯時不會被鄰桌打擾，但可以隱約聽到對面鄰桌人家吃飯的聲音，有一種鄉間歲月靜好的感覺。\n我們問老闆這邊夏天應該也很清幽涼爽吧，老闆卻很誠實說「夏天還蠻熱的」。真的很老實可愛。野放放山雞肉質超👍菜蠻好吃的\n基本要點仙草雞+土雞肉\n吃了第3次了地方真的不好找，這裡是一個很很調適平時勞祿身、心靈的好地方。超不顯眼的餐廳，口味不錯，菜好大盤', 4.2, 52, 218, 'Y3gKqi9wH@gmail.com', '03 593 1284', 2, '', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.11 下午', '2021-06-16 07.59.11 下午', 52, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 512, 1, '81321064', '橫山美而美', '312', '新竹縣', '橫山鄉新興街104號', 24.71660050, 121.14169830, '早午餐', '早午餐 服務好、做餐速度快、餐點好吃😋、奶茶喝起來順口形象管理還可以提升。專業流程再提高會更完美。年輕老闆服務超好，東西好吃！服務態度很好  早餐也不錯 環境很舒服也很乾淨東西好吃平價，老闆態度又好，超推的早餐店。', 4.8, 52, 250, 'lhM0HpPcUa@gmail.com', '03 593 5893', 1, '["星期一: 05:00 \\u2013 11:00","星期二: 05:00 \\u2013 11:00","星期三: 05:00 \\u2013 11:00","星期四: 05:00 \\u2013 11:00","星期五: 05:00 \\u2013 11:00","星期六: 05:00 \\u2013 11:00","星期日: 05:00 \\u2013 11:00"]', 'https://www.facebook.com/meimei.Hengshan/', '0.jpg', '[0.jpg]', '2021-06-16 07.59.17 下午', '2021-06-16 07.59.17 下午', 52, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 513, 1, '22603845', '大洲黃金蛋小吃店', '266', '宜蘭縣', '三星鄉上將路二段463號', 24.69235240, 121.72869910, '小吃', '小吃 傳統料理\n什錦麵，飯，好吃美味經濟實惠👍\n只賣中餐\n乾淨衛生\n待客親切😘\n黃金🥚是招牌😋\n配合時令\n當季蔬菜🥬\n海鮮🦞🐟\n偶而家庭聚會\n可以預購外帶\n芹菜炒鯊魚～父親的最愛🥰料多 味實在蔬菜小吃讚，尤其魚類更佳\n\n家庭味道，非常便宜價廉物美 老板很親切', 3.9, 48, 187, 'RTWhDq@gmail.com', '03 954 3863', 1, '["星期一: 11:30 \\u2013 14:30","星期二: 11:30 \\u2013 14:30","星期三: 11:30 \\u2013 14:30","星期四: 11:30 \\u2013 14:30","星期五: 11:30 \\u2013 14:30","星期六: 11:30 \\u2013 14:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.34 下午', '2021-06-16 07.59.34 下午', 48, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 514, 1, '21206704', '玉里小吃', '266', '宜蘭縣', '三星鄉大義三路45-5號', 24.68589510, 121.71345540, '小吃', '小吃 路過進來吃的小吃店，吃一下飽是可以的，味道還行，蒼蠅有點多，小菜的小捲味道還不錯順路經過吃中餐，本來不抱任何期待，結果沒想到令人驚艷，從炒飯、乾麵到小菜、蛤蜊湯、豬血湯，全部口味道地，完勝宜蘭市中心店家！沒菜單，用餐環境有酒客吵雜，老人家許多人都在此喝酒聊天，小菜要詢問價格再點比較妥當，蝦仁炒飯香氣十足，配上一瓶蘋果西打超爽真的超級好吃又有人情味\n經濟又實惠\n一定要來吃的👍', 4.1, 85, 348, 'x4xDFwz@gmail.com', '03 989 7028', 4, '["星期一: 09:00 \\u2013 19:00","星期二: 09:00 \\u2013 19:00","星期三: 09:00 \\u2013 19:00","星期四: 休息","星期五: 09:00 \\u2013 19:00","星期六: 09:00 \\u2013 19:00","星期日: 09:00 \\u2013 19:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.35 下午', '2021-06-16 07.59.35 下午', 85, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 515, 1, '92111792', '大洲魚寮', '266', '宜蘭縣', '三星鄉上將路二段888號', 24.68766690, 121.71940380, '小吃', '小吃 餐點新鮮，素食料理不錯套餐很豐盛\n空間舒適\n庭院造景讓人放鬆現在，正視東西好吃的問題，是非常非常重要的。因為，巴甫連柯相信，在任何一個成功的後面都有著十五年到二十年的生活經驗，都有著豐富的生活經驗，要是沒有這些經驗，任何才思敏捷恐怕也不會有，而且在這裡，恐怕任何天才也都無濟於事。這讓我的思緒清晰了。東西好吃，發生了會如何，不發生又會如何。若沒有東西好吃的存在，那麼後果可想而知。門捷列夫講過一句值得人反覆尋思的話，天才就是這樣，終身勞動，便成天才！ 這段話對世界的改變有著深遠的影響。由於，若無法徹底理解東西好吃，恐怕會是人類的一大遺憾。希臘相信，無論誰，只要他還活著，你就不能稱他是幸福的。這段話的餘韻不斷在我腦海中迴盪著。儘管如此，別人往往卻不這麼想。東西好吃的發生，到底需要如何實現，不東西好吃的發生，又會如何產生。探討東西好吃時，如果發現非常複雜，那麼想必不簡單。東西好吃似乎是一種巧合，但如果我們從一個更大的角度看待問題，這似乎是一種不可避免的事實。布克萊曾說過，修鑿可以使道路平直，但只有崎嶇的未經修鑿的道路才是天才的道路。這啟發了我。4.4號光顧\n訂位臉書私訊留言訂位成功 但店家還是電話關心用餐事宜\n用餐環境 很棒\n餐點品質 雖不是每道滿意  但大部分都很棒\n出了兩個錯  中間的小飲品忘了上 另一個是鍋物的豬肉給錯給了羊肉 導致我整鍋無食用 我怕羊味\n提醒 當服務人員上菜時 發現顧客餐點幾乎沒有食用 應該要主動關懷 其實我一直再等你們發現 這上述缺失\n但後來我還是主動告知了\n\n會給五星不是因為你們有事後道歉跟補償\n是因為你們真的是一家有在關心顧客的餐廳 當天或許是忙了點 才有一些缺失\n\n期待下次光顧好吃\n愛海鮮的一定要來\n餐點吃很飽', 4.2, 1329, 5582, 'fQwBLa6@gmail.com', '03 955 9110', 2, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期二: 休息","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00"]', 'https://www.facebook.com/dacho.seafood/', '0.jpg', '[0.jpg]', '2021-06-16 07.59.37 下午', '2021-06-16 07.59.37 下午', 1329, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 516, 1, '49284183', '黑肉古早冰', '266', '宜蘭縣', '號 No. 431, 上將路二段三星鄉', 24.69274020, 121.72898380, '小吃', '小吃 炒飯，炒麵都很好吃，份量足夠，冰品種類也很多，份量十足，又很好吃地點好停車\n冰不是很綿密\n香蕉🍌味道不錯\n其他嗎？嗯\n自己去式吧主餐和冰都超讚！炒飯口味很有特色粒粒分明，冰淇淋又好吃口味又豐富又健康(許多老闆娘自創口味)，客人來宜蘭首推小吃之一👍老闆娘熱愛製作美食，常常研發新菜單\n不但好吃而且吃不膩，非常推薦冰很好吃 很綿 老闆娘 老闆態度極好 服務極佳 環境也很衛生 炒飯炒麵都很特別 有一種難以形容的好吃感❤️', 4.4, 44, 194, 'QTOXPv@gmail.com', '03 956 7821', 4, '["星期一: 11:00 \\u2013 20:30","星期二: 11:00 \\u2013 20:30","星期三: 11:00 \\u2013 20:30","星期四: 11:00 \\u2013 20:30","星期五: 11:00 \\u2013 20:30","星期六: 11:00 \\u2013 20:30","星期日: 11:00 \\u2013 20:30"]', 'https://www.facebook.com/oobahfood/', '0.jpg', '[0.jpg]', '2021-06-16 07.59.38 下午', '2021-06-16 07.59.38 下午', 44, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 517, 1, '67145535', '大洲肉羹', '266', '宜蘭縣', '三星鄉上將路二段475號', 24.69213400, 121.72849000, '小吃', '小吃 岳母宜蘭人特地來嘗鮮，排隊時的感受，內用直接進入坐跟櫃檯點，外帶排隊點餐，外帶排了很久，內用比我晚來兩三組的都吃完準備結帳，我的才準備開始煮，很另人傻眼。\n\n吃完如果好吃星數再加，不然方式真的不行\n\n肉羹不錯吃四星，湯麵各兩星，價格四星，整體服務一星，建議內用評分會比較好肉羹不錯，價格也可接受!\n而且方便停車~\n往從羅東三星方向時可以來嘗嘗~便宜、動作快,最重要好吃。\n但等的人很多。。。在地人才知道的肉羹店，中午用餐時間人蠻多的但老闆娘記性超強，用講的點餐都沒漏上餐點，夏天很熱不建議來，肉羹很新鮮又好吃但有時候羹勾芡的太濃我不喜歡，有時候又剛好，可能碰運氣吧😅但口味是我喜歡的微蒜頭香，滷肉飯也不油膩小孩很喜歡，豬血湯蠻大碗也新鮮，小菜種類多，有時候會更換不會每天ㄧ樣，敢吃辣的人建議點大豆乾加自製辣油。停車很方便大洲國小旁都能停，旁邊有間全家便利商店，吃飽去學校散步越南姑娘一大早就開賣，東西也不錯，好吃。', 4.2, 262, 1100, 'SQaBjJX@gmail.com', '03 956 4569', 1, '["星期一: 05:00 \\u2013 14:00","星期二: 05:00 \\u2013 14:00","星期三: 休息","星期四: 05:00 \\u2013 14:00","星期五: 05:00 \\u2013 14:00","星期六: 05:00 \\u2013 14:00","星期日: 05:00 \\u2013 14:00"]', 'https://tachou.business.site/', '0.jpg', '[0.jpg]', '2021-06-16 07.59.39 下午', '2021-06-16 07.59.39 下午', 262, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 518, 1, '17315321', '大隱精緻休閒農園', '266', '宜蘭縣', '三星鄉大埔三路241號', 24.67980100, 121.71929200, '小吃', '小吃 家常菜很合胃口，蔥爆豬肉好吃，鯛魚沒土味，Cp值高，闆娘很親切！這次跟家人聚餐慶祝\n吃 五千元桌菜\n\n料好實在，那個烤魚真的好吃😋\n大家吃超飽，五千一桌超划算的👍我們是來這裡吃晚餐的，合菜一桌好像是2000吧！食材一般，但是料理的很不錯吃，份量也很足。老闆娘很親切熱情 ，菜色也很不錯尤其沙鍋魚頭令人回味。廚師炒功很厲害，蔥爆豬肉非常好吃！', 4.2, 129, 542, 'omc9GQ8U@gmail.com', '03 989 8963', 4, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 休息","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.40 下午', '2021-06-16 07.59.40 下午', 129, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 519, 1, '49567515', '分洪堰水餃', '269', '宜蘭縣', '冬山鄉長春路217號', 24.68835000, 121.73654400, '小吃', '小吃 水餃餡鹹超！\n蛋炒飯烏漆媽黑的一盤！\n吃完肚子痛拉肚子！😫附設停車場，餐點平價好吃\n咖哩飯跟韭菜水餃很讚👍👍👍\n逛完旁邊的宜農牧羊場可以來吃！\n老闆老闆娘很親切大推，東西平價又好吃，水餃，燴飯，魚丸湯正點水餃很好吃，老闆人很親切！水餃不錯，滷肉飯也好吃。', 3.8, 10, 38, 'ZteK3qVDX@gmail.com', '03 953 0130', 4, '["星期一: 10:00 \\u2013 17:00","星期二: 10:00 \\u2013 17:00","星期三: 10:00 \\u2013 17:00","星期四: 10:00 \\u2013 17:00","星期五: 10:00 \\u2013 17:00","星期六: 10:00 \\u2013 17:00","星期日: 10:00 \\u2013 17:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 07.59.41 下午', '2021-06-16 07.59.41 下午', 10, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 520, 1, '41768855', 'YU YU 雩雨 Brunch & Afternoon Tea', '269022', '宜蘭縣', '冬山鄉光華二路36號', 24.68275780, 121.73134000, '小吃', '小吃 整體環境很棒，明亮整潔又舒適。\n餐點很精緻，套餐只要加180元非常的優惠，每一道菜都是主廚用心設計烹調的。\n在上菜的時候都會解說選用的食材，以及建議的食用方式。\n在吃的過程中，除了可以享受食物的美味還有溫暖的服務。\n\n超推薦👍🏻\n\n吃完也可以在園區走走散步一下運用在地食材創造出各式餐點，口味和口感都相當精緻而超超好吃，且物超所值（強烈建議一定要加點套餐！！！）餐點有歐洲的感覺\n戶外有動物可以餵\n店家免費提供飼料給小朋友\n老闆接待很客氣\n大面落地窗氣氛很好 溫度很好 音樂很好\n給好評食材新鮮好吃，很多東西都跟在地小農採購，每道菜都很用心準備並且詳細介紹。店員親切，氣氛很輕鬆。令人想再訪。好吃特色法式料理 環境舒服 最有印象的是水牛奶做的起司（白色的那個）', 5, 82, 410, 'utPzhj8@gmail.com', '0979 217 888', 4, '["星期一: 休息","星期二: 11:00 \\u2013 17:00","星期三: 11:00 \\u2013 17:00","星期四: 11:00 \\u2013 17:00","星期五: 11:00 \\u2013 17:00","星期六: 11:00 \\u2013 17:00","星期日: 11:00 \\u2013 17:00"]', 'https://www.facebook.com/yuyuafternoontea2020', '0.jpg', '[0.jpg]', '2021-06-16 07.59.43 下午', '2021-06-16 07.59.43 下午', 82, 0);
