INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 663, 1, '35790854', '古亭居酒屋', '300', '新竹市', '北區城北街57號', 24.81008790, 120.96374690, '居酒屋', '居酒屋 ', 3.2, 5, 16, '8RvbgVB@gmail.com', '03 523 2185', 4, '', null, '', '', '2021-06-16 08.02.33 下午', '2021-06-16 08.02.33 下午', 5, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 664, 1, '68628965', '天狗舞居酒屋', '300', '新竹市', '1, No. 50之1號光華一街北區', 24.81715040, 120.97336110, '居酒屋', '居酒屋 東西新鮮好吃不小心就包場了，晚餐六點才營業！聽說是新竹人會吃的居酒屋，出餐緩慢只有我們ㄧ桌客人也會是剛好也只有一個廚師，兩人吃了2800結帳是有點傻眼，因為烤串說好的每樣兩串，我們以為是混搭的兩串也可以任選，結果完全不是...變成總量都兩倍出餐🥲\n\n牛肉串太肥了，筋多咬不太動！明太子雞肉蠻好吃的，松阪豬炒水蓮也不錯，適合下酒菜，可惜生啤第一輪很順泡泡很細，第二輪感覺變質像變海尼根一樣好苦喔\n\n再點了highball也非常非常淡...基本上沒有威士忌的味道了！\n\n整體環境很舒服，價錢蠻高的！一人沒有1200應該走完不出來，走出來也非常無感的吧螢墨魚新鮮, 山葵醋味增醬汁非常特別 極推已經來了三四次，每次來的感覺都不錯！是一個很推薦的地方，適合情侶，適合帶岳父，適合聚餐的好地方！老闆專業！超道地日本味！', 4.5, 127, 572, 'HumyfbeLv@gmail.com', '03 535 6525', 4, '["星期一: 休息","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 休息"]', 'https://www.facebook.com/tingumaihs/', '0.jpg', '[0.jpg]', '2021-06-16 08.02.33 下午', '2021-06-16 08.02.33 下午', 127, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 665, 1, '74682524', '私嚐串燒-居酒屋-大同店', '300', '新竹市', '北區大同路132號', 24.80614050, 120.96762960, '居酒屋', '居酒屋 個人覺得食物部分好吃&CP值不錯，尤其味噌湯頗值。因沒點飲料故不評論。\nIG按讚就送的炸杏鮑菇分量蠻多的，可以看出店家的大器。\n但是店家不太會“做人“…\n今日點了牛肉鳥蛋，上面加了芥末、送的炸杏鮑菇上面也撒了芥末鹽，我是個非常不喜歡芥末的人，相信也有許多人怕or不吃芥末，若是放旁邊還能挑掉or不沾即可。但店家並未主動告知or詢問客人便直接加入料理裡，這點是我覺得超級可惜的…之前來吃過一次，感覺還不錯，這一次來感覺極差，不知道是否老闆不在，還是有其他分店，所以不在乎這裡客人的感覺，店裡空位好幾個，卻讓店外的客人一直等，等著店員聯絡前面未到的客人..現場候位基本一小時起跳，很NG..剝皮辣椒類不錯有特色\n起司雞肉燒、明太子雞肉、培根麻糬，都還好😫😫不知道在吃什麼，原本以為很有特色慕名而來，看來這趟是白來了每次來點的東西都差不多！\n因為好吃所以每次來都點一樣的☺️\n再配上啤酒清酒真的好讚啊👍\n是下班後來解放的好去處😊😊😊居酒屋的價位，每道菜都是精心烹調，拍照享用美食兩相宜，特別推薦炸絲瓜，外皮酥脆，內裡有滿滿的湯汁非常好吃！', 4.7, 1332, 6260, 'uRTRuiFcUY@gmail.com', '03 525 4860', 2, '["星期一: 18:00 \\u2013 02:00","星期二: 18:00 \\u2013 02:00","星期三: 18:00 \\u2013 02:00","星期四: 18:00 \\u2013 02:00","星期五: 18:00 \\u2013 02:00","星期六: 18:00 \\u2013 02:00","星期日: 18:00 \\u2013 02:00"]', 'https://www.facebook.com/%E7%A7%81%E5%9A%90%E5%B1%85%E9%85%92%E5%B1%8B-1666997900254172/', '0.jpg', '[0.jpg]', '2021-06-16 08.02.34 下午', '2021-06-16 08.02.34 下午', 1332, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 666, 1, '33755456', 'Dining BAR KAORU', '300', '新竹市', '東區大同路98號', 24.80507990, 120.96844200, '居酒屋', '居酒屋 很多日式酒點供選擇\n可以唱日本歌\n老闆娘態度好安靜居家風的居酒屋，熟客居多。\n食物日式風味，先點杯啤酒再慢慢點菜，可以好好享受在日本的感覺\n\nAs a regular customer, I appreciate not only the delicious Japanese cuisine but also the atmosphere here.\nAfter the whole tired working day, you may need a quiet place to enjoy a cup of beer or Japanese Sake.\nHere, you can have the slow food at your own pace.親切的微笑，料理的美味，讓人有回家的感覺。所有的食材都是要新鮮預約的東西好吃，東西都是用日式調味料所料理\n有很多種酒類\n適合小酌，也可以吃飽', 4.4, 27, 119, 'aoLX5CpF5@gmail.com', '03 522 1197', 4, '["星期一: 休息","星期二: 18:00 \\u2013 00:00","星期三: 18:00 \\u2013 00:00","星期四: 18:00 \\u2013 00:00","星期五: 18:00 \\u2013 00:00","星期六: 18:00 \\u2013 00:00","星期日: 18:00 \\u2013 00:00"]', 'https://www.facebook.com/Dining-Bar-Kaoru-195487134356438/', '0.jpg', '[0.jpg]', '2021-06-16 08.02.36 下午', '2021-06-16 08.02.36 下午', 27, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 667, 1, '09525816', '卡桑の酒場', '300', '新竹市', '東區光復路二段567號', 24.80303370, 120.98046230, '居酒屋', '居酒屋 蠻好吃的；冰箱4大座，看了就爽爽的，裡面國產、進口啤酒都有，非常搭下酒菜本來很期待他們烤物 因為我自己本身也愛吃居酒屋\n結果吃一口超級鹹\n果然還是不能看評價而去一間店用餐環境非常chill，很適合和朋友一起來這邊小酌，餐點好吃👍🏻服務生每個都很漂亮～重點是服務態度很好唷！宵夜好去處，價位基本上也能接受，店內氣氛也不差，而且很多漂亮的銷售人員可以欣賞，是個小酌聊天聚會的好地方環境清潔 服務態度好 是一個可以與朋友一起來吃飯的場所 又可以來一點啤酒小酌一下', 4, 473, 1892, 'hLAcl9a@gmail.com', '03 562 0935', 2, '["星期一: 17:00 \\u2013 01:00","星期二: 17:00 \\u2013 01:00","星期三: 17:00 \\u2013 01:00","星期四: 17:00 \\u2013 01:00","星期五: 17:00 \\u2013 01:00","星期六: 17:00 \\u2013 01:00","星期日: 17:00 \\u2013 01:00"]', 'https://www.facebook.com/kasang5620935/', '0.jpg', '[0.jpg]', '2021-06-16 08.02.38 下午', '2021-06-16 08.02.38 下午', 473, 0);
