INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 749, 1, '10689415', '䒩㖀火鍋/七堵美食/七堵火鍋/七堵餐廳/七堵小吃/基隆小火鍋/基隆美食推薦', '20642', '基隆市', '七堵區明德一路106號', 25.09866830, 121.71602060, '小吃', '小吃 好吃、平價，肉的份量又足夠，大推！湯頭不會讓人口渴 但價位在七堵算中高價，菜盤配菜多澱粉類 (南瓜、地瓜等) 有一尾蝦子 整體CP值普通好吃，但是越來越貴老闆不要再漲價好嗎？食材新鮮、肉品質很棒，服務很好！推！門前道路占車位，沒辦法停車吃飯，東西也還好', 4.1, 70, 287, 'G8F37n@gmail.com', '02 2455 3737', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.helehotpot.com/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.35 下午', '2021-06-16 08.24.35 下午', 70, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 750, 1, '62509632', '無名麵店', '206', '基隆市', '七堵區永富路83號', 25.09857120, 121.71258290, '小吃', '小吃 麵的湯頭清淡無味道，衛生堪憂！旁桌酒客吵鬧，氣氛很差！東西好吃又便宜，古早味的湯麵超好吃\n蚵仔魚卵...都是早上跟咖仔町拿的！很新鮮\n小菜每天都不一樣哦\n20年的小吃攤👍🏻👍🏻👍🏻乾麵\n油豆腐ㄧ塊\n嘴邊肉\n總共80\n小菜新鮮好吃老店了，乾麵大碗又便宜，小菜也蠻有水準的好吃', 3.4, 7, 24, 'EZycP6ELI3@gmail.com', '02-29178844', 4, '["星期一: 05:00 \\u2013 14:30","星期二: 05:00 \\u2013 14:30","星期三: 05:00 \\u2013 14:30","星期四: 05:00 \\u2013 14:30","星期五: 05:00 \\u2013 14:30","星期六: 05:00 \\u2013 14:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.36 下午', '2021-06-16 08.24.36 下午', 7, 0);
