INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 752, 1, '46284545', '阿美麵店', '206', '基隆市', '七堵區崇智街2-3號', 25.09703670, 121.71468090, '小吃', '小吃 下午兩點去吃也不錯~東西便宜又好吃七堵老店，店員服務態度很差，對外地來的都很不客氣，沒有排隊動線，在地老客戶可以插隊，東西普通也沒有比較便宜，CP值超低，店員態度不是很好，尤其是50幾歲男店員，態度差臉很臭，本人不建議去光顧，以後都不會來這家吃🤬價格便宜好吃，但是切滷味的店員找完錢沒洗手也沒戴手套繼續切我挑的食材，結果吃到拉肚子= =建議不要點滷味很不錯的麵店.\n價格便宜.\n老闆和氣友善.難怪幾乎都是熟客.', 4.1, 396, 1624, 'qIoG7Xtip@gmail.com', '02 2455 3595', 1, '["星期一: 休息","星期二: 06:00 \\u2013 21:00","星期三: 06:00 \\u2013 21:00","星期四: 休息","星期五: 06:00 \\u2013 21:00","星期六: 06:00 \\u2013 21:00","星期日: 06:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.38 下午', '2021-06-16 08.24.38 下午', 396, 0);
