INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 754, 1, '44766273', '懿品小珍 城上城店', '204', '基隆市', '安樂區麥金路231-10號', 25.12228100, 121.72115100, '小吃', '小吃 麻辣湯麵的湯簡直跟喝辣椒水沒兩樣 而且送餐送這麼慢 快氣死 每次差點在上工前幾分鐘才送達芝麻蒜香的涼麵配上一碗蛋花貢丸味增湯（柴魚味😋）尤其是這涼涼的天喝著熱呼的麻辣鴨血豆腐，真的是太幸福了🥰軟嫩的鴨血豆腐、香又順口的湯頭，難得有能整碗都嗑光的麻辣湯頭😆 環境整潔舒服，停車方便，老闆和闆娘人超級親切，一有空擋就出來關心客人口味是否合適，平價美食竟能這麼用心經營，真的是大推👍👍👍豬油拌飯真的是美味又划算方便好吃，填飽肚子的好地方。\n麻辣臭豆腐美味\n涼麵，乾麵，小菜\n水餃蛋花湯都不錯好好吃的一家店，口味超讚，尤其是麻辣鴨血冬粉，麻辣夠味，湯頭超讚，值得推薦👍', 4.4, 211, 928, 'lfFfQy1eQf@gmail.com', '02 2433 2728', 1, '["星期一: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期二: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期三: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期四: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期五: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期六: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30","星期日: 11:00 \\u2013 14:30, 16:30 \\u2013 20:30"]', 'https://www.ephot.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.39 下午', '2021-06-16 08.24.39 下午', 211, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 755, 1, '24206558', '極品鮮餐廳', '206', '基隆市', '七堵區光明路64號', 25.09705410, 121.71526710, '小吃', '小吃 旁邊夾娃娃店機台壞掉 台主叫我們找老闆 我們也在店內消費了 結果叫老闆直接不理我臉色超差直接走掉? 不用理客人就對了價格尚合理，但菜，湯普遍偏鹹，要清淡須先告知.這間店為七堵較具規模的之一餐廳舉凡宴會，少則兩人，多則20桌（10人一桌）都沒問題，價位上與基隆的小峨眉差不多，有賣飲料啤酒等等，生日、全家聚餐，都是不錯的選擇地方，停車場在餐廳對面，也非常方便，沒去過的可以試試看。價錢親民，假日還有菜單以外的私房料理，適合家人朋友聚餐。真的超好吃，熱炒桌菜都有，份量足又便宜，工作人員服務專業充滿熱情，空間寬敞舒適，整體氣氛絕佳，對面超大停車一小時才20元，基隆唯一推薦。', 3.6, 289, 1040, 'syoZ0jXF@gmail.com', '02 2456 8822', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.40 下午', '2021-06-16 08.24.40 下午', 289, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 756, 1, '90608183', '菜集口廚房', '206', '基隆市', '七堵區自治街18號', 25.09650320, 121.71276360, '小吃', '小吃 老闆態度很好\n香腸跟紅糟肉都很好吃，看到評論特地加點香腸果然沒有失望\n但是菜脯蛋煎的太焦了，希望能夠注意火候\n打開便當看到這樣115覺得很值得瓜子肉飯好吃，份量夠吃的飽老闆娘人很好 天天笑容超甜美 讓人去買就很開心 炸排骨 香腸很推薦！菜裡面吃過幾次長頭髮...第一次吃，沒想到居然如此之美味，連配菜都好好吃，夠味，4樣配菜沒有一樣讓我覺得不好吃的，每一樣都超讚，非常值得推薦！', 4.7, 18, 85, 'DYklwecuq@gmail.com', '02 2455 1277', 4, '["星期一: 10:00 \\u2013 20:00","星期二: 10:00 \\u2013 20:00","星期三: 10:00 \\u2013 20:00","星期四: 10:00 \\u2013 20:00","星期五: 10:00 \\u2013 20:00","星期六: 10:00 \\u2013 20:00","星期日: 10:00 \\u2013 20:00"]', 'https://reurl.cc/mRvnA', '0.jpg', '[0.jpg]', '2021-06-16 08.24.41 下午', '2021-06-16 08.24.41 下午', 18, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 757, 1, '31808113', '香廚燴飯', '206', '基隆市', '七堵區崇智街2-8號', 25.09681680, 121.71479440, '小吃', '小吃 口味似乎還沒穩定，\n年青老闆還要加油每道料理的鹹度落差很大，宮保雞丁比炒飯鹹很正常，但湯品卻清淡的離奇甚至接近無味。\n\n炒飯中規中矩份量頗多價格不算高，但宮保雞丁跟一般其他間很不一樣，不知道是失敗品還是怎麼回事，湯汁沒有收乾以至於炸過的雞肉已經皮肉分離，算是滿可惜的。青椒羊肉燴飯好吃\n燴飯感覺像是用奶油製作的\n非常特別煮的不錯\n大部分菜色都好吃\n地方小餐廳來說生意相當不錯\n適合全家大小一同前往用餐七堵的餐館排行最爛第一名，實至名歸，無人能及，期待在我搬出七堵前能看到倒店', 3.9, 79, 308, 'VNrp1blu@gmail.com', '02 2456 5271', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 22:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.42 下午', '2021-06-16 08.24.42 下午', 79, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 758, 1, '47722131', '陳林麵線(七堵店)', '206', '基隆市', '七堵區南興路89號騎樓', 25.09663280, 121.71270170, '小吃', '小吃 好好吃的麵線喔！而且老闆的兒子有點帥🖤\n下次有去基隆一定要再去吃，大推的店～麵線大腸、蚵仔料多大碗。油飯好吃。我覺得很便宜 每次吃都不用錢油飯用批貨的很難吃沒有關係，但怎麼連肉羹就像是假肉羹一樣硬又非常的難吃，一碗40不貴，但真的不好吃就算貴。麵線好吃不油膩！', 3.8, 30, 114, 'elKjxsktzb@gmail.com', '0935 748 022', 4, '["星期一: 06:30 \\u2013 20:00","星期二: 06:30 \\u2013 20:00","星期三: 06:30 \\u2013 20:00","星期四: 06:30 \\u2013 20:00","星期五: 06:30 \\u2013 20:00","星期六: 06:30 \\u2013 20:00","星期日: 06:30 \\u2013 13:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.43 下午', '2021-06-16 08.24.43 下午', 30, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 759, 1, '99969436', '來來餃子館', '206', '基隆市', '七堵區開元路81號', 25.09570100, 121.71287300, '小吃', '小吃 七堵老字號的店家，鄰近慶濟宮，位於同樣是老店的宣騰莊對面。\n店裡仍然保持著古早樣貌，裝潢很簡單，有適合少人數的方桌以及多人用的大圓桌。\n除了餃子以外，主食類包含數種麵食（牛肉麵、炸醬麵、鍋燒麵、肉絲炒麵）以及炒飯。另有小菜可自取或現點現切。\n\n水餃 NTD 5/顆\n在店裡可以見到現包的樣子。水餃是單一口味，高麗菜的肉餡，調味很剛好，不沾醬也好吃。皮比較有厚度，口感比較Q而有嚼勁。\n\n牛肉麵（小） NTD 85\n紅燒的湯頭，搭配的麵條是白麵。牛肉本身的口感和調味比較像西式料理，搭配起來有點微妙。\n\n盤裝小菜 NTD 30/盤\n在桌面的小菜可自取，部分在冰櫃的小菜則需由店家人員拿。選了泡菜、茄子和小魚豆干，味道中規中矩，份量滿多的。\n\n寵物友善，用餐可帶狗狗進入。用餐時間人很多，但是上餐很快，有好吃的水餃，炒飯，炸醬麵每次去必點，又有冷氣吹。餃子，炒飯都不錯，2人以上可點酸辣湯大的，料超多，小菜：酸白菜，筍絲，小魚干也不錯，價位合理，值得推薦來這裏就是吃水餃，每粒五元，現包現做，口味尚可，筍絲味道魯的不錯，紫菜蛋花湯CP值最高。最愛的組合是15顆、小酸和酸白菜，每一口都是暖暖的人情和回憶！', 4.2, 37, 155, 'h5NnNFA@gmail.com', '02 2456 4054', 4, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.45 下午', '2021-06-16 08.24.45 下午', 37, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 760, 1, '33861326', '阿忠食堂', '204', '基隆市', '安樂區安和一街2巷21號', 25.12242410, 121.72435350, '小吃', '小吃 個人很愛他們家燴飯跟粥，服務態度又不錯，覺得是這附近比較好吃的東西，現炒，料又多。第一次來，本來不想吃炸的，就點滷雞腿，結果沒想到也是炸過再滷的，而且滷過的外皮有點奇怪的味道，可能像是麵包起酥皮的味道，我個人是不太喜歡。但也許你喜歡也不一定。\n味增湯蠻甜的，也不合我的口味。\n海鮮咖哩炒麵有豬肝和肉片，但同事是因為不吃豬肉才點海鮮炒麵，結果海鮮只有兩隻蝦子，其他是豬肉……只能怪他沒有事先問一下海鮮炒麵有沒有豬肉。生意非常得好\n價格便宜又實在\n老闆娘態度親切\n我想開業至今都可以維持這樣的生意並不是沒有原因的長庚醫院附近用餐，都要走一小段路到後面的巷弄才有比較多選擇！而這家就還行吧～\n\n香酥雞腿飯，雞腿厚度有點不足，但蠻多汁的，可惜稍微有點冷掉，畢竟他們早上11點就一堆便當外送，不然熱熱的吃應該很棒，配菜的部分可能裝的盤子太大，感覺份量有點少，味道稍微有點油，但是可以接受的！\n\n建議外帶，內用環境不是很好，堆滿很多雜物，地板也有點油油黑黑的，桌子也是有些油膩，上餐速度快，小姐有禮貌語言，不算內用環境，整體還是很不錯的～炒飯火侯夠，鍋氣十足，好吃。', 4.1, 91, 373, 'XQHt6mXu@gmail.com', '02 2433 6655', 1, '["星期一: 11:00 \\u2013 21:00","星期二: 11:00 \\u2013 21:00","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 11:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.46 下午', '2021-06-16 08.24.46 下午', 91, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 761, 1, '11615945', '歐林鐵板燒', '206', '基隆市', '七堵區自治北街3號', 25.09736210, 121.71234560, '小吃', '小吃 從住在國家新城到搬到碇內、從70元吃到130元的平價鐵板燒，湯品、白飯和紅茶都是自取吃到飽，除了衛生需加強，吃了20多年~都是滿滿的回憶😋現在開頭特意說明，因為google最低是一星，如果不是為了給差評，我絕對不會按一星，因為這間餐廳今天的態度絕對值得我給負分，服務的態度簡直是噩夢，沒看過這麼沒禮貌，上班態度差，對待客人十分不禮貌跟粗鄙的廚師。\n\n我吃這間鐵板燒很多年了，絕對超過6年以上，小時候開始吃到現在，今天真的是讓我對這家這麼多年的愛毀於一旦的時刻。\n\n我們家有人不吃牛肉，所以會說要把特定的東西跟牛，分開一點，至少不要靠太近，互相噴濺。\n\n今天點了一個鱈魚，「點餐的時候有特別說要離牛肉遠一點」不要放一起，但是他直接把牛肉放在鱈魚旁邊炒，我們都明白是一塊板子，但至少可以放遠一點吧，不要那麼近到有互相噴濺痕跡。\n\n跟廚師反應，廚師只是態度很差的說「我又沒有碰到魚」，我真的蠻問號的，難道你不知道會噴濺嗎，啊放那麼近是...？\n\n你跟我說，魚要很久，所以不可能幫我做完一道再做下一道，ok先不論這個論點的真實性，但就我吃你家那麼多年，你們過去的廚師會用「罩子罩著避免互相噴濺」，或是「給兩道菜一個安全距離」，放遠一點，難道這都做不到，當我是白痴嗎。\n\n今天你們小姐一直跟我說是我感覺問題，但事實上就是距離很近，你做鐵板燒你不知道會互相噴濺？\n一直說是我觀感問題，瘋狂規避談論噴濺問題，你以為說換一塊魚肉給我，我要的是這個嗎？我要的是一個解釋跟態度，不是敷衍帶過。\n\n從一開始我坐下來廚師要炒菜，就口氣很差跟我說：「加辣嗎」，我問他：「什麼東西」，他口氣超級差回我：「看你有什麼東西要加辣啊」，我受不了就去飯鍋旁邊跟我正在盛飯的媽媽說，還故意說很大聲給另一個廚師聽。\n\n抱歉，吃你家那麼多年，連我家人住院都外帶去醫院給他們吃，是真的很熱愛，經過今天以後，不是因為炒菜或噴濺問題，而是你們的態度，我以後不會再來吃了，有人問我，我也絕對不會推薦你們家，因為這個態度真的非常讓人失望^_^\n\n吃了那麼多年的熱愛，超級白費。服務4顆，整潔3顆，餐飲重整潔（需加強），口味4.5，因為吃了好幾碗的白飯，還有免費暢飲的紅茶及例湯喝到飽，總評3.8顆星…飯可以吃到飽，cp值不錯的一間鐵板燒雖然每次去，幾乎都是點相同口味的餐，但就是吃不膩！店裡的廚師們很帥氣、外場人員也一樣忙碌，都辛苦囉！😃😃😃', 3.6, 141, 508, 'WEOMg2iO6@gmail.com', '02 2456 3188', 1, '["星期一: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.46 下午', '2021-06-16 08.24.46 下午', 141, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 762, 1, '16361027', '山城古厝黑豆腐', '206', '基隆市', '七堵區開元路94號', 25.09598770, 121.71286360, '小吃', '小吃 豆腐料理真的好吃到好想吃好多盤🐒\n很～～嫩～～很～～嫩\n口感很好！！！\n酥炸黑豆腐、豆腐羹👍👍👍\n\n廚師味道拿捏不錯，小家庭經營，推薦來第三次才想到菜色跟氛圍很像小時候常去的土雞城。青菜脆口，酸菜豬肚湯夠酸，炸豆腐趁剛上桌吃外皮酥香但就是真的燙。白斬雞肉質甜，紅燒豆腐手藝棒，我們2人830元，價格適中還不錯，看了大家的評價特別來試試看。有種家常菜的感覺，本來不想要點豆腐，但看起來是招牌，吃了一口，還好有點! 外蘇內嫩! 本來還想試試薑絲大腸，可惜去的時間太晚了，已經賣完了! 期待下次去可以吃到咯!\n\n可以收信用卡和linepay, 但有5%手續費。建議還是現金支付咯。老板人好菜也好, 我們一家每次去欣欣回來後, 中午一定在這吃午餐', 4.4, 93, 409, 'EmMqbE@gmail.com', '02 2456 2629', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00"]', 'http://www.8898go.com/mco/index.htm', '0.jpg', '[0.jpg]', '2021-06-16 08.24.47 下午', '2021-06-16 08.24.47 下午', 93, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 763, 1, '05154158', '國富飯店', '206', '基隆市', '七堵區南興路63巷1號', 25.09672200, 121.71350960, '小吃', '小吃 充滿回憶的地方，令人懷念的蚵仔酥，不捨⋯新聞報導，業者要結束經營86年的餐廳了同學會都辦在這裡  老闆還會幫忙拍照真有趣!!推菜好吃很古早味 尤其是油飯炒薑末和麻油 雞腿肉👍美味而價格實在的古早味辦桌料理！\n像肝肫這種很難找到的傳統料理都還能傳承地如此美味，實在難得！\n藏身於七堵菜市場，若非在地人帶路，外地人實在不容易發現～\n部份手路菜需要提前備料的，可以跟店家先行討論預定，以免向隅～', 3.9, 152, 593, 'kjAT0vGP7@gmail.com', '02 2456 1588', 2, '', 'https://www.facebook.com/%E4%BE%86%E4%B8%83%E5%A0%B5%E4%B8%80%E5%AE%9A%E8%A6%81%E5%93%81%E5%98%97%E7%9A%84%E5%9C%A8%E5%9C%B0%E5%A5%BD%E5%91%B3%E9%81%93%E5%9C%8B%E5%AF%8C%E9%A3%AF%E5%BA%97-121735304521155/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.48 下午', '2021-06-16 08.24.48 下午', 152, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 764, 1, '03537408', '三歌野味', '206', '基隆市', '七堵區大華二路94號', 25.10517390, 121.69447890, '小吃', '小吃 海鮮 土雞 野菜\n都是很棒的料理。\n仙草雞湯 味道鮮美。對我來說這口味上我只覺得還行，跟兒子討論的結果是一樣的菜色我煮得會比較好吃🤣🤣，因為菜偏油，對我們來說口味偏淡，兒子們去一次就不想再去了！😅😅價位在這樣的店算便宜的了！不過點了一個白斬雞，雞毛卻很多沒拔乾淨...可能很多人不能接受，但蚵仔烘蛋很好吃，但一樣偏油喔！其他菜色味道不夠鹹其實我還滿失望的...😅😅價格公道，經濟實惠，停車方便。老闆娘熱情，東西新鮮，好吃哦！餐點口味不錯，環境有點簡陋，外場服務人員臉真的很臭，態度也不是很好', 3.8, 165, 627, 'A5P4pH@gmail.com', '02 2455 4881', 1, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://m.facebook.com/SanGeYeWei/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.49 下午', '2021-06-16 08.24.49 下午', 165, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 765, 1, '83977271', '木野和風家庭料理', '204', '基隆市', '安樂區安和一街2巷30號', 25.12263600, 121.72504800, '日式料理', '日式料理 好吃！老闆大方今天中午去用餐\n網上寫11:00開門但超過11:00我們一直在門口等\n直到看到店員在幫客人（本來以為是老闆）點餐我們才知道可以進去了（門口還掛著休息中）\n\n點完餐後店員告知11:15才能開始做餐點（可以接受）\n中間媽媽想要吃藥問有沒有開水\n店員說沒有（戴帽子女性沒戴眼鏡）\n態度也沒有很好\n\n在廚房做餐點店員一直在聊天沒戴口罩衛生何在？\n\n不管餐點好不好吃是都不可能再去消費！日式家庭料理\n每天都有不同的海鮮特餐可以選擇！\n墨式牛小排炒飯，粒粒分明的飯粒，牛肉不會過硬，多了一點孜然的香氣東西新鮮好吃，料理用心，份量也夠，老闆娘熱心介紹菜單。還可以，但蒸蛋60元一小杯太貴', 4.2, 421, 1768, 'ZWwKMsJZ@gmail.com', '02 2431 1037', 2, '["星期一: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期二: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期三: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期四: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期五: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期六: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10","星期日: 11:15 \\u2013 14:00, 16:30 \\u2013 20:10"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.24.53 下午', '2021-06-16 08.24.53 下午', 421, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 766, 1, '09184519', '䒩㖀火鍋/七堵美食/七堵火鍋/七堵餐廳/七堵小吃/基隆小火鍋/基隆美食推薦', '20642', '基隆市', '七堵區明德一路106號', 25.09866830, 121.71602060, '火鍋', '火鍋 好吃、平價，肉的份量又足夠，大推！湯頭不會讓人口渴 但價位在七堵算中高價，菜盤配菜多澱粉類 (南瓜、地瓜等) 有一尾蝦子 整體CP值普通好吃，但是越來越貴老闆不要再漲價好嗎？食材新鮮、肉品質很棒，服務很好！推！門前道路占車位，沒辦法停車吃飯，東西也還好', 4.1, 70, 287, 'xhnEgqo6MD@gmail.com', '02 2455 3737', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.helehotpot.com/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.55 下午', '2021-06-16 08.24.55 下午', 70, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 767, 1, '73868411', '火鍋世家 基隆長庚店(城上城)', '204', '基隆市', '安樂區麥金路231-6號1樓', 25.12219820, 121.72120940, '火鍋', '火鍋 變成個人鍋後\n東西都變的很“單薄”\n想要續訂的動機也消失了\n\n用塑膠袋裝熱食\n也會怕塑化劑\n\n相信其他的消費者也是一樣的感受\n這樣下去店家的生意一定不會好可以訂位服務熱忱，活蝦食材肉品新鮮，多種價位提供選擇。飲料備料區乾淨、裝潢新穎停車方便！我第一次來店消費，服務態度很好,食材新鮮，火鍋番茄好甜喔,最主要蝦子蝦腸有剔掉對食材都很用心。小孩吃的很開心可刷卡，外加10%，目前有抽獎活動\n優點：菜盤比別家量多種類多，cp值算高，環境整潔，有罐裝飲料，咖啡機，冬瓜茶，奶茶等，尤其挖冰的器具很乾淨\n缺點：肉很小份，約5-6片，且口感略鬆散，牛奶是奶精粉，故喝起來就是很淡的水原味湯昆布還算好喝\n冰不錯吃\n飲料很好喝\n環境乾淨\n服務不錯\n肉很少 又不太好吃\n菜盤很多菜 但不好吃', 4.3, 144, 619, 'TkWSscKR@gmail.com', '02 2434 1988', 2, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 11:30 \\u2013 21:00"]', 'http://www.suabu.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.56 下午', '2021-06-16 08.24.56 下午', 144, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 768, 1, '49801055', '築間幸福鍋物 基隆七堵店', '206', '基隆市', '七堵區明德一路156號', 25.09825700, 121.71476300, '火鍋', '火鍋 很久沒來七堵店吃了，這次點了海陸感覺很差。\n缺點：\n1. 調味區辣椒空了很久沒補。\n2. 飯很硬，煮的很難吃。\n3. 菜盤份量感覺比過往少很多。\n\n優點：\n1. 會員點數換的松阪豬肉份量不少。（多補一顆星）\n\n結論：不會再來七堵店吃了！只能說沒有450元的價值，還要收取一成服務費，加起來的價碼可以吃更好的餐廳，真心不推薦，不會再去第二次。七堵築間吃了很多年，初期品質跟份量都讓人很滿意，之前有一陣子品質及環境變很差所以很久沒去，今天跟老公又過去吃小火鍋，環境及品質有改善很多，但菜盤變很少，高麗菜只有二大片份量明顯不足，吃完只好跟老公又去買東西吃，這樣的價格跟份量真的有點搭不上，近期應該不會再過去了……吃過不少次築間，用餐過程，食材品質都很好，原先用餐店家因天冷一位難求，間接找到七堵店。\n湯底依舊選擇奶香、石頭鍋（魷魚），沒想到整個淡到不行，像是一鍋被瓜分成三鍋，由於湯底部分是另外加價換購的，不免讓人覺得被騙了⋯不知道是不是因為加盟店⋯品質沒有受到嚴謹把關，實在覺得很可惜⋯服務不友善，雖然是實習生', 3.9, 425, 1658, 'MU0hqR72@gmail.com', '02 2455 5050', 2, '["星期一: 11:00 \\u2013 21:00","星期二: 11:00 \\u2013 21:00","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 11:00 \\u2013 21:00"]', 'https://www.jhujian.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.24.56 下午', '2021-06-16 08.24.56 下午', 425, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 769, 1, '30175451', '上賀鍋物', '204', '基隆市', '安樂區安樂路二段153-1號2號樓號', 25.12098500, 121.72363090, '火鍋', '火鍋 濃醇湯底！質感食材！食材新鮮，湯頭很棒，肉品在基隆地區用料實在～大家有空來提驗就知道囉……當然高級食材這個地方也是很棒的……用餐前突然有個東西在眼前從上面飄到盤子內，仔細一看發現是一大團黑色的灰塵，抬頭才發現頭頂正上方的電風扇積了厚厚的灰塵，灰塵隨著風一擺一擺的，於是喚來服務生關掉電風扇和更換盤子。\n\n幸好是在用餐前讓我發現，但是心裡還是有疙瘩在，一直很擔心是不是在這之前就已經有其他灰塵落進湯內，只是灰塵太小看不出來？但又怕要求更換湯鍋很像奧客，在撈火鍋時只要看到火鍋料上也黑色小點點就會讓我的心提起來，真的很心累。\n\n努力說服自己別在意，不要浪費食物，認真說食材是好吃的，建議整潔做得更全面，尤其是擺在客人正上方的電風扇更應該注意清潔。最喜歡這裡的松阪豬♥️♥️♥️超級好吃 湯頭也很甜主菜優，配菜一般。', 4.2, 300, 1260, 'okTzsI@gmail.com', '02 2432 2788', 2, '["星期一: 休息","星期二: 11:00 \\u2013 00:00","星期三: 11:00 \\u2013 00:00","星期四: 11:00 \\u2013 00:00","星期五: 11:00 \\u2013 00:00","星期六: 11:00 \\u2013 00:00","星期日: 11:00 \\u2013 00:00"]', 'https://www.facebook.com/pg/24322788shanghe/about/?tab=page_info', '0.jpg', '[0.jpg]', '2021-06-16 08.24.59 下午', '2021-06-16 08.24.59 下午', 300, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 770, 1, '35722641', '鍋仙涮涮鍋 [基隆旗艦店]', '204', '基隆市', '安樂區麥金路197號1樓', 25.12081580, 121.72163940, '火鍋', '火鍋 資深服務阿姨的態度幹練但頗冷淡，對於小孩子要點低消、還是要加錢？也沒有說明得很詳細。雖然不能說她不對或做錯什麼事，但是如果能讓客人感受到更多服務業的熱誠就好了！\n\n口味一般般，價格偏中高，如果沒有打折的話應該不會再去了，曾經有2次吃完有拉肚子的現象，不知道是不是剉冰、粉圓、愛玉的問題？\n\n冬天更偏好去吃一期的「霸味薑母鴨」一位難求。就點個三鮮鍋（蝦，蛤蜊，蚵）送盤肉再加共鍋費100以上兩個人二碗飯，510元吃好飽火鍋品質一般。資深員工服務態度需要改進。\n今天目睹短髮資深阿姨對客人講話態度很差，另外對年輕工讀生也不友善，在外面吃飯時，阿姨罵人的聲音直接從廚房傳出。根本沒在管服務態度和店內裝潢\n因為肉肉劵送一整疊，吃火鍋就是要吃肉，不多解釋，來基隆必吃服務態度是很優良的，人多時難免會有失誤，沒關係啦！值得消費哦！', 4, 29, 116, 'ludoPZyRcW@gmail.com', '02 2432 1448', 4, '["星期一: 11:00 \\u2013 00:00","星期二: 11:00 \\u2013 00:00","星期三: 11:00 \\u2013 00:00","星期四: 11:00 \\u2013 00:00","星期五: 11:00 \\u2013 00:00","星期六: 11:00 \\u2013 00:00","星期日: 11:00 \\u2013 00:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.00 下午', '2021-06-16 08.25.00 下午', 29, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 771, 1, '22884396', '竹棧日式涮涮鍋', '204', '基隆市', '安樂區樂利二街62巷274號', 25.12102170, 121.71589780, '火鍋', '火鍋 👉🏻現在有打卡送肉的活動（每桌限一盤）\n老闆娘很熱情，點了麻辣的湯底，非常喜歡，湯頭不會死鹹，有花椒的香氣，辣的很舒服😌\n這次點了羊肉，肉片有厚度，煮完吃起來有脆脆的口感，好吃👍🏻\n還有小美冰淇淋和飲料自助吧，整體而言是很推薦的鍋物店～今天一個人去吃了，很清淡溫和的湯頭，喝完也不會口渴（說真的其他家的煮一煮都變死鹹）。天氣很冷她們忙得團團轉，但還是很親切的招待每桌客人，光是服務就大加分。祝福她們生意可以再好一些！我覺得這間湯頭，甘甜有蔬菜味，高湯喝多了，都不會口渴，除了原味湯，還有藥膳，味噌\n麻辣鍋，牛奶鍋\n相信店家會詳細跟大家介紹\n我覺得直得品嚐，報給大家知道湯頭完全熬煮能吃的健康，料多實在，環境也很乾淨，重點是老闆娘非常親切～很棒的一家店，有機會再來關顧👍又來吃....好吃，健康，又方便！\n打卡送肉肉\n支持在地美食', 4.5, 74, 333, 'WlJOXAjD@gmail.com', '02 2433 0988', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期二: 休息","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.01 下午', '2021-06-16 08.25.01 下午', 74, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 772, 1, '38583443', '三媽臭臭鍋（基隆明德店）', '20642', '基隆市', '七堵區明德一路94號', 25.09877300, 121.71647700, '火鍋', '火鍋 如果可以不給星 就都不想給\n是老闆娘嗎？ 態度有夠差 吃個飯還要看你的臉色  開店服務態度還要這麼差勁 也不是要多好 但至少別對誰都要那麼差好嗎 ⋯ 一點都不客氣 不想做生意可以不要做啊 又沒有人強迫你。\n老闆態度還比較好一點⋯火鍋CP值很高，好吃。能不給心就選不給心老闆態度口氣極差女店員態度超差 臉臭口氣差 不建議來花錢受氣如果態度惡劣有辦比賽你一定冠軍', 3.5, 156, 546, 'gWx7LAXh@gmail.com', '02 2456 1718', 4, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.02 下午', '2021-06-16 08.25.02 下午', 156, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 773, 1, '18120163', '歡樂購特大牛排（七堵店）', '206', '基隆市', '七堵區明德一路148號', 25.09817180, 121.71502650, '牛排', '牛排 內用有自助飲料、湯、麵包跟冰淇淋\n店內沒半個人\n除了湯匙叉子、牛排的鐵盤外其他全都塑膠到底\n湯碗沒洗乾淨\n玉米濃湯普普\n牛肉，是讓我人生第一次覺得自己不是在吃肉\n我可能味覺敏感，但我真的無法接受服務生品質與效率有待提升，先說我隔壁桌從我們進場就是用餐完的桌面，一直到我們吃完，桌面還是沒人收，或許真的太忙了～服務生就算是版娘店長等級也不該對詢問點餐的客人大吼「等一下好嗎」其實只需要一句「請稍候一下」一句話的表達沒有很困難，客人也不過只問了一句怎麼點餐而已，too over～不可能再訪坐很久都沒人理，桌上前一組客人的也沒收走...坐了快10分鐘只好自已找一張乾淨的桌子坐，最後點了招牌特大牛排300元味道還行但我點7分熟可能太老了，沙拉感覺像冰很久只吃了一口就放棄了。主菜牛排不錯 沙拉蠻新鮮。\n飲品譬如人參雞湯 和咖啡喝起來味道怪怪 ，吐司一般。價位這樣覺得不大ok比孫東寶好吃，外帶份量也很紮實', 3.4, 265, 901, 'wWx15u@gmail.com', '02 2455 8985', 4, '["星期一: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.04 下午', '2021-06-16 08.25.04 下午', 265, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 774, 1, '15672347', '麥味登七堵崇信店', '206', '基隆市', '七堵區明德一路186-2號', 25.09772000, 121.71298000, '早午餐', '早午餐 用餐環境算乾淨，但食物會偷工減料，點了招牌堡裡面只有三、四塊碎肉混在蛋裡，以為客人會沒有感覺嗎？賣的不算便宜，不會再去了吐司烤的有夠硬，我以為在訓練咀嚼肌，飲料點大杯，也給成中杯飲品記得點去冰，冰塊很多。\n點餐後服務台還忘記上菜，呆坐十幾分鐘，問了才發現根本沒準備，消費者記得注追蹤自己餐點。我家倆寶貝從小到大都指定去的早餐店。東西好吃，老闆親切', 3.3, 33, 109, 'nEUMuL@gmail.com', '02 2455 5768', 1, '["星期一: 06:00 \\u2013 14:00","星期二: 06:00 \\u2013 14:00","星期三: 06:00 \\u2013 14:00","星期四: 06:00 \\u2013 14:00","星期五: 06:00 \\u2013 14:00","星期六: 06:00 \\u2013 14:00","星期日: 06:00 \\u2013 14:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.09 下午', '2021-06-16 08.25.09 下午', 33, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 775, 1, '63965475', '幸福微笑早午餐', '206', '基隆市', '七堵區明德一路112號', 25.09859090, 121.71591460, '早午餐', '早午餐 等餐超過半小時，沒有照單出，點大冰奶沒冰塊，是溫的夏天不推薦內用，因為沒冷氣會很熱。\n優點是有電視，用餐同時看新聞。\n老闆也蠻好搭話的。\n但是食物給人的感覺較隔壁東方美精緻。\n奶茶也比較沒加那麼多糖，比較好喝。熱壓吐司滿好吃的，奶茶有香氣但很清爽幸福微笑早午餐店有各式鐵板麵，各式的三明治和漢堡好好吃喔！還有好喝的飲料，有如其名(吃完餐後讓你微笑又幸福)耶耶耶讚讚讚！不是很乾淨，難停車', 4.3, 49, 211, '9ltUW6gu@gmail.com', '02 2456 7928', 4, '["星期一: 05:30 \\u2013 13:00","星期二: 05:30 \\u2013 13:00","星期三: 05:30 \\u2013 13:00","星期四: 休息","星期五: 05:30 \\u2013 13:00","星期六: 05:30 \\u2013 12:00","星期日: 05:30 \\u2013 12:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.11 下午', '2021-06-16 08.25.11 下午', 49, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 776, 1, '60269614', '翻白眼鬆餅吧七堵店', '206', '基隆市', '七堵區自治街16號', 25.09654430, 121.71274520, '早午餐', '早午餐 東西是好吃，我反覆買了好幾次。\n\n可老闆、老闆娘態度不優耶。\n很自大、\n\n甚至，只是反映曾經買的鬆餅有燒焦味，\n希望今天的鬆餅不要有燒焦味，\n隨即就生氣叫我不要買了、不做我的生意；\n還說，這樣代表不適合妳的口味，請妳不要買。\n我說『我有付錢給你啊。』\n店家說『我還沒有拿錢吧。請妳拿回去，我不賣給妳。』\n\n這真是令人大開眼界啊！！！！！鬆餅口味眾多…而且老闆還可以幫你客製喔😃…大家走過路過千萬不要錯過👍👍是好吃的紅豆甜而不膩\n價格便宜想吃甜食的話\n我的七堵第一選擇老闆娘人美又好相處，極推薦吃看看!CP值很高的一家鬆餅店\n價格不貴 餐點份量很足\n鬆餅外酥內軟，內餡也給的十分大氣！！吃完超有飽足感\n鮮奶茶順口好喝', 4.4, 81, 356, '2rfivS7P2@gmail.com', '0906 316 806', 1, '["星期一: 休息","星期二: 08:00 \\u2013 19:00","星期三: 08:00 \\u2013 19:00","星期四: 08:00 \\u2013 19:00","星期五: 08:00 \\u2013 19:00","星期六: 08:00 \\u2013 19:00","星期日: 08:00 \\u2013 19:00"]', 'http://www.facebook.com/QIDUWAFFLE', '0.jpg', '[0.jpg]', '2021-06-16 08.25.12 下午', '2021-06-16 08.25.12 下午', 81, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 777, 1, '87743361', '漢堡屋早餐店', '206', '基隆市', '七堵區自治街48號206', 25.09522380, 121.71327650, '早午餐', '早午餐 環境髒亂 室內悶熱 漏餐也不在意客人等待時間 桌面不會主動收桌三明治漢堡口味不錯，鐵板麵沒有單點，套餐不便宜口味普普。用餐環境以早餐店來說很好，可惜店內一直有親友的嬰幼兒長期進駐，餵奶尿布逗弄玩鬧都在小小的店裡的用餐區（一位歐巴桑帶的），想安靜享用早餐者不建議！蘿蔔糕超好吃有冷氣、服務態度佳動作很慢 趕時間不建議買', 3.9, 39, 152, '0YG7td2a87@gmail.com', '02 2455 4009', 4, '["星期一: 06:00 \\u2013 12:00","星期二: 05:30 \\u2013 12:00","星期三: 06:00 \\u2013 11:00","星期四: 05:30 \\u2013 12:00","星期五: 05:30 \\u2013 12:00","星期六: 05:30 \\u2013 12:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.13 下午', '2021-06-16 08.25.13 下午', 39, 0);
