INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 783, 1, '48046773', '台灣小吃古董民藝', '600', '嘉義市', '西區興達路180號', 23.48903460, 120.44075780, '小吃', '小吃 完全沒有再回頭的機會 昨天晚上訂三桌 一桌3500 幾乎每道菜色不是辣 不然就是沒有味道 只剩下白斬雞 跟膽肝兩道可以接受 這等口味怎麼會評價這麼高店裏用台灣古物當裝飾，點類似的菜點，老板娘會提醒更改。服務親切古早味的台灣小吃，裡面有擺些古蕫級的雕像跟裝飾品，消費一般餐廳價格，不會很高，還會主動附贈每桌一碗免費菜頭丸子湯（台語圓圓滿滿）真正的古早味，特別是各式各樣的老東西收藏...古早味 還有四處擺設物品 件件讓人印象深刻 回到過去', 4.2, 238, 1000, 'z3ECEvDqYT@gmail.com', '05 231 0816', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://jay7134.pixnet.net/blog/post/226452569', '0.jpg', '[0.jpg]', '2021-06-16 08.25.39 下午', '2021-06-16 08.25.39 下午', 238, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 784, 1, '77524348', '郎 小吃店', '600', '嘉義市', '西區興業西路366號', 23.46791100, 120.43757010, '小吃', '小吃 虱目魚湯是無刺的，很新鮮好吃\n炒飯也很有味道\n份量很足夠上面推薦度及下面評分\n為個人對"小吃店"標準主觀感受\n如有冒犯還請見諒\n\n用餐環境:★★\n服務品質:★★★\n整體氣氛:★★\n餐點口味:★★★★\n多元結帳:★\n~CP值:★★★★★\n\n在地一家平價但卻口味美味的小吃店\n店內沒什麼特別的裝潢\n假日時候生意會挺不錯的\n但基本上吃吃就走不會等太久\n付款就是用現金沒其它的了\n停車的話外面有幾個停車格也還算可以\nCP值是真的高～\n適合不想花大錢但又想吃到不錯的好料！滷肉飯是古早味🤤\n好吃好吃好好吃 ♥打電話時，態度很差!平價的菜色，服務態度親切貼心。', 4.5, 34, 153, 'POsMC5Lv5@gmail.com', '05 285 6530', 4, '["星期一: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期二: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期三: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期四: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期五: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期六: 10:00 \\u2013 14:00, 16:30 \\u2013 22:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.40 下午', '2021-06-16 08.25.40 下午', 34, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 785, 1, '88852970', '仁愛路小吃', '600', '嘉義市', '西區仁愛路514號', 23.47688720, 120.44215660, '小吃', '小吃 飯類、麵類、湯類、鍋燒、魯味小菜，\n選擇也滿多的，\n價格也很親民。便宜好吃的好位置\n麻醬麵好吃肝連湯這麼大塊的不多\n唱完歌出來剛好祭祭五臟廟\n吃好吃飽回家睡覺乾麵加上餛飩湯，真的是很好吃的組合，特別介紹是五花肉，不蓋你，入口即化，保證登的上四或五星級的小吃價格親民，份量算多，他們的煎粿是傳統咖啡色的那種，一旁附的醬油不會死鹹，雞肉飯雖然稍微偏油，但雞肉軟嫩不乾柴，整體來說算好吃又便宜。點了乾麵跟湯麵\n桌上有放蒜泥，超棒\n味道ok\n份量ok\n價位ok', 3.9, 58, 226, 'ckCx5i@gmail.com', '05 223 7513', 4, '["星期一: 17:00 \\u2013 00:00","星期二: 17:00 \\u2013 00:00","星期三: 17:00 \\u2013 00:00","星期四: 17:00 \\u2013 00:00","星期五: 17:00 \\u2013 00:00","星期六: 17:00 \\u2013 00:00","星期日: 17:00 \\u2013 00:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.41 下午', '2021-06-16 08.25.41 下午', 58, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 786, 1, '23040975', '巷子裡的小餐館', '600', '嘉義市', '西區延平街373號', 23.47632300, 120.44496600, '小吃', '小吃 用餐體驗不佳，出餐緩慢，沒有告知已無餐點。\n剛開店就進去用餐，點了辣炒豬肉，同桌客人都吃完了，我去詢問服務生說去催餐，過了一下子出來說，剩下最後一份被廚師炒焦了，一直出餐給後面進來的客人，不知道為什麼發生這件事不馬上通知客人，讓我乾等，還要自己詢問才告知，不清楚有什麼誤會，願聞其詳？餐點選項多又好吃，服務人員親切，對幼兒友善，還有特約停車場，對開車族非常方便。地點在巷子內\n裝潢很復古\n餐點ㄧ點都不馬虎\n色香味俱全\n母親節來吃還送康乃馨\n誠意十足推👍換老闆後第一次來吃（吃看\n吃過之後，覺得之前比較好吃，現在餐點偏油，宮保雞丁雞肉不像之前鮮甜，偏乾柴⭐️，泰式椒麻雞還算好吃⭐️⭐️⭐️，炸豬排普通。⭐️⭐️\n因為點餐前服務生有保證說料理跟之前的一樣好吃，但吃了發現沒有，有點失望😅再加油囉！⭐️⭐️很小巧可愛的一間店 下次還會想要來😁', 4.4, 658, 2895, 'Y5gj37jYI@gmail.com', '05 223 3737', 2, '["星期一: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期二: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期三: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期四: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期五: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期六: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期日: 休息"]', 'http://facebook.com/373kitchen', '0.jpg', '[0.jpg]', '2021-06-16 08.25.41 下午', '2021-06-16 08.25.41 下午', 658, 0);
