INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 789, 1, '41905459', '小李子美食料理館', '600', '嘉義市', '西區南京路552號', 23.46833910, 120.43597710, '小吃', '小吃 勞工節連假週六中午，一進門，生意很好，4大+5小(分別2&3&5&5&6歲，完全沒戰鬥力)，菜單上都是小份的價格，點菜時都還特意叮囑服務生都要小份的就好，結果服務生自行幫加量，全部寫成中份的，等你結帳發現時，結帳的小姐才跟你道歉說服務生寫錯，會罵她，分明是詐騙，看評論，也有其他客人發生這樣的情形，太過份了。( ᴄʜɪᴀʏɪ | #西區 )\n各式聚餐的好地方\n店家有附設停車場很方便🅿️\n- - - - - - - -\n點了10幾道但都隨便拍拍😂\n主打台菜與#川菜 🇨🇳\n也有#蘿蔔糕 #牛肉捲餅 #烙餅 等東北麵食\n.\n豆酥鱈魚不死鹹我覺得ɴᴏ.1\n#鹽焗雞 跟炸蝦球也不錯\n- - - - - - - -\n▪️#鐵板雞球 166\n▪️#回鍋肉 96\n▪️#老皮嫩肉 96\n▪️#冬雪蝦球 196\n▪️#豆酥鱈魚 166\n▪️#櫻花蝦高麗菜 96\n▪️#鍋餅 68\n- - - - - - - - - -\n菜單上都是小份價格\n圖片是中份 大概6～8人份\n.\n➰#小李子美食料理館\n🏡嘉義市西區南京路552號\n🔛11:00-14:00、17:00-21:00\n.\n🏷️🔍#hh吃嘉義 #hh吃川菜\n.\n#嘉義美食 #新小李子川菜館 #川菜料理 #嘉義聚餐 #嘉義餐廳 #嘉義美食推薦 #嘉義美食地圖 #南京路今天去吃，感覺真他媽超級差，點菜過程我只有雪魚和炸蚵有說要中份，點菜人員竟然私自把其他的菜也改成中份，搞的一份炒牛肉220、宮保雞丁220，點9道菜，吃了2028元。真是幹，作生意不實在非常便宜，小份可以點多道一點覺得很棒！上菜速度蠻快的，好停車。鍋貼裡面還有湯汁非常好吃，腐乳空心菜味道剛剛好，宮保臭豆腐很香。炒飯雖然有點油但是炒的香，可惜炒飯的蝦子不是好吃的。第一次來，鹹淡適中～\n每一道菜都很棒！\n\n感謝店家的好手藝～', 4.1, 1033, 4235, '7BQc33@gmail.com', '05 285 5708', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://m.facebook.com/%E5%98%89%E7%BE%A9%E5%B8%82%E5%B0%8F%E6%9D%8E%E5%AD%90%E7%BE%8E%E9%A3%9F%E6%96%99%E7%90%86%E5%B7%9D%E8%8F%9C%E9%A4%A8-107509369367471/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-16 08.25.44 下午', '2021-06-16 08.25.44 下午', 1033, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 790, 1, '63235353', '文章小吃店', '600', '嘉義市', '西區民族路781號', 23.47507800, 120.44075400, '小吃', '小吃 之前有一次騎車經過就直接進去品嚐\n吃過以後發現如此平價又美味的小吃！居然就在這\n老闆很客氣、而且重點是他的水蓮只要$50\n雖然我今天沒有吃到、但是我上次有吃到\n老闆的炒功夫\n真的是一流的\n\n非常非常推薦便宜又好吃❤️\n#更新\n我終於吃到水蓮了！\n超級清脆好吃的啦！\n其他炒類也真的非常好吃～～什錦湯麵好吃。不過油有點多。好吃又便宜，老闆的功夫厲害！好吃，價格都很合理好吃，古早味，場地有點不OK', 4.4, 23, 101, '5aBUs9lDdG@gmail.com', '05 224 8177', 4, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', 'https://travel.yam.com/Article.aspx?sn=68621', '0.jpg', '[0.jpg]', '2021-06-16 08.25.46 下午', '2021-06-16 08.25.46 下午', 23, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 791, 1, '33080937', '阿德火雞肉飯', '600', '嘉義市', '西區文化路19號之三號', 23.47563760, 120.44987200, '小吃', '小吃 炒飯一般般，不鹹、太油，但是吃得飽\n煎蛋火候太過，吃起來有苦味，且部分沒有熟\n青菜有部分燒焦，炒得太老\n鍋燒麵太鹹\n感覺是為了求快而使用大火去炒，沒控制好反而降低了美味程度平日下午4點去買飯\n前面只有一位客人，我後面沒有其他客人，\n我也只點一份雞肉飯而已\n廚師態度很不好硬要我劃單\n如果忙的時候我會劃單，\n沒客人還態度差是火氣大嗎？\n重點！還邊嚼檳榔邊炒菜真的好噁心喔🤮\n還好我沒有像前一位客人一樣點快炒類\n口罩也是掛好看的根本沒帶\n真的覺得廚師個人衛生很糟糕在位置上餓了半小時，旁邊別桌的都吃完走掉了，比我們晚來的都要吃完了，我們點的東西還沒來，去問才說我們這桌的單子被忘了......為什麼要漲價把便當盒換大勒\n現在拿起來都變得朗朗\n懷念便當盒變大前紮實的感覺食材新鮮好吃！第一次驚豔炒鱔魚 鍋氣乾香 第二次去甜 水多 洋蔥多 可惜 ！', 3.4, 176, 598, 'yM4Jmyng@gmail.com', '05 225 3365', 1, '["星期一: 11:00 \\u2013 23:00","星期二: 11:00 \\u2013 23:00","星期三: 11:00 \\u2013 23:00","星期四: 休息","星期五: 11:00 \\u2013 23:00","星期六: 11:00 \\u2013 23:00","星期日: 11:00 \\u2013 23:00"]', 'https://www.facebook.com/aderturkeyrice', '0.jpg', '[0.jpg]', '2021-06-16 08.25.47 下午', '2021-06-16 08.25.47 下午', 176, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 792, 1, '24365837', '郭媽媽小吃', '600', '嘉義市', '東區市宅街23號', 23.47359390, 120.46015400, '小吃', '小吃 不行了 我從年輕人接手後發現 味道實在變得太多了 外帶乾麵水都沒瀝乾 麵都爛掉了 味道實在不夠 我都特地吩咐鹹一點了還是不夠味 這已經第二次了老闆夫妻很逗趣很親切\n東西份量足夠又便宜\n兩人這樣吃不到150\n很親民的銅板美食價格實在親民，在地知名早午餐店，豆皮湯很推薦年輕人接手\n口味不變…\n但其中有一位負責掌杓的\n全程不戴口罩…\n令人害怕！不錯全部料都很多又實惠價格也蠻歐給的讚欸好吃到爆', 4.3, 446, 1918, 'kGzTLdFto@gmail.com', '05 228 9647', 1, '["星期一: 06:00 \\u2013 13:00","星期二: 06:00 \\u2013 13:00","星期三: 06:00 \\u2013 13:00","星期四: 06:00 \\u2013 13:00","星期五: 06:00 \\u2013 13:00","星期六: 06:00 \\u2013 13:00","星期日: 06:00 \\u2013 13:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.25.48 下午', '2021-06-16 08.25.48 下午', 446, 0);
