INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 920, 1, '54204831', '初八麵食堂', '600', '嘉義市', '東區安樂街188號', 23.48251050, 120.45523900, '拉麵', '拉麵 料多實在，非常值得再來!帶小孩來用餐，環境乾淨，對小孩友善，東西好吃～料多味美、乾淨衛生，來訪很多次了!吮指回味、念念不忘，吃拉麵必選的店！店內環境乾淨，食物價格合理、好吃', 4.7, 2809, 13202, 'dHCLThVLl@gmail.com', '05 216 6808', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30"]', 'https://restaurant-4270.business.site/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.17 下午', '2021-06-16 08.28.17 下午', 2809, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 921, 1, '51983810', '吾妻拉麵うちラーメン', '600', '嘉義市', '西區北榮街171號', 23.48043300, 120.44566980, '拉麵', '拉麵 Ig搜尋👉維の美食日記 wei_eat_food\n\n［嘉義］吾妻拉麵\n\n這間隱藏在冰果室的拉麵店\n早上賣冰晚上賣拉麵 超酷的\n裝潢擺設也很簡約的工業風\n（好啦重點是拉麵！\n拉麵的湯頭是非常濃郁的雞白湯\n跟常見的拉麵湯頭很不一樣\n而且叉燒的部分也是選用雞肉\n不會很柴非常好吃～\n溏心蛋也是很滑順🤤\n麵條的話就蠻中規中矩\n吃完後還可以看到碗裡的小驚喜\n可是太好吃了 忘記拍😂\n好奇的朋友可以去吃吃看喔\n-\n原味雞白湯拉麵 $160\n-辛辣雞白湯拉麵$190\n味噌雞白湯拉麵$190\n\n身為不專業的拉麵品嚐者\n第一眼送餐來覺得擺盤視覺效果很美\n有洋蔥、青蔥、木耳、溏心蛋、雞叉燒、海苔\n搭配細麵條和湯頭\n光欣賞就令人食指大動\n\n湯底是濃郁但不會到濃稠的雞白湯\n麵體是細麵煮的較有嚼勁偏硬口感\n雞叉燒是炙燒過的雞胸肉有些嫩有些柴\n溏心蛋有些許酒味提香蛋黃某些部分比較熟了\n\n整體來說份量十足口味也不馬虎\n內用座位較少且每日有限量\n冰檸檬水自取、筷子還有挑選配色\n附近比較不好停汽車建議騎機車前往\n今天蠻幸運的\n一到場剛好有客人離席直接入座\n過不久就聽到老闆說已經賣完啦難得在嘉義可以找到攤車拉麵，\n像是回到日本一樣，懷念又感動。\n\n作為在嘉義少數的雞白湯拉麵，\n喝下第一口湯，口感濃郁，油香布滿口腔，\n可以感受到湯裡滿滿的用心，\n但雞湯味的濃厚度稍嫌不足，有點可惜，\n雞叉燒炙燒過，加強了肉的香味，\n這種做法相對比較少見，但是是成功的。\n湯裡附一顆溏心蛋，酒味稍重。\n\n吃下來整體而言是好吃的，份量也足，\n湯頭能再補強的話會更完美。\n\n在嘉義能吃到這種程度的雞白湯拉麵，\n對我而言是感動的，請繼續加油！\n\n最後，剛做好的麵碗燙死人阿！\n差點拿不下來，哈哈哈😅吃到的口味：原味、味噌\n先撇除湯頭來討論其他部分\n麵條意外的是偏硬的細麵，而且量很多，除非食量真的特別大不然不要加麵\n配菜有黑木耳、洋蔥、海苔、雞肉片，其中雞肉片是有用噴槍燒過的，但除了燒過的碳香味之外就沒什麼味道了，跟麵一起吃顯得味道上比較分離，不是很搭\n\n湯頭的感覺比一般店家濃，但也不到日式那種黏稠到會瘋狂巴著麵體的那種感覺\n味道上以原味來說，顯得像是日式濃的那種去加水稀釋的感覺，我品嘗得到湯頭的沉，可是吃麵的時候卻不太有味道，反而好像多一層水感，而且味道很直白，吃到後面大概30%麵量的時候更是開始膩\n味噌的部分我只有淺嘗，但我感覺到的是仿佛直接在原味裡面丟進一整塊味噌塊一般的感覺，一樣很直白，而且味道好濃\n\n店內分室內座跟室外，我坐到室外，身後就是機車道，吃飯途中感受不是很好，更別說室外偶爾會有蚊蟲出現\n而且出餐速度與各大知名拉麵專賣店相比慢上很多\n\n以一名拉麵愛好者而言，個人應該不會光臨第二次\n但也不是難吃，只是覺得各方面都略有不足\n如果是喜歡吃拉麵的人，建議不要保持太高期望光臨，親口感受再下定論雞白湯頭搭配上濃郁順口的蒜香我很喜歡\n麵是我喜愛的偏硬麵體\n配料也給的很大方\n糖心蛋好好吃\n可惜我最近無法吃海苔\n只能望著隔壁客人碗裡的海苔在心中落淚(啥)\n老闆超親切\n因為我的手很怕燙\n所以老闆走出攤位幫我把麵端到我桌前\n以後有機會會想帶朋友們吃吃看', 4.7, 70, 329, 'z5A312z0It@gmail.com', '02-36768246', 4, '', 'https://www.instagram.com/uchi_ramen/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.17 下午', '2021-06-16 08.28.17 下午', 70, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 922, 1, '78503046', 'MR豚骨拉麵', '600', '嘉義市', '西區仁愛路229號', 23.47120500, 120.44252120, '拉麵', '拉麵 湯頭濃郁有點偏甜，應該是口感問題。麵條點硬的還是覺得有點軟，稍微可惜。吃過幾次後，個人覺得沒有辦法給到四星以上，設計雖還不錯可客製化（麵軟硬湯濃淡）不過可惜的是吃起來真的很普通，為什麼要這樣說呢～因為點了招牌：極 豚骨拉麵吃完後覺得有點失望，雖然點了最濃湯頭但感覺口味還是太偏台式了，拉麵除了麵體湯頭外配料也都是靈魂所在，湯還算行、麵體普通再來配料就真的吃起來也soso，沒辦法真心覺得好吃，因為價格稍微偏高，所以我認爲點較便宜的拉麵才有達到價格與口味的平衡推薦 「極MR.拉麵」\n📌餐點五星：豚骨湯頭濃郁、搭配滑順麵條，吃起來非常夠味！點餐可選鹹度與麵條粗細，很貼心~這邊提醒鹹度普通是偏鹹了，如果選擇日本鹹度，就不太適合喝湯了！我喜歡麵條偏軟，吃起來仍保有一點Q彈，可以！極MR.拉麵是給厚叉燒，吃起來口感佳，軟嫩適中，非常推薦！\n📌服務五星：上菜速度很快，店內提供檸檬水（吃拉麵必備啊！）座位寬敞舒適、服務態度良好~\n📌其他四星：附近不好停車，建議機車前往。有搭配熊貓外送，也可先點餐直接外帶，都很方便。拉麵種類蠻多的，極·MR.拉麵的叉燒超乎想像的嫩，可惜炭燒味有點不夠，每碗都可以集點會讓人一來再來台南最愛吃的店之一，搬來嘉義還能吃最愛的餐點，超讚的', 4.3, 251, 1079, '1N3ET2C2@gmail.com', '05 236 1110', 4, '["星期一: 11:30 \\u2013 14:15, 17:00 \\u2013 20:45","星期二: 11:30 \\u2013 14:15, 17:00 \\u2013 20:45","星期三: 11:30 \\u2013 14:15, 17:00 \\u2013 20:45","星期四: 11:30 \\u2013 14:15, 17:00 \\u2013 20:45","星期五: 11:30 \\u2013 14:15, 17:00 \\u2013 20:45","星期六: 11:30 \\u2013 14:30, 17:00 \\u2013 20:45","星期日: 11:30 \\u2013 14:30, 17:00 \\u2013 20:45"]', 'https://tinachiang0824.pixnet.net/blog/post/467156567-%5B%E5%98%89%E7%BE%A9%5Dmr.ramen-%E8%B1%9A%E9%AA%A8%E6%8B%89%E9%BA%B5%E5%BA%97%28%E5%98%89%E7%BE%A9%29-%E5%B9%B3%E6%97%A5%E9%99%90%E5%AE%9A%E7%9A%8480', '0.jpg', '[0.jpg]', '2021-06-16 08.28.18 下午', '2021-06-16 08.28.18 下午', 251, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 923, 1, '43664440', '豚將拉麵-嘉義店', '600', '嘉義市', '東區文化路284號', 23.48358210, 120.44888000, '拉麵', '拉麵 超濃地獄拉麵很好吃，湯頭是我喜歡的辣度，豆芽菜新鮮，叉燒也很不錯，比較驚艷的是蔬菜可樂餅，完全是以前在日本玩時記憶中的口感跟味道，挺好吃的\n覺得不夠辣的人有辣粉跟胡椒可以加拉麵味道很好，但店內位置不多，人多時可能要多等一會辣味增拉麵不辣就算了\n一點味僧味都沒有\n都懷疑是不是端成豚骨了⋯\n自己加了一堆辣粉內用點完餐之後 等了快30分鐘想說餐點怎麼還沒好\n看裡面3個店員都在聊天很像沒事做一樣\n就問說我們的麵怎麼還沒好 隨後就端了一陀麵來 我就直接說是我們的餐點都沒來 不是要點加麵\n他們就頓了一下才開始有動作 隨後解釋說是按照先後順序出餐 啊重點是隔壁有比我們晚來的幾個客人 也都收到餐點 是要不要打臉打成這樣\n之後也沒有什麼解釋 態度感覺也不太好的直接退錢給我們 感受有夠差==第一次吃，還不錯👍🏽', 4.1, 388, 1591, 'U40cfJ6@gmail.com', '05 277 2702', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', 'https://m.facebook.com/wo284/?tsid', '0.jpg', '[0.jpg]', '2021-06-16 08.28.19 下午', '2021-06-16 08.28.19 下午', 388, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 924, 1, '39239299', '九湯屋日本拉麵-嘉義光彩店', '600', '嘉義市', '西區光彩街708號600', 23.47749880, 120.44107500, '拉麵', '拉麵 特濃白湯湯頭偏淡,麵體無法選軟硬 偏軟,叉燒一份2片 價格跟內容差不多原本開在圓環附近的時候就會吃了\n湯頭濃郁，叉燒肉好吃\n還有不少的海帶芽\n100初的價格吃得很飽普普很便宜加麵只要20塊\n加點叉燒有點失望，算符合價錢的拉麵吧東西好吃，點了黑蒜拉麵，喜歡蒜味再重一點，但對不喜歡蒜味太重的會很喜歡。小菜和拉麵湯頭也都超讚，價格便宜！算是一家平價拉麵，味道中規中矩，服務不錯有加分，附近景點多，適合飯後隨興走走。', 4.2, 254, 1067, '1TSINqS1K@gmail.com', '05 222 1296', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 休息","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 15:00, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.28.20 下午', '2021-06-16 08.28.20 下午', 254, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 925, 1, '58082527', '滿龍拉麵', '600', '嘉義市', '西區民權路386號', 23.48209570, 120.44817740, '拉麵', '拉麵 如果你是第一次去，老闆娘會很親切的為你介紹，我食量比較大，點了份豬排味噌拉麵加一份高麗菜，還送了一杯紅茶，真的有飽足感(沒照到高麗菜，30元的量是可以接受的)嘉義市平價的日本拉麵店。\nCP值算高哦。\n好吃。😍\n小菜的選擇也多。\n炸物也不錯吃。\n湯頭還蠻好喝的。\n學生們最愛的拉麵店。😍😍店內很髒，有一股刺鼻的味道，桌子黏黏的，味道也很普通，對不起價格。@Chiang吉：我個人是覺得人家規定怎樣就是怎樣不需要硬是去\n破壞人家的規定，人家就是看學生證加麵，不是因為你是學生而給你優惠。個人覺得不用這個貪小便宜，你也可以選擇不要吃這間但不要因為要不到\n自己想要的就給人家負評。\n你這樣跟那些搭高鐵哭腰為什麼學生票只賣大學生的鯛有什麼不同?好吃！個人特別喜歡他們的揚初炸豆腐，外酥內軟很好吃😋\n\n店內木頭的裝潢特別有吃日式料理的氣氛，如果需要廁所店家也願意外借(但是要問一下店員廁所位置)，斜對面是一座公園，吃飽剛好可以去散散步消化一下。', 3.7, 316, 1169, 'KUwTr7pWt@gmail.com', '05 229 0573', 2, '["星期一: 11:00 \\u2013 21:00","星期二: 11:00 \\u2013 21:00","星期三: 11:00 \\u2013 21:00","星期四: 11:00 \\u2013 21:00","星期五: 11:00 \\u2013 21:00","星期六: 11:00 \\u2013 21:00","星期日: 11:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.28.21 下午', '2021-06-16 08.28.21 下午', 316, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 926, 1, '89867446', '山越拉麵', '600', '嘉義市', '東區中正路349號', 23.47886670, 120.45055690, '拉麵', '拉麵 去年3月到訪。\n\n剛好回老家和老姐前往嘉義去晃晃，前一天好像是在電視節目上看到介紹，看完便決定前往。\n\n一到達店家沒有等很久就被店員帶入座，簡單的介紹點餐流程，店內環境不大，位置數相對也不會很多，可以理解需要等待才能入內用餐。\n\n因為當時還沒有很餓就點了一碗拉麵跟小菜，可以依照菜單上面的選項選擇自己喜好的口感，一碗麵一張單。\n拉麵的確蠻好吃，湯頭夠味肉也很軟嫩，跟老姐兩人一人一口不知不覺就吃完了。\n小菜的冷豆腐跟黑木耳也很爽口，不太餓吃完也是蠻滿足的。\n\n現在疫情期間應該無法內用，外帶也是不錯的選擇。\n\n回訪意願：🌕🌕🌕🌕雞湯系列非常推薦...嘉義少數好吃的拉麵店不錯吃 可是要排很久 以前cp值更高\n瘋狂漲價 2021/2/17再訪發現變成北部的價位了\n更：\n日期打錯也能嘴 看起來不太願意接受批評\n以前一碗120變成200 菜單中沒有註明是大盛\n料是多了不少 不過價位高很多也是事實\n單論美味度有四星吧\n在店內的服務態度不錯\n可惜店家回覆網路評論的態度似乎有待加強：）我算蠻常光顧的客人，本次會給予這麼低評價的原因下面說明\n1 . 應該沒記錯的話是5/2號的時候，一如往常開店前到店內拿取號碼牌，也是瘦瘦小小的小姐出來給予菜單，但因我應該算是第二批才能入店的順位(晚餐12號)，差一號就可以入店以前一直都沒有等到小姐出來再次給菜單，便入店詢問「我是下一號客人請問可以先拿菜單填寫嗎？」，但內場的一位胖胖先生回應口氣非常不好，他回應我「你出去等」態度也非常不耐煩，讓人感受非常差。\n2 . 後續還是入店到櫃檯才匆忙的點餐，等到餐點上桌時我發現桌上並沒有紅辣椒油，便問了那位發菜單的瘦小小姐，他只回應那就是沒有，也沒有意願幫我詢問的意思(隔壁桌都有那灌辣椒油，但因為我的隔壁是雙人在用餐，所以也不好意思跟他們拿取)。\n3 . 本身都習慣會先點加麵的部份，之前去照這樣點餐也是會說等等有需要再跟店員講加麵，但當天是主餐上桌，連加點的麵也一起上桌，等到吃完主餐的麵後，加點的麵根本已經結塊也涼掉了，不知道山越老闆是不是有注意到這點呢？\n\n總結：山越一直是我在嘉義的口袋名單，但店員的應對是不是還需要再注意一下，以及加麵這部份真的讓人問號。從你們在北榮街開業的第一天後 就是你們的忠實顧客 期間你們也生意越來越好 蒸蒸日上\n店面也從北榮街改到中正路上\n拉麵內容也越來越豐富 甚至還有限定款\n隨著物價上漲 你們的拉麵份量不比當初開業那麼大碗\n這好像是所有拉麵店從默默無名到名聲大噪\n比必經過程\n個人也認為你們是嘉義最好吃的拉麵店\n隨著名氣響亮 客人當然絡繹不絕\n面臨要備料 還要讓翻桌率更為順暢\n你們的作法領取號碼牌 當然是很公平\n\n但是 你們的作法 變來變去 我感到很奇怪\n當初是進店裡索取號碼牌\n一段時間過後 又改說工作人員 會出來發號碼牌\n明明那時候店面外就張貼進店零號碼牌\n體諒你們的員工的辛苦 就算了\n現在每每都麻提前半小時就去排隊了\n就外場的服務人員會給來排隊的客人號碼牌\n這樣的模式也習慣了好久\n\n就每逢假日 有回嘉 就想回山越吃拉麵\n也變成 回嘉的固定事項\n\n今天是真的感到很不開心☹️\n也早早的不到11點 今天又是第一個 開心😃\n就坐在外面等待 吃拉麵的美好心情\n就一點小問題變的很不愉快 台語就說是 袂宋\n到後面客人當然跟以往一樣慢慢變多了\n想說奇怪 怎麼 還沒有外場 來發放號碼牌\n就看到有客人直接進場領取號碼牌\n我心想 哇靠 我那麼早來就坐在那邊等\n明明是第一個人到的 等進門領牌也已經第四位了\n你們又改遊戲規則了  實在開心不起來\n疑問的是 有你們的員工也應該老早有看到了\n有客人早到外邊等待 怎不進門領取號碼牌\n你們是不是應該可以提醒一下呢\n我先拿到了以後 看到後面有客人到了\n也會幫忙提醒後面的人 要進門領牌了\n實在不開心 也沒心情吃拉麵了\n\n一樣評價從以前一樣不會改\n好吃的拉麵  不會一點小事就掉評價', 4.5, 1368, 6156, 'DUDYzW2Dr@gmail.com', '05 225 1882', 2, '["星期一: 17:00 \\u2013 21:00","星期二: 休息","星期三: 17:00 \\u2013 21:00","星期四: 17:00 \\u2013 21:00","星期五: 17:00 \\u2013 21:00","星期六: 17:00 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/486014864888310', '0.jpg', '[0.jpg]', '2021-06-16 08.28.22 下午', '2021-06-16 08.28.22 下午', 1368, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 927, 1, '07308322', '豚將拉麵嘉義興業店', '600', '嘉義市', '西區興業西路330號', 23.46799360, 120.43817420, '拉麵', '拉麵 一堆等外帶的，看到人進來也不會問，只顧忙自己的，還要被我大聲問說我的東西好了沒才要查，一次而已，不可能再去吃湯頭味道怎麼跟文化路那家差那麼多....近期吃3次味道還是一樣淡...1年前之前那位師傅跑去哪了跟家人偶爾都會到這邊吃午餐，可以加麵很棒，湯頭也算不錯，平價的消費，東西好吃，棒棒2021/2/9 晚上19：30至20：00在此用餐，店員洗手完甩手將水滴甩入我拉麵中，聊天時口罩又拿下來，最後男店員甚至在離我們約不到一公尺的收銀臺剪指甲，真的讓人很倒胃的一間店。店家有監視器歡迎來對質，準備投訴消保官了個人喜歡濃郁的湯頭，覺得味道不錯。\n但是不方便停車，請注意交通安全。喜歡的人可以嘗試看看。', 3.8, 250, 950, 'RWIYzIZH@gmail.com', '05 283 2138', 1, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30","星期六: 休息","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:30"]', 'https://www.facebook.com/%E8%B1%9A%E5%B0%87%E5%98%89%E7%BE%A9%E5%B8%82%E8%88%88%E6%A5%AD%E5%BA%97-2032910903603257/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.24 下午', '2021-06-16 08.28.24 下午', 250, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 928, 1, '67342926', '來碗拉麵 嘉義店', '600', '嘉義市', '東區垂楊路552號', 23.47372130, 120.44580660, '拉麵', '拉麵 不怕沒位子坐，總是有位子用餐環境佳，有冷氣，上菜速度快，只是他們提供的飲料種類過少，檸檬汁被稀釋的太嚴重了，然後加一份麵，那個麵還是硬的就端上來了，好幾次了難吃，我點叉燒豚骨，湯粉調出來的喝一口就不想再喝，麵軟爛到爆根本不配稱做拉麵，叉燒完全沒有記憶點，整碗唯一好吃的是玉米罐頭的玉米粒，很甜很好吃。差不多的價位建議去吃豚將。\n吃了這家太悲傷的我只好往前騎去吃舒芙蕾撫平我內心的傷痛，多花了一百真是的……日式醬燒牛 不甜不鹹的 味道普通 沒特別好吃 酸辣木瓜絲 味道一般 比較有亮點的就是內用紅茶跟蛋花湯喝到飽了吧吃很多次了，便宜又好吃的拉麵店，加麵不加價，還有喝到飽的蛋花湯和紅茶!', 3.8, 783, 2975, '2jVh6hA@gmail.com', '05 227 1518', 1, '["星期一: 10:30 \\u2013 21:30","星期二: 10:30 \\u2013 21:30","星期三: 10:30 \\u2013 21:30","星期四: 10:30 \\u2013 21:30","星期五: 10:30 \\u2013 21:30","星期六: 10:30 \\u2013 21:30","星期日: 10:30 \\u2013 21:30"]', 'https://www.facebook.com/%E4%BE%86%E7%A2%97%E6%8B%89%E9%BA%B5-%E5%98%89%E7%BE%A9%E5%BA%97-294637457556875/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.25 下午', '2021-06-16 08.28.25 下午', 783, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 929, 1, '05433896', '山崎拉麵', '600', '嘉義市', '西區國華街248號', 23.48190190, 120.44820510, '拉麵', '拉麵 拉麵口味還可以加辣 還可以調整麵的硬度跟湯頭（豚骨已經勾選偏淡 還是會鹹）\n關東煮選擇很多 除了黑輪有的軟爛 其他都很好吃角嗨也好喝 座位空間比較小 冷氣沒很涼 不過東西好吃就夠了 很難得有可以吃拉麵配關東煮的店溏心蛋是粉的，過熟不成功！\n叉燒居然是控肉，太扯了！\n拉麵店沒有開水飲用，不及格！\n價錢不低品質不應如此\n請自我要求一入口就是日式拉麵味道，好吃，但下次我要記得跟店員說我不要紅薑，太辣了2020無法飛日本享受拉麵的當下，某天上網搜到這間店，一間由日本職人老闆遵照日式拉麵傳統所延伸來台的小餐館，環境像是80年代的小餐館，有種懷念的感覺。\n\n不僅有拉麵，還有多種關東煮可以選擇，這次我點了蘿蔔、福袋、竹輪、玉燒、黑豚骨拉麵。\n\n福袋必須點每天限量老闆自己包的，吃起來像是豆腐皮包麻薯，口感很軟綿，咬破後濃縮在食材間的關東煮湯汁流出，有種溫暖的味道。\n\n拉麵的味道可以在濃郁一點，如果跟在日本當地比會差一些，但跟大多臺灣拉麵店比，已經高過三個標準差了，如果跟我一樣不喜歡紅薑的人記得跟老闆說一下不要加，紅薑味道蓋過湯頭的味道了。烏龍麵口感Q彈，湯頭也清甜順口，很好吃。關東煮水準相當高，福袋也很特別。', 4.3, 366, 1574, 'c0FeHr4I@gmail.com', '05 228 3156', 2, '["星期一: 17:00 \\u2013 21:00","星期二: 休息","星期三: 17:00 \\u2013 21:00","星期四: 17:00 \\u2013 21:00","星期五: 17:00 \\u2013 21:00","星期六: 17:00 \\u2013 21:00","星期日: 17:00 \\u2013 21:00"]', 'https://m.facebook.com/YamazakiKorokke/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-16 08.28.27 下午', '2021-06-16 08.28.27 下午', 366, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 930, 1, '13207728', '初食拉麵', '600', '嘉義市', '西區西門街153號', 23.47784570, 120.44295130, '拉麵', '拉麵 老闆人很親切，火鍋料多實在又平價，麻油雞火鍋雞腿肉又嫩又多，cp值高，藍帶豬排也超讚，很推......4/11 13:00用uber eat訂餐，一直追蹤進度，到13:30突然「被取消」訂單⋯⋯傻傻的等吃的好像白癡一樣Foodpanda 顯示20分鐘，最後等了一小時⋯（外送員說他在現場等40分），碗外完全沒有標示餐點內容，幾碗麵打開只能自己揣測，圖片是叉燒肉，實際只有二片火鍋肉片，味道較淡，只吃了1/5就吃不下去了⋯我覺得這不是拉麵原本很餓，吃了幾口就反胃🤢，完全沒食慾，知道我想減肥，真的很棒。叫了外送點了咖哩飯不好吃⋯⋯😫😫\n裡面有兩坨不知道什麼吃了很膩\n阿重點整個咖哩沒有鹹⋯⋯⋯==', 3.1, 17, 53, 'EfnrmjUU@gmail.com', '05 222 8462', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 休息","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.28.28 下午', '2021-06-16 08.28.28 下午', 17, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 931, 1, '60447253', '嘉義市加賀食堂', '600', '嘉義市', '西區忠義街185號', 23.48165230, 120.44703600, '拉麵', '拉麵 魚介豚骨拉麵：＄190 加麵＋30\n\n因為想吃魚介豚骨所以順便來看看，菜單上沒有但是門前的黑板有寫有供應所以就點來吃吃看\n\n一進去覺得冷氣算是偏強的但是不會到太冷，現在這麼熱能夠吹冷氣吃拉麵真的很爽\n\n服務態度真的很好，也會為客人介紹拉麵，問能不能加湯或者是湯頭能不能厚一點也都說可以\n\n麵的軟硬還蠻喜歡的，不過下次應該會請師傅煮硬一點，糖心蛋濃稠入味 叉燒厚切口感很不錯\n湯頭算蠻厚的而且很順口，能喝出湯底的柴魚香味，蔥不會太辣但是蔥的辛味能在給喝了很多湯之後的味蕾適當的刺激跟改口很舒適的一個用餐環境\n餐點品類眾多，\n拉麵.飯.套餐.定食.單點品項任君選擇。\n如果您是第一次來，親切的服務人員\n他會為您一一介紹餐點，或者是推薦。\n飯後出店門口，正前方就是中正公園\n可以去那邊散散步唷。從小吃到大的拉麵，最愛一般的醬油拉麵\n有多出很多口味，但每次都記不住有甚麼差別，都要特別問服務生內容物\n糖心蛋、雞肉堂揚好吃，同行說豬排丼飯也好吃\n\n特別注意拉麵口味有濃郁跟清淡，選適合自己口味的\n點之前要先問清楚服務生比較好推鍋燒烏龍 雖然小貴但真的好吃\n豬排80也超划算\n\n另外這次試看看泡菜豬肉拉麵\n泡菜味道很足，有點微辣\n夏天吃很適合，開胃這間從剛開幕時就來吃了\n當時我才國小吧⋯⋯現在已經大學畢業了\n我記得以前湯頭沒那麼厚XD\n不過去日本玩過 其實日本湯頭真的就是這樣\n鹹又油又厚～\n喜歡台式口味的可能比較不適合吃這間\n或者可以點比較清淡的湯頭喔！\n不過這也表示這間湯頭真的都是自己熬的\n不會像有些拉麵店可能是用那種濃縮湯包下去煮\n是真材實料👍🏼\n最愛這間的茶碗蒸⋯⋯真的好吃到不行😭\n不過份量很少 一次三碗我都吃得下哈哈\n每次必點！！', 4.3, 287, 1234, '55SLLS@gmail.com', '05 225 5690', 2, '["星期一: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15","星期二: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15","星期三: 休息","星期四: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15","星期五: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15","星期六: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15","星期日: 11:00 \\u2013 13:45, 17:00 \\u2013 20:15"]', 'http://facebook.com/pg/347462888746350/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.30 下午', '2021-06-16 08.28.30 下午', 287, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 932, 1, '87360560', '拉麵豚五郎', '600', '嘉義市', '東區文化路204號', 23.48059700, 120.44956000, '拉麵', '拉麵 好吃的拉麵攤，宵夜吃麵首選，叉燒肉好好吃~\n我已經吃完一輪菜單上的口味~最愛香蒜豚骨跟甘鰹醬油拉麵^^好出', 5, 3, 15, 'tR5Y08f5@gmail.com', '0909 195 539', 4, '["星期一: 22:00 \\u2013 02:00","星期二: 22:00 \\u2013 00:00","星期三: 休息","星期四: 00:00 \\u2013 02:00, 22:00 \\u2013 02:00","星期五: 22:00 \\u2013 02:00","星期六: 22:00 \\u2013 02:00","星期日: 22:00 \\u2013 02:00"]', 'https://www.facebook.com/%E6%8B%89%E9%BA%B5%E8%B1%9A%E4%BA%94%E9%83%8E-102118501732731', '0.jpg', '[0.jpg]', '2021-06-16 08.28.31 下午', '2021-06-16 08.28.31 下午', 3, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 933, 1, '80582665', '熊富手打烏龍麵館', '600', '嘉義市', '東區忠孝路211號', 23.48393840, 120.45372070, '拉麵', '拉麵 雖然沒去過，但好像很爛好吃。專業就是不一樣。位於檜意森活村附近的一間烏龍麵店，據說是老闆用一顆頑固的心堅持了多年 食材也是特別用心挑選過，深受饕客的喜愛 。\n\n一走進「熊富」隔著玻璃窗很清楚可以欣賞著師傅用心製作麵條，每條粗細比例原則都沒有在跟你假的！！就連汗水都快滑落下，似乎是在說有感受到我們的“用心”嗎？（開玩笑）！\n\n初次來嚐鮮看著各式各樣的麵條類別，瞬間有點不知所措，所幸服務人員很熱心的跟我們介紹餐點，我後來點了他們招牌餐點 豚王跟赤炸地机G腿麵，另外服務人員還提到說餐點皆是現點現做需要等待一些時間,當下想說不趕時間ok!\n\n終於！！烏龍麵來了！！（敲碗）😄😄我先是品嚐了搭雞腿的那一碗麵，只見上面加了一些配料（玉米、海帶芽、水菜...！）一口喝下是柴魚口味的湯頭，麵條Q彈有嚼勁，吃得到師傅用心製作的靈魂 😭😭😭果然名不虛傳啊！G腿也的蠻大塊！！超好吃👍👍👍 另外一碗豚王也不遑多讓，只見上面鋪著幾片海苔，跟煙燻過豬肉片，一口喝下湯頭😋😋天哪！相當濃郁好喝欲罷不能，我還另外點了一份玉子燒 egg，聽說製作過程也不馬虎是先煎過定型再烤過最後淋上起司醬根本絕配！向來不愛起司的我深受喜愛😋😋😋\n\n我們家小孩向來最挑嘴，同時也是小小的美食評論家，真的很愛你們家的麵條！！餐後妹妹不停拉著我的衣袖說：「媽媽下次什麼時候可以再來啦！我還要來找姐姐吃麵麵啦！」\n\n最後誠摯推薦給各位朋友往後有機會回嘉了別忘了給年輕老闆一個機會，滯留下來好好品嚐純手工的烏龍麵，保證讓你流連忘返，口齒留香😋😋錯過可惜喔！然而店家為了將手工烏龍麵煮出一定的Q度，需耗時一些時間 更要耐心等候包容，並非如同他排的麵條迅速確實，畢竟一分錢一分貨，這點我覺得很值得!🤔🤔\n以上 語畢。住在附近有一段時間了，但每次總只是經過而已。這次因為要去的店人太多不想排隊，所以經過輾轉而來這裡。\n/\n評論送的豬排很大塊，肉也蠻厚的，但是吃起來的豬肉有種死不瞑目的感覺。索性放進湯裡面，這隻豬才總算活了過來。\n-\n泡菜烏龍麵 180$\n黃金泡菜蒜味很重，加進湯裡面使得湯變得麻麻的，味道並沒有因此而變淡，反而很合適的跟湯底結合在一塊，味道很濃。 烏龍麵麵體很渾厚，本身吃起來也很Q彈，有時候吃到一半還會被麵條襲擊。豆腐味道很夠，嫩嫩的我喜歡。其他的配料就沒什麼印象了。\n\n整體來說中規中矩。餐點很好吃😋\n服務很棒👍\n環境衛生整潔✨\n烏龍麵QQ的 好吃❤️很喜歡\n菜單也有很多選擇\n有熱烏龍 、冷烏龍 、小菜類也很多\n喜歡吃飯的也有咖哩飯可以選擇\n還有黃金泡菜也很好吃\n跟烏龍麵一樣都是手工製作的 很棒！\n推👍～', 4.1, 1209, 4957, 'fE9WEwA@gmail.com', '05 277 7575', 2, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:30, 17:30 \\u2013 21:00"]', 'https://www.facebook.com/%E7%86%8A%E5%AF%8C%E6%89%8B%E6%89%93%E7%83%8F%E9%BE%8D%E9%BA%B5-254543191341780/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.32 下午', '2021-06-16 08.28.32 下午', 1209, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 934, 1, '46834468', '初八専売おいしい店 新榮店', '600', '嘉義市', '西區新榮路90號', 23.47502620, 120.44469100, '拉麵', '拉麵 店內乾淨且風格漂亮～～\n但口味普通，吃過虎尾店的超級好吃，想說嘉義市區的比較近來吃吃看，真的沒有比較沒有傷害，一樣都是初八怎麼差這麼多，希望能做的一樣好吃🥲🥲🥲這樣我就不用跑那麼遠了點了味噌與泡菜口味的日式拉麵，覺得普通\n\n湯頭清爽\n蛤蜊也算是肥美\n鮮蝦2尾也算是新鮮\n麵體吃也來也很Q彈\n\n但整體吃起來有種台式鍋燒麵的感覺\n（總覺得像是鍋燒意麵plus的等級）\n食材等級和口味沒有可以嫌棄的地方\n不過真的太貴\n吃起來應該不到100卻可以賣到140\n這價位應該不會再光顧按步就班的好吃拉麵，食材新鮮，湯頭不會太碱。裝潢&餐點都適合拍照,時蔬還可以綜合,打卡送的洛神花茶好喝👍🏻氣氛好，餐點用料實在，小孩子也容易入口的調味。\n揚出豆腐醬汁很棒，下次會再來的店家', 4.8, 353, 1694, '0Mvw7m@gmail.com', '05 227 7008', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', 'https://g.page/early888?gm', '0.jpg', '[0.jpg]', '2021-06-16 08.28.34 下午', '2021-06-16 08.28.34 下午', 353, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 935, 1, '65709783', '炸醬拉麵', '600', '嘉義市', '東區和平路339號', 23.48329440, 120.45599490, '拉麵', '拉麵 ', 3, 1, 3, '1MDQ2azRv@gmail.com', '02-28264451', 4, '', null, '', '', '2021-06-16 08.28.35 下午', '2021-06-16 08.28.35 下午', 1, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 936, 1, '29949413', '赤野拉麵', '600', '嘉義市', '西區國華街137號', 23.47629340, 120.44873760, '拉麵', '拉麵 ☝️不推薦也不會再光顧\n\n點餐人員沒告知要拿兌換卷去跟他們吿知才能加麵（流程疏失）\n沒人點餐和老闆娘就各自玩自己的手機\n\n當我們湯喝的差不多\n詢問老闆娘要加麵的時候\n才冷冷回答說：「要加麵湯要留下來」⋯⋯⋯\n\n流程上問題沒做告知反而說我們不懂！？問號❓\n湯沒了！？我不能單吃麵？老闆都很親切服務很好～\n特濃豚骨+激辛拉麵 +炸肉餅\n\n辣味小辣就很有感了😂\n特濃感受不到濃😅不過叉燒有厚度很好吃～\n炸肉餅的口味很特別\n\n豆芽菜跟蔥特別多😖花生豆腐拿來做和風豆腐很噁(醬油加甜豆花的感覺)，菜單上也沒標注，就只有在外面寫，沒注意到的顧客根本不知道。\n------------\n更正，外面也沒有寫。餐點有水準，加麵很阿莎莉！湯頭濃郁，麵條可選自己喜愛的軟度\n女服務生笑容甜美，拉麵加麵不加價\n還附麥茶；下回來嘉義首選', 4.1, 298, 1222, 'nkQnWbtnn@gmail.com', '05 222 3055', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 休息","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/%E8%B5%A4%E9%87%8E%E6%8B%89%E9%BA%B5-1698086040405612/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.35 下午', '2021-06-16 08.28.35 下午', 298, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 937, 1, '04186313', '川牛木石亭 嘉義店', '600', '嘉義市', '西區仁愛路448號', 23.47515010, 120.44233630, '拉麵', '拉麵 因為過了用餐時間還沒吃中餐，於是谷歌隨便找了間餐廳，看到有網友留言是寵物友善所以決定前往用餐。\n禮貌告知狗狗能否放膝蓋不落地，店家很爽快地說可以啊沒問題。\n一夥四人有點飯類也有麵類，餐點中的肉都有特別火焰炙燒過，吃得出香氣。還提供雞白湯免費飲用，淡淡雞骨味很棒。\n店家可能看見我把肉過水給我家毛孩寶貝吃，主動提供一碗份量不少的汆燙牛肉給毛孩享用，真的非常感動與感謝。有再去嘉義遊玩時，一定會再特地去用餐！外帶餐點聽到有位男員工，說「不要接，讓它響久點...」，「熊貓單怎麼一直來，有夠煩。」拜託，現場才3位客人，有這麼忙嗎？還是不想做生意呢？現在年輕人⋯都這麼吃不了苦嗎\n餐點是好吃的👍牛肉丼飯 醬汁在多點會更好！外送來的東西一點質感都沒有，溏心蛋用袋子裝擠到整袋都是蛋黃，豬排硬又乾，唐揚豆腐沒沾醬?要不要改叫炸豆腐，我點鹽酥雞的雞蛋豆腐都還有胡椒粉哩點了嫩煎極盛牛肉丼現在促銷150而已，這個肉的特點是油花幾乎沒有但是肉很多蠻嫩蠻香的很好吃。\n只是我更喜歡油花多的。\n飯很嫩很Q不會太濕，也沒有加太多醬汁，單純有醬汁的香氣。\n蛋建議倒到飯裡面裹著吃，直接吃他的醬有點太鹹了，蛋超好吃。\n免費喝的雞白湯真的超加分，很好喝叫外送東西難吃、份量少、配菜味道也很差。\n又貴又難吃', 4.1, 423, 1734, 'Rw9uSFeHI@gmail.com', '05 222 3550', 2, '["星期一: 11:00 \\u2013 00:00","星期二: 11:00 \\u2013 00:00","星期三: 11:00 \\u2013 00:00","星期四: 11:00 \\u2013 00:00","星期五: 11:00 \\u2013 00:00","星期六: 11:00 \\u2013 00:00","星期日: 11:00 \\u2013 00:00"]', 'https://chuan-niu.com/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.36 下午', '2021-06-16 08.28.36 下午', 423, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 938, 1, '05675779', '小有名堂 Syuan Noodles & Coffee', '600', '嘉義市', '東區成仁街240-28號', 23.48400450, 120.45093120, '拉麵', '拉麵 一間以豆乳為湯底的素食拉麵店，小清新的風格，但內用位置不多，若假日人潮多時，可能需要等待，不接受預定位置。豆乳湯頭濃郁，不會太鹹，可以喝完喔～麵體Q彈不會很硬，喜歡吃辣的朋友，可以點川辣豆乳麵，吃起來是舒服的辣，也有其他小菜可以單點。因店家位於巷子內，建議開車的人可以停林森路停車格，或附近停車場，再步行前往。在日本生活吃過了無數拉麵後，難得回嘉一趟吃到豆乳拉麵非常驚艷。\n對於熱愛豚骨拉麵的人，可能會覺得口味清淡少了點什麼，我想就是因為少了蒜頭吧。\n至於麵體可以再做改善，不知道是否煮太久還是我自己先拍照再吃的緣故，覺得外層已經稍微糊掉，但內層還是有嚼勁的。如果麵體粗細能再細一點也許能改善這個問題。好吃的蔬食拉麵 ，湯頭濃郁能喝到底青菜豐富很有飽足感 ，不需推薦就人潮滿滿的拉麵店👍👍👍點了川味豆乳麵，一開始蠻好吃，湯頭濃郁麵的份量也多，可是吃到後面有點膩，味道會變得很單一。店的風格小、清、新！；開放；明亮！\n以豆腐乳為湯底的素食餐廳！非常有創意的口味。這是一家素食的豆腐乳湯底的麵食餐廳🍴豆腐乳湯！真的是嚐鮮！從來沒有吃過；還真的不錯！\n川辣口味！老實說覺的有點辣但是真的不夠重。\n四川人應該會說：太淡啦！\n那個苦瓜，有點小貴。但是清淡，清脆口感；帶著酸甜滋味。好吃😋。', 4.6, 268, 1233, 'W3OxCy@gmail.com', '05 276 9979', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 休息","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00"]', 'http://www.facebook.com/SyuanNoodlesandCoffee/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.38 下午', '2021-06-16 08.28.38 下午', 268, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 939, 1, '50692287', '阿威炒飯.日式烏龍麵', '600', '嘉義市', '西區北興街227之15號', 23.48504670, 120.43946870, '拉麵', '拉麵 本來是忠實顧客⋯但是炒飯突然變小盒！份量變很少！這樣賣60真的太貴！附近的炒飯店一樣價錢量比你們多！這樣誰還會跟你們買呢？\n真的不妥⋯⋯會這樣的份量！味噌海鮮湯麵 海鮮部分就蛤蜊跟兩顆蝦仁 基本上裡面的料都是肉絲跟一點點的菜加上兩個火鍋料 一碗75 可能我吃米不知米價嗎 覺得算貴 外面買的湯麵海鮮料會比較多ಥ‿ಥ搬家兩三次但是品質還是一樣穩定，是一間值得推薦的炒飯店。終於在北興街有爆好吃的炒飯了！大推～餐點美味、服務周到，非常棒的店', 4, 26, 104, 'rsxpdIuTK@gmail.com', '05 232 2505', 1, '["星期一: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期二: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期三: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期四: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期五: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期六: 11:00 \\u2013 14:30, 16:30 \\u2013 20:00","星期日: 休息"]', 'https://www.facebook.com/pg/%E9%98%BF%E5%A8%81%E7%82%92%E9%A3%AF%E6%97%A5%E5%BC%8F%E7%83%8F%E9%BE%8D%E9%BA%B5-327056570761167/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.39 下午', '2021-06-16 08.28.39 下午', 26, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 940, 1, '61369000', '樂朋義法創意廚房', '600', '嘉義市', '東區維新路7號', 23.47947690, 120.45866570, '約會餐廳', '約會餐廳 吃了快兩小時，才吃到主餐。兒童餐只有三道菜，但飯麵也等了一個半小時。不太適合小孩老人，會等太餓。XD\n兒童餐燉飯黑胡椒太多，小孩覺得辣。不過有替換不辣的給我們。\n羊排等最久，但裡面卻不太熟。\n前菜，沙拉及飲料令人失望。湯還不錯，義大利麵好吃，牛排也還可以。服務品質爛到不行！就因為中間有個大桌聚餐，所有服務員都只顧那一桌，舉手舉半天都完全沒反應！一桌需要所有服務生顧？\n中式套餐說需要等半個小時一口氣全上上來，雞肉飯居然是涼的！中式套餐等了半小時也要吃完了，排餐才出現！等待的時間還要餵蚊子.蒼蠅！\n向店家反應結果是服務生道歉，經理居然神隱！？\n這樣的餐廳絕對不會再來第二次，也會好好跟所有親朋好友宣傳你們的服務品質的不用謝。爛透了！網路開放訂位成功，前一天中午才打來說當天（隔天）午餐被包場，害我們一家10人生日聚餐被迫取消好好吃。但上菜速度就別強求了，再怎麼催人家就是慢慢來。建議要去使用6折生日優惠點龍蝦主餐的人，一定要在預約的時候請餐廳準備好龍蝦，否則點不到你想吃的。 地下停車場的空間有限，要用餐還得自己花錢停外面的停車場，沒有憑收據代付停車費的措施，還沒坐下來就覺得不舒服。 因為家人生日想去吃大餐慶生，結果好幾樣主餐都賣完，一個四星飯店的西餐廳備菜備的這麼少，實在沒有應有的水準，覺得非常失望。', 4, 40, 160, 'tiuCZhm5qT@gmail.com', '05 275 6111', 4, '', 'http://www.leeleelin.com/2016/10/yuhtonghotel.buffet.html', '0.jpg', '[0.jpg]', '2021-06-16 08.28.41 下午', '2021-06-16 08.28.41 下午', 40, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 941, 1, '39702987', '街角161法式餐廳', '600', '嘉義市', '西區友愛路161號', 23.48085370, 120.43388750, '約會餐廳', '約會餐廳 1、出餐慢，每份餐點間隔至少30分鐘以上。\n2、每份餐點的份量都很少（連麵包都很小一顆），結束後所有大人都沒吃飽。沙拉、湯品、前菜口味都不精緻，等很久但主餐上來是冷的，綿羊豬的肥肉多，主餐搭配的醬料口味很甜。\n3、單點一份奶油義大利麵給小朋友吃（當天唯一可以單點的餐點），250元，沒有配料，大概只有5口的份量。\n4、以此價位，甜點竟然是奶凍捲搭兩片草莓、一顆藍莓、一葉薄荷....。\n5、用餐環境舒服，服務生很親切但專業度不佳（收走湯匙後上湯，我們還要自己去要湯匙；未如其他評語詳細解釋餐點；未記得每位顧客的主餐還上錯餐）\n6、點拿鐵，上美式，有反應但店家未給任何回應。\n\n幾天內先吃了台南尼法，再來吃貴店，餐點品質落差很大。服務態度親切，初次用餐沒有好好了解餐點與期望落差雖然價位略高，但餐點品質好，服務週到，物超所值！好喜歡這家的氣氛🤍\n看來看去還是喜歡這家 打了電話預約\n到這邊真的沒讓人失望\n平常很少給店家評論 真的很喜歡才會給\n甜點真的超級好吃 其他餐點也很好\n很合我的胃口 不吃有筋肉的我\n服務員推薦菲力 五分熟 真的很不錯\n期待再二訪❤️❤️久久沒去街角了 上次去應該幾年前了 因為臨時決定情人節加過年要去吃飯 其他家實在很難訂位\n臨時決定吃這間\n\n02/14去的 因為過年期間 套餐選擇是固定的\n我們兩個人點了日本A5和牛紐約客（$2680）和澳洲和牛肋眼（$2080）\n沙拉還可以 佐以分子料理\n湯品 牛肉湯真的讓人有點失望 很像一般店家煮出來\n前菜 雞肉配薄荷葉味道不是很搭\n焗烤鳳螺味道也是差了一點\n\n重點的主餐\n澳洲肋眼和牛我點五分熟 但是 肉真的很柴 我老婆因為正在戴牙套矯正 她只要咬到太韌的東西就咬不下去\n這澳洲肋眼和牛套餐要2000 肉真的柴到不行\n日本和牛就沒有問題 是真的入口即化 純正日本血統\n不過對於澳洲和牛若價位僅僅比日本和牛差幾百塊 品質卻差這麼多 我覺得很失望\n\n一餐下來加上服務費一成 要5200 老實說 我寧願開車到台中或是台南會有更好的選擇\n\n對了 還有一點小差曲\n我是吃過很多此類的店 包括米其林的星星\n麵包應該是會問客人還需不需要\n不過在沙拉時 麵包一個小小的吃完後 麵包盤及佐料就直接被收走了\n結帳後 老實說真的沒有吃飽 只好回家再煮泡麵了', 4.2, 417, 1751, 'kVY8Du@gmail.com', '05 281 1556', 3, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00"]', 'http://corner161.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.41 下午', '2021-06-16 08.28.41 下午', 417, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 942, 1, '74635236', 'Sunny Queen陽光皇后嘉義店(義大利麵、燉飯、披薩、舒芙蕾、早午餐餐廳)', '600', '嘉義市', '西區新民路755號', 23.46994160, 120.43966220, '約會餐廳', '約會餐廳 東西不錯吃，服務人員的服務態度也都還不錯兩顆星純粹都是給食物的部分。\n雖然點餐時 有特別告知希望可以加辣\n但吃起來似乎沒有加？ 但餐點味道還是不錯的\n\n此次應該是我第五次去了\n服務人員的服務 真的越來越令人不解\n上次去的時候 服務人員總是在看見你用完餐點時候\n默默飄過來 一句話都不說 快速把餐盤收走?\n\n這次是完全不懂 為何收餐盤時 不願意走到客人後面\n近一點的位置\n而總是站在桌子的外側 伸長手要直接收\n或是感覺直接在等客人遞餐盤給服務人員？\n(不解是否這點是公司的sop就是這樣?)\n\n甚至在我們所有主餐都吃完 來收盤子的時候\n又指著我們桌上還有剩下1/3的薯條\n直接問我們還有需要嗎？\n當下感覺 是急著收完桌上東西 要趕客人走?\n\n被問的時候\n同行友人 是有直接告知 可以等我們走了再收嗎\n服務人員也很明顯有嚇到XD\n也不確定是不是因為如此?\n我們離開的時候沒有聽見任何的謝謝光臨之類的?\n\n雖然當時現場只剩下我們一桌\n但我上網查營業時間是到22:00啊(當下大約20:40左右)\n如果真的是要打烊了 服務人員可以主動告知吧\n\n這樣的服務態度真的不ok～裝潢用心，但要成為拍照網美店還不夠\n價格跟gomagi上有明顯落差（當天用餐未使用gomagi）\n1.拍照道具很多髒了壞了，也未見更新\n\n2.低消不便宜，每個人都需點一份主餐，且主餐印象中最低230左右，就算飲料算平價，但起碼也限制一個人低消150左右，對於想吃下午茶或是聊個天的客人比較友善\n\n3.遞菜單時也不先說哪些品項沒有，非得等客人點完餐，隔了三分鐘才說我們蛤蜊今天沒有喔……而且來說這件事情的女店員態度及口氣不好（大雷點）完全沒有一絲不好意思或親切的口吻\n因為當天有參與壽星送雞翅活動，所以點餐幾乎都點海鮮類，送餐速度極快，10分鐘左右都上好餐點，有三道餐都有蝦子，但那卻是最大的敗筆！！！蝦子只剝好中間三節的殼，頭留著，尾巴跟最後兩節的殼也留著，還不是得讓客人自己剝，真不懂是什麼意思！剝了中間的殼也不順便清蝦的腸泥，每一隻蝦都有滿滿的腸泥，看到真的很反胃！蝦肉也非常不新鮮，不是鮮甜Q彈，而是軟爛！！！\n\n4.明太子除了上面點綴的幾顆以外其他都是煮熟後硬硬的口感，是我吃過的明太子料理方式都錯誤嗎？\n\n5.四個人只吃完一道餐點，其他真的只能打包回家給不挑食的老爸吃，對我們當天用餐的四個人來說，餐點口味真的不合胃口（口味是蠻主觀，大家自己謹慎評估）\n\n綜合以上幾點，難怪場地這麼大裝潢算可以的簡餐店，在小小的嘉義市還是沒什麼知名度，週末的晚餐時間連一半位子都坐不滿啊～\n\n---------------------------------------------------------------------\n店家回覆僅期許餐廳為大家能夠感到舒適並且能夠享用美味的餐點為主\n蛤？店員態度、口氣及未事先說明備料不足品項會讓客人感到舒適嗎？剝殼剝一半（照片可證明）、海鮮不新鮮及滿滿腸泥會是美味餐點\n以上店家期許的，許多評論都有反應，很明確不是我個人”主觀感受”！\n\n更改菜單一事，我在4/25透過官方fb詢問店裡活動及4/30致電定位，貴店從未提及喔～是因為新菜單漲價所以沒主動說明嗎？（我都可以提供截圖證明）\n4/25詢問時，早知道假日無法用gomagi，我也沒購買你們家的gomagi！結帳時我也不囉嗦直接掏卡片結帳，提及gomagi是因為想強調我是用原價去經歷怎樣的用餐經驗喔～\n\n如果我是惡意給與過低評論，那何必花大把時間分享自己經驗，直接給一顆星即可。\n\n真心希望你們可以進步，但若是無法虛心接受消費者用心的回饋（包含許多評論提及不新鮮、服務態度以及低消問題），那就公道自在人心囉～期許你們可以正面看待並回覆眾多客人給的評論，謝謝好吃好拍，只是菜單太復雜，服務員也頭大...裝潢是還不錯，但是客群的部分，小朋友偏多素質還不怎麼樣，還以為自己來到麥當勞。\n食物的部分，只能說飲料是裡面最好吃的了，香酥戰斧豬肉老又沒汁，旁邊的配菜連便當店都不如，義大利麵不好說，不怎麼好吃，烤半雞汁很多，結果也沒什麼味道。\n服務的部分，要點餐的時候都沒人來點，結果收盤子倒收的挺快啊。\n上菜很慢沒有半小時以上不會上，連買單都要五十萬年。', 4.2, 1343, 5641, 'MnkZzkUIX8@gmail.com', '05 283 0309', 2, '["星期一: 12:00 \\u2013 20:00","星期二: 12:00 \\u2013 20:00","星期三: 12:00 \\u2013 20:00","星期四: 12:00 \\u2013 20:00","星期五: 12:00 \\u2013 20:00","星期六: 12:00 \\u2013 20:00","星期日: 12:00 \\u2013 20:00"]', 'https://www.facebook.com/SQ520', '0.jpg', '[0.jpg]', '2021-06-16 08.28.42 下午', '2021-06-16 08.28.42 下午', 1343, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 943, 1, '92819142', 'Merkatua Jatetxea 慕可西班牙餐館（預約制，來店前須先完成點餐）', '600', '嘉義市', '東區吳鳳南路17巷1-2號', 23.47343200, 120.45429310, '約會餐廳', '約會餐廳 餐點非常好吃，有秒到歐洲的感覺\n主人又非常和善\n非常推薦大家來吃沒想到嘉義也可以吃到西班牙料理，每一道菜都很美味，是值得再訪的好店。其中我們最喜歡蒜香油泡蝦跟花蓮縱谷鴨胸！（我媽乍吃還以為是牛肉）\n此外，需要預先點餐，所以上菜流程很順暢。服務也滿分👍。\n因為店在小巷子裡，要預抓一點時間找停車位喔。香煎大草蝦非常入味。香甜 新鮮報可口。大推。大廚非常用心料理。嘉義難得的西班牙異國料理。主廚是西班牙人，原本在西班牙經營餐館。用餐需預約，提供停車位2個。店員帶位坐定後要求我們換位子\n位子換了坐定後...店長問說有沒有需要酒?因為開車所以婉拒了沒多久後店長就來跟我們說今天沒辦法接待我們這組客戶請我們離開只說了是因為工讀生弄錯了導致誤會\n如果是沒位子或是其他狀況都可以了解\n但是有必要在坐定兩次之後才說不能接待\n當下有其他客人想說不要影響其他人\n我們離開的時候也沒表示任何的歉意', 4.5, 107, 482, 'OHNKCtLIn@gmail.com', '05 228 8708', 4, '["星期一: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期二: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期三: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期四: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期五: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期六: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30","星期日: 11:30 \\u2013 14:30, 18:00 \\u2013 20:30"]', 'https://www.facebook.com/Merkatua-Jatetxea%E6%85%95%E5%8F%AF%E8%A5%BF%E7%8F%AD%E7%89%99%E9%A4%90%E9%A4%A8-795310157325725/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.43 下午', '2021-06-16 08.28.43 下午', 107, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 944, 1, '17841705', '雲朵 Clouds 嘉義美食餐廳', '600', '嘉義市', '東區新生路456號', 23.48983630, 120.45926080, '約會餐廳', '約會餐廳 此次母親節帶著全家大小過去用餐\n送餐時就發現湯匙上沒有洗乾淨\n有一個很明顯的油漬\n在服務生還在的時候就馬上告知可不可以換一支湯匙呢\n卻被告知湯匙要自助到中間自行取用\n整間餐廳就我一個人需要餐具要自行取用\n這邊就想說算了\n但牛奶鍋真的讓我整個傻爆眼\n開火一煮直接給我變成原味高湯鍋\n我黑人問號 超級變變變？\n去問櫃檯得到的回覆是 ：他都是這樣 正常的\n當下我真的又一個黑人大問號\n牛奶鍋直接大變高湯鍋\n愛吃火鍋的我這輩子真的第一次遇到\n後來告知櫃檯我們吃過大大小小的火鍋 第一次遇到這種的 想知道是不是有哪裡出問題嗎\n她請了一位穿著便服不知道是老闆 還是廚師的人出來 過來就問：啊請問怎了嗎\n我此時此刻收集到了第三個黑人問號\n是我剛剛表達的不夠清楚 還是以為我是來砸場的？\n於是我收起了我驚訝的心情 再次的告知這個牛奶鍋變成清湯鍋是正常的嗎？\n得到的回覆是：因為我們用的是純鮮奶 別的地方用的都不是純鮮奶 所以不會分離出來\n聽到這邊我已經知道他在講廢話了\n第一句話就先抨擊其他人 自己才是對的\n請問那些大大小小的專業火鍋店都是假的騙人的嗎？\n有的業者甚至在我面前倒牛奶也沒這樣過\n我知道鮮奶脂肪濃度過高會分離出渣渣\n但直接分離成清湯真的是生平所見\n不得不讓我懷疑是不是鮮奶油⋯\n\n還有 蝦子很不新鮮\n我老婆吃完嘴巴馬上開始癢一份套餐有：餐前小食（百香水果+梅醋）、套餐主餐、飲品、餐後甜點（梅子果凍）\n\n火鍋類有含雞蛋\n簡餐類的配菜有用心，不像部分店家是隨便炒一炒而已；湯上桌也是熱的，光是這些點我覺得很值得\n（當天剛好吃的雙椒炒糖醋肉+培根高麗菜+蒜炒A菜+醃漬蘿蔔+熱騰騰的小魚乾味增湯）\n服務親切會詢問有無特別需要。\n\n真要評出缺點的話是餐前的梅醋跟餐後的梅凍相似度太高，因為夠酸很好吃所以兩道加起來偏膩\n對於不吃酸的顧客可能原封不動沒法品嚐了\n但我想那應該是一起用釀梅醋的梅子及梅汁製作的！\n建議可以梅凍換其他凍類，比如一天為梅醋+仙草凍或咖啡凍或茉莉花凍；一天改小杯茶類+梅凍。市區裡面可以有一間這樣子坪數的餐廳真的很棒耶!!!\n除了很適合帶親子來之外我覺得各式的聚餐都很適合約在這裡,因為餐點可以符合各個不同需求的人,用餐環境很舒服,服務人員的訓練也相當良好,是當不知道要吃什麼的時候一個很好的餐廳選擇!!!\n整體營造出來的氛圍非常的好.\n另外在疫情期間他們對於環境消毒的要求看得出來做得很好\n\n整體來說是一間會讓人願意重複到訪的餐廳!!環境舒適，氣氛悠閒，服務人員態度真的非常的好，也很親切，會主動關心看起來有需要的客人！\n餐點部分義大利麵跟燉飯吃起來比較普通~~\n簡餐部分個人吃起來是很推薦的，海鮮部分非常新鮮！建議可以嘗試！\n火鍋部分只有吃到海鮮總匯的部分，海鮮就是真的很新鮮！非常的好吃！\n附餐的甜點很香甜（吃了一口才想到要拍照）\n蠻好吃的！併了四張桌子，每張桌子都不平，位子蠻窄的，豬排又乾又硬，焗烤飯很淡裡面一堆蔥..不知道吃的是飯還是蔥，先上了火鍋，火鍋都快吃完了才上其他餐，最後大家都要吃完了還有一個餐沒上，多尷尬，飲料甜點來問了兩次才上..還是從第一桌用傳的傳下去..', 4, 1080, 4320, 'UtziIv@gmail.com', '05 276 5929', 2, '["星期一: 11:30 \\u2013 21:30","星期二: 11:30 \\u2013 21:30","星期三: 11:30 \\u2013 21:30","星期四: 11:30 \\u2013 21:30","星期五: 11:30 \\u2013 21:30","星期六: 11:30 \\u2013 21:30","星期日: 11:30 \\u2013 21:30"]', 'https://www.facebook.com/CafeClouds/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.45 下午', '2021-06-16 08.28.45 下午', 1080, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 945, 1, '75577267', '貝納義式料理&下午茶', '600', '嘉義市', '東區新生路329號', 23.48362700, 120.46076200, '約會餐廳', '約會餐廳 我點的是墨魚海鮮義大利麵，食材很新鮮，給的很大器，\n魚、蝦、蛤蠣都大的很誇張XD，媽媽點了蘿勒松子雞肉燉飯也很喜歡，從前菜到甜點都很滿意，整個套餐吃完對我和媽媽來說是滿飽足的，完全能感受到店家的用心，闆娘很親切，會再回訪且值得推薦的好店:)\n\nⓅⓈ奇異果優格冰沙好好喝❤️❤️❤️朋友推薦的防疫外帶店家，闆娘親切，可以預約時間，親送門口不用下車，我跟老婆都覺得外帶回家後還是很好吃，餐點精緻，料也給的大方，加價的奇異果冰沙和贈送的濃湯都很好喝，包裝部份也很細心，納入我們近期會常外帶的口袋名單。無意間發現的義大利麵店 簡直讓人又驚又喜！\n環境乾淨舒適不壓迫 整個很舒服\n青醬海鮮義大利麵的鮭魚是一大片而且鮮嫩多汁，還有蝦子跟蛤蜊真的💯👍\n醬汁確實濃郁且跟Q彈的麵條融合在一起\n燉飯好吃 雞肉也煎的很香 肉質吃起來很好 是一塊一塊的\n\n這樣的價位可以吃到這樣的料理很是驚人\n另外卡布奇諾濃湯也很厲害 上頭還灑上肉桂粉 真的很少喝到這樣的湯 非常喜歡\n這樣一份主餐其實就很飽了 還補充到很多蛋白質\n\nB餐+120東西太多 雖然好吃 但是會過飽 我覺得+A餐$90就很好了\n套餐飲料的部分 我們三個人各點一種冰的 飲料大杯也都好喝 不是冰塊水路線 套餐加的很超值\n大家都吃的超滿意 吃飽喝足之後還可以再帶到附近嘉義公園植物園走走 真的很不錯青醬好吃不膩，但麵量好少跟價位不成正比。\n卡步布奇諾濃湯（蘑菇湯）好喝，上面還加奶泡，順口\n店門口可停機車，汽車不好停\n冰飲換熱飲要另加201.店裡只有3組客人，餐點卻頻頻出錯\n（1）奇異果冰沙送成奶茶\n（2）蘑菇濃湯沒加鹽巴，沒加鹽巴！味道可怕\n2.入座1小時，居然還無法出餐，所有人餓肚子乾等。莫非廚房只有一口鍋，煮完一道重新刷鍋🙄\n3.此種服務品質居然還多收10%服務費\n4.不推薦。餐點可以貴，可以收服務費，但請讓服務配得上高昂的消費！\n\nps附上像稀飯的德腸燉飯證明', 4.6, 228, 1049, 'Wi1vcszKd@gmail.com', '05 271 6557', 2, '["星期一: 11:00 \\u2013 20:30","星期二: 11:00 \\u2013 20:30","星期三: 11:00 \\u2013 20:30","星期四: 11:00 \\u2013 20:30","星期五: 11:00 \\u2013 20:30","星期六: 11:00 \\u2013 20:30","星期日: 11:00 \\u2013 20:30"]', 'https://m.facebook.com/cafebella2012/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.46 下午', '2021-06-16 08.28.46 下午', 228, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 946, 1, '38397524', 'ROOF', '600', '嘉義市', '西區後驛街172號5樓', 23.47907810, 120.43841270, '約會餐廳', '約會餐廳 我們是吃當晚最後一個時段，獨享整個空間，微微燈光下看似隨意卻用心的桌上擺設，配上徐徐微風，沒有蚊子干擾，氣氛已堪稱完美。\n每道菜都讓人驚喜，菜餚有忠於食材原味，但又把不同的味道做一個平衡，尤其是餐前沙拉跟飯後的地瓜泥佐芭樂芒果。\n非常美好的一個體驗，媽媽很喜歡，這就值了。\n提醒一下，主餐依點選種類加價，所有餐點包含現點飲料都要再10%加服務費，事前先跟老闆溝通清楚，整個體驗會更開心喔！\n另外，因為私廚位於社區內，附近不好停車，可以考慮停後站的地下停車場，走路也才4分鐘左右。即使在台北也容易找道這樣類型的餐廳\n特地從雲林上來吃也沒有讓我失望\n雲嘉南一帶應該很難找到可以比擬的地方了\n環境舒服佈置氣氛美\n有各種藏酒\n餐點也非常用心與美味\n可惜因為疫情 否則好想再去拜訪\n是個有個性卻又舒服的好地方再訪，覺得很棒！酒類跟主食都很棒！老闆介紹食物跟酒類都非常細緻~~~超喜歡，會再來哦！體驗還不錯\n\n1.事前有說明是為了慶生\n店長有說他們自己有提供蛋糕 我說沒關係我自己帶\n回覆訊息有說現場只冰得下小蛋糕\n我以為是我們自己帶 他會幫忙冰起來\n看來不是 可能只是他們自己的蛋糕\n因為我把生日一人份小蛋糕交給店長\n他直接放在旁邊椅子上 ？\n2.吃完飯後\n我請店長幫我們拿蛋糕\n他直接整袋子還給我放在桌上\n？？？？？\n然後我跟我朋友只好在壽星面前拆開包裝拿出蛋糕插蠟燭\n壽星就去廁所不然有點尷尬\n我以為所有的餐廳都可以幫忙端蛋糕出來\n至少我之前幫朋友慶生 餐廳都可以幫忙端蛋糕\n\n走之前店長說可以留好評\n我是覺得擺設食物氣氛是很好 當天天氣也很好\n可是蛋糕環節整個功虧一簣氣氛很好，很美～老闆人也很好很Nice 👍🏻\n不太敢吃番茄都覺得老闆的番茄好好吃唷🤤\n真的很好吃，每一道都是老闆用心做的🧡\n第一次把伊比利豬吃的很像牛排，現在還在回味👍🏻\n男友也很喜歡老闆推薦那兩隻酒，蜂蜜檸檬也很好喝～\n最後最後最期待的國寶茶跟鳳梨乾真的很喜歡♥️\n要走的時候老闆還跟男友聊了一下茶葉的事情～\n男友也說下一次還要再來一趟🤤\n真的很值得去🧡下次一定還會再光顧的', 4.6, 137, 630, 'e8yIHZMQbT@gmail.com', '02-75912862', 4, '["星期一: 12:00 \\u2013 00:00","星期二: 12:00 \\u2013 00:00","星期三: 12:00 \\u2013 00:00","星期四: 12:00 \\u2013 00:00","星期五: 12:00 \\u2013 00:00","星期六: 12:00 \\u2013 00:00","星期日: 12:00 \\u2013 00:00"]', 'https://website--69021728850706958353-restaurant.business.site/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.47 下午', '2021-06-16 08.28.47 下午', 137, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 947, 1, '69289998', '享櫻 SHINE 和風西洋料理餐廳', '600', '嘉義市', '東區安樂街1號', 23.48325000, 120.46310000, '約會餐廳', '約會餐廳 提前一週訂位了母親節當天的七點半，也比原訂時間早十五分進場，等了一個半小時只吃到麵包跟葡萄酒，最扯的是其他桌比我們早到的連麵包都比我們晚吃到？？好奇內場是不是有出現什麼問題？現場工作人員一問三不知態度也很隨便，從頭到尾都不知道在裝忙什麼，連出餐順序都搞不清楚，完全毀了母親節吃飯的興致，還好吃完麵包就離開了不然我看這頓飯是要吃到凌晨，一起身連同其他桌客人也說要離開了，餐廳跟員工素質請加油好嗎？不會再去第二次了謝謝裝潢、音樂、服務有點老派，但前菜、湯、甜點都頗具水準，就是主菜太平庸。米其林二星主廚，主家常菜食材跟料理，麵包裡外都脆皮清淡，沙拉前菜蝦子真的*瞎*，山藥cosplay干貝裝，“今日魚“是吳郭魚長知識了，茶碗蒸不是一般的日式海味而是滿滿“梗"味。\n很適合初次約會，定會留下滿滿回憶。悠閒舒適的用餐環境，餐點口味不會太重，讓人感覺身體不會有太大的負擔，店家也會配合客人的用餐速度上菜，適合帶長輩們一同用餐。服務需要加強   東西不夠好吃', 4.3, 305, 1312, 'oYrsjet@gmail.com', '05 275 5589', 2, '["星期一: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00","星期二: 休息","星期三: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 18:00 \\u2013 21:00"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.28.48 下午', '2021-06-16 08.28.48 下午', 305, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 948, 1, '04406790', '洋城義大利餐廳-嘉義大潤發店', '600', '嘉義市', '西區博愛路二段281號1樓', 23.47818730, 120.43805700, '約會餐廳', '約會餐廳 我法國先生吵了很久想要來試試看\n我個人點了菠菜鴨胸燉飯 +78元經濟套餐（歐式麵包、主廚例湯和飲料）\n我先生點了爆炒蛤蜊義大利麵+58元小資套餐（歐式麵包、主廚例湯或是飲料二選一）\n剛開始的歐式麵包還不錯，是我喜歡的口感，湯品味道不錯只是有點稀\n第一：爆炒蛤蜊義大利麵的醬汁多於麵\n\n第二：菠菜鴨胸燉飯的鴨胸鹹到炸，而且燉飯的口感根本不是燉飯，很像只是把醬跟飯混在一起而已\n\n第三：我跟我先生點了阿薩姆紅茶，服務生問我甜度冰塊，一杯半糖少冰一杯微糖少冰\n結果送上來，問哪一杯的甜度是什麼的時候，服務生看了一下單子，然後說我們甜度是固定的喔\n那當初問我甜度是...？\n\n第四：因為由於門口很多人在等，所以我們想說來排隊候位，一位看似主管的人員站在門口，什麼話都沒說，做自己的事情，抬起頭看著我們一副你們要幹嘛，我就說我們兩位請問需要候位嗎？這位先生完全沒回話，我們兩個就站在那邊等，直到有服務生來帶位\n\n第五：服務生抱怨顧客是很正常的事，但是誰都知道不能在顧客附近抱怨，兩位女服務生就在我們桌子不遠處開始抱怨顧客\n\n整體消費下來很不愉快，花了這樣的錢，服務不好就算了但是餐點也沒有驚艷到哪裡去，不會推薦親朋好友來受氣也不可能再回訪了今天到嘉義大潤發的洋城義大利餐廳，實在感覺不是很好：\n1.等了很久，詢問後才發現有漏餐，其他人都吃完了餐點才上來，送來了也沒說什麼放下服務人員畫完單就走了\n2.青醬義大利麵鹽巴沒散，一整坨鹹到爆\n3.剛好坐在出餐區，看服務人員收完別桌使用完的廚餘餐具，放了整理完也沒洗手又直接拿新的餐點出餐，難道手都不會碰到殘餘的食物嗎？衛生實在堪慮！！\n\n尤其是第三點，我直接跟服務人員反應，結果得到回答是：\n如果收完沾到會洗手再送餐，沒有沾到的話才會直接出餐（這回答令人傻眼，這是衛生問題吧！回收區也沒有設水龍頭⋯⋯\n\n之前也在高雄吃過同一間，沒遇過這樣的狀況，實在非常需要檢討改進！雖然給一顆星，但是我給服務人員5顆星\n\n但我是來吃飯的，重點還是要落在餐點\n點了三道菜；第一道青醬義大利麵真的不錯吃，有讓我驚艷到，但接下來才是地獄的開始\n第二道是奶油焗烤飯 很像奶油粥 杏鮑菇一整條，非常難咬\n第三道 牛肝菌松露燉飯 ，完全沒松露味（但服務人員還是幫我重新料理）我建議廚房可以去古拉X吃看看，就知道自己差異在哪邊\n\n來這邊我強烈建議點麵就好\n另外服務人員真的不錯，會注意客戶的感受用餐環境很有感覺～\n整體餐點味道還行，但我點的奶油蘑菇義大利麵（素）裡面只有奶油、菇類、義大利麵😂沒有其他甜椒之類的配菜，略顯單調；另外香草奶茶（去冰）味道很淡，家人說有一股梅子味，希望只是味覺剛好失誤，不是杯子本來裝什麼沒洗乾淨#\n用餐過程中，服務生送餐、關心餐點是否送齊、詢問甜點是否現在上、贈品布丁沒有了能不能換成蛋糕等等，在短短的時間裡過於頻繁接觸店員讓人有點小小困擾。擺盤要擺精緻，但又不得不趕緊送上桌，導致桌上有點擠，覺得可以控制一下上菜順序。我曾經是米塔集團旗下的員工，台北洋朵重慶店的，內外場我都做過，多少瞭解一些。\n​\n​雖然嘉義開分店我很開心，可以邊吃邊回憶以前台北的生活工作及想念同事、店長、區域經理…等。\n​\n​但我今天中午第一次在嘉義洋城用餐，體驗不是說很好！\n​\n​內場料理有待改進，我點一道青醬義大利麵，真的是好吃，跟在台北幾乎一模一樣。但第二道焗烤雞胸燉飯，味道很淡、蔬菜太大片影響吃燉飯的口感，杏鮑菇太大一塊很靱，我真的咬不斷，如果有刀具就另當別論，我吃一半就不吃了。還有薯條整個油耗味很重，吃兩條就整個不吃了，讓我開始懷疑真的是米塔旗下的店嗎？！如果在台北這樣的話，內場員工早就被罵死了。\n​\n至於外場，​重點來了！我在用餐，外場服務小姐都會用異樣的眼光看待、有時還用不削的眼光看待及不耐煩，我不知道是自己太獨特，還是外場服務小姐人品有問題，還是米塔沒有教育員工如何對待客人？！都戴口罩了已經不用笑，還用這種眼光看待人，也許員工都是嘉義人，這種「素質」可以當作正常來看。依我以前在外場面對客人一定都要保持微笑及親切感，否則台北人很愛客訴，早不知道被客訴到哪裡去了。\n​\n​最後一件事，我去櫃檯結帳，櫃檯小姐的態度讓我找回以前在台北的熟悉感，態度真的很好，對待客人很親切，跟我在台北的外場工作夥伴幾乎一樣。\n​\n​但櫃檯小姐也犯了一件錯誤，我結帳時櫃檯小姐突然就往旁邊走去，丟下我一個人在櫃檯前，後來出來兩個員工，一個男生黑色衣服應該是外場店長還是副理？另外一個女生應該是內場主廚還是副主廚？三個人一起用異樣的眼光看著我，我不知道發生什麼事了？內心想，我有什麼問題嗎？讓我很不舒服！隨後我開口說道：「ㄣ…，這麼了嗎？」後來櫃檯小姐走回來跟我說，百鈔沒有了，稍等一下，我才心安下來。\n​\n​這麼會是這樣處理事情！？應該第一時間跟客人說，百鈔沒有了，請稍等一下，而不是丟下客人自己一個人走掉，又引來兩個員工側目看著我、又竊竊私語，讓我有一點生氣！！！\n\n我只能說，明明是米塔旗下的店，嘉義跟台北的料理及服務真的差十萬八千里。', 4.9, 1418, 6948, 'MDhtUAh@gmail.com', '05 233 0168', 2, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:00","星期日: 11:00 \\u2013 22:00"]', 'https://www.facebook.com/YunChenRestaurant/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.49 下午', '2021-06-16 08.28.49 下午', 1418, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 949, 1, '07564876', '伊尼絲創意風味餐館', '600', '嘉義市', '西區友竹街81號', 23.48469450, 120.43709790, '約會餐廳', '約會餐廳 營業時間前往卻撲空\n門口貼着出租套房濃湯很鮮甜，起司餃挺好的，有創意，熱可可很香純，自製醬汁很不錯老闆親切，裝潢良好，空間舒適\n但餐點等待時間太久，燉飯份量足夠但口味太淡，\n披薩一份150，直徑跟湯匙一樣大，\n份量不足外，口感潮濕，起司不足，\n等待時間非常久但披薩是涼的，燉飯是溫的老闆人不錯，價位不會太高，適合情侶約會或者姐妹講心事，餐點都是老闆用心製作，他的醬汁都是自己調配出來的，吃起來跟一般不一樣，比較不膩口，他們夫妻蠻用心經營的。創意新口味！色，香，味俱全！用餐環境芬圍佳！值得一去再去的好地方。', 4, 24, 96, 'etrmIbwX@gmail.com', '05 232 0215', 4, '', 'http://www.tiip.com.tw/site/companyinfo?id=5312', '0.jpg', '[0.jpg]', '2021-06-16 08.28.50 下午', '2021-06-16 08.28.50 下午', 24, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 950, 1, '88992494', '松築創作和食料理', '600', '嘉義市', '西區中興路221號', 23.48225610, 120.43484890, '約會餐廳', '約會餐廳 用餐環境寬敞舒服，服務人員服務普通\n五月第一週日用餐就已經規定只能點母親節限定套餐，價格比平常高出很多\n但菜色，鮮度，美味度都很一般般……連甜點也敷衍的，像是辦桌後附贈讓你方便帶走的。總之整體cp值很低，可能如果是平時價格落差沒那麼大，但我不知菜色方面差多少！回訪率20.388％是因為他有送300現金抵用劵，可一次使用，這點很棒👏👏雖然餐點不錯吃，但份量並不多，適合小鳥胃的人食用套餐加價吃的生魚片，旗魚跟鮪魚都有很大塊筋很難吃，咬不動東西味道普普,量跟價格Cp還可以！只是服務今天要點餐時，服務人員說怎麼了嗎有點無言XD\n比較偏創意料理不太像日本料理！環境乾淨服務很棒，人員會注意客人用餐的速度，適時的端上下一道菜！餐點沒話說，每一樣都很好吃', 4, 467, 1868, '8H4nGeGLiy@gmail.com', '05 291 0181', 3, '["星期一: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 21:00"]', 'https://www.facebook.com/songzhu168', '0.jpg', '[0.jpg]', '2021-06-16 08.28.52 下午', '2021-06-16 08.28.52 下午', 467, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 951, 1, '53290639', '水木町 板前·鍋物', '600', '嘉義市', '東區興中街172-9號', 23.48203320, 120.45039530, '約會餐廳', '約會餐廳 我們點的是 2000 元套餐，這每一道都是來自日本不同縣市，每一口都很有日本道地風味，也點了他們自釀的梅酒超級好喝，整體來說，性價比超級高，我們會再回訪。\n\n1. 愛知縣螢光烏賊：外型完全擄獲少女星，不小心興奮太久照太久，吃了一個原味的，真的很好吃，入口即化，第二個配了山葵，也好吃！\n2. 自製關東煮：那個豆腐實在好吃到沒話說，綿密又飽滿。\n3. (忘了哪的)尖梭和沖繩冰花：澎湖的冰花比較水嫩有韌性，這道尖梭也是煎的恰當好處。\n4. 蘿蔔(吃到飽)：我很喜歡吃蘿蔔，不小心吃太快，但師傅都會一直補，還說，這不是他遇過最誇張的。\n5. (忘了哪的)蜜柑鯛：韌性。\n6. 富士山小白蝦：我個人是不喜歡蝦貫，但水木町的蝦貫我覺得不錯！\n7. 愛媛縣白甘：軟嫩滑口，入口即化。\n8. 鹿耳島紅甘：有嚼勁。\n9. 鱈魚白子：我今晚最愛的一道料理！\n10. 干貝和章魚：我今晚最愛的一道料理！\n11. 牛小排：油花多卻不膩口，很讚！炭火燒的也好！\n12. 龍蝦湯：龍蝦肉實在很好吃，韌性也吃得出絲味。\n13. 和菓子：沒有澱粉，是用台灣白蝦、蛋、高糖和山藥做的，超級好吃！\n14. 海膽軍壽司：還不錯。\n15. 梅酒：必點！！！！超級好喝！！！！這是一家無菜單日式料理，是我們很喜歡的高級日式料理餐廳。因為每天食材不同也有限，建議用餐一定要事先訂位，並決定預定的價位（目前有1000、1500、2000的價位）\n新鮮美味海鮮很好吃，價格平價，讓人會想回訪的店。如果是像我們一樣品嚐無菜單日式料理，店家會安排在一樓的位置，空間不大，吧台式的座位有限，直接面對吧台後幫我們準備料理的師傅。一邊用餐一邊看著師傅熟練的仔細的準備料理\n一共上了18道菜\n▲位處文化路中央廣場一帶的水木町，附近住商混合，停好車後要步行一段時間往巷弄內移動。店鋪正對著中央廣場，外觀以簡易的日式原木做點綴，一派日式家庭料理餐廳的氣氛。\n店家也會安排不同的料理給客人驚喜的感覺。吧檯上黑板寫著推薦清單，店內最招牌的品象當然首選板前套餐，不喜生食的朋友也是有各式鍋物可以挑選。\n▲鹽烤蘇澳紅喉，以台灣紅喉當作原料的鹽烤料理，一端上桌就香氣四溢，烤得酥脆的魚皮與嫩又多汁的魚肉呈現著鮮魚的獨特口感，加上以鹽烤的料理方式，保證讓你無法忘記這個好味道。\n▲握壽司《赤蝦、黑鮪赤身、長崎青花魚》，鹽烤常吃到的青花魚在這邊以生魚方式呈現，Q彈甘甜的方式也是很棒的味道，加上豐美生鮮的赤蝦與黑鮪，以這道做為完美的結位非常適合。\n能夠在嘉義有這麼一家偶爾能去享用的高級日式料理，真的很幸福。一道道品嚐下來真的非常有飽足感啊～！\n雖然無菜單料理乍看之下價位較高些，但品嚐下來選用的食材和手法及份量！環境很清幽，且很乾淨，座位不會太擁擠\n海鮮部分的餐點相當新鮮，一點腥味都沒有\n當天點的是一整個套餐\n份量雖然都一小口，但因為就如圖一樣\n最後吃甜點還真的會很飽\n不過本人胃口較大，所以多吃了幾份才夠飽\n當然，一般人絕對夠吃，還會很飽\n本店真的值得去品嚐一番💯💯💯1F座位不多約12個，店內用餐預約制(1000元/1500元/2000元)/人以上，味道鮮美、日式料理真的是吃盤子的~所以食物照片都來不及拍就吃完了~店內消費收現金(沒發票)可刷卡加1成服務費。對面的廣場地下室有停車場20元/小時，注意周邊都是單行道開車要繞一下~真的超好吃,老闆對食材非常用心,大推~~吃幾次都不膩', 4.7, 207, 973, 'VjMGWs1@gmail.com', '05 216 7168', 4, '["星期一: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30","星期二: 休息","星期三: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30","星期四: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30","星期五: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30","星期六: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30","星期日: 12:00 \\u2013 14:00, 18:00 \\u2013 21:30"]', 'https://m.facebook.com/itamae9453/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-16 08.28.52 下午', '2021-06-16 08.28.52 下午', 207, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 952, 1, '67264801', '義式屋古拉爵 嘉義遠百店', '600', '嘉義市', 'b1, No. 537號垂楊路西區', 23.47258550, 120.44114450, '約會餐廳', '約會餐廳 環境優美，氣氛佳，服務有條有理。服務員熱情、食材鮮美。服務很棒 食物好吃^^既然要外帶服務就要能夠負擔外帶的點單，4點訂6點拿餐，到了以後才告知要等，那我提早兩小時訂心酸的嗎！服務：⭐️\n我們這桌6大2小，\n被安排在兩個大桌的包廂，\n入座後旁邊一大桌包廂客人離開了，\n詢問是否可以更換座位，\n服務生回應「等等有客人候位所以要先確認候位客人有沒有要來坐」\n然後，就沒有然後了...\n🔥雖然我們是已經入座，在安排座位上雖然必須配合店內情況，但後來直到甜點吃完隔壁桌都沒客人來坐，服務生也沒有來帶領帶位，服務品質欠佳，不會再來第二次。\n\n食物：⭐️⭐️\n點的鮭魚千層麵，看不太到千層麵，看得到很多油跟醬料；點的安格斯嫩肩牛松露義大利麵，牛肉煎得又焦又老，整個埋在生菜裡 第一看不懂擺盤，第二吃不到美味，也許我們不懂美食，但不會再來吃第二次。\n\n飲料：⭐️\n週日下午就說酒類全部都賣完，菜單上的酒類有紅酒、白酒、白蘭地、Mojito、愛爾蘭咖啡奶酒，這幾樣直接被說賣完，每種酒都是不同原酒，是不想賣還是真的賣完？可以直接說現在不供應感受會好一點。（不過後來細問才說啤酒有喔！感覺不太舒服）\n後來也有點了要加價的草莓冰沙，普通。\n\n甜點：⭐️⭐️⭐️\n星星比較多顆是針對至少使用的都是新鮮草莓不是冷凍草莓，但草莓聖代的冰淇淋口味也是普通，生乳酪蛋糕的生乳酪部分吃起來有點鬆散，吃到後面有點膩😅\n\n總分：⭐️⭐️', 4, 566, 2264, 'VfvEISv@gmail.com', '05 236 0337', 2, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 11:00 \\u2013 22:30","星期日: 11:00 \\u2013 22:00"]', 'http://www.grazie.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.53 下午', '2021-06-16 08.28.53 下午', 566, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 953, 1, '37538253', 'NU PASTA 嘉義垂楊店-嘉義市美食|義大利麵|焗烤|聚餐餐廳|平價美食|推薦餐廳', '600', '嘉義市', '西區垂楊路398號', 23.47404930, 120.44994180, '約會餐廳', '約會餐廳 好吃的不得了，份量足，齒頰留香。餐點美味🤤服務人員親切😀服務親切東西好吃cp值高讃！品項選擇性多且餐點美味餐點好吃人員熱情❤️', 4.4, 927, 4079, 'FBK0LBn4f@gmail.com', '05 222 3295', 2, '["星期一: 11:30 \\u2013 21:00","星期二: 11:30 \\u2013 21:00","星期三: 11:30 \\u2013 21:00","星期四: 11:30 \\u2013 21:00","星期五: 11:30 \\u2013 21:00","星期六: 11:30 \\u2013 21:00","星期日: 11:30 \\u2013 21:00"]', 'http://www.nu-pasta.com.tw/main/index.aspx', '0.jpg', '[0.jpg]', '2021-06-16 08.28.55 下午', '2021-06-16 08.28.55 下午', 927, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 954, 1, '69407126', '巷子裡的小餐館', '600', '嘉義市', '西區延平街373號', 23.47632300, 120.44496600, '約會餐廳', '約會餐廳 用餐體驗不佳，出餐緩慢，沒有告知已無餐點。\n剛開店就進去用餐，點了辣炒豬肉，同桌客人都吃完了，我去詢問服務生說去催餐，過了一下子出來說，剩下最後一份被廚師炒焦了，一直出餐給後面進來的客人，不知道為什麼發生這件事不馬上通知客人，讓我乾等，還要自己詢問才告知，不清楚有什麼誤會，願聞其詳？餐點選項多又好吃，服務人員親切，對幼兒友善，還有特約停車場，對開車族非常方便。地點在巷子內\n裝潢很復古\n餐點ㄧ點都不馬虎\n色香味俱全\n母親節來吃還送康乃馨\n誠意十足推👍換老闆後第一次來吃（吃看\n吃過之後，覺得之前比較好吃，現在餐點偏油，宮保雞丁雞肉不像之前鮮甜，偏乾柴⭐️，泰式椒麻雞還算好吃⭐️⭐️⭐️，炸豬排普通。⭐️⭐️\n因為點餐前服務生有保證說料理跟之前的一樣好吃，但吃了發現沒有，有點失望😅再加油囉！⭐️⭐️很小巧可愛的一間店 下次還會想要來😁', 4.4, 658, 2895, 'R3N9f0@gmail.com', '05 223 3737', 2, '["星期一: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期二: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期三: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期四: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期五: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期六: 10:00 \\u2013 13:00, 16:30 \\u2013 19:30","星期日: 休息"]', 'http://facebook.com/373kitchen', '0.jpg', '[0.jpg]', '2021-06-16 08.28.56 下午', '2021-06-16 08.28.56 下午', 658, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 955, 1, '48636070', '樂沙屋 新馬來料理(南洋美食、異國料理推薦)--嘉義店', '600', '嘉義市', '西區林森西路145號', 23.48393740, 120.44817090, '約會餐廳', '約會餐廳 叻沙屋休息一陣子後重新回歸啦！\n不僅店名更改為 #樂沙屋\n餐點品項也改良升級囉✨\n-\n海三鮮紅叻沙配料hen～澎湃，有炸金針菇、蝦、小卷、洋蔥、豆芽菜、青菜等，海鮮新鮮多汁，紅叻沙湯頭是用數十種南洋辛香料與新鮮蔬菜慢火熬煮的高湯，融入紅咖哩及辣椒的香氣，湯頭喝起來濃厚鮮甜帶有淺淺的辣度，麵食類有鍋燒意麵、讚岐烏龍麵、米粉、冬粉四種可選，讚岐烏龍麵Q彈吸附滿滿湯頭👍🏻\n椒麻雞外皮炸得酥酥脆脆～把雞腿的肉汁鎖在裡面，椒麻醬汁真的有辣🌶️推薦給愛吃辣的人\n雞肉串用特製香料醃製後炙燒，單吃就很夠味，搭配沙嗲醬再灑上花生粉，獨特的沙嗲風味很開胃！吃完好想來杯酒🤣\n咖椰吐司是用馬來西亞道地的咖椰醬搭配奶油，金黃香酥的吐司熱熱吃很好吃！\n炸香蕉包裹麵包粉下去炸，炸過的香蕉外酥內綿密，搭配甜甜的蒜蓉醬，據說是馬來西亞道地的小吃，很特別的風味～\n-\n💰海三鮮紅叻沙$250+讚岐烏龍麵$10\n💰椒麻雞腿飯$160\n💰沙嗲雞串$65\n💰炸香蕉$60\n💰咖椰吐司$60\n💰胡麻洋蔥$45\n💰泰式奶茶$60\n💰鮮爽蜂蜜檸檬$60\n-\n☎️：05-2250-217\n營業時間：11:00-14:00、17:00-21:00\n🚷：週二公休\n地址：嘉義市西區林森西路145號\n-\nInstagram 🔍 suilovefood\nFacebook 🔍 郭穗 愛食日常2訪\n去年妮蔻就介紹過在嘉義市林森路上這家來自 #台南 的火紅 #叻沙麵 ，前陣子老闆為了讓產品升級所以休息了一段時間，現在帶著全新的風貌回來，並將店名變更為 #樂沙屋 ，念起來更好念了😜\n((是不是很多人不知道 "叻" 這個字怎麼念🤣🤣))\n\n這次來2訪發現菜單上除了原有的叻沙麵系列外，還新增了打拋豬飯、椒麻雞腿飯、炸香蕉、泰式奶茶等等的新品項，就連叻沙麵條選擇也從三種變成四種了。\n\n所以這次來除了吃想念很久的叻沙麵以外，還點了椒麻雞飯 、 打拋豬飯 、金錢蝦餅 、 炸香蕉 、咖椰土司 、 泰式奶茶 、 印度奶茶 ，滿滿一桌不誇張 !\n\n#舒肥雞紅叻沙鍋燒意麵 這次眼尖的發現樂沙屋的麵食主食新增了舒肥雞，原本以為舒肥雞可能是舒肥雞胸，上桌的時候才發現是軟嫩的舒肥雞腿，搭配麵條與紅叻沙湯頭吃起來毫無違和感，當然碗裡還有放入最經典的炸金針菇，吃完整碗超級滿足的呀~\n\n#打拋豬飯 使用豬絞肉、九層塔與多種香料去料理而成的打拋豬超級下飯，帶點微辣的口感讓整體味道層次更分明，不小心就會把整碗扒光光🤩對了，打拋豬還可以將盤內水煮蛋升級為炸蛋喔~當天妮蔻忘了升級😭太可惜了\n\n#椒麻雞腿飯 將醃漬過的雞腿肉炸的酥脆好吃，接著淋上椒麻醬汁，吃下去嘴唇邊開始蔓延著麻辣感，超級過癮，我真的很推薦椒麻雞，因為真的很好吃，夠麻夠辣😋👍🙌\n\n#金錢蝦餅 樂沙屋這次把月亮蝦餅換成了圓形的金錢蝦餅，口感比原有的更酥脆，喜歡吃蝦餅的你們可以點來吃看看~\n\n#炸香蕉 老闆說這個炸香蕉沾甜醬油就是馬來西亞最道地的吃法，甜醬油裡頭有放入少量蒜容及花生粉，整個口味就是超級特別\n\n#咖椰吐司 第一次吃咖椰吐司的妮蔻我覺得咖椰吐司蠻好吃的耶，咬下去除了有香香的奶油味，還有馬來西亞當地的咖椰醬，整體鹹鹹甜甜😋當飯後甜點剛剛好啊\n\n吃了一大桌後當然要點上 #泰式奶茶 與 #印度拉茶  來配，我還記得上次來喝的是美祿，但是因為最近迷上了奶茶，所以就點了新品泰奶跟印度拉茶，兩杯飲料的甜度跟濃度表現都剛剛好，不會太甜或太薄，而且喝不完還可以打包外帶唷~\n\n總結一下這次的新品我都蠻喜歡的，而且之前老闆休息的時候就有小粉絲來問我說怎麼沒開了，終於等到現在又重新開幕，可以趕緊找時間去吃了~~推🙌\n\n-\n📍樂沙屋🇲🇾新馬來料理-嘉義店\n\n◼舒肥雞紅叻沙鍋燒意麵 $180\n◼椒麻雞腿飯 $160\n◼打拋豬飯 $130\n◼金錢蝦餅 $120\n◼炸香蕉 $60\n◼咖椰吐司 $60\n◼泰式奶茶 $60\n◼印度拉茶 $60\n\n🏠嘉義市西區林森西路145號\n🈺平日 11：00-14：00 / 17：00-20：30、假日 11：00-14：30 / 17：00-21：00\n☎05－2250217\n❌周二公休\n🌐UberEats上次用餐位子背對牆壁，感覺冷氣有點不夠強，吃到流汗有些熱，鍋燒意麵很像是直接泡在湯裡面，口感有些硬或者這是店內特色呢🤣一口氣點了三種不同湯底，口味都不錯，份量不少，看其他桌點的飯類也很好吃。唦爹我愛吃肉很嫩，吐司不合我們味口，乾炒河粉鍋香氣很濃用料算不錯有雞肉和蝦子，但覺得有點偏甜，海南雞飯跟椒麻雞飯都不錯，肉跟飯給的很足，已這價錢cp值算不錯了，小孩吃的很滿足，是會在二訪的店，但冷氣太不涼了，覺得空氣不流通，感覺沒開冷氣一樣', 4.1, 131, 537, 'l7Z02JR2@gmail.com', '05 225 0217', 4, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:30","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 21:00"]', 'https://www.laksahouse.com.tw/index.html', '0.jpg', '[0.jpg]', '2021-06-16 08.28.57 下午', '2021-06-16 08.28.57 下午', 131, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 956, 1, '94544887', '皇爵臺菜料理', '600', '嘉義市', '西區新榮路234號1F', 23.48001780, 120.44417710, '約會餐廳', '約會餐廳 10年前回到嘉義縣番路郷長住，\n很難找到一家好吃的餐廳，又有古早味的味道，cp 值又高。昨天晚上意外在朋友的請客下，踏入皇爵。真的很驚喜，我開玩笑向朋友說有廚師在埸的餐廳一定好好吃喔！生意超好，假日一定要訂位。餐點有點退步，但仍是水準之上。生意太好，雖有訂位，\n但到場之後仍是等了10多分鐘，\n\n菜色特別，又有白飯又有粥，\n招牌聽說是東坡肉跟滷豬腳，\n果然非常下飯，好吃\n不過牛肉就還好，柴柴的，不優！很基本的套餐，出外菜流程有點混亂，但是餐點附的地瓜粥非常好吃皇爵大飯店餐廳的套餐很不錯，服務員態度親切，中餐排餐料理豐富，因我在中餐即將結束才去吃吃了餐點一半麻煩服務員幫我打包，喔！服務員態度親切ok，讓我覺得賓至如歸下次有機會我會帶朋友一道去吃，(感謝服務員態度特優)', 4.3, 165, 710, 'idSg4Kv6f@gmail.com', '05 223 3411', 4, '["星期一: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期二: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期三: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期四: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期五: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期六: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00","星期日: 06:30 \\u2013 10:00, 11:30 \\u2013 14:30, 18:00 \\u2013 21:00"]', 'https://www.chiayikinghotel.com/dining/', '0.jpg', '[0.jpg]', '2021-06-16 08.28.58 下午', '2021-06-16 08.28.58 下午', 165, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 957, 1, '39220228', '龍師牛排', '600', '嘉義市', '西區南京路400號', 23.46417880, 120.43633670, '約會餐廳', '約會餐廳 以前在中興路的時候勝過現在在南京路\n以前便宜又好吃，現在不行了\n兩個點都消費過，沒事上網分享一些吃過的店家的感受，那麼激動幹嘛，不能分享自己消費的感受嗎？\n我知道現在走高檔路線，可是走的失敗啊現切肉 石板烤肉 很特別的體驗 cp值尚可\n不錯吃👍氣氛不錯沒吃到飽方案，就沒特色，好想念過去點餐方式複雜沒說明\n肉也沒問熟度', 3.6, 743, 2675, 'BZb9XyL@gmail.com', '05 283 8869', 2, '["星期一: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期二: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期三: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期四: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期五: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期六: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20","星期日: 11:30 \\u2013 14:20, 17:30 \\u2013 21:20"]', 'https://m.facebook.com/longsteakfans/?locale2=zh_TW', '0.jpg', '[0.jpg]', '2021-06-16 08.28.59 下午', '2021-06-16 08.28.59 下午', 743, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 958, 1, '34310102', '大人味 日.印咖喱', '600', '嘉義市', '東區成仁街94號', 23.47815190, 120.45236540, '約會餐廳', '約會餐廳 #妮蔻吃嘉義  #2訪\n這家位於成仁街上的 #大人味日印咖哩 大家應該都不陌生，只有平日周一到周五才能吃到，而且10點多就要來排隊，11點開門後依排隊順序開始點餐。(但因為店內只有兩人作業 所以就算第一輪進去也不一定馬上就能吃到餐點喔)\n\n當天很幸運的直接當了頭香組，謝謝 @2tea._.food 很給力，10點半就到了🤣🤣🤣\n因為第一次來吃的時候吃過漢堡排，所以這次來就點了印度咖哩-橙汁里肌豬肉咖哩與大人味牛肋咖哩。\n\n印度咖哩的味道偏酸且帶點辣，咖哩醬內還有一點橙子果肉，豬肉煮得相當軟嫩，我個人蠻喜歡這個口味，很開胃😋\n大人味咖哩的部分比較重口味，牛肉的部分也很好吃，但我個人還是比較愛印度咖哩一些🤣\n\n最後一張圖放上2017年初訪時的照片，已經忘記味道如何了，所以就不多敘述🤣但當時也是10點多就來排隊~~~\n\n-\n📍大人味 日.印咖喱\n\n◼大人味牛肋咖哩 $280\n◼橙汁里肌豬肉咖哩 $280\n\n🏠嘉義市東區成仁街94號\n🈺11:00–14:00、17:00–20:00\n❌六日公休〈日式漢堡排咖哩$280 / 4.3★〉\n很久以前就把這家店放在清單裡面，做好功課知道顧客很多，所以在開店前就提早大約半小時來排隊，還先去買了純情紅豆餅邊吃邊等，雖然是平常日但抵達時前面已經有一組客人，到開店時間已經是有兩輪的顧客在等，後來的大概需要等到六點半，覺得最近在嘉義想吃點什麼，大排長龍的情況已經是常態了。老闆請顧客按照順序入座也會按照順序點餐，點了店家推薦的漢堡排咖哩，每種餐點份量有限制，吃不吃得到要看運氣。觀察店裡面的擺設，層板上方有marshall的電吉他音箱與唱片，也掛著一把吉他，感覺老闆以前是個愛玩音樂的人，而店裡面喇叭放著恰克與飛鳥的，電視上播的是日本新聞，廚房區就在吧台前面可以看著老闆料理，真有一種日本餐食店的氛圍。吃咖哩之前先送來蔬菜麻油清湯，甘甜的鮮味伴著麻油香真是好喝。咖哩上來之後迫不及待先吃漢堡排，肉餡多汁紮實有彈性是我喜歡的口感，漢堡肉經過鐵鍋的高溫煎出油脂香氣非常吸引人，台灣牛肉也能有這種表現很驚訝，搭配咖哩與薑黃飯吃很棒。而咖哩走的是日式風格，薑黃香氣濃烈但不偏甜味，反而覺得苦味有些明顯，味道不錯，但我覺得漢堡排表現的比咖哩更突出，光是漢堡排我就願意再來光顧，期待下次來吃不一樣的咖哩。\n時間：2021/4/12平日（星期五）14:23到排第2組。前面有2位～（時間僅供參考）\n\n剛好碰到平日來嘉義才有辦法品嘗到！\n雖然需要開店前30分鐘排隊等候但滿值得的\n喜愛咖哩的我覺得很好吃耶🥺\n跟市售香料/咖哩塊煮的咖哩有滿大的差異\n一入口就可以感受到是用很多蔬果熬製而成的醬汁～吃得到甜味\n但如果喜歡的是重口味咖哩的話可能要斟酌一下喔😅\n\n這次是點牛肋咖哩\n因為想嚐嚐漢堡排以外的主菜～\n牛肋很軟嫩！咬起來完全不費力\n\n內用環境因為有人數限制不會顯得吵雜\n播放的日文歌曲讓氣氛變得好像是在沖繩的家常餐廳😂👍🏻\n\n主餐前有附上蔬菜湯\n麻油蔬菜湯喝起來不油～蔬菜份量也很多\n另外還會附上一大杯麥茶\n雖然一天供應的份數真的不多也可能久候\n但很值得👍🏻好吃這間店已經不只是全嘉義最好吃的咖哩了，是在全台灣排得上號好吃的咖哩，咖哩這種很難難吃的食物要脫穎而出實在不得了，各個元素都搭配得宜且充滿特色，要我說來嘉義比起火雞肉飯，應該先吃大人味才值得。在門口被後面的插隊，進去那位小姐還不確定要不要吃，我們先確定之後，居然讓那位後來才確認硬擠進來的小姐，我也是服了老闆', 4.6, 556, 2558, 'ZgnCH6@gmail.com', '02-58378576', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 20:00","星期六: 休息","星期日: 休息"]', 'https://www.facebook.com/%E5%A4%A7%E4%BA%BA%E5%91%B3-%E6%97%A5%E5%8D%B0%E7%8F%88%E5%93%A9-173336086469276/', '0.jpg', '[0.jpg]', '2021-06-16 08.29.00 下午', '2021-06-16 08.29.00 下午', 556, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 959, 1, '11429515', '旺柴燒肉屋', '600', '嘉義市', '西區建成街36巷7號', 23.47196020, 120.44101900, '約會餐廳', '約會餐廳 個人套餐牛＋牛肉丼飯＋日本山藥。  每道都很不錯。醬汁偏甜。蛤蜊湯頭偏甜。山藥炙燒表皮酥脆口感爽脆多汁。前菜如菇類豆類等整盤都飽滿多汁。非常不錯。服務也很好。烹調用心。很滿意。菜、肉烤的剛好，很多汁～好吃上菜快，每道菜都好吃！很驚艷！\n環境舒適，附近也蠻安靜的\n來這裡用餐物超所值！個人餐+蛋超值！\n其中我最喜歡烤鯖魚與精力湯ヾ(〃^∇^)ﾉ\n尤其是精力湯！回到家還一直念念不忘❤真的超級無敵好吃\n每樣菜都不踩雷', 4.9, 76, 372, 'whQSnxuvP@gmail.com', '0926 277 925', 4, '["星期一: 休息","星期二: 17:00 \\u2013 00:00","星期三: 17:00 \\u2013 00:00","星期四: 17:00 \\u2013 00:00","星期五: 17:00 \\u2013 00:00","星期六: 17:00 \\u2013 00:00","星期日: 17:00 \\u2013 00:00"]', 'https://www.facebook.com/bmp0459L/', '0.jpg', '[0.jpg]', '2021-06-16 08.29.01 下午', '2021-06-16 08.29.01 下午', 76, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 960, 1, '67310601', '常常', '600', '嘉義市', '東區忠孝路336號', 23.48879420, 120.45353350, '甜點', '甜點 二訪\n今天依然吃鹹派跟甜塔\n蒜苗鹹豬肉\n今天的鹹派比之前好吃呢，派皮酥軟不像之前硬到咬不下，鹹豬肉的味道很香，但蒜苗的味道很不明顯，可能是被蓋過了。\n\n沒吃到最喜歡的憨吉蒙布朗，超級難過的，但紅烏龍小山也不錯，裡面有覆盆子奶油陷，酸甜加上茶香，但我覺得覆盆子奶油可以在多一些，整體的甜味跟協調會在更好。\n\n生巧克力塔，我覺得不能放在最後吃，不然嘗不到精彩的味道，畢竟在吃完較甜的甜點後，吃帶有苦味與酸味的巧克力塔，沒辦法好好的感受巧，克力中苦與酸與巧克力的香氣，所以今天對巧克力塔沒有多大興趣。\n\n但比上一次印象好了不少，希望下次在造訪時，能一直有這樣的好印象。\n\n----------------------------------------------------\n一間中規中舉的店，但有很多缺點，先來說說鹹派吧！派皮硬到一個不行，切不下去就算了，還刮傷我的牙齦，真的非常痛，但是內餡是好吃的有白醬搭配雞肉和菇類，奶香十足帶著雞肉的香味，菇類吸收了兩種味道，感覺很精華，非常棒，如果沒有那個內餡，可能連三顆星都不到吧，不OK。\n甜塔的部分，我點了憨吉蒙布朗，地瓜泥配上黑糖漬的地瓜，底下有一層優格生乳酪，生乳酪把原本甜膩的味道中和掉了，迎來的是地瓜的黑糖香，與軟糯的口感，跟地瓜泥的香味，地瓜泥的糖放的恰到好處，帶出了地瓜本身的香味，這讓我非常喜歡。\n飲料的部分，我很無言，冷泡的太淡了，可能茶葉的量不夠，雖然不會到像水那麼淡就是了，建議泡濃一點，雖然你們重點不在飲品，但這點真的太差了，畢竟85元的250ml玻璃杯，只有這個程度，不優。這裡的甜點超好吃！\n雖然不能訂位，但感受到店家做甜點的用心\n以成本來說，價格真的經濟實惠\n\n紅酒牛肉鹹派很到味又酥又脆\n嘉義最推的鹹派\n\n飲品牛奶是用嘉大牛奶～推薦！\n\n更新評論\n\n每次回訪都覺得好吃\n#回嘉必吃疫情期間帶回家吃，口感層次很豐富，外層的憨吉很濃厚很好吃，中間像是咖啡慕絲，吃不出口味的小塊羊羹，以及底部一層是檸檬帶有微酸的味道，塔皮酥脆，偶爾會咬到杏仁片，搭配起來很適當!這次來嘉義玩搜尋到這間店，點了一個瘋狂起司鹹派跟紅烏龍小山蛋糕，飲料點了拿鐵跟鮮奶茶\n鹹派的部分帶有蛋黃香的起司搭配酥脆的外皮趁熱吃口感一流，蛋糕的部分切面會有小驚喜，蛋糕偏甜，頂端的醬嚐起來有烏龍香味，味道有層次感\n飲料的部分鮮奶茶是奶味較重的那種，咖啡焙味十足很香\n吃完附近也有許多地方可以走走\n如果有機會還會想來光顧有生以來吃過最好吃的檸檬塔٩(˃̶͈̀௰˂̶͈́)و\n（其他塔也好吃！）\n這個精緻度卻只要這個價錢真的太神奇\n去就對ㄌ', 4.7, 247, 1161, 'IRcQawK@gmail.com', '05 276 5830', 2, '["星期一: 休息","星期二: 休息","星期三: 休息","星期四: 14:00 \\u2013 18:00","星期五: 14:00 \\u2013 18:00","星期六: 14:00 \\u2013 18:00","星期日: 14:00 \\u2013 18:00"]', 'http://www.facebook.com/frequent09/', '0.jpg', '[0.jpg]', '2021-06-16 08.29.03 下午', '2021-06-16 08.29.03 下午', 247, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 961, 1, '56972772', '初禾-手作甜點‧咖啡-回嘉店', '600', '嘉義市', '西區民生南路137號', 23.47024080, 120.44726560, '甜點', '甜點 今天試買一塊檸檬塔，清爽好吃，味道偏酸，是我喜歡的口味。室內乾淨，產品中價位，會再回購其他商品。如果你跟我一樣是看到google評價而滿懷期待第一來來「初禾」，你可能會非常失望。店員態度非常差，短短不到五分鐘的交談，我甚至覺得她非常討厭甜點（以及她的工作），吝於開金口為第一次到店的客人介紹（我一股腦熱心的想了解，想吃好吃甜點的心被狂潑冷水），還沒吃之前我抱持這家甜點很好吃所以妳（店員）對待客人態度可以非常傲僈（畢竟評價很高）那我接受，但我對於她（店員）搞不清楚自家甜點食材產地，說甜點都是在外面廚房做的？？（原來不是自己做的？），沒有採用嘉義在地食材？（好像沒有），她說想知道菜單何時更新自己網路查（心裡默默想所以妳此刻在我面前的作用是？）\n\n（我看不如建議跟拉麵店一樣換成投幣式點餐結帳可能會比較好，旁邊附上條碼掃描甜點訊息自己看⋯⋯，省的妳還需要耗費「不必要」的時間在客人身上⋯⋯大誤）\n\n這些完全不太想理人且敷衍的回答，我看得出來妳很跩，我可以不放在心上（畢竟我尊重美食，而我當下還沒吃到，我不介意店員的冷漠），甜點店的店員可以有個性（態度），但是不應該對食物沒有熱情，而且看起來並不尊重身為甜點的行業。拒絕回應、介紹「初禾」任何甜點訊息，是蠻令人傻眼的，問不出任何專業（之於我是個普通吃客的熱情）的回應，最後我只好所幸乾脆說一聲那我買四個綜合好了！（很糟糕的體驗）我買回家吃吃看到底是怎樣，我是為了甜點來的，不是為了妳的冷漠來的！\n\n這間甜點店同條街的她他香煲仔飯《港廣式料理》老闆娘也很有態度，雖然人（性格）冷冷的（可能因為忙）但是至少不討厭做料理。我只能說「初禾」的店員讓我對這間甜點店的初體驗，期待值已經變成0分（吃完已經變成負值）。\n\n回到家，泡了一杯咖啡，準備要來好好享用。既然店員給我的態度這麼「不想介紹」。那我也會只講重點簡單分享這四塊蛋糕的評價，但我可能還能比她分享的多⋯⋯\n\n四塊蛋糕（巧克力、檸檬、草莓、乳酪），都以塔皮作為基底，因為食材搭配關係，每個塔皮軟硬度都不太一樣。食材除了草莓（好吃），藍莓（藍莓很難難吃到哪裡）。其他倒是這位店員她率性的態度中表達得很清楚了，並沒有特別講究，所以吃起來也不特別驚豔。口感層次上很普通。紙盒包裝是蠻用心，但我覺得一片蛋糕一百多是偏貴了！\n\n1.檸檬 口味 餡料酸而不膩 好吃\n2.巧克力口味 皮塔一碰就脆 很甜 非常普通 應該不是用純巧克力 不推 (忘記講那顆白色類似馬卡龍的甜點，不足以用這個名稱稱呼它，一團糖塊餅皮罷了)\n3.草莓 口味 草莓非常新鮮 擺設非常用心漂亮 皮塔層次普通\n4.乳酪 口味 作為一位常常點純乳酪的人 這個非常普通 不推\n\n我其實最想吃那個芋泥千層（看起來非常厲害），但我不知道我能不能再忍受這位店員一次，再次去消費，用我的熱情去貼別人（店員）的冷屁股（等等，這通常角色應該反過來吧ＸＤ）\n\n謝謝店員讓我跟這家甜點的初體驗糟糕到不行，下次還是去吃吃嘉義其他家吧～也沒有這麼好吃到讓我為了美食折腰的地步。暫時不會再去了。很抱歉，我今天跟朋友吃完紅茶奶酒蛋糕跟栗子什麼的蛋糕，直接賽在滾，加上肚子狂翻滾^_^，有點失望。甜點店系列\n推薦乳酪系列是他們招牌  本次吃的是百香果慕斯   好好吃！  推推壓秒光顧店面，還好還有剩下切片蛋糕可以外帶，一直看網路上聲量很高，親自到店後加上店員親切的介紹，還送了小禮物給我們，真的是比預期的好太多了！蛋糕也是真的超級好吃，不膩！\n也感謝當天228連假的美麗店員😆', 4.2, 140, 588, 'TGPICsr6Z7@gmail.com', '05 225 0386', 4, '["星期一: 10:30 \\u2013 18:30","星期二: 10:30 \\u2013 18:30","星期三: 10:30 \\u2013 18:30","星期四: 10:30 \\u2013 18:30","星期五: 10:30 \\u2013 18:30","星期六: 10:30 \\u2013 18:30","星期日: 10:30 \\u2013 18:30"]', 'https://chiayi-chuho-cake.business.site/', '0.jpg', '[0.jpg]', '2021-06-16 08.29.04 下午', '2021-06-16 08.29.04 下午', 140, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 962, 1, '03098846', '平安京鯛魚燒/嘉義美食/嘉義小吃/嘉義下午茶/嘉義鯛魚燒/嘉義甜點/西區美食/西區下午茶', '60083', '嘉義市', '西區龍江街55號', 23.49001660, 120.44088230, '甜點', '甜點 這家精緻小店是嘉義3天兩夜遊的意外驚喜：白Q皮萬丹紅豆、薄皮黑糖、酥皮咖喱豬 各有特色。  下次有機會再來試試其他口味。超好吃的鯛魚燒，又脆餡又多，不死甜薄皮、白Q、可頌三種外皮，搭配多樣鹹、甜內餡可供選擇！不在嘉義大排長龍的主街上，位處寧靜街廓的別緻店面，充滿日式小店的氛圍。與其吃不到的玉米、排不到的魚頭，一通電話預定，保證享有熱騰騰Q彈彈的日式鯛魚燒，真的不能錯過！（每條魚都是老闆和老闆娘用正統鯛魚燒鐵夾，一隻隻慢慢做出來的，量大時最好前一天預訂）脆皮鯛魚燒，真的好好吃，雖然價格真的很高，\n但每一口都覺得值得，\n等待時間長了點，因為現做才好吃～\n\n有跑去吃別間，不是脆皮的便宜的，但有一種雞肋感～\n這間真的有道地～美食令人感覺幸福，餡好實在', 4.8, 119, 571, 'Z6PLmSzy5@gmail.com', '05 231 3539', 4, '["星期一: 休息","星期二: 12:00 \\u2013 18:30","星期三: 12:00 \\u2013 18:30","星期四: 12:00 \\u2013 18:30","星期五: 12:00 \\u2013 18:30","星期六: 12:00 \\u2013 18:30","星期日: 12:00 \\u2013 18:30"]', 'https://www.heiankyu.com/', '0.jpg', '[0.jpg]', '2021-06-16 08.29.05 下午', '2021-06-16 08.29.05 下午', 119, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 963, 1, '69773273', '50號烘焙餐庫 (露比夫人) 【疫情期間暫停供餐】| 嘉義健康早午餐 | 嘉義西區下午茶 咖啡 甜點', '600', '嘉義市', '西區遠東街50號', 23.48555440, 120.43686370, '甜點', '甜點 整體挑高的空間令人感到舒適不壓迫。\n餐點的口味也符合大眾口味，尤其喜歡店家真材實料的全麥軟歐！\n麵包香氣和口感搭配無糖鮮奶茶真的是很享受的一餐。\n推薦飲料可以墊個杯墊，不然桌上有水珠會一直濕濕的～\n\n當天打卡送的咖啡布蕾口感超好✨\n列入會二訪的口袋名單☺鮭魚肚可頌好好吃😋鮭魚肚很嫩無刺，份量也不錯飽👍\n用餐環境氣氛佳，很舒服愉快的用餐體驗❤️總算來吃開了好久的露比夫人裡頭的早午餐，但這邊除了供應早午餐以外還有火鍋、披薩、各式飯食以及多樣化的甜點!\nIG上的地標似乎沒有太多的打卡紀錄，想必可能是因為地點離市中心太遙遠的關係~\n\n今天來吃之後發現其實餐點不錯呀，整份早午餐有他們自製的手工麵包以外，上頭還有一整顆的水煮蛋且幫你切片、滿滿的蔬菜、堅果、一杯優格再配上主食，完全沒有那些炸來炸去的垃圾食物，對於注重健康飲食的人來說很棒欸，而且早餐盤給的麵包很大一個我還吃不完，很有誠意，我應該很快就會再來二訪了😚\n\n推🙌🏻🙌🏻🙌🏻🙌🏻🙌🏻\n\n-\n📍50號烘焙餐庫\n\n◼黑菠蘿板塊早午餐(煙燻櫻桃鴨胸) $260\n◼全麥果乾軟歐早午餐(黑胡椒醬燒雞腿排) $230\n◼皇家伯爵茶 $100\n◼50號鮮奶茶 $110\n\n🏠嘉義市遠東街50號\n🈺10:00 - 16:30\n📞05-2313168\n-早午餐輕食都很棒, 尤其是用餐環境！優雅清靜。整體而言值得推薦。用餐環境乾淨舒適！餐點健康又好吃😋且是寵物友善餐廳👍👍推推～', 4.9, 193, 946, 'BGOwCte@gmail.com', '05 231 3168', 4, '["星期一: 10:00 \\u2013 16:30","星期二: 10:00 \\u2013 16:30","星期三: 10:00 \\u2013 16:30","星期四: 10:00 \\u2013 16:30","星期五: 10:00 \\u2013 16:30","星期六: 10:00 \\u2013 16:30","星期日: 10:00 \\u2013 16:30"]', 'https://madamrubyfood.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-16 08.29.06 下午', '2021-06-16 08.29.06 下午', 193, 0);
