INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 965, 1, '83006743', 'Fun Tower. 日式可麗餅', '600', '嘉義市', '東區興中街2-1號', 23.47446060, 120.45186200, '甜點', '甜點 進店內就有人工香料味撲鼻而來，個人不太喜歡。但還是想嚐嚐網路上所謂日式可麗餅的味道就留了下來，看了價錢完全點不下去，最後選了看起來可能會很好吃的50元甜點（波麗露之類的），吃完肩膀覺得很沉重。不會再回購。不怎麼樣 奶油一堆 很膩 又很甜 價格也貴  性價比低  不推薦水蜜桃卡士達95$\n好好吃\n一做好立馬吃掉\n太好吃了😋少見的軟可麗餅，比起那種硬硬的更喜歡這種軟的，不會刮嘴巴\n草莓是新鮮草莓，不是冷凍的那種很加分\n透明窗戶，可以看到甜點師親手料理，很療癒～～可麗餅的皮不錯，但是我覺得料偏少，跟日本的比起來稍微美中不足！', 4, 118, 472, 'f4HB5Yg@gmail.com', '05 228 5678', 2, '["星期一: 15:00 \\u2013 20:30","星期二: 休息","星期三: 15:00 \\u2013 20:30","星期四: 15:00 \\u2013 20:30","星期五: 15:00 \\u2013 20:30","星期六: 15:00 \\u2013 20:30","星期日: 15:00 \\u2013 20:30"]', 'https://www.facebook.com/funtowerchiayi', '0.jpg', '[0.jpg]', '2021-06-16 08.29.08 下午', '2021-06-16 08.29.08 下午', 118, 0);
