INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 1110, 1, '78164950', '重慶麻辣魚', '893', '', '金城鎮光前路31號', 24.43210460, 118.31623920, '居酒屋', '居酒屋 環境衛生髒 還會黏黏的\n但是份量還可以\n味道普普通通\n\n尖峰時刻會等很久\n洗碗可以認真洗嗎\n還有水不要亂噴2014年來到現在，依然口味不變。\n料多、味美實在好吃😋！\n\n我們團隊必點的是：香腸炒飯、麻婆豆腐、羊肉燴飯、回鍋肉蓋飯\n如果時間上可以，可以在裡面吃，感受一下真正的金門金城到地的快餐店感覺（老闆娘手腳很利索）上菜速度超快！\n非常重口味的菜\n吃一口就想喝啤酒\n酸菜炒肉酸的很不客氣\n反而是酸菜魚湯沒有想像中的酸\n燴飯超大份，白飯翩軟黏的口感 老人族會愛豬肝湯$50×豬肝蠻脆\n蝦仁炒飯$60×蝦仁算給的蠻多，口味上鹹度剛剛好\n重慶水煮肉片$200×我真的很討厭肉片上有粿粉耶!!!!有點壞了這鍋...\n\n⭐悠悠飲食時參考：\n甜：手搖飲半糖>偶爾3分糖\n鹹：蠻重鹹的\n辣：中辣.炒飯份量很夠\n入味好吃\n可以加上店內特製沙茶\n味道滿搭的\n酸辣魚會有一個土味\n不敢吃魚下巴的人\n建議不要點哦\n蛤蜊湯簡單入味\n價格親民', 4.5, 103, 464, 'XbpVbO@gmail.com', '0963 280 523', 1, '["星期一: 10:00 \\u2013 20:30","星期二: 10:00 \\u2013 20:30","星期三: 10:00 \\u2013 20:30","星期四: 10:00 \\u2013 20:30","星期五: 10:00 \\u2013 20:30","星期六: 10:00 \\u2013 20:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-16 08.41.10 下午', '2021-06-16 08.41.10 下午', 103, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 1111, 1, '73308899', '五井拉麵(金門環島店)', '892', '', '金寧鄉環島西路二段46 號', 24.44174660, 118.31994370, '拉麵', '拉麵 真的超不好吃，我是訂熊貓，點了勁爆麻辣拉麵跟味增拉麵都好難吃...湯頭幾乎沒什麼用心就是鹹而已，還不如去買個蛋花湯加麵味道應該差不多，配上這個價位還以為自己在台北==有點雷 點了日式風味什麼拉麵，兩百多元，鹹到爆，吃不出什麼其他味道，包含蛋真的一個字鹹，重點還不便宜...距離金大很近~久久吃一次，沒有很特別，普通一般般老闆與老闆娘客氣 幽默有禮 料理也好吃 ： )價格普普\n小果蠅有點多，偶爾會有蒼蠅\n牛肉丼飯味道淡\n糖心蛋感覺有進出冰箱多次的感覺\n如果有個別需求可能要提比較多次（不加蔥…）\n位子較擁擠\n老闆較熱情', 3.8, 130, 494, 'l1ObcWlsA@gmail.com', '08 232 0889', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'http://www.umai.com.tw/', '0.jpg', '[0.jpg]', '2021-06-16 08.41.12 下午', '2021-06-16 08.41.12 下午', 130, 0);
INSERT INTO `Team4DB`.shop( `shop_id`, `member_id`, `shop_tax_id`, `shop_name`, `shop_zip_code`, `shop_city`, `shop_address`, `shop_latitude`, `shop_longitude`, `shop_description`, `shop_tag`, `shop_rating`, `shop_rating_count`, `shop_rating_total`, `shop_email`, `shop_phone`, `shop_price_level`, `shop_opening_time`, `shop_website`, `shop_main_img`, `shop_gallery`, `shop_create_time`, `shop_update_time`, `shop_total_view`, `shop_reserv_status` ) VALUES ( 1112, 1, '12311105', '麵屋無粹 MuIki', '89350', '', '金城鎮環島北路一段126號', 24.44321320, 118.32499010, '拉麵', '拉麵 好吃到想立即分享…\n名稱：鹽味雞白湯拉麵（日本口味）\n麵條：中卷麵，可詢問老闆依調整軟硬度.\n湯頭：濃郁的湯頭，比起在台灣吃的毫不遜色，而且還分日本/台灣兩種口味. 主要是針對台灣人不敢吃住鹹的而有所調整.很難得在外地有看到賣雞白湯的拉麵.\n心得：濕冷的金門，配上溫暖且好吃的拉麵；真的是很幸福. 拉麵彈且久泡不爛；叉燒肉雖薄但很夠味，鹹香繚繞在口腔許久；湯頭除了上述所說，還有其他配料,如黑木耳及紫色洋蔥可在濃郁的口感中帶入一股清甜.\n價格：180，算是便宜了.\n喜歡吃拉麵的可以試試看喔！金門難得的日本風味拉麵，內用座位區分成吧檯及四人坐椅，位子數量看起來足夠，另外店外就是停車場，提供顧客停放汽車及機車，開車來吃也方便；這一天點了鹽味雞白湯拉麵（大碗）、泡菜、唐揚炸雞、味玉蛋，先從拉麵說起，拉麵湯頭非常的濃郁，喝得出來是熬煮出來的，清甜好喝，但較於濃稠，麵體帶有Q勁，不會軟爛，另外拉麵未附糖心蛋，且桌上也未提供辣粉;泡菜則是黃金泡菜，非常好吃，會讓您一口接一口的口味，推；唐揚炸雞，一份總共有4塊，炸雞炸的外酥內嫩，鮮嫩多汁，但覺得肉體好像少了一味，不過還是好吃；另外友人點了豬叉燒丼及濃郁雞白湯（很單純的雞白湯，未有料），他說飯也蠻好吃的，湯野蠻好喝的；最後整體用餐體驗算蠻不錯的，餐點也好吃，雖然價格於金門在地物價比起來是貴了一點，但是使用真材實料，唯一建議可以改善的就是內部會有油煙味，如果能改善則更棒了真心大推！在金門可以吃到日式拉麵，辛咖哩沾麵非常好吃，咖哩真的優，唐揚炸物很不錯真材實料熬煮而成的濃郁湯頭\n值得一吃再吃飯便宜又快又好吃！\n麵類也不錯，湯頭口味稍重(個人是清淡派+貓舌)，所以比較常來吃飯XD', 4.4, 99, 436, 'qHRXLL@gmail.com', '0905 866 893', 4, '["星期一: 休息","星期二: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30","星期三: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30","星期四: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30","星期五: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30","星期六: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30","星期日: 11:30 \\u2013 14:00, 17:00 \\u2013 20:30"]', 'https://m.facebook.com/%E9%BA%B5%E5%B1%8B%E7%84%A1%E7%B2%B9-muiki-176728829529384/', '0.jpg', '[0.jpg]', '2021-06-16 08.41.13 下午', '2021-06-16 08.41.13 下午', 99, 0);
