CREATE SCHEMA Team4DB;

CREATE TABLE Team4DB.shop ( 
	`SHOP_ID`            int  NOT NULL  AUTO_INCREMENT  PRIMARY KEY,
	`MEMBER_ID`          int  NOT NULL    ,
	`SHOP_TAX_ID`        char(8)  NOT NULL    ,
	`SHOP_NAME`          varchar(200)  NOT NULL    ,
	`SHOP_ZIP_CODE`      char(6)  NOT NULL    ,
	`SHOP_CITY`          char(6)  NOT NULL    ,
	`SHOP_ADDRESS`       longtext  NOT NULL    ,
