	`SHOP_LONGITUDE`     decimal(11,8)  NOT NULL    ,
	`SHOP_DESCRIPTION`   longtext      ,
	`SHOP_TAG`           text      ,
	`SHOP_RATING`        decimal(2,1)  NOT NULL DEFAULT 0.0   ,
	`SHOP_RATING_COUNT`  int  NOT NULL DEFAULT 0   ,
	`SHOP_RATING_TOTAL`  int  NOT NULL DEFAULT 0   ,
	`SHOP_EMAIL`         varchar(100)      ,
	`SHOP_PHONE`         varchar(50)  NOT NULL    ,
	`SHOP_PRICE_LEVEL`   int      ,
	`SHOP_OPENING_TIME`  text      ,
	`SHOP_WEBSITE`       text      ,
	`SHOP_MAIN_IMG`      varchar(200)      ,
	`SHOP_GALLERY`       text      ,
	`SHOP_CREATE_TIME`   timestamp  NOT NULL DEFAULT CURRENT_TIMESTAMP   ,
	`SHOP_UPDATE_TIME`   timestamp  NOT NULL DEFAULT CURRENT_TIMESTAMP   ,
	`SHOP_TOTAL_VIEW`    int  NOT NULL DEFAULT 0   ,
	`SHOP_RESERV_STATUS` int  NOT NULL DEFAULT 0   ,
	CONSTRAINT `SHOP_TAX_ID_UNIQUE` UNIQUE ( `SHOP_TAX_ID` ) 
 );

CREATE INDEX `SHOP_MEMBER_ID_FK` ON Team4DB.shop ( `MEMBER_ID` );

