INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 27, 1, '54353922', '品方小吃店', '106', '台北市', '大安區大安路一段3號', 25.04491880, 121.54634640, '小吃', '小吃 門口的水果攤也是同位老闆經營，買了兩盒番茄三百，洗淨後一盒內將近一半都有發黑凹洞、不新鮮?，但另盒卻只出現約莫五六顆有此情形、番茄也挺甜。看了評價發現原來不是只有我這麼覺得...\n皮超級硬\n不知道是沒熟還是煎好再去加熱的？\n打電話去，店員斬釘截鐵說不可能沒熟，所以看來是.....不誇張我吃的皮就是硬到難以下嚥，最後換老闆娘來接電話，沒有想要當下補償，只說下次要再補五個給我...\n但我完全不想再吃下一次：/\n店員自己不知道有沒有吃過自己煎出來的鍋貼？今天點外送\n餛飩湯有加紫菜，可惜有股怪味，感覺是太油了一點第一次經過這家店，還以為是8方雲急？進來點了10個水餃，發現口味還真的不錯，後來再來時，發現他的牛肉麵是附近店家沒有的好吃，從此只要經過這裡，必定要點一碗牛肉麵來吃。每次到東區逛街 都一定來這家店 吃鍋貼再加一碗料多味美的酸辣湯 真的是一頓簡單快速又美味的美食 比去有m的速食店 好吃又營養多了', 4, 436, 1744, 'DS6AT5dV@gmail.com', '02 2775 5511', 1, '["星期一: 11:00 \\u2013 22:00","星期二: 11:00 \\u2013 22:00","星期三: 11:00 \\u2013 22:00","星期四: 11:00 \\u2013 22:00","星期五: 11:00 \\u2013 22:00","星期六: 休息","星期日: 11:00 \\u2013 22:00"]', null, '0.jpg', '[0.jpg]', '2021-06-08 01.40.47 上午', '2021-06-08 01.40.47 上午', 436, 0);
