INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 29, 1, '86276028', '美食家常小吃', '105', '台北市', '號1樓, No. 5-3南京東路三段335巷松山區', 25.05260120, 121.54749540, '小吃', '小吃 簡單乾淨的小店。老闆娘一個人照顧整個店面。選擇性還不算少。簡單午餐的地方光是原味麵線有獨特的香味，腿褲肉的份量不少，不過麵的份量偏少，建議加大，老闆娘親切推個巷弄裡的小吃店，阿姨蠻親切的，\n蛤蜊雞湯給的料也蠻多的，但湯有點溫，不是燙口的那種！\n麵線好吃，地瓜葉就是一般家常味！店面小小的，但乾淨整齊，控肉飯味道不錯但份量稍小，重質不重量的商家，好吃?真的好吃，老闆又漂亮，我愛死了', 4.1, 27, 111, 'uLoLRxBz@gmail.com', '02 2514 8681', 4, '["星期一: 12:00 \\u2013 19:30","星期二: 12:00 \\u2013 19:30","星期三: 12:00 \\u2013 19:30","星期四: 12:00 \\u2013 19:30","星期五: 12:00 \\u2013 19:30","星期六: 休息","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-08 01.40.52 上午', '2021-06-08 01.40.52 上午', 27, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 30, 1, '34136626', '飄香小吃', '100', '台北市', '中正區忠孝東路二段134巷26號', 25.04073110, 121.53165990, '小吃', '小吃 在週邊有不少以裝潢取勝的店家中，飄香小吃仍保樸實的舊式小店,會被吸引進來是菜單的與眾不同之處,紅燒獅子頭..烤魚下巴.豆豉排骨.咖哩.鯖魚飯，肉羹麵等等..\n口味算特殊不馬虎，但點來盤飯有個遺憾就是配菜是冷的，餐盤也很快就冷掉了，想吃熱騰騰的大概是麵湯羹之類的品項..可惜了!但還不錯！人員服務上是貼心的!滷肉飯跟獅子頭超讚∼原本要去的餐題大排長龍，就在附近找Google評價高的店就來到了這間，主食點了一個鵝肉麵跟一個廣州炒飯，小吃比較沒在拍照，所以沒有照片，味道來說兩個都很好吃，鵝肉很嫩、炒飯的味道很豐富，價格也實惠。\n下次有再去附近會考慮回訪。口味清淡、湯頭濃郁，湯品類溫度適宜。傳統小吃，不至於令人驚艷，但也是有聲有色不落俗套。\n\n咖哩飯有中等水準，不同於自助餐那種合成咖哩，是家庭式的濃稠醬汁，並充滿豐沛的洋蔥、紅蘿蔔、馬鈴薯。\n\n燙青菜不加任何的調味料，直接吃出青菜的原始風貌，建議在重口味餐點以後品嚐，會享受到人生的淡定感。\n\n米糕的米粒偏硬，味道偏辣，肉片偏柴，本想說這道餐點註定了下限，沒想到淋上米糕醬撒上辣菜脯之後享用，卻強勢扭轉我的味蕾一舉拔得今天的頭籌！！！\n\n整體環境整潔，服務態度親切，富有四星水準。', 4, 143, 572, 'ly3IX4YqXv@gmail.com', '02 2391 9828', 1, '["星期一: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期二: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期三: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期四: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期五: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 20:30","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-08 01.40.53 上午', '2021-06-08 01.40.53 上午', 143, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 31, 1, '21430384', '快樂小吃', '106', '台北市', '大安區106', 25.04268390, 121.53978440, '小吃', '小吃 炒飯的肉絲有夠少的大一吃完氣到現在都快畢業了還忘不了那個味道炒麵炒辣第一次看過用辣豆瓣醬，麵鹹到無法入口 舌頭超麻 無言炒麵炒辣竟然用豆瓣辣椒醬，鹹到無法入口 舌頭超麻 不推薦炒飯是粒粒分明\n但味道不夠 不夠鹹\n份量也少', 2.9, 33, 96, 'RJkGLd@gmail.com', '02-69953545', 4, '["星期一: 11:30 \\u2013 20:00","星期二: 11:30 \\u2013 20:00","星期三: 11:30 \\u2013 20:00","星期四: 11:30 \\u2013 20:00","星期五: 11:30 \\u2013 20:00","星期六: 11:30 \\u2013 20:00","星期日: 休息"]', null, '0.jpg', '[0.jpg]', '2021-06-08 01.40.54 上午', '2021-06-08 01.40.54 上午', 33, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 32, 1, '92405071', '鄞家食堂/中山美食/台北餐廳/台北便當/台北美食/台北小吃/台北restaurants', '104', '台北市', '中山區龍江路318巷18號1樓', 25.06469540, 121.54013400, '小吃', '小吃 2021、4月29日下午拜訪鄞ㄧㄣˊ家食堂，八德店點了，臭豆腐麵?燙青菜加滷肉飯，我真實?我的用餐感想，1-滷肉飯真的好吃?入口的想法就是能吃多快就多快，還要追加一碗，燙青菜?也非常新鮮爽口但是臭豆腐麵就百聞不如一吃，真的很不好?吃為什麼1-麵很硬、湯就冷、豆腐就免強可以，不是傳?中的神奇。我的人是就事論事好吃?我會幫忙拉客人來吃，不好吃我也不怕得罪店家，因為好跟不好吃進嘴巴奡N會分勝負了，相信店家自己很清楚，我還是給4顆星因為滷肉飯的感覺真的太完美?湯青菜這二種就值得70分，所以我還是會推薦大家要親自來吃看看是不是像我?的這樣呢。沒有到不好吃大概是三顆星\n但因為4.9有點過譽所以給兩顆星\n實際上吃起來會和期待的有些出入\n燙高麗菜是切絲的 滷肉飯的滷肉很像米糕的口味卻淋在白飯上 鮮魚湯大概是這個套餐裡面比較能接受的吧，但是也不是典型的鮮魚湯，湯裡有枸杞可能想凸顯甘味但卻讓人覺得不夠清爽味道很好，但店家要關了\n\n剛好遇到店家最後一天營業，之後只做便當太可惜\n\n配菜很特別，不是隨便放一些豆芽菜的打發，未來可以去本店吃食材優、份量足，對得起價格，僅口感稍鹹了些。\n可惜2021年4月底就要熄燈，之後僅提供便當外送，若要內用，得回鄞家小館。「鄞ㄧㄣˊ家小館」健康美味的家常料理\n無意間網路搜尋八德路附近的餐廳\n驚見居然有4.9顆星店家\n那就二話不說 一定要去吃吃看\n看著網路評論和店家貼心的照片菜單\n兩人用餐 每一樣都想試試看\n但眼睛大肚子小\n不能浪費食物 就只好先選擇最想吃的∼\n梅干扣肉飯套餐+煎魚+滷臭豆腐?\n\n「梅干肉」入味不油膩 肉質適中 一點也不乾柴\n本日套餐青菜「高麗菜」切絲的高麗菜川燙後加上蒜蓉醬 有淡淡的蒜味配上高麗菜本身的清甜 很不錯?\n本日套餐湯品「蘿蔔湯」一樣清甜爽口 好喝\n「滷臭豆腐」小小兩塊大大滿足 滷得入味 加上一點花椒提味 就是讚?\n「煎魚」雖不認識是什麼魚，魚肉鮮嫩，灑上檸檬汁，再加點芥末胡椒，也是很對味?', 4.9, 1399, 6855, '85Gll7F@gmail.com', '02 2507 6000', 4, '["星期一: 09:00 \\u2013 14:00","星期二: 09:00 \\u2013 14:00","星期三: 09:00 \\u2013 14:00","星期四: 09:00 \\u2013 14:00","星期五: 09:00 \\u2013 14:00","星期六: 休息","星期日: 休息"]', 'https://www.yinhomefood.com/', '0.jpg', '[0.jpg]', '2021-06-08 01.40.55 上午', '2021-06-08 01.40.55 上午', 1399, 0);
