INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 66, 1, '81454172', '極禾優質鍋物(農安店)台北火鍋/中山區美食/中山區火鍋/中山區鍋物/行天宮美食/行天宮餐廳/行天宮火鍋', '10482', '台北市', '中山區農安街168號', 25.06455830, 121.53439860, '火鍋', '火鍋 昨天跟妹妹們開開心心吃她們強推的火鍋店，我們兩個單點蛤蠣一個是套餐堶惘陬蟤(老闆還說現在蛤蠣正貴的時候，我們點了很聰明)，聽說這家蛤蠣很大顆，應該不會差吧！所以點了！\n\n但最近天氣很熱，蛤蠣本身的鮮度如果店家保存不好，一定都會死掉臭掉(重點：蛤蠣都開開臭掉了）\n\n昨天我們有一個妹妹先煮了吃了，蛤蠣都臭掉，決定「要退不要換新的」（因為我自己也知道應該都問題，我吃了也臭掉了。十顆蛤蠣中，只有一兩顆是好的，全臭掉！表示這店家沒有新鮮保存好，每顆都有問題。）\n\n店家竟然說「現在蛤蠣一盤有十顆有臭的很正常…不給退也不能換，告訴我們退了他們也丟掉，所以很抱歉不能退款也不能換新的。」我們所有人聽了也傻眼…自己摸摸鼻子就算了，但這樣的服務態度，下次我們也不會期待再次吃了。\n\n所以大家要來請慎選，如果想吃就別點海鮮，可是如果海鮮食材都不好，我難以想像其他食材都是新鮮！超級好吃的火鍋！！真心不騙！！\n老闆長得又帥~ 食材都很新鮮.\n尤其是活蝦小卷 還有極禾的牛肉\n真的有夠好吃.湯頭也很多可選擇\n首推胡椒海鮮.牛奶鍋和藥膳都很棒\n人間美味.推推推非假日的中午來用餐，客人不多，環境舒適，服務人員親切有禮，海鮮鍋大碗滿意（蝦子是活跳跳的），若有來行天宮拜拜，可以來，應該不會失望！番茄鍋+牛奶鍋的湯好喝！\n\n雖然店員很想離職，但服務還是100分！原味湯頭很像玉米排骨湯 喝的出來用心\n肉品算還可以 蛤蜊非常新鮮', 4.3, 990, 4257, 's9kN0f@gmail.com', '02 2516 6062', 2, '["星期一: 休息","星期二: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30","星期三: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30","星期四: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30","星期五: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30","星期六: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30","星期日: 11:30 \\u2013 14:00, 17:30 \\u2013 20:30"]', 'https://www.jiheshabu.com/', '0.jpg', '[0.jpg]', '2021-06-08 01.41.51 上午', '2021-06-08 01.41.51 上午', 990, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 67, 1, '50573368', '瀧厚鍋物 台北敦北店?高級肉品火鍋專門店?', '10574', '台北市', '松山區民生東路四段56巷1弄1號', 25.05759000, 121.55171100, '火鍋', '火鍋 今天給一顆星是針對外帶丼飯的部份，先前很常吃瀧厚的火鍋，覺得他們火鍋食材很新鮮、肉品質也不錯，服務品質好像因為人員的更換變異很大。因為疫情期間很多餐飲業者滿辛苦的，看到瀧厚傳來的外帶丼飯想說去捧場一下，前一天就先預訂隔天12點30分要去店內自取(我們是訂3個丼飯)，進去店裡前面只有一個臨時的路過客點了一個丼飯，聽到店員跟她說要等5~10分鐘。輪到我們時，我們跟店員說有預訂三個，12點半自取的。店員只說了一句好，等一下~人就進去廚房了，接下來我們拿到丼飯時已經是12:54分了。我不懂我們已經先預訂了，為什麼現場還要等這麼久，期間甚至還聽到他們在切肉片的聲音，覺得那我們預訂的意義到底何在？店員也只有很敷衍的說了幾句不好意思，等候的過程中，問了一次：請問還要等多久？第一次來說再10分鐘，第二次說再2分鐘。等到整個怒火中燒。本來看到櫃檯的宅配牛肉麵組合還想訂購回家，整個也打了退堂鼓。奉勸要訂購這家外帶丼飯的人，請作好因為現切現煮所以會等到天長地久的心理準備。by the way，外帶丼飯會上層會有不熟的蛋一顆、兩條玉米筍、兩片青江菜、一隻蝦、牛肉片大概5~7片左右，下層則是白飯拌牛油、醬油、一點蔥花，平心而論大概有70~80分左右的評價，但等太久，店員的態度實在讓人不敢恭維。以上和牛還可以，雪花牛肥就算了（目測肥瘦6/4）非常腥。\n蔬菜裡的玉米筍是冷凍的！實在無法理解蔬菜不是都新鮮的嗎？為何要冷凍？\n櫃檯寫開兩鍋打卡送蝦，詢問服務人員說送完了，結帳時看到立牌再次詢問也是說賣完了，那為何還要續放也是令人不解?或許是挑人給嗎？空間寬敞，裝潢溫馨舒適，餐點也非常棒。\n上週五遇到一位戴眼鏡的服務人員，印象叫"阿澤"，非常親切熱心，從一開始上桌介紹環境到點餐－餐點送上後加湯、收盤服務周到！火鍋食材也非常新鮮，當天是點安格斯無骨牛小排~涮幾秒即可入口~油花分佈細緻，口感柔嫩真的很好吃。對了~最後甜點波士頓蛋糕也超好吃，希望蛋糕也能額外加購。肉的品質不錯 而且份量都很大 吃得飽\n餐後還有附一塊波士頓派 是好吃的火鍋店\n\n但是要避開用餐尖峰時刻 店裡很小 動線不順暢 記得要訂位喔?現在兩人同行打卡送白蝦\n上菜速度蠻快的\n湯頭有石頭火鍋跟昆布鍋可以選擇\n這次點的是 板腱牛\n量蠻多的唷\n肉片也切蠻厚的∼', 4.5, 462, 2079, 'O3hOoP@gmail.com', '02 2718 8805', 2, '["星期一: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期六: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00","星期日: 11:30 \\u2013 14:30, 17:00 \\u2013 22:00"]', 'https://may1215may.pixnet.net/blog/post/404012543', '0.jpg', '[0.jpg]', '2021-06-08 01.41.52 上午', '2021-06-08 01.41.52 上午', 462, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 68, 1, '58511109', 'Sanzo三藏和牛火鍋 ???????門店-和牛火鍋 現流海鮮 鍋物 涮涮鍋 高CP值火鍋 嚴選牛肉 家庭聚餐 餐廳推薦', '106', '台北市', '大安區忠孝東路三段251巷8弄2號', 25.04293100, 121.54151760, '火鍋', '火鍋 很好肉質 ！服務親切！?不便宜但是肉真的非常優質，服務也很好！CP值高,海鮮新鮮,和牛軟嫩入口即化,服務態度佳,環境舒適乾淨,價格平實 位置便利 忠孝復興一號出口出來後走一下就到了五顆星好店大大推薦！下次還會再帶家人去用餐週五晚上臨時訂位，現場發現整晚只有我們與另一組客人，以這價位來說沒有甚麼特別值得推薦\n只有一個服務生，訂位兩大一小，到餐廳孩子大聲喊著口渴想喝水，我就跟服務生要了水，卻只給一杯！？第一次遇到只給一杯水的...\n\n湯頭沒有選擇，就一個高湯\n最傻眼的是點了M9澳洲和牛龍蝦雙人套餐，都點完才說澳洲和牛沒有了會升級其他牛肉，選擇已經很少了還有缺，沒有澳洲和牛後來我們就選日本和牛\n\n雙人菜盤普通，份量普通，貢丸跟蛋餃比較特別\n推薦可以單點草蝦280，六隻且都有剝殼，也比較大隻一點點價位：600∼1500（有服務費）｜美味：★★★★☆｜CP值：★★★★｜服務：★★★｜環境：★★★★（內有詳細\n\n·肉類算是非常的優秀，推薦點雙人套餐會非常的划算，因為還會有加上海鮮，但還是有點偏小貴\n\n·豬肉部份我覺得表現起來還是比某家以豬為主打的火鍋來的差一些，不管是肉香味還是口感，而牛肉部分吃起來，就是與其他涮涮鍋類的高價肉類，其實差距並沒有到令人驚艷，\n\n·醬汁非常的美味，而菜盤我覺得CP值都非常高，特別有印象的不是肉，反而是貢丸，玉米筍，還有蛋餃，另外這家的白飯也是出乎意料的好吃，算是在台北吃過最棒的白飯\n\n·服務部份還算普通，因為其實店家算小，客人也不多，這樣的價位卻感覺沒有太有溫度的服務\n\n·不太需要訂位，環境整體還不錯，廁所是狹長型的', 4.6, 366, 1684, 'YJ2a9F@gmail.com', '02 2740 7710', 4, '["星期一: 休息","星期二: 休息","星期三: 17:00 \\u2013 22:00","星期四: 17:00 \\u2013 22:00","星期五: 17:00 \\u2013 22:00","星期六: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期日: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00"]', 'https://zh-tw.facebook.com/pages/category/Nabe-Restaurant/Sanzo%E4%B8%89%E8%97%8F%E5%92%8C%E7%89%9B%E7%81%AB%E9%8D%8B-%E3%81%97%E3%82%83%E3%81%B6%E3%81%97%E3%82%83%E3%81%B6%E5%B0%82%E9%96%80%E5%BA%97-182200059090615/', '0.jpg', '[0.jpg]', '2021-06-08 01.41.52 上午', '2021-06-08 01.41.52 上午', 366, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 69, 1, '17965574', '忻殿堂火鍋 Xin HOT POT', '106', '台北市', '大安區敦化南路一段160巷12號', 25.04344040, 121.54792200, '火鍋', '火鍋 回訪好多次了！\n老闆熱情老闆娘可愛都是愛貓人士\n東西真的很好吃所以很喜歡：）\n但真的\n店家很用心\n一直進步\n有服務越來越好的的趨勢！！！\n每次來都很開心??\n\n這次來我懷孕\n越南弟弟很貼心的給了我護腰的枕頭\n也很耐心的給我建議聽我點餐！\n\n我所有的朋友被我推薦來這家吃都非常滿意\n希望這間店開長長久久?\n老闆老闆娘身體健康\n希望我還能帶我孩子跟一堆朋友來來\n\n- - -\n\n很好吃 上次跟姐妹吃\n服務好\n海鮮超新鮮\n蝦漿也很好吃\n非常喜歡東西好吃但是服務有待加強。安排的座位在冷氣正下方，吃到一半冷氣開始一直滴水，直接滴到小朋友的碗內。反應了以後除了幫我們擦乾，沒有道歉也沒有任何處置。過程中共有3位服務生與我們交談，都是年輕，外國口音的服務生。小朋友碗內食物被冷氣水滴到後，把碗交給其中一位請他幫忙清理，拿走了以後折回來問我說，是要用水沖一下嗎？讓我很無言。感覺是訓練不足，也不想為難他們。結帳時再次詢問，那個冷氣以前就有滴過水嗎？對方回有時候外面下雨會滴水。如果是這樣根本不應該在下方安排桌位啊…也不是非常平價的火鍋，整體經驗蠻糟的。補充:\n後來老闆有解釋因為店員是越南留學生,中文不佳講錯了.\n肉片厚薄不同是因為非重組肉,肉片本身有弧度切出來才會變那樣.\n雖然我在其他火鍋店裡點牛小排是沒遇過這種情形,\n不過老闆態度還滿誠懇的,所以姑且信之.\n\n今天點了招牌的叻沙口味,前面半小時吃起來的確不錯,\n但是後面就越煮越鹹,即使中間加了兩次白開水,\n最後青菜和粥還是鹹得吃不下去.\n(感覺還不如點原味就好,不過這樣好像就沒有來這家吃的必要)\n\n另外店家的服務流程感覺也還有改善的空間.\n今天初訪一進門沒看到外場有工作人員(只有滿裡面有一個在埋頭掃地),\n門口也沒有候位的地方(櫃檯也不在門口),\n在後面等了好幾分鐘,\n完全不知道應該自己走進去找人還是默默地待在門口直到被發現.\n\n等到有人來帶位,\n被安排到吧檯的位置,\n還沒點餐桌上就已經有兩鍋湯了.\n(一般餐廳都只會預擺碗筷,還沒遇過預擺湯底的,\n感覺有點詭異,又不是自助吧,\n煮好的湯不加蓋直接擺在客席,\n座位靠近門口偶爾又會有小果蠅路過......\n雖然沒有嚴重到讓人覺得有食安問題\n就是有一種難以言喻的違和感)\n\n*****************************\n建議用餐前先拍照存證，今天因為肚子餓沒拍，涮肉的時候覺得有點不對勁，前面五分之四肉片都切得很薄，後五分之一明顯厚蠻多的。本來以為只是店員刀工不佳而已，我有點忘記自己是點小羔羊還是羊小排，就有問店員，店員說是小羔羊。結果結帳時發現算的是羊小排的錢，因為只差20元就懶得和店員分辯，不過有點懷疑同一盤裡有兩種不同的肉。超級誇張！！沒位置坐各人鍋用卡斯爐就算了，\n坐下才發現湯底不是叻沙，問了才說只剩下一鍋，\n根本沒有給我們選擇。\n我一個月吃好幾次，就是喜歡叻沙才會來吃，\n說是湯底要預訂...........？老實說鍋的味道真的不錯?海鮮肉品也都水準之上！反而小點不用特別點，蝦仁漿有點失望！但最後的雜炊超棒！但不論如何有老鼠爬過朋友腳下真的讓我傻眼??雖然店家道歉解釋不過這真的陰影啊', 4.8, 2142, 10282, 'CD2pRKZ@gmail.com', '02 8771 3936', 2, '["星期一: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期二: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期三: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期四: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期五: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期六: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00","星期日: 12:00 \\u2013 14:30, 17:40 \\u2013 23:00"]', 'https://www.facebook.com/XinDianTang', '0.jpg', '[0.jpg]', '2021-06-08 01.41.54 上午', '2021-06-08 01.41.54 上午', 2142, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 70, 1, '81026159', '和牛涮 日式鍋物放題 台北忠孝東店', '106', '台北市', '大安區忠孝東路四段128號2樓', 25.04130700, 121.54799020, '火鍋', '火鍋 ※或許是制度跟生意把熱情逐漸澆熄的一家王品旗下品牌(是我期待過高了嗎??)\n◎前情提要：因為工作及家庭兼顧的關係(大夜班). 本人與太座分居已達2年(小孩晚上太座顧.這堶t責白天接送跟臨時生病之照料). 又因排假的關係無法同"一般人"可每周帶家人出去走走甚至一起聚個餐. 此次為了慶祝太座生日(12/1)&結婚紀念日(11/30). 在網路爬文許久後. 決定於選擇此間餐廳在11/19提前慶祝.\n◎訂位：OK..沒接觸過多. 很幸運的可以訂位到(聽說超難訂)\n◎帶位：備註錯誤(誤認我有交代要跟太座分開兩桌用膳).\n用餐時間：1.5小時坦白說真的太短(感謝現場人員有多給點時間讓我最後拚個甜點) / 現場人員其實也知道1.5小時很趕. 所以在點餐當下這堛磼有可能會多加人(太座還在確認友人是否會到)以及決定是哪項套餐. 工作人員都不斷貼心"提醒"時間在倒數中.\n◎食物：品質不賴. 配餐也很讚.還有很多不常見的青菜. 但是炙燒壽司居然是冷的(聽說很難排.而且都現點現做). 餐期末經過炙燒檯還發現有幾顆壽司是已經先被烤好放著.與期待有些落差.\n◎活動：真的頗多. 多到有點難記得. 很多東西要自己提及才會有納入折扣或是計點等.  唯一"主動提及"的是請人填寫意見卡送蛤蜊(隔壁桌被問起時 還是已經用餐完才被問. 意義已經不大了) . 另一個主動問起的是在結帳時被問到是否有壽星.(這埵^答是來慶祝結婚紀念日的. 得到的回應是"喔..那沒有優惠喔"←我也知道.但是回應讓人有些傻眼. 因為是很難得才可以成行的小小約會.不想說將心比心. 只能說或許是這家店的"制度"讓員工無法自由發揮.也呼應了一開始所說的.也許是生意太好+制度 讓員工的熱忱也被澆熄了..也或許是我自己的期待太高. 簡單開心地回應句"恭喜"之類的話都沒得到.自己難免情緒有受到影響) / 話說有個吃光光活動. 跟太座很努力的拚完了. 只是沒被問到. 應該也沒計點了吧.\n◎看到很多回覆發現回應倒是挺制式的，真的會去查清楚細節再回覆嗎？還是一樣“你好 我是XXX+職位 配上簡單回應+請自行跟我們聯繫好敘述細節”；相關內容其實已經敘述的挺詳細的啦。食物好吃~~\n肉質優\n少數吃到飽料用很好的9/10初訪王品集團首個吃到飽火鍋餐廳"和牛涮"，兩人各點一份718元頂級和牛套餐與498元安格斯牛套餐，分類評比如下。\n\n1.湯頭?: 干貝昆布湯、一番牛骨湯。喝起來直接連想到聚的口味，雖然同集團不意外，只是似乎比印象中聚的口味更死鹹些，導致在吃到飽策略上很不利，死鹹湯頭任何東西涮久了都容易膩，建議90分鐘用餐期間需穿插副食、甜點與飲料解油膩。\n\n2.肉類?: 和牛霜降與雪花口感個人覺得沒想像優，反倒是炙燒和牛壽司口感頗有驚艷，以718元價位來說個人覺得CP值還好，雖號稱和牛並不覺得實際吃起來口感有勝過安格斯牛多少，倒不如吃498元套餐就很划算。\n\n3.鍋物自助吧?: 菜類、火鍋料新鮮乾淨，食材種類眾多，搭配乾冰視覺效果，仙氣十足。\n\n4.副食甜點飲料吧?: 傳聞的肉燥飯與咖哩飯的確厲害，亮點靜岡抹茶與北海道牛奶霜淇淋令人滿意，跟黑糖珍珠絕配。\n\n5.服務: 服務人員無止盡忙進忙出，餐點解說和桌邊服務稍嫌急促，也還保有一定品質與水準。\n\n整體而言，和牛涮算一個不錯的品牌火鍋，但個人不會推薦吃到和牛的價位，除非湯頭鹹度會再做調整，或者能提供更多元選擇，像是牛奶,番茄,麻辣。好吃，不貴，適合好友聚聚整體服務就是糟糕??還王品集團的！？\n*我從頭到尾我都沒有去刁難任何服務員*\n*因為做餐飲業真的很辛苦，我又不是奧客*\n\n1、電話訂位打好幾通沒人接！只好去現場登記候位，登記後告知需等1.5小時，好∼為了吃我等???過40分鐘店家來店確認快要有位子了，是否會來用餐，因朋友不在身旁，需要與他們商討是否前往！告知店員說1分鐘後會回電會不會前往！一分鐘過去?我回電打了7通，沒人接，還是一樣，為了吃，自己再去門店告知我們會用餐，到了跟店員說我剛剛有登記過了，也接到你們電話說有位子，店員可能太習慣說詞：剛剛登記大約還要再等1小時？？？滿滿問號\n\n2、總於可入座了∼服務生詢問是否第一次用餐，我是∼∼∼只介紹價位、如何點餐、使用服務鈴????點完了！看到旁邊桌子有人拿著其他蔬菜、附餐，於是又問了服務生說醬料、蔬菜怎麼點或怎麼拿取！卻一臉不耐煩的說：阿就那邊拿啊（手指著方向），我還是微笑點頭跟他說謝謝∼他們很忙很辛苦！\n\n3、隔壁桌的客人不小心打翻東西（雖然不關我的事，只是看看服務生怎麼處理）\n我卻看到服務生對著那桌客人「翻白眼」∼哇\n還好不是我們打翻∼∼不然我這桌白眼應該更多吧！\n\n4、（我最氣的來了）90分鐘到了，服務生來告知我們時間到了∼順口問一下你們有什麼活動或信用卡優惠的嗎？服務生：現在都沒有唷。我：好的，謝謝辛苦了。\n於是我們就去櫃檯排隊買單，排隊時有2位店員分批往我們隊伍中用力擠過去！？ 一位也就算了∼還兩位勒！（阿是不會說借過嗎？）\n\n買單前再問了買單人員有什麼活動或優惠嗎？此時他才說可以下載什麼在登陸什麼可以折抵500元！看來員工訓練非常不足？下載在登錄需要很久時間，後面又大排長龍，那就算了吧?\n\n嗯???這是我第一次難忘的感受！\n這種價位就能吃和牛吃到飽∼！\n所以給他唯一的一顆星?', 4.8, 15047, 72226, 'BVh0ohBZ@gmail.com', '02 2751 1759', 2, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', 'https://www.wagyushabu.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.00 上午', '2021-06-08 01.42.00 上午', 15047, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 71, 1, '56831378', '竹苑shabu', '106', '台北市', '大安區大安路一段31巷31號', 25.04410180, 121.54753490, '火鍋', '火鍋 服務親切，食物又好吃? 推推?服務餐點品質皆滿意\n星彤服務好環境優雅\n雖然感覺服務生幾乎好像是新進人員\n但每位都算有禮貌\n不懂的也會幫忙詢問\n食材新鮮\n可圈可點\n吃過許多高級火鍋\n竹苑算是用心的\n我不當奧客 因為本身也是餐飲業\n不是付錢就老大\n有與一位感覺稍為資深的人員聊天\n很愉快也知道食材的用心\n有機會會再次造訪\n店家不吝嗇 下次可以參觀他們的廚房\n代表這間店隨時準備好願意讓客人參觀\n就算一位新人不小心把茶弄翻了\n後續處理事宜\n也讓我們滿意\n很棒的一間店\n人情味 讓我願意再次光臨海鮮超級新鮮，活體秀?裡面海鮮非常新鮮好吃！\n值得大家來品嚐！', 4.8, 1581, 7589, 'BTnJePDK@gmail.com', '02 2771 3377', 4, '["星期一: 17:30 \\u2013 00:00","星期二: 17:30 \\u2013 00:00","星期三: 17:30 \\u2013 00:00","星期四: 17:30 \\u2013 00:00","星期五: 17:30 \\u2013 00:00","星期六: 11:00 \\u2013 14:30, 17:30 \\u2013 00:00","星期日: 11:00 \\u2013 14:30, 17:30 \\u2013 00:00"]', 'https://www.facebook.com/chikuenshabu/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.01 上午', '2021-06-08 01.42.01 上午', 1581, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 72, 1, '89025922', '樂香小火鍋中山總店', '10479', '台北市', '中山區合江街103號', 25.05971100, 121.53930200, '火鍋', '火鍋 外送點原味梅花豬肉鍋159元，肉大概只有三、四片，而且有股不新鮮的臭味，整體的量少、味道差、食材品質不佳，不推！uber了小火鍋，把主食換成王子麵，但送來的是整包沒煮的，是要我當科學麵吃膩公司外送的火鍋愛店\n造訪內用的時候也覺得不錯，滷肉飯、冬粉可以無限吃cp值高\n店內有爆米花機及冰淇淋\n特別推薦牛奶鍋，目前吃過最讚的小火鍋牛奶鍋冠軍?飲料機出水口一堆污垢，ㄧ按飲料污垢都流到飲料裡，肉眼即可見，衛生堪慮。價位平價藥膳鍋加肉 195\n味道不錯 還有一顆蛋\n可口可樂機無限續\n聖提亞的冰無限挖\n白飯冬粉無限拿\n\n食物部分都很ok\n就是店員真的蠻冷淡的\n連面無表情的說聲謝謝都沒有', 3.9, 235, 917, 'Gn6sfNLmk@gmail.com', '0908 667 103', 4, '["星期一: 11:00 \\u2013 22:15","星期二: 11:00 \\u2013 22:15","星期三: 11:00 \\u2013 22:15","星期四: 11:00 \\u2013 22:15","星期五: 11:00 \\u2013 22:15","星期六: 11:00 \\u2013 22:15","星期日: 11:00 \\u2013 22:15"]', 'http://g.page/Lexianghotpot', '0.jpg', '[0.jpg]', '2021-06-08 01.42.01 上午', '2021-06-08 01.42.01 上午', 235, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 73, 1, '96339716', '武鶴和牛火鍋(吉林)--台北推薦火鍋店|美食推薦|海鮮火鍋|和牛火鍋店|首推火鍋店|豪華火鍋推薦', '104', '台北市', '中山區吉林路228號', 25.05919100, 121.53004020, '火鍋', '火鍋 伊比利豬很美味！\n環境乾淨、服務親切！先來說說環境\n滿乾淨的\n我們座位安排在二樓\n(樓梯過道要小心，比較矮)\n洗手間維護的很乾爽\n(我是個寧願憋尿回公司或者回家上廁所的人)\n座位間距還滿寬敞的\n再來是食材\n這裡是社團社友七嘴八舌推薦，褒貶不一\n今天邀約朋友聚會結論?好極了\n原本朋友不習慣吃牛肉\n所以我們才選了7百多的吃到飽\n(想說沒人陪我們嗑和牛)\n海鮮新鮮極了\n草蝦紮實又大尾\n小卷生蠔很鮮嫩\n四人份上菜一點也不小氣\n牛豬肉片切得厚實油花漂亮\n雞肉魚肉煮出來咬湯汁鮮甜\n*小插曲的驚喜*\n我們四個本來安排在六人桌\n來了一組帶小孩的客人\n我們就順勢讓了改坐四人桌(其實桌子也夠大)\n店家非常客氣招呼我們招待一份和牛\n朋友嚐了小驚艷到\n(他後悔沒一起吃牛肉了?)\n二樓三個漂亮妹妹店員辛苦了\n今天被我們這桌神豬客人累壞了\n謝謝店家，也麻煩妳們了\n我們久沒重逢的朋友聚會話就是多\n聊到沒注意妳們營業時間^__^"..\n下次再訪時我們會多加注意??\n謝謝招待??和牛超新鮮欸！蜂蜜冰淇淋也很好吃！還送小朋友飲料喝揪感心和牛火鍋，物超所值，和牛搭配店家醬料實在好吃，值得前往。', 4.6, 777, 3574, 'DQBRjjxY9h@gmail.com', '02 2581 8066', 2, '["星期一: 17:00 \\u2013 23:00","星期二: 17:00 \\u2013 23:00","星期三: 17:00 \\u2013 23:00","星期四: 17:00 \\u2013 23:00","星期五: 17:00 \\u2013 23:00","星期六: 11:30 \\u2013 15:00, 17:00 \\u2013 23:00","星期日: 11:30 \\u2013 15:00, 17:00 \\u2013 23:00"]', 'https://www.facebook.com/pg/wuhe228/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.02 上午', '2021-06-08 01.42.02 上午', 777, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 74, 1, '37840118', '火鍋世家新民生店', '104', '台北市', '中山區民生東路二段75號', 25.05811590, 121.53041590, '火鍋', '火鍋 菜料與肉很多，超大厚肉片，要分2口才吃得下，很讚喔∼\n個人最喜歡那芋頭，綿、Q、香∼\n還有杜老爺冰淇淋∼\n看起來應該是重新裝潢新開張，乾淨衛生明亮∼照片上的餐點是300元，超值得的∼新開幕的，新裝潢，乾淨，平價好吃，剝皮辣椒鍋量辣椒給好多，好好吃！肉好吃、海鮮新鮮都慢超值！\n有付飲料、冰淇淋、甜點！\n附近最好的的一間！重新開幕就來去嚐鮮，果然沒讓我失望，料多味美，環境更是乾淨，強烈推薦！環境佳.服務好.海鮮青.肉夠多.價格實?老闆娘人親切，食材新鮮又好吃！', 4.2, 476, 1999, 'nccqqxGqw@gmail.com', '02 2523 9322', 2, '["星期一: 11:30 \\u2013 20:00","星期二: 11:30 \\u2013 20:00","星期三: 11:30 \\u2013 20:00","星期四: 11:30 \\u2013 20:00","星期五: 11:30 \\u2013 20:00","星期六: 11:30 \\u2013 20:00","星期日: 11:30 \\u2013 20:00"]', 'https://www.facebook.com/pages/%E7%81%AB%E9%8D%8B%E4%B8%96%E5%AE%B6-%E6%B0%91%E7%94%9F%E5%BA%97/337071249737049', '0.jpg', '[0.jpg]', '2021-06-08 01.42.03 上午', '2021-06-08 01.42.03 上午', 476, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 75, 1, '32377601', '輕鬆煮— 雙主餐 |中山區火鍋|平價火鍋|行天宮捷運站', '104', '台北市', '中山區民生東路二段101號', 25.05807430, 121.53133480, '火鍋', '火鍋 1. 肉雙拼，但牛肉三片+豬肉6片，258+10%價格便宜所以尚可接受\n2. 有些火鍋料跟送的魚都不新鮮，感覺冷凍很久，送上時還結凍要等退冰\n3. 週年慶活動打卡送低脂雞肉片（不知道真的付錢點的是不是也是這種肉）是最扯的，共六片，放約10分鐘後肉是整個夾不起來的，完全碎裂一夾就化開= =\n詢問店員說為什麼這個肉一下子就化開\n店員剛開始還說所有肉放久了都會這樣（？？？\n我回他可是只有雞肉會欸\n他就說他去問問\n說別家店不會這樣化開是因為他們有用其他添加物讓肉凝聚在一起\n?\n4. 甜點好像是紅茶奶凍，我覺得唯一還不錯\n\n總結：湯頭普普（跟其他火鍋店的差不多）、肉中下（肉質差也不一定新鮮 ，但花錢買的肉還算可以cp值仍低）、店員服務態度尚可，唯獨狡辯每種肉都會這樣碎掉讓我傻眼而已（是說收清潔費不是服務費啦所以也不需要什麼服務態度）、環境不錯\n但不會再去也非常不推?雙主餐很特別，我點雙牛\n超大盤子上面貼了6片牛肉薄片（比面膜還薄）\n還有飛來飛去小蟲蟲（繼續看完）\n朋友點了雞肉鴨胸\n夾起來就散掉的肉你跟我說是組合肉我也會生氣\n哪裡組合？煮下湯變雜碎湯啊∼\n\n要先結帳\n但第一次遇到店家收了服務費\n還要?\n自己加火鍋湯\n自己裝醬料\n自己去櫃檯點餐\n自己裝飲料\n甚至要裝飲料時，整台被店員拿走收起來，也沒收到：「飲料機要清洗囉！要裝趕快裝」之類的提醒（看隔壁桌準備裝飲料也找不到飲料機的樣子，酷∼）\n後面口渴只能喝湯喔！\n離開店員一句謝謝光臨也沒有\n所以你說，我的服務費付給誰了？\n\n往下滑其他2000多則優質評論哪裡來\n原來是五星評論打卡送啊！?????評分4.9實在有失公允，如果今天評分3.5~4來吃這家，我可能還不會有種受騙上當的感覺，我們看google評論來選擇店家就是相信其他客人的經驗，店家會提供配得上4.9分的品質與服務，可是你們自己憑良心想想看你們有嗎？只能說你們很會做行銷，送東西讓客人幫你們留下好評，\n實際上肉量偏少一夾就散，品質不敢恭維，收10%服務費可是連加湯都要客人自己動手到醬料台取用，店員只負責點餐上菜，點錯單上錯菜還要客人自己去櫃檯處理，怎麼不是你們該負責呢？自助飲料也是，還在營業中就提早半小時收起來洗，也沒有留一壺飲料讓客人取用耶！雙主菜+送的海鮮吃完也沒有滿足的感覺啊……\n結論是如果要花300多吃火鍋，我不會再選擇這家，時間會還評分一個公道東西好吃，有送小甜點，但有個男店員口罩戴一半，酒精瓶都用扔的，收餐盤時也很粗魯、丟很大聲，在旁邊很怕會被湯汁噴到，不過其他店員都很親切正常。一般的火鍋店，肉量偏少\n特色是可以選兩種肉組合，\n另外會附小甜點（當天是奶酪）\n不過環境算是蠻乾淨的，醬料區有在維護，\n用電扇吹，所以沒有小蟲。\n假日人不多，如果趕時間可以來用餐。\n當天服務員很好，幫忙安排比較舒適的位置 ?\n底下附上當天的肉跟菜量供參考', 4.9, 2791, 13676, 'pUrrVrtt84@gmail.com', '02 2523 5059', 2, '["星期一: 11:00 \\u2013 20:00","星期二: 11:00 \\u2013 20:00","星期三: 11:00 \\u2013 20:00","星期四: 11:00 \\u2013 20:00","星期五: 11:00 \\u2013 20:00","星期六: 11:00 \\u2013 20:00","星期日: 11:00 \\u2013 20:00"]', 'https://www.foodpanda.com.tw/restaurant/e8sz/s1', '0.jpg', '[0.jpg]', '2021-06-08 01.42.04 上午', '2021-06-08 01.42.04 上午', 2791, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 76, 1, '89104949', '石石鍋創', '105', '台北市', '松山區敦化北路155巷12號', 25.05441200, 121.55090800, '火鍋', '火鍋 香味十足，特色鍋物！大推！整體氛圍棒、服務很親切、份量足夠店內環境整潔，食材新鮮，服務人員態度積極良好簡單，晚餐人不多。份量吃起來剛好好鄰居?每次來都吃超級飽?', 4.8, 1457, 6994, 'H7SPTh@gmail.com', '02 2716 8228', 2, '["星期一: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期二: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期三: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期四: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期五: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期六: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00","星期日: 12:00 \\u2013 15:00, 17:00 \\u2013 22:00"]', 'https://www.facebook.com/ShiShiHotPot/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.04 上午', '2021-06-08 01.42.04 上午', 1457, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 77, 1, '17985813', '川之流龍江店—肉品海鮮任你選 | 中山區火鍋 | 龍江路餐廳 | 濕式熟成牛肉 | 附自助吧檯 | 甜品櫃 | 火鍋', '104', '台北市', '中山區龍江路277號', 25.05970590, 121.54088800, '火鍋', '火鍋 價位普通食材也普通部要期待太高平日的超值套餐也好吃不馬虎?服務態度也很優整體而言都蠻好的，\n上菜速度較慢\n台式麻辣湯頭有點像麻辣鴨血豆腐湯頭，\n肉質非常好吃，\n推薦霸王牛、老饕牛\n老饕牛肉質瘦肉帶點油花\n自助吧餐點選擇很多，\n飲料機設備選擇，冷熱都有\n冰淇淋明治的，冰箱溫度稍顯不夠冰，\n挖的時候偏軟\n甜點選擇從冰地瓜、蛋糕、布丁、豆花、泡芙到愛玉都有，非常多選擇\n\n加湯底的部分\n有的是加原湯，有看到番茄湯是加番茄原湯\n台式麻辣反而加一般高湯，\n有跟服務生確認過\n如果能夠加原湯會更好\n\n店內有活動超過699可以搖骰子\n還好可以外帶???\n不然太晚拿都吃飽了\n\n店內還有集點活動\n辦會員卡可以9折（免服務費）\n\n肉質不錯，會再來造訪超推這間CP值很高的火鍋店，肉質高檔和價格很合理，平民消費頂級的體驗，重點是老闆帥氣，老闆娘甜美，超級推薦，靠近行天宮及松江南京，每次來幾乎都吃超飽，甜點和飲料、冰淇淋無限使用，大推！！！服務親切', 4.3, 292, 1256, 'Lm67LZ26To@gmail.com', '02 2502 8333', 2, '', 'https://hot-pot-restaurant-319.business.site/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.05 上午', '2021-06-08 01.42.05 上午', 292, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 78, 1, '57106040', '雅室牛排-仁愛圓環店', '106', '台北市', '大安區仁愛路四段105巷1號', 25.03866160, 121.54985790, '牛排', '牛排 牛排讚 甜點讚 商業午餐的性價比都快突破天際了！一流的服務加上美味餐點，超值。廚房連牛排熟度都不能確認。\n\n#仁愛圓環將近30年的牛排館\n#服務員不可思議的解釋\n\n點餐\n6盎司三分熟\n8盎司五分熟\n切開第一刀看到的是?\n6盎司七分熟\n8盎司三分熟\n反應給服務員後的回應是?\n遲疑五秒後端起8盎司盤子先到入口櫃檯、再去後方吧台、前後大概瞎晃了3分鐘。\n另一位服務員再次回到桌邊說要去確認一下。(著實不知道他要確認什麼、而且另外一盤沒人理會。)\n再次回來說明因六盎司的肉比較小塊所以3分熟看起來會像5分熟、八盎司因為大一點所以5分熟看起來會像3分熟?\n是把來吃飯的客人當白癡、\n還是這個餐廳有嚴重潛在問題呢？\n\n這麼不誠實的餐廳還能存活多久呢？\n\n#熟度是國際認定的OK三餐來雅室牛排的體驗都非常好。\n商業午餐的部分非常划算，口味也不會因為是商業午餐有所馬虎。\n一般午餐/晚餐的部分，價格相比其他牛排餐廳算是中價位，但肉的品質調味都不輸給其他高價的餐廳。\n且每次遇到的服務人員都非常親切，無論是櫃台人員或是桌邊服務人員都展現高品質的服務態度！（在這邊特別想感謝3/25中午時的服務人員Sandy以及櫃台人員（可惜沒有記到名字），讓媽媽第一次在雅室用餐就有個愉快的經驗）\n總結而已，是一間值得大力推薦及無限回訪的餐廳吧。水資一人80沒提前告知，看到帳單才知道感覺很差，點餐人員在幫我們點餐的中途竟然去跟其他服務生交代別桌的事情非常不專業，我話說到一半他轉頭去跟別人說話？！明顯沒尊重我們\n點了4人老饕龍蝦分享餐，因為對oz份量不熟悉，問了點餐人員是否4人都會有一份牛排然後配2隻龍蝦，點餐人員回覆對，結果來了牛排分切下來一人是一條?傻眼至極，如果一開始有告知牛排一人份量這麼少就不會點分享餐，然後有在問卷上寫上此事，人員打來瞭解，我跟他說明他的回覆竟然是上面有註明16oz了，好像每個人都應該知道分切下來一人是一條??照片上的兩條就是分切下來一人的份量\n我們6大1小（小孩5歲）用餐，大人點餐完跟我說本來12歲以下小孩都是500元低消，但是就算了（就是這個口氣告訴我）叫我點200元的東西就好了，我也吃過無數餐廳，第一次被要求一定要幫孩子點餐，加上他們的餐點不是非常平價的價格，有必要非要5歲孩子點餐嗎？有跟來電人員反應，他卻回覆我，他們的客人是都可以接受？！請問我都坐下去點好餐你才告訴我我能說我不接受嗎？請問別人接受代表我一定要接受嗎？如果訂位前有先告知感覺不會這麼差?況且6個大人的餐點已經超過15000了，有差那200元嗎？吃過很多偏高價位的牛排還是第一次遇到這種要求然後這種服務態度\n最後甜點還差一人沒上竟然叫人來問我要結帳了嗎？哈囉，你甜點沒上給我我怎麼結帳？？？總之用餐經驗非常差，絕對不會再去第二次，然後來電人員不知道是打來幹嘛的，大概是打來要跟我吵架的，回覆的話非常不專業，最後就告訴我會向上反應，已經讓人不想再去第二次，反應有用嗎?傻眼至極\n看了大多評論，高分的都是商業午餐居多，好像吃正價排餐的像冤大頭，這個價位沒得到相對的品質，那是否直接更改為平價牛排更好?\n媽媽生日特地去幫媽媽過生日，結果整個搞砸了', 4.2, 2157, 9059, 'y8Jd44hw@gmail.com', '02 2775 3011', 3, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00"]', 'https://steakinn-renai.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-08 01.42.19 上午', '2021-06-08 01.42.19 上午', 2157, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 79, 1, '14750516', '總督西餐廳', '106', '台北市', '大安區忠孝東路四段104號?2樓', 25.04136900, 121.54665300, '牛排', '牛排 打電話去訂餐時，感覺接電話的人員口氣有點不耐煩，第一印象不是很好??\n?\n餐前飲料有柳橙汁跟梅子醋可以選，我們就各點了一份。?\n?\n之前看評價提到沙拉吧很厲害，實際品嚐後，最讓人驚艷的是帶有松露香氣的義大利麵，不是印象中沙拉吧會出現的∼光沙拉吧就可以吃蠻飽的！\n?\n總督的洋蔥湯據說也很有名，果然名不虛傳！香氣十足，口感滑順，平常不太吃洋蔥的人也會喜歡∼\n?\n??香煎比目魚加明蝦 $900\n?\n服務人員推薦我們點牛排，看到我們沒點牛很納悶? 因為友人不吃牛，我們就點海鮮跟豬排。?\n?魚排很嫩，蝦也很鮮甜∼?\n?\n??鐵板嫩豬排 $680\n?\n豬排份量比預期少，外面煎到焦焦脆脆的，裡面很軟嫩，這間的豬肉真的很好吃！?\n?\n飲料是一人一杯(非自助式)，我們選擇的是檸檬紅茶。?\n甜點類都是自取，有乳酪蛋糕、波士頓派、巧克力蛋糕、布丁…等經典甜點。?\n餐後甜點還有一人一球冰淇淋??\n?\n年紀較長的服務生比較親切，比較年輕的客人動作感覺比較直接(?)。還沒打烊時，服務人員就開始收拾水果跟沙拉吧，沒有事先告知客人。?\n?\n用餐的客人幾乎都是家庭聚餐，或是一群50歲以上的朋友聚會。?\n看Google地圖評價，這間好像是很多人家庭聚餐、全家人慶生時會吃的餐廳。?\n?\n我們兩人一開始是被安排在四人桌，用餐到尾聲時，有一大群客人進來，服務人員想要併中間的長桌，詢問我們之後幫我們換到窗邊的位置，結帳時主動幫我們打了95折左右！?\n?\n訂位時告知壽星會送蛋糕，不過因為沙拉吧已經有蛋糕了，我們也吃蠻飽，當天就沒特別詢問這部分。?\n?\nIG:dorisbaseball環境整潔，餐點美味\n自助吧的部份也有一定水準\n價位較高，但以用餐體驗來說\n還算不錯每次上下班經過，我都會多看一眼這間餐廳。since 1978，這不剛好是我的出生年嗎？！我也依稀記得，我很小的時候，大約5歲時，台灣正開始到處流行興起西式點心及餐廳，例如麵包店和牛排館、鐵板燒等。尤其是有歷史的東西，才是最吸引我的原因了。說什麼也要找機會來試試。話說前陣子每天加班，讓老婆一個人顧孩子，我真的過意不去，年節後剛好招待老婆來，順便試試這間我夢想很久的西餐。\n入口進來的裝潢果然有年紀，接待人員也是資深人員，都很親切。上座後點了餐，我和老婆就開始沙拉吧品嚐。我們發現總督的沙拉吧種類不多，但是都好吃。這真的很重要，就像朋友不用多，知心的一個就好。\n本來主攻我最愛的馬鈴薯泥，然後確發現這裡竟然有芋泥。好吧，沒吃過我來試試。結果一吃驚為天人！也太好吃了。來回幾次我吃了4球，連我都不敢相信。再來呢，試試排餐，我點了去骨牛小排，沾醬是薑絲調味醬油，看似不和，其實很搭，這也是特色之一呀，至少我沒遇過。牛排熟度很準確，肉嫩甜味夠，美國牛就是我的第一選擇。因為之前職業的關係，每天切澳洲牛及日本牛。所以每個部位及特色都算熟悉，也發現餐廳有不浪費的好習慣。因為師傅會將原料肉精修下來的肉，發揮巧思來招待客人，真的很棒喔。甜點屬布丁較優。下次來再吃芋泥，我期待。在固守過往的同時慢慢的向下沉淪。\n隨著時間老去必然是人乃至物會發生的事情，但在老去的同時進行修繕、增進自我，事物就不會被時間拋在後頭。這些事物同時有著新的潮流同時又保有過去的歷史與風格，我們讚許著這些種種。\n依靠著經歷過輝煌的客人，不思進取的守著過去，連一般的咖啡店都會盡心盡力的讓店內環境的品質維持一致，這間呢？感謝某些員工在點餐、俐落的倒水與收拾等等規矩維持的很好，但同時的一間起跳價位在7、800以上的店面使用的杯子居然還是IKEA的產品，門面都不顧及，還有那退冰不全的海綿蛋糕，看到許許多多品質高低不一。\n\n餐點，也沒什麼讓人留下印象的地方。\n\n令人惋惜。老西餐廳要不就很好，要不就很雷。在這個價位裡有許多新興的美式牛排可以選擇，但老餐廳能屹立不搖，自有其原因。\n本日是家庭餐，共點4份主餐。\n\n1.湯品：海鮮、南瓜、洋蔥湯都濃郁好喝\n2.沙拉吧：生菜新鮮，搭配一些台式小菜，適合蔬菜類需求高的長輩\n3.麵包：還不錯，缺點是沒有奶油或是橄欖油醋的選擇\n4.飲品：梅子醋有點發酵酒味，不難喝但恐怕有些人不習慣。柳橙汁一般\n5.主菜：牛排熟度剛好，搭配醬汁或胡椒鹽，搭配的蔬菜是標準的蘿蔔球、花椰菜跟洋蔥。 羊排香氣夠，但近骨頭處略有血色，可考慮改進。 魚排跟明蝦火侯剛好，水份保持的好，表現不凡。\n6.甜點吧：看似很平淡無奇，實則樣樣驚喜好吃。\n7.冰淇淋：招待的，牛奶香味重，灑上一些細鹽，極為好吃\n8.水果：西瓜不甜，鳳梨與柳丁甜\n9.飲料：咖啡與茶，還OK\n10.服務：收桌上餐盤速度很快，經驗老道，應對得體。\n\n價位上比占美跟小統一略便宜，加上在忠孝東路2F，靠窗位置有街上夜景，CP值不錯。', 4.3, 951, 4089, '6QQN0V@gmail.com', '02 2752 3981', 2, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 22:00","星期六: 11:30 \\u2013 22:00","星期日: 11:30 \\u2013 22:00"]', 'https://www.facebook.com/viceory/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.20 上午', '2021-06-08 01.42.20 上午', 951, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 81, 1, '85680324', '紅屋牛排館-忠孝店', '106', '台北市', '大安區忠孝東路四段223巷9號', 25.04222220, 121.55277780, '牛排', '牛排 家族企業, 東西從小吃到大, 好吃！老牌牛排館，親切服務好好ㄔ\n中餐cp值很高\n服務也很好老字號的牛排餐館，服務人員很有老派的服務風格，很溫馨熱情。\n餐點也很好吃，這次點了主廚特餐，是臺式的鐵板牛排，但是肉的熟度也控制得不錯！明蝦跟鱈魚也都好吃。\n啊另外法式麵包烤得脆脆的不會很硬，很難得遇到XD。\n可惜因為開車沒辦法點酒，不然相較起來餐後飲料就比較沒有特色。\n整體來說，果然老字號還是有它的價值呀！疑 我不是有寫過嗎 被刪掉惹?？\n從小吃到大 不變的老味道∼\n\n【ig: jacquelyn1030】', 4.4, 1638, 7207, 'OpzfnV4X@gmail.com', '02 2751 7675', 2, '["星期一: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期二: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期三: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期四: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期五: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 22:30"]', 'https://www.facebook.com/homesteak1981/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.21 上午', '2021-06-08 01.42.21 上午', 1638, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 82, 1, '65149897', '星辰牛排', '106', '台北市', '大安區光復南路180巷16號', 25.04265080, 121.55674030, '牛排', '牛排 今天下午訂了星辰牛排推出的防疫外帶，買一送一方案。（我真的是便宜心態去享用）(6:30取餐)\n\n取餐時 結果餐點做錯、湯也忘了給，結果回去拿少給的湯時，裡面還有不知道是主管還是誰沒戴口罩。我只希望可以要這樣推出方案請拿出有這樣價值的服務，再來是牛排還是要現點現吃。(因為還收了服務費)\n\n1.舒肥牛排?好吃\n(準時去取餐還是冷冷的但還可以接受)\n2.肋眼牛排5分熟\n(可能也是冷了所以不太好咬不推薦外帶吃這個)\n3.濃湯外帶只給小杯碗的一半是真的滿精緻的\n(很好喝也喝的飽只是看著小小的碗裝半杯就是視覺少少的)\n4.菜單裡面的飲料外帶只能帶美式跟紅茶\n(所以買一送一菜單裡的任何飲料都不用看)\n5.甜點只有燕麥紫米粥or瑪德蓮巧克力口味\n(其他菜單裡寫的甜點直接說沒有辦法外帶)\n6.員工有帶口罩但是裡面那位先生沒戴口罩！？\n(疫情期間拜託請您要營業請帶好)前菜-圓舞曲跟主餐舒肥牛排非常美味！\n然而其他附餐表現平平，有點可惜...\n另外比較需要改善的是，服務人員在跟顧客點餐/流程及說明上的邏輯 應該要再順一下：詢問透過EZ table訂位是否有相關優惠，點餐人員回答可招待牛舌一份，且不可與壽星招待併行，事後上了點餐單後便無任何說明（點菜單上寫了招待布朗尼），再之後便上了餐後甜點，為釐清差異點便詢問服務人員，結果回答很不清楚~感覺到她的混亂，後續又再問另一位人員，雖然清楚說明上菜流程，但針對點菜單上的差異仍無法解釋差異性，之後沒多久又有另一位服務人員靠桌告知用餐時間已到，但我們甜點後的飲品及預定之招待都還沒上...感覺蠻糟糕的；而原先點菜單上的布朗尼也突然被換成一塊瑪德蓮。其實招待項目被更換也沒有關係，但如果一開始就知道沒有布朗尼，那給單時應該就要直接說明，還有沒有牛舌這件事也是；而服務人員本應清楚掌握客戶的上菜狀態，但卻沒有掌握到，這也是很基本卻不該發生的的失誤。\n\n除了食物，服務要是能更周全會更好，希望店家能再加強訓練今天來到一家西餐廳，店名叫星辰牛排，聽起來感覺會是一間很夢幻、氣氛不錯的地方。\n抵達地點時，用深藍色打造的店面外觀有吸引到我，進門時，服務人員的接待，到安排入座，整體感覺及氣氛相當不錯，空間及視覺也都很好。\n菜單的設計風格也與店家有對應到，菜單內容較簡單化，配合一些圖片及幾行說明的文字，讓人可以大致上了解菜色的味道會是如何，在請服務人員作介紹時，我覺得沒有得到我所想知道的，也介紹的不是那麼完整，於是點了一個自己也蠻愛吃起司的熔岩起司牛排。\n等待主餐上菜之前，依序有前菜-沙拉、湯品-羅宋湯、香蒜麵包，先暫時墊墊胃，沙拉和外面一般差不多，特殊的芝麻醬聞起來很香，令我忍不住多嚐了兩三口，湯品的部分味道比較沒有想像中的那麼濃厚，和其他羅宋湯相比，是稍微清淡且沒有太多香料的味道，香蒜麵包就是普通，沒有什麼太特別之處。\n一段時間，主餐來了，視覺上眼睛有先被吸引到，非常期待口感會是如何！！\n自己本身是愛吃肉的人，牛排更是抵擋不住。第一刀下去時，發現起司並不是我所想的那種樣子，我以為會是融化在牛排上，切下去有牽絲的感覺，但並不是這樣，起司焗烤的鋪在牛排上，彷彿是兩層東西疊上去而已，口感上面不會難咬，很軟嫩，先吃了一口原味再吃一口沾了旁邊的黑胡椒醬，兩者風味都很好吃，原味吃的出牛肉的新鮮及甜味，沾了醬的不僅沒有搶走肉本身的風味，反而還帶出另一種層次的味道，每一口都是很棒的享受，除了起司的部分以外。\n主食吃完就來到甜點-黑芝麻奶酪，味道不會太甜，口感方面也很滑順，芝麻的香氣沒有很重，不會搶過奶酪本身的風味，還蠻解膩的！\n用餐時，我也不時在觀察周遭環境，發現天花板的著色令我相當驚訝，雖然不太影響整體視覺，但使用偏深藍色的，坐太久會有種壓迫感，有點太昏暗的感覺，另外廁所的部分，挺加分的是，有放置女性用品可以使用，以備女生的不時之需，這點是目前很少餐廳會這麼做，整體也乾淨清新，還蠻令人印象深刻的！肉很軟嫩 服務態度佳平價卻不馬虎的牛排館！??\n生日送和牛蛋糕，可能來不及退冰，煎完之後冷凍味太重，不太喜歡\n\n從湯到沙拉一切都很讚\n?湯的部分：\n我們點了南瓜湯和牛肉湯，南瓜湯味道濃郁，但我覺得有加些太白粉，我不喜歡那種勾芡感；牛肉湯很讚！蕃茄味調得剛剛好！甘甜好喝\n\n?沙拉：是胡麻醬口味，沙拉種類豐富有許多不常見的菜類\n\n?牛排：我們點了12盎司美國菲力和16盎司舒肥牛\n，菲力牛排非常軟嫩！嫩到切了就倒∼有點切不斷，但細嚼有種牛肉香氣！\n舒肥牛雖是使用低溫舒肥，但表皮有煎過，香氣重、酥脆好吃，和菲力比較起來較有咬勁\n（這邊建議刀子不要用夜市的那種牛排刀，希望用鋒利一點的刀子∼）\n\n比較起來我覺得舒肥牛略勝一籌！\n\n?飲品：選擇較少，花茶和檸檬汁不錯，\n花茶是玫瑰洛神口味，檸檬汁酸到爆炸！愛喝酸的可以試試，都是好喝的\n\n?甜點：首先奶酪奶味濃郁細緻，但是！裝在這種容器裡真滴超滅火！看起來超廉價..??\n冰淇淋香草口味還不錯，旁邊附一個愛心巧克力裡面是一樣的冰淇淋\n（店堨洫唹夾S有販售了，改成瑪德蓮）\n甜點的部分我覺得普通擺盤可以再精緻一點\n\n總結：服務不錯，店內聊天聲大但不影響，cp值極高！物超所值！會再回訪！', 4.3, 1760, 7568, 'SU9Ho0V@gmail.com', '02 2721 7512', 2, '["星期一: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期二: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期三: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期四: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期五: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期六: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 14:30, 17:30 \\u2013 22:00"]', 'https://newstarsteak.business.site/?utm_source=gmb&utm_medium=referral', '0.jpg', '[0.jpg]', '2021-06-08 01.42.22 上午', '2021-06-08 01.42.22 上午', 1760, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 83, 1, '42946893', 'N°168 PRIME', '106', '台北市', '大安區敦化南路1段246號7樓', 25.04046770, 121.54835400, '牛排', '牛排 以前到現場吃過不少次，算是愛店之一，後來近一年多沒去了這次看有做外帶餐點的優惠活動就訂購了，店家要求不能電話預訂要網路訂購且先付款以便查驗，結果家人訂好餐去領回來後，店家整整少給一份2人套餐，甚至連說好會放袋子裡的發票也沒有給，18:30聯繫他們少給東西，結果補做餐點到補送到我們家的時間已經接近20:00，結果發票還是沒給！毀了客人晚餐的經驗也沒有任何賠償。\n\n最誇張的是外帶盒是錫箔紙盒加透明塑膠蓋，熱湯用跟飲料杯一樣的紙杯裝，真的非常非常沒有質感，訂過其他家相對高檔次餐廳的外帶，沒看過像168一樣低檔次的包裝。\n\n甜點載過來都東倒西歪，餐點份量少的可憐，訂一個四人餐+戰斧豬排總共也得要4、5000元，整體餐點份量和品質我倒覺得1500元內有找。\n\n要做外帶卻沒有給客人相應品質的餐點，人員的專業度也不足，真的讓人非常失望。我只能說，疫情和緩後我也不會再給這家餐廳任何機會。\n\n牛排好吃的很多家，真的不用花錢在這家身上。一家餐廳的品質都是從細節透露的，這家從出包到補償都看不出任何誠意和專業。母親節當天來用餐，有送母親節的乾燥小花+拍立得一張。用餐需搭電梯至頂樓，電扶梯是沒有到的。\n服務生有碰到態度良好無微不至的服務，但同時也有沒什麼笑容，講話語氣不太專業的服務生，希望能維持一定水平會更好。\n餐點部分都是套餐式的，價格不會太貴，也不會因為特別節日亂加價，值得嘉許。\n當天麵包都是蔓越莓麵包，但個人比較愛傳統的法式麵棍+奶油，比較有香氣跟麵包的韌度，希望下次會有這個選擇。\n前菜部分表現OK，沒有太令人驚豔的菜色或手法，但是是好吃的。\n洋蔥湯的話洋蔥絲好像有一點少，感覺可以再濃郁一點。\n主菜老饕牛排的話煎得剛剛好，不會有血水滲出，口感有嚼勁，但有沒有筋，不會太油，口味極佳。\n甜點部分表現一般，當天是主廚甜點水蜜桃果凍，但給的是叉子，滿不好食用的，應該用湯匙比較適合。\n店內裝潢舒適典雅，桌距滿足夠的，保有隱私。初體驗該業者自主外送，按規則於11點網頁點選訂單，店家電話通知12:30出餐。爾後於13:00來電通知因送餐地址太近，配合外送不接單。引述業者說法：1. 延誤半小時出餐餐點已經在保溫。2. 請我們繼續等候外送業者接單，之後再送。綜上，因無法接受保溫至少半小時以上餐點，去電取消本訂單，業者隨後更改說法：待外送接單後才開始準備餐點（那11點預定的用意是？），很遺憾的還是取消訂單。相當完整的負面評價體驗。在敦化5樓按摩完後必須搭電梯才能到7樓餐廳（手扶梯無法到唷）\n環境優雅舒適、訂位時有告知懷孕中，\n服務人員貼心幫我先準備了靠枕?\n\n每道菜出餐速度比一般餐廳稍微慢些，\n我看別桌客人們也幾乎都是桌上空空在等餐，\n所以比較適合不趕時間的人去吃唷∼\n\n餐點擺盤精緻、每道都好吃?\n尤其甜點（還有招待的甜點忘記拍）味道都不會太甜！?\n\n另外餐廳的廁所很寬敞漂亮，\n比樓下各樓層的廁所高級太多?選擇情人節來嚐鮮，沒想到沒有出特別的餐點就是一般的中午四道式料理。?哈哈?\n\n我們點了\n《緬因龍蝦?》\n服務人員建議9分熟，吃起來還好。 雖然沒有因為過熟而太老的口感但肉質沒有很扎實的感覺。\n\n《老饕牛排?》\n好吃?、又香噴噴\n\n麵包的?醬吃起來很焦糖風，讓人想要一直?一直??（?◇?）\n《主廚季節創作開胃小點》\n外面是用竹碳包裹著，裡面有飯拌蟹湯？蟹肉？\n\n《南瓜?奶油濃湯?》\n是將料盤上桌後再現場淋上湯的，可以看到盤中的南瓜?、青蘋果?、還有一層透明的裙子?，也是青蘋果?口味的哦！ 最上方?的是芥末奶油，淋上之後一起\n入口的氣味很有層次！\n\n《經典例湯》\n就是蘑菇濃湯?，最上方?還撒著蘑菇粉很夠味。\n\n開胃菜 是《野菇塔》\n草菇做基底，配上酥皮塔皮當底最上面那層亮晶晶?的是豬背油；搭上芝麻葉松露奶油醬，讚?！\n\n主廚甜點?《黑芝麻布朗尼》\n烤的無敵好吃?，外表完美無瑕叉下去後芝麻醬爆出來啦∼ 旁邊的草莓?搭配著鮮奶油吃起來還好，沒想到跟芝麻布朗尼卻意外的搭配！！！\n\n《青蘋果?》\n由於它頭上撒滿肉桂粉DD就直接pass ~\n\n附餐飲料我們選了熱紅茶??，原來以為搭配甜點會需要這來解膩，沒想到完全派不上用場?肚皮也爆了！\n\n總結一下，節慶、慶祝?會想再來這邊！\n\n* 情人節活動有幫客人拍照?（拍立得留念）', 4.4, 749, 3296, 'CMamYzk2J@gmail.com', '02 6617 6168', 4, '["星期一: 11:30 \\u2013 15:00, 18:00 \\u2013 21:30","星期二: 11:30 \\u2013 15:00, 18:00 \\u2013 21:30","星期三: 11:30 \\u2013 15:00, 18:00 \\u2013 21:30","星期四: 11:30 \\u2013 15:00, 18:00 \\u2013 21:30","星期五: 11:30 \\u2013 15:00, 18:00 \\u2013 22:00","星期六: 11:30 \\u2013 15:00, 17:30 \\u2013 22:00","星期日: 11:30 \\u2013 15:00, 17:30 \\u2013 21:30"]', 'http://www.168prime.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.23 上午', '2021-06-08 01.42.23 上午', 749, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 84, 1, '27309121', '御書園食尚牛排', '10491', '台北市', '中山區松江路206號2樓', 25.05776110, 121.53280550, '牛排', '牛排 Ok price with ok quality steak.  Poor service with super slow serving.  If you are not hungry before walking into the restaurant,  I am SURE you WILL be by the time they serve you food.\n\n點餐時問侍者牛排部位她說是板腱過一會兒回來才改口說是沙朗､問她果汁是還原或是現榨果汁，她說都不是卻也無法回答果汁調理方式...(這輩子從來沒這麼想知道那果汁是怎麼弄的！)\n\n另一位上牛排後要我們先切開看熟度滿不滿意，我說我先拍一下照，照還沒拍她就一聲不響離開了。(不是要我們看熟度嗎？)\n一開始還沒什麼客人就點完餐了，餐點卻是等整間餐廳快坐滿客人才跟比我們晚到很久的客人一起上...why??? 且餐前麵包先上不是最基本的嗎？\n\n整間餐廳員工很多，也看得出想營造一種專業有品質的氛圍，但少部份員工若能有適當的職前訓練再上陣才不會拖垮更大部份積極熱情的員工。\n\nP. S. 洋?湯好喝､前菜好吃但分量超少､牛排筋太多，一整套吃下來男生可能會吃不飽。從小吃到大，一直在跟上時代的老牌西餐廳！人多在包箱可以開懷暢談，聚餐的好場所。第一次用餐，\n餐點以這價格來說不錯所以多給一星，不過甜點很糟，提拉米蘇是假的。\n塑膠水杯的刮痕都多到杯子都變霧面了該換了吧。\n7/28晚上用餐時間一直有兩隻果蠅在旁邊飛非常煩人。\n上菜慢，甜點漏上還要客人主動提醒，5:30入席吃完都8點了，收盤子倒是非常勤快，盤內剩一塊肉就馬上來說幫您整理唷~我總共說了三次這個還要用。\n員工普遍態度不佳，離席結帳時從座位走到櫃台的沿途經過5.6位服務員眼神交會都只會直盯盯的看著你一句謝謝光臨也不會說，那我寧願你眼神趕快移開我還感覺比較舒服。\n櫃檯買單時只是從背包裡翻找錢包花了點時間，服務員就露出非常不耐煩的表情，我後面可沒人排隊等結帳。\n若哪天覺得浪費服務費沒關係可能會再光顧。慶生去這家，以前去過蠻多次的\n但這次特別讓人失望，餐點份量越來越小不提，還有和menu長得完全不一樣的\n（沙拉在照片上看起來至少有幾片葉子，實體拿到卻只有一株小小裝飾用的）\n全部料理乃至整個套餐都極度缺乏蔬菜，完全不注重飲食種類均衡性\n就連素食套餐看起來也乏善可陳\n最讓人不滿的是等餐時間超久，一個小時只上了前菜、沙拉和湯\n就算是來客數尖峰時段，是否也應加派廚房人手以消化客人數量？更何況其實我去的那天根本沒有客滿', 4.1, 2489, 10205, '9KaF2zi8aA@gmail.com', '02 2567 5688', 2, '["星期一: 11:30 \\u2013 22:00","星期二: 11:30 \\u2013 22:00","星期三: 11:30 \\u2013 22:00","星期四: 11:30 \\u2013 22:00","星期五: 11:30 \\u2013 22:00","星期六: 11:30 \\u2013 22:00","星期日: 11:30 \\u2013 22:00"]', 'http://www.rgarden.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.24 上午', '2021-06-08 01.42.24 上午', 2489, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 85, 1, '99333860', '史坦利美式牛排 敦化店', '106', '台北市', '大安區敦化南路一段233巷22號', 25.04026350, 121.55056230, '牛排', '牛排 4/24下午前往用餐，為我們點餐的人員為小綠，點餐時點了一份雞蛋沙拉時蔬堡以及舒肥炭烤美國雪花牛排，在點牛排時小綠店員詢問是要16oz還是8oz，明確地表示要8oz，又詢問了牛排的熟度，因為有小孩所以選擇了全熟，爾後店員說那可以挑一杯飲料，而我選了一杯蘋果汁去冰，店員則說都不會加冰塊，以上就是點餐時的對話。接著另名男店員上來了一碗湯及蘋果汁，我當下疑惑並表示我們沒有點湯，這名店員回頭訊問，得到的答案為我們有點套餐，所以有湯跟飲料，以上這些對話讓人理所當然的以為8oz的牛排餐就是有包含湯跟飲料，我們就安心的喝著湯及飲料。過一陣子牛排來了，上餐時店員說著16oz全熟的雪花牛，我當下聽到表示我是點8oz的全熟雪花牛，上餐店員再次去詢問，得到的答案是在點餐時有推薦我點16oz的雪花牛，而我聽到後選擇了16oz的雪花牛，聽到這個答案我雖然心中覺得疑惑但想說算了，餐點都已準備好就吃吧。這件事讓我的感覺真的很不好，因為我並沒有說我要16oz的雪花牛，這讓我覺得我被強迫推銷。雖然餐點不錯，但因為這次的消費經驗讓我完全不會再踏入貴店。\n在上餐時已經反映了2次，為何點餐人員不親自過來說明而是透過上餐店員呢？\n如對以上內容有不同的想法，您們可調閱店內監視器觀看內容以確認以上是否屬實。\n還煩請貴公司在點餐方面可教育員工於點完餐後複述一遍點餐內容，可以讓雙方都確認點的餐是否有誤，也讓點錯餐的機率降低。食物很美味，但點七分熟來三分熟爆血的牛排ㄧ切開爆血就被嚇到....服務人員用無言的表情把餐送回廚房，又把配菜另外裝盤送回桌上，然後才另外把牛排另外裝盤送餐，但失去整體視覺的美味。很像來吃自助餐...\n男服務生也沒表示任何歉意....連牛排刀都沒給還要自己舉手跟女服務生拿，雖然很好吃，但令人失望的服務大過食物美味的印象會讓人覺得扣分。點了凱薩沙拉，小點水牛城辣雞翅，主餐特意點了人氣舒肥美式碳烤雪花牛五分熟，另外點了墨西哥香料烤半雞，主餐配菜皆可以7選2。\n加價超值B套餐，有主廚湯+飲料+甜點。\n店員介紹說雪花牛吃起來比較嫩，實際吃覺得可能自己口味偏好熟成牛肉的香及口感，所以入口感想普通，8盎司以他們價格來說，或許很多人會覺得CP值高，但以肉質口味來說，沒有吸引到我，且我這塊牛排，有一條筋阻礙我的每一口咀嚼口感。\n烤半雞卻令我喜愛，骨頭處烤的香酥，肉質不柴，且帶有淡淡甜味，搭配香料烤起來很好吃。\n凱薩沙拉醬料調的不錯，水牛城辣雞翅是很酸很甜很辣，很像韓式炸雞，不像我印象中的水牛城辣雞翅。\n甜點熔岩巧克力好吃，但擺盤很直白，跟菜單上美味的擺盤照差很大，一上來有點錯愕，還確認了一次，店員說一樣呀，只是盤子不一樣。\n飲料選有糖檸檬紅茶，好喝不過份甜，配餐點很剛好。\n以價格及地點來說，餐點很實惠，如果再訪，應該是為了烤雞。服務好，牛排好吃，薯條很不錯。價格又很實惠。推！\n唯一小缺點，因為經常去吃，但是發現點五分熟，大概都只有三分熟，有點常常需要回烤，請廚房師傅多留意，謝謝！食材新鮮，加點套餐每道都還蠻好吃，服務人員態度也很好\n可能是星期六太忙，2份點5分熟的牛排，吃起來都7-8分熟\n另1份點7分熟的牛排，反而像5分熟，肉心較生，血水很多', 4.4, 1269, 5584, '6XFglk@gmail.com', '02 8773 0036', 2, '["星期一: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期二: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期三: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期四: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期五: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期六: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00","星期日: 11:00 \\u2013 14:00, 17:00 \\u2013 21:00"]', 'https://www.facebook.com/StanleysSteakhouse/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.25 上午', '2021-06-08 01.42.25 上午', 1269, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 86, 1, '43847702', 'Wayne’s Boston 瑋恩波士頓美式龍蝦牛排餐廳', '10491', '台北市', '中山區新生北路一段88號', 25.04774640, 121.52971380, '牛排', '牛排 看到臉書推薦媽媽指定母親節想吃這家\n馬上訂了六人位置∼帶媽媽跟阿嬤去\n因為節日關係只有提供2人或4人套餐\n若未點餐可以達低消500/人即可\n但都母親節了所以我們點了2人及4人各一份\n\n店內裝潢很美式∼裡面還有一間小包廂\n我們是第一組客人 11:30就進場 有消毒量額溫\n入座後介紹餐點及告知母親節會幫我們拍一張拍立得讓我們帶回家紀念∼∼\n\n餐點一道道上 麵包、湯品、前菜、龍蝦海鮮盤、牛排、甜點，飲料是另外單點的\n?麵包給很多 整個吃很飽（喂）\n?湯品有松露蘑菇跟龍蝦湯\n?龍蝦是真的很濃很濃有蝦殼的那種口味 真的超美式 那天大家比較喝習慣蘑菇湯xD\n?前菜是培根∼酒燒很特別 蒜味也好吃\n?另一個前菜有升等生蠔∼給母親了沒吃到\n?因為都有點套餐 一人半隻清蒸龍蝦 副餐的薯條跟燉飯都好好吃 連阿嬤都吃得很開心?\n?最後牛排點5分我們覺得剛好 一家人都不敢吃太生的 但實在是太飽吃掉小塊的 大塊的就帶回家了\n?奶油啤酒是黑松沙士+鹹奶油(?)跟環球哈利波特那個很像 個人很喜歡 用啤酒杯裝很可愛?\n\n整體來說 家人感受非常好 好吃好拍好飽?\n那天因為節日餐廳坐得滿滿的∼∼\n\n不過有個小插曲 坐我後面的那桌 服務生上飲料時沒拿好灑在那個人背後..我阿母有被濺到脖子但轉過去看了那個阿姨整個背都濕了她就轉回來了?與姊妹淘們一起去慶生，原本還蠻享受在美食中…直到服務人員來提醒我們只有2小時的用餐時間，整個破壞原本開心的氣氛，我們告訴她我們才剛用完正餐，還沒切蛋糕，她就是一直說預約時電話有說只有2小時…只好急忙吃完我們四個人點了$9,000多的餐點，提著我們自備的蛋糕(原本說壽星有小點心，後來我們沒有點雙人套餐就說不能給...然後說生日會拍拍立得最後也沒有，只是催我們快速結帳趕人)，我們只能匆忙的結帳離開另覓地點切蛋糕…想說的是，後面也沒有人預約我們位置，為什麼就不能彈性點，一直要強調2小時的用餐呢？4個女生也點了不少的食物，還沒吃完消化就一直趕人？真的是很有原則很掃興的服務…以前如果同時想要又有新鮮海鮮又有牛排，Wayne''s Boston絕對是我會想到的前三! 但最近一次消費，龍蝦和豬腳等餐點還算有維持水準，但Prime Ribeye真的大走鐘，寫評論是希望店家能夠正視而且改進，牛肉進貨以後的修肉是不可避免絕對必要的成本。\n\n當天點Prime Ribeye 10o.z，店家上菜時分切成兩塊，厚度確實比其他一樣以10o.z.上菜的牛排餐廳厚了許多，淡粉紅但不帶血的熟度也令人期待! 但一刀切下去，是切不斷的，我不明所以的把肉用力刮開，才看到這長條狀牛排中間是一整片的筋膜根本用刀切不斷 (第一張照片)，我翻面再切一樣切不斷，因為另一面的肉裡一樣藏了許多的韌筋(第二張)。只好再切另外一塊，結果側邊一樣有切不斷的筋 (第三張)。\n\n服務生過來詢問熟度和口味，我告知筋真的太多，得到的回覆是「肋眼都是這樣的，我們有盡量去掉，不過會破壞他的完整性。」\n\n老實說，「肋眼就是這樣的」這句話比切不斷的筋還讓人生氣，台灣不是只有貴餐廳一家在賣牛排，Choice等級的Ribeye也不會這樣出餐，況且貴餐廳店名是牛排餐廳、菜單上標榜的牛肉等級是Prime，收取的是10o.z./NT$1,080.-的單點價格，負責任的修整牛肉不應出到客戶面前的肉筋和部位是基本功，被告知對餐點不滿意時好好檢討是基本的自重。\n\n整份牛排到最後有1/3的部份都因為切不斷筋，而且手也用力到發紅了，只好放棄請小姐收走。來收餐的小姐聽到要收走也停了一下但沒有多問，不過說真的，有前一位男服務生的回覆，我也不想再多說什麼了。這是一間適合慶祝的店\n食材新鮮份量夠(龍蝦拼盤2人份)\n服務很好龍蝦會幫忙處理，店內氣氛裝潢也都很用心，停車位也很好找(高架下)節慶還能升級甜點及合照??My google reviews: 4.9 stars\n.\n賀成交！\n為慶祝成交，今天來吃個海陸大餐，\n不過成交價有點低，可能少賺有50萬...\n好像也不是特別開心，\n所以今天就順便來把振興三倍券花一花，\n現在疫情大家都被關在國內振興經濟，\n尤其是離島，根本被振到爆，\n最好先閃遠點，\n其實現在最欠振的應該是台北才對，\n相較於振興，感覺更像是賑災\n.\n單點了一隻\n?清蒸波士頓龍蝦，\n第3張圖，1300元，\n價位當然還是比#美國 當地高，\n也比上次去#巴黎 吃的#PedraAlta 貴，\n挑選600~650g左右的龍蝦，\n太大隻的肉會太老，\n服務人員會幫忙碎龍蝦殼，\n蝦頭綠色的部分店員說是蝦膏可以吃，\n吃起來好像還好，\n而且我的膽固醇有點爆表，就不吃了~@@\n搭配的熱奶油跟雞尾酒醬，\n這個雞尾酒醬還真不錯，\n阿龍蝦肉質紮實彈牙，好吃，\n不會輸美國跟巴黎的，\n其實龍蝦就是要吃現撈的，\n跟冷凍的美味程度明顯差一個檔次，\n但大家都知道其實波士頓龍蝦不是真的龍蝦，\n而是便宜的螯蝦，\n真的龍蝦譬如說藍龍蝦，價錢基本上要再貴個2~3倍，\n未來小弟若有吃出兩者的差異，再來跟大家分享\n.\n海陸雙人套餐，2990元，\n湯品\n?龍蝦濃湯，\n第5張圖，\n這湯濃厚到已經有點不是液態，\n不過卻濃而不稠，\n滿滿龍蝦味，不過可惜沒有龍蝦肉塊，\n但是還是相當好吃的一道湯品，\n口味偏重，\n建議一碗多個人分即可，\n可用餐前麵包沾來吃\n.\n開胃菜加了120元換成4顆\n?新英格蘭賭場烤大蛤，\n第6張圖，\n蛤仔上面鋪的是烤培根，再灑上起司，\n並且用大蒜下去調味，\n有去過基隆夜市吃過那種超大顆蛤仔的都知道，\n蛤仔太大顆其實不好吃，\n肉質會太老，跟嚼口香糖一樣，\n這個烤大蛤是沒到那麼大顆，\n但是肉還是比較有嚼勁的，\n吃起來就還可以\n.\n主菜波士頓龍蝦\n加了660元升級成\n?豪華龍蝦海鮮拼盤 (佐微辣濃郁龍蝦醬)，\n第2張圖，\n除了波龍外，\n還有2顆馬祖貽貝(淡菜)、1顆干貝、白蝦、墨魚跟文蛤，\n白蝦有幫去殼，\n龍蝦醬一碟就要100塊，\n不會辣，味道就跟龍蝦濃湯很像，\n也滿好吃，\n附餐有玉米、薯條跟迷你燉飯，\n玉米甜，薯條還不錯，\n燉飯培根味道很濃，白醬口味也不錯，\n附餐也並不會敷衍了事，\n3個人吃到目前，其實已經開始飽了\n.\n主菜\n?美國極佳級肋眼牛排10oz，\n第4張圖，\n這裡的極佳級就是Prime等級，\n盤子有預熱，\n搭配的油封大蒜、玫瑰鹽跟芥末籽醬，\n由於跟我老母一起吃不敢點太生，\n所以就點個5分熟，不帶血水，\n但結果還是吃不習慣，\n就再切一部分肉下來請店員回烤到7分熟，\n即使龍蝦是主角，\n這間餐廳的牛排其實也不馬虎，口味也不俗\n.\n甜點加了100元升級成\n?主廚自製冰涼杏仁霜派，\n第7張圖，\n歐這個好好吃，\n杏仁香氣混花生香氣，\n而且又是非常清爽的冰淇淋口感的派，\n我已經吃很脹了還是一直吃，\n必點\n.\n?總結，\n相隔8個月我終於又給出一家超過4.5顆星的餐廳了，\n上一家好像就是巴黎的Pedra Alta ~@@\n這間裝潢頗有波士頓風情，\n桌邊埋單，\n?3個人吃5742元，\n一個人還不到2000元，\n說貴也沒有到真的很貴，\n龍蝦雖然比國外貴，\n但是台灣的服務自然是好多了，\n再加上機票成本，\n而且同時有海陸雙重享受，\n這間很推阿，\n阿後來也在信義區開了二店：Wayne''s New York\n.\n更多精準中立的美食評論在\nIG: tedchen5152\n#工程師Ted吃美食台北', 4.3, 805, 3462, 'vMWnY1@gmail.com', '02 2541 9530', 3, '["星期一: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00","星期二: 休息","星期三: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00","星期四: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00","星期五: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00","星期六: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00","星期日: 11:30 \\u2013 15:00, 17:00 \\u2013 22:00"]', 'https://www.facebook.com/waynesboston', '0.jpg', '[0.jpg]', '2021-06-08 01.42.25 上午', '2021-06-08 01.42.25 上午', 805, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 87, 1, '32317231', 'ToTsuZen Steak 現切現煎以克計價濕式熟成牛排（捷運忠孝敦化、東區美食、台北人氣餐廳推薦）', '106', '台北市', '大安區敦化南路一段233巷59號', 25.04048160, 121.55232430, '牛排', '牛排 菲力點了250g 感覺吃的有點飽，看來下次點基本200g就可以了∼ fb打卡送飲料?，商業午餐送湯或沙拉，中午去吃很悠閒，下次有機會再光顧∼初訪\n\n店面給人感覺簡單乾淨\n也是單純吃肉的地方\n餐前有點了當日的巧達湯，是有用心下去煮的，是拿罐頭湊數~\n\n主餐點了最基本的沙朗心跟雞腿排，沙朗300g，雞肉40g，如果沒有加什麼配菜，也可以配個白飯吃~\n\n沙朗本身帶筋跟油，要點的話得注意這會影響口感\n不過肉煎得很恰到好處，雞腿排也是，但口味上就沒有一種會讓人很驚豔或是wow~的那種感覺\n然後既然都來到專門賣牛的地方了，建議還是都點牛吧?\n\n服務上值得一提的是，我們的兩個主餐是一起上桌的，上桌時兩盤都是剛煎好熱得冒煙，其實兩種肉的處理時間假如同時開始，應該很難同時完成得恰到好處，所以應該是有特別去注意這點~打卡送無酒精飲料也不錯，另外自行收盤就免服務費也值得讚賞\n\n總體來說是會想再訪的店，下次應該會試試菲力之類牛排選擇蠻多樣，可以自己決定份量。牛肉的品質很不賴。\n不過有幾個可以強化的點\n1. 來過幾次，整間店都是空的，卻說沒位置\n2. 店員在用手機的時候，如果你要點配菜，店員態度會比較差，如果你抗壓性比較不好，可能會吵架或玻璃心。還好我當過兵跳過傘，完全扛得住。點了紐約客與沙朗牛排，很特別的牛排店，牛排在你眼前現切現秤重給你看，店員很會切一次就對了\n牛排熟度的話，紐約客依照建議三分，但可能是鐵板的關係變得比較熟一點大概五分吧200克的亂切牛排現在要329 另加其他配菜3樣99元 一餐吃下來也要快450 但亂切的肉真的慘不忍睹 又小塊又全部都是筋 嚼到我嘴都痠了 最後根本每一塊都用吞的= = 相比之下阪急的鐵火牛排亂切 肉嫩又大塊 還只要250又有一堆配菜和吃到飽的附餐 整個CP值大勝 只能說難怪這家現在連週末假日中午都沒人 真的不意外', 4.1, 842, 3452, 'cVpHUVA@gmail.com', '02 2711 7521', 2, '["星期一: 12:00 \\u2013 23:00","星期二: 12:00 \\u2013 23:00","星期三: 12:00 \\u2013 23:00","星期四: 12:00 \\u2013 23:00","星期五: 12:00 \\u2013 23:00","星期六: 12:00 \\u2013 23:00","星期日: 12:00 \\u2013 23:00"]', 'https://www.totsuzen.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.26 上午', '2021-06-08 01.42.26 上午', 842, 0);
INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 88, 1, '00159875', '王品牛排台北南京東店', '105', '台北市', '松山區南京東路四段169號', 25.05174360, 121.55631550, '牛排', '牛排 綜合評比1.5顆星\n\n?服務態度：整體偏冷漠。只有一位男服務員，做事比較細心，親切主動。\n\n其中一位服務員在倒香檳氣泡飲的時候，還不小心滴到旁邊的檸檬水裡面，而不自覺，或是裝作沒看到。\n\n可能母親節假期訂餐多，上餐速度非常慢，都是桌面全部清空後很久*才來，然後又會兩道菜擠在一起出。鮭魚剛來，主餐就馬上來還催促趁熱吃焗烤生蠔?（那前一道就要放冷吃了嗎?）\n\n?餐點品質：很多細節都顯得馬乎，可惜了這個價位跟王品的招牌。 （看照片就看得出來： 湯湯水水的會液到盤子上、菇會跑到盤子邊緣等）主餐的魚肉太硬，還很多刺。基本上都沒有鮮甜味。干貝有腥味，太噁心。現打的蔬果汁非常稀，加了過多的水，一點自然甜味都沒有。甜點還oK\n\n競爭的餐飲業大家都在求新求變，對比王品牛排不夠用心，時間久了也只能被時代淘汰了服務五顆星，幫忙慶生還有拍照很讚，但餐點只能給一顆星了，餐點不會到不好吃但就明顯不到那個價位，可能是公司想省錢，牛排用比較低等級的，估計頂多美牛choice等級吧，我自己買Costco 煎起來好吃很多，其他餐點如湯、沙拉甜點就安全牌沒有亮點，麵包以前有鵝肝醬可以配，現在省成本也沒了，飲料的水果汁超淡，我很常打果汁，這個一定加了超多水前幾個禮拜失戀，好不容易走出來，朋友請我吃王品，結果裡面好多情侶，害我又哭了兩天（但是很好吃）想帶老人回味從前王品的牛排美味，卻對口感大失所望，牛排老硬( 黑松露紐約客)，還不少筋。。。只有服務和擺盤很好，干貝，蝦都超小，送的龍蝦也小也硬梆梆。。。!CP值當然低。。黑松露紐約客牛排套餐整體表現都在水準之上，無論沙拉，主餐，飲料，甜點都令人驚艷，唯一缺點就是五分熟還是有點硬。', 4.2, 1127, 4733, 'jnnHvac@gmail.com', '02 8770 7989', 3, '["星期一: 11:30 \\u2013 14:30, 17:30 \\u2013 20:00","星期二: 11:30 \\u2013 14:30, 17:30 \\u2013 20:00","星期三: 11:30 \\u2013 14:30, 17:30 \\u2013 20:00","星期四: 11:30 \\u2013 14:30, 17:30 \\u2013 20:00","星期五: 11:30 \\u2013 14:30, 17:30 \\u2013 20:00","星期六: 11:00 \\u2013 14:30, 17:00 \\u2013 20:00","星期日: 11:00 \\u2013 14:30, 17:00 \\u2013 20:00"]', 'https://www.wangsteak.com.tw/', '0.jpg', '[0.jpg]', '2021-06-08 01.42.27 上午', '2021-06-08 01.42.27 上午', 1127, 0);
