INSERT INTO Team4DB.shop( `SHOP_ID`, `MEMBER_ID`, `SHOP_TAX_ID`, `SHOP_NAME`, `SHOP_ZIP_CODE`, `SHOP_CITY`, `SHOP_ADDRESS`, `SHOP_LATITUDE`, `SHOP_LONGITUDE`, `SHOP_DESCRIPTION`, `SHOP_TAG`, `SHOP_RATING`, `SHOP_RATING_COUNT`, `SHOP_RATING_TOTAL`, `SHOP_EMAIL`, `SHOP_PHONE`, `SHOP_PRICE_LEVEL`, `SHOP_OPENING_TIME`, `SHOP_WEBSITE`, `SHOP_MAIN_IMG`, `SHOP_GALLERY`, `SHOP_CREATE_TIME`, `SHOP_UPDATE_TIME`, `SHOP_TOTAL_VIEW`, `SHOP_RESERV_STATUS` ) VALUES ( 142, 1, '31601263', '川越?-午餐定食 中山區美食居酒屋 串燒料理推薦 特色美食餐廳 建國美食 日式串燒', '10491', '台北市', '中山區建國北路一段70號', 25.04909290, 121.53634240, '居酒屋', '居酒屋 很難吃而且餐點很不精緻、調味死鹹，兩人1500up且吃不飽相撲火鍋3-4人份，上來就4片肉片，3顆丸子。收我528！！超差的用餐體驗！！\n點餐的時候店員還說這3-4人份很大份喔！吃的完嗎？心想正餓著，吃不完再說。結果是這種狀況，感覺很差！跟店員反應，店員還說那可以加湯。眼翻到太平洋去了。我說：我不要喝湯喝飽！很差的用餐體驗，再也不會去用餐了。這是我畢生吃過最難吃日式串燒店，沒有之一。\n點了串燒拼盤結果只有一串吃得完，因為整盤比7-11加熱食品還不如，醬多到根本看不出來有沒有烤過，當然吃起來也不像烤過，都不知道在吃什麼！\n價格當然是飛天了，好吃價格高就算了，吃起來像微波食品你給我賣這價格就說不過去了。\n如果可以我希望是0星評價。完全浪費我的錢！\n而且食材不新鮮\n戴著料理手套給錢\n噁心?價錢餐點不太符合，沒有生啤酒?有麒麟精釀啤酒，只好點了金牌2支！才發現怎麼不去熱炒店吃串燒就好?', 3.7, 129, 477, 'W5NRpT@gmail.com', '02 2503 1870', 2, '["星期一: 17:00 \\u2013 23:30","星期二: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期三: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期四: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期五: 11:30 \\u2013 14:30, 17:00 \\u2013 23:30","星期六: 17:00 \\u2013 23:30","星期日: 休息"]', 'https://www.facebook.com/%E5%B7%9D%E8%B6%8A%E9%A7%85%E6%97%A5%E5%BC%8F%E4%B8%B2%E7%87%92%E5%B0%88%E9%96%80%E5%BA%97-460941213966266/', '0.jpg', '[0.jpg]', '2021-06-08 01.43.56 上午', '2021-06-08 01.43.56 上午', 129, 0);
